(window.webpackJsonp=window.webpackJsonp||[]).push([[2223],{2578:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-923-luc-tinh-danh-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-923-luc-tinh-danh-su"}},[n._v("#")]),n._v(" Chương 923: Lục tinh danh sư!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 923: Lục tinh danh sư! "),t("br"),t("br"),n._v("Hai tháng sau."),t("br"),t("br"),n._v("Hội nghị trưởng lão sảnh."),t("br"),t("br"),n._v("Mi trưởng lão, Triệu Bính Tuất đám người ngồi ngay ngắn một nhà."),t("br"),t("br"),n._v("Lục Phong từ đi Đan viện viện trưởng chức vụ về sau, từ phó viện trưởng Chu Kình tiếp nhận, trận pháp sư học viện Trần Thừa Tuần cũng lui ra đến, đổi một vị phó viện trưởng."),t("br"),t("br"),n._v("Nói cách khác, mười đại viện trưởng, tất cả đều đúng chỗ."),t("br"),t("br"),n._v("“Mi trưởng lão, viện trưởng tấn thăng đế quốc, tiến hành đến một bước nào?” Triệu Bính Tuất hỏi."),t("br"),t("br"),n._v("Viện trưởng khảo hạch một chút lục tinh danh sư, là danh sư học viện hàng đầu đại sự, chuyện này, một mực từ Mi trưởng lão theo vào, những người khác còn có sự tình khác xử lý, biết đến không nhiều."),t("br"),t("br"),n._v("“Tốt, ta đến nói một chút!”"),t("br"),t("br"),n._v("Mi trưởng lão đứng dậy: “Viện trưởng lựa chọn muốn tấn thăng phong hào vương quốc là... Hiên Viên vương quốc! Đây là Huyễn Vũ nhị đẳng đế quốc cấp dưới Vạn Quốc liên minh ở dưới một quốc gia, mười phần vắng vẻ cùng cằn cỗi!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Loại này tiểu quốc lần, nói thật... Bọn họ nghe đều chưa từng nghe qua, đủ thấy xa xôi."),t("br"),t("br"),n._v("Danh sư đại lục thực sự quá lớn, vương quốc, đế quốc, phân cấp quá nhiều, coi như danh sư thu được nghe rộng rãi biết, cũng không có khả năng toàn không biết được."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc còn có điều nghe thấy, nhưng cấp dưới Vạn Quốc liên minh liền nghe cực ít, huống chi càng phía dưới Hiên Viên vương quốc."),t("br"),t("br"),n._v("Đối với lục tinh danh sư tới nói, đã thuộc về xa xôi chưa khai hóa khu vực."),t("br"),t("br"),n._v("“Viện trưởng hai tháng trước đến vương quốc cảnh nội, không có tuyên dương, trực tiếp đi những nơi Danh Sư đường!”"),t("br"),t("br"),n._v("Mi trưởng lão tựa hồ đối với Trương Huyền cử động hết sức rõ ràng, giải thích cặn kẽ: “Cái này Danh Sư đường đường chủ Lạc Thiên Hồng, là vị tam tinh đỉnh phong danh sư, từng cùng viện trưởng từng có mâu thuẫn, bị viện trưởng xông đường thành công!”"),t("br"),t("br"),n._v("“Xông đường?”"),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("Trước là danh sư, tự nhiên biết xông đường độ khó, cần đối mặt toàn bộ Danh Sư đường chỗ có danh sư, thậm chí còn có lưu tại trong đường tất cả ý niệm."),t("br"),t("br"),n._v("Căn cứ bọn họ biết đến tin tức, lúc trước viện trưởng tại Hiên Viên vương quốc thời điểm, chẳng qua nhị tinh danh sư, lúc này liền xông đường thành công, há không có nghĩa là... Khi đó, tam tinh đỉnh phong, thậm chí tứ tinh danh sư, cũng không là đối thủ?"),t("br"),t("br"),n._v("“Bị xông đường danh sư, sẽ vô điều kiện nghe theo người thành công phân phó, không biết viện trưởng phân phó bọn họ đã làm những gì?” Vệ Nhiễm Tuyết hỏi."),t("br"),t("br"),n._v("Danh Sư đường xông đường, cùng võ quán đá quán đồng dạng, kẻ thất bại liền phải tiếp nhận đến trừng phạt, vô điều kiện nghe theo người thắng trận phân phó."),t("br"),t("br"),n._v("“Viện trưởng cũng không có phân phó, mà là trước cho bọn hắn giảng bài, ba ngày sau, Hiên Viên vương quốc Danh Sư đường chỗ có danh sư, không chỉ tu vi tăng lên, đối danh sư lý giải, cũng gia tăng thật lớn. Nhất là vị kia Lạc Thiên Hồng, trực tiếp xin khảo hạch tứ tinh danh sư, chẳng những thông qua, còn xung kích đến tứ tinh đỉnh phong!”"),t("br"),t("br"),n._v("http://ngantruyen.com/"),t("br"),n._v("Chần chờ một chút, Mi trưởng lão đem tự mình biết tin tức nói ra."),t("br"),t("br"),n._v("“Ba ngày thời gian, để một vị tam tinh đỉnh phong danh sư đạt tới tứ tinh đỉnh phong?”"),t("br"),t("br"),n._v("“Cái này... Có chút quá nhanh đi?”"),t("br"),t("br"),n._v("Tất cả trưởng lão tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Danh sư khó mà tấn cấp, không riêng gì thực lực tăng lên, ánh mắt, kiến thức, tâm cảnh khắc độ, phụ tu... Mỗi một dạng đều cần tiêu tốn rất nhiều thời gian, truyền thụ ba ngày, vị này Lạc Thiên Hồng liền theo tam tinh đỉnh phong, đạt tới tứ tinh đỉnh phong, tiến bộ có phải hay không có chút quá nhanh chóng nhưng mãnh liệt?"),t("br"),t("br"),n._v("“Không chỉ có như vậy, trước đó Hiên Viên vương quốc, tổng cộng có hơn mười vị tam tinh danh sư, ba ngày qua đi, lại có tám vị đột phá tứ tinh, mặc dù đều chỉ là sơ kỳ, cũng đủ đáng sợ!” Mi trưởng lão cười khổ."),t("br"),t("br"),n._v("Mới vừa nghe được tin tức này thời điểm, hắn cũng đầy là không thể tin được, thẳng đến xác nhận nhiều lần, chân thực không sai, mới hiểu được... Thiên tài thế giới không phải bọn họ loại người này, có thể tưởng tượng."),t("br"),t("br"),n._v("“Tám vị đột phá, cộng thêm vị đường chủ kia, tổng cộng chín vị... Cho dù có chín vị tứ tinh danh sư, muốn trong thời gian ngắn, để một cái vương quốc tấn cấp thành Đế quốc, nội tình còn chưa đủ...”"),t("br"),t("br"),n._v("Một vị trưởng lão nói."),t("br"),t("br"),n._v("Mặc dù vương quốc tấn cấp đế quốc căn cơ là Danh Sư đường, nhưng cần tích lũy tháng ngày biến hóa, trong thời gian ngắn liền muốn hoàn thành, chỉ bằng vào mấy vị danh sư, vẫn là làm không được."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, tấn cấp đế quốc mới là khó khăn nhất đến khảo hạch, không có cái thứ hai."),t("br"),t("br"),n._v("“Là không đủ, nhưng mà... Viện trưởng đến Hiên Viên vương quốc ngày thứ mười, chung quanh tất cả bất nhập lưu đế quốc, cùng không kém bao nhiêu tông môn, thế lực, rất nhiều danh sư lao qua, còn có tu luyện giả, Hóa Phàm cảnh thực lực cao thủ, nhao nhao yêu cầu gia nhập vương quốc, trở thành một thành viên trong đó...” Mi trưởng lão nói tiếp."),t("br"),t("br"),n._v("“Gia nhập Hiên Viên vương quốc?”"),t("br"),t("br"),n._v("“Rất nhiều? Có bao nhiêu?”"),t("br"),t("br"),n._v("Mọi người cau mày."),t("br"),t("br"),n._v("“Cho dù có người muốn gia nhập, cũng không có gì đi! Một cái phong hào vương quốc, ít nhất có mấy trăm triệu nhân khẩu, gia nhập mấy vị danh sư, mấy vị tu luyện giả, không làm nên chuyện gì...” Có người lắc đầu."),t("br"),n._v("Cường giả có cường giả kiêu ngạo, sẽ rất ít đi thêm cấp thấp quốc gia, làm như vậy tương đương tự ngăn đường lui, lại muốn tấn cấp, liền khó hơn."),t("br"),t("br"),n._v("Bởi vậy, tại mọi người nhìn lại, cho dù có người gia nhập, khẳng định cũng sẽ không nhiều, hơn mười vị danh sư, mấy trăm vị cường giả, căng hết cỡ... Nhiều người như vậy, đặt ở mấy trăm triệu nhân khẩu quốc gia, như giọt nước trong biển cả, không đáng giá nhắc tới."),t("br"),t("br"),n._v("Vương quốc tấn thăng đế quốc, là toàn dân tố chất tăng lên, toàn thể quốc lực tấn cấp, không chỉ là nhiều mười cái tám cái tu luyện giả liền có thể làm được."),t("br"),t("br"),n._v("“Mấy vị danh sư? Mấy vị tu luyện giả?”"),t("br"),t("br"),n._v("Mi trưởng lão cười khổ: “Các ngươi quá coi thường viện trưởng tại Vạn Quốc liên minh lực hiệu triệu! Trong mười ngày, tổng cộng tới hơn 3000 vị tứ tinh danh sư, gần mười vạn Hóa Phàm trở lên cường giả...”"),t("br"),t("br"),n._v("“Hơn 3000 vị tứ tinh danh sư?”"),t("br"),t("br"),n._v("“Mười vạn Hóa Phàm trở lên cường giả?”"),t("br"),t("br"),n._v("Mọi người toàn giật nảy mình."),t("br"),t("br"),n._v("Nhiều như vậy danh sư, nhiều cường giả như vậy, coi như là bình thường bất nhập lưu đế quốc, cũng không có có nhiều như vậy a?"),t("br"),t("br"),n._v("“Nhiều cường giả như vậy đồng thời gia nhập Hiên Viên vương quốc... Một khi xuất hiện lòng phản loạn, vương quốc như thế nào ngăn được?” Tưởng Thanh Cầm nhịn không được cau mày."),t("br"),t("br"),n._v("Hắn mặc dù đối rất nhiều chuyện không quá quan tâm, nhưng mà quốc gia ở giữa cân bằng vẫn là rất rõ ràng."),t("br"),t("br"),n._v("Nhiều cường giả như vậy, so vương quốc tất cả đại thần, quốc vương bệ hạ đều cường đại hơn đi, cơ chế quản lý tu vi không lên đây, thực lực so phổ thông bách tính đều thấp, như thế nào quản lý? Như thế nào chính lệnh ảnh theo, để cho người ta thần phục?"),t("br"),t("br"),n._v("Bình thường phong hào vương quốc, lão tổ cấp bậc cũng mới Hóa Phàm nhất trọng, quốc vương bệ hạ, có thể đạt tới nửa bước Hóa Phàm đều rất ít... Mười vạn trở lên Hóa Phàm cường giả quy thuận, đối với loại này vương quốc tới nói, không hề là một chuyện tốt."),t("br"),t("br"),n._v("“Điểm ấy, chúng ta không cần lo lắng, viện trưởng đã sớm nghĩ kỹ, hắn theo Danh Sư đường đi ra đến những người này quy thuận bảy ngày thời gian, cơ bản đều tại Hiên Viên hoàng cung... Trong vòng vài ngày, vương quốc quốc vương Mạc Vũ bệ hạ, đã là Hóa Phàm ngũ trọng cao thủ! Cái khác tất cả cùng nàng gần gũi đại thần, cũng đều nhao nhao tăng cao tu vi, có Hóa Phàm nhị trọng, tam trọng thực lực...”"),t("br"),t("br"),n._v("Mi trưởng lão nói tiếp."),t("br"),t("br"),n._v("Ngắn ngủi bảy ngày, làm cho cả hoàng cung, có biến hóa nghiêng trời lệch đất, tất cả mọi người tu vi tăng nhiều... Đổi lấy trước kia, nghĩ cũng không dám nghĩ."),t("br"),t("br"),n._v("Nhưng mà tận mắt nhìn đến viện trưởng công khai khóa rầm rộ, thấy được vô số động vật đột phá thành man thú... Biết, bất kể ở trên người hắn xảy ra chuyện gì, đều là không thể bình thường hơn được."),t("br"),t("br"),n._v("Mi trưởng lão tiếp tục: “Không chỉ có như vậy, viện trưởng còn đem Vân Vụ lĩnh linh thú, mang đến không ít, chừng hàng ngàn con nhiều, mỗi một cái đều có Hóa Phàm lục trọng sức chiến đấu, chỉ phụ trách nghe theo Mạc Vũ bệ hạ một người phân phó... Như vậy dựa dẫm, coi như một chút nhị đẳng đế quốc đều không dám tùy tiện xâm phạm, huống chi những này tu luyện giả...”"),t("br"),t("br"),n._v("“Hơn ngàn chỉ Hóa Phàm lục trọng linh thú? Cũng liền viện trưởng có thủ bút lớn như vậy!”"),t("br"),t("br"),n._v("“Đúng vậy a, đổi lại người khác, coi như muốn, cũng không bỏ ra nổi nhiều như vậy trung thành tuyệt đối linh thú ah!”"),t("br"),t("br"),n._v("“Dạng này thủ hộ hoàng thất, có thể nói vững như thành đồng...”"),t("br"),t("br"),n._v("Mọi người yên lặng, lại nói không ra lời."),t("br"),t("br"),n._v("Bình thường thuần thú sư, cơ bản chỉ có một hai đầu thú sủng, khẳng định không có khả năng đưa người... Viện trưởng ngược lại tốt, Vân Vụ lĩnh mười vạn linh thú thần phục, tùy tiện đưa lên một ngàn đầu thực lực thấp, không đáng kể chút nào!"),t("br"),t("br"),n._v("Đối với hắn mà nói không có gì, nhưng đối Hiên Viên vương quốc tới nói, tác dụng liền rất lớn, linh thú tuổi thọ bản thân liền so với nhân loại càng dài, lại thêm thực lực, vượt xa vương quốc người mạnh nhất... Dùng để canh giữ vương thất, tuyệt đối không ai dám nói nửa câu nói nhảm."),t("br"),t("br"),n._v("“Nhiều người như vậy, đi vào Hiên Viên vương thành về sau, viện trưởng đi đầu nói một tiết khóa, kết quả, thanh âm bao phủ mấy trăm cây số, toàn bộ vương thành tu luyện giả, đều chiếm được chỗ tốt cực lớn, hầu như hơn phân nửa tu luyện giả đều đột phá... Hơn nữa lời tụ linh khí, kéo dài không tiêu tan, làm cho cả vương thành, trong nháy mắt biến thành linh khí dư dả Thánh địa...”"),t("br"),t("br"),n._v("Mi trưởng lão nói tiếp."),t("br"),t("br"),n._v("“Lời tụ linh khí, thiên hoa loạn trụy... Viện trưởng có năng lực như thế, để rất nhiều người nhanh chóng tấn cấp, cũng có loại này bản lĩnh, nhưng... Tấn thăng đế quốc, không chỉ tu luyện người muốn nhiều, thực lực tổng hợp gia tăng, còn muốn quốc lực tăng cường, cùng tất cả chức nghiệp phồn vinh phát triển...”"),t("br"),t("br"),n._v("Thư họa sư học viện viện trưởng, Tống Đan Thanh nói."),t("br"),t("br"),n._v("Cân nhắc quốc gia mạnh yếu, toàn dân thực lực mạnh yếu, chỉ là bên trong một cái tiêu chuẩn, càng quan trọng hơn, còn có quốc lực mạnh, cùng tất cả chức nghiệp bồng bột phát triển."),t("br"),t("br"),n._v("Quốc lực, là ngón tay một quốc gia kinh tế, văn hóa, nhân dân tiêu phí trình độ."),t("br"),t("br"),n._v("Tất cả chức nghiệp, dính dáng liền có thêm, chỉ cần là đế quốc, dù là bất nhập lưu, cũng không chỉ chỉ có cường đại Danh Sư đường, thư họa sư hiệp hội, luyện đan sư hiệp hội, luyện khí sư hiệp sẽ... Đều biết tồn tại, hơn nữa thu hút vô số người đến đây học tập."),t("br"),t("br"),n._v("“Khả năng ngươi gần nhất thai nang, không có chú ý, viện trưởng rời khỏi Hồng Viễn thành cùng một ngày, Hồng Viễn thành tứ đại gia tộc Thẩm gia, Liễu gia, liền tuyên bố muốn tại Hiên Viên vương quốc thành lập thương hội, khai thông mậu dịch... Tin tức này vừa ra, dẫn động cấp dưới vô số đế quốc kinh tế thể, nhao nhao vào ở... Thời khắc này Hiên Viên vương quốc, đơn thuần mới mở thương gia số lượng, so với một chút nhị đẳng đế quốc, đều không hề yếu!”"),t("br"),t("br"),n._v("“Còn những nghề nghiệp khác, viện trưởng hao tốn thời gian mười ngày, truyền thụ thư hoạ, luyện đan, luyện khí, ma âm... Rất nhiều chức nghiệp, đưa tới tất cả chức nghiệp công hội coi trọng, hiện nay đã bảy loại chức nghiệp tại Hiên Viên vương quốc mới mở phân bộ...”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("“Toàn dân thực lực, quốc lực, kinh tế, phụ tu... Tất cả đều làm được, nói như vậy lời nói, Hiên Viên vương quốc, đã hoàn toàn có thể có tư cách tấn cấp đế quốc!”"),t("br"),t("br"),n._v("Nghe xong hắn, một trưởng lão nhịn không được tự lẩm bẩm."),t("br"),t("br"),n._v("Cân nhắc một cái quốc gia thực lực tiêu chuẩn, đơn giản liền mấy dạng này, viện trưởng đi không đến hai tháng, liền để hắn có biến hóa lớn như vậy, chẳng lẽ... Đã thành công tấn cấp?"),t("br"),t("br"),n._v("“Ừm, hai mươi ngày phía trước, tổng bộ đã phái người đi khảo sát, hôm nay đem mọi người triệu tập tới, chính là muốn nói cho các ngươi kết quả...”"),t("br"),t("br"),n._v("Mi trưởng lão nhẹ gật đầu: “Viện trưởng... Lục tinh danh sư khảo hạch, tấn cấp đế quốc... Đã hoàn thành! Nói cách khác, hắn hiện tại đã là một vị lục tinh danh sư!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-931"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-933"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);