(window.webpackJsonp=window.webpackJsonp||[]).push([[2224],{2579:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-924-bon-đai-vien-truong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-924-bon-đai-vien-truong"}},[n._v("#")]),n._v(" Chương 924: Bốn đại viện trưởng")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 924: Bốn đại viện trưởng "),i("br"),i("br"),n._v("“Đã kiểm tra xong?”"),i("br"),i("br"),n._v("“Ngắn ngủi hai tháng, liền đem một cái phong hào vương quốc, tấn cấp làm đế quốc?”"),i("br"),i("br"),n._v("“Ta nhớ được, chúng ta học viện cao nhất ghi chép Mạc Lưu Chân viện trưởng, dùng ròng rã ba năm a?”"),i("br"),i("br"),n._v("“Đây cũng quá nhanh..”"),i("br"),i("br"),n._v("Tất cả trưởng lão tất cả đều sửng sốt."),i("br"),i("br"),n._v("Bọn họ đã sớm đoán được, viện trưởng khảo hạch tấn thăng đế quốc, tất nhiên có thể thông qua, có thể làm giấc mơ đều không nghĩ tới, nhanh như vậy!"),i("br"),i("br"),n._v("Hai tháng liền thành công, chỉ sợ đã phá vỡ Danh Sư đường ghi chép."),i("br"),i("br"),n._v("“Đúng vậy a, quá nhanh, không có gì bất ngờ xảy ra, viện trưởng cũng đã khởi hành trở về...”"),i("br"),i("br"),n._v("Mi trưởng lão gật đầu."),i("br"),i("br"),n._v("“Dạng này tốt nhất, vừa vặn có thể đuổi kịp Chiến sư tuyển chọn...” Triệu Bính Tuất nói."),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Nói đến Chiến sư tuyển chọn, mọi người tất cả đều sắc mặt nghiêm túc."),i("br"),i("br"),n._v("Tứ đại danh sư học viện trăm năm tiến hành một lần, mặc dù mặt ngoài không có cái gì, trên thực tế lại là âm thầm đọ sức, lúc này, viện trưởng có thể tự mình trở về, chủ trì đại cục, càng là trở thành lục tinh danh sư, không còn gì tốt hơn."),i("br"),i("br"),n._v("“Tuyển chọn Chiến sư, mỗi cái niên cấp đều có cơ hội, mỗi bên niên cấp hai mươi người đứng đầu, trải qua hai tháng này tỷ thí, đã không sai biệt lắm đã chọn được.”"),i("br"),i("br"),n._v("Tu Trường Thanh nói."),i("br"),i("br"),n._v("Chiến sư tuyển chọn, dùng vũ lực làm đầu, hai tháng này đến, mỗi bên niên cấp một mực tại tiến hành thi đấu, rốt cục chọn lựa từng người hai mươi người đứng đầu, những người này đem đại biểu học viện, cùng cái khác ba đại học viện tiến hành tranh đấu, tranh đoạt sau cùng danh ngạch."),i("br"),i("br"),n._v("“Chúng ta học sinh thực lực như thế nào?” Nghe được tuyển ra, Triệu Bính Tuất dừng lại một chút hỏi."),i("br"),i("br"),n._v("“Căn cứ trước kia tình huống, vẫn là hơi yếu, có thể thông qua, chỉ sợ... Không có mấy cái!” Tu Trường Thanh hơi đỏ mặt."),i("br"),i("br"),n._v("Hồng Viễn danh sư học viện, từ trước tới nay đều là bốn đại học viện hạng chót, thành lập vạn năm qua, tổng cộng mới có 327 người trở thành Chiến sư, phân tán đến trăm năm một lần, cũng liền bình quân mỗi lần ba, bốn cá nhân mà thôi."),i("br"),i("br"),n._v("“Khoảng cách tuyển chọn còn có mấy ngày thời gian, đem những học sinh này tập trung lại, thật tốt tu luyện, tranh thủ để bọn hắn tiến thêm một bước!” Nghe được tình huống cũng không lạc quan, Triệu Bính Tuất, nói."),i("br"),i("br"),n._v("“Cũng chỉ có thể dạng này!” Mọi người gật đầu."),i("br"),i("br"),n._v("Lâm trận mới mài gươm, mặc dù hiệu quả cực kỳ bé nhỏ, nhưng cũng là không có cách nào chuyện."),i("br"),i("br"),n._v("Cái khác danh sư học viện, nắm giữ thất tinh danh sư tọa trấn, tài nguyên cũng so Hồng Viễn học viện phong phú, rất nhiều thiên tài đứng đầu, đều lựa chọn cái này ba sở học tập, mà bọn họ có thể chiêu đến, đều là còn lại... So bất quá đối phương, cũng không có cách nào."),i("br"),i("br"),n._v("Dạy và học cùng tiến bộ, lão sư chỉ riêng dạy thật không được, sinh nguyên không tốt, học sinh không có thiên phú, đồng dạng không có tỉ lệ lên lớp."),i("br"),i("br"),n._v("Đây chính là tuần hoàn ác tính, liên tục nhiều năm đều lấy không được thành tích tốt, Danh Sư đường tổng bộ ban thưởng càng ngày càng ít, thanh danh hạ thấp, học sinh tốt đương nhiên sẽ không lại đến... Về sau thì càng kém."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Danh sư học viện, tất cả trưởng lão nghị sự, Hồng Viễn ngoài thành, một cái to lớn phi chu ngừng hạ hạ tới."),i("br"),i("br"),n._v("Chừng bảy, dài tám mươi mét, hơn mười tầng lầu cao, phía trên lầu các đình đài đầy đủ mọi thứ, từ xa nhìn lại, dị thường xa hoa."),i("br"),i("br"),n._v("Phi chu phía trước, đằng sau, đều có tám đầu ẩn chứa Long tộc huyết mạch Thánh thú điều khiển, đem to lớn thân tàu nâng nâng trên không trung, chậm rãi hạ xuống, không có một tia xóc nảy."),i("br"),i("br"),n._v("Boong thuyền, hai cái lão giả đứng ở phía trên, nhìn trước mắt nguy nga bát ngát tường thành, toàn đều mỉm cười."),i("br"),i("br"),n._v("“Ô viện trưởng, may mắn mà có ngươi , nếu không, nhanh như vậy muốn chạy tới nơi này, thật không rất dễ dàng!”"),i("br"),i("br"),n._v("Bên tay trái một cái thanh y lão giả vuốt râu nói."),i("br"),i("br"),n._v("“Bốn đại học viện, như thể chân tay, trợ giúp lẫn nhau hẳn là! Lại nói, lần này tại Hồng Viễn học viện tiến hành tuyển chọn, theo chúng ta Vân Hư học viện tới, vừa vặn đi ngang qua các ngươi La Thanh học viện, đem bọn ngươi nối liền... Cũng có thể để tất cả học sinh, sớm chút giao lưu, trước thời hạn biết thế giới bao lớn, cường giả như rừng! Nếu không một mực bế quan tự phong, cảm giác đến anh hùng thiên hạ duy ngã độc tôn, đối bọn hắn trưởng thành, cũng không có bất kỳ cái gì chỗ tốt.”"),i("br"),i("br"),n._v("Bên phải lão giả áo xám, cười nói."),i("br"),i("br"),n._v("Hai người này, mỗi một cái đều có Thánh vực trở lên thực lực, khí tức lượn lờ cho người ta một loại cảm giác thâm bất khả trắc."),i("br"),i("br"),n._v("Nhất là danh sư bào huy chương trước ngực, bảy vì sao lấp lánh phát sáng, vừa nhìn liền biết, đã đạt đến thất tinh tình trạng."),i("br"),i("br"),n._v("Thất tinh danh sư, đây chính là Hồng Viễn đế quốc Danh Sư đường, thậm chí danh sư học viện, đều không người đi đến."),i("br"),i("br"),n._v("“Đúng vậy a, lý không phân biệt không rõ, đạo không nói không rõ... Những học sinh này, có thể theo trong học viện trổ hết tài năng, tham gia Chiến sư tuyển chọn, cơ bản đều là hạng người kinh tài tuyệt diễm, cậy tài khinh người, là khó mà tránh khỏi, để bọn hắn trước thời hạn biết, sơn ngoại hữu sơn, người bên trên có người, cũng không phải cái gì sai lầm, ngược lại có ích lợi rất lớn!”"),i("br"),i("br"),n._v("Thanh y lão giả nhẹ gật đầu."),i("br"),i("br"),n._v("“Chúng ta đều tới, Ngũ viện trưởng đâu? Làm sao một mực không thấy?”"),i("br"),i("br"),n._v("Ô viện trưởng nghi ngờ hỏi."),i("br"),i("br"),n._v("“Ngũ Nhiên cái tên này, ngươi cũng biết, si mê với Phong Thánh đài Khổng sư lưu lại tâm đắc, còn lấy cái Phong Thánh hội, mấy tháng trước liền trước thời hạn ra cửa, đoán chừng hiện tại đã sớm vào thành!”"),i("br"),i("br"),n._v("Thanh y lão giả nói."),i("br"),i("br"),n._v("Bọn họ trong miệng nói Ngũ Nhiên, đang là lúc trước Trương Huyền tại Phong Thánh đài gặp phải vị kia Thanh Trúc danh sư học viện viện trưởng, Phong Thánh hội hội trưởng."),i("br"),i("br"),n._v("Vị này thanh y lão giả thì là La Thanh danh sư học viện viện trưởng Thẩm Bình Triều, Ô viện trưởng thì là Vân Hư học viện viện trưởng, Ô Thiên Khung."),i("br"),i("br"),n._v("Vân Hư, La Thanh, Thanh Trúc, Hồng Viễn... Là Thanh Nguyên phong hào vương quốc cấp dưới tứ đại danh sư học viện."),i("br"),i("br"),n._v("Vân Hư thứ nhất, Hồng Viễn cuối cùng."),i("br"),i("br"),n._v("“Ô sư, Thẩm sư, gia sư Ngũ Nhiên có lời mời!”"),i("br"),i("br"),n._v("Hai người đang tại trò chuyện, một người trung niên liền bay tới, rơi ở bên cạnh, cung kính ôm quyền."),i("br"),i("br"),n._v("“Thế nào, ta nói cái tên này đã sớm tới...” Thẩm Bình Triều cười cười, cũng không nhiều lời, lúc này trả lời: “Chúng ta cái này liền đi qua!”"),i("br"),n._v(" Nói xong cùng Ô Thiên Khung bàn giao sau lưng thầy trò vài câu, đi theo thanh niên sau lưng, thẳng tắp hướng nội thành bay đi."),i("br"),i("br"),n._v("Không lâu sau, đi tới một cái rộng rãi sân nhỏ."),i("br"),i("br"),n._v("Đi vào đại sảnh, quả nhiên thấy Ngũ Nhiên đang ngồi ở bên trong, đem nước nóng chậm rãi đổ vào ấm trà, động tác ưu nhã mà thoải mái dễ chịu, vừa nhìn liền biết là một vị trà đạo đại sư."),i("br"),i("br"),n._v("“Ngươi cái lão già, tới rất nhanh! Làm sao, kêu chúng ta qua tới uống trà? Nói trước một tiếng, không phải trà ngon, hai ta không uống!”"),i("br"),i("br"),n._v("Ô Thiên Khung cười nói."),i("br"),i("br"),n._v("“Yên tâm đi, nhất định là trà ngon!” Nhẹ nhàng cười một tiếng, Ngũ Nhiên giương mắt nhìn lại: “Đây chính là ta sai người tốn giá tiền rất lớn, chuyên môn mua sắm, chờ các ngươi hai tới!”"),i("br"),i("br"),n._v("Đang khi nói chuyện, đem trong ấm trà nước trà, rót vào chén nước."),i("br"),i("br"),n._v("Hai người tiếp nhận, còn không có nhấm nháp, liền thấy một đạo nồng đậm mây mù, từ đó bay lên, uốn lượn như yên hà, chói lóa mắt."),i("br"),i("br"),n._v("“Thật xinh đẹp trà...”"),i("br"),i("br"),n._v("Ánh mắt sáng lên."),i("br"),i("br"),n._v("Không nói trước trà đến cùng được không, riêng là loại này phẩm tướng, liền làm vô số trà, theo không kịp."),i("br"),i("br"),n._v("Mây phiêu sương mù nặng, cho người ta một loại khói mù bao phủ giang hà cảm giác, trà chưa nhập khẩu, đã tâm thần thanh thản."),i("br"),i("br"),n._v("“Cái kia là tự nhiên, đây là Hồng Viễn đế quốc, Vân Vụ lĩnh đặc thù Vân Vụ trà, liền cái này một bình nhỏ, liền muốn ta mười cái linh thạch thượng phẩm! Mà lại còn có tiền mà không mua được, trên cơ bản mua không được, ta nâng thật nhiều người, cũng chỉ mua được một hai. Một mực không có cam lòng uống, liền chờ các ngươi hai tới...”"),i("br"),i("br"),n._v("Ngũ Nhiên cười cười."),i("br"),i("br"),n._v("“Riêng này phẩm tướng liền đáng giá!” Ô Thiên Khung cười cười: “Đến, ta thử xem, hương vị như thế nào!”"),i("br"),i("br"),n._v("Ngưng cười, phẩm một cái, lập tức ánh mắt sáng lên, khen không dứt miệng: “Trà ngon!”"),i("br"),i("br"),n._v("Một bên Thẩm Bình Triều cũng không chậm trễ, uống hết cũng đồng dạng hai mắt tỏa ánh sáng."),i("br"),i("br"),n._v("“Trà này từ chỗ nào mua được? Cho ta cũng mang hộ chút...”"),i("br"),i("br"),n._v("“Mang hộ? Nếu như có thể mang hộ đến, ta khẳng định mua trước mười cân tích trữ...” Ngũ Nhiên lắc đầu."),i("br"),i("br"),n._v("“Cái kia đem ngươi cái này một lạng bán cho ta, cùng lắm thì ta ra gấp đôi giá cả!” Thẩm Bình Triều vội nói."),i("br"),i("br"),n._v("Hắn cũng là thích trà người, như vậy đẹp trà, tuy chỉ uống một lần, dĩ nhiên đã khó mà dứt bỏ."),i("br"),i("br"),n._v("“Gấp đôi giá cả cũng không có... Được rồi, không cùng các ngươi nhiều lời!” Ngũ Nhiên lắc đầu: “Các ngươi có thể nghe nói, Hồng Viễn học viện lão viện trưởng xảy ra chuyện tin tức?”"),i("br"),i("br"),n._v("“Nghe nói, hai năm trước mất tích, không rõ sống chết, hiện tại Hồng Viễn học viện, hẳn là đều còn không có mới viện trưởng đi!”"),i("br"),i("br"),n._v("Ô Thiên Khung lắc đầu."),i("br"),i("br"),n._v("Tứ đại danh sư học viện viện trưởng, địa vị tương đồng, vị này lão viện trưởng một lần thí luyện mất tích, lại không có trở về, thậm chí không rõ sống chết, bọn họ đã sớm nghe nói, còn âm thầm thần thương rất lâu."),i("br"),i("br"),n._v("“Lão viện trưởng là hai năm trước mất tích tin tức này chính xác, có điều, Hồng Viễn học viện, đã chọn lựa mới viện trưởng!” Ngũ Nhiên nói."),i("br"),i("br"),n._v("“Tuyển ra mới viện trưởng? Là cái hạng người gì, hỏi thăm sao?” Thẩm Bình Triều hỏi."),i("br"),i("br"),n._v("“Coi như không hỏi thăm cũng biết không ít... Cái này Hồng Viễn học viện mới viện trưởng, nếu như ta nói ra, các ngươi khẳng định sẽ giật mình!”"),i("br"),i("br"),n._v("Hắn nâng trước mấy ngày, đến Hồng Viễn thành, đâu đâu cũng có mới viện trưởng tin tức, muốn không biết cũng khó khăn."),i("br"),i("br"),n._v("“Giật mình?” Ô Thiên Khung cùng Thẩm Bình Triều liếc mắt nhìn nhau: “Chẳng lẽ viện trưởng này rất nổi danh? Là tổng bộ phái xuống?”"),i("br"),i("br"),n._v("“Dĩ nhiên không phải, là danh sư học viện năm nay mới vừa vào học học viên, chỉ có hai mươi tuổi, gọi Trương Huyền!” Ngũ Nhiên nói."),i("br"),i("br"),n._v("“Mới vừa vào học học viên?”"),i("br"),i("br"),n._v("“Hai mươi tuổi? Ngươi xác định tin tức này là chính xác?”"),i("br"),i("br"),n._v("Hai vị viện trưởng đồng thời ngẩn ngơ, cảm thấy mình có nghe lầm hay không."),i("br"),i("br"),n._v("Hồng Viễn học viện đây là muốn điên, vẫn là vò đã mẻ không sợ rơi?"),i("br"),i("br"),n._v("Một người hai mươi tuổi gia hỏa, lông đều không có đâm đủ, khi học viện viện trưởng? Thật hay giả?"),i("br"),i("br"),n._v("“Đương nhiên là chính xác, không tin ngươi có thể đến Hồng Viễn thành tùy tiện hỏi thăm!” Ngũ Nhiên cười khổ một tiếng: “Hai ngươi không nên coi thường vị này chỉ có hai mươi tuổi viện trưởng, ta nghe qua hắn tất cả tin tức, đến bây giờ, đều cảm thấy có không thể tưởng tượng nổi...”"),i("br"),i("br"),n._v("“Không thể tưởng tượng nổi? Nói nghe một chút!”"),i("br"),i("br"),n._v("Ô Thiên Khung nhịn không được nói."),i("br"),i("br"),n._v("“Ô viện trưởng, năm đó kế vị thời điểm, cũng xin qua Tiên Tổ a? Không biết nhận được bao nhiêu Tiên Tổ công nhận?” Ngũ Nhiên nhìn qua."),i("br"),i("br"),n._v("“Ta bất tài, chỉ lấy được 45% Tiên Tổ công nhận, tại Vân Hư học viện lịch đại hơn một trăm vị viện trưởng bên trong xếp hạng thứ bảy!”"),i("br"),i("br"),n._v("Ngoài miệng nói bất tài, trên thực tế mang theo đầy người ngạo khí."),i("br"),i("br"),n._v("Hơn một trăm vị Tiên Tổ, đạt tới 45% công nhận, nói rõ đã vượt qua hơn bốn mươi người thừa nhận thân phận của hắn, lịch đại bên trong đều được cho mạnh lớn."),i("br"),i("br"),n._v("Dù sao, chỉ cần có hai mươi phần trăm công nhận, coi như thông qua, hắn không sai biệt lắm đã tăng gấp đôi."),i("br"),i("br"),n._v("“Thẩm viện trưởng đâu?” Ngũ Nhiên nhìn về phía một cái khác."),i("br"),i("br"),n._v("“Ta chỉ lấy được 37% công nhận, tại La Thanh học viện lịch đại viện trưởng bên trong, còn không có đi vào mười vị trí đầu... Hổ thẹn!” Thẩm Bình Triều nói."),i("br"),i("br"),n._v("“45%, 37%... Đối lịch đại Tiên Tổ tới nói, cũng không tính yếu, nhưng đối với Hồng Viễn học viện vị này tân nhiệm viện trưởng tới nói, quả thực có thể nói một tiếng xấu hổ!”"),i("br"),i("br"),n._v("Ngũ Nhiên lắc đầu, ánh mắt lộ ra không thể tin được vẻ, dường như biết đến tin tức, quá mức rung động, đến bây giờ đều không thể tiêu hóa."),i("br"),i("br"),n._v("“Ngươi ý tứ... Chúng ta không bằng hắn?”"),i("br"),i("br"),n._v("Hai đại viện trưởng cau mày."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-932"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-934"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);