(window.webpackJsonp=window.webpackJsonp||[]).push([[2230],{2585:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-930-lam-chien-giang-bai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-930-lam-chien-giang-bai"}},[n._v("#")]),n._v(" Chương 930: Lâm chiến giảng bài")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 930: Lâm chiến giảng bài"),i("br"),i("br"),n._v("“Như thế!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Muốn thực lực mạnh hơn, nhất định phải trải qua sống chết."),i("br"),i("br"),n._v("Không có có thời khắc sinh tử sợ hãi sợ, tâm cảnh sẽ rất khó đột phá."),i("br"),i("br"),n._v("Hắn thời gian tu luyện mặc dù không dài, nhưng có thể có thực lực bây giờ, cũng là đã trải qua vô số sống chết cùng nguy hiểm mới đổi lấy, bất kỳ người nào thực lực đều khó có khả năng giải quyết trong chốc lát, không có trả giá, liền không công nhận được."),i("br"),i("br"),n._v("Danh sư bản thân sức chiến đấu liền mạnh, Chiến sư tự nhiên càng thêm đáng sợ, mà muốn lấy được thực lực như thế, cần phải bỏ ra tâm huyết, nhất định là thường nhân gấp mấy lần."),i("br"),i("br"),n._v("Tựa như hắn, vì xung kích cấp bậc cao hơn nguyên thai, gần hai tháng, mới từ Bán Thánh sơ kỳ, tấn thăng đến đỉnh phong nếu là đổi lại tu luyện bình thường Thiên Đạo công pháp lời nói, hiện tại chỉ sợ đã là cường giả Thánh vực."),i("br"),i("br"),n._v("Đây chính là trả giá cùng vất vả!"),i("br"),i("br"),n._v("“Không biết Chiến sư đường như thế nào khảo hạch? Ba vị viện trưởng có thể nghe nói tin tức?” Trương Huyền hỏi."),i("br"),i("br"),n._v("“Cụ thể quy tắc còn không rõ lắm, có điều, bình thường đều là mỗi bên học viện tiến hành tỷ thí, bài danh trước một trăm, tự nhiên sẽ trúng tuyển!” Ô Thiên Khung nói."),i("br"),i("br"),n._v("Mặc dù mỗi lần tuyển chọn quy tắc cũng không quá tương đồng, nhưng Chiến sư liền một cái yêu cầu, cái kia chính là thực lực mạnh!"),i("br"),i("br"),n._v("Bất kể dùng sao thủ đoạn, chỉ cần cùng cấp bậc, tại không mượn bất kỳ pháp bảo nào, linh thú dưới tình huống, đem đối thủ chiến thắng, chính là người thành công."),i("br"),i("br"),n._v("“Luận võ?”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Cái này đích xác là rất trực quan trực tiếp nhất tuyển chọn phương pháp, cũng có thể khiến người ta tâm phục khẩu phục, khuyết điểm duy nhất chính là cùng Thẩm Bình Triều nói đồng dạng, dính dáng sống chết."),i("br"),i("br"),n._v("Dưới loại tình huống này, một khi dính dáng dùng hết toàn lực, lực lượng không dễ khống chế, tỷ số chết tất nhiên tăng nhiều."),i("br"),i("br"),n._v("“Đúng vậy a, đúng, dù sao hiện tại Chiến sư đường người còn chưa tới, không bằng, chúng ta trước hết để cho bốn đại học viện tham gia tuyển chọn học viên gặp mặt một lần, lẫn nhau biểu diễn một điểm, cũng tốt trước thời hạn làm chuẩn bị, miễn phải tỷ thí thời điểm luống cuống tay chân!”"),i("br"),i("br"),n._v("Nhớ tới cái gì, Ô Thiên Khung cười nhìn qua."),i("br"),i("br"),n._v("“Không sai, để bọn hắn trước biểu diễn một điểm! Đối từng người thực lực, cũng có cái lý giải.”"),i("br"),i("br"),n._v("“Danh sư tầm đó, muốn nhiều giao lưu, mới có thể đi vào bước, giậm chân tại chỗ, sẽ chỉ làm người trở nên tầm nhìn hạn hẹp!”"),i("br"),i("br"),n._v("Thẩm Bình Triều cùng Ngũ Nhiên đồng thời gật đầu."),i("br"),i("br"),n._v("“Cũng tốt!” Bốn đại học viện ba cái đồng ý, Trương Huyền cũng không tiện cự tuyệt."),i("br"),i("br"),n._v("Lại nói, hắn cũng muốn nhìn một chút đối phương học viên thực lực, tốt để học viên của mình có chuẩn bị tâm lý."),i("br"),i("br"),n._v("Nghe được đáp ứng, chư vị viện trưởng phân phó một câu, chỉ chốc lát, theo phi chu bên trong đi ra mấy trăm vị học viên, theo Hợp Linh cảnh đến Tằm Phong cảnh, mỗi cái cấp bậc đều có."),i("br"),i("br"),n._v("Cùng Hồng Viễn học viện đồng dạng, mỗi cái cấp bậc, mỗi cái học viện đều tuyển chọn hai mươi người, năm cái niên cấp, cũng chính là một trăm người."),i("br"),i("br"),n._v("Ba đại học viện, 300 tuổi trẻ tài tuấn, đồng loạt đi tới, cho người ta một loại không giống khí thế."),i("br"),i("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, Trương Huyền hướng những học viên này nhìn sang."),i("br"),i("br"),n._v("Mỗi một cái đều cơ bắp chặt chẽ, thật khí hùng hồn, tính toán là cùng cấp bậc bên trong người nổi bật."),i("br"),i("br"),n._v("“Mi trưởng lão, đem học sinh của chúng ta, cũng kêu đi ra đi!”"),i("br"),i("br"),n._v("Trương Huyền phân phó."),i("br"),i("br"),n._v("Mi trưởng lão lên tiếng, không lâu sau, Hồng Viễn học viện tuyển ra một trăm vị học sinh, cũng đi ra."),i("br"),i("br"),n._v("Đây là hắn lần thứ nhất nhìn thấy những học viên này, đối chiếu một cái cái khác ba đại học viện, nhịn không được lắc đầu."),i("br"),i("br"),n._v("Mặc dù cấp bậc tương đồng, tuổi tác cũng không kém nhiều, nhưng bất kể là khí thế, chân khí độ hùng hậu, tựa hồ cũng so với đối phương muốn yếu một ít."),i("br"),i("br"),n._v("[ ngantruyen.com"),i("br"),n._v("]  Thật muốn chiến đấu, chỉ sợ có thể thông qua tuyển chọn, ít đến thương cảm."),i("br"),i("br"),n._v("Khó trách thập đại trường lão liền như vậy thịnh hội, đều có thể quên mất, còn cần Kim Nguyên đỉnh tới nhắc nhở, xem ra Chiến sư tuyển chọn, đối Hồng Viễn học viện tới nói, quả thực không tính là cái đại sự gì, trái lại, còn rất mất mặt, không có người vui lòng nhớ tới."),i("br"),i("br"),n._v("Dần dà, cũng liền quên."),i("br"),i("br"),n._v("“Tại hạ Vân Hư học viện năm nhất học sinh, Ngụy Xương!”"),i("br"),i("br"),n._v("Tham gia tuyển chọn bốn trăm học viên đi tới đài diễn võ trước ngồi xuống, Vân Hư học viện một người học viên nhảy lên đài cao, hai tay chấn động, một cỗ cường đại khí tức phun ra ngoài, tất cả huyệt đạo, giống như là thổi lên cái còi, phát ra nghẹn ngào thanh âm."),i("br"),i("br"),n._v("Mặc dù chỉ có Hợp Linh cảnh tu vi, nhưng cho người ta một loại thẳng phá thương khung cảm giác."),i("br"),i("br"),n._v("“Thật mạnh!”"),i("br"),i("br"),n._v("“Đúng vậy a, loại thực lực này, năm thứ hai chỉ sợ đều có rất ít người có thể đủ thắng quá?”"),i("br"),i("br"),n._v("“Ta gặp gỡ, khẳng định không phải là đối thủ”"),i("br"),i("br"),n._v("“Quá lợi hại, xếp hàng thứ nhất Vân Hư học viện, quả thực so ra kém!”"),i("br"),i("br"),n._v("Nhìn thấy vị này Ngụy Xương biểu hiện ra thực lực, dưới đài năm nhất học viên, nguyên một đám vẻ mặt nghiêm túc."),i("br"),i("br"),n._v("Đồng dạng là Hợp Linh cảnh, nhưng là thực lực của đối phương, rõ ràng mạnh hơn bọn họ quá nhiều, đừng nói năm nhất tân sinh, coi như năm thứ hai học viên, chỉ sợ cũng không là đối thủ."),i("br"),i("br"),n._v("Vừa ra tay, liền đem bọn hắn chấn nhiếp rồi."),i("br"),i("br"),n._v("Trương Huyền mày nhăn lại."),i("br"),i("br"),n._v("Đối phương cái này biểu hiện ra, mặc dù tìm không ra khuyết điểm, nhưng rất dễ dàng cho học viên của mình tạo thành áp lực tâm lý."),i("br"),i("br"),n._v("Xem ra vừa rồi ở trong tay chính mình bị thiệt thòi, muốn dùng học sinh cứu danh dự."),i("br"),i("br"),n._v("“Tại hạ La Thanh học viện năm nhất học sinh,”"),i("br"),n._v(" “Tại hạ là Thanh Trúc học viên năm nhất học sinh”"),i("br"),i("br"),n._v("Rất nhanh, ba đại học viện học sinh nhao nhao nhảy lên đài cao, những học viên này, đều chỉ có Hợp Linh cảnh tu vi, sức chiến đấu nhưng cực kỳ cường đại."),i("br"),i("br"),n._v("Có mấy vị, càng là đặc thù huyết thống, lực lượng kích hoạt về sau, chiến lực gấp bội, so với Hợp Linh cảnh thời kỳ Vương Dĩnh đám người, đều không hề yếu."),i("br"),i("br"),n._v("Trái lại, phía bên mình học viên còn kém rất nhiều, sức chiến đấu nhỏ yếu không nói, đối võ kỹ lý giải, cũng kém xa tít tắp, thật muốn chiến đấu, khó mà chiến thắng ngược lại là thứ nhì, làm không cẩn thận còn có thể trọng thương."),i("br"),i("br"),n._v("Năm nhất tuyển ra hai mươi người, cũng liền miễn cưỡng có một, hai cái thực lực cũng tạm được, cái khác, đều hoàn toàn không có khả năng so sánh."),i("br"),i("br"),n._v("Cũng khó trách, những người này tuyển ra, ít nhất phải ba tháng trở lên, trên đường đi, càng là từ thất tinh danh sư tự mình chỉ điểm, sức chiến đấu muốn không tăng lên cũng khó khăn."),i("br"),i("br"),n._v("Mà phía bên mình, vừa mới theo Hiên Viên đế quốc trở về, những học sinh này còn là lần đầu tiên nhìn thấy, chỉ điểm đều không có chỉ điểm qua, làm sao có thể so ra mà vượt?"),i("br"),i("br"),n._v("Coi như Triệu Bính Tuất, Mi trưởng lão đám người tự thân đi làm, có thể nhận hạn chế ánh mắt của bọn hắn cùng kiến thức, cùng thất tinh danh sư chỉ điểm vẫn là không cách nào sánh được."),i("br"),i("br"),n._v("“Này làm sao đánh?”"),i("br"),i("br"),n._v("Xoa xoa mi tâm."),i("br"),i("br"),n._v("Không cần ra tay, chỉ bằng vào so sánh, liền đem học sinh của mình, lòng tin đả kích không còn, chân chính tỷ thí, khẳng định cũng không chống được mấy chiêu."),i("br"),i("br"),n._v("Ngay sau đó năm thứ hai, năm thứ ba, năm thứ tư, lớp năm học sinh biểu hiện ra, Hồng Viễn học viện biểu hiện càng thêm không vừa ý người."),i("br"),i("br"),n._v("Vân Hư học viện mạnh nhất, hai mươi cái học sinh, hầu như từng cái đều có thể vượt cấp chiến đấu, căn bản không phải bọn họ những học sinh này có thể sánh ngang."),i("br"),i("br"),n._v("“Nếu là Tiết Chân Dương, Long Thương Nguyệt bọn họ không đột phá là được”"),i("br"),i("br"),n._v("Mấy vị này chuyên môn tiếp thụ qua chỉ điểm của hắn, nhất là trải qua Ngô Hư, Lộc Thành sự tình về sau, sức chiến đấu gia tăng thật lớn, từ bọn họ xuất thủ, lớp năm có lẽ còn có thể tranh thủ mấy cái danh ngạch đáng tiếc, bọn họ đã đột phá đến Bán Thánh, mất đi tư cách."),i("br"),i("br"),n._v("Mặc dù tại Huyền Huyền hội cũng chuyên môn dạy qua khóa, nhưng bọn hắn năm nay mới vừa vào trường học, điểm xuất phát quá thấp, đạt tới Hợp Linh cảnh cũng không nhiều, cùng tại học viện chờ đợi nhiều năm lão sinh so, vẫn là chênh lệch không ít."),i("br"),i("br"),n._v("Cơ bản đều không có thông qua tuyển chọn."),i("br"),i("br"),n._v("“Trương viện trưởng, học sinh của các ngươi, thoạt nhìn không quá lạc quan!”"),i("br"),i("br"),n._v("Đem biểu hiện ra nhìn một lần, Ngũ Nhiên nhíu nhíu mày."),i("br"),i("br"),n._v("Thân là thất tinh danh sư, nhãn lực siêu quần, tự nhiên đó có thể thấy được, Hồng Viễn học viện học sinh, so với ba người bọn hắn, rõ ràng hơi yếu."),i("br"),i("br"),n._v("Nằm trong loại trạng thái này, có thể lấy được thứ tự, thực sự quá khó khăn."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền lắc đầu, nhìn về phía Ô Thiên Khung đám người: “Đã Chiến sư đường người còn chưa tới, ta có thể hay không thừa dịp hiện tại thời gian rảnh rỗi, cho những học sinh này, nói chút kỹ xảo chiến đấu?”"),i("br"),i("br"),n._v("“Giảng bài? Hiện tại sao?”"),i("br"),i("br"),n._v("Nghe được hắn, Ô Thiên Khung đám người sững sờ."),i("br"),i("br"),n._v('Mặc dù lâm trận mới mài gươm sẽ có hiệu quả nhất định, có thể căn cứ tin tức, Chiến sư đường người, trong vòng một canh giờ liền sẽ đi tới, giờ phút này nói lại khóa, sẽ không có cái gì dùng đi!"'),i("br"),i("br"),n._v("“Đúng!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Nhìn thấy những học sinh này tình huống, không làm cải biến lời nói, đừng nói chỉ tuyển một trăm cái, liền xem như hai trăm, 300, có thể thông qua cũng không có mấy cái."),i("br"),i("br"),n._v("Kế trước mắt, chỉ có thể nhanh cho bọn hắn giảng giải một chút chiến đấu kỹ xảo, để hắn mau chóng lĩnh ngộ."),i("br"),i("br"),n._v("“Trương viện trưởng, muốn cho mình học viện học sinh chỉ điểm, tự đều có thể, chỉ bất quá tuyển chọn liền muốn bắt đầu, hiện tại học tập lời nói, ta sợ ngược lại sẽ để hắn tâm tính dao động, thực lực không tiến bộ, còn có thể lui về phía sau!”"),i("br"),i("br"),n._v("Chần chờ một chút, Ngũ Nhiên an ủi nói."),i("br"),i("br"),n._v("Có thể theo tất cả học sinh bên trong tuyển ra, nói rõ nhất định là thiên tài tu luyện, đã nắm giữ thuộc về mình chiến đấu hệ thống."),i("br"),i("br"),n._v("Hiện đang giảng bài, để hắn sửa lại, một khi không thành công, rất dễ dàng xuất hiện Tứ Bất Tượng cục diện, không những không có tiến bộ, ngược lại thực lực hạ thấp."),i("br"),i("br"),n._v("“Ta biết, vẫn là thử một chút đi!”"),i("br"),i("br"),n._v("Biết đối phương là hảo ý, Trương Huyền cười cười, để Mi trưởng lão đem tham gia tuyển chọn một trăm vị học viên tập trung lại, bốn phía hạ một cái cách âm cấm chế, cái này mới nhìn lại."),i("br"),i("br"),n._v("“Chư vị có hay không không muốn trở thành Chiến sư?”"),i("br"),i("br"),n._v("Nhìn quanh một vòng, Trương Huyền nhìn sang."),i("br"),i("br"),n._v("Chiến sư tuy tốt, sức chiến đấu mạnh, nhưng cũng không ít người cũng không nguyện ý đi làm."),i("br"),i("br"),n._v("“Không có!”"),i("br"),i("br"),n._v("Mọi người đồng thời lắc đầu."),i("br"),i("br"),n._v("Đã thông qua tuyển chọn, khẳng định đã là nghĩ kỹ."),i("br"),i("br"),n._v("“Đều muốn làm Chiến sư, cũng chính là đều hi vọng lực chiến đấu của mình càng ngày càng mạnh.”"),i("br"),i("br"),n._v("Thấy không có người không nguyện ý, Trương Huyền hai mắt nheo lại, nhìn lại: “Vậy thì tốt, ta hiện tại cho các ngươi giảng giải nhanh chóng tăng lên võ kỹ kỹ xảo, cùng đối với chiến đấu lĩnh ngộ! Hy vọng có thể nghiêm túc học tập!”"),i("br"),i("br"),n._v("Bây giờ muốn để tu vi trở nên càng thêm hùng hậu, khẳng định là không còn kịp rồi, duy nhất phương pháp nâng cao sức chiến đấu chính là đề cao đối võ kỹ lý giải, cùng lúc chiến đấu ứng biến."),i("br"),i("br"),n._v("“Đúng!”"),i("br"),i("br"),n._v("Nghe được viện trưởng muốn cho bọn hắn tự mình giảng bài, đại bộ phận học viên con mắt tỏa ánh sáng."),i("br"),i("br"),n._v("Vốn nhìn thấy học viện khác học viên thực lực, đánh mất một bộ phận lòng tin, cũng lần nữa bốc cháy lên."),i("br"),i("br"),n._v("Viện trưởng kế vị đại điển thời điểm nói qua một bài giảng, tạo thành oanh động, đến bây giờ đều còn nhớ, lúc ấy, là đối toàn trường thầy trò cùng một chỗ nói, không có tính nhắm vào, hiệu quả liền lợi hại như vậy"),i("br"),i("br"),n._v("Hiện tại nhằm vào giảng giải, lại sẽ có loại nào hiệu quả?"),i("br"),i("br"),n._v("Không cần nghĩ đều để người kích động."),i("br"),i("br"),n._v("“Võ kỹ mục đích là vì tốt hơn phát huy chân khí trong cơ thể, để lực lượng càng thêm thuần túy, bởi vậy, muốn để võ kỹ mạnh hơn, đầu tiên muốn có, một viên thuần túy tâm, một viên dũng cảm tiến tới, không cam lòng nhận thua tâm cảnh”"),i("br"),i("br"),n._v("Thấy ánh mắt mọi người sáng ngời, toàn đều chuẩn bị kỹ càng, Trương Huyền cũng không do dự, thanh âm nhàn nhạt, lập tức vang lên."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-938"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-940"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);