(window.webpackJsonp=window.webpackJsonp||[]).push([[2231],{2586:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-85-hanh-hung-chu-hong-trung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-85-hanh-hung-chu-hong-trung"}},[n._v("#")]),n._v(" Chương 85: Hành hung Chu Hồng (trung)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Chân khí truyền âm!"),t("br"),t("br"),n._v(" “Ngu ngốc, sử dụng ta vừa nãy dạy ngươi ba chiêu! Phía trước hai chiêu, ngược lại đánh một lần, tay trái đổi thành tay phải, chân trái đổi thành chân phải! Một chiêu cuối cùng bất biến, nắm đấm biến thành ngón tay, lại hướng phía dưới công kích ba tấc, nhớ kỹ, nhất định là ba tấc, không thể nhiều cũng không có thể thiếu!” "),t("br"),t("br"),n._v(" Chân khí truyền âm, chí ít cần đạt được võ giả ngũ trọng có khả năng sử dụng, Trương lão sư tùy tiện truyền tới, để Viên Đào toàn thân không nhịn được chấn động, trong lòng không tên tin tưởng mấy phần."),t("br"),t("br"),n._v(" “Liều mạng! Ngược lại đều như vậy!” "),t("br"),t("br"),n._v(" Thời gian không kịp ngẫm nghĩ nữa, Viên Đào lấy ngựa chết làm ngựa sống, coi như không như vậy cũng không có biện pháp nào khác, lúc này không chút do dự nào, trực tiếp dựa theo âm thanh dặn dò làm."),t("br"),t("br"),n._v(" Làm như người tu luyện, tay trái động tác đổi thành tay phải, vô cùng đơn giản!"),t("br"),t("br"),n._v(" Vốn là tay trái đưa tay về phía trước, đổi thành tay phải, để Chu Hồng không nhịn được sững sờ, ngẹo đầu tránh thoát công kích, một chưởng nghênh tiếp."),t("br"),t("br"),n._v(" Bất quá, hắn này chưởng còn chưa tới đến Viên Đào trước mặt, liền thấy người sau theo bên phải di động đến bên trái, trùng hợp tránh thoát sự công kích của hắn."),t("br"),t("br"),n._v(" “Nguy rồi, hắn luyện được là ngược lại” "),t("br"),t("br"),n._v(" Chu Hồng lúc này nơi đó không hiểu xảy ra chuyện gì, vội vàng thu hồi thủ chưởng niêm phong lại mặt mũi."),t("br"),t("br"),n._v(" Vừa nãy chiêu thứ ba, là hướng phía dưới đánh, nếu chiêu số đều là ngược lại, khẳng định là hướng lên trên đánh mặt của hắn, bởi vì Trương Huyền trước đã nói, muốn đem hắn đánh thành đầu heo."),t("br"),t("br"),n._v(" Hắn phản ứng rất nhanh, cũng rất cấp tốc, chỉ tiếc Viên Đào chiêu thứ ba công kích, căn bản không ngược lại, vẫn như cũ là hướng phía dưới, thậm chí, còn hướng phía dưới di động ba tấc."),t("br"),t("br"),n._v(" Phốc!"),t("br"),t("br"),n._v(" Chu Hồng còn không phản ứng lại, lại đột nhiên cảm thấy bên hông tê rần, tiếp theo toàn thân tê rần."),t("br"),t("br"),n._v(" “Nguy rồi, hắn đánh trúng của ta mệnh môn hắn làm sao biết của ta mệnh môn” "),t("br"),t("br"),n._v(" Chu Hồng sắc mặt một hồi trở nên trắng bệch."),t("br"),t("br"),n._v(" Hắn tu luyện mệnh môn tại bên hông, bình thường ẩn giấu rất tốt, vừa nãy Viên Đào liên tục dùng ngược lại hai lần chiêu số, để hắn đến thăm bảo vệ mặt mũi, nằm mơ đều không nghĩ tới, đối phương mục tiêu thực sự ở đây."),t("br"),t("br"),n._v(" Mệnh môn bị đánh trúng, cả người như là bị điểm huyệt giống nhau, trong thời gian ngắn có chút cứng ngắc."),t("br"),t("br"),n._v(" “Cơ hội tốt!” "),t("br"),t("br"),n._v(" Nhìn thấy Chu Hồng thân thể một cái cứng ngắc, Viên Đào có ngốc cũng biết bị đánh trúng mệnh môn, hưng phấn một tiếng gầm rú, vọt tới."),t("br"),t("br"),n._v(" Loại này cơ hội tốt, không cố gắng lợi dụng đó mới là kẻ ngu si."),t("br"),t("br"),n._v(" Oành oành oành oành!"),t("br"),t("br"),n._v(" Vốn là không thể tới gần người phố phường lưu manh chiêu số, giờ khắc này mưa lớn giống như rơi vào Chu Hồng trên mặt, người sau còn không khôi phục như cũ, liền mắt tối sầm lại, bị Viên Đào thô to nắm đấm, mạnh mẽ nện trên mặt đất."),t("br"),t("br"),n._v(" Lúc này, nhiều năm đánh nhau kinh nghiệm chiếm thượng phong, không có quá nhiều dừng lại, vừa tung người, thân thể mập mạp liền mạnh mẽ ngồi ở đối phương lồng ngực, hai cái nắm đấm đối với mặt của đối phương trứng mạnh mẽ đập xuống."),t("br"),t("br"),n._v(" “Ngươi” "),t("br"),t("br"),n._v(" Theo mất cảm giác trạng thái khôi phục như cũ, nhìn thấy bị tên béo đặt ở dưới thân, liên tục làm mất mặt, Chu Hồng phiền muộn sắp thổ huyết."),t("br"),t("br"),n._v(" Hắn là Lục Tầm lão sư môn hạ cao đồ, nhập học sát hạch người thứ bốn cường giả siêu cấp, hiện tại bị nhập học sát hạch đếm ngược đệ nhất gia hỏa, đè lên đánh mặt"),t("br"),t("br"),n._v(" Đều cảm thấy sắp điên rồi."),t("br"),t("br"),n._v(" “Cút ngay” "),t("br"),t("br"),n._v(" Muốn đem đối phương đẩy ra, nhưng thân thể đối phương thực sự quá nặng, giống như núi nhỏ, dụng hết toàn lực, đều không nhúc nhích mảy may, hơn nữa nắm đấm lại y như tảng đá cứng rắn, một hồi hạ liên tục hạ xuống, nói còn chưa dứt lời, liền cảm thấy mặt xưng phù giống như bánh màn thầu, hàm răng cũng rơi mất vài cái."),t("br"),t("br"),n._v(' "Thật hay giả?'),t("br"),t("br"),n._v(" Triệu Nhã mấy người từng cái từng cái hai mặt nhìn nhau, tất cả đều xem choáng váng."),t("br"),t("br"),n._v(" Chu Hồng thực lực, vừa nãy từng thử, liền bọn họ đều không phải là đối thủ, khó có thể ngăn cản, Viên Đào là trong bọn họ yếu nhất, nhanh như vậy liền thắng"),t("br"),t("br"),n._v(" Khi nào, biến mạnh như vậy?"),t("br"),t("br"),n._v(" Mấu chốt nhất chính là lão sư ba chiêu quyền pháp dĩ nhiên thật sự có dùng?"),t("br"),t("br"),n._v(" “Được rồi! Viên Đào, ta mới vừa nói qua, đánh thành đầu heo là được, không muốn đánh chết!” "),t("br"),n._v("  Thấy bị Viên Đào cuồng đánh bên dưới, Chu Hồng đã không có hình người, Trương Huyền vội vã khuyên can."),t("br"),t("br"),n._v(" Bất kể nói thế nào, này đều là Lục Tầm phái tới sứ giả, ý tứ ý tứ là được."),t("br"),t("br"),n._v(" Thật đánh không nhúc nhích, nhiều lúng túng a!"),t("br"),t("br"),n._v(" Lão sư khác, dù cho trưởng lão, muốn muốn chỉ điểm người khác đối kháng, đều cần cân nhắc rất lâu, chung quy, chiến đấu chớp mắt biến hóa, không thể nhất thành bất biến! Nhưng Trương Huyền không giống nhau, nắm giữ Thiên Đạo thư viện, sớm biết đối phương sẽ cái gì võ kỹ, làm ra phản ứng gì, biết chiêu số trung khuyết điểm liền có thể sớm lập ra phương án!"),t("br"),t("br"),n._v(" Coi như Viên Đào thực lực so Chu Hồng thấp không ít, sớm đã biết mệnh môn, chuẩn bị sẵn sàng, đem hắn đánh bại còn là vô cùng đơn giản."),t("br"),t("br"),n._v(" “Vâng!” "),t("br"),t("br"),n._v(" Lại liên tục đánh mấy quyền, cảm thấy khí ra gần như rồi, Viên Đào lúc này mới đứng dậy."),t("br"),t("br"),n._v(" Giờ khắc này Chu Hồng đã cùng đầu heo không khác nhau gì cả, hai mắt sưng đỏ, khuôn mặt sưng, căn bản không nhìn ra vừa nãy nửa phần dáng vẻ."),t("br"),t("br"),n._v(" “Ngươi đê tiện!” "),t("br"),t("br"),n._v(" Giẫy giụa đứng dậy, nhìn về phía Viên Đào, Chu Hồng nghiến răng nghiến lợi."),t("br"),t("br"),n._v(" Dưới cái nhìn của hắn, cái tên mập mạp này khẳng định là giả vờ giả vịt, có thực lực lại cố ý trang không đỡ nổi một đòn, để cho mình phớt lờ phía sau, lúc này mới đánh lén thủ thắng!"),t("br"),t("br"),n._v(" Đáng ghét!"),t("br"),t("br"),n._v(" “Đê tiện? Ta liền đê tiện làm sao? Ngươi muốn lại tới một lần nữa?” Mới mặc kệ cái gì đê tiện không đê tiện, Viên Đào giờ khắc này đang chìm đắm tại cuồng ẩu đối phương vui vẻ bên trong, tràn đầy vô sỉ nhìn sang."),t("br"),t("br"),n._v(" “Ngươi” "),t("br"),t("br"),n._v(" Thấy hắn vẻ không có gì sợ, Chu Hồng sắc mặt khó coi, không dám đáp ứng."),t("br"),t("br"),n._v(" Cái tên mập mạp này rõ ràng đã biết mệnh môn của hắn, vạn nhất lại tới một lần nữa, tương tự bị đánh a!"),t("br"),t("br"),n._v(" Đang tại xoắn xuýt, liền nghe đến phía trước Trương lão sư lên tiếng."),t("br"),t("br"),n._v(" “Được rồi, đều để người ta đánh thành như vậy, Viên Đào, không cần động thủ nữa nữa!” Trương Huyền một mặt nghiêm túc."),t("br"),t("br"),n._v(" “Tốt” nghe nói như thế, Chu Hồng thở phào nhẹ nhõm, đang muốn lại nói hai câu nói mang tính hình thức, sau đó xoay người rời đi, liền nghe đến Trương lão sư tiếp tục nói: “Trịnh Dương, ngươi vừa nãy không phải muốn ra tay thử xem à? Đi thôi!” "),t("br"),t("br"),n._v(" “A?” "),t("br"),t("br"),n._v(" Chu Hồng sững sờ."),t("br"),t("br"),n._v(" Không phải là để ta rời đi sao? Làm sao còn muốn cho người khác tiếp tục thử xem?"),t("br"),t("br"),n._v(" Sửng sốt một chút, lập tức trong mắt loé ra một đạo tàn nhẫn."),t("br"),t("br"),n._v(" Đánh một hồi cũng tốt."),t("br"),t("br"),n._v(" Vừa nãy cái này Trịnh Dương cái thứ nhất xông lại, bị một chân đạp bay, rất rõ ràng thực lực không ra sao, hơn nữa, có Viên Đào kinh nghiệm, chỉ cần phòng ngừa đối phương đem ba chiêu ngược lại sử dụng, chắc chắn sẽ không có sự tình!"),t("br"),t("br"),n._v(" Lại nói, mới vừa bị đánh như vậy thảm, nếu như không đánh trở về, trở lại làm sao bàn giao?"),t("br"),t("br"),n._v(" Nghĩ tới đây, tuy rằng nghi hoặc, Chu Hồng vẫn là cắn răng một cái, cười lạnh một tiếng: “Đến đây đi!” "),t("br"),t("br"),n._v(" “Được!” Trịnh Dương đi lên phía trước."),t("br"),t("br"),n._v(" Oành oành oành!"),t("br"),t("br"),n._v(" Hai người giao thủ với nhau, mới vừa rồi bị một chân đạp lăn Trịnh Dương giờ khắc này không biết ăn cái gì đại bổ dược giống nhau, chung quanh di động, căn bản không cùng hắn chính diện đối chiến."),t("br"),t("br"),n._v(" “Có bản lĩnh, theo ta quang minh chính đại đánh một trận!” Chu Hồng quát."),t("br"),t("br"),n._v(" Đổi làm bình thường, hắn mặc kệ làm sao di động, đều có năng lực đuổi theo, cùng đem nó đánh bại, có thể vừa mới bị Viên Đào đánh rất thảm, trên người đau dữ dội, như vậy né tránh, muốn nắm lấy, hầu như không thể."),t("br"),t("br"),n._v(" “Tốt!” "),t("br"),t("br"),n._v(" Trịnh Dương một tiếng đáp ứng, tay trái đưa tay về phía trước, thân thể hướng bên phải loáng một cái, chiêu thứ nhất đang sử dụng, chiêu thứ hai ngược lại sử dụng, sau đó nhân dịp đối phương còn không phản ứng lại, chập chỉ làm kiếm, liền đối với Chu Hồng bên hông nhấn tới."),t("br"),t("br"),n._v(" Ps: Phiếu đề cử!!! Phiếu đề cử!!! Phiếu đề cử!!!!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-93"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-95"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);