(window.webpackJsonp=window.webpackJsonp||[]).push([[2232],{2588:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-931-chien-su-đuong-nguoi-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-931-chien-su-đuong-nguoi-toi"}},[n._v("#")]),n._v(" Chương 931: Chiến sư đường người tới!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 931: Chiến sư đường người tới!"),t("br"),t("br"),n._v("Chử Kiện là năm nhất tuyển ra học viên, hai năm trước, bị Hồng Trì đế quốc, cử đi đến nơi đây học tập."),t("br"),t("br"),n._v("Trải qua hơn hai năm hun đúc cùng tôi luyện, đã đạt đến Hợp Linh cảnh đỉnh phong, khoảng cách Kiều Thiên cảnh, chỉ có kém một bước."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, ba tháng trước liền có cơ hội đột phá, nhưng vì nghênh đón lần này Chiến sư tuyển chọn, một mực áp chế tu vi, Hợp Linh cảnh lực lượng bị hắn rèn luyện mượt mà như ý, hầu như đạt đến cảnh giới này đỉnh phong nhất."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, lần này học viện tuyển chọn, mới có thể trổ hết tài năng, nhận được hạng năm thành tích tốt."),t("br"),t("br"),n._v("Hắn chỉ là đế quốc không được sủng ái hoàng tử, có thể trở thành Chiến sư, liền có thể trổ hết tài năng, coi như không thể kế thừa hoàng vị, cũng tất nhiên hiển lộ tài năng."),t("br"),t("br"),n._v("Vốn cho rằng nhận được học viện hạng năm, tuyển chọn thi đấu bên trên, chỉ phải cố gắng, liền có cơ hội thành công."),t("br"),t("br"),n._v("Khi thấy cái khác ba cái học viện biểu hiện ra, trái tim nguội đi, cùng một cấp, hắn cùng thực lực của đối phương căn bản cũng không tại một cái cấp bậc bên trên."),t("br"),t("br"),n._v("Mặc dù không có giao đấu, nhưng thông qua đối phương toả ra lực lượng có thể rõ ràng phán đoán, một khi chiến đấu, thực lực của hắn khả năng liền trước bốn mười đều không xếp hạng tới."),t("br"),t("br"),n._v("Mỗi cái niên cấp chỉ tuyển tuyển trước 20 danh, bốn mươi căn bản không có chút nào hi vọng."),t("br"),t("br"),n._v("Tuy nói viện trưởng muốn cho bọn hắn giảng bài, chuyên môn giảng giải chiến đấu kỹ xảo, nhưng hắn không hề cho rằng, ngắn ngắn chưa tới một canh giờ thời gian, liền có thể để thực lực của mình cải biến."),t("br"),t("br"),n._v("Tu luyện là tích lũy quá trình, nhất là sức chiến đấu, căn bản không phải thời gian ngắn huấn luyện liền có thể cấp tốc tăng lên."),t("br"),t("br"),n._v("Coi như viện trưởng giảng giải tri thức, ảo diệu vô cùng, có thể để cho hắn có một ít tăng lên, nhưng đối mặt ba đại học viện cao thủ, đồng dạng sẽ thua không còn manh giáp."),t("br"),t("br"),n._v("Nhìn thấy thực lực của đối phương, tự tin đã bị triệt để đả kích."),t("br"),t("br"),n._v("Than thở một tiếng, nhìn về phía trước."),t("br"),t("br"),n._v("Chỉ thấy viện trưởng, một mặt hòa ái nhìn qua, ấm áp thanh âm, dường như tiên nhạc, tại vang lên bên tai, giải thích chiến đấu bên trong một chút kỹ xảo cùng võ kỹ vận dụng, để cho người ta chỉ nghe vài câu, liền kìm lòng không được chân khí nhảy lên, muốn phải đi theo học tập."),t("br"),t("br"),n._v("Nghe một hồi, Chử Kiện trong lòng bái phục."),t("br"),t("br"),n._v("Không thể không nói, viện trưởng đối võ kỹ cùng chiến đấu lý giải, đã đạt đến xuất thần nhập hóa cảnh giới, so với hắn chỗ nghe qua chỗ có danh sư, đều lợi hại hơn!"),t("br"),t("br"),n._v("Nhưng cũng tiếc thời gian quá ngắn, ngắn ngủi một canh giờ, căn bản không thay đổi được cái gì."),t("br"),t("br"),n._v("“Nếu là viện trưởng có thể trước thời hạn nửa tháng trở về, có lẽ còn có hy vọng chiến thắng”"),t("br"),t("br"),n._v("Than thở một tiếng."),t("br"),t("br"),n._v("Trước thời hạn nửa tháng, đem bọn hắn tập trung lại, giảng giải kỹ xảo chiến đấu, phối hợp pháp quyết tu luyện cùng thực chiến, có lẽ bọn họ thật có thể trong thời gian ngắn thoát thai hoán cốt, tu vi tăng nhiều."),t("br"),t("br"),n._v("Hiện đang tuyển chọn lập tức bắt đầu, nói lại còn để làm gì?"),t("br"),t("br"),n._v("Lắc đầu, trong lòng đang nghĩ đến có muốn nghe hay không, chỉ thấy cách đó không xa một cái học đệ, dường như lĩnh ngộ được cái gì, hai mắt tỏa ánh sáng, ngón tay hướng về phía trước một chút, một đạo kiếm mang lập tức ở đầu ngón tay hình thành, trực tiếp rơi trên mặt đất, đâm ra một cái lỗ thủng."),t("br"),t("br"),n._v("“Cái này đây là nhân kiếm hợp nhất?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Vị này học đệ, bọn họ trước đó đọ sức qua, đối kiếm thuật là có chút thiên phú, nhưng cùng hắn so, vẫn là kém rất lớn một đoạn, làm sao hắn còn chưa tới nhân kiếm hợp nhất, đối phương đi đầu đạt tới?"),t("br"),t("br"),n._v("Nhân kiếm hợp nhất, là kiếm pháp bên trong một cái cực kỳ trọng yếu cảnh giới, một khi đạt tới thực lực tuyệt đối sẽ lập tức bạo tăng, cái tên này lúc trước chỉ xếp hạng thứ mười, lĩnh ngộ loại cảnh giới này, có thể đoán được, lần nữa tỷ thí lời nói, năm nhất bên trong, ít nhất trước ba!"),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Đang đang khiếp sợ, còn không có hoàn toàn tiêu hóa, lại nghe được cách đó không xa, một hồi xương cốt, cơ bắp nhúc nhích thanh âm, tựa như sấm rền, chấn mặt đất đều có chút rất nhỏ lay động."),t("br"),t("br"),n._v("“Đây là Hổ Khiếu Kim Cương quyền tu luyện tới đại thành mới có thanh âm! Hôm qua ta cùng hắn lúc tỷ thí, không mới tu luyện đến nhập môn ư?”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Chử Kiện con mắt choáng váng."),t("br"),t("br"),n._v("Trong cơ thể phát ra âm thanh cái tên này, hắn cũng quen thuộc, năm nhất thi đấu, xếp hạng thứ mười bảy, hôm qua còn tới so tài, tu luyện võ kỹ Hổ Khiếu Kim Cương quyền, mới vừa vặn nhập môn, khoảng cách tinh thông cũng còn có khoảng cách nhất định"),t("br"),t("br"),n._v("Đối võ kỹ lý giải, có thể chia làm: Nhập môn, tinh thông, tiểu thành, đại thành, viên mãn như ý."),t("br"),t("br"),n._v("Làm sao thời gian nháy mắt đã đột phá ba cái đại cấp bậc, đạt đến đại thành?"),t("br"),t("br"),n._v("Thế này thì quá mức rồi!"),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Khiếp sợ vẫn như cũ không có kết thúc, cách đó không xa một thanh niên chân khí theo huyệt thái dương tuôn ra, trên đầu tạo thành một cái giống như thực chất quái điểu, bất cứ lúc nào cũng sẽ bay ra."),t("br"),t("br"),n._v("Chỉ nhìn một cái, liền hiểu được, đây cũng là một loại võ kỹ tu luyện tới đại thành tiêu chí."),t("br"),t("br"),n._v("Bành bành bành! Ba ba ba!"),t("br"),t("br"),n._v("Chung quanh, một hồi vang rền, cấm chế phạm vi bên trong, vô số chân khí bay loạn, dường như tất cả học viên, nghe viện trưởng giảng giải về sau, đều có chỗ lĩnh ngộ, nhao nhao đột phá."),t("br"),t("br"),n._v("“Viện trưởng khóa lợi hại như vậy?”"),t("br"),t("br"),n._v("Chử Kiện lúc này mới ý thức được vấn đề, rốt cuộc kìm nén không được, vội vàng ngăn chặn trong lòng rung động cùng không hiểu, cẩn thận nghe tới."),t("br"),t("br"),n._v("Không thể không nói, viện trưởng giảng giải mười phần đúng chỗ, từ cạn tới sâu, đem võ kỹ cùng kỹ xảo chiến đấu, kéo tơ gẩy kén đồng dạng hiện ra ở trước mắt mọi người."),t("br"),t("br"),n._v("Nghe hắn giảng giải, trước đó rất thần bí đồ vật, trở nên mười phần đơn giản, rất dễ dàng liền có thể hiểu được."),t("br"),t("br"),n._v("Chỉ nghe một hồi, liền như si như say, khó mà tự kềm chế."),t("br"),t("br"),n._v("Giờ mới hiểu được, trước đó tu luyện nhiều năm, hiểu nhiều năm võ kỹ, là cỡ nào nông cạn, cùng viện trưởng giảng giải so, buồn cười không đáng giá nhắc tới."),t("br"),t("br"),n._v("Cấm chế bên ngoài, không nhìn thấy tình huống bên trong, cũng nghe không được thanh âm, cũng không biết đã có rất nhiều người, đối võ kỹ lý giải, có đột phá."),t("br"),t("br"),n._v("Thấy viện trưởng lúc này lôi kéo tất cả học sinh, vì bọn họ giảng bài, Mi trưởng lão chỉ có một mặt choáng váng."),t("br"),n._v(" “Viện trưởng hiện tại bắt đầu giảng giải, có phải hay không có chút quá muộn?”"),t("br"),t("br"),n._v("Căn cứ hắn biết đến tin tức, Chiến sư đường người bất cứ lúc nào cũng sẽ đi tới, hiện tại lại giảng bài coi như ôm chân phật cũng quá muộn chút đi!"),t("br"),t("br"),n._v("“Là muộn” Triệu Bính Tuất lắc đầu."),t("br"),t("br"),n._v("“Thực lực của người tu luyện, là dựa vào vô số chiến đấu tích lũy mà thành, bằng vào lớp học học tập, không có khả năng tăng lên quá nhiều, viện trưởng làm như vậy, đoán chừng cũng là cái gì cũng có thể thử khi tuyệt vọng đi!”"),t("br"),t("br"),n._v("Tu Trường Thanh cũng nhẹ gật đầu."),t("br"),t("br"),n._v("Không phải bọn họ không tin viện trưởng, mà là học sinh năng lực phân tích có hạn, một cái canh giờ, coi như lão sư nói thiên hoa loạn trụy, lý giải không được, cũng vô dụng thôi!"),t("br"),t("br"),n._v("Thậm chí coi như hiểu, không thể dùng để thực chiến, cũng là uổng công."),t("br"),t("br"),n._v("Xem ra viện trưởng vẫn là tuổi còn rất trẻ, ý nghĩ quá ngây thơ rồi."),t("br"),t("br"),n._v("“Đúng vậy a! Chúng ta cũng hết cách rồi, chỉ có thể chờ đợi kết quả”"),t("br"),t("br"),n._v("Triệu Bính Tuất lắc đầu, không nói thêm lời, nhưng trong mắt vẻ lo lắng, lộ rõ trên mặt, căn bản không che giấu được."),t("br"),t("br"),n._v("Không chỉ thập đại trường lão đối viện trưởng không có có lòng tin."),t("br"),t("br"),n._v("Ô Thiên Khung, Thẩm Bình Triều, Ngũ Nhiên ba người, cũng toàn cũng nhịn không được lắc đầu."),t("br"),t("br"),n._v("Cho học sinh giảng bài, để bọn hắn tăng lên đối với chiến đấu lý giải là chuyện tốt, nhưng hiệu quả, cũng không phải là dễ dàng như vậy thấy hiệu quả."),t("br"),t("br"),n._v("Liên lụy đến học sinh thiên phú, học tập thái độ cùng rất nhiều vấn đề. Nếu quả thật nói mấy lớp, cũng làm người ta sức chiến đấu tăng nhiều, chẳng phải người người đều có thể trở thành Chiến sư đường cao thủ?"),t("br"),t("br"),n._v("Chiến sư cũng liền không có cường đại như vậy, để vô số danh sư vì đó phấn đấu!"),t("br"),t("br"),n._v("“Có thể là hắn không nguyện ý nhận thua đi!”"),t("br"),t("br"),n._v("Nhớ tới thân phận của Trương Huyền, Ngũ Nhiên cảm khái."),t("br"),t("br"),n._v("Vị này chính là liền Khổng sư sai lầm đều có thể chỉ ra tới siêu cấp thiên tài!"),t("br"),t("br"),n._v("Trong lòng kiêu ngạo có thể tưởng tượng được, nhìn thấy bản thân học sinh, so với bọn hắn kém nhiều như vậy, tất nhiên không chịu nổi, làm như vậy cũng liền có thể hiểu."),t("br"),t("br"),n._v("Nhưng kết quả, đã định trước chỉ có thất bại!"),t("br"),t("br"),n._v("Không đi quản ý nghĩ của mọi người, Trương Huyền đem bản thân lý giải Thiên Đạo võ kỹ, cùng kinh nghiệm chiến đấu, dùng rất tinh giản phương pháp kỹ càng truyền thụ, không có chút nào bảo lưu cùng che giấu."),t("br"),t("br"),n._v("Cùng đối Vương Dĩnh đám người giảng giải không khác chút nào, chân chính làm được, đại công vô tư."),t("br"),t("br"),n._v("“Chiến đấu cũng không phải là ai xuất chiêu nhanh ai liền chiếm cứ ưu thế, trọng yếu nhất chính là, rõ ràng hắn ra chiêu này mục đích, dụng ý, hiểu những này, đối chiêu mấy có dự phòng, mới có thể trước thời hạn dự phán cùng ứng đối”"),t("br"),t("br"),n._v("Trương Huyền thanh âm khoan thai, mang theo đặc thù vận luật."),t("br"),t("br"),n._v("Sư giả chi tâm phối hợp có thể so với thất tinh danh sư tâm cảnh khắc độ, để hắn giảng giải tri thức, học sinh nghe xong liền rõ ràng, một rõ ràng liền sẽ, một hồi liền tinh."),t("br"),t("br"),n._v("“Không được, linh khí không đủ”"),t("br"),t("br"),n._v("Nhìn trước mắt tất cả học sinh, không ít đã lĩnh ngộ giảng giải nội dung chính, chân khí trong cơ thể sôi trào, đối võ kỹ lý giải liên tục tăng lên, Trương Huyền chẳng những không có cao hứng, ngược lại mày nhăn lại."),t("br"),t("br"),n._v("Võ giả muốn đem võ kỹ dung hội quán thông, lĩnh ngộ là một mặt, chân khí thông suốt, cũng là một mặt."),t("br"),t("br"),n._v("Thật giống như rất nhiều võ kỹ tuyệt chiêu, đều có bản thân đặc biệt vận chuyển chân khí lộ tuyến, chân khí chưa hề vận chuyển qua, rất dễ dàng xuất hiện tắc nghẽn."),t("br"),t("br"),n._v("Đây cũng là rất nhiều võ kỹ, cũng không phải là vừa học liền biết nguyên nhân. Dù sao, không phải người nào đều có Thiên Đạo chân khí, bất kỳ cái gì kinh mạch đều có thể tùy ý không trở ngại."),t("br"),t("br"),n._v("Đã tu luyện võ kỹ, vận chuyển chân khí, tự nhiên đối với linh khí cũng có tiêu hao, bản thân giảng bài, để nhiều như vậy học sinh đồng thời đột phá, cấm chế phạm vi bên trong linh khí đã bị hấp thu sạch sẽ, không đủ sử dụng."),t("br"),t("br"),n._v("Tiếp tục nữa, không có linh khí bổ sung, rất dễ dàng gián đoạn bọn họ đối võ kỹ lĩnh ngộ."),t("br"),t("br"),n._v("Một khi gián đoạn, lại nghĩ tìm tới loại trạng thái này, sẽ rất khó."),t("br"),t("br"),n._v("Vậy thì cùng đốn ngộ đồng dạng, là không thể quấy nhiễu."),t("br"),t("br"),n._v("Híp mắt lại, không chút do dự, bàn tay lớn vồ một cái, địa mạch linh dịch theo đầu ngón tay phun ra ngoài, uốn lượn như giọt mưa rơi vào tất cả học sinh huyệt trên đường."),t("br"),t("br"),n._v("Mặc dù địa mạch linh dịch càng dùng càng ít, lúc này cũng không chiếu cố được nhiều như vậy, linh thạch thượng phẩm linh khí mặc dù càng thêm tinh thuần, nhưng hút thu lại, không có dễ dàng như vậy, hiệu quả kém xa tít tắp."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Linh dịch tự nhiên, tất cả đắm chìm trong đốn ngộ bên trong học viên, tinh thần đại chấn, đối võ kỹ lý giải càng ngày càng sâu, giam cầm kinh mạch cũng trong nháy mắt mở ra."),t("br"),t("br"),n._v("Linh dịch bên trong không chỉ có ẩn hàm hùng hậu linh khí, còn xen lẫn chút ít Thiên Đạo chân khí, nương theo linh dịch tiến vào thân thể, tự động xông phá trước đó tắc nghẽn."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm, đang muốn tiếp tục giảng giải, lông mày đột nhiên nhíu một cái, nhịn không được hướng về phía trước nhìn lại."),t("br"),t("br"),n._v("Chỉ thấy xanh thẳm dưới bầu trời, một đầu to lớn phi hành Thánh thú, cắt ra không khí, đi tới học viện bầu trời."),t("br"),t("br"),n._v("“Là Chiến sư đường người, lúc này mới nửa canh giờ”"),t("br"),t("br"),n._v("Vốn cho rằng có thể giảng giải một canh giờ, không nghĩ tới, mới nửa canh giờ người liền đến."),t("br"),t("br"),n._v("“Được rồi, bài học hôm nay liền đến nơi đây!”"),t("br"),t("br"),n._v("Ngừng giảng bài, thấy mọi người đều theo trong tu luyện tỉnh táo lại, Trương Huyền đưa tay xé đi cấm chế."),t("br"),t("br"),n._v("Chiến sư đường người đến, giảng bài tự nhiên không thể lại tiếp tục tiến hành tiếp."),t("br"),t("br"),n._v("Chiến sư tuyển chọn chính thức bắt đầu!"),t("br"),t("br"),n._v("(Canh [4] đến, chờ, đằng sau còn có!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-939"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-941"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);