(window.webpackJsonp=window.webpackJsonp||[]).push([[2235],{2589:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-934-tran-truc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-934-tran-truc"}},[n._v("#")]),n._v(" Chương 934: Trần Trúc")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 934: Trần Trúc (canh thứ nhất)"),i("br"),i("br"),n._v("Chử Kiện căn bản là nghĩ không ra, có thể đem đối phương đánh bại."),i("br"),i("br"),n._v("Thực lực của người này, vừa rồi đều thấy được, kiếm khí tung hoành, chiêu số tinh xảo, liền xem như ba cái hắn, tính gộp lại cũng không là đối thủ, vốn cùng hắn chiến đấu đều không có lòng tin ra tay, còn là nhớ tới viện trưởng giảng giải, mới vội vàng ứng đối."),i("br"),i("br"),n._v("Viện trưởng nói qua cùng loại này thân thể, kiếm pháp, phản ứng... Đều cơ hồ hoàn mỹ người, làm như thế nào chiến đấu, chỉ là hắn cảm thấy, không có cơ hội gặp được, không có coi ra gì, nằm mơ đều không nghĩ tới, dựa theo giảng giải lời nói đi tiến công, lại có như vậy kỳ hiệu!"),i("br"),i("br"),n._v("Chỉ tay liền điểm đến trên mặt đất... Thật hay giả, cái này cũng quá dễ dàng a?"),i("br"),i("br"),n._v("Cái tên này thật sự là vừa rồi liền Triệu Trung Xuyên, đều ba chiêu kiên trì không đến siêu cấp cường giả?"),i("br"),i("br"),n._v("“Chuyện gì xảy ra? Xảy ra chuyện gì?”"),i("br"),i("br"),n._v("“Ta cũng không biết, hình như Hứa Đài Chiến sư, bị đánh bại...”"),i("br"),i("br"),n._v("“Chúng ta nhiều người như vậy, liền hắn ba chiêu đều không kháng nổi, cái tên này nhưng đem hắn đánh bại?”"),i("br"),i("br"),n._v("“Không phải nói Hồng Viễn học viện học sinh thực lực rất kém cỏi ư? Đây là... Sao cái tình huống?”"),i("br"),i("br"),n._v("Hắn trên đài ngẩn người, có chút không rõ ràng cho lắm, dưới đài đều đã điên rồi."),i("br"),i("br"),n._v("Nguyên một đám trợn mắt hốc mồm, giống như là thấy được quỷ đồng dạng."),i("br"),i("br"),n._v("Vân Hư học viện, La Thanh học viện, Thanh Trúc học viện, vừa đến liền tiến hành qua biểu hiện ra, mọi người đối từng người thực lực, đều có nhất định lý giải."),i("br"),i("br"),n._v("Vị này Chử Kiện, 80 người bên trong, trước 50 đều không xếp hạng tới, kết quả lên đài về sau, một đầu ngón tay liền đem Hứa Đài chọc té xuống đất... Chênh lệch này, khó tránh khỏi có chút quá lớn đi!"),i("br"),i("br"),n._v("“Ngươi gian lận!”"),i("br"),i("br"),n._v("Ói ra mấy ngụm máu tươi, Hứa Đài cái này mới bớt đau mà đến, đứng dậy lạnh lùng chằm chằm lấy thanh niên trước mắt, như cùng một đầu hung thú."),i("br"),i("br"),n._v("Đường đường Chiến sư, thế mà bị một cái chân khí, lực lượng, tốc độ phản ứng... Cũng không bằng hắn người, chỉ tay chọc lật, phẫn nộ trong lòng có thể tưởng tượng được."),i("br"),i("br"),n._v("“Ta...” Chử Kiện ngẩn ngơ, muốn nói cái gì, nhưng nói không nên lời."),i("br"),i("br"),n._v("Không có gian lận, làm sao đem đối phương đánh bại, thật không biết rõ."),i("br"),i("br"),n._v("Muốn nói gian lận, tất cả mọi người trên lôi đài công bằng quyết đấu, hơn nữa đối phương còn dùng trường kiếm, tính như vậy, tuyệt đối là thực sự chiến thắng..."),i("br"),i("br"),n._v("“Có dám hay không cùng ta lần nữa tỷ thí một trận!”"),i("br"),i("br"),n._v("Hứa Đài cắn răng."),i("br"),i("br"),n._v("“Tốt, có gì không dám!”"),i("br"),i("br"),n._v("Chần chờ một chút, Chử Kiện gật đầu."),i("br"),i("br"),n._v("Thật ra thì không chỉ Hứa Đài cho rằng như vậy, dưới đài rất nhiều người cũng đều nghĩ như vậy, nếu như bây giờ liền nói đối phương thua, chắc chắn sẽ không thừa nhận, thắng cũng sẽ không hào quang."),i("br"),i("br"),n._v("“Bắt đầu đi, lần này ta sẽ không đi hạ thủ lưu tình...”"),i("br"),i("br"),n._v("Nghe được đáp ứng, Hứa Đài hét dài một tiếng, thân thể nhoáng một cái, trường kiếm hóa thành cự long, trực tiếp bắn tới."),i("br"),i("br"),n._v("Lần này vừa ra tay liền dùng lực lượng mạnh nhất, so vừa rồi còn còn đáng sợ hơn, trường kiếm còn chưa tới đến trước mặt, kiếm khí liền đem tỷ thí đài mặt đất xé rách."),i("br"),i("br"),n._v("“Cùng tình huống vừa rồi đồng dạng... Viện trưởng đối với loại công kích này, cũng tiến hành giảng giải...”"),i("br"),i("br"),n._v("Nhìn đối phương, uy lực vô cùng một kiếm, Chử Kiện trong đầu rơi vào nhớ lại, lần nữa nhớ tới viện trưởng truyền thụ."),i("br"),i("br"),n._v("Thân thể nhẹ nhàng xoay một cái, chập ngón tay làm kiếm, hướng một cái phương hướng nhấn tới."),i("br"),i("br"),n._v("Phốc phốc!"),i("br"),i("br"),n._v("Đầu ngón tay vào thịt, chân khí dường như môt cây chủy thủ."),i("br"),i("br"),n._v("Sau một khắc, khí thế hung hăng Hứa Đài, lần nữa nằm trên mặt đất, vẻ mặt trắng bệch, lần nữa từng ngụm từng ngụm hộc máu."),i("br"),i("br"),n._v("Diện mạo so vừa rồi còn muốn mãnh liệt mấy phần."),i("br"),i("br"),n._v("Dường như trong cơ thể có nhả không xong máu tươi đồng dạng."),i("br"),i("br"),n._v("“Lại thua?”"),i("br"),i("br"),n._v("“Lần này hắn dùng hết toàn lực, đều bị chỉ tay đánh bại, chẳng lẽ vị này Chử Kiện, một mực che giấu tu vi?”"),i("br"),i("br"),n._v("“Ta cũng xem không hiểu, chiêu số của hắn thực sự quá thâm ảo...”"),i("br"),i("br"),n._v("Dưới đài hoàn toàn yên tĩnh, tất cả mọi người cả đám đều nói không ra lời."),i("br"),i("br"),n._v("Ba đại học viện tên thứ nhất, tất cả đều liếc mắt nhìn nhau, từng người trên đầu mồ hôi lạnh ứa ra."),i("br"),i("br"),n._v("Trước đó, bọn họ căn bản là không có đem Hồng Viễn học viện để vào mắt, cảm thấy tuyển chọn ra trước 20 danh, đối phương khả năng liền một cái đều không có, nằm mơ đều không nghĩ tới, đối phương vị này bài danh không hề gần phía trước gia hỏa, dễ như trở bàn tay liền đem Chiến sư đường Chiến sư đánh bại."),i("br"),i("br"),n._v("“Ngươi... Ngươi...”"),i("br"),i("br"),n._v("Đấu tranh đứng dậy, Hứa Đài cắn răng."),i("br"),i("br"),n._v("Nói thật, hắn chính mình cũng không biết làm sao bại!"),i("br"),i("br"),n._v("Đường đường Hợp Linh cảnh, hầu như không có địch thủ tồn tại, bị người đánh bại cũng không biết, để hắn có chút phát điên."),i("br"),i("br"),n._v("“Được rồi, Hứa Đài, còn ngại mất mặt ném không đủ?”"),i("br"),i("br"),n._v("Thấy hắn không phục, Trác Thanh Phong hừ lạnh."),i("br"),i("br"),n._v("“Đội trưởng, ta...”"),i("br"),i("br"),n._v("Hứa Đài vội vàng quay đầu."),i("br"),i("br"),n._v("“Vị tiểu huynh đệ này, chân khí cùng lực lượng, quả thực không bằng ngươi, nhưng hắn đối võ kỹ lý giải cùng đối với chiến đấu nắm chắc thời cơ, là ngươi còn kém rất rất xa, vừa rồi cái kia chỉ tay, thoạt nhìn đơn giản, trên thực tế nhưng muốn đối ngươi kiếm pháp cùng vận chuyển chân khí tình huống, rõ như lòng bàn tay mới có thể làm đến!”"),i("br"),i("br"),n._v("Trác Thanh Phong nhãn lực kinh người, nhìn ra chỗ mấu chốt, giải thích một câu: “Lấy ngón tay làm kiếm, thu hút ngươi lộ ra sơ hở, sau đó lại tiến hành công kích... Phần này nhãn lực, cũng đủ làm Chiến sư!”"),i("br"),i("br"),n._v("“Vâng...”"),i("br"),i("br"),n._v("Thấy đội trưởng lên tiếng, Hứa Đài không dám nhiều lời, đành phải gật đầu lui xuống."),i("br"),i("br"),n._v("“Ta... Thông qua được?”"),i("br"),i("br"),n._v("Vốn cảm giác đến mức hoàn toàn không có hi vọng, nghe được thông qua tuyển chọn, Chử Kiện mặt mũi không thể tin được."),i("br"),i("br"),n._v("“Chúng ta khảo hạch là, chỉ cần có thể tiếp lấy ba chiêu, liền có thể thông qua, ngươi đem chúng ta Chiến sư đánh bại, không tính là thông qua lời nói, ai có thể thông qua?”"),i("br"),i("br"),n._v("Trác Thanh Phong cười cười."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Chử Kiện gật gật đầu, theo trên đài cao nhảy xuống tới."),i("br"),i("br"),n._v("“Được rồi, Hứa Đài, ngươi nghỉ ngơi một chút, Trần Trúc, tiếp tục khảo hạch!”"),i("br"),n._v("Chiến sư mặc dù mạnh mẽ, nhưng cũng không phải chân chính vô địch, chí ít hắn tại đồng cấp lúc khác, vị này Hứa Đài liền không phải là đối thủ."),i("br"),i("br"),n._v("Bởi vậy, bốn đại học viện ngẫu nhiên xuất hiện một thiên tài, đem hắn đánh bại, không tính là gì, Trác Thanh Phong cũng không có quá bất cẩn bên ngoài, để khảo hạch tiếp tục tiến hành."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Trần Trúc gật gật đầu, nhảy lên, ánh mắt rơi xuống: “Nhưng có dám cùng ta chiến đấu? Đồng dạng... Chỉ cần tiếp lấy ta ba chiêu, có thể trở thành Chiến sư!”"),i("br"),i("br"),n._v("Hắn dùng chính là một chuôi trường đao, đao mang sắc bén, phối hợp chân khí cùng khí thế, để cho người ta còn chưa chiến đấu, liền không rét mà run."),i("br"),i("br"),n._v("Cái tên này thoạt nhìn so Hứa Đài còn còn đáng sợ hơn, thậm chí thực lực càng mạnh!"),i("br"),i("br"),n._v("“Sớm biết hắn lợi hại hơn, vừa rồi nên lên...”"),i("br"),i("br"),n._v("“Lên cũng vô dụng thôi, Hứa Đài Chiến sư ba chiêu, liền Triệu Trung Xuyên đều không tiếp nổi, coi như đi lên, cũng là uổng công!”"),i("br"),i("br"),n._v("“Đúng vậy a, không biết lần này ai có thể trong tay hắn vượt qua ba chiêu...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn thấy Trần Trúc phóng thích lực lượng, mạnh mẽ vô cùng, mọi người nguyên một đám lần nữa trên mặt thần sắc lo lắng."),i("br"),i("br"),n._v("Hứa Đài liền khó đối phó như vậy, huống chi người này."),i("br"),i("br"),n._v("“Ta tới!”"),i("br"),i("br"),n._v("Một thanh niên nhảy lên."),i("br"),i("br"),n._v("“Là La Thanh học viện đứng hàng thứ nhất, Trương Thanh Sơn!”"),i("br"),i("br"),n._v("“Thực lực của hắn rất mạnh, có lẽ có thể ngăn cản ba chiêu...”"),i("br"),i("br"),n._v("Nhìn thấy người này, đài kế tiếp cái ánh mắt tập trung."),i("br"),i("br"),n._v("Trương Thanh Sơn, La Thanh học viện năm nhất người mạnh nhất, trước đó vị kia Triệu Trung Xuyên, trong tay liền hai chiêu đều nối không đến."),i("br"),i("br"),n._v("Từ hắn ra tay, khẳng định có thể thành công."),i("br"),i("br"),n._v("Hô hô hô!"),i("br"),i("br"),n._v("Tiếng nghị luận bên trong, trên đài giao đấu đã bắt đầu, Trương Thanh Sơn năm ngón tay mở ra, chân khí tự nhiên, dường như Trường Giang triều cường, lao nhanh không ngừng."),i("br"),i("br"),n._v("Đổi lại những người khác, khẳng định liền một chiêu đều không chịu được nữa, liền sẽ bị như vậy cuồng bạo chân khí xé rách, đối diện Trần Trúc, nhưng giống như sóng biển bên trong thuyền nhỏ, bất kể gió lốc lại lớn, đều nước chảy bèo trôi, sẽ không đắm chìm."),i("br"),i("br"),n._v("Trong nháy mắt, hai chiêu liền qua, hai người chiến cái ngang tay, không ai nhường ai."),i("br"),i("br"),n._v("“Muốn thông qua ba chiêu, nào có dễ dàng như vậy!”"),i("br"),i("br"),n._v("Thấy hai chiêu không có kết quả, Trần Trúc ánh mắt híp lại, hừ lạnh một tiếng, trường đao đột nhiên rời khỏi tay, hóa thành một đạo thiểm điện, trực tiếp hướng về phía trước bổ xuống."),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Tốc độ quá nhanh, không khí trong nháy mắt nứt ra, Trương Thanh Sơn còn không có kịp phản ứng, liền cảm thấy một cỗ nóng rực sóng khí đánh tới, tựa hồ muốn hắn chém thành hai khúc."),i("br"),i("br"),n._v("“Nguy rồi...”"),i("br"),i("br"),n._v("Con ngươi co rụt lại, thân thể kìm lòng không được lui về sau đi, đồng thời binh khí trong tay khẽ đảo, tiến lên đón."),i("br"),i("br"),n._v("Bành!"),i("br"),i("br"),n._v("Trường đao cùng vũ khí đối đầu, kình khí cường đại, đem mặt lôi đài, lấy hai người làm trung tâm, chấn vỡ một mảng lớn, Trương Thanh Sơn hơi đỏ mặt, máu tươi phun ra, bay ngược mà ra, rơi xuống dưới đài."),i("br"),i("br"),n._v("“Thua? Ta làm sao có thể thua?”"),i("br"),i("br"),n._v("Nằm trên mặt đất, Trương Thanh Sơn mặt như giấy trắng, không thể tin được."),i("br"),i("br"),n._v("Hắn nhưng là La Thanh học viện tên thứ nhất, thực lực cường đại như vậy, thế mà liền ba chiêu đều không có tiếp lấy... Khảo hạch Chiến sư thất bại, mãnh liệt cảm giác bị thất bại, để hắn khó mà tiếp nhận."),i("br"),i("br"),n._v("“Trước khi đến ta bàn giao thế nào? Không cho phép sử dụng cấm chiêu, Trần Trúc, ngươi làm cái gì vậy?”"),i("br"),i("br"),n._v("Đang tại buồn bực, liền nghe đến Trác Thanh Phong sầm mặt lại, lớn tiếng trách mắng."),i("br"),i("br"),n._v("Nếu như bình thường ba chiêu lời nói, vị này Trương Thanh Sơn tất nhiên có thể nhẹ nhõm chiến thắng, ai biết bản thân vị này thuộc hạ, thế mà thời khắc mấu chốt sử dụng cấm chiêu, quả thực làm trái quy tắc."),i("br"),i("br"),n._v("“Ta...” Trần Trúc hơi đỏ mặt."),i("br"),i("br"),n._v("Tận mắt thấy Hứa Đài bị đánh bại, trong lòng của hắn có chấn động, dưới tình thế cấp bách dùng ra cấm chiêu."),i("br"),i("br"),n._v("“Không có ý tứ, Thẩm viện trưởng, là ta thuộc hạ dùng sai chiêu số, ngươi học viện vị này Trương Thanh Sơn, mặc dù thua, nhưng cũng có tư cách trở thành Chiến sư!”"),i("br"),i("br"),n._v("Trách mắng xong đối phương, Trác Thanh Phong quay đầu nhìn về phía Thẩm Bình Triều."),i("br"),i("br"),n._v("“Vậy làm phiền Trác Chiến sư!”"),i("br"),i("br"),n._v("Nghe được học sinh của mình còn có cơ hội trở thành Chiến sư, Thẩm Bình Triều thở phào nhẹ nhõm, nhịn không được ôm quyền."),i("br"),i("br"),n._v("Đối phương làm như vậy, rất rõ ràng cho hắn mặt mũi, không thể không lĩnh chuyện này."),i("br"),i("br"),n._v("“Tiếp tục tiến hành đi!”"),i("br"),i("br"),n._v("Xử lý xong Trương Thanh Sơn sự tình, Trác Thanh Phong tiếp tục nói."),i("br"),i("br"),n._v("Trần Trúc nhìn quanh một vòng, mang theo ngạo nghễ: “Ai nguyện ý đi lên?”"),i("br"),i("br"),n._v("Vừa rồi một đao đem Trương Thanh Sơn đánh bay, thực sự quá mức khí thế, vốn cảm thấy mình còn có chút hi vọng người tham gia khảo hạch, giờ phút này nguyên một đám rụt rụt đầu."),i("br"),i("br"),n._v("Bọn họ nhưng không có Trương Thanh Sơn tốc độ phản ứng cùng lực lượng, đối phương thật muốn thi triển ra như thế cuồng bạo đao pháp, chỉ sợ liền phản ứng đều không có kịp phản ứng, liền sẽ bản thân bị trọng thương, thậm chí... Bị tại chỗ đánh chết!"),i("br"),i("br"),n._v("“Làm sao? Lại không người đi lên? Ta không nhìn lầm, Hồng Viễn học viện, cái này cả buổi chỉ lên tới một cái đi! Nhưng còn có người dám cùng ta chiến đấu?”"),i("br"),i("br"),n._v("Ánh mắt tập trung, Trần Trúc lạnh lùng nói."),i("br"),i("br"),n._v("Bạn tốt Hứa Đài bị Hồng Viễn học viện học sinh đánh thành trọng thương, hắn ý định trả thù thoáng cái, cho đối phương một hạ mã uy, để hắn rõ ràng, Chiến sư thực lực chân chính, coi như chỉ cần ba chiêu, cũng không phải là các ngươi những này gà đất chó sành có thể chống lại."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Nghe được hắn ngang nhiên mở miệng, khiêu chiến Hồng Viễn học viện học viên, mọi người tất cả đều chần chừ."),i("br"),i("br"),n._v("Một lát sau, một thanh niên cắn răng một cái đứng lên."),i("br"),i("br"),n._v("“Ta tới!”"),i("br"),i("br"),n._v("Nói liền muốn nhảy lên đài cao."),i("br"),i("br"),n._v("“Chu Hi... Ngươi là tham gia tuyển chọn thứ nhất đếm ngược, dạng này đi lên, không phải là tìm chết sao?”"),i("br"),i("br"),n._v("Trong đám người, không biết ai hô lên."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-942"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-944"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);