(window.webpackJsonp=window.webpackJsonp||[]).push([[2239],{2594:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-938-vo-đich-trinh-duong-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-938-vo-đich-trinh-duong-thuong"}},[n._v("#")]),n._v(" Chương 938: Vô địch Trịnh Dương (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 938: Vô địch Trịnh Dương (thượng) "),t("br"),t("br"),n._v("Đối với cái đồ chơi này, hắn không có chút nào lý giải, chỉ có bản thân tự mình thí nghiệm, mới biết được cái gọi là hai cùng bốn, là khái niệm gì."),t("br"),t("br"),n._v("“Viện trưởng muốn thử một chút?”"),t("br"),t("br"),n._v("“Đừng...”"),t("br"),t("br"),n._v("Dưới đài Mi trưởng lão đám người sắc mặt đều trắng."),t("br"),t("br"),n._v("Bọn họ viện trưởng này, thử cái gì hỏng cái gì, trừ phi vị này Trác Chiến sư không muốn cái này viên chân tức thạch, nếu không, tốt nhất đừng để hắn động thủ, nếu không, muốn khóc cũng không kịp..."),t("br"),t("br"),n._v("Tựa hồ nghe đến tiếng lòng của bọn họ, Trác Thanh Phong cười cười: “Không làm phiền viện trưởng tự mình thí nghiệm, Trần Trúc, ngươi đi qua!”"),t("br"),t("br"),n._v("“Vâng!” Trần Trúc nhẹ gật đầu, đi tới trước mặt, bàn tay ở phía trên một dựng, lập tức ánh sáng lóe lên, một cái số lượng xuất hiện ở trước mắt mọi người —— năm!"),t("br"),t("br"),n._v("“Điều này nói rõ, Trần Trúc chân khí cùng lực lượng, đều vượt qua bốn tiêu chuẩn!”"),t("br"),t("br"),n._v("Trác Thanh Phong giải thích."),t("br"),t("br"),n._v("“Ta thử xem!”"),t("br"),t("br"),n._v("Vừa rồi bị đánh bại La Thanh học viện viện trưởng Trương Thanh Sơn, đi tới, bàn tay ở phía trên một dựng, ánh sáng lóe lên, cho thấy con số... Bốn giờ năm."),t("br"),t("br"),n._v("Mặc dù không tới năm, không bằng Trần Trúc, nhưng cũng vượt qua “Bốn” tiêu chuẩn này."),t("br"),t("br"),n._v("“Ta xem một chút!” Chử Kiện đi tới, ở phía trên một dựng, ánh sáng lóe lên, chỉ có 2.1."),t("br"),t("br"),n._v("“Ta mới như thế điểm?”"),t("br"),t("br"),n._v("Da mặt co lại."),t("br"),t("br"),n._v("Hắn tại Hồng Viễn học viện hai mươi vị tuyển chọn người bên trong, bài danh thứ năm, tính toán là rất cao thứ tự, kết quả cũng chỉ có chút thành tích này, đằng sau mười lăm vị, thật muốn như vậy coi là, đoán chừng cái này “Hai” đều rất khó đạt tới."),t("br"),t("br"),n._v("Nhìn như vậy lên, nếu không phải viện trưởng giảng bài, bằng mượn bọn họ thực lực của mình, thật muốn trở thành Chiến sư, là không thể nào."),t("br"),t("br"),n._v("“Chắc hẳn Trương viện trưởng đối cái này chân tức thạch cũng có hiểu biết đi!”"),t("br"),t("br"),n._v("Có ba người thí nghiệm, chân tức thạch tình huống, đã không sai biệt lắm biết, Trác Thanh Phong cười nhìn lại."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu, mày nhăn lại."),t("br"),t("br"),n._v("Chử Kiện thực lực, hắn vừa rồi cũng nhìn, tại tất cả học viên bên trong sắp xếp bên trên gần phía trước, hắn mới 2.1, thật nếu để cho bản thân học viện học viên bên trên, đoán chừng có thể vượt qua hai, không cao hơn một nửa!"),t("br"),t("br"),n._v("Nói cách khác, ít nhất phải đào thải năm mươi nhiều!"),t("br"),t("br"),n._v("“Nếu không như vậy đi, Trác Chiến sư, có thể hay không cho ta hai cái canh giờ, ta cho những học sinh này, truyền thụ vài thứ... Sau hai canh giờ, tới đo lường!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“Hai canh giờ?” Trác Thanh Phong sững sờ."),t("br"),t("br"),n._v("Võ kỹ cùng kỹ xảo chiến đấu, có thể thời gian ngắn tăng lên, nhưng chân khí cùng lực lượng, thì cần muốn thời gian dài tích lũy đi!"),t("br"),t("br"),n._v("Hai canh giờ truyền thụ... Có thể có ích lợi gì?"),t("br"),t("br"),n._v("“Cũng không cần hai canh giờ, hiện tại sáng sớm đã tối, dạng này, cho các ngươi một đêm thời gian, buổi sáng ngày mai tới đo lường!”"),t("br"),t("br"),n._v("Do dự một chút, Trác Thanh Phong nói."),t("br"),t("br"),n._v("Ánh sáng ba đại học viện chọn lựa, liền hao tốn bốn canh giờ, hiện tại trời đã tối đen."),t("br"),t("br"),n._v("Dù sao trong ba ngày tuyển ra Chiến sư là được, không cần thiết hôm nay đuổi ra."),t("br"),t("br"),n._v("“Vậy liền rất cảm tạ Trác Chiến sư...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đám học sinh này bất kể thân thể vẫn là chân khí, đều mười phần yếu kém, nếu như có thể có một đêm thời gian giảng bài, học tập, tuyệt đối có thể có biến hóa thoát thai hoán cốt."),t("br"),t("br"),n._v("“Cái này không có gì, cũng là vì tuyển chọn Chiến sư...” Trác Thanh Phong cười cười."),t("br"),t("br"),n._v("“Vậy thì tốt, ta cũng không muốn nói nhiều, Triệu trưởng lão, Mi trưởng lão, các ngươi chiêu đãi ba vị viện trưởng cùng Trác Chiến sư đám người, ta trước cho những người này, bổ điểm khóa!”"),t("br"),t("br"),n._v("Cười cười, Trương Huyền an bài một tiếng, mang theo mọi người trực tiếp hướng trưởng lão viện đại điện đi đến."),t("br"),t("br"),n._v("Trưởng lão viện đại điện, có ngăn cách khí tức trận pháp, còn có lợi hại Tụ Linh trận, mấu chốt nhất là, chứa đựng một trăm người, mười phần rộng rãi, không thể thích hợp hơn."),t("br"),t("br"),n._v("Hắn bên này rời khỏi, Mi trưởng lão đám người thì an bài Ô viện trưởng đám người tiến hành tiệc tối, sau đó ở lại."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi tới trưởng lão viện đại điện, Trương Huyền trước đem Tụ Linh trận lần nữa bố trí một lần, để hắn uy lực càng lớn, lập tức lấy ra năm trăm viên linh thạch thượng phẩm, đặt ở bốn phía."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Một trong nháy mắt, đại điện liền bị linh khí nồng nặc rót đầy, cảm nhận được dường như chất lỏng linh khí, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Người khác muốn muốn tăng lên thân thể cùng chân khí lực lượng, mười phần khó khăn, đối với hắn mà nói, nhưng cũng không phức tạp."),t("br"),t("br"),n._v("Sưu tập danh sư học viện nhiều như vậy luyện thể công pháp, Ngũ Diệu Kim Thân trước bốn trọng, đã hoàn toàn lột xác thành Thiên Đạo công pháp, coi như không có bảo vật, cũng có thể nhẹ nhõm tu luyện."),t("br"),t("br"),n._v("Còn chân khí, những người này coi như không thể tu luyện Thiên Đạo chân khí, nhưng tinh giản, tinh giản, bản rút gọn, vẫn là rất dễ dàng lĩnh ngộ, chỉ cần thêm chút chỉ điểm, rèn luyện chân khí, để tích lũy trở nên hùng hậu, không hề khó khăn."),t("br"),t("br"),n._v("“Các ngươi chỉ có một đêm thời gian, có thể học được bao nhiêu, xem ngộ tính của mình cùng năng lực!”"),t("br"),t("br"),n._v("Đem mọi người an bài tốt, Trương Huyền nhìn quanh một vòng, thanh âm đám lãng vang lên: “Hiện tại ta cho mọi người truyền thụ một bộ luyện thể pháp quyết, Ngũ Diệu Kim Thân...”"),t("br"),t("br"),n._v("Thanh âm bên trong xen lẫn sư ngôn thiên bẩm, để tất cả học sinh, tất cả đều kìm lòng không được dựa theo tiếng nói của hắn vận chuyển chân khí trong cơ thể."),t("br"),t("br"),n._v("Ngô Dương Tử Ngũ Diệu Kim Thân, rườm rà phức tạp, liền xem như học viện mỗi cái niên cấp hai mươi người đứng đầu, muốn luyện thành không có có vài chục năm công phu, đều không thể nào làm được."),t("br"),t("br"),n._v("Bất quá, Trương Huyền mượn nhờ Thiên Đạo công pháp sửa chữa về sau, liền không đồng dạng, đã trở nên mười phần đơn giản, đắm chìm trong tu luyện, tất cả học sinh thân thể lực lượng, không ngừng gia tăng, càng ngày càng mạnh."),t("br"),t("br"),n._v("..."),t("br"),n._v("Hoài Dương vương đưa tặng trong phủ đệ, Trịnh Dương duỗi lưng một cái theo gian phòng đi ra."),t("br"),t("br"),n._v("Trải qua ròng rã một ngày tu luyện, đã đem lão sư giảng giải nội dung hoàn toàn tiêu hóa, thực lực cũng có tăng lên trên diện rộng."),t("br"),t("br"),n._v("“Nghe nói hôm nay học viện Chiến sư đường tuyển chọn... Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Một ngụm trọc khí phun ra, nhớ tới sự kiện, Trịnh Dương ánh mắt sáng lên."),t("br"),t("br"),n._v("Trong truyền thuyết Chiến sư, mỗi một cái đều có thể vượt cấp chiến đấu, thực lực kinh người, hắn muốn nhìn một chút rốt cuộc mạnh cỡ nào."),t("br"),t("br"),n._v("Cũng không khai vù vù Vương Dĩnh đám người, nhanh chân hướng học viện đi đến."),t("br"),t("br"),n._v("Không lâu sau, đi tới trong đó."),t("br"),t("br"),n._v("Lúc này trời đã tối đen, yến hội cũng kết thúc, tốp năm tốp ba học sinh, đi ở sân trường phía trong, hiện ra đến hưng phấn dị thường."),t("br"),t("br"),n._v("Hồng Viễn học viện, tại Chiến sư tuyển chọn bên trên, từ trước tới nay bị ba đại học viện kéo rất xa, lần này mới viện trưởng kế vị, thay đổi trạng thái bình thường, đem Chiến sư đường đều đánh không còn cách nào khác, để bọn hắn đều cảm thấy mở mày mở mặt, không nói ra được sảng khoái."),t("br"),t("br"),n._v("Nghe được mọi người thảo luận, Trịnh Dương cũng gần như lý giải ban ngày tình huống, nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Một đường cùng lão sư đi tới, biết cái này tuổi không lớn lắm lão sư, có loại nào kinh thiên động địa năng lực!"),t("br"),t("br"),n._v("Đừng nói chỉ là đánh bại Chiến sư đường cấp thấp nhất người tham gia khảo hạch... Coi như huấn luyện được một đám người, đem Chiến sư đường chọn lấy, cũng không thấy đến kỳ quái."),t("br"),t("br"),n._v("“Chân khí, lực lượng, là tu luyện giả tích lũy tháng ngày quá trình, cái này Trương viện trưởng, cho rằng nói một đêm khóa, liền có thể tăng lên... Thật sự là không biết trời cao đất rộng!”"),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta vì tăng lên chân khí độ tinh thuần, đề cao thân thể lực lượng, ngậm bao nhiêu đắng, bị bao nhiêu khổ? Thậm chí tới thời điểm, đều phải thừa nhận Phượng Diễm Thánh thú nướng... Coi như thế, cũng chỉ là để chân khí cùng thân thể tăng lên một chút thôi!”"),t("br"),t("br"),n._v("“Nói cái kỹ xảo chiến đấu, thu hoạch được thành công, liền tự cho mình siêu phàm, ngày mai, chờ lấy chế giễu chính là...”"),t("br"),t("br"),n._v("Dọc theo đường đi tiến lên, mấy cái thảo luận thanh âm truyền vào lỗ tai."),t("br"),t("br"),n._v("Nhấc mắt nhìn đi, chỉ thấy mấy cái người mặc cổ quái danh sư bào thanh niên, cười khanh khách đi thẳng về phía trước, trên mặt lộ ra vẻ đùa cợt."),t("br"),t("br"),n._v("Hôm nay Hồng Viễn học viện học sinh, để bọn hắn Chiến sư đường, mất mặt xấu hổ, đổi lại ai, trong lòng đều sẽ không cam lòng!"),t("br"),t("br"),n._v("Giờ phút này nghe nói vị này Trương viện trưởng, tới gần mài thương giảng giải võ kỹ cùng kỹ xảo chiến đấu thì cũng thôi đi, thế mà còn muốn trong thời gian ngắn tăng lên chân khí độ tinh thuần cùng thân thể lực lượng... Đây không phải nằm mơ ư?"),t("br"),t("br"),n._v("Hai thứ này, là tu luyện giả cường đại cơ sở, nếu như dễ dàng như vậy liền tăng lên, bọn họ Chiến sư đường cũng không trở thành khổ cực như thế tu luyện."),t("br"),t("br"),n._v("“Mấy vị bằng hữu, còn xin dừng bước!”"),t("br"),t("br"),n._v("Nghe lời nói của đối phương, đối lão sư chế giễu, lộ rõ trên mặt, Trịnh Dương nhướng mày, nhịn không được xòe bàn tay ra, ngăn ở mấy người trước mặt."),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("Mấy cái thanh niên mày nhăn lại."),t("br"),t("br"),n._v("“Không có gì, chỉ là nghe nói Chiến sư đường người, từng cái sức chiến đấu mạnh mẽ, ta nhàn rỗi vô sự, vừa vặn muốn thử xem thực lực của các ngươi, có phải hay không cùng trong truyền thuyết đáng sợ như vậy!”"),t("br"),t("br"),n._v("Trịnh Dương nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Biết nói chúng ta là Chiến sư đường người, còn dám ngăn trở? Tự tìm cái chết!”"),t("br"),t("br"),n._v("“Từ đâu tới đứa nhà quê? Sống chán ngấy đi!”"),t("br"),t("br"),n._v("Mấy người vốn là mất hứng, thấy một cái chỉ có mười sáu mười bảy tuổi thiếu niên, dám cản bọn họ lại, còn muốn thử một chút thực lực của bọn hắn, lập tức tất cả đều giận dữ."),t("br"),t("br"),n._v("Bọn họ thế nhưng là đường đường Chiến sư, đi đến bất kỳ địa phương nào, đều được người tôn kính bị người kính sợ tồn tại."),t("br"),t("br"),n._v("Kết quả, đến cái này Hồng Viễn học viện, trước bị người liên tiếp đánh ngất xỉu không nói, một tên thiếu niên mười mấy tuổi, đều ngông cuồng như thế, làm sao có thể phải nhịn xuống!"),t("br"),t("br"),n._v("“Có phải hay không tự tìm cái chết, thử một chút thì biết...”"),t("br"),t("br"),n._v("Cũng không tức giận, Trịnh Dương nhẹ nhàng cười một tiếng, một chuôi trường thương xuất hiện lòng bàn tay, trên không trung vạch một cái, phóng xuất ra khí thế kinh người."),t("br"),t("br"),n._v("“Đã muốn tìm phiền toái, vậy cũng đừng trách chúng ta không khách khí...”"),t("br"),t("br"),n._v("Thấy hắn lấy ra vũ khí, mấy vị Chiến sư tất cả đều sầm mặt lại, bên trong một cái, khi trước một bước vượt qua tới."),t("br"),t("br"),n._v("Vị thanh niên này, là Đạp Hư cảnh đỉnh phong thực lực, chuyên môn khảo hạch năm thứ tư học viên, hôm nay khảo hạch ba đại học viện thời điểm, đại triển thần uy, bức đến vô số tài tuấn, giơ tay nhận thua, thực lực mạnh, có thể nói kinh khủng."),t("br"),t("br"),n._v("Hắn nhìn ra Trịnh Dương, chỉ có Tằm Phong cảnh sơ kỳ, ý định ra tay thử xem."),t("br"),t("br"),n._v("Thân là Chiến sư, có thể vượt cấp chiến đấu, đối phương cùng hắn chỉ kém một cái tiểu cấp bậc, trước đánh bại, hẳn là rất là nhẹ nhõm."),t("br"),t("br"),n._v("“Ra tay đi! Ta có thể để cho ngươi ba chiêu...”"),t("br"),t("br"),n._v("Lông mày giương lên, vũ khí lấy ra, thanh niên vẻ mặt lạnh nhạt nói."),t("br"),t("br"),n._v("Trên người toát ra một cỗ cường đại tự tin."),t("br"),t("br"),n._v("“Để cho ta ba chiêu? Không cần... Ta sẽ đem tu vi áp chế giống như ngươi!”"),t("br"),t("br"),n._v("Thấy cái tên này còn không có làm rõ sự thật, Trịnh Dương lắc đầu, chân khí trong cơ thể vận chuyển, một hồi giòn vang, tu vi bị áp chế đến Đạp Hư cảnh đỉnh phong."),t("br"),t("br"),n._v("“Nếu ngươi tự tìm phiền phức, vậy ta liền không khách khí...”"),t("br"),t("br"),n._v("Thấy cái này không biết từ đâu xuất hiện thiếu niên, dám đè thấp tu vi cùng hắn chiến đấu, thanh niên cười lạnh, binh khí trong tay, bỗng nhiên vung lên, một cỗ cường đại tới cực điểm lực lượng, ầm vang quét sạch."),t("br"),t("br"),n._v("Mặc dù thực lực của hắn, chỉ là Đạp Hư cảnh đỉnh phong, nhưng một chiêu này uy lực, liền xem như Tằm Phong cảnh đỉnh phong, cũng không dám mạnh mẽ chống đỡ."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Đối mặt như đòn công kích này, Trịnh Dương nhẹ nhàng cười một tiếng, cũng không lui về phía sau, trường thương trong tay, nhẹ nhàng lắc một cái, đâm ra ngoài."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Thương cùng đối phương vũ khí đối đầu, chỉ một chút, thanh niên liền bay ngược mà ra, nằm trên mặt đất, phun máu phè phè."),t("br"),t("br"),n._v("(Đầu tháng, cầu giữ gốc nguyệt phiếu! Sau đó có cái hàng năm tổng kết.)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-946"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-948"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);