(window.webpackJsonp=window.webpackJsonp||[]).push([[224],{577:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1179-han-goi-truong-huyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1179-han-goi-truong-huyen"}},[n._v("#")]),n._v(" Chương 1179: Hắn gọi Trương Huyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1179: Hắn gọi Trương Huyền"),t("br"),t("br"),n._v("Giám bảo sư công hội."),t("br"),t("br"),n._v("“Thế nào? Ta cái này dược, hiệu quả coi như không tệ đi!”"),t("br"),t("br"),n._v("Trong đại sảnh, Tề lão cười nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("Nhìn thấy trong gương, tuấn mỹ dung nhan lần nữa khôi phục, thanh niên lúc này mới thở phào nhẹ nhõm, ôm quyền khom người: “Đa tạ Tề lão linh đan diệu dược, nếu không, chỉ sợ còn không biết bao lâu mới có thể khôi phục...”"),t("br"),t("br"),n._v("“Không cần khách khí, y giả phụ mẫu tâm, đây cũng là ta phải làm!”"),t("br"),t("br"),n._v("Tề lão gật đầu."),t("br"),t("br"),n._v("“Cửu Tiêu, về sau giám bảo, có thể làm được liền làm, làm không được, không cần cậy mạnh, nếu không, mất mặt không riêng gì bản thân, làm không cẩn thận ngươi giám bảo sư thân phận, đều có thể hủy hoại chỉ trong chốc lát!”"),t("br"),t("br"),n._v("Một bên mục hội trưởng lắc đầu."),t("br"),t("br"),n._v("Thanh niên tự nhiên là Trương Cửu Tiêu."),t("br"),t("br"),n._v("Đêm qua, Trương Huyền phối trí độc dược đem hắn đầy đầu sầu riêng diệt trừ về sau, độc tính cũng không hề hoàn toàn trừ tận gốc, trên mặt sưng tấy, không cách nào ra ngoài gặp người, đành phải lưu tại nơi này, không hề rời đi."),t("br"),t("br"),n._v("Đến buổi sáng, vẫn không có chuyển biến tốt đẹp, đành phải đem Y Sư công hội lão hội trưởng Tề lão xin đi qua."),t("br"),t("br"),n._v("Tề lão cừu nhân mặc dù thế lớn, tới ban ngày giám bảo sư công hội, hơn nữa còn có danh sư công hội tại gần đây, chắc hẳn cũng không ai dám đem hắn như thế nào."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu gật đầu."),t("br"),t("br"),n._v("Đêm qua, ỷ vào bản thân đại gia tộc hậu nhân, kiến thức rộng rãi, vốn định đại xuất danh tiếng, làm cho tất cả mọi người đều lau mắt mà nhìn, kết quả... Thảm tao đánh mặt, thảm không thể lại thảm."),t("br"),t("br"),n._v("Suy nghĩ một chút đều tràn đầy tâm tắc."),t("br"),t("br"),n._v("Nhất là anh tuấn bề ngoài, trở nên hoàn toàn thay đổi, nhờ có tại giám bảo sư công hội phía trong, không có bị đám fan hâm mộ phát hiện, nếu không đều không mặt mũi thấy người."),t("br"),t("br"),n._v("Đương nhiên, mấu chốt nhất là, giám bảo sư một đời chỉ có thể xuất hiện ba lần sai lầm, phá mở phong ấn, mặc dù không tính là giám bảo, nhưng cũng là tại công sẽ phát sinh, tin tức truyền ra ngoài, tất nhiên trở thành cả đời chỗ bẩn."),t("br"),t("br"),n._v("“Đều là vị kia Trương Huyền... Rõ ràng sẽ phá mở phong ấn phương pháp, nhưng chậm chạp không nói...”"),t("br"),t("br"),n._v("Nghĩ tới tên kia vô tội nụ cười, Trương Cửu Tiêu liền giận không chỗ phát tiết."),t("br"),t("br"),n._v("Xông luyện tâm cầu, đã để hắn mất mặt một lần, vốn cho rằng lần này có thể đoạt lại mặt mũi, mở mày mở mặt, nằm mơ đều không nghĩ tới bị đánh ác hơn."),t("br"),t("br"),n._v("“Nhất định là cố ý...”"),t("br"),t("br"),n._v("Thoạt nhìn một mặt hiền lành, cái gì cũng không biết, trên thực tế nhưng cố ý để hắn mất mặt."),t("br"),t("br"),n._v("“Tề lão, ngươi nói trên mặt ta bên trong là một loại độc?”"),t("br"),t("br"),n._v("Bất kể nói thế nào đều là thất tinh danh sư, tâm cảnh mạnh mẽ, mặc dù buồn bực, vẫn là đem tâm sự đặt ở đáy lòng, trên mặt không biểu hiện ra ngoài."),t("br"),t("br"),n._v("“Là một loại rất lợi hại độc, đoán chừng đạt đến thất tinh, nếu không phải những năm này ta thân trúng kịch độc, ngày ngày nghiên cứu giải độc phương pháp, chỉ sợ cũng không giải quyết được!” Tề lão nói."),t("br"),t("br"),n._v("Lúc trước thân trúng kịch độc về sau mất hết can đảm, chạy trốn tới Tĩnh Viễn thành, thoạt nhìn là hành động bất đắc dĩ, trên thực tế cũng làm ra chuẩn bị."),t("br"),t("br"),n._v("Đã sớm nghe nói Tĩnh Viễn thành có Độc điện, vừa mới qua đi, thử thời vận."),t("br"),t("br"),n._v("Ở nơi đó hai mươi năm, cứ việc không có giải quyết triệt để đi kịch độc trong cơ thể, nhưng cũng ngăn chặn, không để hắn phạm vi lớn khuếch tán."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, mới sống đến nay."),t("br"),t("br"),n._v("Bệnh lâu thành y, thân trúng kịch độc, không phải độc sư, đối độc cũng biết rất nhiều, không phải đặc biệt cường đại độc, đã có thể nhẹ nhõm giải quyết."),t("br"),t("br"),n._v("“Trương sư phối cho ta ăn, lại là độc dược... Chẳng lẽ hắn vẫn là là một vị độc sư?”"),t("br"),t("br"),n._v("Trương Cửu Tiêu nhịn không được nói."),t("br"),t("br"),n._v("Độc dược phương thuốc mười phần cơ mật, cơ bản đều là có độc sư nắm giữ, đối phương tiện tay viết ra, chẳng lẽ vẫn là độc sư?"),t("br"),t("br"),n._v("“Không biết ngươi nói Trương sư là...” Nghe được hắn, Tề lão nhìn lại."),t("br"),t("br"),n._v("“A, là Hồng Viễn đế quốc tới một vị lục tinh danh sư, gọi là Trương Huyền.” Mục hội trưởng nói."),t("br"),t("br"),n._v("“Hắn ah! Hắn nhưng là ân nhân cứu mạng của ta, một tay y thuật lợi hại hơn ta không biết gấp bao nhiêu lần... Mấu chốt nhất là, nhân tâm nhân đức, chân thực nhiệt tình, làm sao có thể là độc sư!” Tề lão lắc đầu."),t("br"),t("br"),n._v("“Ân nhân cứu mạng? Y thuật so Tề lão còn cao?” Trương Cửu Tiêu ngẩn ngơ."),t("br"),t("br"),n._v("Cái tên này tâm cảnh cao hơn hắn, nhận."),t("br"),t("br"),n._v("Giám bảo năng lực mạnh hơn hắn, cũng nhận."),t("br"),t("br"),n._v("Hiện tại y thuật vẫn còn so sánh hắn lợi hại... Đến cùng hắn là thất tinh danh sư, vẫn là đối phương là?"),t("br"),t("br"),n._v("Có muốn hay không đánh như vậy đánh người?"),t("br"),t("br"),n._v("“May mắn ta danh sư cấp bậc cao hơn hắn, tại danh sư trên phương diện, mạnh mẽ hơn hắn rất nhiều, nếu không chỉ sợ thật bị so không bằng...”"),t("br"),t("br"),n._v("Trong lòng âm thầm thở dài một hơi, chính muốn nói chuyện, liền nghe đến một cái cút lôi thanh âm ầm vang vang lên."),t("br"),t("br"),n._v("“Ta Trương Huyền, lấy lục tinh danh sư thân phận, xin tổng bộ, xông Thanh Nguyên đế quốc Danh Sư đường, không chết... Không ngừng!”"),t("br"),t("br"),n._v("“Cái gì? Xông đường?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Trương Cửu Tiêu té ngã trên đất."),t("br"),t("br"),n._v("Xông đường, là chỉ lẻ loi một mình, khiêu chiến một tòa Danh Sư đường."),t("br"),t("br"),n._v("Có mâu thuẫn cũng tốt, có vấn đề cũng được, phạm sai lầm danh sư, chỉ cần không phải phản bội nhân tộc, có thể xông qua Danh Sư đường đủ loại ràng buộc, liền không tư cách vì đó định tội, tổng bộ cũng không truy cứu nữa."),t("br"),t("br"),n._v("Mới vừa cảm thấy mình còn có tên sư so với đối phương mạnh, sẽ không nhận đả kích, kết quả... Đối phương liền muốn một mình xông Danh Sư đường..."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Vội vã vọt tới bên ngoài phòng, lập tức nhìn thấy một cái danh sư huy chương, phóng lên tận trời, lơ lửng giữa không trung."),t("br"),t("br"),n._v("Danh Sư đường, Phong Sư điện bên trong một đạo tinh thuần khí tức, xông lên thiên đình, đem huy chương bao phủ, chậm rãi tạo thành hai chữ lớn: “Đồng ý!”"),t("br"),t("br"),n._v("“Tổng bộ... Đồng ý? Lục tinh danh sư xông thất tinh phân bộ... Cái này chỉ sợ muốn phá vỡ lịch sử đi!”"),t("br"),t("br"),n._v("“Nào chỉ là phá vỡ, nếu quả thật muốn thông qua... Chỉ sợ Thanh Nguyên đế quốc Danh Sư đường, đem mất hết mặt mũi, lại không mặt mũi sừng sững, cẩu đường chủ cũng sẽ biến thành tất cả danh sư trò cười...”"),t("br"),t("br"),n._v("Mục hội trưởng cùng Tề lão trợn mắt hốc mồm."),t("br"),t("br"),n._v("“Lão sư, Tề lão, thân là Thanh Nguyên đế quốc Danh Sư đường một thành viên, đối phương xông đường, ta muốn trở về nghênh chiến... Cáo từ!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu ôm quyền, vội vã hướng Danh Sư đường phương hướng bay vút đi."),t("br"),n._v("Có người xông đường, thì tương đương với có người đập phá quán, thân là trong đó danh sư, nhất định phải tới thủ hộ!"),t("br"),t("br"),n._v("“Trương Huyền, mặc kệ ngươi thiên phú như thế nào, muốn xông đường... Trước qua ta Trương Cửu Tiêu cái này liên quan lại nói!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trương Cửu Tiêu híp mắt lại."),t("br"),t("br"),n._v("Liên tục hai lần đánh mặt, đã như vậy... Vậy liền quang minh chính đại, đem mặt đánh lại!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thanh Nguyên hoàng cung."),t("br"),t("br"),n._v("Một cái tựa như đầu heo bộ dáng trung niên nhân nằm trên mặt đất, miệng lớn thở hổn hển."),t("br"),t("br"),n._v("Trung Thanh Vương."),t("br"),t("br"),n._v("Liên tục cuồng quất một đêm, độc trong người tạm lúc mặc dù bị áp chế lại, nhưng người cũng sắp đánh phế đi, nếu không phải công lực tinh thuần, thực lực mạnh mẽ, lại có vô số đan dược duy trì, chỉ sợ sớm đã chết rồi."),t("br"),t("br"),n._v("Bởi vì quất hắn, liên tục đổi hơn hai mươi tên hộ vệ, mỗi một cái đều bàn tay sưng tấy, toàn thân thoát lực."),t("br"),t("br"),n._v("“Vương gia, hiện tại cảm giác thế nào?”"),t("br"),t("br"),n._v("Tần quản sự đi tới gần."),t("br"),t("br"),n._v("“Không sao... Đúng, đem cái kia hai mươi cái đánh ta hộ vệ, tất cả đều giết! Chuyện của ta, không hy vọng bên ngoài người biết được!” Híp mắt lại, Trung Thanh Vương khẽ nói."),t("br"),t("br"),n._v("“Yên tâm đi, vương gia, ta đã an bài xong xuôi...” Tần quản sự vội nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Giãy dụa lấy ngồi dưới đất, điều tức nửa ngày, cảm thấy khôi phục một chút, lần nữa ngẩng đầu: “Điền phó đường chủ bên kia có tin tức ư?”"),t("br"),t("br"),n._v("“Ta đã đem Phó đường chủ bị giam giữ tin tức, dựa theo như ngươi nói vậy, truyền đến cẩu đường chủ nơi đó...”"),t("br"),t("br"),n._v("Nói đến đây, tần quản sự có chút chần chờ: “Cẩu đường chủ sẽ cứu Điền phó đường chủ? Mưu hại... Danh sư tội danh, ta nghe nói nhưng mà không nhỏ!”"),t("br"),t("br"),n._v("“Yên tâm đi, khẳng định sẽ cứu!”"),t("br"),t("br"),n._v("Trung Thanh Vương cười lạnh: “Chỉ cần Điền phó đường chủ không có việc gì, ta liền không sao, làm không cẩn thận còn có thể tuyệt địa phản kích! Để Ngô như phong cùng cái này Trương Huyền, chịu không nổi!”"),t("br"),t("br"),n._v("“Vậy là tốt rồi...” Tần quản sự thở phào nhẹ nhõm, hơi nghi hoặc một chút: “Vương gia, vị này Trương Huyền, đến cùng lai lịch gì? Một cái lục tinh danh sư, không chỉ đem vương gia ngươi, biến thành như vậy, liền Điền phó đường chủ đều bại té ngã?”"),t("br"),t("br"),n._v("“Không bất kể hắn là cái gì lai lịch, đắc tội Điền phó đường chủ, đều chết chắc... Yên tâm đi, nhảy nhót không được bao lâu, đoán chừng hôm nay hoặc là ngày mai, liền sẽ bị Danh Sư đường bắt, chết tại trong lao...”"),t("br"),t("br"),n._v("Trung Thanh Vương khoát tay áo."),t("br"),t("br"),n._v("Lời còn chưa dứt, liền nghe đến Trương Huyền lang lãng thanh âm vang vọng mà lên, chấn toàn bộ thiên địa, đều không ngừng run rẩy."),t("br"),t("br"),n._v("“Ta Trương Huyền, lấy lục tinh danh sư thân phận, xin tổng bộ, xông Thanh Nguyên đế quốc Danh Sư đường, không chết... Không ngừng!”"),t("br"),t("br"),n._v("“Xông đường? Ha ha, một cái lục tinh danh sư, muốn xông đường? Đây không phải muốn chết sao? Thanh Nguyên đế quốc Danh Sư đường nội tình, bát tinh hạ phẩm danh sư, cũng không dám xông... Một cái lục tinh liền muốn xông, thật là không biết trời cao đất rộng...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trung Thanh Vương cười ha ha, nước mắt đều nhanh muốn bật cười."),t("br"),t("br"),n._v("Xông đường độ khó, chỉ cần là danh sư, đều rất rõ ràng, có thể nói cửu tử nhất sinh, hầu như không người có thể hoàn thành."),t("br"),t("br"),n._v("Cái tên này lại muốn tới một lần, thật là bản thân tự tìm cái chết!"),t("br"),t("br"),n._v("“Bệ hạ!”"),t("br"),t("br"),n._v("Đang tại cười to, chỉ thấy Hoàng đế Sở Thiên đi đi nhanh tới, vội vàng hưng phấn hô lên: “Vị kia Trương Huyền muốn xông đường, xem ra không cần ta ra tay, cũng sống không lâu...”"),t("br"),t("br"),n._v("“Xông đường ta trước đó nghe qua, quả thực là hẳn phải chết không nghi ngờ...”"),t("br"),t("br"),n._v("Sở Thiên đi gật đầu, hơi nghi hoặc một chút nhìn qua: “Đúng rồi, trong lịch sử, xông đường thành công có hay không?”"),t("br"),t("br"),n._v("“Cái này... Ta không rõ ràng, có điều, đem Bạch sư kêu đến, chẳng phải sẽ biết...”"),t("br"),t("br"),n._v("Trung Thanh Vương nói."),t("br"),t("br"),n._v("Mặc dù Danh Sư đường mặc kệ trong triều đình sự tình, nhưng một chút lợi hại đế quốc, cũng có thể đưa tới một chút rải rác danh sư, để bản thân sử dụng."),t("br"),t("br"),n._v("Tên hội đường danh sư, tương đương với trong biên chế, rải rác danh sư, thì thuộc về tư nhân."),t("br"),t("br"),n._v("Vị này Bạch sư, chính là Thanh Nguyên đế quốc hộ quốc danh sư."),t("br"),t("br"),n._v("Sở Thiên đi gật gật đầu, phái người đi mời, không lâu sau, một cái ông lão liền đi tới."),t("br"),t("br"),n._v("“Bạch sư, ta muốn hỏi một chút, chúng ta Thanh Nguyên đế quốc trong lịch sử, có người hay không xông đường thành công?”"),t("br"),t("br"),n._v("“Bệ hạ chờ, ta để bằng hữu điều tra thêm!”"),t("br"),t("br"),n._v("Bạch sư gật đầu."),t("br"),t("br"),n._v("Hắn hiện tại không thuộc về Danh Sư đường danh sư, không thể nào điều tra, chỉ có thể dựa vào bằng hữu thẩm tra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Chỉ chốc lát, lòng bàn tay ngọc bài lấp lóe, Bạch sư nhìn thoáng qua, ôm quyền: “Bẩm báo bệ hạ, trong lịch sử, quả thực có người xông đường thành công, tổng cộng ba vị...”"),t("br"),t("br"),n._v("“Ba vị?”"),t("br"),t("br"),n._v("Nghe được số lượng ít như vậy, Trung Thanh Vương cùng Sở Thiên đi đồng thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Theo cầm giữ có danh sư chức nghiệp đến bây giờ, đã trải qua vài vạn năm, xuất hiện nhân vật thiên tài, như cá diếc sang sông, nhiều vô số kể, nhiều cường giả như vậy, chỉ có ba cái thông qua, đủ thấy độ khó."),t("br"),t("br"),n._v("Xem ra, vị này Trương Huyền, tuyệt không có khả năng thông qua."),t("br"),t("br"),n._v("“Bọn họ đều là ai, có thể có danh tự?”"),t("br"),t("br"),n._v("Trung Thanh Vương hỏi."),t("br"),t("br"),n._v("“Có... Cái thứ nhất gọi Lạc Thiên Vân, cái thứ hai gọi Viên Hi, một cái là Thánh Nhân Lạc gia lão tổ, một cái là Thánh Nhân Viên gia lão tổ...”"),t("br"),t("br"),n._v("“Cái thứ ba đâu?”"),t("br"),t("br"),n._v("Nhìn xem trên ngọc bài biểu hiện tên, Bạch sư dừng lại một chút."),t("br"),t("br"),n._v("“Hắn gọi...”"),t("br"),t("br"),n._v("“... Trương Huyền!”"),t("br"),t("br"),n._v("(Các ngươi quá xấu rồi, liền cầu cái nguyệt phiếu, các ngươi đều để ta tự sát, ta nhai nhai không phục!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1187"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1189"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);