(window.webpackJsonp=window.webpackJsonp||[]).push([[2245],{2600:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-943-tam-tac-chien-su-đuong-moi-nguoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-943-tam-tac-chien-su-đuong-moi-nguoi"}},[n._v("#")]),n._v(" Chương 943: Tâm tắc Chiến sư đường mọi người")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 943: Tâm tắc Chiến sư đường mọi người"),i("br"),i("br"),n._v("Hắn giảng bài, mặc dù có thể khiến người ta nhanh chóng tiến bộ, thực lực bạo tăng."),i("br"),i("br"),n._v("Nhưng cũng xem tu luyện giả thiên phú, có ít người trời sinh đối võ kỹ lĩnh ngộ chậm chạp, coi như nói nhiều hơn nữa, cũng là uổng công."),i("br"),i("br"),n._v("Lại nói... Vì để cho cái này một trăm vị học sinh, mau chóng tăng lên chân khí lực lượng cùng thân thể, hắn nhưng là hao tốn ròng rã năm trăm viên linh thạch thượng phẩm, toàn bộ học viện mười vạn học viên... Hắn có thể không bỏ ra nổi 500 ngàn viên linh thạch thượng phẩm, cũng bố trí không ra, to lớn như vậy Tụ Linh trận."),i("br"),i("br"),n._v("Cho nên, coi như lại thế nào giảng bài, làm cho cả học viện tất cả học sinh, đều trở thành Chiến sư, cũng không thực tế."),i("br"),i("br"),n._v("Lại nói, Chiến sư đường hàng năm đều hữu chiêu thu danh ngạch, coi như thực lực đủ rồi, có thể hay không trở thành một thành viên trong đó, cũng không phải ta quyết định, mà là cách đó không xa vị này..."),i("br"),i("br"),n._v("Muốn đến nơi này, nhịn không được quay đầu nhìn sang, muốn muốn nghe một chút Trác Thanh Phong ý kiến, lại thấy hắn da mặt cuồng quất, một mặt u oán."),i("br"),i("br"),n._v("Nói thật, thời khắc này Trác Thanh Phong, cảm thấy mình muốn chết."),i("br"),i("br"),n._v("Vị này Trương viện trưởng, thật muốn làm như vậy, sợ không bao lâu, toàn bộ Chiến sư đường, liền thành Hồng Viễn học viện một cái phân viện..."),i("br"),i("br"),n._v("“Khụ khụ, Trương viện trưởng, đối tại chúng ta Chiến sư đường tới nói, thiên tài tự nhiên càng nhiều càng tốt... Chỉ là, danh sư càng trọng yếu hơn, chỗ có danh sư đều thành Chiến sư, Hồng Viễn học viện chẳng phải liền tên thực tồn vong?”"),i("br"),i("br"),n._v("Đem trong lòng điên cuồng ngăn chặn, Trác Thanh Phong cắn răng nói."),i("br"),i("br"),n._v("Đối với Chiến sư đường tới nói, người mặc dù càng nhiều càng tốt, nhưng... Quá nhiều, tài nguyên liền sẽ trở nên khẩn trương, ngược lại không phải là chuyện tốt."),i("br"),i("br"),n._v("Thật giống như, một quốc gia nhất định phải có quân đội canh giữ, quân nhiều người, quốc gia càng thêm vững chắc... Nhưng số lượng quá nhiều lời nói, hao tổn quá lớn, cũng là không chịu nổi."),i("br"),i("br"),n._v("Đồ vật gì đều có cân bằng, hăng quá hoá dở."),i("br"),i("br"),n._v("Chính vì vậy, mỗi lần Chiến sư đường nhận người, đều là có tỉ lệ."),i("br"),i("br"),n._v("Người người đều thành Chiến sư, ai còn giáo thư dục nhân, ai còn truyền thụ tri thức?"),i("br"),i("br"),n._v("Người người chỉ chú trọng sát phạt, chiến đấu, thiên hạ chưa đủ lớn loạn?"),i("br"),i("br"),n._v("“Cũng phải...”"),i("br"),i("br"),n._v("Trương Huyền nhẹ gật đầu, từ bỏ muốn để tất cả mọi người trở thành Chiến sư ý định."),i("br"),i("br"),n._v("Mới vừa tiếp nhận danh sư học viện, liền trực tiếp khiến cho học sinh cũng bị mất... Đúng là không tốt lắm."),i("br"),i("br"),n._v("Chần chờ một chút, mở miệng nói: “Như vậy đi, một lát nữa, ta sẽ tại Huyền Huyền hội giảng bài, chỉ có hội viên chính thức, mới có thể nghe giảng. Bất quá, nghe xong khóa về sau, không cho phép khảo hạch Chiến sư!”"),i("br"),i("br"),n._v("Làm cho cả học viện học sinh, toàn bộ tăng lên, hắn làm không được, để Huyền Huyền hội người tiến bộ, vẫn là có thể."),i("br"),i("br"),n._v("Dù sao những người này rất sớm đã đi theo bản thân, xem như trung thành nhất một bộ phận."),i("br"),i("br"),n._v("Có trả giá liền muốn có hồi báo, thưởng phạt cân bằng, mới có thể để cho người nắm giữ động lực."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Nghe được viện trưởng thật muốn giảng bài, Huyền Huyền hội tất cả thành viên, tất cả đều hưng phấn gật đầu."),i("br"),i("br"),n._v("Coi như không thể khảo hạch Chiến sư, chỉ cần thực lực bạo tăng, cũng tuyệt đối kiếm lợi lớn."),i("br"),i("br"),n._v("“Xong rồi, lần này Chiến sư đường tuyển chọn kết thúc!”"),i("br"),i("br"),n._v("Trác Thanh Phong khoát tay áo: “Ba ngày sau, có mấy vị không phải danh sư tu luyện giả, muốn muốn khảo hạch Chiến sư, đến lúc đó lại ở chỗ này tổ chức, cũng hi vọng chư vị có thể tới quan sát!”"),i("br"),i("br"),n._v("“Không phải danh sư tu luyện giả muốn khảo hạch?”"),i("br"),i("br"),n._v("“Ai vậy?”"),i("br"),i("br"),n._v("“Ta cũng không biết...”"),i("br"),i("br"),n._v("Mọi người một hồi kỳ quái."),i("br"),i("br"),n._v("Trương Huyền cũng đầy là tò mò, nhịn không được hỏi lên: “Không biết là dạng gì thiên tài, liền danh sư đều không phải là, liền muốn khảo hạch Chiến sư?”"),i("br"),i("br"),n._v("“Khụ khụ!” Nghe được câu hỏi của hắn, Trác Thanh Phong bị nước bọt sặc một cái: “Là... Trương viện trưởng học sinh thân truyền!”"),i("br"),i("br"),n._v("“Ta thân truyền? Ngươi nói là Vương Dĩnh, Trịnh Dương bọn họ? Làm sao ngươi biết bọn họ?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Trịnh Dương hôm qua chạy tới khiêu chiến tất cả Chiến sư, hắn đang tại giảng bài, cũng không biết rõ tình hình."),i("br"),i("br"),n._v("“Là... Hôm qua ngươi vị kia kêu Trịnh Dương học sinh qua tới khiêu chiến chúng ta... Chúng ta cùng cấp bậc không ai là đối thủ, ngay cả ta đều thua...”"),i("br"),i("br"),n._v("Trác Thanh Phong tràn đầy xấu hổ."),i("br"),i("br"),n._v("“Cái tên này... Thật sự là quấy rối!” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Khó trách buổi sáng hôm nay, thấy Chiến sư đường người, đều uể oải, nguyên một đám tinh thần uể oải, dạng này chạy tới tiến công người, tự nhiên sẽ cảm thấy không chịu nổi."),i("br"),i("br"),n._v("Cái này Trịnh Dương, thật đúng là đủ quấy rối."),i("br"),i("br"),n._v("“Các ngươi cũng không cần xoắn xuýt, Trịnh Dương là ta học sinh thân truyền, dạy đã gần một năm, lúc trước Tụ Tức cảnh liền mang theo, cùng nhau đi tới, trải qua rất nhiều... Các ngươi đánh không lại cũng rất bình thường!”"),i("br"),i("br"),n._v("Thấy đối phương uể oải suy sụp, Trương Huyền nhịn không được an ủi."),i("br"),i("br"),n._v("Không an ủi còn tốt, vừa an ủi mọi người tất cả đều trước mắt càng thêm đen."),i("br"),i("br"),n._v("Dạy vẫn chưa tới một năm, theo Tụ Tức cảnh biến thành Tằm Phong cảnh cao thủ, vượt qua mười cái đại cấp bậc... Mấu chốt vẫn là có thể vượt cấp chiến đấu..."),i("br"),i("br"),n._v("Làm sao cảm giác, không an ủi còn tốt, an ủi qua đi, tâm càng tắc?"),i("br"),i("br"),n._v("Lại nói, cái gì gọi là đánh không lại bình thường?"),i("br"),i("br"),n._v("Chúng ta thế nhưng là Chiến sư... Sức chiến đấu mạnh nhất danh sư..."),i("br"),i("br"),n._v("“Không tán gẫu nữa, ta còn muốn dẫn bọn hắn tu luyện!”"),i("br"),i("br"),n._v("Ngực kìm nén khó chịu, Trác Thanh Phong biết cùng kẻ trước mắt này tiếp tục trò chuyện đi xuống, sớm muộn gì đều sẽ tươi sống ngộp chết, đứng dậy, giơ tay cáo từ."),i("br"),n._v("Có một loại thiên tài, làm cho người ta không nói được lời nào, mặc dù hắn không phải có ý, nhưng mỗi một câu đều buộc vào trái tim bên trên... Đoán chừng chính là kẻ trước mắt này."),i("br"),i("br"),n._v("Rõ ràng ánh mắt rất chân thành, nói chuyện rất thực sự, có thể... Vì sao nghe vào trong tai, tựa như đang không ngừng đánh mặt đâu?"),i("br"),i("br"),n._v("Chúng ta thế nhưng là thiên tài ah..."),i("br"),i("br"),n._v("Thấy hắn rời khỏi, tất cả Chiến sư, cũng đi theo sát, chỉ chốc lát liền đi sạch sẽ."),i("br"),i("br"),n._v("Ba đại viện trưởng mấy người cũng cảm thấy kìm nén khó chịu, biết tiếp tục tiếp tục chờ đợi, sẽ chỉ càng thêm mất mặt, cũng tìm cái cớ, vội vã mà đi, nếu không phải vì quan sát ba ngày sau tuyển chọn, đoán chừng sẽ lập tức mở ra Vân Nhai phi chu, biến mất tại tất cả mọi người trước mặt."),i("br"),i("br"),n._v("Mọi người rời khỏi, Trương Huyền đành phải lắc đầu, đứng dậy hướng Huyền Huyền hội tổng bộ phương hướng đi đến."),i("br"),i("br"),n._v("Chờ hắn đến lúc đó, đã người đông nghìn nghịt, Huyền Huyền hội người cơ bản đều đến đông đủ."),i("br"),i("br"),n._v("Mở đường giảng bài, cùng cho những người khác nói đồng dạng, chỉ là không có sử dụng sư ngôn thiên bẩm, hiệu quả kém rất nhiều, nhiều người không thể tại chỗ đột phá."),i("br"),i("br"),n._v("Dù vậy, hiệu quả cũng rất khá, tất cả hội viên, đều đối võ kỹ cùng kỹ xảo chiến đấu hiểu, gia tăng thật lớn, càng là học được mới luyện thể cùng rèn luyện chân khí pháp quyết, nguyên một đám hưng phấn không thôi."),i("br"),i("br"),n._v("Nói ròng rã năm canh giờ, trời lần nữa đen xuống, nghỉ ngơi một hồi, Trương Huyền cái này mới tìm được Mi trưởng lão, mở miệng phân phó."),i("br"),i("br"),n._v("“Chuẩn bị một chút, mang ta đi viện trưởng lăng đi!”"),i("br"),i("br"),n._v("Dù sao hiện tại ba đại viện trưởng, Chiến sư đường người đều tránh hắn chỉ sợ không kịp, cũng không cần chiêu đãi, tiết kiệm không ít phiền phức."),i("br"),i("br"),n._v("Cái kia lại vừa vặn đi viện trưởng lăng nhìn xem, bên trong Mục Khải tổ sư lưu lại một viên Lôi Minh thạch, có thể giúp hắn theo Bán Thánh đột phá đến Tòng Thánh."),i("br"),i("br"),n._v("Hiện tại đã đạt tới Bán Thánh đỉnh phong, cũng nên là thời điểm đi tìm thứ này, thuận tiện đột phá."),i("br"),i("br"),n._v("“Sớm nghĩ đến viện trưởng sẽ đi, cho nên viện trưởng lục tinh danh sư khảo hạch thông qua tin tức một truyền đến, ta liền hướng tổng bộ xin một cái phân hoá hồn phách bảo vật, trước mấy ngày liền đưa đến...”"),i("br"),i("br"),n._v("Mi trưởng lão cười gật đầu."),i("br"),i("br"),n._v("Trước đó, không cho vào, là bởi vì không phải lục tinh danh sư, không có cách nào xin phân hoá hồn phách bảo vật, mà quá khứ liền muốn lưu lại ý niệm, đối linh hồn sẽ có rất lớn tổn thương."),i("br"),i("br"),n._v("Hiện tại khảo hạch lục tinh danh sư thành công, bảo vật cũng xin, tự nhiên muốn mang đi qua nhìn một chút."),i("br"),i("br"),n._v("“Tốt!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Hai người hướng viện trưởng lăng phương hướng đi đến, mới đi đến trước mặt, Triệu Bính Tuất liền đi tới, đem tổng bộ xin cái này phân hoá linh hồn bảo vật đưa tới."),i("br"),i("br"),n._v("“Là kiện thanh hồn chuông, chỉ cần đem thứ này cầm ở lòng bàn tay, sẽ tự động ôn dưỡng linh hồn, để hắn chia ra một cái có thể lưu giữ ý niệm!”"),i("br"),i("br"),n._v("Triệu Bính Tuất giải thích."),i("br"),i("br"),n._v("Trương Huyền nhìn sang, quả nhiên là cái kích thước không lớn toàn vẹn chuông, so với hắn nắm đấm lớn hơn không được bao nhiêu, đặt ở lòng bàn tay, lập tức cảm thấy một dòng nước ấm tràn vào thức hải, để linh hồn của hắn, không ngừng gia tăng."),i("br"),i("br"),n._v("“Quả nhiên là đồ tốt...”"),i("br"),i("br"),n._v("Ánh mắt sáng lên."),i("br"),i("br"),n._v("Tu luyện Vu hồn, phân hoá linh hồn không khó, người bình thường liền có chút phiền phức, trước đó còn kỳ quái, học viện nhiều như vậy viện trưởng, làm sao đem ý niệm lưu lại, nguyên lai là có loại bảo vật này."),i("br"),i("br"),n._v("Ôn dưỡng linh hồn, sau đó lại tách ra, lấy thứ này làm vật trung gian, coi như lưu giữ trên vạn năm, cũng đồng dạng sẽ không tiêu tán."),i("br"),i("br"),n._v("Dù sao, chỉ là lưu giữ, cũng không phải là sống sót, hồn lực ngũ suy ảnh hưởng liền sẽ không rất lớn."),i("br"),i("br"),n._v("“Viện trưởng chỉ cần cầm trong tay viện trưởng lệnh, liền có thể mở ra môn hộ, trực tiếp đi vào, ta chờ không có tư cách liền không thể cùng tiến vào...”"),i("br"),i("br"),n._v("Mi trưởng lão hai người cười cười."),i("br"),i("br"),n._v("“Ừm!” Gật gật đầu, Trương Huyền hướng trước mắt viện trưởng lăng nhìn lại."),i("br"),i("br"),n._v("Vẫn tại trưởng lão viện phía trong, xây dựa lưng vào núi, bố trí đủ loại to lớn ẩn nặc trận pháp, đem trọn cái lăng mộ, toàn bộ che lấp, thậm chí đỉnh núi đều như là biến mất, nếu không phải hai đại trưởng lão dẫn hắn tới, căn bản không phát hiện được."),i("br"),i("br"),n._v("Xem ra cái này lịch đại viện trưởng ngủ yên chỗ, tại danh sư học viện trình độ trọng yếu, thậm chí vượt qua Tàng Thư các."),i("br"),i("br"),n._v("Cổ tay khẽ đảo, đem viện trưởng lệnh cầm ở lòng bàn tay, nhẹ nhàng hướng về phía trước chiếu một cái, trận pháp lập tức xuất hiện một vết nứt, nhấc chân đi vào."),i("br"),i("br"),n._v("Vù vù!"),i("br"),i("br"),n._v("Sau một khắc, một hồi lay động, người đã rơi vào một cái xanh biếc trong núi rừng."),i("br"),i("br"),n._v("Núi cũng không cao, ngược lại như cái đồi núi, cùng cảnh tượng bên ngoài hoàn toàn khác biệt, dọc theo đá xanh trải tốt đường đi, Trương Huyền đi vài bước, một cái to lớn bia đá chặn tại phía trước."),i("br"),i("br"),n._v("Chừng bảy, cao tám mươi mét, độ rộng cũng đạt tới hơn hai mươi mét, không biết cái gì điêu khắc mà thành, mang theo nguy nga già nua khí tức, cho người ta một loại xuyên việt lịch sử cảm giác."),i("br"),i("br"),n._v("“Chẳng lẽ đây chính là lăng bia?” Trương Huyền cảm thấy rất ngờ vực."),i("br"),i("br"),n._v("Trước đó, Mi trưởng lão từng nói qua, đi vào viện trưởng lăng cần đem ý niệm lưu tại lăng bia phía trên, hiện tại xuất hiện một cái to lớn như vậy bia đá, hẳn là thứ này."),i("br"),i("br"),n._v("Dọc theo bia đá dạo qua một vòng, quả nhiên cảm nhận được trong đó nồng đậm lực lượng linh hồn, dường như có vô số ý niệm chiếm cứ trong đó, cho người ta một loại nặng nề như núi, trấn áp mặt đất cảm giác."),i("br"),i("br"),n._v("“Xem ra là lăng bia không thể nghi ngờ...”"),i("br"),i("br"),n._v("Trương Huyền nhẹ gật đầu: “Trước đi tìm Mục Khải tổ sư lăng mộ đi, tìm tới Lôi Minh thạch tại đem linh hồn phân hoá đi vào!”"),i("br"),i("br"),n._v("Tới này mục đích là vì tìm kiếm Lôi Minh thạch, phân hoá linh hồn, lúc gần đi hoàn thành là được, dù sao vừa mới luyện hóa thanh hồn chuông, còn làm không được phân hoá hồn phách."),i("br"),i("br"),n._v("Vòng qua bia đá, dọc theo đá đường, chậm rãi đi vào trong."),i("br"),i("br"),n._v("Toàn bộ viện trưởng lăng an tĩnh trang nghiêm, cho người ta một loại cảm giác ngột ngạt, Trương Huyền biết đây là học viện cấm địa, cũng chỉ nhiều dừng lại, tăng nhanh tốc độ."),i("br"),i("br"),n._v("Không lâu sau, một cái lăng mộ liền xuất hiện tại cách đó không xa, phía trên một hàng chữ, tiến vào mí mắt —— danh sư học viện thứ một trăm lẻ ba đảm nhiệm viện trưởng chi mộ!"),i("br"),i("br"),n._v("“Một trăm lẻ ba đảm nhiệm, không phải là lão viện trưởng ư? Không phải mất tích, chưa từng tới nơi này ư? Lăng mộ tại sao lại ở chỗ này?”"),i("br"),i("br"),n._v("Trương Huyền sửng sốt một cái."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-951"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-953"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);