(window.webpackJsonp=window.webpackJsonp||[]).push([[2246],{2601:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-944-tien-thien-thai-đoc-tin-tuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-944-tien-thien-thai-đoc-tin-tuc"}},[n._v("#")]),n._v(" Chương 944: Tiên Thiên thai độc tin tức?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 944: Tiên Thiên thai độc tin tức?"),t("br"),t("br"),n._v("Hắn là thứ 104 đảm nhiệm viện trưởng, một trăm lẻ ba đảm nhiệm, tự nhiên cũng chính là đời trước, Tử Dương thú chủ nhân trước."),t("br"),t("br"),n._v("Vị danh sư kia học viện mất tích lão viện trưởng!"),t("br"),t("br"),n._v("Căn cứ hắn biết đến tin tức, vị viện trưởng này, cũng không có từng tiến vào nơi này, mất tích về sau cũng là không rõ sống chết, làm sao nơi này đột nhiên nhiều hơn một tòa phần mộ?"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, Trương Huyền nhìn kỹ đi qua."),t("br"),t("br"),n._v("Trước mắt lăng mộ, dùng bình thường đá xanh xây dựng, cùng phía ngoài mộ huyệt không có bất kỳ cái gì hai loại, không có chút nào thất tinh danh sư cái kia có địa vị cùng tôn quý."),t("br"),t("br"),n._v("Dạo qua một vòng, rất nhanh phát hiện một chỗ không đúng, mày nhăn lại, đi tới mộ bia trước mặt, ở phía trên nhẹ nhàng phất một cái, một cái cơ quan liền bị dẫn động, lập tức một tiếng nói già nua chậm rãi vang lên."),t("br"),t("br"),n._v("Cùng Địa Cung đồng dạng, là một loại cơ lò xo lưu tiếng đồ vật."),t("br"),t("br"),n._v("“Ta là danh sư học viện thứ một trăm lẻ ba đảm nhiệm viện trưởng, Chương Dẫn Khâu. Lần này ra ngoài, đoán chừng rất khó trở về, liền ích kỷ thoáng cái, ai cũng không có nói cho, lặng lẽ đi vào nơi này, trước thời hạn đem bản thân mộ huyệt sửa chữa tốt, bên trong có ta tùy thân quần áo, xem như mộ chôn quần áo và di vật đi, cũng làm cho sau khi chết, có cái chỗ ở...”"),t("br"),t("br"),n._v("Cơ lò xo lưu âm, nói mặc dù bình thản như nước, nhưng Trương Huyền từ đó nghe được nồng đậm bất đắc dĩ."),t("br"),t("br"),n._v("Hào phóng hy sinh dễ, ung dung chịu chết khó!"),t("br"),t("br"),n._v("Biết rõ hẳn phải chết, trước thời hạn làm ra chuẩn bị, cái kia có bao nhiêu bi thương?"),t("br"),t("br"),n._v("Trước đó một mực nghe nói, lão viện trưởng tất cả sự tích, lại từ Vưu Hư trong miệng biết, bị hãm hại trải qua, vẫn cảm thấy hắn là không có chính xác ước định thí luyện nguy hiểm, mới khốn nhập hiểm cảnh."),t("br"),t("br"),n._v("Hiện tại xem ra cũng không phải là như vậy."),t("br"),t("br"),n._v("Trước thời hạn đi tới viện trưởng lăng, vì chính mình tu một tòa phần mộ, nhất định là đã biết lần này đi nguy hiểm, trước thời hạn làm xong đường lui."),t("br"),t("br"),n._v("“Chưa lưu lại ý niệm, canh giữ học viện, là ta cả đời việc đáng tiếc! Không phải là không muốn lưu, mà là ta việc cần phải làm, so cái này càng trọng yếu hơn, không cho phép linh hồn của ta, có bất kỳ tổn thất cùng thiếu hụt. Coi như phân hoá linh hồn bảo vật có thể giảm bớt tổn thương, khả thi ở giữa đã tới không kịp để cho ta linh hồn chia rẽ!”"),t("br"),t("br"),n._v("Thanh âm tiếp tục nói."),t("br"),t("br"),n._v("Phân hoá linh hồn bảo vật, có thể không phải vu hồn sư tu luyện giả, thành công phân chia ra ý niệm, nhưng muốn thành công, cần thời gian rất lâu ôn dưỡng."),t("br"),t("br"),n._v("Nếu không, đối linh hồn vẫn như cũ không nhỏ tổn thương."),t("br"),t("br"),n._v("Cũng chỉ có Trương Huyền dạng này, tu luyện Thiên Đạo Vu hồn, lại thêm hồn phách vô cùng to lớn, mới có thể nhẹ nhõm như ý, không bị ảnh hưởng, nếu không, liền xem như thất tinh danh sư, đều không được."),t("br"),t("br"),n._v("“Có thể đến nơi đây, cũng kích hoạt cơ quan, nói rõ ngươi là tân nhiệm viện trưởng, có lẽ chúng ta vẫn là quen biết đã lâu. Hy vọng có thể đối xử tử tế Tử Dương thú, nói cho nó biết, ta là vì nhân tộc mà chết, quang vinh mà vĩ đại, không có chút nào oan ức cùng không cam lòng, không cần lo lắng. Còn có, phải cẩn thận Vân Vụ lĩnh Thánh thú...”"),t("br"),t("br"),n._v("Than thở một tiếng, thanh âm tiếp tục."),t("br"),t("br"),n._v("Lão viện trưởng xem ra, một khi hắn mất tích, học viện có thể kế thừa vị trí hắn, tổng cộng liền mấy người kia, xem như trước thời hạn dặn dò hậu sự."),t("br"),t("br"),n._v("Chỉ là nằm mộng cũng muốn đến, vị này tân nhiệm viện trưởng, mặc dù trước đến giờ chưa thấy qua, nhưng đem hắn giao phó hai chuyện, trước thời hạn hoàn thành."),t("br"),t("br"),n._v("“Ngươi muốn đi làm cái gì?” Biết rõ cơ quan, không có cách nào trả lời, Trương Huyền vẫn hỏi đi ra."),t("br"),t("br"),n._v("Nghe đối phương giọng điệu, biết hẳn phải chết, nhưng lại không thể không đi làm, thậm chí không tiếc trước thời hạn xây dựng mộ chôn quần áo và di vật... Đến cùng làm gì?"),t("br"),t("br"),n._v("Không chỉ là một lần thí luyện ư?"),t("br"),t("br"),n._v("“Khả năng ngươi biết tin tức, ta chỉ là trải qua một lần bình thường thí luyện, mà trên thực tế, cũng không phải là như vậy. Chuyện này, dính dáng Khổng sư lúc tuổi còn trẻ bí mật, còn giống như cùng độc sư chức nghiệp liên quan đến, ta biết cũng không tỉ mỉ, chỉ có tận mắt đi gặp, mới có thể xác nhận! Mặt khác, dính dáng vạn thế chi sư, không thể nói bừa, chỉ có thể nói năng thận trọng!”"),t("br"),t("br"),n._v("Thanh âm nói."),t("br"),t("br"),n._v("“Khổng sư lúc tuổi còn trẻ bí mật? Cùng độc sư chức nghiệp liên quan đến?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lập tức thân thể cứng đờ: “Chẳng lẽ là... Tiên Thiên thai độc?”"),t("br"),t("br"),n._v("Căn cứ hắn biết đến tin tức, Khổng sư lúc còn trẻ giống như hắn, cũng mắc có Tiên Thiên thai độc, chỉ có tại ba mươi tuổi phía trước, xung kích cửu tinh danh sư thành công, có lẽ mới có thể giải quyết tình huống tuyệt vọng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, một mực cố gắng tu luyện, mảy may cũng không dám ngừng lại."),t("br"),t("br"),n._v("Vị này lão viện trưởng nhắn lại, lại còn nói dính dáng Khổng sư tuổi trẻ bí mật, cùng độc sư liên quan đến, để hắn làm sao không kích động?"),t("br"),t("br"),n._v("“Nếu như ngươi thật muốn biết, có thể đi một chuyến Hồng Viễn đế quốc Độc điện. Chỉ là nơi này, từ trước tới nay bí ẩn, nhất là đối danh sư, rất là kính sợ, vị trí cụ thể, ta điều tra rất nhiều năm, đều không rõ ràng, có điều, căn cứ phỏng đoán, có lẽ tại Tĩnh Viễn thành lân cận. Nơi này sẽ có vì thí luyện chỗ bản đồ chi tiết, chỉ tiếc, thời gian không cho phép ta xem kỹ, làm sách lược vẹn toàn, chỉ có thể đi trước đi qua...”"),t("br"),t("br"),n._v("“Kỹ càng minh hoạ?” Trương Huyền nghi ngờ thoáng cái, lập tức hiểu được."),t("br"),t("br"),n._v("Vị này lão viện trưởng, địa phương muốn đi, mười phần nguy hiểm."),t("br"),t("br"),n._v("Còn nếu như có thể tại Độc điện tìm tới bản đồ chi tiết, tất nhiên sẽ an toàn rất nhiều, chỉ tiếc chuyện gì tương đối gấp, buộc hắn phải đi làm, lúc này mới bí quá hoá liều, một đi không trở lại."),t("br"),t("br"),n._v("Đối phương đem chuyện này lưu lại, rất rõ ràng là muốn cho hậu bối viện trưởng, dùng đầy đủ thời gian chuẩn bị, mới quyết định."),t("br"),t("br"),n._v("“Xong rồi, có thể nói chỉ có những này, như thế nào lấy hay bỏ, liền xem chính ngươi.”"),t("br"),t("br"),n._v("Thanh âm chậm rãi tiêu tán, giống như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Biết chỉ là nhắn lại, cũng không phải là người thật, Trương Huyền vẫn là cung kính bái một cái."),t("br"),t("br"),n._v("Không phải cảm ơn hắn nói ra liên quan tới Tiên Thiên thai độc tin tức, mà là tôn kính biết rõ hẳn phải chết, còn việc nghĩa chẳng từ nan thái độ."),t("br"),t("br"),n._v("Biết lần này đi, khả năng không trở về, trước thời hạn để Tử Dương thú lưu lại, càng là lưu lại mộ chôn quần áo và di vật, nhắc nhở hậu nhân... Vẫn là đi, thậm chí liền ý niệm đều không có lưu lại!"),t("br"),t("br"),n._v("Cái này cùng lúc trước Vân Vụ lĩnh những cái kia hào phóng chịu chết danh sư đồng dạng, vì trong lòng kiên trì, tính mạng gì gì đó đều có thể không để ý."),t("br"),t("br"),n._v("Có lẽ đây mới là danh sư, để vô số người tre già măng mọc chân chính mị lực!"),t("br"),n._v(" Chân chính để cho người ta tôn kính chỗ."),t("br"),t("br"),n._v("Cẩu lợi nhân tộc sống chết lấy, *!"),t("br"),t("br"),n._v("Lắc đầu, Minh Lý Chi Nhãn nhúc nhích, lại không có phát hiện cái khác cơ quan hoặc là cấm chế, Trương Huyền lúc này mới tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Con đường hai bên, mọc như rừng cái khác viện trưởng mộ huyệt, phía trên đều viết cuộc đời cùng trải qua, nhìn một hồi, Trương Huyền nhịn không được than thở."),t("br"),t("br"),n._v("Những này quyền cao chức trọng viện trưởng, lại có một bộ phận lớn đều không phải là thọ hết chết già, mà là cùng Dị Linh tộc nhân đối kháng quá trình bên trong tử vong."),t("br"),t("br"),n._v("Thật giống như mệnh danh thiên phú đệ nhất Mạc Lưu Chân viện trưởng, cũng là đi vào Địa quật về sau, lại không có trở về."),t("br"),t("br"),n._v("“Dị Linh tộc nhân không phải rất ít gặp ư? Làm sao lại đều chết tại bọn họ tay?” Trương Huyền trầm lặng."),t("br"),t("br"),n._v("Theo Thiên Huyền vương quốc cùng nhau đi tới, đã gần thời gian một năm, trong khoảng thời gian này, tổng cộng cũng liền bái kiến một đầu còn sống Dị Linh tộc nhân, hai mươi đầu khôi lỗi, cùng vỡ vụn quá quắt Ngoan Nhân..."),t("br"),t("br"),n._v("Cái chủng tộc này, không phải hoàn toàn bị đuổi đi ư?"),t("br"),t("br"),n._v("Thấy thế nào những viện trưởng này tình huống, hầu như đều vì đối kháng bọn họ mà chết?"),t("br"),t("br"),n._v("“Trước đem Lôi Minh thạch lấy ra lại nói! Mặc kệ đối kháng không đối kháng Dị Linh tộc, cũng phải có thực lực làm dựa dẫm...”"),t("br"),t("br"),n._v("Lại nhìn mười cái mộ bia, phía trên cũng không có giải thích, Trương Huyền biết tạm thời cũng nghĩ không thông, tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("Mục Khải Mục sư, là đời thứ nhất tổ sư, tại toàn bộ viện trưởng lăng tận cùng bên trong, sau mười mấy phút, rốt cục đi tới trước mặt."),t("br"),t("br"),n._v("Cái mộ huyệt này, thoạt nhìn cùng cái khác đồng dạng đơn giản, cung kính dập đầu mấy cái, lúc này mới lấy ra khôi lỗi cho viên kia đặc thù lệnh bài, ngón tay búng một cái, chậm rãi hướng về phía trước bay đi."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Lệnh bài đi tới mộ huyệt bầu trời, lập tức mở ra một cấm chế, một tiếng nổ vang, một cái trữ vật giới chỉ từ dưới đất chậm rãi bay lên."),t("br"),t("br"),n._v("Ngón tay nhất câu, nhẫn rơi vào lòng bàn tay, Trương Huyền chân khí tiến vào bên trong, nhìn sang."),t("br"),t("br"),n._v("“Thật nhiều bảo vật!”"),t("br"),t("br"),n._v("Trong giới chỉ, không thiếu trân quý khoáng thạch cùng bảo vật, bản thân cần viên kia Lôi Minh thạch, thình lình trưng bày trong đó."),t("br"),t("br"),n._v("Cũng đều là Mục Khải tổ sư khi còn sống thu thập."),t("br"),t("br"),n._v("Sau khi chết táng tại mộ huyệt, lưu cho có cần hậu nhân, không cầu hồi báo, danh sư cao quý, đủ thấy đốm."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Tướng lệnh bài cùng nhẫn thu hồi, lần nữa cung kính đối mộ huyệt thi lễ một cái, Trương Huyền lúc này mới đi ra ngoài."),t("br"),t("br"),n._v("Lôi Minh thạch tới tay, cũng nên tu luyện xung kích Tòng Thánh."),t("br"),t("br"),n._v("Không lâu sau, trở lại lối vào lăng bia trước mặt."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem trước thanh hồn chuông lấy ra ngoài."),t("br"),t("br"),n._v("Lúc này thanh hồn chuông, vẫn tại không ngừng tẩm bổ linh hồn, mặc dù hiệu quả không là rất lớn, nhưng chỉ cần kết hợp lâu, tất nhiên có thể hình thành độc lập ý thức."),t("br"),t("br"),n._v("“Sử dụng Vu hồn chia lìa phương pháp đi...”"),t("br"),t("br"),n._v("Biết dạng này tách rời ý niệm, không biết phải hao phí bao lâu, Trương Huyền lắc đầu, khoanh chân ngồi dưới đất, tinh thần khẽ động, Vu hồn xuất thể."),t("br"),t("br"),n._v("To lớn hồn phách, cao tới hơn mười mét, dựa theo lúc trước Mặc Hồn Sinh lĩnh ngộ phương pháp, nhẹ nhàng nhúc nhích, rất nhanh một cái cùng thân thể giống nhau như đúc hồn phách xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Tách rời hồn phách, đối bản nguyên tổn thương xác thực rất lớn...”"),t("br"),t("br"),n._v("Phân chia ra mới hồn phách, hồn thể lần nữa thu nhỏ đến khoảng mười mét, Trương Huyền cảm thấy nồng đậm mệt mỏi, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Cho dù chỉ là tách rời một phần nhỏ, không nguyên tác tôn một phần mười, vẫn như cũ để hắn, có chút không chịu nổi."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, tại Vân Vụ lĩnh lấy được cái kia Thanh Giác Long thú tàn hồn, xuất hiện ở trước mắt, hóa thành từng đạo ấm áp khí lưu, chậm chạp tẩm bổ."),t("br"),t("br"),n._v("Dám trực tiếp vận dụng Vu hồn tách rời, không sợ làm bị thương bản nguyên, cũng bởi vì hắn có cái này làm dựa dẫm."),t("br"),t("br"),n._v("Cái này tàn hồn, đã bị Ngoan Nhân xóa sạch bản thân ý niệm, có thể tùy thời hấp thu, không cần lo lắng gặp phải phản phệ."),t("br"),t("br"),n._v("Hắn bên này hấp thu tàn hồn, khôi phục tổn thương tiêu hao, chia ra hồn phách, thì thân thể nhoáng một cái chui vào thanh hồn chuông bên trong."),t("br"),t("br"),n._v("Cái này hồn phách, không có gửi thân chỗ, sẽ tiêu hao bản nguyên, trải qua hồn lực ngũ suy, cuối cùng tiêu tán."),t("br"),t("br"),n._v("Thanh hồn chuông chính là bảo tồn ý niệm chỗ, lịch đại viện trưởng, cũng là như vậy."),t("br"),t("br"),n._v("“Cả hai dung hợp, đi vào lăng bia, là được...”"),t("br"),t("br"),n._v("Hồn phách cùng thanh hồn chuông dung hợp, Trương Huyền tách ra cỗ này ý niệm, thở phào nhẹ nhõm, tinh thần khẽ động, chậm rãi bay lên, trực tiếp hướng trước mắt lăng bia bay đi."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Thân thể cùng lăng bia tiếp xúc, lập tức cảm thấy một cỗ to lớn hấp lực tuôn ra mà đến, lập tức thân thể nhoáng một cái, theo biến mất tại chỗ, sau một khắc, xuất hiện tại một cái phòng kín mít bên trong."),t("br"),t("br"),n._v("Cùng hắn nói là cái gian phòng, còn không bằng nói là sơn động, ngay sau đó một cỗ đặc thù ý niệm xông vào trong óc, để hắn kìm lòng không được tê cả da đầu."),t("br"),t("br"),n._v("“Đây là... Dị Linh tộc nhân sát lục chi khí?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Trương Huyền cỗ này ý niệm, không khỏi cứng ngắc."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-952"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-954"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);