(window.webpackJsonp=window.webpackJsonp||[]).push([[2256],{2611:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-953-that-gia-kim-diep-vuong-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-953-that-gia-kim-diep-vuong-ha"}},[n._v("#")]),n._v(" Chương 953: Thật giả Kim Diệp Vương (hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 953: Thật giả Kim Diệp Vương (hạ)"),i("br"),i("br"),n._v("Cũng khó trách Kim Diệp Vương nổi điên."),i("br"),i("br"),n._v("Thanh Diệp Vương truyền âm qua tới tiếng nói, đem hắn từ trong ra ngoài, cào toàn bộ, ngay cả quần lót màu sắc, đều rõ như lòng bàn tay."),i("br"),i("br"),n._v("Bị một cái chưa từng thấy qua người, nói như vậy kỹ càng, mấu chốt nhất còn không biết hắn là ai... Mãnh liệt sợ hãi, để hắn có chút phát điên."),i("br"),i("br"),n._v("“Ta là ai? Ta là Kim Diệp Vương!”"),i("br"),i("br"),n._v("Trương Huyền hất lên ống tay áo: “Ta ngược lại muốn hỏi một chút ngươi là ai? Vì sao phải giả mạo ta?”"),i("br"),i("br"),n._v("“Giả mạo ngươi? Ngươi nói láo...” Kim Diệp Vương hô hấp dồn dập."),i("br"),i("br"),n._v("“Thanh Diệp Vương, chắc hẳn ngươi cũng thấy đấy, vừa rồi ta nói, càng nhiều kỹ lưỡng hơn.”"),i("br"),i("br"),n._v("Không thèm để ý đối phương, Trương Huyền nhìn về phía cách đó không xa Thanh Diệp Vương, sắc mặt nghiêm túc: “Bất quá, hiện tại truy cứu những này đều không có ý nghĩa, chúng ta tiếp tục tiếp tục như thế, ngược lại để đám này danh sư, chê cười! Không bằng dạng này, các ngươi rời đi trước, không yên lòng lời nói, cũng có thể đem chung quanh toàn bộ bao vây. Ta có một số việc, muốn đơn độc tiến hành hỏi thăm, không tiện để bên ngoài người biết được... Cái này dính dáng chúng ta tộc nhân lớn cơ mật, là phía trên giao xuống nhiệm vụ, không thể sai sót!”"),i("br"),i("br"),n._v("Mục đích của hắn cũng không phải là nhất định phải để cho đối phương tin tưởng mình chính là Kim Diệp Vương."),i("br"),i("br"),n._v("Cái này không quá hiện thực, cuối cùng thật ở phía đối diện."),i("br"),i("br"),n._v("Mà là kéo dài thời gian... Chỉ cần làm cho đối phương cảm thấy khó bề phân biệt, đầu óc choáng váng, cũng đã hoàn thành mục đích."),i("br"),i("br"),n._v("“Đơn độc hỏi thăm?”"),i("br"),i("br"),n._v("Thanh Diệp Vương cau mày."),i("br"),i("br"),n._v("“Yên tâm đi, bên ngoài nhiều người như vậy, bọn họ những người này coi như muốn chạy trốn cũng trốn không thoát, ta thẩm vấn xong xuôi, liền ra tới lần nữa giằng co, chứng minh thân phận chân thật của ta!”"),i("br"),i("br"),n._v("Trương Huyền khoát tay áo."),i("br"),i("br"),n._v("“Không biết muốn thẩm vấn cái gì, chúng ta có thể hay không tại đây dự thính?” Thanh Diệp Vương nói."),i("br"),i("br"),n._v("“Đương nhiên không được! Ta trước thời hạn phá vỡ phong ấn, đi tới nơi này, chính là gánh vác vô cùng lớn lớn trách nhiệm, một khi xảy ra vấn đề, phá hủy toàn bộ đại kế, các ngươi ai gánh được trách nhiệm?”"),i("br"),i("br"),n._v("Lông mày nâng lên, Trương Huyền ánh mắt như điện (giật)."),i("br"),i("br"),n._v("“Ít nghe hắn nói bậy, khẳng định là tại nói sang chuyện khác...” Kim Diệp Vương nói."),i("br"),i("br"),n._v("“Nói sang chuyện khác? Thật là lớn gan chó! Chúng ta tộc nhân năm đó bị Khổng sư đuổi đi, ở ẩn nhiều năm như vậy, thật vất vả tìm tới cơ hội... Ta chỉ hỏi ngươi một câu, một khi xảy ra chuyện, chỉ bằng ngươi một cái Vương Giả, chịu đựng nổi ư?”"),i("br"),i("br"),n._v("Bỗng nhiên trong chớp mắt, Trương Huyền khí tức áp bức mà đến, hai mắt nhìn chằm chằm đối diện Kim Diệp Vương, giống như là muốn đem hắn ăn đi."),i("br"),i("br"),n._v("“Ta...” Cảm nhận được mãnh liệt áp bức, Kim Diệp Vương nghẹn lời."),i("br"),i("br"),n._v("Bọn họ tộc nhân năm đó bị Khổng sư đuổi rời khỏi đại lục, trốn tại tối tăm không ánh mặt trời, âm trầm vô cùng vực ngoại chiến trường, bao nhiêu năm rồi, một mực lấy đoạt lại đại lục làm mục tiêu, chuyện này thật muốn bị hắn phá hư... Chết đến một vạn lần, đều khẳng định không đủ."),i("br"),i("br"),n._v("Đang không biết trả lời như thế nào, liền nghe đến cách đó không xa Thanh Diệp Vương, truyền âm tới: “Ân huynh, ngươi đến cùng có biết hay không hắn?”"),i("br"),i("br"),n._v("Theo ở sâu trong nội tâm, Thanh Diệp Vương vẫn tin tưởng trước mắt vị này."),i("br"),i("br"),n._v("“Ta nơi nào nhận biết?” Kim Diệp Vương mặt mũi buồn bực."),i("br"),i("br"),n._v("“Không được... Chúng ta liền đi ra ngoài trước! Dù sao chung quanh, đều là người của chúng ta, cũng không có khả năng chạy trốn! Ngộ nhỡ hắn nói là sự thật, làm trễ nải đại sự, chúng ta liền tội danh lớn!”"),i("br"),i("br"),n._v("Chần chờ một chút, Thanh Diệp Vương nói."),i("br"),i("br"),n._v("Mặc dù hắn tin tưởng vị này là Kim Diệp Vương, nhưng đối phương tuỳ tiện nói ra thiếu hụt, trong cơ thể càng là nắm giữ tinh thuần vô cùng sát lục chân khí, không có chỗ nào không tại cho thấy, thân phận không đơn giản."),i("br"),i("br"),n._v("Vạn nhất là cấp trên phái tới người, thật có chuyện quan trọng muốn làm, ngược lại bị bọn họ chậm trễ, liền phiền toái."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Kim Diệp Vương chần chừ."),i("br"),i("br"),n._v("Cũng hơi nghi hoặc một chút."),i("br"),i("br"),n._v("Mặc dù đối với đối phương giả mạo thân phận của hắn, vô cùng tức giận, nhưng cái tên này dám như thế không có sợ hãi, thậm chí đối với hắn rõ như lòng bàn tay, tất nhiên có chỗ ỷ lại."),i("br"),n._v(" “Mặc kệ là hắn giả mạo ngươi, vẫn là ngươi giả mạo hắn, đều là chúng ta nội bộ chuyện, chỉ cần thời gian đầy đủ, rất dễ giải quyết! Cùng lắm thì các ngươi từng người thi triển am hiểu nhất tuyệt chiêu, thông qua chiêu số, cũng có thể nhìn ra được!”"),i("br"),i("br"),n._v("Thanh Diệp Vương tiếp tục nói: “Mà bây giờ, đối phương muốn thẩm vấn danh sư, thật dính dáng phía trên quyết định, chúng ta không gánh nổi...”"),i("br"),i("br"),n._v("“Ừm!” Kim Diệp Vương gật đầu."),i("br"),i("br"),n._v("Bọn họ mặc dù là Vương Giả, tại tất cả Dị Linh tộc nhân bên trong, địa vị kính trọng, nhưng trên thực tế lại biết, loại thân phận này không đáng giá nhắc tới."),i("br"),i("br"),n._v("Vương Giả bên trong, cũng là nhỏ yếu nhất."),i("br"),i("br"),n._v("“Ngươi ý tứ, chúng ta đi ra ngoài trước?”"),i("br"),i("br"),n._v("Nghĩ tới những thứ này, Kim Diệp Vương nhìn qua."),i("br"),i("br"),n._v("“Không sai, chúng ta đi ra ngoài trước, ngươi không phải am hiểu trận pháp ư? Thực sự không yên lòng, có thể đem chung quanh bày lên trận pháp, để bọn hắn không thể chạy trốn, sau đó, chờ hắn thẩm vấn xong, lại thương nghị như thế nào giải quyết thật giả vấn đề, yên tâm đi, ngươi ta cùng một chỗ xuyên qua phong ấn tới, ta đối với ngươi khẳng định càng thêm tín nhiệm!”"),i("br"),i("br"),n._v("Thanh Diệp Vương nói."),i("br"),i("br"),n._v("“Cũng tốt!”"),i("br"),i("br"),n._v("  Kim Diệp Vương nhẹ gật đầu."),i("br"),i("br"),n._v("Cũng đúng, hiện đang xoắn xuýt nhiều như vậy, vô dụng, đối phương bất kể thực lực vẫn là chân khí độ tinh thuần, đều không thể so với hắn thấp, thậm chí càng cao thêm một bậc, cưỡng ép trở mặt, ở đây chiến đấu, làm không cẩn thận cá chết lưới rách, ngược lại ảnh hưởng tới đại kế."),i("br"),i("br"),n._v("Phía trên vì để cho bọn họ thông qua phong ấn, tiêu tốn đánh đổi bao lớn, có thể nói kinh khủng, ngộ nhỡ bởi vì chút chuyện nhỏ này, thương vong một mảng lớn, tin tức truyền trở về, tội danh liền lớn!"),i("br"),i("br"),n._v("Nhỏ không nhẫn sẽ bị loạn mưu lớn."),i("br"),i("br"),n._v("Dị Linh tộc nhân đợi đến cơ hội này, phải đợi quá lâu, cũng không thể bởi vì hắn, trở thành lịch sử tội nhân."),i("br"),i("br"),n._v("Thấy hắn đồng ý, Thanh Diệp Vương lúc này mới thở dài một hơi, ngẩng đầu nhìn về phía cách đó không xa Trương Huyền, liền ôm quyền: “Nếu ngươi muốn thẩm vấn, cũng tốt, chúng ta trước đi ra bên ngoài đợi chờ, cho ngươi nửa canh giờ thời gian!”"),i("br"),i("br"),n._v("“Ừm!” Nhẹ gật đầu, Trương Huyền xoay người sang chỗ khác, không nhìn lại đối phương, tùy ý khoát tay áo."),i("br"),i("br"),n._v("Thanh Diệp Vương cùng Kim Diệp Vương liếc mắt nhìn nhau, xoay người hướng ra phía ngoài đi ra ngoài, còn chưa đi xa, liền nghe đến một cái danh sư tức giận tiếng âm vang lên."),i("br"),i("br"),n._v("“Ngươi không mới vừa nói mình là danh sư học viện viện trưởng Trương Huyền ngụy trang, đồng thời muốn thả chúng ta chạy trốn ư? Làm sao biến thành Kim Diệp Vương?”"),i("br"),i("br"),n._v("Nói chuyện chính là Lục Phong."),i("br"),i("br"),n._v("Cái tên này, cho rằng Trương Huyền chính là Dị Linh tộc nhân, đã ăn sâu bén rễ, biết mình không giết được hắn, muốn muốn nhờ tay của đối phương chém giết, để bọn hắn Dị Linh tộc nhân chó cắn nhau!"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Nghe nói như thế, Trương Huyền suýt chút nữa không có hộc máu ngất đi."),i("br"),i("br"),n._v("Thật vất vả đem mấy người nghi ngờ loại bỏ, ngay lúc sắp đại công cáo thành, liền tới một màn như thế... Con mẹ nó ngươi là cảm thấy ta không chết được vẫn là thế nào?"),i("br"),i("br"),n._v("Đường đường danh sư học viện luyện đan học viện viện trưởng, làm sao như thế không có đầu óc..."),i("br"),i("br"),n._v("Mặc dù tức giận, muốn đem cái này hai hàng tại chỗ bóp chết, nhưng cũng có thể hiểu đối phương vì cái gì làm như thế."),i("br"),i("br"),n._v("Mới vừa nói với bọn họ xong, bản thân là Trương viện trưởng, tiếp lấy liền biến thành Kim Diệp Vương, mà lại nói đến như vậy hùng hồn... Đổi lại ai, khẳng định đều sẽ tâm tồn lo nghĩ, thừa cơ để bọn hắn nội đấu."),i("br"),i("br"),n._v("Trong lòng buồn bực, muốn bùng nổ, nhưng cũng biết, bây giờ không phải là nổi giận thời điểm, vội vàng quay đầu nhìn lại, quả nhiên thấy lập tức đi ra cổng Thanh Diệp Vương cùng Kim Diệp Vương ngừng lại, vẻ mặt vô cùng nghi hoặc nhìn qua."),i("br"),i("br"),n._v("“Trương viện trưởng?”"),i("br"),i("br"),n._v("“Cứu bọn họ? Đây là có chuyện gì?”"),i("br"),i("br"),n._v("Hai người ánh mắt sáng ngời, muốn xem thấu Trương Huyền ngụy trang."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Toàn thân cứng ngắc, Trương Huyền trong đầu nhanh chóng vận chuyển, đột nhiên nhẹ nhàng cười một tiếng, trên mặt lộ ra thản nhiên: “Không sai, ta chính là danh sư học viện viện trưởng, Trương Huyền!”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-961"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-963"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);