(window.webpackJsonp=window.webpackJsonp||[]).push([[2259],{2614:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-956-suyt-tuc-gian-khoc-kim-diep-vuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-956-suyt-tuc-gian-khoc-kim-diep-vuong"}},[n._v("#")]),n._v(" Chương 956: Suýt tức giận khóc Kim Diệp Vương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 956: Suýt tức giận khóc Kim Diệp Vương"),t("br"),t("br"),n._v("Trước đó giam giữ tất cả danh sư cung điện bên ngoài, hai đại vương giả, chính cau mày, hướng phía trong nhìn lại."),t("br"),t("br"),n._v("“Làm sao một điểm động tĩnh đều không có?” Thanh Diệp Vương vẻ mặt u ám."),t("br"),t("br"),n._v("Bọn họ chờ ở bên ngoài gần nửa canh giờ, vốn cho rằng chí ít có tranh đấu, giết mấy người chấn nhiếp thoáng cái, kết quả động tĩnh gì đều không có, thật giống như người ở bên trong đã chết hết đồng dạng."),t("br"),t("br"),n._v("“Ta cũng cảm thấy kỳ quái, thực sự không được, dùng thần thức dò xét tra một chút!” Kim Diệp Vương nói."),t("br"),t("br"),n._v("“Đối phương là thực lực không thua tại chúng ta Vương Giả, trực tiếp dùng thần thức dò xét quá mức không lễ phép! Lại nói, ngộ nhỡ liên lụy đến phía trên cơ mật, cứ như vậy tùy tiện đi làm, ta sợ làm cho rất lớn mâu thuẫn...” Thanh Diệp Vương mặt mũi xoắn xuýt."),t("br"),t("br"),n._v("Nếu không phải thực lực của đối phương cùng hắn không kém bao nhiêu, càng là mang theo nhiệm vụ đặc thù, khẳng định sớm đã dùng thần thức dò xét, làm sao đến mức trông mong chờ ở bên ngoài."),t("br"),t("br"),n._v("“Như thế...” Kim Diệp Vương vậy nhẹ gật đầu."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hai người chính đang nói chuyện, đột nhiên mặt một hồi nổ vang, chung quanh vô số linh khí bỗng nhiên hướng ở giữa nhất đại điện hội tụ mà đi."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Thanh Diệp Vương nhìn sang."),t("br"),t("br"),n._v("“Bẩm báo vương gia, hình như là phù văn điện truyền đến thanh âm...” Đông Việt nhìn một chút, sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Phù văn điện là cái này một mảng lớn kiến trúc hạch tâm, một khi xảy ra vấn đề, trước đó tất cả cố gắng đều đem trôi theo nước chảy."),t("br"),t("br"),n._v("“Phù văn điện? Đi xem một chút!”"),t("br"),t("br"),n._v("Lông mày giương lên, Thanh Diệp Vương đang muốn dặn dò, liền cảm giác tới mặt đất kịch liệt oanh động."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Bụi bặm dâng lên, khói mù lượn lờ."),t("br"),t("br"),n._v("Mọi người ngay sau đó liền thấy trước mắt một mảnh kiến trúc, tuyết lở đồng dạng ngược lại sập xuống, trước đó bố trí đủ loại trận pháp, như băng tuyết tan rã tán loạn, thậm chí rất nhiều đột nhiên nổ tung, đem không ít không có phòng bị thuộc hạ đều nổ thành trọng thương."),t("br"),t("br"),t("br"),n._v("Trận pháp đều lấy phù văn điện linh khí làm cơ sở thôi thúc, uy lực càng lớn trận pháp, một khi phá hư, lực phản phệ lại càng lớn. Thật giống như tràn ngập tức giận khí cầu đồng dạng, bên trong không khí bị rút đi, liền sẽ lập tức thu hẹp, xuất hiện không thể đoán được kết quả."),t("br"),t("br"),n._v("“Ta kiến trúc...” Sắc mặt trắng nhợt, Đông Việt suýt chút nữa không có tại chỗ khóc lên."),t("br"),t("br"),n._v("Hơn hai tháng trước, theo phong in ra, bọn họ liền bắt đầu xây dựng mảnh này kiến trúc, hao phí vô số tâm huyết cùng tâm lực, hao tốn không biết giá lớn bao nhiêu, kết quả một cái chớp mắt biến thành phế tích, chỉ cảm thấy con mắt biến thành màu đen, ngực một im lìm, nhịn không được một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Nhìn xem đầy đất phế tích, Thanh Diệp Vương vậy toàn thân co lại: “Là phù văn điện linh khí biến mất, mới xuất hiện biến cố...”"),t("br"),t("br"),n._v("Lấy nhãn lực của hắn có thể nhẹ nhõm nhìn ra nguyên do, có điều, loại này kiến trúc, đều sẽ đem phù văn điện bảo vệ rất tốt, không xuất hiện một chút vấn đề, làm sao đột nhiên liền sập?"),t("br"),t("br"),n._v("Bên này chính đang kỳ quái, bên kia Kim Diệp Vương đột nhiên biến sắc, một tiếng kinh hô: “Không đúng!”"),t("br"),t("br"),n._v("“Thế nào?” Thanh Diệp Vương cùng Đông Việt đồng thời nhìn qua."),t("br"),t("br"),n._v("“Các ngươi xem phía trước...” Kim Diệp Vương vẻ mặt trắng bệch, chỉ về phía trước."),t("br"),t("br"),n._v("Hai người cau mày, đồng thời nhìn về phía trước."),t("br"),t("br"),n._v("Chỉ thấy trước mắt cung điện vậy đổ sụp, khói bụi bồng bềnh, một vùng phế tích."),t("br"),t("br"),n._v("“Thế nào? Cái cung điện này xây dựng mặc dù rất chắc chắn, dùng cũng đều là thuần một sắc Thanh Cương nham, lại có rất nhiều trận pháp gia cố, có thể... Vậy không có gì đặc thù, cái khác đều sập, nó tự nhiên cũng không chịu nổi...” Đông Việt nghi ngờ."),t("br"),t("br"),n._v("“Sập cái rắm, tên kia cùng những danh sư kia đâu? Nơi này đều sập thành dạng này, đều không có đi ra, các ngươi không cảm thấy kỳ quái sao?”"),t("br"),t("br"),n._v("Kim Diệp Vương hét."),t("br"),t("br"),n._v("“Cái này...” Hai người cái này mới phản ứng được, vẻ mặt đồng thời biến đổi, nhất là Thanh Diệp Vương thần thức liếc nhìn đi qua, thân thể cứng đờ."),t("br"),t("br"),n._v("Trước mắt phế tích bên trong rỗng tuếch, nào có nửa cái bóng người, thật giống như trước đó vị kia tộc nhân cùng tất cả danh sư hư không tiêu thất đồng dạng."),t("br"),t("br"),n._v("“Không thấy...” Nhìn Kim Diệp Vương một cái, thấy hắn mặt mũi xanh xám, răng cắn chặt, tức giận đến gần nổ tung."),t("br"),t("br"),n._v("“Chúng ta đều bị cái tên này lừa, nhanh đi phù văn điện!”"),t("br"),t("br"),n._v("Đi đầu kịp phản ứng, Thanh Diệp Vương la hét một tiếng, mặc kệ hai người khác trực tiếp hướng về phía trước bay ra ngoài."),t("br"),t("br"),n._v("Trong nháy mắt liền đến đến, sụp đổ phù văn điện trước mặt, chỉ nhìn một chút, liền mắt tối sầm lại."),t("br"),t("br"),n._v("Lấy nhãn lực của hắn tự nhiên đó có thể thấy được, đó cũng không phải một lần tai nạn, mà là con người làm ra đưa đến."),t("br"),t("br"),n._v("Nói cách khác, vừa rồi có người tại phù văn điện, đem linh khí trong đó toàn bộ hấp thu, phá hủy phía trên phù văn, đồng thời dẫn nổ trong đó lực lượng, mới đưa đến tất cả kiến trúc toàn diện sụp đổ."),t("br"),t("br"),n._v("Nếu không coi như phù văn điện xuất hiện biến cố vậy không có khả năng để trong này biến thành dạng này."),t("br"),t("br"),n._v("“Là danh sư! Tên kia nhất định là danh sư!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Thanh Diệp Vương kịp phản ứng."),t("br"),t("br"),n._v("Nổi danh sư, người bình thường rất khó coi ra phù văn điện cùng những kiến trúc khác điểm kết nối, coi như muốn phá hư vậy không có khả năng như thế hoàn toàn. Lại thêm đám người kia biến mất không thấy gì nữa, ngu nữa cũng biết đối phương căn bản cũng không phải là thẩm vấn mà là cứu người."),t("br"),t("br"),n._v("“Bọn họ làm sao chạy trốn? Chúng ta một mực trông coi, còn có trận pháp thủ hộ...”"),t("br"),t("br"),n._v("Kim Diệp Vương cũng tới đến trước mặt."),t("br"),t("br"),n._v("Đường đường Dị Linh tộc vương gia, Thánh vực tứ trọng cường giả, thế mà bị người đùa bỡn ở trong lòng bàn tay mà không biết, suy nghĩ một chút đều cảm thấy phát điên."),t("br"),t("br"),n._v("“Mặc kệ hắn như thế nào chạy trốn, vừa đem nơi này tiêu hủy, lại mang theo nhiều người như vậy, khẳng định trốn không xa, người tới, cho ta bốn phương tám hướng đuổi! Một khi phát hiện tung tích cùng tin tức, lập tức báo tin, tuyệt đối không nên hành động thiếu suy nghĩ.”"),t("br"),t("br"),n._v("Thanh Diệp Vương cắn răng, quay đầu phân phó."),t("br"),t("br"),n._v("Mang theo nhiều như vậy già yếu tàn tật danh sư, hắn không tin, có thể chạy được nhanh hơn!"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Đông Việt đám người lên tiếng, đồng thời hướng bốn phương tám hướng bắn ra."),t("br"),t("br"),n._v("“Đáng ghét, đáng ghét, cái tên này đến cùng là ai!”"),t("br"),n._v("Đem tất cả thuộc hạ phái ra, hai đại vương giả nhớ tới trước đó trải qua, tức giận đến răng cắn chặt, cảm thấy cả người đều nhanh muốn bạo."),t("br"),t("br"),n._v("“Nếu thật là danh sư ngụy trang lời nói, sẽ không phải là bọn họ nói tới người danh sư kia học viện viện trưởng Trương Huyền?”"),t("br"),t("br"),n._v("Đột nhiên, Kim Diệp Vương nhớ ra cái gì đó, trầm ngâm nói."),t("br"),t("br"),n._v("Lúc đó vị kia phó viện trưởng Lục Phong, từng chính miệng nói tên kia tự xưng Trương Huyền, chỉ là bọn hắn cho rằng, là vị tộc nhân này lừa gạt đối phương thu hoạch tín nhiệm lời nói, không có coi ra gì."),t("br"),t("br"),n._v("Hiện tại xem ra có thể là thật."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Thanh Diệp Vương nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù bọn họ đối với danh tự này còn rất lạ lẫm, nhưng có thể trở thành danh sư học viện viện trưởng, làm sao có thể đơn giản!"),t("br"),t("br"),n._v("“Bất kể có phải hay không là, trước để bọn hắn dò xét, chờ bắt đến, không phải đem hắn chém thành muôn mảnh không thể...”"),t("br"),t("br"),n._v("Thanh Diệp Vương hét."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này tức giận đến nổi điên, khoảng cách mảnh này kiến trúc không biết nơi bao xa, một cái trữ vật giới chỉ từ không trung rớt xuống, ngay sau đó, một thân ảnh bất ngờ xuất hiện."),t("br"),t("br"),n._v("Thân ảnh vừa mới bắt đầu còn có chút cứng ngắc, một lát sau hoạt động một chút, giãn ra."),t("br"),t("br"),n._v("“Vù vù!”"),t("br"),t("br"),n._v("Thở ra một hơi, mỉm cười, ra hàm răng trắng noãn, chính là từ phù văn điện trốn ra được Trương Huyền."),t("br"),t("br"),n._v("“Cũng không tệ lắm, những khôi lỗi này đều đạt đến Thánh vực tam trọng cấp bậc, hơn nữa đều bổ sung đủ năng lượng, sau này sẽ là gặp được đơn độc Thánh vực tứ trọng vậy không cần sợ hãi...”"),t("br"),t("br"),n._v("Vừa rồi một phen hấp thu, mặc dù đem phù văn điện làm cho sụp đổ, nhưng mà hắn tất cả khôi lỗi, cũng đều được chỗ tốt cực lớn, trong cơ thể năng lượng bổ sung hoàn thành, càng là đạt đến Thánh vực tam trọng cấp bậc."),t("br"),t("br"),n._v("Nhiều cường giả như vậy, về sau cho dù gặp được Thánh vực tứ trọng, đều có thể dựa vào vây công chiến thắng."),t("br"),t("br"),n._v("Thủ đoạn bảo mệnh gia tăng thật lớn."),t("br"),t("br"),n._v("“Ra đi!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một đoàn danh sư xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Chính là bị hắn thu vào Thiên Nghĩ Phong sào mọi người."),t("br"),t("br"),n._v("“Đây là đâu? Chúng ta đã trốn ra ngoài?”"),t("br"),t("br"),n._v("“Là Địa quật rất phía nam, ta đã từng tới một lần... Khoảng cách Dị Linh tộc nhân xây dựng cái kia mảnh kiến trúc, có chừng hơn một trăm cây số.”"),t("br"),t("br"),n._v("“Một trăm cây số bên ngoài?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Chư vị danh sư rất nhanh xác định vị trí, thỉnh thoảng nhìn một chút, phát hiện không có Dị Linh tộc nhân theo ở phía sau, lần nữa nhìn về phía trước mắt thân ảnh, tràn đầy cảm kích."),t("br"),t("br"),n._v("Nhìn tới đối phương nói không sai, quả thực là tới cứu bọn họ."),t("br"),t("br"),n._v("Nhìn thấy ánh mắt của mọi người, Trương Huyền vậy vậy theo Dị Linh tộc nhân diện mạo, thay đổi trở về, đồng thời đem danh sư bào đổi tới, trước ngực sáu vì sao lấp lánh phát sáng."),t("br"),t("br"),n._v("“Cảm ơn Trương sư ân cứu mạng...” Một vị lão giả ôm quyền, người khác theo sát phía sau."),t("br"),t("br"),n._v("“Không cần khách khí, đều là danh sư, cứu người cũng là nên...” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Đám người này vì trong lòng đạo nghĩa, sống chết đều có thể không để ý, đã gặp gỡ không thể không cứu."),t("br"),t("br"),n._v("“Bây giờ không phải là nói lời khách sáo thời điểm, chúng ta lặng lẽ chạy trốn, Dị Linh tộc nhân khẳng định đã nổi điên, bắt đầu khắp nơi tìm tòi, tìm tới nơi này chỉ là vấn đề thời gian, ta xem không bằng dạng này, các ngươi trước tìm địa phương nghỉ ngơi dưỡng thương, tuyệt đối không nên bị phát hiện!”"),t("br"),t("br"),n._v("Đánh gãy mọi người khách khí, Trương Huyền nói."),t("br"),t("br"),n._v("“Nghỉ ngơi dưỡng thương? Không được!”"),t("br"),t("br"),n._v("Một vị lão danh sư, mày nhíu lại thành u cục: “Dị Linh tộc nhân xây dựng công sự phòng ngự, càng là có hai vị Vương Giả đích thân tới, khẳng định mưu đồ quấy rối, nhất định phải ngăn cản bọn họ, nếu không ta sợ... Sẽ xuất hiện không thể suy nghĩ biến cố!”"),t("br"),t("br"),n._v("Dị Linh tộc nhân khí thế hùng hổ mà đến, còn chưa tới bọn họ có thể nghỉ ngơi, an tâm dưỡng thương thời điểm."),t("br"),t("br"),n._v("“Chuyện của bọn hắn các ngươi không cần phải để ý đến, ta một người có thể xử lý!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Lần này xuống, chính là vì xử lý lăng bia dị động tình huống, hiện tại chỉ là chơi sập công sự phòng ngự, hơn hai trăm đầu Dị Linh tộc nhân, còn sống rất tốt, làm sao có thể trực tiếp rời khỏi?"),t("br"),t("br"),n._v("“Một mình ngươi? Cái này không được, quá nguy hiểm...”"),t("br"),t("br"),n._v("Mọi người vội vàng nói."),t("br"),t("br"),n._v("“Các ngươi tình huống hiện tại, theo tới lời nói, không những không thể hỗ trợ, còn chỉ có thể thêm phiền! Các ngươi cần có nhất làm, là dưỡng thương, đợi thương lành, lại cùng chi chiến đấu!”"),t("br"),t("br"),n._v("Biết hảo ý của đối phương, nhưng mà Trương Huyền vẫn lắc đầu một cái."),t("br"),t("br"),n._v("Không phải hắn không tín nhiệm đối phương, mà là Dị Linh tộc nhân quá nhiều, quá cường đại, căn bản không phải bọn họ loại thực lực này có thể chống lại."),t("br"),t("br"),n._v("Thật muốn đi qua lời nói, chỉ sợ lại muốn phiền phức bản thân lại đi xuất thủ cứu một lần."),t("br"),t("br"),n._v("“Cái này...” Mọi người sắc mặt trướng hồng."),t("br"),t("br"),n._v("Đối phương nói không sai, bọn họ mặc dù đều là lục tinh đỉnh phong danh sư, nhưng mà đối mặt hơn hai trăm đầu Dị Linh tộc nhân, còn có hai đầu Thánh vực tứ trọng Vương Giả, quả thực hữu tâm vô lực, không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("“Trương sư, chúng ta mặc dù không được, nhưng một mình ngươi, khẳng định vậy khó! Mọi người coi như không thể giúp đại ân, vậy nhất định có thể giúp chuyện nhỏ, lẫn nhau chăm sóc, tất nhiên có thể để bọn hắn ăn một bình!”"),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta gần bốn mươi người, mỗi người giết nhiều một cái, cũng có thể để áp lực giảm thiếu một phân...”"),t("br"),t("br"),n._v("“Vẫn là để chúng ta đi theo ngươi đi, lần này chỉ cần cẩn thận, không bị vây công, hẳn là sẽ không cùng vừa rồi chật vật như vậy!”"),t("br"),t("br"),n._v("Chúng người vẫn là không yên lòng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-964"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-966"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);