(window.webpackJsonp=window.webpackJsonp||[]).push([[2260],{2615:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-957-đot-pha-tong-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-957-đot-pha-tong-thanh"}},[n._v("#")]),n._v(" Chương 957: Đột phá, Tòng Thánh!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 957: Đột phá, Tòng Thánh!"),t("br"),t("br"),n._v("“Ta có thể theo trong tay bọn họ đem bọn ngươi cứu ra, đã nói lên bảo vệ tính mạng năng lực khẳng định không có vấn đề! Cho nên, điểm ấy không cần lo lắng. Cùng lắm thì, ta đi thẳng một mạch!”"),t("br"),t("br"),n._v('Trương Huyền cười cười: "Ta biết các ngươi không sợ chết, nhưng mà, coi như danh sư, cũng muốn chết có giá trị mới được, không minh bạch bị đám người kia chém giết, ngộ nhỡ bọn họ lao ra Địa quật, đến mặt đất, lại có ai cứu ngàn vạn nhân tộc?'),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Mọi người trầm lặng."),t("br"),t("br"),n._v("Quả thực, coi như không sợ chết, cũng không thể chết vô ích."),t("br"),t("br"),n._v("Bất cứ chuyện gì đều muốn làm có giá trị mới được."),t("br"),t("br"),n._v("“Tốt, liền nghe lời ngươi, chúng ta trước dưỡng thương! Vậy Trương sư... Ngươi vậy cẩn thận, thực sự không được, chúng ta bẩm báo tổng bộ, để tổng bộ tới xử lý...”"),t("br"),t("br"),n._v("Trước đó lão giả nói."),t("br"),t("br"),n._v("“Yên tâm đi!” Biết hảo ý của đối phương, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Ừm!” Mọi người lúc này mới muốn này trước mắt vị thanh niên này thần kỳ thủ đoạn, đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Nhiều như vậy Dị Linh tộc nhân bao vây bên dưới, đều đem bọn hắn có thể thuận lợi cứu ra, người thanh niên này mặc dù tuổi không lớn lắm, năng lực lại là bọn họ đều còn kém rất rất xa."),t("br"),t("br"),n._v("“Đúng rồi, trong tay các ngươi nhưng có trận kỳ hoặc là trận bàn, cấp bậc càng cao càng tốt!”"),t("br"),t("br"),n._v("Chính muốn rời khỏi, đột nhiên nhớ tới cái gì, Trương Huyền nhìn về phía mọi người."),t("br"),t("br"),n._v("“Trận kỳ? Trận bàn?”"),t("br"),t("br"),n._v("Mọi người liếc mắt nhìn nhau."),t("br"),t("br"),n._v("“Ta cái này có một bộ trận kỳ, đại khái tại cấp sáu đỉnh phong tả hữu, tổng cộng hơn 120 căn, có thể dùng tới bố trí Chính Vũ huyễn trận.”"),t("br"),t("br"),n._v("“Ta cái này cũng có, 81 căn, có thể bố trí cấp sáu đỉnh phong sát trận!”"),t("br"),t("br"),n._v("đọc ngantruyen.com"),t("br"),n._v("“Ta rời đi thời điểm, mua một cái cấp sáu đỉnh phong trận bàn, có điều, chỉ là cái mê trận, uy lực không phải đặc biệt lớn...”"),t("br"),t("br"),n._v("“Ta vậy có một cái trận bàn, cấp sáu đỉnh phong...”"),t("br"),t("br"),n._v("Nghe hắn cần trận kỳ cùng trận bàn, mọi người nhao nhao lấy được đi ra, lấy ra trọn một đống."),t("br"),t("br"),n._v("Những người này biết đi tới Địa quật, cửu tử nhất sinh, trước thời hạn đều đem trên người gia sản đều hối đoái thành bảo vệ tính mạng bảo vật, trận kỳ, trận bàn có thể lấy yếu thắng mạnh, để cho người ta thực lực tăng nhiều, tự nhiên đều chuẩn bị rất nhiều."),t("br"),t("br"),n._v("“Không sai, đủ...” Nhìn thấy đầy đất trận kỳ cùng trận bàn Trương Huyền cười cười."),t("br"),t("br"),n._v("Không hổ là lục tinh đỉnh phong danh sư, trong tay đồ tốt, quả nhiên không ít, mặc dù đều chỉ là cấp sáu đỉnh phong, đều tại cấp sáu bên trong, được cho cường đại nhất, ra ngoài mua, không biết phải hao phí bao nhiêu, hơn nữa rất nhiều vẫn là có tiền mà không mua được."),t("br"),t("br"),n._v("Bàn tay một trảo, đem những vật này thu vào trữ vật giới chỉ, Trương Huyền đối với mọi người ôm quyền: “Cáo từ!”"),t("br"),t("br"),n._v("Nói xong xoay người liền muốn rời khỏi, còn chưa đi xa, liền nghe phía sau một thanh âm vang lên."),t("br"),t("br"),n._v("“Trương viện trưởng chậm đã!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, nói chuyện chính là Lục Phong."),t("br"),t("br"),n._v("Chỉ thấy cái tên này trướng đến đỏ bừng cả khuôn mặt, nhìn lại."),t("br"),t("br"),n._v("“Lục sư!” Trương Huyền thật có lỗi."),t("br"),t("br"),n._v("“Trương viện trưởng, ta... Chuyện lúc trước xin lỗi ngươi, còn hi vọng ngươi đừng nên trách...” Lục Phong nói."),t("br"),t("br"),n._v("“Lục sư khách khí! Đối kháng Dị Linh tộc nhân là chúng ta danh sư cái kia vai chịu trách nhiệm, không phân ngươi ta.” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Cảm ơn viện trưởng đại lượng.”"),t("br"),t("br"),n._v("Chần chờ một chút, Lục Phong cổ tay khẽ đảo, lấy ra một cái mâm tròn: “Đây là năm đó ta lấy được một cái trận bàn, là cái cấp bảy mê trận, có lẽ có thể đối ngươi hữu dụng...”"),t("br"),t("br"),n._v("“Cấp bảy trận pháp?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền vội vàng nhận lấy."),t("br"),t("br"),n._v("Cái này trận bàn không biết dùng làm bằng vật liệu gì điêu khắc mà thành, trong đó hoa văn lít nha lít nhít, mảnh vô cùng, cho dù hắn Minh Lý Chi Nhãn đều có chút thấy không rõ lắm."),t("br"),t("br"),n._v("“Đây là dùng thần thức quan sát điêu khắc mà thành, một khi kích hoạt uy lực cực lớn, ta cũng là cơ duyên xảo hợp mới lấy được, một mực không có sử dụng...” Lục Phong thấy hắn quan sát, giải thích nói."),t("br"),t("br"),n._v("“Dùng thần thức quan sát điêu khắc? Khó trách!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Trận pháp đạt tới cấp bảy, chỉ có Thất Tinh trận pháp sư mới có thể điêu khắc, mà muốn trở thành loại cấp bậc này trận pháp sư, điều yêu cầu thứ nhất, chính là tu vi đạt tới Thánh vực nhị trọng Thần Thức cảnh."),t("br"),t("br"),n._v("Thần thức là linh hồn cảm xúc, có thể quan sát càng nhỏ xíu đồ vật, mắt thường bằng được không lên."),t("br"),t("br"),n._v("“Đa tạ!” Biết cái này trận bàn đối với hắn có rất lớn tác dụng, vậy không chối từ, Trương Huyền cổ tay khẽ đảo, thu vào nhẫn."),t("br"),t("br"),n._v("“Cáo từ!”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, trực tiếp hướng về phía trước vọt ra ngoài, trong nháy mắt liền theo trước mắt mọi người biến mất."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, tất cả danh sư, đồng loạt hướng Lục Phong nhìn lại."),t("br"),t("br"),n._v("“Vị này... Thật sự là danh sư học viện tân viện trưởng?”"),t("br"),t("br"),n._v("Tất cả mọi người khó tin, cuối cùng tuổi tác thực sự quá nhỏ."),t("br"),t("br"),n._v("“Vâng!” Lục Phong nhẹ gật đầu."),t("br"),t("br"),n._v("Trước đó hắn cũng cảm thấy tất cả trưởng lão chọn hắn làm viện trưởng là một sai lầm, hiện tại xem ra, quả thực là sáng suốt nhất cách làm."),t("br"),t("br"),n._v("Theo nhiều như vậy Dị Linh tộc nhân bên trong, bình tĩnh đem bọn hắn cứu ra, không tham công, không cao ngạo, coi như năm đó Mạc Lưu Chân viện trưởng cũng làm không được đi."),t("br"),t("br"),n._v("Chí ít, hắn là không cách nào sánh được, kém không chỉ một đẳng cấp."),t("br"),t("br"),n._v("“Hắn có có thể trở thành danh sư học viện lợi hại nhất một vị viện trưởng, mang theo học viện đi về phía huy hoàng...”"),t("br"),t("br"),n._v("Nhìn xem Trương Huyền biến mất phương hướng, Lục Phong tự lẩm bẩm."),t("br"),t("br"),n._v("Thấy tận mắt người thanh niên này tất cả năng lực, mới biết được hắn đáng sợ, loại người này thành tâm vì học viện, học viện sao có thể không huy hoàng?"),t("br"),n._v(" ..."),t("br"),t("br"),n._v("Bay về phía trước một đoạn thời gian, Trương Huyền tại một cái khe núi ngừng lại."),t("br"),t("br"),n._v("“Dạng này đi qua khẳng định không phải là đối thủ, nhất định phải nghĩ biện pháp mới được...”"),t("br"),t("br"),n._v("Mặc dù có hơn 20 đầu Thánh vực tam trọng khôi lỗi, nhưng mà thật muốn cùng hơn hai trăm đầu Thánh vực nhất trọng Dị Linh tộc, hai đầu tứ trọng Vương Giả đối đầu, vẫn là sẽ bị dễ dàng chém giết, chống lại không được."),t("br"),t("br"),n._v("Nhất định phải trước thời hạn muốn cái đối sách, tốt nhất có thể đem những người này một mẻ hốt gọn, nhất tuyệt tai hoạ về sau."),t("br"),t("br"),n._v("“Xem trước một chút cái này cấp bảy trận bàn...” Tinh thần khẽ động, đem Lục Phong cho hắn trận bàn lấy ra ngoài, bàn tay xiết chặt, chân khí điên cuồng truyền vào."),t("br"),t("br"),n._v("“Ừm? Không có động tĩnh?” Nhướng mày."),t("br"),t("br"),n._v("Chẳng lẽ thứ này là hỏng?"),t("br"),t("br"),n._v("Không nên ah."),t("br"),t("br"),n._v("Hô khẽ thiếu hụt, trong đầu xuất hiện thư tịch, nhìn một cái, vẻ mặt tối sầm lại, hận không thể có một nơi chui vào."),t("br"),t("br"),n._v("May mắn không có người nhìn thấy, nếu không hắn viện trưởng này thật sự mất mặt ném đại phát!"),t("br"),t("br"),n._v("Cũng không phải là trận bàn hỏng, mà là... Hắn thực lực không đến, chân khí cấp bậc không đủ, căn bản không có cách nào kích hoạt."),t("br"),t("br"),n._v("Hiện tại chân khí lực lượng, chỉ có Bán Thánh đỉnh phong, sức chiến đấu toàn bộ tính gộp lại, cũng chỉ cùng Thánh vực nhất trọng không kém bao nhiêu, khởi động cấp sáu trận bàn mà nói vấn đề không lớn, cấp bảy... Căn bản không có khả năng!."),t("br"),t("br"),n._v("“Tử Dương thú mặc dù đạt đến Thần Thức cảnh, nhưng mà nó là Thánh thú, đồng dạng không cách nào sử dụng... Kim Nguyên đỉnh liền càng không cần phải nói, chỉ là cái Thánh khí...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("“Trừ phi... Ta thực lực bây giờ, đạt tới Tòng Thánh đỉnh phong, có lẽ có thể nhờ vào Phong Thánh giải uy lực, thôi thúc Thánh vực nhị trọng mới có thể kích hoạt cấp bảy trận bàn!”"),t("br"),t("br"),n._v("Thực lực bây giờ kích hoạt trận bàn là khẳng định làm không được, chỉ có thể nghĩ biện pháp đột phá."),t("br"),t("br"),n._v("May mắn đột phá Tòng Thánh cần có Lôi Minh thạch, đã tới tay."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền không có gì có thể do dự, thân thể nhoáng một cái, đi vào Thiên Nghĩ Phong sào."),t("br"),t("br"),n._v("Tu luyện đột phá cảnh giới cần tuyệt đối yên tĩnh, không thể bị quấy rầy, hiện tại Dị Linh tộc nhân chính đang truy tung, ở đây khẳng định không thể an tâm."),t("br"),t("br"),n._v("Tiến vào bên trong, Trương Huyền đi tới dưới cây bồ đề khoanh chân ngồi xuống, cổ tay khẽ đảo, một đống linh thạch thượng phẩm xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Thẩm gia cho nhóm đầu tiên lợi nhuận, đều có hơn 3000 viên, mặc dù trợ giúp tất cả học sinh tăng thực lực lên thời điểm dùng hết 500, đối với hắn mà nói vẫn là có rất nhiều, hoàn toàn đủ, trong thời gian ngắn không cần vì linh thạch mà lo lắng."),t("br"),t("br"),n._v("Tiện tay gảy ra một đống trận kỳ, bố trí một cái Tụ Linh trận, đem tất cả linh thạch thượng phẩm bên trong linh khí tụ lại, Trương Huyền lúc này mới đem Lôi Minh thạch giữ tại lòng bàn tay, nhắm mắt tu luyện."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Tinh thuần, hùng hậu chân khí tại trải qua trong kinh mạch chảy xuôi, tựa như một con rồng lớn, tại thể nội vọt đi, Trương Huyền toàn thân huyệt đạo toàn bộ mở ra, tham lam hấp thu chung quanh linh khí nồng nặc, như cùng một cái đói khát Thao Thiết cự thú, hưởng thụ lấy phong phú mỹ thực."),t("br"),t("br"),n._v("Hai tháng qua, hắn đã sớm đạt đến Bán Thánh đỉnh phong, bởi vì không có Lôi Minh thạch, một mực tích lũy, trong cơ thể đối đột phá khát vọng, tựa như là khô héo mặt đất, đã sớm không chịu nổi."),t("br"),t("br"),n._v("Lôi Minh thạch vừa xuất hiện, lại thêm đầy đủ linh khí, khí tức cả người lập tức phát sinh biến hóa."),t("br"),t("br"),n._v("Kẽo kẹt kẽo kẹt!"),t("br"),t("br"),n._v("Nương theo linh khí, càng hấp thu càng nhiều, vận chuyển chân khí thời gian càng ngày càng dài, giam cầm tại thân thể bình chướng, bị đánh đến càng ngày càng yếu kém, dần dần lay động."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không biết qua bao lâu, đột nhiên một tiếng kịch liệt nổ vang, huyền quan mở ra, Trương Huyền chỉ cảm thấy con mắt cùng lỗ tai biến đến mức dị thường rõ ràng, tư duy vậy so trước đó càng thêm mau lẹ."),t("br"),t("br"),n._v("Tòng Thánh, đến!"),t("br"),t("br"),n._v("Tòng Thánh là chân chính đột phá Thánh vực đặt nền móng, để tu luyện giả sinh mệnh bản chất có một cái bay vọt về chất, bởi vậy theo thân thể, linh hồn chân khí đều có tăng lên trên diện rộng."),t("br"),t("br"),n._v("Đừng nhìn Bán Thánh đỉnh phong cùng Tòng Thánh sơ kỳ, chỉ kém một cái tiểu cấp bậc, nhưng lại có rồng cùng cá chênh lệch, không có thể đồng thời mà nói."),t("br"),t("br"),n._v("Lại thêm, hắn tu luyện chính là Phong Thánh giải, chân khí càng thêm ngưng tụ, càng thêm dày hơn nặng, để hắn lực lượng trong cơ thể, nước biển đồng dạng cuộn trào mãnh liệt."),t("br"),t("br"),n._v("Chân khí vậy theo trước đó 84 triệu đỉnh, lần nữa có đột phá, đạt đến trọn 90 triệu đỉnh!"),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Biết vừa mới đột phá, đồng dạng rất khó khởi động cấp bảy trận bàn, híp mắt lại, Trương Huyền tiếp tục tu luyện."),t("br"),t("br"),n._v("Đột phá ràng buộc, tiếp tục tu luyện, liền đơn giản rất nhiều."),t("br"),t("br"),n._v("Sau hai canh giờ, đã đạt đến Tòng Thánh đỉnh phong."),t("br"),t("br"),n._v("Chân khí lực lượng, thành công đột phá Thánh vực mới có thể đi đến tuyệt đối đỉnh, đạt đến một ức hai ngàn vạn đỉnh!"),t("br"),t("br"),n._v("Càng quan trọng hơn là, trong đan điền nguyên thai, mượt mà vô cùng, tản mát ra hào quang chói sáng, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("“Xem ra, hiện nay liền có nhị đẳng tuyệt phẩm!”"),t("br"),t("br"),n._v("Mục Khải Mục sư, hao tốn vô số tâm huyết, cả một đời cũng mới đem trong cơ thể nguyên thai, tu luyện tới nhị đẳng thượng phẩm, hắn bây giờ còn chưa đột phá Thánh vực, liền đã đạt đến nhị đẳng tuyệt phẩm."),t("br"),t("br"),n._v("Cái này cũng khó trách, Thiên Đạo chân khí, cộng thêm Thiên Đạo công pháp, nếu như ngay cả loại này cấp bậc đều không đạt tới, hắn cũng có thể tìm khối đậu phụ đâm chết được rồi!"),t("br"),t("br"),n._v("“Chỉ cần tiếp tục tôi luyện, nguyên thai đẳng cấp còn sẽ tăng lên, đạt tới nhất đẳng không khó lắm!”"),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hao tốn như thế lớn đánh đổi, như thế lớn vất vả tu luyện, quả nhiên không có uổng phí."),t("br"),t("br"),n._v("Chỉ cần có thể trở thành nhất đẳng nguyên thai, bất kể về sau, vẫn là sức chiến đấu, đều có cực trên phạm vi lớn gia tăng."),t("br"),t("br"),n._v("Thậm chí có có thể trở thành trời nhận Thánh giả."),t("br"),t("br"),n._v("Đương nhiên, muốn làm đến điểm ấy, còn cần tiếp tục rèn luyện nguyên thai, để nó biến đến càng thêm ngưng kết mới được."),t("br"),t("br"),n._v("“Hiện tại không có thời gian rèn luyện, thử xem trận pháp!”"),t("br"),t("br"),n._v("Biết bây giờ không phải là lúc tu luyện, Trương Huyền đem cái kia cấp bảy trận bàn lấy ra ngoài."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-965"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-967"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);