(window.webpackJsonp=window.webpackJsonp||[]).push([[2267],{2623:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-963-chung-ta-la-huyen-huyen-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-963-chung-ta-la-huyen-huyen-hoi"}},[n._v("#")]),n._v(" Chương 963: Chúng ta là Huyền Huyền hội")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 963: Chúng ta là Huyền Huyền hội"),i("br"),i("br"),n._v("Võ kỹ khiêu chiến, tại danh sư học viện, là chuyện thường xảy ra, thường xuyên có danh sư vì nghiệm chứng bản thân đối với chiến đấu nắm chắc, khiêu chiến một chút sức chiến đấu mạnh."),i("br"),i("br"),n._v("Nhìn thấy vị này Chu Diệp, trực tiếp mở miệng, đối diện cái kia gọi Nhược Hoan thanh niên sửng sốt một chút, nhẹ nhàng cười một tiếng: “Đương nhiên có thể!”"),i("br"),i("br"),n._v("“Vậy thì bắt đầu đi!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Chu Diệp hít sâu một hơi, lực lượng trong cơ thể, từng tầng từng tầng bị áp chế, rất nhanh cùng đối phương đồng dạng, đều là Kiều Thiên cảnh."),i("br"),i("br"),n._v("“Thấy ngươi vừa rồi thi triển chính là chưởng pháp, ta cũng không dùng binh khí, bắt đầu đi!”"),i("br"),i("br"),n._v("Cười cười, Chu Diệp cổ tay khẽ đảo, thân thể nhanh chóng hướng về phía trước nhảy lên ra."),i("br"),i("br"),n._v("Thân ảnh của hắn giống như quỷ giống như mị, mặc dù dùng chính là Kiều Thiên cảnh lực lượng, cả người nhưng giống như một đầu như u linh, để cho người ta thấy không rõ cụ thể muốn tiến công phương vị."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Ánh mắt sáng lên, Nhược Hoan thân thể bất động, bàn tay bỗng nhiên khẽ đảo, đối ngay phía trước đánh ra."),i("br"),i("br"),n._v("Trương viện trưởng đã từng nói, nếu như vừa nhìn thấy loại này chiêu số liền bối rối, bạn chuyển động theo hắn, chẳng khác nào trúng mưu kế, bị đối phương mang theo tiết tấu, còn muốn chiến thắng liền khó khăn."),i("br"),i("br"),n._v("Do đó, đối với loại này hư chiêu rất nhiều công kích, muốn xuyên thấu qua hiện tượng nhìn bản chất, mặc kệ đối phương như thế nào lay động... Nhưng muốn tấn công, cũng chỉ có mấy cái cố định vị trí, lấy bất động, đáp vạn biến."),i("br"),i("br"),n._v("“Đến hay lắm!”"),i("br"),i("br"),n._v("Thấy thân thể của hắn bất động, đập hướng về phía trước, Chu Diệp ánh mắt sáng lên."),i("br"),i("br"),n._v("Cùng đối phương nghĩ tới đồng dạng, hắn chiêu này mục đích, chính là vì thu hút đối phương ra tay, một khi động thủ, chẳng khác nào có sơ hở."),i("br"),i("br"),n._v("Đi tới đối phương bên cạnh, lật bàn tay một cái, đè xuống."),i("br"),i("br"),n._v("Vốn cho rằng lần này, chỉ cần dùng bên trên lực lượng, đối phương tất nhiên bị thương nhận thua, ai ngờ trên lòng bàn tay lực lượng, còn chưa rơi vào trên người đối phương, liền cảm thấy ngực một hồi khó chịu."),i("br"),i("br"),n._v("Con ngươi co rụt lại, lúc này mới phát hiện, đối phương chẳng biết lúc nào đã phát hiện hắn chân thân vị trí, cái tay còn lại chưởng đã ở chỗ này chờ!"),i("br"),i("br"),n._v("Nói cách khác, vừa rồi hướng về phía trước đánh ra, là hư chiêu, dẫn dụ hắn mắc câu, một khi ra chiêu xác định vị trí, cái tay còn lại chưởng, theo sát mà lên."),i("br"),i("br"),n._v("Bành!"),i("br"),i("br"),n._v("Lúc này muốn trốn tránh cũng không kịp, ngực một buồn bực, Chu Diệp thẳng tắp theo tu luyện tràng bay xuống, ngã ở dọc đường."),i("br"),i("br"),n._v("“Một chiêu liền đem Chu Diệp đánh bại?”"),i("br"),i("br"),n._v("“Chẳng lẽ vị này chính là muốn khảo hạch cái kia?”"),i("br"),i("br"),n._v("“Vô cùng có khả năng, tại sao có thể có như thế lực chiến đấu mạnh mẽ?”"),i("br"),i("br"),n._v("Chiến sư đường mọi người, toàn bộ giật nảy mình."),i("br"),i("br"),n._v("Liêu Tùng cũng lông mày nhảy một cái."),i("br"),i("br"),n._v("Chiến đấu mới vừa rồi, hắn có thể tuỳ tiện nhìn ra, đối phương cái này gọi Nhược Hoan gia hỏa, chân khí lực lượng, tốc độ phản ứng cũng không bằng Chu Diệp, nhưng tỷ thí lại là thắng, nói rõ đối võ kỹ cùng chiến đấu lý giải, có cực cao thiên phú."),i("br"),i("br"),n._v("Chỉ sợ chính là Trác Thanh Phong nói tới cái kia thiên tài tu luyện."),i("br"),i("br"),n._v("Nếu không, làm sao có thể mạnh như vậy?"),i("br"),i("br"),n._v("“Hồ Thần, ngươi bên trên đi thử xem!”"),i("br"),i("br"),n._v("Lông mày giương lên, Liêu Tùng khoát tay áo."),i("br"),i("br"),n._v("Đương nhiên, phải hay không phải, còn cần tiếp tục thử một chút."),i("br"),i("br"),n._v("“Được!”"),i("br"),i("br"),n._v("Gọi là Hồ Thần thanh niên nhẹ gật đầu, tung người nhảy lên, cũng nhảy lên tu luyện tràng."),i("br"),i("br"),n._v("“Nhìn ngươi chiêu số tinh ngoài dự đoán, ta cũng muốn thử một chút...”"),i("br"),i("br"),n._v("Nói xong giảm thấp xuống tu vi."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Nhược Hoan chính muốn động thủ, chỉ thấy bên người một thanh niên đi ra: “Nhược Hoan, có loại này kẻ yếu giao đấu cơ hội, nhường cho ta, ngươi nhìn ta học kém cỏi nhất, viện trưởng giảng giải, hiện tại cũng không để ý tới giải, không nếu như để cho ta thử một chút!”"),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Chuyển biến tốt bạn bộ dáng này, cười khổ một tiếng, Nhược Hoan chỉ thật nhẹ gật đầu."),i("br"),i("br"),n._v("Người bạn thân này quả thực đối võ kỹ lý giải yếu nhược, cùng bọn hắn đám người giao đấu, rất khó luyện tập, cùng cái này không biết từ đâu xuất hiện kẻ yếu so lời nói, có lẽ có thể có được rất lớn dẫn dắt."),i("br"),i("br"),n._v("Vị này Nhược Hoan, chính là lúc trước cùng Trương Huyền theo vạn quốc liên minh cùng đi Nhược Hoan công tử, đi qua một phen khổ tu, đã đạt đến Kiều Thiên cảnh."),i("br"),i("br"),n._v("Theo vạn quốc liên minh liền cùng với Trương Huyền, đối võ kỹ lý giải tự nhiên càng thêm khắc sâu, lại thêm bản thân lại là Huyền Huyền hội người sáng lập một trong, xuống công phu rất lớn khổ tu, bất kể là ánh mắt vẫn là thực lực, đều cùng trước kia khác nhau rất lớn, xưa đâu bằng nay."),i("br"),i("br"),n._v("Hắn thấy đột nhiên xuất hiện hai người, mặc dù sức chiến đấu không yếu, chiêu số cũng rất mới lạ, nhưng là cùng viện trưởng giảng giải những cái kia so vẫn là kém rất nhiều."),i("br"),i("br"),n._v("Viện trưởng giảng giải chính là cơ sở, biến hóa bao nhiêu cũng không rời bản chất, coi như võ kỹ lại sức tưởng tượng, thoạt nhìn lại lóa mắt, bản chất vẫn là muốn đối với người tiến hành tiến công, chỉ muốn tiến công liền có thiếu hụt, liền có thể tiến hành phản kích!"),i("br"),i("br"),n._v("Do đó để bạn tốt động thủ, vừa vặn có thể đối với hắn tiến hành tôi luyện."),i("br"),i("br"),n._v("“Kẻ yếu?” Hai người bọn họ đối thoại, Hồ Thần nghe vào trong tai, chỉ cảm thấy chân khí trong cơ thể tuôn ra, suýt chút nữa không tại chỗ bạo tạc."),i("br"),i("br"),n._v("Đại ca, ngươi mở mắt nhìn xem, ta là Tằm Phong cảnh Chiến sư, giảm thấp xuống tu vi cùng các ngươi chiến đấu, lại còn nói ta là kẻ yếu..."),i("br"),i("br"),n._v("Không chỉ hắn như thế, cái khác Chiến sư nghe nói như thế cũng thiếu chút không hộc máu, bọn họ Chiến sư đi tới chỗ nào đều là hơn người một bậc tồn tại, chiến lực vô cùng, cái này còn là lần đầu tiên bị người như vậy xem thường."),i("br"),i("br"),n._v("“Hồ Thần, không nên lưu tình...” Một vị Chiến sư thực sự nhịn không được truyền âm tới."),i("br"),i("br"),n._v("“Ta biết!” Mắt sáng lên, Hồ Thần lộ ra hung ác chi ý."),i("br"),i("br"),n._v("Vốn là hắn còn muốn lấy, đối phương thực sự không tiếp nổi lời nói, cho lưu cái mặt mũi, đã như vậy không thức thời, vậy cũng đừng trách hắn không khách khí."),i("br"),i("br"),n._v("“Bắt đầu đi!” Bàn tay duỗi ra, Hồ Thần làm cái mời động tác."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Đối diện thanh niên nhẹ gật đầu, đi tới gần."),i("br"),i("br"),n._v("“Viện trưởng mặc dù kỹ càng giảng giải chiến đấu ứng đối kỹ xảo, nhưng mà ta học còn không tinh, qua một sẽ... Vạn nhất đem thương thế của ngươi, đừng trách ta...”"),i("br"),i("br"),n._v("Thanh niên nói."),i("br"),i("br"),n._v("“Bớt nói nhảm, tới đi!” Cũng nhịn không được nữa, Hồ Thần bàn chân trên mặt đất đạp mạnh, trực tiếp lao đến."),i("br"),i("br"),n._v("Công kích của hắn chiêu số đại khai đại hợp, chân khí chạy bát phương, cho người ta một loại khí thôn vạn tượng cảm giác."),i("br"),i("br"),n._v("Nhìn thấy chiêu số của hắn, thanh niên chẳng những không có sợ hãi, ngược lại mắt sáng rực lên, rít lên một tiếng: “Đến hay lắm!”"),i("br"),i("br"),n._v("Nói xong cũng mặc kệ mọi người ngạc nhiên ánh mắt, không có chút nào kỹ xảo, một quyền nghênh đón tiếp lấy."),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Quyền chưởng va chạm, Hồ Thần chỉ cảm thấy cánh tay mềm nhũn, hung mãnh lực lượng vội vàng xông đến, hơi đỏ mặt, trong nháy mắt bay ngược mà ra."),i("br"),i("br"),n._v("Bành!"),i("br"),i("br"),n._v("Cùng trước đó Chu Diệp đồng dạng, theo tu luyện tràng bay ra ngoài, một đầu cắm trên mặt đất."),i("br"),i("br"),n._v("Nếu như hắn không phải mới vừa đã dùng hết toàn lực, lần này phản phệ khẳng định sẽ ít một chút, mà bây giờ, cứng đối cứng, cả cánh tay đều không thể động đậy, đoán chừng ba, trong năm ngày, muốn khôi phục đã không thể nào."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("“Cứng đối cứng, chúng ta Chiến sư không phải là đối thủ?”"),i("br"),i("br"),n._v("Rất nhiều Chiến sư lần nữa bối rối."),i("br"),i("br"),n._v("Liêu Tùng càng là khóe miệng giật một cái."),i("br"),i("br"),n._v("Hắn vừa rồi tận mắt thấy, vị kia Nhược Hoan, chân khí, lực lượng so lên thuộc hạ của mình kém xa tít tắp, mà cái này hướng người khác thỉnh giáo gia hỏa, thế mà một chiêu liền đem Hồ Thần đánh bay... Có phần quá không thể tưởng tượng nổi đi!"),i("br"),i("br"),n._v("Chính một mặt cổ quái, liền nghe đến trên đài Nhược Hoan, một mặt chỉ tiếc rèn sắt không thành thép bộ dạng, nhìn về phía cách đó không xa thanh niên: “Không phải cùng ngươi nói nha, chiến đấu cũng không chỉ có man lực! Muốn kỹ xảo, kỹ xảo... Làm sao lại đần như vậy đâu?”"),i("br"),i("br"),n._v("Nhược Hoan công tử quả thực rất tức giận, cái tên này chỉ học được viện trưởng truyền thụ cho chân khí cùng lực lượng, đối võ kỹ lĩnh ngộ rất kém cỏi, nguyên nhân chính là như thế, mỗi lần đều bại trên tay bọn họ, lần này chuyên môn để hắn cùng một cái đối võ kỹ lý giải không người mạnh giao đấu... Kết quả, vẫn như cũ dùng man lực, tại sao ngu xuẩn như vậy?"),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Thanh niên vò đầu, tràn đầy xấu hổ: “Thấy hắn đi thẳng về thẳng, ta có chút nhịn không được... Như vậy đi!”"),i("br"),i("br"),n._v("Nói xong nhìn về phía đứng tại cách đó không xa mọi người: “Các ngươi còn có ai muốn so với ta, lần này cam đoan không dùng sức lượng nghiền ép các ngươi...”"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Nhìn thấy đối phương loại thái độ này, rất nhiều Chiến sư tức giận nhanh muốn điên rồi."),i("br"),i("br"),n._v("Đây là từ trước tới nay lần thứ nhất, người khác đối chiến Chiến sư, nói không dùng sức lượng nghiền ép..."),i("br"),i("br"),n._v("Quá tùy tiện, quá phách lối!"),i("br"),i("br"),n._v("“Ta tới...”"),i("br"),i("br"),n._v("Thực sự nhịn không được, một cái Chiến sư lao đến, chính là Tằm Phong cảnh vị cuối cùng."),i("br"),i("br"),n._v("Giờ phút này cũng giảm thấp xuống tu vi, vẫy tay một cái: “Tới đi!”"),i("br"),i("br"),n._v("“Được!” Thanh niên gật gật đầu, chính muốn động thủ, một bên một người học viên có chút mất hứng: “Triệu Thanh, ngươi có ý tứ gì? Loại này tỷ thí cơ hội tốt, ngươi chẳng lẽ muốn một người độc chiếm?”"),i("br"),i("br"),n._v("“Ta...” Thanh niên vò đầu."),i("br"),i("br"),n._v("“Được rồi, ta nhìn mấy cái này bằng hữu thực lực cũng không tính là quá kém, loại này đối luyện cơ hội không nhiều, Triệu Thanh, ngươi tặng cho Tống Doanh đi!”"),i("br"),i("br"),n._v("Nhược Hoan nói."),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Thanh niên Triệu Thanh mặt mũi không thôi nhẹ gật đầu."),i("br"),i("br"),n._v("“Các ngươi...”"),i("br"),i("br"),n._v("Đi tới tu luyện tràng Chiến sư, đều muốn tức khóc."),i("br"),i("br"),n._v("Cái gì cũng không tính là quá kém?"),i("br"),i("br"),n._v("Chúng ta rất mạnh được không?"),i("br"),i("br"),n._v("Nhưng mà... Vừa quay đầu nhìn thấy hai cái đầu ngã trên mặt đất đồng bạn, một hơi liền giấu ở ngực rốt cuộc nói không nên lời."),i("br"),i("br"),n._v("Bị nhẹ nhõm đánh bại... Cái này cũng gọi rất mạnh?"),i("br"),i("br"),n._v("Tại trong mắt đối phương... Có lẽ thật chỉ là không sai đi!"),i("br"),i("br"),n._v("“Bớt nói nhảm, tới đi!”"),i("br"),i("br"),n._v("Cắn răng một cái, vị này Chiến sư nhìn lại."),i("br"),i("br"),n._v("“Phập phồng không yên, làm sao có thể giao đấu...”"),i("br"),i("br"),n._v("Đi lên phía trước, Tống Doanh lắc đầu."),i("br"),i("br"),n._v("“Ngươi...” Vị này Chiến sư mặc dù nổi giận nhưng cũng biết, hắn hiện tại có chút thất thố, hít sâu một hơi, đem buồn bực trong lòng ngăn chặn, lần nữa nhìn lại: “Bắt đầu đi!”"),i("br"),i("br"),n._v("Thấy hắn nhanh như vậy liền ngăn chặn trong lòng xao động, Tống Doanh lộ ra vẻ ngạc nhiên, lập tức nhẹ gật đầu: “Bắt đầu!”"),i("br"),i("br"),n._v("Nói xong thân thể nhoáng một cái, chui ra."),i("br"),i("br"),n._v("Bành bành bành bành!"),i("br"),i("br"),n._v("Hai người lập tức giao thủ."),i("br"),i("br"),n._v("Ba chiêu qua đi, Chiến sư hơi đỏ mặt, bị đối phương bắt lấy một cái sơ hở, một cước đá bay ra ngoài, ngã xuống đất."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Thấy liền thêm lên ba cái, đều không phải là đối thủ của bọn họ, rất nhiều Chiến sư hai mặt nhìn nhau."),i("br"),i("br"),n._v("Vốn cho rằng, Trác Thanh Phong nói ngoa, cố ý phủ lên Hồng Viễn học viện đáng sợ, tận mắt nhìn đến, mới hiểu được, thật rất cường đại."),i("br"),i("br"),n._v("“Các ngươi chẳng lẽ là... Lần này cần tham gia Chiến sư tuyển chọn Trịnh Dương, Lưu Dương đám người?”"),i("br"),i("br"),n._v("Một vị Chiến sư thực sự nhịn không được, hỏi."),i("br"),i("br"),n._v("“Trịnh Dương, Lưu Dương?”"),i("br"),i("br"),n._v("Nghe nói như thế, Nhược Hoan cười khổ lắc đầu: “Bọn họ là viện trưởng thân truyền đệ tử, so với chúng ta lợi hại gấp trăm lần, làm sao có thể so sánh cùng nhau!”"),i("br"),i("br"),n._v("“Không phải? Cái kia chẳng lẽ... Các ngươi là năm nay thông qua Chiến sư khảo hạch cái kia trong một trăm người ba vị?” Liêu Tùng hỏi."),i("br"),i("br"),n._v("“Một trăm người?” Nhược Hoan lần nữa lắc đầu: “Thực lực chúng ta quá thấp, không tư cách tham gia Chiến sư tuyển chọn...”"),i("br"),i("br"),n._v("Không tư cách tham gia tuyển chọn?"),i("br"),i("br"),n._v("Liêu Tùng nhoáng một cái, chỉ cảm thấy ngực khó chịu: “Vậy các ngươi là...”"),i("br"),i("br"),n._v("“Chúng ta là Huyền Huyền hội... Học viện còn có hơn ba vạn hội viên, cùng chúng ta sức chiến đấu xấp xỉ!”"),i("br"),i("br"),n._v("Nhược Hoan công tử vẻ mặt thành thật nhìn qua."),i("br"),i("br"),n._v("“Chúng ta loại thực lực này, tại trong hội, không tính là gì, một trảo một nắm lớn...”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-971"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-973"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);