(window.webpackJsonp=window.webpackJsonp||[]).push([[2269],{2624:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-965-long-mon-tran"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-965-long-mon-tran"}},[n._v("#")]),n._v(" Chương 965: Long Môn trận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 965: Long Môn trận"),t("br"),t("br"),n._v("“Hóa thân thành rồng?”"),t("br"),t("br"),n._v("“Nhân vật phong vân?”"),t("br"),t("br"),n._v("Tất cả mọi người nhíu mày."),t("br"),t("br"),n._v("Chiến sư đường mặc dù mạnh mẽ, nhưng bọn hắn cũng chỉ thuộc về phân bộ, một lần khảo hạch, liền muốn danh chấn toàn bộ đại lục, có phần quá nói ngoa đi!"),t("br"),t("br"),n._v("“Chư vị đừng không tin, Long Môn trận muốn thông qua không dễ dàng như vậy, Chiến sư đường vô số tuổi trẻ Chiến sư, ba trăm năm qua, không có một cái thành công! Như vậy có thể thấy được khó khăn, nếu quả thật có thể thông qua... Khẳng định sẽ được tổng bộ trực tiếp mượn đi, oanh động thiên hạ, chỉ là vấn đề thời gian!”"),t("br"),t("br"),n._v("Thấy vẻ mặt của mọi người, Liêu Tùng biết bọn họ không tin, giải thích nói."),t("br"),t("br"),n._v("“Ba trăm năm qua không có một cái thành công?”"),t("br"),t("br"),n._v("Mọi người líu lưỡi."),t("br"),t("br"),n._v("Chiến sư đường mạnh mẽ, bọn họ thấm sâu trong người, thấu hiểu rất rõ, nhiều người trẻ tuổi tài tuấn, thiên tài đều không có người thành công, có phần thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Ngươi nói cái này Long Môn trận đến cùng như thế nào xông, có cái gì quy củ?” Mi trưởng lão nhịn không được hỏi."),t("br"),t("br"),n._v("Đem những người khác cũng đồng loạt nhìn lại."),t("br"),t("br"),n._v("Đối phương nói khoa trương như vậy, cái này Long Môn trận, tất nhiên vô cùng khó khăn, bằng không thì cũng không có khả năng để vô số Chiến sư cũng chùn bước."),t("br"),t("br"),n._v("“Rất đơn giản, Long Môn trận chia làm đuôi rồng, lưng rồng cùng đầu rồng ba cái cửa ải, mỗi một vị trí, cũng có ba người tu luyện! Đuôi rồng ba người, phân biệt cùng vượt quan người, lẫn nhau cùng cấp bậc, cao một cái tiểu cấp bậc, cao hai cái tiểu cấp bậc...”"),t("br"),t("br"),n._v("Liêu Tùng nói."),t("br"),t("br"),n._v("“Ba người? Hơn nữa còn cấp bậc càng ngày càng cao?” Mọi người lông mày nhảy một cái."),t("br"),t("br"),n._v("Chiến sư bản thân sức chiến đấu liền mạnh, có thể cùng cấp bậc chiến thắng liền rất lợi hại, ba người cùng một chỗ tiến công, còn có tu vi cao hai cái tiểu cấp bậc, một cái tiểu cấp bậc... Cái này liền đáng sợ!"),t("br"),t("br"),n._v("“Lưng rồng bên trên, đồng dạng ba người, phân biệt so vượt quan người cao ba cái tiểu cấp bậc, bốn cái, năm cái, đồng dạng tăng lên!”"),t("br"),t("br"),n._v("Liêu Tùng giải thích: “Thật giống như trước mắt vị này Trịnh Dương, Tằm Phong cảnh sơ kỳ, lưng rồng bên trên người cao hơn hắn ba, bốn, năm cái tiểu cấp bậc, cũng chính là Tằm Phong cảnh đỉnh phong, Bán Thánh sơ kỳ cùng Bán Thánh trung kỳ!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người da mặt co lại, ngay cả Trịnh Dương, cũng nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Cuối cùng là đầu rồng, cũng là ba người, cấp bậc thì cao hơn, so vượt quan người cao sáu, bảy, tám ba cái tiểu cấp bậc, đồng dạng lấy Trịnh Dương làm ví dụ, cũng chính là theo thứ tự là, Bán Thánh hậu kỳ, Bán Thánh đỉnh phong, cùng... Tòng Thánh sơ kỳ!”"),t("br"),t("br"),n._v("“Muốn xông qua Long Môn trận, nhất định phải đem chín người này toàn bộ đánh bại! Mà những người này, thấp nhất đều là bách phu trưởng cấp bậc, trọng yếu nhất chính là... Bọn họ liên thủ sức chiến đấu mạnh hơn, ta loại thực lực này, liền đuôi rồng, cũng không vượt qua nổi...”"),t("br"),t("br"),n._v("Liêu Tùng nói."),t("br"),t("br"),n._v("Nghe được quy tắc, tất cả mọi người trầm lặng."),t("br"),t("br"),n._v("Nói thật thực sự quá khó khăn."),t("br"),t("br"),t("br"),n._v("Chiến sư đường bách phu trưởng, mỗi một cái cũng có vượt cấp năng lực chiến đấu, một chút xuất hiện ba cái, hơn nữa từng cái thực lực cũng so vượt quan người cao hơn... Quả thực liền không khả năng thành công!"),t("br"),t("br"),n._v("Khó trách Chiến sư đường ba trăm năm qua, không có một cái nào thông qua, loại này khảo hạch, quả thực biến thái!"),t("br"),t("br"),n._v("E là cho dù là thống lĩnh, đều không thể thông qua."),t("br"),t("br"),n._v("“Thế nào, có muốn hay không thử một lần?” Giải thích xong, Liêu Tùng ngẩng đầu nhìn về phía Trịnh Dương đám người."),t("br"),t("br"),n._v("Không có trực tiếp trả lời, mọi người cúi đầu trầm tư."),t("br"),t("br"),n._v("Trác Thanh Phong chính là bách phu trưởng thực lực, Trịnh Dương tới giao thủ qua, biết đáng sợ, liên tục chiến thắng chín vị, thực lực tăng lên không nói, còn muốn liên thủ... Độ khó bao lớn, có thể nói kinh khủng!"),t("br"),t("br"),n._v("“Thực tế không dám giấu giếm, ta nói cái này Long Môn trận, chỉ là đơn giản hóa nhất, tổng bộ Long Môn trận, vượt qua đầu rồng, còn cần khiêu chiến Long châu! Long châu vị trí bên trên, có một vị thực lực cao hơn cường giả chờ đợi...”"),t("br"),t("br"),n._v("Liêu Tùng nói."),t("br"),t("br"),n._v("Tổng bộ Long Môn trận càng khó, hắn đây chỉ là đơn giản hóa nhất phiên bản."),t("br"),t("br"),n._v("Dù vậy, ba trăm năm qua, có thể thông qua loại này phiên bản, cũng tuyệt đối không cao hơn hai tay số lượng!"),t("br"),t("br"),n._v("Nói cách khác, Chiến sư đường ba trăm năm qua, vô số tuổi trẻ thiên tài tính gộp lại, đều không có mười cái thông qua, như vậy có thể thấy được độ khó."),t("br"),t("br"),n._v("“Ta muốn thử một lần...”"),t("br"),t("br"),n._v("Không có do dự quá lâu, Trịnh Dương nhìn lại, ánh mắt kiên định."),t("br"),t("br"),n._v("Hắn muốn thử xem!"),t("br"),t("br"),n._v("Thật ra thì hắn biết, đi theo lão sư đằng sau, trưởng thành nhanh nhất, sức chiến đấu cũng mạnh nhất, nhưng mà cái này muốn một hồi đi theo, chính là lão sư vướng víu..."),t("br"),t("br"),n._v("Vì bọn họ, lão sư quá mệt mỏi!"),t("br"),t("br"),n._v("Theo võ giả tu luyện tới Hóa Phàm, liền hao tốn không biết bao nhiêu vật tư, tiêu hao không biết bao nhiêu linh thạch, một khi đến Thánh vực, mỗi tăng lên một cái cấp bậc, sử dụng vật tư càng là lượng lớn, loại này gánh nặng để một mình hắn tiếp nhận, làm học sinh, thật sự là rất bất hiếu!"),t("br"),t("br"),n._v("Có thể để bọn hắn đi tìm, khó mà tìm không nói, lão sư khẳng định cũng không nguyện ý..."),t("br"),t("br"),n._v("Suy đi nghĩ lại, biện pháp tốt nhất, chính là đi vào Chiến sư đường..."),t("br"),t("br"),n._v("Nơi này Chiến sư hội tụ, nắm giữ toàn bộ đại lục tốt nhất vật tư, chỉ cần trở thành một thành viên, liền không còn là lão sư vướng víu, thậm chí, về sau có thể vì lão sư phân ưu giải nạn."),t("br"),t("br"),n._v("Dù là lần khảo hạch này mười phần nguy hiểm, mười phần khó khăn, vì lão sư, cũng muốn đi làm!"),t("br"),t("br"),n._v("Giống như lão sư vì bọn họ, cái gì đều có thể từ bỏ đồng dạng."),t("br"),t("br"),n._v("“Thử một chút? Được!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Liêu Tùng gật đầu cười, nhìn về phía Vương Dĩnh đám người: “Các ngươi đâu?”"),t("br"),t("br"),n._v("“Chúng ta cũng muốn thử một chút!”"),t("br"),t("br"),n._v("Vương Dĩnh đám người, nguyên một đám ánh mắt kiên định."),t("br"),t("br"),n._v("Trịnh Dương ý nghĩ, bọn họ tự nhiên rõ ràng, lão sư những năm này vì bọn họ, đã trả giá quá nhiều... Thật không đành lòng, lại để cho hắn tiếp tục vì bọn họ vất vả."),t("br"),t("br"),n._v("Trở thành Chiến sư, xông ra một phiến thiên địa, có lẽ cũng là lão sư nguyện vọng."),t("br"),t("br"),n._v("Không có lão sư nào, vẫn muốn đem học sinh lưu tại bản thân dưới cánh chim, không để cho giương cánh bay cao."),t("br"),t("br"),n._v("“Vậy chúng ta liền đi đài diễn võ đi!”"),t("br"),t("br"),n._v("Thấy khảo hạch nội dung xác định, Trịnh Dương mấy người cũng đồng ý, Mi trưởng lão nói."),t("br"),n._v(" “Được!”"),t("br"),t("br"),n._v("Biết nơi này cũng không phải địa phương chiến đấu, mọi người đồng thời gật đầu, đồng loạt hướng đài diễn võ đi đến."),t("br"),t("br"),n._v("Lúc này đài diễn võ, đã kín người hết chỗ, biết lần này khảo hạch, so với một lần trước càng thêm khó khăn, sáng sớm vô số học sinh liền đem vị trí chiếm tốt, thậm chí Hồng Viễn thành không ít cường giả cũng đặc biệt tới quan sát."),t("br"),t("br"),n._v("Lại thêm thời gian qua ba ngày, Hồng Viễn đế quốc xung quanh không ít Đế nước cường giả cũng tới."),t("br"),t("br"),n._v("Cả đám đều muốn mở mang kiến thức một chút, không phải danh sư, như thế nào khảo hạch Chiến sư, lại phải kinh lịch cỡ nào khảo nghiệm tàn khốc."),t("br"),t("br"),n._v("Trịnh Dương đám người đi tới qua trước mặt, lập tức đưa tới vô số người reo hò."),t("br"),t("br"),n._v("“Bọn họ chính là Trương viện trưởng học sinh? Thoạt nhìn rất trẻ trung ah...”"),t("br"),t("br"),n._v("“Trương viện trưởng tuổi tác cũng không lớn nha!”"),t("br"),t("br"),n._v("“Trương viện trưởng vài vị thân truyền bị các ngươi học viện mời tới, làm khách tọa trưởng lão?”"),t("br"),t("br"),n._v("“Đúng vậy a, bọn họ đối võ kỹ hiểu, xuất thần nhập hóa...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài nghị luận ầm ĩ."),t("br"),t("br"),n._v("Không ít lần đầu tiên tới học viện cường giả, nghe được Trịnh Dương đám người, lại là học viện khách tọa trưởng lão, tất cả đều sững sờ."),t("br"),t("br"),n._v("Khách tọa trưởng lão trọng lượng, bọn họ biết đến rất rõ ràng, mời một cái liền danh sư đều không phải là, hơn nữa liền mười tám cũng chưa tới người trẻ tuổi tới làm, cái kia mạnh bao nhiêu, mới có thể thu được như vinh hạnh đặc biệt này?"),t("br"),t("br"),n._v("“Viện trưởng cùng đám học sinh của hắn, đều là truyền kỳ...”"),t("br"),t("br"),n._v("“Đúng vậy a, xem một chút đi, có lẽ, hôm nay... Chúng ta có thể chứng kiến truyền kỳ sinh ra...”"),t("br"),t("br"),n._v("Đem Trương sư cùng hắn tất cả học sinh nghe đồn nghe xong, tất cả mọi người trầm mặc xuống."),t("br"),t("br"),n._v("Lão sư lợi hại, học sinh cũng không có kẽ hở, đây mới thật sự là dạy và học cùng tiến bộ, chân chính thầy trò tình nghĩa!"),t("br"),t("br"),n._v("“Được rồi, Long Môn trận bắt đầu!”"),t("br"),t("br"),n._v("An bài tốt tất cả, Liêu Tùng hét dài một tiếng, bên cạnh chín vị Chiến sư, nhảy lên đài cao, chỗ đứng uốn lượn, như cùng một cái cự long hoành trên mặt đất, bất cứ lúc nào cũng sẽ phá không bay lượn."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Một hồi giòn vang, chín vị Chiến sư, rất mau đem tu vi toàn bộ áp chế tốt, theo Tằm Phong cảnh sơ kỳ, một đường lên cao, đạt đến Tòng Thánh."),t("br"),t("br"),n._v("“Tại hạ Chiến sư đường thiên phu trưởng Liêu Tùng, đặc biệt xin Long Môn trận, khảo nghiệm Chiến sư, mong rằng phê chuẩn!”"),t("br"),t("br"),n._v("Thấy đứng vào vị trí, giảm thấp xuống tu vi, Liêu Tùng lấy ra một cái lệnh bài, bàn tay lắc một cái, lơ lửng trên không, cao giọng hô lên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo lời của hắn, chung quanh linh khí lập tức tụ lại, một cỗ lực lượng khổng lồ, từ không biết tên nơi xa, lan tràn mà tới."),t("br"),t("br"),n._v("“Đây là... Chiến sư đường tổng bộ khí tức? Cái này Long Môn trận, đến cùng là cái gì? Cần xin tổng bộ đồng ý?”"),t("br"),t("br"),n._v("“Không biết, cái này cùng danh sư sinh tử chiến đồng dạng, cần câu thông tổng bộ mới có thể thực hiện, nhìn tới... Đây cũng không phải là đồng dạng khảo hạch!”"),t("br"),t("br"),n._v("“Đúng vậy a, để tổng bộ giám sát, mới có thể đi vào đi, nói rõ bọn họ không có khảo hạch quyền lợi, thậm chí khảo hạch cũng không được thừa nhận!”"),t("br"),t("br"),n._v("Dưới đài mọi người, không biết Long Môn trận ý tứ, nhưng nhìn thấy thế mà cần Liêu Tùng câu thông tổng bộ, tất cả đều hoảng sợ biến sắc."),t("br"),t("br"),n._v("Bất kỳ một cái nào chức nghiệp, chỉ cần câu thông tổng bộ, đều cần tiêu tốn cực lớn đánh đổi, một cái khảo hạch liền muốn như vậy làm, cái kia có bao nhiêu khó?"),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Mọi người trong lúc khiếp sợ, trên bầu trời linh khí, chiếu xạ trên mặt đất, tựa như bố trí một cái kỳ quái trận pháp, đem chín người chia làm ba bộ phận che giấu, lộ ở bên ngoài, hình như một cái cự long đuôi rồng, nhẹ khẽ vẫy một cái, co rúm phong vân, cho người ta một loại cảm giác áp bách mãnh liệt."),t("br"),t("br"),n._v("“Đây là... Trận pháp gia trì?”"),t("br"),t("br"),n._v("Ô Thiên Khung đám người da mặt co lại."),t("br"),t("br"),n._v("Đuôi rồng ba người, bản thân thực lực mạnh ngược lại cũng thôi, còn có trận pháp gia trì, thế thì còn đánh như thế nào?"),t("br"),t("br"),n._v("Đừng nói thực lực so với bọn hắn thấp, coi như một cái Bán Thánh đi lên, chỉ sợ đều khó mà chống lại."),t("br"),t("br"),n._v("“Trịnh Dương... Cẩn thận!”"),t("br"),t("br"),n._v("Vương Dĩnh cũng mày nhăn lại."),t("br"),t("br"),n._v("“Yên tâm đi!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Trịnh Dương nhẹ gật đầu, nhấc chân nhảy lên đài cao, nhẹ nhàng cười một tiếng, thanh âm đám lãng vang lên: “Trương Huyền Trương sư, học sinh thân truyền, Trịnh Dương, xông Long Môn trận!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lời nói kết thúc, một đạo quang mang đem bao phủ, tựa hồ tại xác nhận thân phận cùng thực lực."),t("br"),t("br"),n._v("Rất nhanh, xác nhận xong xuôi, bầu trời hiện ra hai chữ lớn “Đồng ý!”."),t("br"),t("br"),n._v("Cùng sinh tử đấu, giống nhau như đúc."),t("br"),t("br"),n._v("Nói cách khác, hiện tại khiêu chiến của hắn, đem tiếp nhận Chiến sư đường tổng bộ giám sát."),t("br"),t("br"),n._v("“Được rồi, có thể bắt đầu...”"),t("br"),t("br"),n._v("Thấy tổng bộ xác nhận, liêu buông thở phào nhẹ nhõm, phân phó một tiếng."),t("br"),t("br"),n._v("“Ừm!” Trịnh Dương nhẹ gật đầu, cổ tay khẽ đảo, lấy ra một cây trường thương."),t("br"),t("br"),n._v("Trường thương nơi tay, khí tức cả người, lập tức thay đổi, như rồng như hổ, toàn thân chân khí xao động bên dưới, tóc tung bay, như là một tôn bất bại Chiến Thần."),t("br"),t("br"),n._v("“Thật mạnh!”"),t("br"),t("br"),n._v("Cảm nhận được cỗ khí tức này, vốn cũng không thèm để ý tất cả Chiến sư, nguyên một đám vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Tu vi của đối phương mặc dù chỉ có Tằm Phong cảnh sơ kỳ, nhưng lại cho người ta một loại, cường đại đến cực điểm cảm giác, ngay cả Bán Thánh, đều khó mà chống lại."),t("br"),t("br"),n._v("“Tới đi!”"),t("br"),t("br"),n._v("Đem khí tức trên thân tích trữ tới cực điểm, hét dài một tiếng, Trịnh Dương thân thể nhoáng một cái, đã đi tới đuôi rồng ba người trước mặt, trường thương hóa thành điểm sáng, trực tiếp đâm tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thương mang xao động, sóng khí lăn mình, toàn bộ đài diễn võ dường như ẩn chứa cự lôi, một chút nổ tung."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-973"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-975"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);