(window.webpackJsonp=window.webpackJsonp||[]).push([[2271],{2626:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-967-truong-lao-su-cac-hoc-sinh-trung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-967-truong-lao-su-cac-hoc-sinh-trung"}},[n._v("#")]),n._v(" Chương 967: Trương lão sư các học sinh (trung)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 967: Trương lão sư các học sinh (trung)"),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Nhìn thấy quấn ở chung quanh công kích, Trịnh Dương cũng không có bối rối, ngược lại nhẹ nhàng cười một tiếng, mắt sáng rực lên."),t("br"),t("br"),n._v("“Tới thật đúng lúc!”"),t("br"),t("br"),n._v("Một tiếng hô khẽ, không để ý đến đối phương bất luận cái gì công kích, ngược lại trực tiếp hướng trên không nhảy tới."),t("br"),t("br"),n._v("“Đây là muốn làm gì? Người trên không trung, sơ hở càng lớn, sẽ chỉ thua nhanh hơn!”"),t("br"),t("br"),n._v("“Gặp được công kích, tránh cũng không thể tránh, sẽ chỉ rơi vào càng thêm cục diện bị động, đây là muốn trước thời hạn nhận thua ư?”"),t("br"),t("br"),n._v("“Làm như vậy mặc dù có thể tạm thời trốn tiến công, nhưng để cho mình lâm vào mới tình thế nguy hiểm, là thất bại nhất chiêu số...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài một hồi xôn xao."),t("br"),t("br"),n._v("Vừa rồi cái này Trịnh Dương, bất kể chiêu số vẫn là ý thức chiến đấu, đều làm người kinh diễm, làm sao thời điểm mấu chốt nhất, xuất hiện như thế lớn sai lầm?"),t("br"),t("br"),n._v("Tằm Phong cảnh là không thể phi hành, tại mặt đất thành thành thật thật, còn có một trận chiến khả năng, bay đến trên không chẳng khác nào thành người ta mục tiêu công kích, một khi tiến công liên tiếp không ngừng, lại muốn tránh lóe cũng không kịp."),t("br"),t("br"),n._v("Mọi người trong khiếp sợ, Trịnh Dương người trên không trung, bình di vài mét, trong nháy mắt liền đem phía dưới công kích toàn bộ tránh khỏi, sau một khắc, bàn chân đạp trên hư không, lăng không lơ lửng, biến thành một cái trên chân dưới đầu dáng vẻ, mũi thương hướng về phía trước đâm đi."),t("br"),t("br"),n._v("“Hắn đây là tại phi hành?”"),t("br"),t("br"),n._v("“Tằm Phong cảnh làm sao có thể phi?”"),t("br"),t("br"),n._v("Tất cả mọi người đều ngây người."),t("br"),t("br"),n._v("Chỉ có tu vi đột phá Bán Thánh, mở ra Trục Không huyệt, tu luyện giả mới có cơ hội bay đến bầu trời, bay lượn trong mây."),t("br"),t("br"),n._v("Người thiếu niên trước mắt này, chỉ có Hóa Phàm cửu trọng Tằm Phong cảnh, làm sao có thể trên không trung tùy ý bay lượn, mà không rơi xuống?"),t("br"),t("br"),n._v("“Nhìn tới lão sư đem Hồng Trần Đạp Thiên bộ cũng truyền thụ cho hắn...” Vương Dĩnh cười cười."),t("br"),t("br"),n._v("Lão sư mới đi vào Hóa Phàm, liền có thể trên không trung tùy ý phi hành, chính là tu luyện chiêu này, hiện tại Trịnh Dương cũng đang phi hành, rõ ràng nhận được chân truyền."),t("br"),t("br"),n._v("“Trịnh Dương rất thông minh, dựa theo tình huống bình thường, ba người này thi triển ra tuyệt chiêu, hắn là không cách nào chiến thắng. Cố ý lộ ra sơ hở, làm cho tất cả mọi người công kích, tập trung lại, mà hắn nhưng mượn nhờ mọi người không có thể phi hành tư duy theo quán tính, một lần đi tới trên không, không chỉ có hóa giải tình thế nguy hiểm, còn đem bị động chuyển hóa làm chủ động, cái này ba người bọn hắn xong...”"),t("br"),t("br"),n._v("Lưu Dương nói."),t("br"),t("br"),n._v("Người bình thường đều cho rằng Tằm Phong cảnh, không có thể phi hành, căn bản cũng không có phòng ngự trên không, vậy thì cho Trịnh Dương lưu lại quá lớn cơ hội."),t("br"),t("br"),n._v("Lời còn chưa dứt, liền thấy trống không trường thương, hóa thành vạn điểm ánh sáng, bắn đi, rơi vào đài cao đem tiến công ba vị Chiến sư toàn bộ bao phủ ở bên trong."),t("br"),t("br"),n._v("Bành! Bành! Bành!"),t("br"),t("br"),n._v("Lực lượng hướng bốn phía bắn nhanh, ba vị Chiến sư sắc mặt trắng nhợt, đồng thời bay ngược mà ra, thậm chí có hai cái trực tiếp té ra lôi đài."),t("br"),t("br"),n._v("“Không có ý tứ, ta thắng...”"),t("br"),t("br"),n._v("Nắm chặt trường thương, đứng trên đài, Trịnh Dương nhàn nhạt ngắm nhìn bốn phía, một cỗ khí ngạo nghễ tự nhiên sinh ra."),t("br"),t("br"),n._v("Để cho người ta cảm giác đến mức hoàn toàn không có hi vọng lưng rồng khảo hạch, cũng thuận lợi thông qua."),t("br"),t("br"),n._v("Thông qua cửa ải này, Trịnh Dương cũng không có gấp hướng về phía trước, là khoanh chân ngồi dưới đất, lấy ra một viên khôi phục thể lực đan dược, khôi phục hao tổn chân khí."),t("br"),t("br"),n._v("Nhìn thấy cử động của hắn, bốn phía tất cả đều thấp giọng, sợ quấy rầy hắn tu luyện."),t("br"),t("br"),n._v("Dưới đài Liêu Tùng càng là nắm đấm xiết chặt, kích động vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Thông qua đuôi rồng cùng lưng rồng đã nói rõ thực lực, đi vào Chiến sư đường, thật tốt huấn luyện, một khi thực lực đạt tới, ít nhất đều là thống lĩnh cấp bậc, loại này cường giả bị bản thân phát hiện, chỗ tốt không thể nghi ngờ."),t("br"),t("br"),n._v("Có thể nói coi như không có thông qua phía sau đầu rồng, cũng kiếm lợi lớn."),t("br"),t("br"),n._v("Nghỉ ngơi gần nửa canh giờ, Trịnh Dương lúc này mới đứng dậy, giờ phút này lực lượng trong cơ thể, như là như gió bão quét sạch, phát ra trầm muộn lôi âm."),t("br"),t("br"),n._v("Mỗi tiến về phía trước một bước, khí thế trên người liền tăng cường một phần, đi đến bước thứ tám thời điểm, đột nhiên một tiếng nổ vang, Tằm Phong cảnh sơ kỳ tu vi đột phá, đạt đến trung kỳ!"),t("br"),t("br"),n._v("“Đây là lâm chiến đột phá?”"),t("br"),t("br"),n._v("Ô Thiên Khung ngẩn ngơ, nhịn không được nhìn về phía cách đó không xa Liêu Tùng: “Canh giữ ở cửa thứ ba người cũng sẽ tương ứng tăng thực lực lên ư?”"),t("br"),t("br"),n._v("Long Môn trận đuôi rồng, lưng rồng, đầu rồng dựa theo vượt quan người tu luyện đẳng cấp tiến hành sắp xếp, hiện tại đột phá, phía sau cửa ải, có thể hay không cũng căn cứ tình huống thực tế, tiếp tục tăng lên độ khó?"),t("br"),t("br"),n._v("“Không biết, khảo hạch chỉ có một cái canh giờ, thời điểm chiến đấu lâm thời đột phá, thuộc về ứng biến, phía sau sẽ không sửa đổi.”"),t("br"),t("br"),n._v("Liêu Tùng nói."),t("br"),t("br"),n._v("Khảo hạch chiến tử, công bằng công chính, có thể tại thời điểm chiến đấu đột phá, là ngươi bản lãnh của mình, coi như mặt sau này chênh lệch giảm bớt, cũng không có khả năng lâm thời sửa đổi."),t("br"),t("br"),n._v("Cuối cùng, lâm trận đột phá, một cái tiểu cấp bậc thì cũng thôi đi, không ảnh hưởng toàn cục, cũng không thể, trực tiếp đột phá một cái đại cấp bậc đi!"),t("br"),t("br"),n._v("Hai người nói chuyện ở giữa, trên đài Trịnh Dương tiếp tục đi đến phía trước, ba bước qua đi, vừa mới tấn thăng đến Tằm Phong cảnh trung kỳ tu vi cũng đã hoàn toàn củng cố."),t("br"),t("br"),n._v("Tiếp tục tiến lên, chân khí trong cơ thể sôi trào như sấm, cùng vừa rồi đồng dạng, khí tức tiếp tục kéo lên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bước thứ tám về sau, giống như là lần nữa phá vỡ ràng buộc, đạt đến Tằm Phong cảnh hậu kỳ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Dưới đài mọi người tất cả đều lông mày nhảy một cái, gặp qua đột phá nhanh, chưa thấy qua nhanh như vậy."),t("br"),t("br"),n._v("Đi tầm mười bước, tu vi đã đột phá hai cấp, quả thực chính là không thể tưởng tượng."),t("br"),t("br"),n._v("Chẳng qua Trịnh Dương dường như còn không đình chỉ ở đây, tiếp tục hướng phía trước, tinh thuần lực lượng vẫn như cũ lăn mình không ngừng."),t("br"),n._v("Đợi đi tới đầu rồng vị trí thời điểm, đã đạt đến Tằm Phong cảnh đỉnh phong!"),t("br"),t("br"),n._v("“Mấy ngày nay, Trịnh Dương một mực súc tích lực lượng, áp chế tu vi không đột phá, rốt cục làm được hậu tích bạc phát...”"),t("br"),t("br"),n._v("Vương Dĩnh nở nụ cười."),t("br"),t("br"),n._v("Muốn khảo hạch Chiến sư tin tức về sau, bản thân vị sư đệ này, một mực cố gắng tu luyện nhưng áp chế tu vi, không đi đột phá, mục đích đúng là vì hiện tại, một lần chạy nước rút cảnh giới cao hơn, đánh đối phương trở tay không kịp."),t("br"),t("br"),n._v("Đương nhiên, có thể làm được điểm ấy, cũng là lão sư truyền thụ công pháp thần kỳ, đổi lại cái khác, có thể tấn cấp một cái tiểu cấp bậc, coi như rất lợi hại, một hơi tăng lên ba cái, quả thực chính là không thể tưởng tượng nghe rợn cả người."),t("br"),t("br"),n._v("“Hắn chiêu này không sai, trước đó cùng đầu rồng chênh lệch vẫn còn lớn, hiện đang không ngừng thu nhỏ, nếu như ta vượt quan lời nói cũng có thể dùng một chút...”"),t("br"),t("br"),n._v("Lưu Dương cười nói."),t("br"),t("br"),n._v("Hai người trò chuyện, trên đài Trịnh Dương cũng không có gấp tiến công, mà là cổ tay khẽ đảo, một cái xích trái cây màu đỏ để vào trong miệng."),t("br"),t("br"),n._v("“Đây là... Xích Huỳnh quả?”"),t("br"),t("br"),n._v("Dưới đài Ngô Hư cùng Lộc Thành nhận ra được, khóe miệng kìm lòng không được đồng thời co lại."),t("br"),t("br"),n._v("Lúc trước bọn họ đi sơn cốc kia, chính là vì tìm thứ này, kết quả tay không mà về, một mực suy đoán có phải hay không Trương Huyền đem trái cây lấy đi, hiện tại xem ra đúng là như thế."),t("br"),t("br"),n._v("Thứ này là đột phá Bán Thánh lợi khí, trên đài cái tên này sẽ không liên tục tăng lên ba cái tiểu cấp bậc sau... Còn muốn trực tiếp trùng kích Bán Thánh đi."),t("br"),t("br"),n._v("Bán Thánh cùng Tằm Phong cảnh chênh lệch rất lớn, một khi thành công, thực lực tất nhiên có bay vọt về chất, đến lúc đó xông qua đầu rồng, liền có thể dễ như trở bàn tay."),t("br"),t("br"),n._v("“Qua sông chưa tế đánh hắn giữa dòng...” Không chỉ hắn nhìn ra vấn đề này, Liêu Tùng cũng nhìn ra đến, nhịn không được hét lớn."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Rồng đầu vị trí ba vị Chiến sư, nghe được tiếng la, tất cả đều lông mày giương lên, giơ lên binh khí trong tay, đồng loạt công kích mà tới."),t("br"),t("br"),n._v("Dù sao đối phương đã đi tới chiến đấu phạm vi, làm như vậy cũng không tính là trái lệ."),t("br"),t("br"),n._v("“Hắc hắc, vừa vặn cỗ lực lượng này không cách nào tiêu hóa, liền bắt các ngươi tới luyện thương đi!”"),t("br"),t("br"),n._v("Hai hàng lông mày giương lên, Trịnh Dương trường thương tiến lên đón."),t("br"),t("br"),n._v("Xích Huỳnh quả là đột phá Bán Thánh bảo vật, đơn độc luyện hóa lời nói khả năng cần ít nhất một hai canh giờ thời gian, mà có đối phương công kích áp lực bức bách, tốc độ liền sẽ nhanh lên rất nhiều."),t("br"),t("br"),n._v("Lúc trước tại Vạn Quốc liên minh, Trương Huyền liền dùng qua loại phương pháp này, để Hóa Phàm tứ trọng Trọc Thanh cảnh thực lực cấp tốc tăng lên."),t("br"),t("br"),n._v("Giờ phút này, muốn bình thường vượt qua đuôi rồng, gần như không có khả năng, mượn nhờ Xích Huỳnh quả, khả năng tuyệt đối sẽ gia tăng gấp bội!"),t("br"),t("br"),n._v("Quả nhiên, nương theo Trịnh Dương cùng bọn hắn giao thủ thời gian càng dài, lực lượng trong cơ thể liền càng cường đại càng dâng trào, thật giống như cả người hắn là không biết mệt mỏi động cơ vĩnh cửu đồng dạng."),t("br"),t("br"),n._v("Không chỉ có như vậy, cỗ lực lượng này còn đang không ngừng hướng Bán Thánh chuyển hóa, liên tục chiến đấu 100 chiêu hơn về sau, trong cơ thể một tiếng kịch liệt nổ vang, cả cá nhân thực lực cá chép vượt long môn, phát sinh bay vọt về chất."),t("br"),t("br"),n._v("Bán Thánh, đến!"),t("br"),t("br"),n._v("Tu vi đạt tới Bán Thánh, sức chiến đấu tăng lên không chỉ một lần, trước đó, đầu rồng ba vị, sẽ rất khó chiến thắng, giờ phút này làm sao có thể ngăn lại được, mười chiêu qua đi, ba người liền bị trường thương cứ thế mà quét xuống lôi đài."),t("br"),t("br"),n._v("Long Môn trận, thông qua!"),t("br"),t("br"),n._v("“Cái này... Thật thành công?” Kích động nắm đấm xiết chặt, cứ việc tận mắt nhìn thấy, Liêu Tùng nhưng đến hiện tại đều có chút không dám tin tưởng."),t("br"),t("br"),n._v("Chiến sư đường vô số thiên tài, đều làm không được chuyện, liền danh sư đều không phải là mười sáu mười bảy tuổi thiếu niên, làm được dễ dàng... Quả thực chính là không thể tưởng tượng."),t("br"),t("br"),n._v("“Thông qua!”"),t("br"),t("br"),n._v("Trên bầu trời phụ trách giám sát tổng bộ ý niệm, lưu lại hai chữ chậm rãi lui đi, nói cách khác, lần khảo hạch này nhận được Chiến sư đường tổng bộ công nhận."),t("br"),t("br"),n._v("Về sau Trịnh Dương lại đi tổng bộ, chỉ cần khảo hạch Long châu vị trí là được, trước mặt tam quan, đã không tất yếu lặp lại khảo nghiệm!"),t("br"),t("br"),n._v("Mặc dù đây không phải hoàn chỉnh Long Môn trận, nhưng mà 300 năm qua có thể thông qua cũng không cao hơn mười vị... Nói rõ ràng một chút, thời khắc này Trịnh Dương đã trở thành chiến tử người dự bị một trong."),t("br"),t("br"),n._v("“Ta thành công...”"),t("br"),t("br"),n._v("Trịnh Dương nắm đấm xiết chặt, trong mắt chảy ra nước mắt."),t("br"),t("br"),n._v("Hắn chỉ là cái đối thương pháp tương đối yêu quý thiếu niên, xem như hơi có chút thiên phú, nếu như không phải gặp đến lão sư, khả năng về sau nhiều nhất tại Thiên Huyền vương quốc làm một cái thương pháp huấn luyện viên, tu vi chẳng qua Thông Huyền, tuổi thọ không hơn trăm năm, tầm thường vô vi, thường thường xoàng xĩnh, trăm năm về sau hóa thành bụi bặm, mảy may dấu vết cũng sẽ không lưu lại."),t("br"),t("br"),n._v("Là lão sư thay đổi vận mệnh của hắn, cho hắn lần thứ hai nhân sinh."),t("br"),t("br"),n._v("Là lão sư để hắn đi tới hôm nay tình trạng này, tu vi đột phá trước kia nằm mơ đều không dám nghĩ cảnh giới."),t("br"),t("br"),n._v("Là lão sư cho hắn tự tin, cho hắn nhân sinh mục tiêu, cho hắn biết vì cái gì mà phấn đấu."),t("br"),t("br"),n._v("“Lão sư yên tâm đi, ta nhất định sẽ càng đi càng xa, tại Chiến sư đường, xông ra thanh danh, sẽ không cô phụ ngươi đối kỳ vọng của ta!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trịnh Dương trong lòng âm thầm phát ra lời thề."),t("br"),t("br"),n._v("“Được rồi, Trịnh Dương, khảo hạch thông qua... Các ngươi còn muốn tiếp tục kiểm tra ư?”"),t("br"),t("br"),n._v("Trịnh Dương đi xuống lôi đài, Liêu Tùng quay đầu nhìn về phía Vương Dĩnh đám người."),t("br"),t("br"),n._v("“Đương nhiên!” Vương Dĩnh cười cười."),t("br"),t("br"),n._v("Coi như không là trở thành Chiến sư, cũng muốn chứng minh thực lực của mình, là lão sư dương danh!"),t("br"),t("br"),n._v("“Sư tỷ, sư huynh, nếu không để cho ta trước đi thử một chút đi, ta nhập môn trễ nhất, nếu như ta có thể thông qua, các ngươi khẳng định cũng được!”"),t("br"),t("br"),n._v("Vương Dĩnh đang muốn nhảy lên đài cao, liền nghe đến một cái mang theo nhu nhược thanh âm vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn lại chỉ thấy Ngụy Như Yên, một đôi đôi mắt thanh tú, tản ra cực nóng ánh sáng."),t("br"),t("br"),n._v("(Phiền phức mọi người giúp lão nhai ném cái vé, mở ra điện thoại QQ, click bên trái phía dưới động thái, vị trí trung tâm có cái đọc, mở ra sau khi bên phải phía dưới có cái thư thành, click một chút trên cao nhất phong đẩy vị trí cắt mấy lần liền có thể nhìn thấy “Bầu bằng phiếu 2017 được hoan nghênh nhất tác giả”, kéo xuống phía dưới cùng có lão nhai bỏ phiếu vị trí, còn có lão nhai hình chụp. Cho ta tặng 1 phiếu ah! Cảm ơn mọi người! Bỏ phiếu, có thư tệ ban thưởng, mọi người còn có thể kiếm chút, mặt khác, chúng ta có thể đâm Trạch Trư hoa cúc, có thể vượt qua hắn, ta mời mọi người ăn thịt heo, hắc hắc!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-975"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-977"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);