(window.webpackJsonp=window.webpackJsonp||[]).push([[2274],{2629:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-970-moc-su-tro-ve"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-970-moc-su-tro-ve"}},[n._v("#")]),n._v(" Chương 970: Mộc sư trở về")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 970: Mộc sư trở về"),t("br"),t("br"),n._v("Giải thích cả buổi, nói nhiều lần viện trưởng lăng hoàn hảo không chút tổn hại, hai người từ đầu đến cuối nửa tin nửa ngờ, Trương Huyền bất đắc dĩ lắc đầu, chẳng muốn lại tiếp tục giải thích."),t("br"),t("br"),n._v("Thanh giả tự thanh, trọc giả tự trọc, giống như hắn dạng này ngay thẳng không có có tỳ vết người, thời gian sẽ trả một cái công đạo."),t("br"),t("br"),n._v("“Viện trưởng, ngươi đi năm ngày, Chiến sư đường người đã sớm tới, Trịnh Dương cùng Ngụy Như Yên thông qua được khảo hạch...”"),t("br"),t("br"),n._v("Lại không xoắn xuýt chuyện này, Mi trưởng lão đem mấy ngày chuyện phát sinh, kỹ càng nói một lần."),t("br"),t("br"),n._v("Nghe xong, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Trịnh Dương nhận được hắn thân truyền, thông qua khảo hạch, trong dự liệu, không nghĩ tới Ngụy Như Yên cũng thông qua được, nhìn như vậy đến, Chiến sư đường Chiến sư cũng chả có gì đặc biệt!"),t("br"),t("br"),n._v("Lại hỏi thăm một chút học viện chuyện, làm ra xử lý cùng ứng đối, Trương Huyền lúc này mới đi theo hai vị trưởng lão đằng sau, trở lại trưởng lão viện bản thân chỗ ở."),t("br"),t("br"),n._v("Thân là viện trưởng, Chiến sư đường mọi người, ba đại học viện viện trưởng, đến rồi nhiều ngày như vậy, hắn một lần đều không có thăm hỏi, thực sự không thể nào nói nổi."),t("br"),t("br"),n._v("Tắm rửa một cái, đem trong lòng đất dơ dáy bẩn thỉu quần áo đổi lại, đang định đi gặp một lần, mấy vị này danh sư, chỉ thấy quản gia Tôn Cường tràn đầy lo lắng đi tới nơi này."),t("br"),t("br"),n._v("“Thiếu gia!” Nhìn thấy hắn tới, vội vã tiến lên đón."),t("br"),t("br"),n._v("“Thế nào?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Theo sau lưng lâu như vậy, biết hắn đặc biệt ngại phiền phức, không có đặc biệt chuyện quan trọng, Tôn Cường bình thường đều sẽ tự mình xử lý, hiện tại chạy đến nơi đây chờ lấy, hơn nữa mặt mũi cuống cuồng, khẳng định là đã xảy ra chuyện gì."),t("br"),t("br"),n._v("“Như Yên tiểu thư nàng... Lại bất tỉnh!” Tôn Cường vội nói."),t("br"),t("br"),n._v("“Bất tỉnh?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Không nên nha!"),t("br"),t("br"),n._v("Ngụy Như Yên hồn phách đi qua Bồ Đề thụ tẩm bổ, đã cùng đang thường nhân không khác, thậm chí càng bền bỉ mấy phần."),t("br"),t("br"),n._v("Thân thể đi qua cấp bảy đan dược Đoạn Tục đan tẩm bổ, cũng khôi phục khỏe mạnh, càng là tu luyện tinh giản bản Thiên Đạo công pháp, liên chiến sư đều có thể nhẹ nhõm chiến thắng, làm sao đột nhiên hôn mê?"),t("br"),t("br"),n._v("“Nguyên nhân cụ thể ta cũng không rõ lắm, ngày đó khiêu chiến xong Chiến sư đường mọi người, trở về liền không thoải mái, sau đó liền trực tiếp bị bệnh...”"),t("br"),t("br"),n._v("Tôn Cường nói."),t("br"),t("br"),n._v("“Qua xem một chút đi!”"),t("br"),t("br"),n._v("Biết chỉ bằng vào nói như vậy, khẳng định không biết nguyên do, Trương Huyền ôm quyền cùng Mi trưởng lão đám người cáo từ, đi theo Tôn Cường sau lưng, vội vã hướng phủ đệ của mình đi đến."),t("br"),t("br"),n._v("Đi tới Ngụy Như Yên nơi ở, quả nhiên thấy nàng đang nằm ở trên giường, mày nhíu lại thành u cục, hôn mê bất tỉnh, Vương Dĩnh đang ngồi ở một bên, biết lòng chiếu cố."),t("br"),t("br"),n._v("Vị trí trong tiểu viện tất cả cây cối, giờ phút này cũng tất cả đều ỉu xìu đầu đạp não, tựa như đến mùa đông, từng cây khô héo muốn chết."),t("br"),t("br"),n._v("“Đây là có chuyện gì?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhăn lại."),t("br"),t("br"),n._v("“Lão sư, ngươi trở về...” Thấy hắn đi tới, Vương Dĩnh vội vàng đứng lên."),t("br"),t("br"),n._v("Đánh gãy đối phương tra hỏi, đi tới Ngụy Như Yên trước mặt, Trương Huyền ngón tay đáp lên trên cổ tay của đối phương."),t("br"),t("br"),n._v("Một lát sau, vẻ mặt nghiêm túc, ngẩng đầu nhìn tới: “Đem ngày đó Như Yên cùng Chiến sư đường giao đấu tình huống, kỹ càng nói với ta một lần!”"),t("br"),t("br"),n._v("“Vâng! Ngày đó Như Yên sư muội, lên đài sau cùng người chiến đấu, chân khí trong cơ thể lan tràn, để chín vị Chiến sư toàn đều trúng độc...” Vương Dĩnh không có chút nào giấu diếm, đem chuyện ngày đó kỹ càng nói một lần."),t("br"),t("br"),n._v("Mi trưởng lão đám người mặc dù biết Ngụy Như Yên vượt qua Chiến sư đường là dựa vào hạ độc, nhưng bẩm sinh độc thể chuyện cũng không rõ ràng, cũng nói không đủ kỹ càng."),t("br"),t("br"),n._v("Nàng nói đến, kỹ càng vô cùng, có thể nói đem tình huống lúc đó hoàn mỹ hoàn nguyên."),t("br"),t("br"),n._v("“Lão sư, nàng thế nào?” Nói xong, Vương Dĩnh khẩn trương nhìn qua."),t("br"),t("br"),n._v("Trương Huyền mày nhíu lại chặt: “Đến không có gì, chỉ là nàng bẩm sinh độc thể bạo phát... Lực lượng quá lớn, bản thân có chút áp chế không nổi, lúc này mới lâm vào hôn mê!”"),t("br"),t("br"),n._v("Ngụy Như Yên không riêng gì bẩm sinh độc thể, càng là bẩm sinh độc hồn thể, thuộc về toàn bộ đại lục mấy vạn năm cũng không tìm tới như nhau hiếm thấy thể chất."),t("br"),t("br"),n._v("Loại thể chất này chỉ cần tu luyện, chân khí đều là có mang kịch độc!"),t("br"),t("br"),n._v("Dưới tình huống bình thường, thể chất một khi kích hoạt, thực lực tăng lên rất nhanh, sức chiến đấu cũng rất mạnh, khiến cho người chùn bước."),t("br"),t("br"),n._v("Nhưng cũng có quá lớn thiếu hụt, cái kia chính là... Thể nội độc khí mỗi một lần sử dụng, liền sẽ lớn mạnh một phần, dùng càng nhiều, tích tụ càng nhiều."),t("br"),t("br"),n._v("Nếu như không có tốt phương pháp, đem hắn xua tan lời nói, rất dễ dàng bộc phát, lực lượng cường đại, để cho người ta hôn mê."),t("br"),t("br"),n._v("Nàng hiện tại chính là loại tình huống này, thực lực chưa đủ dưới tình huống, cưỡng ép khiêu chiến Long Môn trận, dùng xuất lực lượng vượt qua thân thể có thể thừa nhận được phạm vi lớn nhất, rốt cuộc áp chế không nổi, độc thể hoàn toàn bộc phát."),t("br"),t("br"),n._v("Nàng tu luyện chính là trời đạo công pháp bản rút gọn, chưa hề tu luyện qua liên quan tới độc sư bất kỳ cái gì công pháp, đối ở thể nội bộc phát lực lượng, không cách nào khơi thông, một cách tự nhiên liền hôn mê bất tỉnh."),t("br"),t("br"),n._v("“May mắn nàng tự kiềm chế năng lực rất mạnh, bằng không toàn bộ Hồng Viễn thành chỉ sợ đều muốn bị liên lụy...”"),t("br"),t("br"),n._v("Nói xong lời cuối cùng, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Chiến sư đường mọi người, đều gánh không được độc khí của nàng, như vậy có thể thấy được đáng sợ, nếu không phải nàng ý chí kiên định, trước khi hôn mê ngăn chặn lực lượng, một khi tùy ý khuếch tán lời nói, khí độc theo gió phiêu tán, không biết muốn có bao nhiêu người gặp nạn."),t("br"),t("br"),n._v("Chính là bởi vì bẩm sinh độc thể, không cách nào khống chế, cũng được thế nhân xưng là , để người chùn bước."),t("br"),t("br"),n._v("Coi như chế trụ khí tức, trong viện thực vật, quá mức tới gần, vẫn như cũ bị độc nhanh phải chết sạch, mới xuất hiện vừa rồi nhìn thấy một màn."),t("br"),t("br"),n._v("May mắn nàng tu luyện Thiên Đạo công pháp bản rút gọn, độc thể đặc tính hoàn mỹ che giấu, không có khiến người khác nhìn ra, nếu không sợ rằng sẽ bị vô số chính nghĩa chi sĩ vây công, toàn bộ phủ đệ đều muốn nổ."),t("br"),n._v(" “Lão sư, vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Nghe được giải thích, Vương Dĩnh vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Trước đó nàng còn một mực hâm mộ đối phương là bẩm sinh thể chất, không nghĩ tới đáng sợ như thế."),t("br"),t("br"),n._v("“Ta trước giúp nàng đem thể nội độc khí rút ra một bộ phận đi ra, hóa giải một chút áp lực, muốn giải quyết triệt để lời nói, có thể muốn đi Độc điện một chuyến...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Liên quan tới loại này bẩm sinh độc thể phương pháp giải quyết, hắn mặc dù y thuật cao minh, nhưng cũng cân nhắc không ra chính xác trình tự, duy nhất có thể giải quyết chỗ, hẳn là Độc điện."),t("br"),t("br"),n._v("Nơi này có quan hệ với “Độc” hoàn mỹ truyền thừa, cũng có quan hệ với độc thể ghi chép, có lẽ có thể tìm tới, để hắn tùy ý thi triển thực lực mà lại sẽ không xuất hiện khí độc tích lũy phương pháp."),t("br"),t("br"),n._v("“Độc điện?” Vương Dĩnh gật đầu."),t("br"),t("br"),n._v("Chỉ có thể có cái này một cái biện pháp."),t("br"),t("br"),n._v("Bàn tay đáp lên đối phương trên cổ tay, Thiên Đạo chân khí chậm rãi hướng Ngụy Như Yên trong cơ thể truyền vào, nương theo thời gian trôi qua, Trương Huyền không những không có trầm tĩnh lại, lông mày ngược lại càng nhăn càng chặt."),t("br"),t("br"),n._v("Vốn hắn cho rằng chỉ cần đem đối phương thể nội độc khí hóa giải mất, nghĩ biện pháp dẫn dắt đi ra là có thể giải quyết, hiện tại xem ra, vẫn là nghĩ quá đơn giản."),t("br"),t("br"),n._v("Đối phương độc trong người cùng thân thể linh hồn hoàn mỹ dung hợp, cưỡng ép hóa giải lời nói sẽ để cho độc thể bị trọng thương, nhẹ thì trọng thương, nặng thì tại chỗ tử vong."),t("br"),t("br"),n._v("Mà đem khí độc dẫn ra ngoài thân thể, mặc dù độ nguy hiểm giảm mạnh, nhưng cũng sẽ cho thân thể tạo thành tổn thương cực lớn."),t("br"),t("br"),n._v("Nói cách khác, hiện tại mặc kệ là giải độc vẫn là đem khí độc phóng thích, đối hôn mê Ngụy Như Yên tới nói, đều không phải là chuyện gì tốt."),t("br"),t("br"),n._v("“Nhìn tới chỉ có thể đi Độc điện!”"),t("br"),t("br"),n._v("Nghĩ một hồi, cân nhắc rất nhiều loại phương pháp, đều không nghĩ ra như thế nào điều trị, Trương Huyền xoa xoa mi tâm, có chút phát sầu."),t("br"),t("br"),n._v("Thủ đoạn của hắn mặc dù rất nhiều, đáng tiếc đối độc hiểu chỉ đạt tới nhị tinh cấp bậc, loại này bẩm sinh độc thể càng là lần đầu tiên nhìn thấy."),t("br"),t("br"),n._v("Thực sự tìm không ra càng vì phương pháp thích hợp, có lẽ chỉ có trừ độc điện đọc sách, sưu tập đủ nhiều tư liệu mới có thể nghĩ biện pháp cứu chữa."),t("br"),t("br"),n._v("“Vừa vặn vốn cũng ý định muốn đi Độc điện một chuyến, nhìn tới có thể muốn trước thời hạn lên đường rồi...”"),t("br"),t("br"),n._v("Chi tiền viện trưởng lăng, lão viện trưởng nhắn lại, nói qua liên quan tới Độc điện sự tình, hắn cũng ý định lúc rảnh rỗi đi qua dò xét một phen, tìm học viện nguy cơ đang tiềm ẩn, hiện tại Ngụy Như Yên xuất hiện loại tình huống này, khẳng định muốn trước thời hạn xuất phát."),t("br"),t("br"),n._v("“Tôn Cường, đây là một ngàn linh thạch thượng phẩm, mau chóng giúp ta hỏi thăm Độc điện tin tức, càng kỹ càng càng tốt!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra một đống linh thạch thượng phẩm, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Mặc dù lão viện trưởng nhắn lại nói, Độc điện vô cùng có khả năng tại Tĩnh Viễn thành lân cận, nhưng vẫn là muốn hỏi thăm một chút, tốt nhất biết chuẩn xác nhất tin tức."),t("br"),t("br"),n._v("Nếu không, đi không một chuyến, liền xem như hắn, đều không vẫy vùng nổi."),t("br"),t("br"),n._v("“Thiếu gia, không cần linh thạch, loại sự tình này, ta trực tiếp hỏi Thẩm gia là tốt rồi, bọn họ dược liệu buôn bán cũng làm, hẳn là có thể rất nhanh điều tra ra!”"),t("br"),t("br"),n._v("Tôn Cường lắc đầu cự tuyệt."),t("br"),t("br"),n._v("Thẩm gia là Hồng Viễn thành tứ đại gia tộc, buôn bán trải rộng toàn bộ đế quốc, trong đó làm được lớn nhất, liền là dược liệu, Độc điện muốn luyện độc, liền không khả năng rời khỏi được dược vật, có lẽ giữa hai bên, liền có liên hệ đặc thù nào đó."),t("br"),t("br"),n._v("Đổi lại những người khác, thậm chí trước kia lão viện trưởng, muốn còn muốn hỏi khả năng đều hỏi ý kiến hỏi không ra đến, cuối cùng, danh sư cùng độc sư không hợp nhau lắm, Thẩm gia cũng muốn chú ý đến buôn bán."),t("br"),t("br"),n._v("Mà bây giờ, bọn họ theo Trương Huyền nơi này nhận được chỗ tốt cực lớn, thật còn muốn hỏi, tất nhiên sẽ không dấu diếm."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi đi hỏi một chút!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Đối với đạo lí đối nhân xử thế cùng làm ăn, hắn khẳng định không bằng Tôn Cường, còn không bằng giao cho hắn toàn quyền làm."),t("br"),t("br"),n._v("“Vâng!” Tôn Cường lui ra ngoài."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Trương Huyền đang muốn lần nữa hỏi ý kiến hỏi một chút, Ngụy Như Yên hai ngày này biến hóa, liền nghe tới mặt đất một trận rung động, to tiếng chuông đột nhiên vang lên, truyền thấu Hồng Viễn thành mỗi một cái góc, tựa như đang tiến hành một loại nào đó triệu hoán."),t("br"),t("br"),n._v("“Là tụ hợp chuông?”"),t("br"),t("br"),n._v("Lông mày nhảy một cái, Trương Huyền nhịn không được đứng dậy."),t("br"),t("br"),n._v("Tụ hợp chuông là khẩn cấp hội tụ danh sư tiếng chuông, một khi gõ vang, nghe được thanh âm danh sư, đều phải nhanh một chút tiến đến."),t("br"),t("br"),n._v("Nhìn một cái, nghe được phương hướng âm thanh truyền tới, là danh sư học viện, Trương Huyền hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Hắn mới từ học viện đi ra, không đến nửa canh giờ, thanh âm liền vang lên, chẳng lẽ chuyện gì xảy ra?"),t("br"),t("br"),n._v("Có chút kỳ quái, cổ tay khẽ đảo, một cái đưa tin ngọc bài xuất hiện tại lòng bàn tay, Mi trưởng lão cho hắn truyền lại tin tức, phía trên chỉ có một hàng chữ dấu vết."),t("br"),t("br"),n._v("“Thanh Nguyên phong hào đế quốc, Thất Tinh thượng phẩm Ngô Như Phong Ngô sư, Mộc Hoành Mộc sư đi tới học viện, viện trưởng mau trở về!”"),t("br"),t("br"),n._v("“Mộc sư trở về?” Trương Huyền sửng sốt một chút."),t("br"),t("br"),n._v("Lúc trước tra ra Vưu Hư phản bội nhân tộc, liền đem hắn giao cho Mộc Hoành, hiện tại lần nữa trở về, chỉ sợ đã tra ra cái gì."),t("br"),t("br"),n._v("“Ta liền tới đây!”"),t("br"),t("br"),n._v("Hồi phục một câu, Trương Huyền lấy ra mấy cái cấp sáu đan dược, cho Ngụy Như Yên uống vào, để nàng tạm thời ngăn chặn thể nội độc khí, không đến mức tàn phá bừa bãi, làm bị thương những người khác, lúc này mới dặn dò Vương Dĩnh một tiếng, đi ra ngoài."),t("br"),t("br"),n._v("Không lâu sau, liền trở lại học viện, lúc này sở hữu học viên đều đã trở về, thậm chí Hồng Viễn thành Danh Sư đường chỗ có danh sư, cũng đều chạy tới, tất cả đều chồng chất tại diễn luyện tràng, không biết chuyện gì xảy ra."),t("br"),t("br"),n._v("PS: Đề cử quyển sách, Cô Đan Địa Phi đại thần 《 thần đạo đan tôn 》, hơn tám triệu chữ, một mực đứng hàng QQ đọc bán chạy đầu bảng, tuyệt đối chín mọng, có thể giết!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-978"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-980"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);