(window.webpackJsonp=window.webpackJsonp||[]).push([[2277],{2632:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-972-boi-phan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-972-boi-phan"}},[n._v("#")]),n._v(" Chương 972: Bối phận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 972: Bối phận"),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Sớm biết hôm nay, sao lúc trước còn như thế!"),t("br"),t("br"),n._v("Thời khắc sinh tử là có đại e ngại, nhưng mà đầu hàng Dị Linh tộc nhân, chẳng khác nào mất đi trong lòng đạo nghĩa."),t("br"),t("br"),n._v("Đạo nghĩa cùng sinh mệnh, được này liền sẽ mất kia, đã làm ra lựa chọn, liền phải thừa nhận thất bại sau này đánh đổi."),t("br"),t("br"),n._v("“Đáng tiếc Ngụy Như Yên hôn mê, không nhìn thấy hiện tại tình cảnh...”"),t("br"),t("br"),n._v("Ngụy Trường Phong bởi vì Vưu Hư mà chết, với tư cách nữ nhi, không thể tận mắt thấy cừu nhân chết tại trước mặt, cũng coi như hơi có tiếc nuối đi!"),t("br"),t("br"),n._v("Bất quá, chém giết trước mặt mọi người Vưu Hư, biểu hiện ra Danh Sư đường đối xử quyết phản đồ quyết tâm, cái này là tổng bộ làm ra quyết định, hắn vậy không có cách nào sửa đổi."),t("br"),t("br"),n._v("Lại nói Vưu Hư kéo dài hơi tàn lâu như vậy, cũng nên là thời điểm tiếp bị trừng phạt."),t("br"),t("br"),n._v("“Bốn vị viện trưởng, Liêu chiến sư, Trác Chiến sư, chư vị trưởng lão, hai người chúng ta hôm nay tới đây, là có chuyện quan trọng thương lượng, mời đi theo ta đi!”"),t("br"),t("br"),n._v("Xử trí xong Vưu Hư, Mộc sư nhìn về phía mọi người."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Lên tiếng, mọi người theo sát ở phía sau, hướng trưởng lão viện phương hướng đi đến."),t("br"),t("br"),n._v("Trương Huyền cũng không nhiều lời, chặt theo sau."),t("br"),t("br"),n._v("Tổng bộ phái tới hai vị thất tinh danh sư, trong đó một vị còn đạt đến Thánh vực tứ trọng, trước đó liền cảm thấy kỳ quái, nếu như chỉ là vì xử lý Vưu Hư một chuyện, căn bản không có cần thiết."),t("br"),t("br"),n._v("Khẳng định có sự tình khác phải xử lý."),t("br"),t("br"),n._v("Mộc sư tại học viện đợi qua một đoạn thời gian rất dài, xe nhẹ đường quen, rất mau tới đến trưởng lão viện đại thính nghị sự."),t("br"),t("br"),n._v("Mọi người vào chỗ, khởi động trận pháp, đem bốn phía toàn bộ ngăn cách, Mộc sư nhìn quanh một tuần: “Đem chư vị kêu đến là có chuyện quan trọng thương lượng, cũng là chúng ta thận trọng Vưu Hư thời điểm lấy được tin tức.”"),t("br"),t("br"),n._v("Nói xong cổ tay khẽ đảo, một cái lệnh bài xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Cái lệnh bài này cũ nát không chịu nổi, mài mòn chỗ khá nhiều, thoạt nhìn bất cứ lúc nào cũng sẽ vỡ vụn."),t("br"),t("br"),n._v("“Thứ này chắc hẳn chư vị đều biết đi!” Mộc sư nhìn quanh một tuần."),t("br"),t("br"),n._v("“Chẳng lẽ là viện trưởng lệnh?” Trương Huyền nhíu nhíu mày, mở miệng nói."),t("br"),t("br"),n._v("Cái lệnh bài này mặc dù thoạt nhìn cũ nát, nhưng từ bên trong có thể rõ ràng cảm nhận được vô số ý niệm gia trì, cùng trong tay hắn viên kia viện trưởng lệnh có chút tương tự."),t("br"),t("br"),n._v("Chỉ là thoạt nhìn mười phần đơn sơ thôi."),t("br"),t("br"),n._v("“Không sai, đây là viện trưởng lệnh, các ngươi Hồng Viễn học viện lão viện trưởng Chương Dẫn Khâu viện trưởng lệnh!”"),t("br"),t("br"),n._v("Mộc sư giải thích nói."),t("br"),t("br"),n._v("“Lão viện trưởng viện trưởng lệnh? Chẳng lẽ...”"),t("br"),t("br"),n._v("Nghe được giới thiệu, Mi trưởng lão sửng sốt một chút, lập tức kích động vẻ mặt đỏ lên: “Hắn... Không chết?”"),t("br"),t("br"),n._v("Triệu Bính Tuất người cũng khẩn trương nhìn lại."),t("br"),t("br"),n._v("Bọn họ mặc dù thừa nhận hiện tại Trương Huyền, nhưng là cùng lão viện trưởng cuối cùng thời gian chung đụng lâu, nghe được tin tức này, tất cả đều kích động đến khó mà tự kiềm chế."),t("br"),t("br"),n._v("“Hắn chết hay không, ta cũng không rõ ràng, có điều, thứ này là theo một chỗ di tích, bị người dùng đại lực lượng đưa ra tới!”"),t("br"),t("br"),n._v("Lông mày khóa chặt, Mộc sư nói."),t("br"),t("br"),n._v("“Bị người đưa ra đến, đến cùng chuyện gì xảy ra? Mộc sư có thể hay không nói rõ chi tiết một chút!”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Chuyện này cũng không phức tạp, hôm đó ta đem Vưu Hư mang về tổng bộ về sau, đi qua thẩm vấn, biết được lão viện trưởng muốn đi chỗ kia di tích vị trí cụ thể, ngay sau đó liền phái người tiến đến đi dò xét!”"),t("br"),t("br"),n._v("Mộc sư nhẹ gật đầu, lâm vào nhớ lại: “Di tích rườm rà phức tạp, dường như tồn tại một loại nào đó quy luật, nhất định phải tại riêng biệt thời cơ mới có thể đi vào, một khi bỏ lỡ, liền cần chờ đợi! Chúng ta người đang đang suy tính, lúc nào mới là tốt nhất tiến vào thời kì, cái này viên lệnh bài liền cảm ứng được danh sư khí tức, phá không bay tới!”"),t("br"),t("br"),n._v("Nghe nói như thế, một bên Mi trưởng lão kích động vẻ mặt đỏ lên: “Mộc sư nói là... Bắt đến Vưu Hư sau đi dò xét di tích, viện trưởng lệnh bay ra?”"),t("br"),t("br"),n._v("“Không sai!” Mộc sư nhẹ gật đầu."),t("br"),t("br"),n._v("“Cái kia...” Mi trưởng lão hô hấp dồn dập: “Có phải hay không đại biểu lão viện trưởng đem viện trưởng lệnh đưa đi ra?”"),t("br"),t("br"),n._v("Theo bắt lấy Vưu Hư đến bây giờ chẳng qua thời gian ba tháng, tại đây về sau, viện trưởng lệnh còn có thể lại xuất hiện, nói rõ lão viện trưởng khả năng không chết!"),t("br"),t("br"),n._v("Có thể là vây ở di tích một một chỗ, không cách nào trốn đi, cái này mới đem dùng đại lực lượng đưa ra đến, nói cho thế nhân."),t("br"),t("br"),n._v("“Trong di tích tình huống chúng ta ai cũng không biết, vậy có thể là hai năm trước, liền đem cái này viên viện trưởng lệnh phóng thích, nhưng mà, cảm ứng được danh sư khí tức mới hoàn toàn xuất hiện!”"),t("br"),t("br"),n._v("Không có hắn lạc quan như vậy, Mộc sư mở miệng nói."),t("br"),t("br"),n._v("“Cái này...” Mọi người trầm lặng."),t("br"),t("br"),n._v("Hắn nói vậy có đạo lý, viện trưởng lệnh là từ vô số ý niệm tổ hợp mà thành, bản thân liền nắm giữ đặc thù linh tính, nếu như là hai năm trước lão viện trưởng gặp được nguy hiểm phóng xuất ra, dặn dò chỉ có gặp được danh sư mới có thể xuất hiện... Danh Sư đường người đi qua mới phát giác, cũng không phải là không thể được."),t("br"),t("br"),n._v("Bởi vậy, chỉ bằng vào cái lệnh bài này xuất hiện, là không cách nào phán định lão viện trưởng, đến cùng chết vẫn là không chết."),t("br"),t("br"),n._v("“Mộc sư, cái này di tích ở đâu, chúng ta có thể hay không có thể đi qua nhìn một chút?”"),t("br"),t("br"),n._v("Chần chờ một chút, Mi trưởng lão nói."),t("br"),t("br"),n._v("Bất kể nói thế nào, lão viện trưởng đều là bọn họ ở chung được mấy trăm năm bằng hữu, hiện khi biết tin tức, làm sao đều muốn đi dò xét một phen, nếu không trong lòng làm sao có thể an?"),t("br"),t("br"),n._v("“Đem chư vị đi tìm đến, chính là vì chuyện này!”"),t("br"),t("br"),n._v("Mộc sư nhìn về phía mọi người: “Đi qua chúng ta dò xét, cái này di tích đại khái một tháng sau sẽ trùng mới mở ra, cho nên muốn mời mời mọi người cùng nhau đi qua! Vừa đến, có thể đi tìm kiếm lão viện trưởng bọn họ, nếu như không có chết, trước đi cứu người. Thứ hai, gần nhất Dị Linh tộc nhân hoạt động nhiều lần, ta hoài nghi cùng chỗ này di tích có quan hệ rất lớn!”"),t("br"),t("br"),n._v("“Tốt, chúng ta đi qua!”"),t("br"),n._v("Nghe lời này, Mi trưởng lão ánh mắt sáng lên, không chút do dự, liền vội vàng gật đầu, nói xong lúc này mới phát hiện có chút vượt quá chức phận, làm thay việc của người khác, vội vàng quay đầu nhìn về phía cách đó không xa Trương Huyền: “Viện trưởng...”"),t("br"),t("br"),n._v("“Mi trưởng lão nói rất đúng, khẳng định muốn đi, đây là chúng ta Hồng Viễn danh sư học viện thái độ!” Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Tại viện trưởng lăng phía trong, liền biết lão viện trưởng muốn đi di tích, lúc trước liền định đi qua nhìn một chút, lại đã trải qua Địa quật sự kiện, Dị Linh tộc nhân cử động dị thường..."),t("br"),t("br"),n._v("Vô luận như thế nào, không đi qua nhìn một chút, tại tâm bất an."),t("br"),t("br"),n._v("“Ừm! Chư vị đâu?”"),t("br"),t("br"),n._v("Dường như sớm biết kết quả này, Mộc sư cũng không cố ý bên ngoài, nhẹ gật đầu nhìn về phía cách đó không xa cái khác ba vị viện trưởng."),t("br"),t("br"),n._v("“Đã dính dáng Dị Linh tộc nhân, chúng ta vậy không thể đổ trách nhiệm cho người khác!”"),t("br"),t("br"),n._v("Ô Thiên Khung, Thẩm Bình Triều mấy người cũng đồng thời gật đầu."),t("br"),t("br"),n._v("“Chúng ta Chiến sư đường như thế nào rớt lại phía sau!” Liêu Tùng cũng cười cười."),t("br"),t("br"),n._v("“Đã tất cả mọi người đồng ý, ta liền không nói thêm lời, một tháng sau, chúng ta liền đi chỗ đó di tích, đương nhiên, xấu nói được đằng trước, cái này di tích mười phần nguy hiểm, bằng không thì cũng không có khả năng để Chương Dẫn Khâu đám người, toàn quân bị diệt! Liền coi như chúng ta tổng bộ, đều không có quá nhiều tin tức... Một khi đi qua, vô cùng có khả năng cũng không còn cách nào trở về!”"),t("br"),t("br"),n._v("Mộc sư nhìn qua."),t("br"),t("br"),n._v("“Điểm ấy chúng ta rõ ràng!” Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi, quyết định như vậy đi...”"),t("br"),t("br"),n._v("Thấy mọi người không chút do dự, Mộc sư lên tiếng, đang muốn làm ra quyết định, liền nghe đến một bên Ngô Như Phong Ngô sư lông mày giương lên, nhìn lại: “Lần này đi di tích, để bảo đảm an toàn, thực lực thấp nhất đều muốn đạt tới Thánh vực mới có tư cách! Vị này Trương viện trưởng, chỉ có Tòng Thánh cấp bậc, ta nhìn vẫn là chớ đi, bằng không thì cũng là kéo mọi người chân sau!”"),t("br"),t("br"),n._v("“Ta?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn mặc dù là Tòng Thánh, nhưng thực lực chân chính thi triển đi ra, Thánh vực nhất trọng đỉnh phong đều có thể nhẹ nhõm đánh chết, hơn nữa, mấu chốt nhất đúng thế... Hắn thủ đoạn bảo mệnh phần đông, có thể nói, trong phòng tất cả mọi người cùng một chỗ vây công, chết nhưng khẳng định vậy là đối phương!"),t("br"),t("br"),n._v("Cái tên này cái gì cũng không biết, liền nói mình cản trở... Đường đường thất tinh thượng phẩm danh sư, có phần quá võ đoán đi!"),t("br"),t("br"),n._v("“Sư thúc, Ngô sư... Có chút bảo thủ, mong được tha thứ...”"),t("br"),t("br"),n._v("Thấy sư thúc mất hứng, Mộc sư vội vàng truyền âm tới."),t("br"),t("br"),n._v("“Bảo thủ?”"),t("br"),t("br"),n._v("“Vâng, mặc dù Vưu Hư làm phản nhân loại, cùng sư thúc không quan hệ, nhưng... Thẩm tra lúc đi ra, ngươi là viện trưởng, nói cách khác... Có chút quản lý không nghiêm! Lại thêm, tuổi tác cùng thực lực đều cùng viện trưởng có chút chênh lệch, cho nên, hắn đối sư thúc có chút ý kiến...”"),t("br"),t("br"),n._v("Mộc sư vội vàng nói."),t("br"),t("br"),n._v("Nghe được đối phương giải thích, Trương Huyền cười khổ một tiếng, bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Nhìn tới cái tên này, tại cực cao vị trí đợi được thời gian lâu dài, đã thoát ly nhân loại bình thường sinh hoạt."),t("br"),t("br"),n._v("Thuộc hạ phạm sai lầm, liền muốn liên luỵ... Bản thân tới học viện, tính toán đâu ra đấy cũng liền bốn cái tháng sau, mới nhìn thấy Vưu Hư, liền đem hắn bắt đi ra, không gia thưởng ngược lại cũng thôi, còn đem tội danh cũng trách trên người mình..."),t("br"),t("br"),n._v("Thật sự là điển hình lãnh đạo tác phong —— ra chuyện sai, bắt một cái dê thế tội đội bao, thật biểu hiện quyền uy của hắn cùng nhìn rõ mọi việc..."),t("br"),t("br"),n._v("Mà bản thân, chính là bị bắt cái kia dê!"),t("br"),t("br"),n._v("Đổi lại người ta, thất tinh thượng phẩm danh sư chính miệng chỉ trích, khẳng định không cách nào phản bác, nhưng Trương Huyền làm sao có thể ăn dạng này lỗ, giương mắt nhìn lại: “Ngô Như Phong đúng không?”"),t("br"),t("br"),n._v("“Láo xược! Cấp thấp danh sư, nhìn thấy cấp cao danh sư, gọi thẳng tên, còn thể thống gì?”"),t("br"),t("br"),n._v("Không nghĩ tới hắn sẽ lái miệng, Ngô sư sầm mặt lại."),t("br"),t("br"),n._v("“Còn thể thống gì?” Trương Huyền cười cười, hai tay chắp sau lưng, vẻ mặt lạnh nhạt nhìn qua: “Xin hỏi Ngô sư, ngươi cùng Mộc sư ở giữa xưng hô như thế nào?”"),t("br"),t("br"),n._v("“Mộc sư cùng ta cùng là thất tinh danh sư, tự nhiên ngang hàng!”"),t("br"),t("br"),n._v("Híp mắt lại, Ngô sư mặc dù mang theo lửa giận, vẫn là hừ một tiếng."),t("br"),t("br"),n._v("Mọi người cùng là Thanh Nguyên đế quốc Danh Sư đường danh sư, hơn nữa cùng là thất tinh, tự nhiên là ngang hàng quan hệ."),t("br"),t("br"),n._v("“Ngang hàng? Cái kia tốt...” Trương Huyền quay đầu nhìn về phía cách đó không xa Mộc sư: “Tiểu Hoành tử, ngươi đối ta xưng hô như thế nào?”"),t("br"),t("br"),n._v("“Sư thúc!”"),t("br"),t("br"),n._v("Mộc sư thành thật trả lời."),t("br"),t("br"),n._v("“Ngươi nghe được, ngươi cùng Mộc sư ngang hàng, mà hắn xưng hô sư thúc ta... Nói cách khác, ngươi so ta thấp một cái bối phận, ta trực tiếp bảo ngươi Ngô Như Phong, có gì sai trái? Chẳng lẽ... Ngươi cảm thấy xưng hô tên đầy đủ, không đủ thân mật?”"),t("br"),t("br"),n._v("Trương Huyền trầm tư một chút, nói: “Như vậy đi, ta gọi ngươi, tiểu Phong tử...”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghe được “Tiểu Phong tử” ba chữ, Ngô sư suýt chút nữa không có hộc máu ngất đi."),t("br"),t("br"),n._v("Vội vàng quay đầu nhìn về phía Mộc sư, muốn nhìn một chút hắn... Nói có đúng không là lời nói thật."),t("br"),t("br"),n._v("Vì sao phải xưng hô đối phương là sư thúc."),t("br"),t("br"),n._v("“Trương viện trưởng lão sư, ta muốn xưng hô một tiếng sư tổ, dựa theo bối phận, xưng hô hắn sư thúc, cũng không có sai...” Biết hắn muốn hỏi chút gì, Mộc sư vội vàng nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngô sư sắc mặt xanh lét."),t("br"),t("br"),n._v("Danh sư đăng ký trọng lễ nghi, coi trọng bối phận, cái này Trương viện trưởng mặc dù chẳng ra sao cả, nhưng lão sư bối phận cao lời nói, coi như bản thân cấp bậc thấp, vậy nắm giữ cực cao địa vị."),t("br"),t("br"),n._v("Thật giống như Khổng sư học sinh, dù là cái gì cũng không biết, cửu tinh danh sư gặp gỡ cũng muốn xưng hô một tiếng “Tiền bối”!"),t("br"),t("br"),n._v("Đây là quy củ."),t("br"),t("br"),n._v("Vốn định cho đối phương một hạ mã uy, không nghĩ tới trong nháy mắt, biến thành sư điệt, suy nghĩ một chút đều cảm thấy buồn bực."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-980"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-982"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);