(window.webpackJsonp=window.webpackJsonp||[]).push([[2279],{2633:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-974-ngo-su-xau-ho"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-974-ngo-su-xau-ho"}},[n._v("#")]),n._v(" Chương 974: Ngô sư xấu hổ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 974: Ngô sư xấu hổ"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Ngô sư lông mày nhảy một cái."),t("br"),t("br"),n._v("Tòng Thánh cấp bậc tay không đánh bại Thánh vực nhị trọng Chiến sư... Cái này có chút quá kinh khủng đi."),t("br"),t("br"),n._v("Mấu chốt nhất là hắn mới vừa nói xong người ta không có thực lực, hiện tại ngay tại chỗ đánh bại Trác Chiến sư, quả thực chính là trần trụi mà làm mất mặt."),t("br"),t("br"),n._v("“Căn cứ biết đến tin tức, cái này di tích bên trong có rất nhiều Dị Linh tộc cường giả, một khi bị bắt lại, liền Vưu Hư vị này lục tinh đỉnh phong danh sư đều không thể chống lại! Ngươi có cái gì tự tin, cảm thấy mình có thể chống cự?”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Ngô sư lạnh lùng nhìn qua, mang theo giọng chất vấn khí, liên tiếp: “Ngươi gặp qua Dị Linh tộc ư? Cùng bọn hắn đã từng quen biết ư? Biết sự đáng sợ của bọn họ ư?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trương Huyền đang muốn trả lời, chỉ thấy đối diện thất tinh danh sư lần nữa khoát tay áo."),t("br"),t("br"),n._v("“Nói thật cho ngươi biết, ta cùng bọn hắn chiến đấu qua, từng tự tay chém giết 52 đầu, bọn họ không chỉ thực lực mạnh, có thể vượt cấp chiến đấu, càng quan trọng hơn là trong cơ thể toả ra sát lục chi khí, làm cho không người nào có thể ngăn cản!”"),t("br"),t("br"),n._v("“Gặp được loại người này, cũng không phải là ngươi sức chiến đấu mạnh liền có thể chống cự, càng quan trọng hơn là tâm cảnh cùng ý chí, nếu như không có đủ loại năng lực này, vẫn là không đi cho thỏa đáng.”"),t("br"),t("br"),n._v("Hắn không phải cố ý nhằm vào Trương Huyền, mà là lần này thực sự quá mức nguy hiểm, không thể xuất hiện bất luận cái gì một tia sai lầm, nếu không làm không cẩn thận đồng dạng sẽ toàn quân bị diệt."),t("br"),t("br"),n._v("Đối phương coi như sức chiến đấu rất mạnh, có một cái rất lợi hại lão sư, cũng chỉ là cái 20 tới tuổi người trẻ tuổi, loại thiên tài này hắn thấy cũng nhiều, gặp được đáng sợ Dị Linh tộc nhân, rất dễ dàng bị công phá tâm lý bình chướng, xuất hiện không thể biết được biến cố."),t("br"),t("br"),n._v("“Đối với Dị Linh tộc nhân...” Biết đối phương chủ ý cũng không xấu, chỉ là lãnh đạo làm lâu, có chút chủ quan ước đoán, Trương Huyền lắc đầu, đang muốn giải thích, chỉ thấy một trưởng lão vội vã từ bên ngoài đi vào."),t("br"),t("br"),n._v("“Viện trưởng...” Đi tới trước mặt vội vàng ôm quyền."),t("br"),t("br"),n._v("Thấy sắc mặt hắn không đúng, Trương Huyền cau mày: “Đã xảy ra chuyện gì?”"),t("br"),t("br"),n._v("Vị trưởng lão này xoắn xuýt một chút, mở miệng nói: “Bẩm báo viện trưởng, Lục Phong viện... Lục sư cầu kiến!”"),t("br"),t("br"),n._v("Lục Phong cùng vị này Trương viện trưởng gây ra mâu thuẫn, suýt chút nữa bị khai trừ danh sư tư chất, mặc dù chỉ ở trong phạm vi nhỏ phổ biến, nhưng mà hắn cũng là biết đến... Từ đó có thể biết, hai người quan hệ khẳng định không hợp nhau lắm, đây là chạy tới tìm viện trưởng, để hắn cảm thấy tràn đầy xấu hổ, không biết nên như thế nào bẩm báo."),t("br"),t("br"),n._v("“Lục Phong?”"),t("br"),t("br"),n._v("Không biết ý nghĩ của hắn, Trương Huyền sửng sốt một chút, lập tức bật cười."),t("br"),t("br"),n._v("Rời khỏi phong ấn, chuyên môn đi đi tìm đám người này, cũng không tìm được, hiện tại xem ra, bọn họ thật theo Địa quật đi ra."),t("br"),t("br"),n._v("“Cho mời!” Khoát tay áo."),t("br"),t("br"),n._v("“Được...” Thấy viện trưởng cũng không bởi vậy trách cứ, vị trưởng lão này thở phào nhẹ nhõm, chuyển thân vội vã đi ra ngoài."),t("br"),t("br"),n._v("Thời gian không dài, một cái lão giả liền theo ở phía sau đi đến, không phải Lục Phong lại sẽ có là ai."),t("br"),t("br"),n._v("Lúc này Lục Phong lại không còn trước đó phong mang khí thế, hai mắt mộc mạc, thoạt nhìn cho người ta một loại càng thêm bình tĩnh ổn trọng cảm giác."),t("br"),t("br"),n._v("Nhìn tới đi qua sự tình lần trước về sau, toàn bộ tâm thái của người ta đều có biến hóa cực lớn."),t("br"),t("br"),n._v("“Viện trưởng, ngươi quả nhiên không có việc gì? Thật sự là quá tốt...”"),t("br"),t("br"),n._v("Nhìn thấy thanh niên yên lặng ngồi trong đại sảnh ở giữa, Lục Phong thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Mặc dù đoán ra hắn không có việc gì, đã rời khỏi Địa quật, tận mắt thấy, vẫn cảm thấy đáy lòng buông lỏng."),t("br"),t("br"),n._v("Nếu như không phải vị này, bọn họ khẳng định đã chết, đối phương bất chấp nguy hiểm cứu, đồng thời đem nhiều như vậy Dị Linh tộc nhân chém giết, ân tình bao lớn, như là tái tạo."),t("br"),t("br"),n._v("“Đương nhiên không có việc gì, ngược lại là các ngươi, thương thế đều khỏe?” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Thương thế của chúng ta, đã khỏi hẳn, là viện trưởng dũng cảm quên mình, xuất thủ cứu giúp mới để chúng ta còn sống, không đến mức ủ thành sai lầm lớn, xin nhận Lục Phong cúi đầu...”"),t("br"),t("br"),n._v("Nói xong, liền muốn quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Lục sư khách khí...” Thân thể nhoáng một cái, Trương Huyền đi tới trước mặt, đem hắn đỡ lên."),t("br"),t("br"),n._v("Nhìn thấy nguyên bản còn không hợp nhau hai người, khách khí như thế, Mi trưởng lão cùng Triệu trưởng lão đám người liếc mắt nhìn nhau, một mặt kỳ quái."),t("br"),t("br"),n._v("“Đây là có chuyện gì?”"),t("br"),t("br"),n._v("Một bên Tưởng Thanh Cầm viện trưởng, thực sự nhịn không được hỏi lên."),t("br"),t("br"),n._v("“A, là như vậy!”"),t("br"),t("br"),n._v("Nghe được hỏi thăm, Lục Phong nhẹ gật đầu: “Năm ngày phía trước, Trương viện trưởng tự xuống Địa quật, đã cứu chúng ta 40 vị lục tinh danh sư, đồng thời tự mình chém giết hơn hai trăm đầu Thánh vực cấp bậc Dị Linh tộc nhân cùng hai vị Thánh vực tứ trọng hai vị vương giả... Ta đã đem chuyện đã xảy ra hướng tổng bộ kỹ càng báo cáo một lần, vừa mới nhận được trả lời chắc chắn, vốn chỉ là nghĩ, không thể để cho Trương viện trưởng công lao như vậy mai một, không nghĩ tới viện trưởng đã theo Địa quật đi ra...”"),t("br"),t("br"),n._v("“Tự xuống Địa quật?”"),t("br"),t("br"),n._v("“Cứu các ngươi hơn 40 vị danh sư?”"),t("br"),t("br"),n._v("“Chém giết hơn hai trăm đầu Thánh vực cấp bậc Dị Linh tộc nhân, còn có vương giả?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắn, chung quanh một mảnh yên lặng, mới vừa rồi còn khí thế hung hăng Ngô sư, càng là mở to hai mắt nhìn, suýt chút nữa không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Mới vừa nói xong đối phương chưa thấy qua Dị Linh tộc nhân, vị này Lục Phong liền chạy tới nói hắn giết hơn hai trăm đầu... Thật hay giả?"),t("br"),t("br"),n._v("Liền xem như hắn, đường đường Thánh vực tứ trọng cường giả, cũng chưa từng giết nhiều như vậy nha!"),t("br"),t("br"),n._v("Mấu chốt nhất còn có hai vị vương giả!"),t("br"),n._v(" Dị Linh tộc vương giả đáng sợ, không cần nghĩ cũng biết, liền xem như hắn gặp gỡ, đều chỉ có một con đường chết, nghe Lục Phong hơi thở, vị này Trương viện trưởng không chỉ không có việc gì, còn một hơi giết hai phía..."),t("br"),t("br"),n._v("Để hắn cảm thấy hô hấp có chút ngạt thở, phản ứng không kịp."),t("br"),t("br"),n._v("“Ngươi nói đều là thật?” Thực sự nhịn không được nhìn sang."),t("br"),t("br"),n._v("“Đương nhiên, ta có thể lấy tính mạng của ta đảm bảo!” Lục Phong gật đầu: “Hơn nữa chúng ta đã đem tình huống cặn kẽ truyền cho tổng bộ, tổng bộ đã thừa nhận, đồng thời hối đoái thành công lao, lấy đó ban thưởng... Hôm nay ta tới chính là đem những này công lao, đưa cho viện trưởng.”"),t("br"),t("br"),n._v("Nói xong, Lục Phong cổ tay khẽ đảo, lấy ra một cái, màu vàng nhạt ngọc bài, đưa tới."),t("br"),t("br"),n._v("“Viện trưởng, ngươi tiêu tốn tâm huyết tru sát nhiều như vậy Dị Linh tộc nhân, cứu chúng ta, chúng ta cũng không thể để công lao của ngươi nhấn chìm... Tổng bộ căn cứ tình huống lúc đó đưa cho đánh giá, tổng cộng là 1211 điểm.”"),t("br"),t("br"),n._v("“Hơn một ngàn... Điểm?”"),t("br"),t("br"),n._v("Ngô sư thân thể nhoáng một cái, suýt chút nữa hộc máu."),t("br"),t("br"),n._v("Theo mười sáu tuổi, chém giết con thứ nhất Dị Linh tộc nhân bắt đầu, đến bây giờ đạt tới Thất Tinh thượng phẩm danh sư... Mấy trăm năm tất cả công lao tính gộp lại cũng chỉ có hai mươi hai điểm..."),t("br"),t("br"),n._v("Đã đủ để tự ngạo, để vô số người sợ hãi than."),t("br"),t("br"),n._v("Mới vừa chế giễu xong vị này Trương viện trưởng, không có có công lao, kết quả người ta liền vung ra hơn một ngàn điểm... Có muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("Coi như thật chém giết hơn hai trăm đầu Thánh vực cấp bậc Dị Linh tộc nhân, cùng hai phía vương giả cũng không có có nhiều như vậy đi!"),t("br"),t("br"),n._v("Không chỉ hắn nghĩ như vậy những người khác cũng đều có như thế nghi vấn."),t("br"),t("br"),n._v("Dường như nhìn ra bọn hắn ý nghĩ, Lục Phong nói tiếp: “Những này Dị Linh tộc nhân xuyên thấu qua Địa quật phong ấn mà đến, mang theo đặc thù nhiệm vụ, rõ ràng mưu đồ quấy rối, một khi để trốn đi đi ra, toàn bộ Hồng Viễn đế quốc đều khả năng tiếp nhận đến hủy diệt tính tiến công, Trương viện trưởng giết bọn hắn, tương đương với ngăn trở âm mưu, công lao bao lớn, không thể đo lường, hơn một ngàn điểm đều là ít...”"),t("br"),t("br"),n._v("“Cái này...” Mọi người giờ mới hiểu được tới."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Giết mấy cái Dị Linh tộc nhân công lao khả năng không lớn, nhưng mà nếu như ngăn trở âm mưu của bọn hắn, cứu vớt vô số sinh mệnh, mới là công lao lớn."),t("br"),t("br"),n._v("“Còn có một việc, hiện tại tổng bộ không có xác nhận, có điều, đã phái người xuống tới xác minh, một khi xác nhận khả năng liền không chỉ là hơn một ngàn điểm công lao, mà có thể là mấy vạn điểm, mười mấy vạn điểm...”"),t("br"),t("br"),n._v("Không để ý tới mọi người khiếp sợ, Lục Phong nói tiếp."),t("br"),t("br"),n._v("“Mấy vạn điểm, mười mấy vạn điểm, đến cùng là cái gì đại công lao?”"),t("br"),t("br"),n._v("Một bên Mộc sư, vội vàng hỏi."),t("br"),t("br"),n._v("Chém giết một đầu Thánh vực cấp bậc Dị Linh tộc nhân cũng không chiếm được một điểm, như vậy có thể thấy được công lao độ khó."),t("br"),t("br"),n._v("Thoáng cái đến hơn một ngàn điểm, đã rất làm cho người khác khiếp sợ, thế mà còn có mấy vạn điểm, mười mấy vạn điểm không được đến xác minh, đến cùng là cái gì?"),t("br"),t("br"),n._v("Không chỉ hắn như vậy nghi ngờ, Ngô sư, Mi trưởng lão người cũng đều mặt mũi mê man, cảm giác được bất luận thế nào nghĩ mãi mà không rõ."),t("br"),t("br"),n._v("“Là như vậy, ta vừa mới lại xuống một chuyến Địa quật, phát hiện ngăn cản vực ngoại chiến trường phong ấn, đã bị triệt để chữa trị... Đây cũng là viện trưởng làm a?”"),t("br"),t("br"),n._v("Lục Phong ngẩng đầu nhìn tới."),t("br"),t("br"),n._v("“Phong ấn không chữa trị, Dị Linh tộc nhân còn có thể xâm lấn, ta liền hao tốn mấy ngày thời gian, dựa theo lão sư đã từng truyền thụ cho phương pháp, tu bổ một chút.”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("“Quả nhiên...” Lục Phong ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn ở trong hang ngây người hơn ba tháng, biết phong ấn mài mòn mức độ, vốn cho rằng lần này Dị Linh tộc nhân tới cũng không phải là trường hợp đặc biệt, về sau khẳng định còn có thể liên tục không ngừng, ai biết lại đi nhìn thời điểm, phong ấn đã hoàn hảo không chút tổn hại."),t("br"),t("br"),n._v("Bọn họ tất cả mọi người cùng một chỗ, Dị Linh tộc nhân chắc chắn sẽ không tự chui đầu vào rọ, khả năng duy nhất chính là sáng tạo ra tất cả kỳ tích vị này Trương viện trưởng."),t("br"),t("br"),n._v("Người ta muốn nói có thể chữa trị, hắn là không tin, nhưng mà vị này Trương viện trưởng sáng tạo ra quá nhiều kỳ tích, không thể không tin."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới ngôn ngữ xác minh, quả nhiên nhận được đối phương khẳng định."),t("br"),t("br"),n._v("Tu bổ phong ấn, miễn trừ Dị Linh tộc nhân tiến công, tương đương với một lần vất vả suốt đời nhàn nhã, công lao to lớn, không thể đo lường, đừng nói mấy vạn điểm, mười mấy vạn điểm đều là ít."),t("br"),t("br"),n._v("Còn như thế nào đánh giá, chỉ có thể tổng bộ người tới mảnh quan sát kỹ, mới có thể đưa ra rõ ràng đáp án."),t("br"),t("br"),n._v("“Cái này phong ấn không phải dính dáng không gian pháp tắc ư? Cũng có thể tu bổ?”"),t("br"),t("br"),n._v("“Nếu như là thật, đây thật là tạo phúc nhân loại công lao lớn...”"),t("br"),t("br"),n._v("“Đến cùng làm sao làm được?”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, mọi người chung quanh tất cả đều sững sờ."),t("br"),t("br"),n._v("Vừa mới còn cảm thấy tự cho mình siêu phàm Ngô sư, càng là như là bị sét đánh đồng dạng."),t("br"),t("br"),n._v("“Đúng rồi, viện trưởng, ta trước đó đi phong ấn thời điểm còn phát hiện có chút tổn hại, lần này đi qua cũng đã hoàn hảo... Nếu như không nhìn lầm, ngươi hẳn là mới từ Địa quật ra đi?”"),t("br"),t("br"),n._v("Nhớ tới một việc, Lục Phong nhịn không được nói."),t("br"),t("br"),n._v("Vì tìm kiếm Trương Huyền, lúc trước hắn cũng tới đến phong ấn trước mặt qua, biết tình huống, lần này đi qua cũng đã hoàn hảo, nói rõ mới vừa chữa trị không lâu."),t("br"),t("br"),n._v("“Ừm, mới vừa đi ra không đến nửa canh giờ!” Trương Huyền cười khổ một tiếng: “Ngươi nhìn, liền y phục cũng không kịp đổi...”"),t("br"),t("br"),n._v("“Mới vừa cứu vớt toàn bộ Hồng Viễn đế quốc, chém giết mấy trăm đầu Dị Linh tộc nhân, trải qua cửu tử nhất sinh, theo Địa quật chỗ nguy hiểm như vậy đi ra, nghe được tụ hợp chuông vang lên, liền nghỉ ngơi đều không có nghỉ ngơi, quần áo cũng không kịp đổi, liền đến... Ta thế mà bởi vì hắn đến trễ mà trách tội? Cảm thấy hắn kiêu ngạo lớn nha?”"),t("br"),t("br"),n._v("Nghe đến nơi này, Ngô sư mặt một chút biến đến đỏ bừng, hận không thể có một nơi lập tức chui vào."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-982"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-984"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);