(window.webpackJsonp=window.webpackJsonp||[]).push([[2283],{2638:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-978-tinh-vien-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-978-tinh-vien-thanh"}},[n._v("#")]),n._v(" Chương 978: Tĩnh Viễn thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 978: Tĩnh Viễn thành"),t("br"),t("br"),n._v("Phía trên chỉ có một hàng chữ, viết mười phần đơn giản."),t("br"),t("br"),n._v("Ý tứ chính là nói di tích gặp lại, Chiến sư đường còn có việc, xin cáo từ trước, còn lý do, cũng không nói rõ."),t("br"),t("br"),n._v("Nhìn xong thư để lại, Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Hôm qua không còn rất tốt, muốn cùng Huyền Huyền hội luận bàn, nói đến lời thề son sắt, chung tiến bộ, làm sao một cái chớp mắt liền đi?"),t("br"),t("br"),n._v("Làm sao cùng hắn chung đụng, mặc kệ là trưởng lão vẫn là danh sư, đều như thế không đáng tin cậy?"),t("br"),t("br"),n._v("Ở tại Hồng Viễn học viện, cùng chính mình cái này viện trưởng, ngay cả chào hỏi cũng không nói một tiếng liền đi, hình như có cái gì thúc giục đồng dạng, quả thực quá vô lễ."),t("br"),t("br"),n._v("Bản thân như vậy một cái tuân thủ quy củ người, kết quả bên cạnh một đống không đáng tin cậy bằng hữu, tâm thật mệt mỏi..."),t("br"),t("br"),n._v("“Chiến sư đường không có xảy ra chuyện gì chứ?”"),t("br"),t("br"),n._v("Lắc đầu, ngẩng đầu hỏi."),t("br"),t("br"),n._v("“Có lẽ không có việc gì đi! Ta chuyên môn hỏi thăm, đối phương nói là việc tư, còn nguyên do, làm sao đều không nói... A, đúng, viện trưởng, đêm qua ta trở về thời điểm, nhìn thấy bọn họ hình như đều bị thương, có thể hay không cùng cái này liên quan đến?”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Mi trưởng lão nói."),t("br"),t("br"),n._v("Hôm qua xử lý xong chư nhiều chuyện, quay về chỗ ở thời điểm, nhìn thấy Liêu chiến sư đám người sưng mặt sưng mũi từ bên ngoài đi về tới, vì để tránh cho xấu hổ, không có đi hỏi nhiều, bây giờ nghĩ lại, có phải hay không là nguyên nhân này."),t("br"),t("br"),n._v("“Bị thương?” Trương Huyền gãi đầu một cái."),t("br"),t("br"),n._v("Không phải để bọn hắn chỉ điểm Huyền Huyền hội mọi người ư? Làm sao lại bị thương?"),t("br"),t("br"),n._v("“Quên đi, đừng để ý tới bọn hắn, một tháng sau gặp, lại hỏi thăm cũng không muộn!”"),t("br"),t("br"),n._v("Người đều đã đi, tiếp tục xoắn xuýt cũng không có ý nghĩa gì, lại nói hắn cũng không có có nhiều thời gian như vậy cùng tinh lực."),t("br"),t("br"),n._v("Lại hỏi thăm vài câu, đem học viện công việc đối Mi trưởng lão an bài một chút, trở lại phủ đệ, khai báo Vương Dĩnh đám người nghiêm túc tu luyện, lúc này mới đem Ngụy Như Yên bỏ vào tổ ong, ngồi lên Tử Dương thú sống lưng, trực tiếp hướng về phía trước Tĩnh Viễn thành phương hướng bay đi."),t("br"),t("br"),n._v("Lần này đi ra ngoài, là vì tìm kiếm Độc điện, nguy hiểm vô cùng, rất nhiều đệ tử đều không mang theo, cũng không có nói cho những người khác hành tung, lẻ loi một mình, không có người cản lời nói, hành động cũng thuận tiện rất nhiều."),t("br"),t("br"),n._v("Tĩnh Viễn thành tại Hồng Viễn đế quốc rất đông bắc phương hướng, tới gần đế quốc lớn nhất đất liền biển. Bởi vì địa lý vắng vẻ, quanh năm tuyết đọng lạnh lẽo, so với Hỏa Nguyên thành chỗ như vậy, có vẻ không đủ phát triển."),t("br"),t("br"),n._v("Chính là bởi vì lui tới thương khách tương đối ít, tình huống nơi này rất nhiều người đều không rõ lắm, danh sư học viện phần đông trong tư liệu cũng không có quá nhiều ghi chép."),t("br"),t("br"),n._v("Chỉ nói là một cái lạnh lẽo thị trấn nhỏ nơi biên giới, dựa theo Độc điện trốn thế nhân đặc tính, đem phân bộ xây dựng ở đây, ngược lại là thật có khả năng."),t("br"),t("br"),n._v("Đương nhiên, tình huống cụ thể, chỉ có đến lúc đó mới có thể xác định."),t("br"),t("br"),n._v("Lão viện trưởng mặc dù đã điều tra rất nhiều, nhưng cũng không dám trăm phần trăm cam đoan."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, một tháng thời gian thoạt nhìn rất dài, nếu như tìm không thấy Độc điện lời nói, cũng chưa chắc đủ."),t("br"),t("br"),n._v("“Ta trở thành viện trưởng, danh khí dần dần tăng lớn, lấy diện mục thật sự gặp người lời nói, rất dễ dàng bị phát giác...”"),t("br"),t("br"),n._v("Ngồi tại Tử Dương lưng thú bên trên, dựa theo địa đồ phương hướng tiến lên, Trương Huyền cúi đầu trầm tư."),t("br"),t("br"),n._v("Hắn hiện tại, đã không phải là lấy trước kia cái một tên không văn tiểu nhân vật, mà là Thanh Nguyên đế quốc cấp dưới bốn đại học viện một trong, Hồng Viễn học viện viện trưởng, địa vị có thể so với thất tinh danh sư."),t("br"),t("br"),n._v("Độc điện cùng Danh Sư đường từ trước tới nay không hợp nhau, nếu như cứ như vậy chạy đến Tĩnh Viễn thành hỏi thăm, một khi bị người nhận ra thân phận, cái gì hỏi thăm không ra là chuyện nhỏ, làm không cẩn thận còn có thể để cho mình rơi vào nguy cơ."),t("br"),t("br"),n._v("Nhất định phải nghĩ phương pháp ngụy trang một chút."),t("br"),t("br"),n._v("Suy tư một chút, toàn thân khung xương cơ bắp nhúc nhích, dung mạo phát sinh thay đổi, hình thể so trước đó càng cao hơn lớn, tuổi tác cũng theo hai mươi tuổi biến thành chừng bốn mươi tuổi."),t("br"),t("br"),n._v("Thiên Đạo vận chuyển chân khí, khí tức trên thân cũng bắt đầu biến hóa, cùng trước kia hoàn toàn khác biệt, có chút cuồng bạo cùng hung mãnh, không còn trước đó khiêm tốn cùng nội liễm."),t("br"),t("br"),n._v("Nếu như đi đi ra bên ngoài, không nói mình là Trương Huyền, coi như Vương Dĩnh đám người khẳng định đều nhận không ra."),t("br"),t("br"),n._v("Thiên Đạo chân khí thay đổi khí tức, Ngũ Diệu kim thân thay đổi dung mạo, liền Dị Linh tộc nhân đều có thể ngụy trang, chớ nói chi là người bình thường."),t("br"),t("br"),n._v("Tử Dương thú toàn lực phi hành, mười ngày sau, một cái tuyết đọng bao phủ xuống thành thị xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Từ xa nhìn lại, so với Hồng Viễn thành nhỏ rất nhiều, nhưng là cùng Huyễn Vũ đế đô này địa phương so, vẫn là càng càng bao la."),t("br"),t("br"),n._v("Màu xám tro kiến trúc, đứng sừng sững ở trắng tinh trên mặt tuyết, cùng thiên địa nối liền, uốn lượn lấy bước về phía xa xôi bình địa mặt, tựa như một bức yên tĩnh, tinh tế mực in vẽ."),t("br"),t("br"),n._v("“Đều nhanh đến mùa hạ, nơi này thế mà còn băng tuyết đầy trời...”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được khen ngợi."),t("br"),t("br"),n._v("Hồng Viễn Đế đều đã nhanh đến mùa hạ, màu xanh biếc dạt dào, đâu đâu cũng có áo mỏng mỏng quần, nơi này nhưng rét lạnh như thế, chợt nhìn đến cho người ta một loại dường như đã có mấy đời cảm giác."),t("br"),t("br"),n._v("“Ngươi tới trước tổ ong tu luyện!”"),t("br"),t("br"),n._v("Đi tới ngoài thành một cái không người rừng cây, đem Tử Dương thú thu vào tổ ong, lúc này mới đạp trên tuyết đọng hướng nội thành đi đến."),t("br"),t("br"),n._v("Mặc dù Tử Dương thú tại học viện rất ít đi ra, vì lý do an toàn, vẫn là đừng để nó lộ diện tốt."),t("br"),t("br"),n._v("Giao nộp mười cái hạ phẩm linh thạch, tiến vào trong thành."),t("br"),t("br"),n._v("“Như vậy mù quáng đi khẳng định là không được, nhất định phải tìm tin tức đường nhiều chỗ hỏi thăm một chút!”"),t("br"),t("br"),n._v("Đi dọc theo đường phố một hồi, Trương Huyền trong lòng suy tư."),t("br"),t("br"),n._v("Mặc dù căn cứ manh mối đến xem, Độc điện tại Tĩnh Viễn thành khả năng tương đối lớn, nhưng muốn hỏi thăm ra đến, khẳng định cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Lúc trước tìm tới Hồng Liên sơn mạch Độc điện, cũng là dựa vào Đại Dược Vương dẫn đường, nếu không, coi như tự tìm cái chết, đều không nhất định có thể phát giác."),t("br"),t("br"),n._v("Không phải như thế, Độc điện khẳng định sớm đã bị người diệt tuyệt, cũng không trở thành còn sống đến bây giờ."),t("br"),t("br"),n._v("Giương mắt nhìn một vòng, lập tức nhìn thấy một cái quán rượu đứng sừng sững ở cách đó không xa."),t("br"),t("br"),n._v("“Vong ưu lầu... Vui mà vong ưu, tên không sai!”"),t("br"),n._v(" Nhẹ nhàng cười một tiếng, nhấc chân đi tới."),t("br"),t("br"),n._v("Quán rượu nhiều người nhiều miệng, là hỏi thăm tin tức tốt nhất nơi chốn, lại nói, liên tục phi hành mười ngày, trong miệng cũng đã nhạt không có hương vị, đã đi tới nơi này, vừa vặn nếm thử nơi đó thức ăn ngon."),t("br"),t("br"),n._v("“Vị gia này mời vào bên trong...”"),t("br"),t("br"),n._v("Một cái gã sai vặt tiến lên đón, cười rạng rỡ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu, tiện tay ném ra một viên linh thạch trung phẩm: “Tìm cho ta cái an tĩnh chút vị trí!”"),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Gã sai vặt ánh mắt sáng lên, vội vàng chào hỏi: “Gia, mời tới bên này!”"),t("br"),t("br"),n._v("Linh thạch trung phẩm hiện tại đối Trương Huyền tới nói đã không quan hệ đau khổ, nhưng mà đối một chút sinh hoạt tại tầng dưới chót nhất, dựa vào làm công thu nhập người mà nói, vẫn như cũ xem như một khoản lớn của cải."),t("br"),t("br"),n._v("Tìm một chỗ yên tĩnh ngồi xuống, Trương Huyền tùy tiện chọn chút đặc sắc, cái này mới nhìn lại."),t("br"),t("br"),n._v("“Ta lần đầu tiên tới Tĩnh Viễn thành, không biết nơi nào hỏi thăm tin tức, tương đối tinh chuẩn?”"),t("br"),t("br"),n._v("Loại rượu này cửa hàng gã sai vặt, chắc chắn sẽ không biết Độc điện tin tức, cũng lười hỏi thăm, mà là trực tiếp hỏi bán ra tin tức vị trí."),t("br"),t("br"),n._v("Tình báo, tin tức, luôn luôn là lôi cuốn, rất nhiều người đều bằng lòng ra giá cao mua sắm, loại địa phương này, mỗi tòa thành thị đều có, không tính là hiếm lạ."),t("br"),t("br"),n._v("“Muốn đánh nghe tin tức lời nói, khẳng định muốn đi ! Nơi này tin tức nhất chính xác, nguồn gốc cũng phổ biến nhất...”"),t("br"),t("br"),n._v("Gã sai vặt thấp giọng."),t("br"),t("br"),n._v("“Mặc Vân hiên?”"),t("br"),t("br"),n._v("“Ừm, là chúng ta Tĩnh Viễn thành lớn nhất phòng đấu giá, không chỉ đấu giá bảo vật, đủ loại tin tức tình báo, cũng bán ra! Thậm chí ngay cả Hồng Viễn hoàng thất tin tức đều có, lần trước liền có người chuyên môn mua Ngọc Phi Nhi công chúa tin tức, ý định đi Đế đô cầu hôn...”"),t("br"),t("br"),n._v("Có trước đó linh thạch, gã sai vặt hiện ra đến mức dị thường niềm nở."),t("br"),t("br"),n._v("“Cầu hôn?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Ngọc Phi Nhi loại kia tính tình nóng nảy, dám đi cầu hôn, đoán chừng sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v("Bất quá, nơi này khoảng cách Hồng Viễn thành xa như vậy, liền Ngọc Phi Nhi công chúa tin tức đều có thể nghe ngóng đến, nói rõ cái này Mặc Vân hiên, thật có chút bản lĩnh."),t("br"),t("br"),n._v("Hỏi thăm một chút vị trí cụ thể, biết rời cái này không xa, Trương Huyền không hỏi thêm nữa."),t("br"),t("br"),n._v("Tĩnh Viễn thành mặc dù chỗ vắng vẻ, đồ ăn ngược lại là làm được cực kỳ vừa miệng, ăn một bữa, đứng dậy rời đi, hướng Mặc Vân hiên phương hướng đi đến."),t("br"),t("br"),n._v("Mặc Vân hiên là cái cực lớn cửa hàng, đủ loại bảo vật, tranh chữ, khoáng thạch loại hình cái gì cần có đều có."),t("br"),t("br"),n._v("Trước cửa thủ hộ hộ vệ, nguyên một đám khí tức hùng hồn, tản mát ra người lạ chớ quấy rầy khí tức, vậy mà đều là Hóa Phàm thất trọng Quy Nhất cảnh cường giả."),t("br"),t("br"),n._v("Loại thực lực này, tại Huyễn Vũ đế quốc, tuyệt đối là đỉnh phong nhất một nhóm, ở đây chỉ là thủ vệ, cái này Mặc Vân hiên, nhìn tới quả thực không đơn giản."),t("br"),t("br"),n._v("Đi vào trong đó, đâu đâu cũng có bán, mua sắm, dòng người cuồn cuộn, phi thường náo nhiệt."),t("br"),t("br"),n._v("Biết như vậy tìm kiếm, không biết tìm tới năm nào tháng nào, hơn nữa dính dáng Độc điện tin tức mười phần quan trọng, không có khả năng dễ dàng bán ra, mấy bước đi tới trước đài, biểu hiện trên mặt băng lãnh: “Đem bọn ngươi quản sự kêu đi ra, ta có việc còn muốn hỏi!”"),t("br"),t("br"),n._v("Nói xong, ngón tay dựng thẳng lên, một đạo kiếm khí, phun ra nuốt vào bất định, Tòng Thánh đỉnh phong khí tức toả ra, cho người ta một loại nồng đậm uy áp."),t("br"),t("br"),n._v("“Vâng, vâng!”"),t("br"),t("br"),n._v("Trước đài nhân viên phục vụ, nhìn thấy hắn kẻ đến không thiện, không dám nhiều lời, vội vã đi ra ngoài, thời gian không dài, một người trung niên đi tới trước mặt."),t("br"),t("br"),n._v("“Vị bằng hữu này, tại hạ Mặc Vân hiên chấp sự Chu Khiếu, không biết có gì nhu cầu?”"),t("br"),t("br"),n._v("Là cái đạt tới Bán Thánh cấp bậc trung niên nhân, mặc dù khiếp sợ Trương Huyền khí tức, lại có vẻ đúng mực."),t("br"),t("br"),n._v("“An bài cho ta cái tĩnh thất, ta có chuyện còn muốn hỏi!”"),t("br"),t("br"),n._v("Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Mời tới bên này!”"),t("br"),t("br"),n._v("Chu Khiếu cũng không nói nhảm, chuyển thân ở phía trước dẫn đường."),t("br"),t("br"),n._v("Theo sát mà đằng sau đi một hồi, đi tới một gian phòng kín mít, Trương Huyền nhìn chung quanh một chút, nhẹ gật đầu."),t("br"),t("br"),n._v("Không thể không nói, cái này Mặc Vân hiên giữ bí mật công việc, làm vẫn là khẩu nghiêm cẩn, gian phòng chung quanh che kín đủ loại cấm chế, đừng trò chuyện, coi như chiến đấu, tin tức cũng không biết truyền ra ngoài, hết sức an toàn."),t("br"),t("br"),n._v("“Bằng hữu cảm thấy gian phòng này đã hoàn hảo?”"),t("br"),t("br"),n._v("Chu Khiếu cười cười."),t("br"),t("br"),n._v("“Không sai!” Trương Huyền nhẹ gật đầu, mí mắt vừa nhấc, nhìn lại: “Ta muốn hỏi thăm Độc điện tin tức, nói cái giá đi!”"),t("br"),t("br"),n._v("“Độc điện?”"),t("br"),t("br"),n._v("Chu Khiếu nhíu nhíu mày, lập tức lắc đầu: “Không có ý tứ, chúng ta nơi này không có tin tức của bọn hắn, vẫn là thay đổi một cái đi!”"),t("br"),t("br"),n._v("“Không có?”"),t("br"),t("br"),n._v("Trương Huyền cười cười, lật bàn tay một cái, trên mặt đất lập tức nhiều ra một đống linh thạch thượng phẩm, chừng một trăm cái nhiều: “Đủ sao?”"),t("br"),t("br"),n._v("Nhìn thấy một chút lấy ra hơn trăm viên linh thạch thượng phẩm, Chu Khiếu lông mày nhảy lên, cuối cùng vẫn là cắn răng một cái: “Không có ý tứ, không phải chuyện tiền, là bây giờ không có!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương, Trương Huyền cổ tay khẽ đảo, trên mặt đất lần nữa nhiều ra một đống linh thạch, linh khí xao động, lấp lánh phát sáng, chừng năm trăm viên nhiều."),t("br"),t("br"),n._v("“Đủ sao?”"),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Chu Khiếu vẻ mặt trắng bệch: “Chuyện này đã vượt ra khỏi ta hiểu phạm vi, cần muốn bẩm báo Hiên chủ...”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-986"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-988"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);