(window.webpackJsonp=window.webpackJsonp||[]).push([[2284],{2639:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-979-nap-lieu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-979-nap-lieu"}},[n._v("#")]),n._v(" Chương 979: Nạp liệu")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 979: Nạp liệu"),i("br"),i("br"),n._v("Hắn chỉ là Mặc Vân hiên một cái chấp sự, liên lụy đến mấy trăm linh thạch thượng phẩm, đã không có tư cách làm chủ."),i("br"),i("br"),n._v("“Đi đi!”"),i("br"),i("br"),n._v("Trương Huyền khoát tay áo."),i("br"),i("br"),n._v("Độc điện tin tức nếu như dễ dàng như vậy liền có thể biết được, cũng không trở thành nhiều năm như vậy, đều để người không rõ ràng cho lắm."),i("br"),i("br"),n._v("Cho nên, trong lòng của hắn đã sớm có chuẩn bị, vừa đến liền dùng tiền đập."),i("br"),i("br"),n._v("Coi như đối phương không biết tin tức xác thật, nhưng ở Tĩnh Viễn thành đợi lâu như vậy, cũng hẳn là nghe qua một chút dấu vết để lại, đổi lại người ta, cho dù biết những tin tức này, cũng không phân biệt ra được tới thật giả, không có một chút tác dụng nào."),i("br"),i("br"),n._v("Nhưng đối Trương Huyền tới nói, chỉ cần có tin tức, ngay tại Độc điện trốn đến dưới đất, đều có thể cho nó đào ra."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Chu Khiếu vội vã lui ra khỏi phòng, trực tiếp đi ra ngoài, thời gian không dài, đi tới một cái rộng rãi gian phòng."),i("br"),i("br"),n._v("“Hiên chủ...”"),i("br"),i("br"),n._v("Đi vào trong đó, đối một người trung niên ôm quyền."),i("br"),i("br"),n._v("Mặc Vân hiên Hiên chủ, Hồ Vân Sinh, Thánh vực nhất trọng cường giả đỉnh phong!"),i("br"),i("br"),n._v("“Thế nào?”"),i("br"),i("br"),n._v("Đang tại cúi đầu đọc qua cái gì, thấy hắn đi vào, Hồ Vân Sinh ngẩng đầu nhìn tới."),i("br"),i("br"),n._v("“Bẩm báo Hiên chủ, vừa mới vị võ giả tới, muốn mua... Liên quan tới Độc điện tin tức!”"),i("br"),i("br"),n._v("Chu Khiếu nói."),i("br"),i("br"),n._v("“Độc điện tin tức?” Hồ Vân Sinh nhướng mày: “Liền nói chúng ta nơi này chưa có, đuổi đi là được!”"),i("br"),i("br"),n._v("“Ta có nói hay chưa, có điều, đối phương dường như rất có tiền, lấy ra ít nhất hơn 500 viên linh thạch thượng phẩm, ý định mua sắm, ta không cách nào làm chủ... Chuyên tới để bẩm báo!”"),i("br"),i("br"),n._v("Chu Khiếu đem chuyện giải thích một lần."),i("br"),i("br"),n._v("“Hơn 500 viên linh thạch thượng phẩm?” Hồ Vân Sinh con ngươi co rụt lại."),i("br"),i("br"),n._v("Danh sư học viện Mi trưởng lão, Triệu trưởng lão đám người, linh thạch thượng phẩm một hơi cũng nhiều nhất lấy ra mấy chục, không cao hơn một trăm, như vậy đủ thấy trân quý."),i("br"),i("br"),n._v("Đối phương một hơi lấy ra năm trăm... Liền xem như Mặc Vân hiên một năm lợi nhuận, cũng không có nhiều như vậy."),i("br"),i("br"),n._v("“Vâng!” Chu Khiếu gật đầu."),i("br"),i("br"),n._v("“Hạng người gì?” Nhíu nhíu mày, Hồ Vân Sinh hỏi."),i("br"),i("br"),n._v("“40 tuổi khoảng chừng, thực lực giống như ta, Tòng Thánh đỉnh phong, cụ thể cái gì lai lịch, không rõ lắm...” Muốn hỏi một chút, Chu Khiếu nói."),i("br"),i("br"),n._v("“Tòng Thánh đỉnh phong, một hơi lấy ra 500 viên linh thạch thượng phẩm?” Hồ Vân Sinh tựa hồ có chút khó tin."),i("br"),i("br"),n._v("Liền hắn đều không bỏ ra nổi nhiều như vậy, một cái Tòng Thánh, tại sao có thể có nhiều như vậy của cải?"),i("br"),i("br"),n._v("Chẳng lẽ là một ít thế lực lớn, người của đại gia tộc?"),i("br"),i("br"),n._v("“Ta đi qua nhìn một chút!”"),i("br"),i("br"),n._v("Hồ Vân Sinh đứng dậy."),i("br"),i("br"),n._v("“Vâng... Chỉ là Hiên chủ, chúng ta thực sự không có Độc điện tin tức, nếu như hắn khăng khăng mua sắm, nên làm cái gì?” Chu Khiếu nói."),i("br"),i("br"),n._v("Độc điện thập phần thần bí, liền coi như bọn họ Mặc Vân hiên nguồn tin tức rất rộng rãi, cũng không có không biết vị trí cụ thể, đối phương có tiền như vậy, khăng khăng mua, bọn họ không bỏ ra nổi đến, đối danh dự sẽ có rất lớn tổn hại."),i("br"),i("br"),n._v("“Nhìn tình huống mà quyết định, nếu như là người của đại gia tộc, bảo chúng ta sưu tập một chút tin tức, cho hắn không sao... Nếu như chỉ là cái nhà giàu mới nổi loại hình, loại này dê béo, đã đi vào Mặc Vân hiên, cũng đừng nghĩ đi...”"),i("br"),i("br"),n._v("Hồ Vân Sinh mắt sáng lên."),i("br"),i("br"),n._v("Mặc Vân hiên không chỉ bán ra bảo vật, toàn bộ Tĩnh Viễn thành chợ đen, cũng đều từ hắn khống chế, đối phương nếu là thế lực lớn hậu bối, sau lưng có cường giả thủ hộ ngược lại cũng thôi, nếu không... Một cái Tòng Thánh, lấy ra nhiều linh thạch như vậy, không là muốn chết ư?"),i("br"),i("br"),n._v("Mặc dù danh sư cho rằng quy củ, có rất ít người dám đụng vào, nhưng nơi này núi cao Hoàng đế xa, liền cái Danh Sư đường đều không có, giết từng cái đem hai cái, căn bản không có người có thể phát giác."),i("br"),i("br"),n._v("“Cái này... Có thể lấy ra nhiều linh thạch như vậy, liền xem như nhà giàu mới nổi, khẳng định phía sau cũng có cường giả...” Chu Khiếu có chút lo lắng."),i("br"),i("br"),n._v("Có tiền, muốn có thực lực mới có thể thủ hộ."),i("br"),i("br"),n._v("Nếu không, tiền nhiều hơn nữa cũng là của người khác."),i("br"),i("br"),n._v("Đối phương có thể không có sợ hãi lấy ra nhiều như vậy, coi như không có đại gia tộc, thế lực lớn bối cảnh, cũng tất nhiên không đơn giản, thật muốn lặng lẽ giết, đối Mặc Vân hiên đoán chừng cũng là phiền phức."),i("br"),i("br"),n._v("“Ha ha, hắn không phải hỏi thăm Độc điện tin tức ư? Bị Độc điện người giết không kỳ quái a? Chỉ cần làm tốt, ai sẽ hoài nghi chúng ta Mặc Vân hiên?”"),i("br"),i("br"),n._v("Hồ Vân Sinh cười lạnh."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Chu Khiếu ngẩn ngơ."),i("br"),i("br"),n._v("Như thế."),i("br"),i("br"),n._v("Đối phương hỏi thăm Độc điện, chọc giận đối phương, bị độc chết... Điểm ấy thuận lý thành chương, chỉ cần làm tốt lắm, không có người sẽ hoài nghi đến trên người bọn họ."),i("br"),i("br"),n._v("“Được rồi, đi đem ta trước đó đưa cho ngươi hồ lô kia rượu ngon chuẩn bị một chút, sau khi tới, hành sự tùy theo hoàn cảnh!”"),i("br"),i("br"),n._v("Không nói thêm lời, Hồ Vân Sinh khoát tay áo."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Chu Khiếu gật gật đầu, vội vã hướng một cái phòng đi đến, thời gian không dài lần nữa trở lại trước mặt, hai người một trước một sau hướng Trương Huyền vị trí tĩnh thất đi đến."),i("br"),i("br"),n._v("“Nghe nói vị bằng hữu này, muốn mua liên quan tới Độc điện tin tức?”"),i("br"),i("br"),n._v("Đi tới gian phòng, ngồi xuống, Hồ Vân Sinh tử quan sát kỹ Trương Huyền."),i("br"),i("br"),n._v("Trước mắt trung niên nhân, khí tức hùng hồn, quả thực là Tòng Thánh đỉnh phong, dung mạo khá là lạ lẫm, chưa bao giờ thấy qua, hẳn không phải là Tĩnh Viễn thành người."),i("br"),i("br"),n._v("Tĩnh Viễn thành cùng xung quanh hơn mười thành thị Tòng Thánh cường giả, tổng cộng cứ như vậy nhiều, coi như chưa thấy qua, cũng xem qua tài liệu tương quan, không có một vị cùng trước mắt vị này tương xứng."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Trương Huyền nhàn nhạt nhìn qua."),i("br"),i("br"),n._v("“Độc điện không chỉ bí ẩn, hơn nữa có thù tất báo, nếu như chúng ta tiết lộ tin tức của bọn hắn, vô cùng có khả năng gặp phải trả thù... Không biết vị bằng hữu này xưng hô như thế nào, đi Độc điện lại là vì chuyện gì? Có thể hay không thuận tiện để lộ một chút? Chúng ta cũng tốt tổng hợp cân nhắc.”"),i("br"),i("br"),n._v("Chần chờ một chút, Hồ Vân Sinh nói."),i("br"),i("br"),n._v("“Tại hạ Tôn Cường!” Trương Huyền nói."),i("br"),i("br"),n._v("Tên của hắn, tại Hồng Viễn đế quốc xem như rất nổi danh, tùy tiện đều có thể tra, Tôn Cường làm quản gia, không có bất kỳ cái gì danh khí, coi như nói ra, đối phương cũng nhiều nhất tưởng rằng cùng tên, sẽ không đoán được trên người hắn."),i("br"),i("br"),n._v("“Tôn Cường? Thế nhưng là... Kình Viễn thành Tôn gia người?”"),i("br"),n._v("Hồ Vân Sinh sững sờ, nhịn không được nói."),i("br"),i("br"),n._v("“Không phải!”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu."),i("br"),i("br"),n._v("“Cái kia... Là Nghiêu Hổ tông tông chủ hậu nhân?” Hồ Vân Sinh hỏi tiếp."),i("br"),i("br"),n._v("Trương Huyền tiếp tục lắc đầu."),i("br"),i("br"),n._v("“Cái kia...”"),i("br"),i("br"),n._v("Hồ Vân Sinh liên tục hỏi Hồng Viễn đế quốc mười cái nổi danh Tôn gia."),i("br"),i("br"),n._v("“Không cần suy nghĩ, tại hạ chỉ là cái tán tu, không môn không phái, hỏi thăm Độc điện, là có chút việc tư phải xử lý, sẽ không dính dấp đến những người khác, yên tâm đi, ta sẽ không nói là theo ngươi nơi này lấy được tin tức!”"),i("br"),i("br"),n._v("Thấy đối phương có đánh vỡ nồi đất hỏi đến tột cùng tư thế, Trương Huyền khoát tay áo, nói."),i("br"),i("br"),n._v("“Tán tu?”"),i("br"),i("br"),n._v("Hồ Vân Sinh cười cười: “Thì ra là thế, đã Tôn huynh như vậy cam đoan, ta cũng không còn nói nhảm, ta chỗ này là có một ít liên quan tới Độc điện tin tức, chỉ bất quá đều không tỉ mỉ... Chu Khiếu, ngươi đi cho vị này Tôn huynh lấy tới!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Chu Khiếu gật gật đầu đứng dậy, còn chưa đi ra gian phòng, liền nghe đến đối phương phân phó tiếng tiếp tục vang lên: “Không vội, trước phái người cầm chút thịt rượu tới!”"),i("br"),i("br"),n._v("Nói xong nhìn về phía Trương Huyền: “Tôn huynh đừng có gấp, những tài liệu này, sửa sang lại cần một chút thời gian, ngươi ta không ngại uống rượu đợi chờ!”"),i("br"),i("br"),n._v("Chu Khiếu gật đầu lui ra ngoài, thời gian không dài, liền mang thức nhắm cùng một bầu rượu đi đến."),i("br"),i("br"),n._v("“Tôn huynh mời!”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Hồ Vân Sinh đem chén rượu đổ đầy."),i("br"),i("br"),n._v("Trương Huyền gật gật đầu bưng chén rượu lên, một mặt chân thành nhìn qua: “Không biết Hiên chủ xưng hô như thế nào?”"),i("br"),i("br"),n._v("“Quên tự giới thiệu mình... Tại hạ họ Hồ!”"),i("br"),i("br"),n._v("Hồ Vân Sinh nói."),i("br"),i("br"),n._v("“Hồ? Ta vừa vặn có người bằng hữu cũng họ Hồ, có điều, nàng giảo hoạt như cáo, nhìn Hồ huynh bộ dạng, lại hết sức chân thành!”"),i("br"),i("br"),n._v("Trương Huyền cảm khái."),i("br"),i("br"),n._v("“Cái kia là tự nhiên, ngươi tới đến nơi này của ta, chính là khách nhân của ta, thương nghiệp lấy tin làm gốc, tự nhiên muốn chân thành một chút!”"),i("br"),i("br"),n._v("Đem trên mặt vẻ lúng túng chi ý che giấu, Hồ Vân Sinh vội nói."),i("br"),i("br"),n._v("“Cũng thế, làm ăn, tự nhiên muốn chân thành một chút, làm!”"),i("br"),i("br"),n._v("Trò chuyện hai câu, Trương Huyền giơ ly rượu lên, một uống mà xuống."),i("br"),i("br"),n._v("“Cạn!” Hồ Vân Sinh gật gật đầu, dùng miệng mấp máy chén rượu, để xuống."),i("br"),i("br"),n._v("“Hồ huynh uống điểm ấy, có phải hay không xem thường Tôn mỗ?”"),i("br"),i("br"),n._v("Thấy hắn chỉ nhấp một chút, Trương Huyền lông mày nâng lên."),i("br"),i("br"),n._v("“Dĩ nhiên không phải, ta chỉ là chuyện rất nhiều, hơn nữa bản thân tửu lực kém, cho nên...” Hồ Vân Sinh khóe miệng giật một cái, vội vàng giải thích."),i("br"),i("br"),n._v("“Cái gì tửu lực kém, ngươi mới nói ngươi chân thành, uống rượu tự nhiên cũng phải chân thành một chút, đến, ta giúp ngươi!”"),i("br"),i("br"),n._v("Cười một tiếng, Trương Huyền tay phải tại đối phương bả vai vừa đáp, tay trái bưng lên đối phương chén rượu, đối với miệng của hắn liền đưa tới."),i("br"),i("br"),n._v("Thấy cái tên này lại muốn cho mình này rượu, Hồ Vân Sinh tràn đầy không vui, vẻ mặt trầm thấp xuống: “Không cần làm phiền Tôn huynh, ta tự mình tới...”"),i("br"),i("br"),n._v("“Không làm phiền, tất cả mọi người là bằng hữu, không cần khách khí...”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền khóe miệng nâng lên."),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Thấy đối phương ý tứ muốn đem chén rượu này rót vào miệng của hắn, Hồ Vân Sinh chân khí trong cơ thể vận chuyển. Đang muốn tránh thoát bàn tay của đối phương, đẩy ra chén rượu, lại phát hiện toàn thân giống như là bị giam cầm đồng dạng."),i("br"),i("br"),n._v("Đối phương chân khí, như cùng một cái không thể phản kháng gông xiềng, đem toàn thân hắn khóa lại, một không thể động đậy được."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Con ngươi co rụt lại, sống lưng tràn đầy mồ hôi lạnh."),i("br"),i("br"),n._v("Trước đó chỉ cho là đối phương là cái Tòng Thánh, không để vào mắt, cảm nhận được cỗ lực lượng này, lập tức rõ ràng... Thế này sao lại là Tòng Thánh, coi như hắn cái này Thánh vực nhất trọng đỉnh phong đối đầu, cũng chỉ có bị hoàn ngược phần!"),i("br"),i("br"),n._v("Cái tên này, quả thực chính là giả heo ăn thịt hổ."),i("br"),i("br"),n._v("Từ nơi nào xuất hiện?"),i("br"),i("br"),n._v("Mấu chốt nhất là, đối phương dường như nhìn ra rượu của hắn có vấn đề, lúc này mới cứng rắn rót bản thân..."),i("br"),i("br"),n._v("“Đến, uống đi, không cần khách khí!”"),i("br"),i("br"),n._v("Đang đang khiếp sợ, liền thấy đối phương chén rượu, đã đi tới miệng trước mặt, không nói lời gì, liền rót xuống dưới."),i("br"),i("br"),n._v("“Khụ khụ khụ!”"),i("br"),i("br"),n._v("Hồ Vân Sinh sặc nước mắt chảy ròng, đang muốn nói chuyện, liền thấy đối phương đem hồ lô rượu cũng cầm lên, còn không có giải thích, liền trực tiếp nhét vào trong miệng của hắn."),i("br"),i("br"),n._v("Ừng ực, ừng ực, ừng ực!"),i("br"),i("br"),n._v("Mấy đạo rượu theo cổ họng rót vào, suýt chút nữa không có ngất đi."),i("br"),i("br"),n._v("Đây chính là ẩn chứa kịch độc rượu, một chút uống nhiều như vậy, cho dù có giải dược, cũng chịu không được ah! Không nghĩ tới ngày ngày đánh nhạn, bị nhạn mổ vào mắt!"),i("br"),i("br"),n._v("Cô cô cô cục cục!"),i("br"),i("br"),n._v("Thời gian nháy mắt, bị đối phương nắm miệng, đem một hồ lô rượu tất cả đều rót đi vào."),i("br"),i("br"),n._v("Rót xong, đối phương lúc này mới buông tay ra chưởng, cười khanh khách nhìn qua: “Vẫn là chính ngươi uống rượu ngon đi, hương vị như thế nào?”"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Vẻ mặt lúc trắng lúc xanh, Hồ Vân Sinh vội vã lấy ra một hoàn thuốc nhét vào miệng bên trong, nhìn về phía đối diện trung niên nhân, ánh mắt lộ ra trần trụi sát ý: “Ngươi đến cùng là ai?”"),i("br"),i("br"),n._v("“Ta là Tôn Cường, vừa rồi không sẽ nói cho ngươi biết sao?”"),i("br"),i("br"),n._v("Trương Huyền cười cười, vỗ trán một cái: “A, đúng, vừa rồi ta cẩn thận tại ngươi trong rượu tăng thêm chút gia vị, có chút mãnh liệt, không biết ngươi quen thuộc không quen...”"),i("br"),i("br"),n._v("Lời còn chưa dứt, Hồ Vân Sinh sắc mặt trắng nhợt, liền cảm thấy toàn thân cứng ngắc, “Oa!” Một ngụm máu tươi phun tới."),i("br"),i("br"),n._v("“Ngươi đối ta hạ độc?” Hồ Vân Sinh con ngươi co rụt lại: “Ngươi là độc sư?”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-987"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-989"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);