(window.webpackJsonp=window.webpackJsonp||[]).push([[2285],{2640:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-980-đia-tang-hoa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-980-đia-tang-hoa"}},[n._v("#")]),n._v(" Chương 980: Địa Tàng hoa")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 980: Địa Tàng hoa"),t("br"),t("br"),n._v("Vừa rồi uống rượu, hắn thẳng nhìn ở trong mắt, đối phương chỉ là bắt lấy chén rượu, hồ lô rượu, cũng không đụng tới rượu, loại tình huống này chính mình cũng có thể trúng độc, hơn nữa để cho mình một vị Thánh vực nhất trọng trung kỳ cường giả đều không hề có lực hoàn thủ, không phải độc sư là cái gì?"),t("br"),t("br"),n._v("Mấu chốt nhất đúng vậy, có thể lặng yên không một tiếng động cho mình hạ độc, hơn nữa độc tính mãnh liệt như vậy, chỉ sợ cấp bậc ít nhất đều muốn đạt tới lục tinh cấp bậc!"),t("br"),t("br"),n._v("Loại cấp bậc này độc sư, thế mà liền Độc điện vị trí cũng không biết... Ngươi là cố ý tới thu thập ta a?"),t("br"),t("br"),n._v("Buồn cười hắn còn định dùng độc, đem người ta hạ độc chết... Cái này được rồi, bản thân trước bị độc bán thân bất toại..."),t("br"),t("br"),n._v("“Ha ha, không phải độc sư, ai hỏi thăm Độc điện vị trí làm gì?”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền xoa cái búng tay: “Đến, lại phun một ngụm chơi đùa!”"),t("br"),t("br"),n._v("Đối phương tại trong rượu bỏ thuốc độc, hắn nhẹ nhõm phát hiện, Thiên Đạo vận chuyển chân khí, lập tức liền hoàn toàn hóa giải, sau đó thuận tiện truyền vào hai đạo chân khí tại đối phương trong rượu, tâm thần khẽ động, biến thành kịch độc."),t("br"),t("br"),n._v("Thiên Đạo chân khí tinh thuần vô cùng, có thể tiến nhập thể nội bất luận cái gì một chỗ huyệt vị cùng huyệt đạo, coi như đối phương là Thánh vực nhất trọng trung kỳ, đều ngăn cản không nổi."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Nương theo lời của hắn, chân khí tại đối phương trong cơ thể chạy, vù vù một tiếng, Hồ Vân Sinh lần nữa máu tươi phun ra."),t("br"),t("br"),n._v("“Ngươi... Ta muốn ngươi chết!”"),t("br"),t("br"),n._v("Vẻ mặt trắng bệch, gào lên một tiếng, giãy dụa lấy vận chuyển chân khí, Hồ Vân Sinh bỗng nhiên nhảy dựng lên, đối Trương Huyền liền nhào tới."),t("br"),t("br"),n._v("Đường đường Mặc Vân hiên Hiên chủ, liền Tĩnh Viễn thành thành chủ đều nhường nhau mấy phần tồn tại, bị một cái Tòng Thánh hạ độc, tức giận đến sắp bùng nổ, thân thể lăng không, bàn tay đột nhiên đè ép xuống."),t("br"),t("br"),n._v("“Không có chút nào ngoan, xuống!”"),t("br"),t("br"),n._v("Ngồi tại nguyên chỗ không động chút nào, Trương Huyền mí mắt vừa nhấc, lần nữa xoa cái búng tay."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Chưởng lực còn không rơi xuống, Hồ Vân Sinh lần nữa co lại, từ không trung rớt xuống, nằm rạp trên mặt đất, như cùng một cái con cóc."),t("br"),t("br"),n._v("“Cái này... Độc, có linh tính?”"),t("br"),t("br"),n._v("Thân thể phát run, Hồ Vân Sinh giống như là gặp quỷ đồng dạng."),t("br"),t("br"),n._v("Độc chia làm hai loại, một là không có linh tính, một loại là có linh tính."),t("br"),t("br"),n._v("Cái trước chỉ muốn tìm phương pháp, tìm tìm thuốc giải, khẳng định có thể giải quyết; Người sau cùng sinh mệnh đồng dạng, có thể xu cát tị hung, thậm chí nghe theo mệnh lệnh, coi như tìm tới giải dược cũng chưa chắc có thể giải độc."),t("br"),t("br"),n._v("Chỉ là có linh tính độc, rườm rà phức tạp, cùng cổ trùng đồng dạng, cần nuôi dưỡng. Thấp nhất đều muốn đạt tới bát tinh độc sư mới có thể phối trí, chẳng lẽ trước mắt đây là một vị loại cấp bậc này độc sư?"),t("br"),t("br"),n._v("Chỉ là, bát tinh độc sư, tu vi ít nhất phải đạt tới Hóa Phàm lục trọng, loại người này mỗi một cái đều là đại lục ở bên trên nổi danh cường giả, làm sao lại đi tới cái này vắng vẻ tiểu thành tìm kiếm Độc điện?"),t("br"),t("br"),n._v("Trong lòng kinh nghi bất định, tràn đầy sợ hãi."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền cũng không giải thích."),t("br"),t("br"),n._v("Thiên Đạo chân khí coi như rời khỏi thân thể, vẫn như cũ có thể dựa theo ý niệm của hắn tùy ý vận chuyển, mặc dù không có linh tính, nhưng cũng cùng có linh tính độc dược không có khác nhau quá nhiều."),t("br"),t("br"),n._v("Đương nhiên, khoảng cách gần, có thể tùy ý khống chế, một khi qua xa, tinh thần lực lan tràn không đến, cũng liền không khống chế được."),t("br"),t("br"),n._v("Không giống trong cơ thể thai độc, vô luận đi đến nơi nào, đều không thể thoát khỏi, đã có ý thức tự chủ, liền xem như hắn đều không có cách nào."),t("br"),t("br"),n._v("“Ta... Nhận thua!” Nghe được là có linh tính độc, vẻ mặt lúc thì đỏ, lúc thì trắng, qua cả buổi cắn răng một cái, Hồ Vân Sinh nói."),t("br"),t("br"),n._v("Không nhận ngã cũng không được, đánh lại đánh không lại, huống chi trúng độc, sinh mệnh khống chế tại trong tay đối phương, sống chết còn không phải đối phương chuyện một câu nói?"),t("br"),t("br"),n._v("Muốn đen ăn đen, lời ít tiền, kết quả cái gì đều không có lấy tới, bản thân trước sắp bị chơi chết rồi, suy nghĩ một chút liền cảm thấy tràn đầy tâm tắc."),t("br"),t("br"),n._v("“Cái này còn tạm được...” Nghe được nhận thua, Trương Huyền cười cười: “Ta muốn biết Độc điện chỗ có tin tức!”"),t("br"),t("br"),n._v("Hồ Vân Sinh cắn răng: “Nói thật, chúng ta Mặc Vân hiên mặc dù tin tức làm rất khá, nhưng là đối với Độc điện nên cũng biết không nhiều, bọn họ hết sức cẩn thận...”"),t("br"),t("br"),n._v("“Ngươi liền nói cho ta biết Độc điện, có phải hay không tại đây Tĩnh Viễn thành lân cận?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Vâng!” Hồ Vân Sinh nhẹ gật đầu: “Những năm này cũng không ít người hỏi thăm Độc điện tin tức, ta cũng sưu tập một chút, xác nhận ngay tại cách đó không xa Vân Thương hải, vị trí cụ thể nhưng là thật không biết. Hơn nữa nghe nói coi như tìm được vị trí thực sự, không có đặc thù thủ lệnh, cũng là không cách nào tiến vào!”"),t("br"),t("br"),n._v("“Vân Thương hải?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Vân Thương hải là Hồng Viễn đế quốc lớn nhất lục địa biển, phạm vi bao phủ mấy chục vạn cây số, bao la vô biên, trọng yếu nhất chính là bên trong có vô số hung mãnh linh thú, Thánh thú, liền xem như Thánh vực tam tứ trọng, tứ ngũ trọng cường giả đều không dám tùy tiện đi vào."),t("br"),t("br"),n._v("Độc điện nếu như thật sự ở nơi này mặt lời nói, hơn nữa có trận pháp ẩn hình, không biết xác thực vị trí, căn bản là không thể nào tìm tới."),t("br"),t("br"),n._v("Khó trách liền lão viện trưởng đều chỉ là xác nhận phạm vi, ẩn trốn ở chỗ này, quả thực đủ bí ẩn."),t("br"),t("br"),n._v("“Vâng, tin tức này, cũng là lần trước một vị Độc điện trưởng lão, tới đây mua đồ, trong lúc vô tình nói ra được... Cái khác ta là thật không biết...” Hồ Vân Sinh hơi đỏ mặt."),t("br"),t("br"),n._v("Hiện tại trúng đối phương độc, không cho giải lời nói, vô cùng có khả năng tử vong."),t("br"),t("br"),n._v("Thế nhưng là, hắn lại là thật không có đối phương muốn biết tin tức."),t("br"),n._v(" Độc điện tại rồng biển cả... Nói tương đương nói vô ích, hải dương phạm vi như thế lớn, lại có vô số Thánh thú, không có phương hướng, ai dám tùy tiện dò xét?"),t("br"),t("br"),n._v("“Mua đồ?” Không để ý đến sự lo lắng của hắn, Trương Huyền ngược lại nhíu nhíu mày, nhìn lại: “Độc điện trưởng lão, tới ngươi nơi này mua đồ? Mua cái gì?”"),t("br"),t("br"),n._v("“Lần trước đấu giá hội, bán ra một gốc trăm năm Địa Tàng hoa, là phối chế độc dược tốt nhất dược vật, mười phần thưa thớt, cho nên mới đưa tới Độc điện một vị trưởng lão ra tay mua sắm... Cuối cùng, hắn vô cùng giá tiền thấp mua đi...”"),t("br"),t("br"),n._v("Hồ Vân Sinh khuôn mặt xanh xám."),t("br"),t("br"),n._v("Cái này gốc Địa Tàng hoa, thế nhưng là bọn họ Mặc Vân hiên hao tốn rất lớn đánh đổi lấy được, tổng giá trị gần hơn trăm linh thạch thượng phẩm, kết quả... Đối phương tới mua, một chút độc trèo bọn họ mười mấy người, bản thân cũng thiếu chút trúng độc, lúc này mới hết cách rồi, giá rẻ bán ra."),t("br"),t("br"),n._v("Có thể nói, lần này buôn bán, bị thiệt lớn, để hắn ròng rã một năm đều không có thong thả lại sức."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, bọn họ phòng đấu giá, hiện tại rất ít làm dược tài buôn bán, chính là sợ đối phương ăn cướp trắng trợn, cũng tới cái đen ăn đen."),t("br"),t("br"),n._v("“Ngươi nơi này nhưng còn có Độc điện nhất định phải sử dụng dược liệu, hơn nữa niên đại cao? Có thể hay không đem bọn hắn thu hút tới?” Suy tư một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Cái này Mặc Vân hiên không biết Độc điện vị trí cụ thể, nhưng mà Độc điện trưởng lão, tất nhiên biết, hoàn toàn có thể tới cái dẫn xà xuất động."),t("br"),t("br"),n._v("Nói cách khác, lợi dụng bọn họ nhất định dược liệu, đem hắn thu hút tới, chỉ cần có độc sư xuất hiện, đem hắn đồng phục, tìm Độc điện vị trí, liền đơn giản."),t("br"),t("br"),n._v("Mặc dù hắn hiện tại đối độc lý giải, chỉ có nhị tinh trái phải, nhưng người mang Thiên Đạo chân khí, coi như lục tinh đỉnh phong độc sư, thậm chí thất tinh tới, đều không sợ chút nào."),t("br"),t("br"),n._v("Đương nhiên, thực lực mạnh hơn lại không được, tựa như Tiên Thiên thai độc, có thể xu cát tị hung, cho dù Thiên Đạo chân khí, cũng nại hắn không sao."),t("br"),t("br"),n._v("Độc sư có thể làm cho vô số người nghe tin đã sợ mất mật, quả thực có bản thân đặc thù năng lực cùng thực lực."),t("br"),t("br"),n._v("Nếu như không phải kiêng kị Danh Sư đường, hơn nữa độc vô cùng khó học, làm không cẩn thận liền sẽ tổn hại bản thân, chỉ sợ thượng cửu lưu chức nghiệp bên trong, tất nhiên có thứ nhất chỗ vị trí."),t("br"),t("br"),n._v("“Nhất định phải sử dụng dược liệu?”"),t("br"),t("br"),n._v("Lập tức rõ ràng đối phương ý tứ, Hồ Vân Sinh lập tức tràn đầy xấu hổ: “Chúng ta Mặc Vân hiên, mấy năm gần đây đều không có thu qua dược liệu, cho dù có một chút, cũng giá trị không cao... Chỉ sợ rất khó đem hắn đả động!”"),t("br"),t("br"),n._v("Ăn một lần thua thiệt, bọn họ cũng biết độc sư đáng sợ, tự nhiên lại không nguyện ý thu mua dược liệu."),t("br"),t("br"),n._v("Nếu không, lại đến mấy cái cướp, còn không bệnh thiếu máu?"),t("br"),t("br"),n._v("“Cái kia ngươi cũng đã biết, dạng gì dược liệu, có thể để cho động tâm?”"),t("br"),t("br"),n._v("Biết mình Thiên Đạo chân khí, tại đối phương trong cơ thể quanh quẩn một chỗ, khẳng định không dám nói láo, Trương Huyền nhướng mày."),t("br"),t("br"),n._v("“Theo ta được biết, lục tinh độc sư muốn muốn đánh vào thất tinh, cần phối chế có thể độc chết Thánh vực nhị trọng cường giả kịch độc, mà loại vật này, rất tài liệu chủ yếu chính là Địa Tàng hoa... Nếu có niên đại lớn dược liệu, tất nhiên có thể dẫn tới độc sư trước tới mua. Chỉ là... Thuốc này mười phần hiếm có, chúng ta lần trước có thể có được, cũng là có rất lớn vận may ở bên trong...”"),t("br"),t("br"),n._v("Hồ Vân Sinh nói."),t("br"),t("br"),n._v("“Địa Tàng hoa?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lần trước Vân Vụ lĩnh rất nhiều Thánh thú, đưa không ít dược liệu, nhưng mà, cũng không có Địa Tàng hoa, hắn sưu tập rất nhiều vật phẩm bên trong, cũng không có loại thuốc này vật."),t("br"),t("br"),n._v("Nhìn tới cùng đối phương nói đồng dạng, quả thực mười phần thưa thớt."),t("br"),t("br"),n._v("“Không bằng như vậy, ngươi đối ngoại tuyên bố, nắm giữ hai trăm năm phần Địa Tàng hoa, muốn đấu giá, chỉ cần đem bọn hắn dẫn tới là được!” Trương Huyền nói."),t("br"),t("br"),n._v("Mục đích của hắn chỉ là thu hút đối phương, cũng không phải là thật cần loại thuốc này vật, chỉ cần có thể đem hắn chơi tới bắt lấy, chuyện về sau liền dễ làm."),t("br"),t("br"),n._v("“Cái này... Chỉ sợ không dễ dàng như vậy! Độc sư không dùng độc dưới tình huống, cùng bình thường tu luyện giả không có khác nhau chút nào, nếu như bọn họ phát hiện cái gọi là Địa Tàng hoa tin tức là giả... Một khi không ra tay, chúng ta không có cách nào xác nhận, sao lại biết?”"),t("br"),t("br"),n._v("Hồ Vân Sinh cười khổ."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Độc sư cùng vu hồn sư đồng dạng, tại đại lục thuộc về chuột chạy qua đường, người người kêu đánh, chỉ cần không dùng độc, liền xem như hắn... Đều nhận không ra."),t("br"),t("br"),n._v("Thiên Đạo thư viện chỉ có thể quan sát sử dụng võ kỹ tu luyện giả, đối phương không sử dụng, hơn nữa trước tới mua nhiều người như vậy, đi nơi nào tìm kiếm?"),t("br"),t("br"),n._v("“Mấu chốt nhất là, nếu như bọn họ tuân thủ quy củ, dùng tiền mua sắm... Chúng ta cũng không có cách nào cự tuyệt...” Hồ Vân Sinh tiếp tục nói."),t("br"),t("br"),n._v("Lần trước hắn bị người ép mua, là tình huống đặc biệt, cũng không thể tất cả độc sư đều hư hỏng như vậy đi, vạn nhất đối phương dùng tiền mua sắm, mình không lấy ra dược liệu... Mặc Vân hiên về sau đoán chừng cũng không cần làm nữa, khẳng định sẽ bị toàn bộ độc chết."),t("br"),t("br"),n._v("“Cũng đúng...” Trương Huyền xoa xoa lông mày."),t("br"),t("br"),n._v("Nhìn tới hắn biện pháp này cũng không thể được."),t("br"),t("br"),n._v("“Thật ra thì... Cũng không cần làm bộ, Địa Tàng hoa mặc dù thưa thớt, nhưng... Ta biết có người, khẳng định có, hơn nữa năm số tuyệt đối vượt qua trăm năm!”"),t("br"),t("br"),n._v("Cắn răng một cái, Hồ Vân Sinh nhớ ra cái gì đó, nói."),t("br"),t("br"),n._v("“Ồ? Ai?”"),t("br"),t("br"),n._v("Nghe được có người có đất giấu hoa, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("“Hắn tên gọi là gì, ta cũng không rõ ràng, chỉ biết là là cái mười phần cổ quái người, tất cả mọi người xưng hô , không chỉ trồng trọt dược vật, còn sưu tập không ít bảo bối, lần trước ta gốc kia Địa Tàng hoa, chính là theo trong tay hắn lấy được... Nếu như đi chỗ của hắn tìm, tất nhiên có thể tìm tới!”"),t("br"),t("br"),n._v("Hồ Vân Sinh nói."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-988"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-990"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);