(window.webpackJsonp=window.webpackJsonp||[]).push([[2287],{2642:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-981-quai-tau-ong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-981-quai-tau-ong"}},[n._v("#")]),n._v(" Chương 981: Quái tẩu ông")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 981: Quái tẩu ông"),t("br"),t("br"),n._v("“Quái tẩu ông? Hắn ở đâu? Mang ta tới nhìn xem!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đã có người có, trực tiếp mua sắm chính là, còn chơi đùa cái gì?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hồ Vân Sinh cười khổ: “Nếu đơn giản như vậy, liền dễ dàng! Vị này quái tẩu ông, tính khí đặc biệt không tốt, mềm không được cứng không xong, không phải có tiền liền có thể giải quyết. Bảo vật của hắn cùng dược vật, theo không bán ra. Lần trước thành chủ nhu cầu cấp bách một phần dược liệu, chuẩn bị rất nhiều linh thạch, giá trị đầy đủ mua ba cây, kết quả... Đồng dạng bị đánh đi ra, liền cửa cũng không vào...”"),t("br"),t("br"),n._v("“Thành chủ đều bị đánh ra? Người này thực lực rất mạnh?” Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Có thể trở thành Tĩnh Viễn thành thành chủ, thực lực đoán chừng cũng đạt tới Thánh vực nhất trọng, mạnh như thế người, bị trực tiếp đánh ra... Chỉ sợ vị này quái tẩu ông thực lực, cũng không có trong tưởng tượng đơn giản."),t("br"),t("br"),n._v("“Thực lực của hắn giống như ta, chỉ có Thánh vực nhất trọng trung kỳ, nhưng mà... Có một đầu Thánh vực nhị trọng thú sủng...”"),t("br"),t("br"),n._v("Hồ Vân Sinh nói."),t("br"),t("br"),n._v("“Hắn là thuần thú sư?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Nhờ vào Thánh vực nhất trọng thực lực, thuần phục nhị trọng thú sủng, thuần thú cấp bậc chỉ sợ đã đạt tới thất tinh."),t("br"),t("br"),n._v("Loại năng lực này cường giả, liền danh sư học viện đều không có, đế quốc một cái xa xôi tiểu thành, có nhân vật như vậy?"),t("br"),t("br"),n._v("“Ta không biết, nhưng đầu này thú sủng đối hắn nói gì nghe nấy, chỉ cần có người dám làm loạn, liền sẽ bị trực tiếp ném ra, liền xem như ta, cũng không dám tùy ý làm loạn...”"),t("br"),t("br"),n._v("Hồ Vân Sinh nói."),t("br"),t("br"),n._v("Lại trò chuyện vài câu, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Thông qua vị này Hiên chủ lời nói, có thể biết cái này quái tẩu ông, quả thực rất quái lạ, ưa thích sưu tập đủ loại bảo vật, trân quý dược liệu cũng nuôi không biết bao nhiêu."),t("br"),t("br"),n._v("Dựa theo bình thường đạo lý, chơi những vật này, cơ bản đều là treo giá, kỳ quái là, cái tên này theo không bán ra, thậm chí liền cửa còn không thể nào vào được."),t("br"),t("br"),n._v("“Mang ta xem một chút!” Mặc kệ cái tên này có bao nhiêu kỳ quái, hắn khẳng định là phải nghĩ biện pháp thu hút độc sư tới, nhờ vào đó tìm tới Độc điện."),t("br"),t("br"),n._v("Phi hành mười ngày, Ngụy Như Yên vẫn như cũ không tỉnh lại nữa, nói rõ độc thể càng ngày càng nghiêm trọng, lại không cứu chữa có thể sẽ thương tới sinh mệnh, bất kể theo cái nào phương diện tới nói cũng không thể để hắn từ chối, cũng không cách nào từ chối."),t("br"),t("br"),n._v("“Tìm hắn?” Hồ Vân Sinh vẻ mặt phát khổ: “Ta sợ không chỉ bị sập cửa vào mặt, còn có thể bị vứt ra...”"),t("br"),t("br"),n._v("Trước mắt vị này vừa tới Tĩnh Viễn thành, không biết vị kia quái tẩu ông đáng sợ, hắn nhưng là tự mình trải qua, ngay cả thành chủ đều không thể làm gì, huống chi là bọn họ."),t("br"),t("br"),n._v("Hắn liền đã từng bái phỏng qua mấy lần, kết quả đều không ngoại lệ, đều bị ném đi đi ra, mỗi lần chịu không ít khổ, bị thương không nhẹ."),t("br"),t("br"),n._v("“Không sao cả!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thân phận của hắn bây giờ, là một vị độc sư, mà không phải danh sư, không cần tuân thủ quy củ nhiều như vậy."),t("br"),t("br"),n._v("Chỉ cần nhìn, nhất định có thể nghĩ đến phương pháp."),t("br"),t("br"),n._v("“Tốt a!” Nhìn bộ dáng của đối phương, biết không cách nào khuyên can, tính mạng của hắn lại nắm tại người ta trong tay, Hồ Vân Sinh đành phải gật đầu bất đắc dĩ."),t("br"),t("br"),n._v("http://truy"),t("br"),n._v("encuatui．net/ Hai người mới vừa ra khỏi phòng, chỉ thấy Chu Khiếu tới đón, nhìn thấy bản thân Hiên chủ, không những không có giết người trung niên này, ngược lại theo ở phía sau nhắm mắt theo đuôi, cùng cái hạ nhân, Chu Khiếu mặt mũi khó tin."),t("br"),t("br"),n._v("Bất quá, Hồ Vân Sinh cũng không có cách nào giải thích, hơi đỏ mặt, khai báo Chu Khiếu đám người xem trọng Mặc Vân hiên, bản thân thì ở phía trước dẫn đường, bước nhanh ra ngoài đi đến."),t("br"),t("br"),n._v("Nếu như không trúng độc, cũng có thể ra hiệu ngầm thuộc hạ, tìm thêm một số người đến, mai phục tại chung quanh, đối vị này Tôn Cường tiến hành đánh lén, nhưng chỉ cần độc không giải, cho mười cái lá gan cũng không dám làm như thế, nếu không, khả năng còn không có động thủ, bản thân trước hết chết rồi."),t("br"),t("br"),n._v("Hơn nữa, vô cùng có khả năng thuộc hạ đều bị độc chết, đối phương không có việc gì."),t("br"),t("br"),n._v("Độc sư nếu là dễ dàng như vậy chém giết, đoán chừng Độc điện đã sớm không tồn tại."),t("br"),t("br"),n._v("Quái tẩu ông chỗ ở, khoảng cách Mặc Vân hiên không xa, đi một hồi, liền thấy một cái không đại viện tử xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cửa viện đóng kín, phía trên mang theo đóng cửa từ chối tiếp khách bảng hiệu, chung quanh vắng ngắt, liền tên hộ vệ đều không có, mảy may nhìn không ra, bên trong lại một vị cường giả Thánh vực."),t("br"),t("br"),n._v("“Gõ cửa đi!”"),t("br"),t("br"),n._v("Đi tới trước mặt, Trương Huyền phân phó."),t("br"),t("br"),n._v("“Cái này... Cứ như vậy trực tiếp gõ cửa? Không cần chuẩn bị bái thiếp?” Hồ Vân Sinh sững sờ."),t("br"),t("br"),n._v("Bình thường thăm hỏi đều cần trao tay bái thiếp, nói rõ thân phận của mình cùng lý do... Cái gì đều không chuẩn bị liền gõ cửa, chẳng lẽ cứ như vậy đi vào?"),t("br"),t("br"),n._v("Đối phương vốn là kỳ quái, bất kỳ cái gì cấp bậc lễ nghĩa đều không có, làm sao có thể cho sắc mặt tốt?"),t("br"),t("br"),n._v("“Gõ là được!”"),t("br"),t("br"),n._v("Cũng không trả lời, Trương Huyền cười cười."),t("br"),t("br"),n._v("Bất kể kỳ quái vẫn là không trách, chỉ cần là người, đều có ham muốn cùng theo đuổi, có những thứ này... Hắn liền có đầy đủ tự tin, để hắn ngoan ngoãn bán thuốc."),t("br"),t("br"),n._v("“Tốt a!” Thấy đối phương không nghe khuyến cáo, Hồ Vân Sinh lắc đầu, đi lên phía trước, đem cửa gõ vang."),t("br"),t("br"),n._v("Đông đông đông đông!"),t("br"),t("br"),n._v("Thanh âm tại yên tĩnh đường hầm đặc biệt lanh lảnh, vang vọng bốn phía."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Quái tẩu ông cửa sân không có mở, đối diện một cái sân nhưng từ từ mở ra, lập tức nhìn thấy một cái lão giả cùng người hầu, đi ra."),t("br"),t("br"),n._v("“Không cần gõ, người ở bên trong là không gặp qua tới thấy các ngươi!”"),t("br"),t("br"),n._v("Nhìn hai người một cái, lão giả nói."),t("br"),t("br"),n._v("“Không thấy?” Trương Huyền cau mày: “Ngươi là quái tẩu ông hạ nhân?”"),t("br"),t("br"),n._v("“Dĩ nhiên không phải, ta chỉ là tiền bối một cái người ngưỡng mộ...” Lão giả xua tay."),t("br"),t("br"),n._v("“Cái kia tiếp tục gõ cửa đi!”"),t("br"),t("br"),n._v("Nghe đối phương cùng quái tẩu ông không quan hệ, Trương Huyền quay đầu đi, để Hồ Vân Sinh tiếp tục."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy cái tên này, đối khuyến cáo của mình không nhúc nhích, lão giả nhướng mày."),t("br"),t("br"),n._v("Người hầu càng là sầm mặt lại, đi lên phía trước, ánh mắt lấp lánh nhìn qua: “Dám cùng nhà chúng ta chủ nhân nói như vậy, ngươi biết hắn là ai ư?”"),t("br"),t("br"),n._v("Liếc đối phương một cái, Trương Huyền không thèm để ý."),t("br"),n._v(" Chẳng cần biết ngươi là ai, ngươi biết ta là ai không?"),t("br"),t("br"),n._v("Nói ra hù chết ngươi..."),t("br"),t("br"),n._v("Đông đông đông!"),t("br"),t("br"),n._v("Lần nữa đem cửa gõ vang."),t("br"),t("br"),n._v("Không nghĩ tới bị coi thường, người hầu khí khuôn mặt khó coi, tiến về phía trước một bước: “Nhà chúng ta chủ nhân là Y Sư công hội hội trưởng, Chu Tuyên đại sư!”"),t("br"),t("br"),n._v("“Chu Tuyên?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Không biết! Tiếp tục gõ đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hồ Vân Sinh khóe miệng giật một cái."),t("br"),t("br"),n._v("Vị này Chu Tuyên cùng hắn cùng xuất hiện không nhiều, chợt nhìn đến chỉ cảm thấy quen mặt, không nhớ ra được, lúc này nghe được giới thiệu, cũng nghĩ tới."),t("br"),t("br"),n._v("Y Sư công hội hội trưởng... Coi như thành chủ gặp được đều lấy lễ đãi chi, không dám có chút khinh thường, cái tên này ngược lại tốt, không thèm để ý, không nhìn thẳng... Suy nghĩ một chút đều để hắn làm chuyện điên rồ."),t("br"),t("br"),n._v("Bất quá, chịu người chế trụ, cũng hết cách rồi, đành phải giả bộ như không biết, tiếp tục gõ."),t("br"),t("br"),n._v("Thấy cái tên này bộ dáng, bất kể người hầu vẫn là lão giả Chu Tuyên, đều có chút điên rồi."),t("br"),t("br"),n._v("Tĩnh Viễn thành Y Sư công hội hội trưởng, địa vị so với thành chủ, đều không kém chút nào... Chỉ cần là người, ai không có có thụ thương cùng sinh bệnh thời điểm?"),t("br"),t("br"),n._v("Bởi vậy, cái khác công hội hội trưởng dám đắc tội, hắn tuyệt đối không ai dám tìm phiền toái."),t("br"),t("br"),n._v("Người này ngược lại tốt, trực tiếp tới cái không để ý tới, xem như không khí..."),t("br"),t("br"),n._v("“Ngươi có nghe hay không đến ta nói chuyện? Nhà chúng ta chủ nhân chính là lục tinh y sư!” Người hầu cắn răng."),t("br"),t("br"),n._v("“Nghe được a, ta muốn tìm chính là quái tẩu ông, cũng không phải hắn!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lục tinh y sư mà thôi, ba tháng trước, một cái lục tinh đỉnh phong phó viện trưởng, tìm hắn để gây sự, đều suýt chút nữa bị hắn giết chết, huống chi vị này."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Người hầu khí không có ngất đi."),t("br"),t("br"),n._v("Lục tinh y sư, đi đến nơi nào đều được người kính ngưỡng tôn sùng siêu cấp tồn tại, cái tên này thế mà... Không thèm quan tâm?"),t("br"),t("br"),n._v("Đáng ghét!"),t("br"),t("br"),n._v("Chẳng lẽ không biết quan hệ thân thiết một vị y sư đem sẽ được cái gì chỗ tốt, đắc tội một tên y sư, sẽ trả giá là như thế nào đánh đổi ư?"),t("br"),t("br"),n._v("Còn muốn nói gì, Chu Tuyên đại sư đưa tay ngăn lại, đi lên phía trước: “Bằng hữu, ta không biết ngươi là ai, không quan tâm thân phận của ta cũng không tính là gì, có điều, quái tẩu ông tiền bối, không muốn gặp người, coi như lại đập xuống, cũng là vô dụng!”"),t("br"),t("br"),n._v("Chu Tuyên đại sư thân là Y Sư công hội hội trưởng, đã gặp không ít người, trước mắt vị này biết thân phận của hắn về sau, như vậy bình tĩnh, chỉ bằng vào điểm này, liền biết chắc không đơn giản."),t("br"),t("br"),n._v("Bởi vậy, hắn sẽ không giống người hầu đồng dạng, tự tin thân phận lớn tiếng trách mắng, mà là dùng an ủi giọng nói."),t("br"),t("br"),n._v("“Không muốn gặp người?”"),t("br"),t("br"),n._v("“Vâng, tiền bối tính khí quái dị, coi như lúc trước thành chủ thăm hỏi, cũng là đưa nhiều lần danh thiếp, ta khuyên ngươi trước chuẩn bị danh thiếp, tìm người hỗ trợ đưa qua, nếu không, ở đây gõ cửa, chỉ sẽ khiến phản cảm, được không bù mất...”"),t("br"),t("br"),n._v("Chu Tuyên đại sư nói tiếp."),t("br"),t("br"),n._v("Hắn thực sự nói thật."),t("br"),t("br"),n._v("Quái tẩu ông tính khí thực sự quá quái dị, liền xem như hắn, ở tại đối diện ngày ngày tới bái kiến, đối phương cũng không thấy, ngươi một người xa lạ, cái gì đều không chuẩn bị, liền chạy tới gõ cửa, người ta không phải càng không thấy ư?"),t("br"),t("br"),n._v("“Là như thế này ah!” Trương Huyền cau mày, bất đắc dĩ lắc đầu: “Đã như vậy...”"),t("br"),t("br"),n._v("Nghe nói như thế, Chu Tuyên tưởng là đối phương sẽ biết khó mà lui, xoay người rời đi, chuẩn bị kỹ càng bái thiếp lại đến, kết quả, nghe đến phía dưới, suýt chút nữa không có một cái lảo đảo té ngã trên đất."),t("br"),t("br"),n._v("“... Hồ Vân Sinh, đem cửa đập phá đi!”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền giống như là làm kiện đơn giản đến cực điểm sự tình, râu ria."),t("br"),t("br"),n._v("“Phá cửa!” Khóe miệng giật một cái, Hồ Vân Sinh cũng thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhàn nhạt nhìn qua: “Nếu không ta tới đi!”"),t("br"),t("br"),n._v("Biết vị này Hồ Vân Sinh, chưa hẳn dám nghe từ nơi này ly kinh phản đạo mệnh lệnh, chẳng muốn nói nhảm, Trương Huyền tiến về phía trước một bước, đi tới cửa lớn trước mặt, bàn tay theo ở phía trên, lực lượng đột nhiên ngưng tụ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trước mắt cửa lớn chỉ là gỗ, làm sao có thể chống đỡ được lực lượng của hắn, chỉ một chút liền chia năm xẻ bảy, bay ngược đi vào trong nội viện."),t("br"),t("br"),n._v("Thanh âm nổ vang, bụi đất tung bay."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn nói làm liền làm, căn bản không phải trò đùa, Chu Tuyên thân thể run lên, suýt chút nữa không có ngất đi tại chỗ."),t("br"),t("br"),n._v("Ta đều nói cho ngươi không nên quấy rầy... Kết quả lại đem cửa đập phá..."),t("br"),t("br"),n._v("Khí hai mắt biến thành màu đen, hô hấp dồn dập, đều nhanh không thở nổi."),t("br"),t("br"),n._v("Cái này là từ đâu xuất hiện lăng đầu thanh (*thanh niên sức trâu)?"),t("br"),t("br"),n._v("Bắt đầu hắn cũng không quan tâm cái này quái tẩu ông, cảm giác đối phương cố làm ra vẻ huyền bí, bản thân đường đường Y Sư công hội hội trưởng tự mình thăm hỏi, nên tự mình nghênh đón, không kênh kiệu, sau tới bái kiến đối phương chữa bệnh, giờ mới hiểu được đối phương y thuật mạnh mẽ hơn hắn quá nhiều, nói cách khác... Vị này quái tẩu ông có thể là lục tinh đỉnh phong thậm chí... Thất tinh cấp bậc y sư!"),t("br"),t("br"),n._v("Chính vì vậy, chuyên môn đem đối diện phòng ở thuê đi, ngày ngày bái yết, mục đích đúng là hi vọng có một ngày có thể thu được lọt mắt xanh, truyền thụ cái một tay nửa tay."),t("br"),t("br"),n._v("Cũng đang bởi vì cái này duyên cớ, nghe được có người gõ cửa, chạy đến khuyên can, tốt cho đối phương lưu cái ấn tượng tốt."),t("br"),t("br"),n._v("Thế nhưng là... Khuyên can là khuyên can, nên nói cũng nói, cái tên này làm sao như thế chày gỗ? Để cho người ta một quyền đem cửa làm hỏng?"),t("br"),t("br"),n._v("Cái này nhưng tốt như vậy?"),t("br"),t("br"),n._v("Quái tẩu ông tiền bối, khẳng định sẽ nổi giận!"),t("br"),t("br"),n._v("“Cái này chẳng phải giải quyết, đi vào đi!”"),t("br"),t("br"),n._v("Không để ý tới sắp làm chuyện điên rồ Chu Tuyên, Trương Huyền khoát tay chặn lại, đi vào trước."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-989"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-991"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);