(window.webpackJsonp=window.webpackJsonp||[]).push([[2288],{2643:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-982-thanh-tien-đoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-982-thanh-tien-đoc"}},[n._v("#")]),n._v(" Chương 982: Thanh Tiên độc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 982: Thanh Tiên độc"),t("br"),t("br"),n._v("Sân nhỏ không lớn, nhưng linh khí kinh người, tùy tiện nhìn một cái, Trương Huyền liền biết trong đó bố trí một cái Tụ Linh trận, hơn nữa không thấp, không dưới cấp sáu đỉnh phong."),t("br"),t("br"),n._v("Tụ Linh trận hội tụ xuống, nơi này linh khí so ngoại giới nếu dày đặc gấp bội, cả viện lít nha lít nhít trồng một chút dược liệu, hầu như đều là trên thị trường không thấy được trân quý vật phẩm."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Tùy tiện nhìn lướt qua, Trương Huyền lập tức nhìn thấy ít nhất mười cây đạt tới Thánh vực cấp bậc, con mắt nhịn không được sáng lên."),t("br"),t("br"),n._v("Lúc trước Vưu Hư, đều không có có nhiều như vậy."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Đang tại khắp nơi quan sát, cửa phòng mở ra, một cái thoạt nhìn lôi thôi lếch thếch lão giả đi ra, nhìn về phía Trương Huyền đám người, gầm lên giận dữ."),t("br"),t("br"),n._v("“Ai cho các ngươi đi vào? Thật to gan!”"),t("br"),t("br"),n._v("Hỏa khí ngút trời."),t("br"),t("br"),n._v("Đổi lại ai cũng tức giận, trong nhà cố gắng, người ta trực tiếp phá cửa xông tới..."),t("br"),t("br"),n._v("Có thể hay không phách lối nữa một chút?"),t("br"),t("br"),n._v("“Tiền bối, là hắn cố xông vào, ta cũng không nghĩ tới...”"),t("br"),t("br"),n._v("Nhìn thấy quái tẩu ông tức giận, Chu Tuyên nhịn không được run rẩy, liền vội vàng tiến lên giải thích."),t("br"),t("br"),n._v("Duy trì thời gian dài như vậy tốt hình ảnh, cũng không muốn bởi vậy tống táng."),t("br"),t("br"),n._v("“Hổ Thanh thú, tiễn khách!”"),t("br"),t("br"),n._v("Không để ý tới hắn, quái tẩu ông một tiếng a nói."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Nương theo lời nói, một cái dài hơn năm thước cực lớn Thánh thú, từ một bên gian phòng đi tới, mỗi một bước mặt đất đều phát ra kịch liệt rung động."),t("br"),t("br"),n._v("Hổ Thanh thú, Thánh vực nhị trọng sơ kỳ Thánh thú, sức chiến đấu so với Tử Dương thú, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("Sóng khí cùng cảm giác áp bách mạnh mẽ, để sắc mặt người trắng bệch, thân thể không tự chủ được phát run."),t("br"),t("br"),n._v("Nhất là Hồ Vân Sinh, suýt chút nữa không có nhả ra máu, lúc trước hắn tới qua mấy lần, đều bị đầu này đại gia hỏa đánh hoàn toàn thay đổi, lần nữa gặp được, bản năng theo ở sâu trong nội tâm cảm thấy run rẩy."),t("br"),t("br"),n._v("Chu Tuyên cũng kìm lòng không được lùi về phía sau mấy bước, loại cấp bậc này Thánh thú căn bản cũng không phải là thực lực của hắn bây giờ có thể chống lại."),t("br"),t("br"),n._v("“Bản thân cút ra ngoài, vẫn là ta đưa ngươi đánh thành rác rưởi, ném đi?”"),t("br"),t("br"),n._v("Hổ Thanh thú là đèn lồng con mắt nhìn chằm chằm Trương Huyền."),t("br"),t("br"),n._v("“Hai cái đều không chọn đâu?” Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Cái kia tới nói nhảm nhiều như vậy, đem hắn ném đi.” Khoát tay áo, quái tẩu ông xoay người tiếp tục đi về phòng."),t("br"),t("br"),n._v("Thú sủng tự mình ra tay, hắn hết sức yên tâm, căn bản không cần suy nghĩ nhiều."),t("br"),t("br"),n._v("Mới xoay người sang chỗ khác, liền nghe đến phía sau Hổ Thanh thú một tiếng tức giận rầm, ngay sau đó mạnh mẽ sóng khí trùng kích vào, mặt đất không ngừng rung rung."),t("br"),t("br"),n._v("Lập tức đồ vật gì, bay lên không trung."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nặng nề mà đập xuống đất, tóe lên đầy sân bụi bặm."),t("br"),t("br"),n._v("“Không biết tốt xấu!”"),t("br"),t("br"),n._v("Quái tẩu ông thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Hổ Thanh thú từ trước tới nay hung mãnh, nghe được những âm thanh này liền biết, khẳng định là đem cái kia không biết trời cao đất rộng gia hỏa điên cuồng đánh một trận."),t("br"),t("br"),n._v("“Quên đi, giáo huấn một lần là được, không nên giết... A?”"),t("br"),t("br"),n._v("Khoát tay áo, quái tẩu ông xoay người lại."),t("br"),t("br"),n._v("Mặc dù không quan tâm đối phương thân phận chân chính, nhưng mà dám trực tiếp xông cửa đi vào, khẳng định vị không thấp, hắn chỉ là ham thanh tĩnh, không muốn đem chuyện dẫn đến lớn hơn."),t("br"),t("br"),n._v("Lời còn chưa nói hết, liền thấy cảnh tượng trước mắt, thân thể cứng đờ, quái tẩu ông nhịn không được ngây người tại chỗ."),t("br"),t("br"),n._v("“Cái này, đây là có chuyện gì?”"),t("br"),t("br"),n._v("Qua thật lâu, mới nói ra được, cả người tựa hồ cũng có chút choáng váng."),t("br"),t("br"),n._v("Chỉ thấy phía trước tình huống cùng tưởng tượng hoàn toàn không giống, vốn nên dạy dạy bảo người khác Hổ Thanh thú, lúc này đang nằm ở đối diện người trung niên kia trước mặt, đầu lưỡi liếm láp bàn tay của đối phương, ánh mắt tràn đầy nịnh nọt, một bộ hiển nhiên chó xù hình ảnh."),t("br"),t("br"),n._v("Cái này là thế nào cái tình huống?"),t("br"),t("br"),n._v("Ta bảo ngươi đem người ném đi, không phải bảo ngươi ở đây thấp kém lấy lòng."),t("br"),t("br"),n._v("Làm sao cảm giác ngươi không phải ta thú sủng, mà là đối phương?"),t("br"),t("br"),n._v("Trong lòng không giải, vội vàng hướng cách đó không xa Chu Tuyên đám người nhìn lại, thiếu thấy ba người này cũng choáng váng đồng dạng đứng tại chỗ, gặp quỷ đồng dạng."),t("br"),t("br"),n._v("“Hổ Thanh thú, ngươi đang làm gì?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, lão giả lớn tiếng trách mắng."),t("br"),t("br"),n._v("Cái này kêu chuyện gì, chính hắn thú sủng, nhận được mệnh lệnh đi dạy bảo đối phương, kết quả nhưng nằm sấp trước mặt đối phương, một bộ mất mặt xấu hổ bộ dáng, để cho mình mặt mũi để vào đâu?"),t("br"),t("br"),n._v("Có phần quá giật đi!"),t("br"),t("br"),n._v("“Chủ nhân, ta...” Hổ Thanh thú xoay đầu lại, cực lớn móng vuốt gãi gãi đầu, không biết nên trả lời như thế nào."),t("br"),t("br"),n._v("“Được rồi, ta tới đi!” Mỉm cười, Trương Huyền đi về phía trước mấy bước, tới đến lão giả trước mặt."),t("br"),t("br"),n._v("Hắn hiện tại mặc dù là Tòng Thánh đỉnh phong, nhưng là thực lực chân chính, liền Thánh vực nhị trọng Chiến sư Trác Thanh Phong cũng không là đối thủ, Hổ Thanh thú tuy mạnh, cũng không phải địch."),t("br"),n._v("Một quyền đánh bay lên trời, sau một khắc, liền ngoan ngoãn nghe theo phân phó, mặc dù đối phương có chủ, không có thuần phục, nhưng đã cấu bất thành uy hiếp."),t("br"),t("br"),n._v("“Ngươi hẳn là quái tẩu ông tiền bối đi!” Trương Huyền nhìn lại: “Tới đây cái này có chuyện xảy ra muốn nhờ, muốn cùng ngươi mua sắm một gốc Địa Tàng hoa, niên đại càng lâu càng tốt.”"),t("br"),t("br"),n._v("“Có chuyện xảy ra muốn nhờ?” Nhìn một cái nằm thẳng tại cách đó không xa, chia năm xẻ bảy cửa lớn cùng một mặt nịnh nọt thú sủng, quái tẩu ông khóe miệng giật một cái."),t("br"),t("br"),n._v("Ngươi đây là cầu người cái kia có cử động ư?"),t("br"),t("br"),n._v("Vừa đến liền đem đại môn của ta phá hủy không nói, còn đem thú sủng cũng biến thành như vậy... Ngươi đây không phải đi cầu ta, mà là để cho ta tới cầu ngươi a..."),t("br"),t("br"),n._v("“Muốn mua dược liệu, trừ phi theo ta trên thi thể bước qua đi!” Răng cắn chặt quái tẩu ông khí đến sắc mặt đỏ lên."),t("br"),t("br"),n._v("“Theo ngươi trên thi thể bước qua đi?” Trương Huyền lắc đầu, trên mặt có chút bất đắc dĩ: “Đã ngươi nói như vậy, chỉ có thể không không có ý tứ!”"),t("br"),t("br"),n._v("Nói xong lông mày giương lên, trên người một cỗ khí thế cường đại phun ra ngoài, ngay sau đó thân thể nhoáng một cái, năm ngón tay mở ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Quái tẩu ông chỉ cảm thấy đối diện chân khí, như là như vòi rồng, tuôn ra mà tới, mặc dù cấp bậc còn không bằng hắn, nhưng mà không luận lực lượng vẫn là độ tinh thuần, đều xa hoàn toàn không phải hắn có thể so sánh."),t("br"),t("br"),n._v("Có thể đoán được, chỉ cần bàn tay của đối phương hạ xuống, vô cùng có khả năng bị tại chỗ đập thành thịt nát."),t("br"),t("br"),n._v("“Đáng ghét!” Vẻ mặt trở nên xanh xám, bàn chân trên mặt đất đạp mạnh, quái tẩu ông vội vàng về phía sau nhảy xuống, lần này thi triển ra lực lượng toàn thân, thân ảnh nhanh như thiểm điện."),t("br"),t("br"),n._v("Thực lực của hắn mặc dù chỉ là Thánh vực nhất trọng, cái này tránh né thân pháp lại hết sức cao minh, trong nháy mắt liền thoát ly bàn tay phạm vi bao phủ."),t("br"),t("br"),n._v("Sợ hãi công kích của đối phương tiếp tục truy kích mà đến, nhảy ra vòng chiến, quái tẩu ông không dám ngừng lại, lại liên tục về phía sau nhảy hai lần, không sai biệt lắm sống lưng nhanh đụng vào phòng ốc, cái này mới dừng lại."),t("br"),t("br"),n._v("“Ách?”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, lúc này mới phát hiện đối phương vẫn đứng tại chỗ, không có tiếp tục truy kích ý tứ, hai tay chắp sau lưng, đầu hơi hơi nâng lên, gió nhẹ phật đến, tay áo phiêu nâng, tựa như một cái không thể với cao thế ngoại cao nhân."),t("br"),t("br"),n._v("Da mặt co lại."),t("br"),t("br"),n._v("Biến hóa cũng quá nhanh đi, vừa rồi một chưởng vỗ đến, dường như hận không thể đem hắn đưa vào chỗ chết, mà bây giờ trong nháy mắt, liền một bộ mọc lên mà không nhơ, trơ trọi trên nước lăn tăn mà không ẻo lả yêu siêu cấp cường giả bộ dáng... Có muốn hay không như thế lớn?"),t("br"),t("br"),n._v("Không riêng gì hắn một bên Chu Tuyên cùng Hồ Vân Sinh, cũng đều hai mặt nhìn nhau, một mặt mộng bức."),t("br"),t("br"),n._v("Kẻ trước mắt này, thực lực cường đại cũng được, trở mặt tốc độ, dường như càng thêm cường đại, để cho người ta căn bản xem không hiểu đến cùng đang suy nghĩ gì, lại muốn làm gì."),t("br"),t("br"),n._v("“Muốn giết cứ giết, muốn trêu đùa ta, không có cửa đâu!” Quái tẩu ông sắc mặt tái xanh."),t("br"),t("br"),n._v("Cảm giác khác cảm giác đối phương giống như là đang cố ý trêu đùa hắn, rõ ràng cầm giữ có thể tùy ý đem hắn đánh chết thực lực, nhưng không động thủ."),t("br"),t("br"),n._v("“Trêu đùa ngươi? Ngươi suy nghĩ nhiều!” Trương Huyền lắc đầu, một mặt đồng tình nhìn qua: “Ta chỉ là muốn hỏi một câu ngươi,  xâm nhập xuống, còn có thể kiên trì nhiều năm như vậy, không rất dễ dàng đi!”"),t("br"),t("br"),n._v("“A? Ngươi... Ngươi...”"),t("br"),t("br"),n._v("Bản muốn nổi giận quái tẩu ông, nghe nói như thế, liên tục lui về sau hai bước, miệng một chút mở ra, trong mắt tràn đầy khó tin."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, hắn đích xác trúng Thanh Tiên độc, sau khi trúng độc, nhờ vào y thuật tinh xảo, một mực kéo dài hơi tàn... Dù vậy, cũng nhanh sắp không kiên trì được nữa, chỉ là... Chuyện này ai cũng chưa nói qua, càng là chưa bao giờ thấy qua trước mắt vị này, làm sao có thể chỉ gặp lần đầu tiên, liền một cái nói ra?"),t("br"),t("br"),n._v("Có chút bối rối."),t("br"),t("br"),n._v("“Ha ha, Thanh Tiên độc, dùng Thánh vực tứ trọng Thanh Tiên xà xà tiên vì tài liệu chính luyện chế mà thành, Thánh vực tứ trọng cường giả, đều có thể dễ dàng hạ độc chết. Ngươi sở dĩ đều có thể sống sót, không nhìn lầm, hẳn là ấu niên thời điểm, dùng qua một gốc Thất Hoa thảo đi!”"),t("br"),t("br"),n._v("Còn chưa kịp hỏi thăm, liền nghe đến đối diện gia hỏa, thanh âm nhàn nhạt tiếp tục vang lên."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Quái tẩu ông chấn động toàn thân."),t("br"),t("br"),n._v("Đối phương nói không sai, hắn bảy tuổi thời điểm, từng lầm dùng một gốc Thất Hoa thảo."),t("br"),t("br"),n._v("Thứ này thuộc về mãn tính dược vật, mặc dù không có bị tại chỗ hạ độc chết, nhưng cũng bị hại nặng nề, nguyên nhân chính là như vậy, mới cố gắng học tập y thuật, muốn giải quyết triệt để."),t("br"),t("br"),n._v("Chuyện này, đồng dạng rất ít người biết, trước mắt vị này, một cái nói ra, chẳng lẽ lúc trước lão bằng hữu?"),t("br"),t("br"),n._v("Nhìn kỹ một cái, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Bất kể dung mạo vẫn là khí tức, đều lúc trước chưa từng thấy qua."),t("br"),t("br"),n._v("Hắn có thể xác nhận, trước đó tuyệt đối chưa thấy qua vị này."),t("br"),t("br"),n._v("“Thật ra thì muốn xem ra điểm ấy, cũng không phức tạp, dùng Thanh Tiên độc về sau, không chỉ thân thể phát xanh, con mắt cũng sẽ phát sinh biến hóa, màu nâu xanh bên trong mang theo huyết hồng sắc dây nhỏ, từ xa nhìn lại, như là rắn con mắt.”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Đương nhiên, chỉ bằng vào điểm ấy chỉ có thể phỏng đoán, ngươi là có hay không bị Thanh Tiên xà cắn, chân chính xác nhận, là bởi vì, ngón tay của ngươi trở nên trắng, không có màu nâu xanh, nếu như không nhìn lầm... Năm đó sử dụng cái tay này hái Thất Hoa thảo, hơn nữa bị hắn đâm hư đi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Quái tẩu ông lần nữa run lên."),t("br"),t("br"),n._v("Khi đó tuổi tác quá nhỏ, rất nhiều chuyện đều không nhớ được, nhưng đối phương nói chuyện, nghĩ tới, cái này ngón tay, quả thực từng bị Thất Hoa thảo đâm thủng qua."),t("br"),t("br"),n._v("“Thất Hoa thảo mặt ngoài có gai, ẩn chứa trong đó độc tính, nhưng cũng đối Thanh Tiên xà độc rắn, có tác dụng khắc chế, cả hai dung hợp, màu xanh biến trắng, có thể duy trì sinh mệnh của ngươi sẽ không tạm thời tử vong, lại sẽ dằn vặt ngươi thống khổ không chịu nổi... Ngươi nên mỗi đến ban đêm giờ Tý, đều sẽ toàn thân sưng vù, khung xương ngứa a?”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Quái tẩu ông thân thể cứng ngắc."),t("br"),t("br"),n._v("“Được rồi, hiện tại có thể tiến phòng ngươi, cùng ngươi thương nghị mua sắm Địa Tàng hoa chuyện ư?” Thấy nét mặt của hắn, Trương Huyền biết mục đích của mình đã đạt tới, lần nữa cười một tiếng."),t("br"),t("br"),n._v("“Nhanh mời vào bên trong, mau mời!”"),t("br"),t("br"),n._v("Quái tẩu ông liền vội vàng khom người."),t("br"),t("br"),n._v("“Tiền bối...” Đem hai người đối thoại nghe vào trong tai, Chu Tuyên mặt mũi choáng váng, nhịn không được hướng về phía trước, muốn hỏi cho ra nhẽ."),t("br"),t("br"),n._v("“Còn không mau một chút bưng trà rót nước, lăng ở đây làm gì?”"),t("br"),t("br"),n._v("Quái tẩu ông một tiếng trách mắng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-990"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-992"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);