(window.webpackJsonp=window.webpackJsonp||[]).push([[2290],{2644:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-984-thanh-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-984-thanh-chu"}},[n._v("#")]),n._v(" Chương 984: Thành chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 984: Thành chủ"),t("br"),t("br"),n._v("“Thế nào?” Nhìn hắn vội vã bộ dạng, Trương Huyền lông mày nhíu lên."),t("br"),t("br"),n._v("Vị này là Mặc Vân hiên hiên Hiên chủ, Thánh vực nhất trọng cường giả, Tĩnh Viễn thành đều nổi danh một phương đại quan, hiện tại lo lắng như thế, khẳng định là xảy ra chuyện gì."),t("br"),t("br"),n._v("“Ta vừa đến liền đem Địa Tàng hoa bán đấu giá tin tức truyền ra ngoài... Nửa canh giờ trước, tới một nhóm người, nhất định muốn tại đấu giá hội trước đó đem hắn mua đi.”"),t("br"),t("br"),n._v("Vẻ mặt khó coi, Hồ Vân Sinh giải thích nói: “Nhìn bộ dáng của bọn hắn, rất có ép mua ép bán tư thế.”"),t("br"),t("br"),n._v("“Ép mua ép bán? Chẳng lẽ là độc sư?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Địa Tàng hoa không phải đối độc sư có cực lớn lực hấp dẫn ư? Chẳng lẽ những người khác cũng cần?"),t("br"),t("br"),n._v("“Không phải...” Hồ Vân Sinh lắc đầu."),t("br"),t("br"),n._v("“Ngươi làm sao xác nhận?” Nghi ngờ nhìn lại, Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Độc sư không dùng độc thời điểm, không phải không cách nào nhận ra sao?"),t("br"),t("br"),n._v("Làm sao như vậy chắc chắn như thế?"),t("br"),t("br"),n._v("“Bởi vì... Tới người kia ta biết!”"),t("br"),t("br"),n._v("Hồ Vân Sinh hơi đỏ mặt: “Là Tĩnh Viễn thành Hồng Kim thành chủ!”"),t("br"),t("br"),n._v("“Hắn muốn Địa Tàng hoa làm gì?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Địa Tàng hoa là độc sư cần có, một vị thành chủ muốn cái này làm gì? Hơn nữa nghe ngữ khí, còn không để ý đến thân phận ý định ép mua."),t("br"),t("br"),n._v("“Tình huống cụ thể hắn không nói, ta cũng không rõ lắm, chỉ là hắn nhất định muốn mua, ta đã nói là tiền bối đồ vật, hiện tại không phải muốn gặp ngươi...” Hồ Vân Sinh nói."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc kệ đối phương muốn Địa Tàng hoa làm gì, đã chỉ đích danh nhường cho mình đi qua, tự nhiên muốn đi qua nhìn một chút."),t("br"),t("br"),n._v("Đi theo Hồ Vân Sinh sau lưng, đi thẳng về phía trước, thời gian không dài, đi tới một cái rộng lớn gian phòng."),t("br"),t("br"),n._v("Một cái khuôn mặt có phần mang uy nghiêm trung niên nhân, kim đao đại mã ngồi tại chủ vị, ánh mắt sáng ngời, mang theo cao cao tại thượng khí thế."),t("br"),t("br"),n._v("Phía sau hắn là mấy cái người mặc giáp trụ hộ vệ, nguyên một đám khí tức như rồng, cư nhưng đã nắm giữ Hóa Phàm cửu trọng Tằm Phong cảnh thực lực."),t("br"),t("br"),n._v("“Thành chủ đại nhân, vị này chính là Địa Tàng hoa chủ nhân, Tôn Cường tiền bối!”"),t("br"),t("br"),n._v("Đi tới trước mặt, Hồ Vân Sinh ôm quyền nói."),t("br"),t("br"),n._v("Chủ vị trung niên nhân nhìn Trương Huyền một cái, thấy chỉ là một vị Tòng Thánh, cười cười, nói: “Vị bằng hữu này, ta muốn mua sắm của ngươi Địa Tàng hoa, nói cái giá đi!”"),t("br"),t("br"),n._v("“Ngươi có thể cho giá bao nhiêu?” Thấy vị này như vậy trực tiếp, Trương Huyền có chút ngoài ý muốn, lông mày vừa nhấc, cười nhạt một tiếng."),t("br"),t("br"),n._v("“100 niên đại Địa Tàng hoa, bình thường cần 80 linh thạch thượng phẩm, ngươi cái này niên đại hơi lâu xa một chút, ta cho ngươi một trăm, cộng thêm một cái thượng phẩm Linh khí!”"),t("br"),t("br"),n._v("Trung niên nhân nhìn lại."),t("br"),t("br"),n._v("“Cái giá tiền này có chút thấp đi!” Mí mắt nâng lên, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Không nói trước Địa Tàng hoa thưa thớt vô cùng, coi như trước đó Hồ Vân Sinh bán ra, đều so cái này cao hơn. Đối phương ra dạng này giá, quả thực cũng là có chút ép mua ép bán."),t("br"),t("br"),n._v("“Láo xược! Chúng ta thành chủ có thể mua ngươi đồ vật là nể mặt ngươi, đừng cho thể diện mà không cần!”"),t("br"),t("br"),n._v("Trung niên nhân sau lưng một tên hộ vệ, lên tiếng hét lớn."),t("br"),t("br"),n._v("“Một trăm linh thạch thượng phẩm, cộng thêm một cái thượng phẩm Linh khí đã là cao nhất giá cả, đấu giá cũng chưa chắc có thể cầm đến, hơn nữa còn sẽ gặp phải càng nhiều người ngấp nghé, ta nhìn vẫn là bán đi đi!”"),t("br"),t("br"),n._v("Cái thứ hai hộ vệ nói."),t("br"),t("br"),n._v("Hai người một cái hát mặt đỏ, một cái hát mặt trắng, uy hiếp cộng thêm an ủi, thoạt nhìn hảo tâm, trên thực tế là để hắn ngoan ngoãn đem dược liệu bán đi."),t("br"),t("br"),n._v("Nhìn thấy đối phương thái độ, Trương Huyền cười lắc đầu, vẻ mặt thản nhiên: “Một vạn linh thạch thượng phẩm, ta liền bán cho ngươi!”"),t("br"),t("br"),n._v("“Ngươi nói cái gì?” Trung niên nhân lông mày nhíu một cái."),t("br"),t("br"),n._v("Hắn ra giá một trăm, đối phương trả giá một vạn, quả thực chính là không muốn nói, không có đem hắn thành chủ thân phận để vào mắt."),t("br"),t("br"),n._v("“Ngươi thật to gan...” Cái thứ nhất hộ vệ lần nữa hét lớn, tiếng nói còn không có kết thúc, liền nghe đến đối diện Trương Huyền thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Hai vạn!”"),t("br"),t("br"),n._v("“Ngươi có ý tứ gì? Đến cùng là bán vẫn là không bán?” Cái thứ hai hộ vệ nói tiếp."),t("br"),t("br"),n._v("“3 vạn!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ngươi...” Sắc mặt của người trung niên biến thành trắng bệch, lông mày nhàu cùng một chỗ, uốn lượn như dãy núi, dường như đè lên lửa giận, bất cứ lúc nào cũng sẽ bộc phát."),t("br"),t("br"),n._v("Đối phương loại thái độ này căn bản là không có ý định bán ra, hơn nữa nhìn bộ dáng không quan tâm thân phận của hắn là cái gì."),t("br"),t("br"),n._v("“Bằng hữu, nơi này là Tĩnh Viễn thành, chẳng cần biết ngươi là ai, ta khuyên ngươi vẫn là khiêm tốn một chút, miễn cho gặp được cái gì không thể tránh khỏi phiền phức.”"),t("br"),t("br"),n._v("Lạnh hừ một tiếng, trung niên nhân híp mắt lại."),t("br"),t("br"),n._v("“Năm vạn!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương uy hiếp, Trương Huyền khoát tay áo, tiếp tục nói."),t("br"),t("br"),n._v("Cùng hắn trang lão sói vẫy đuôi, hắn trang thời điểm còn không biết đối phương ở nơi nào chơi bùn đây!"),t("br"),t("br"),n._v("“Hừ, chúng ta đi!”"),t("br"),t("br"),n._v("Thấy cái này Địa Tàng hoa chủ nhân, khó chơi, mềm không được cứng không xong, trung niên nhân hừ lạnh một tiếng xoay người đi ra ngoài."),t("br"),t("br"),n._v("Mới vừa rời phòng, vẻ mặt lập tức trở nên xanh xám."),t("br"),t("br"),n._v("“Thành chủ, có muốn hay không chúng ta phái một số người đem đồ vật đoạt tới?”"),t("br"),t("br"),n._v("Trước đó cái thứ nhất hộ vệ, làm cái giết tay của người thế, ánh mắt lạnh lùng."),t("br"),t("br"),n._v("“Không cần, cái này đồ vật, ta tình thế bắt buộc, đã hắn đấu giá, liền theo bình thường đấu giá chương trình đi.”"),t("br"),t("br"),n._v("Thành chủ híp mắt lại: “Ta không tin, ta xuất thủ, toàn bộ Tĩnh Viễn thành, còn có người dám giành với ta đồ vật!”"),t("br"),t("br"),n._v("“Cũng đúng...” Hai vị hộ vệ hai mắt tỏa sáng."),t("br"),t("br"),n._v("Chỉ cần phòng đấu giá, thành chủ cho thấy thân phận, ai còn dám đấu giá?"),t("br"),t("br"),n._v("Đến lúc đó chỉ sợ đều không hao phí một trăm linh thạch thượng phẩm, liền có thể mua được."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),n._v(" Thành chủ bước nhanh ra ngoài đi đến."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Tiền bối, như vậy ngang nhiên đắc tội thành chủ, có phải hay không quá lỗ mãng...” Hồ Vân Sinh mặt mũi xoắn xuýt nhìn lại."),t("br"),t("br"),n._v("“Không sao, ngoan ngoãn nghe lời ngược lại cũng thôi, không nghe lời, ta không ngại để hắn cái này vị thành chủ đổi thành người khác!”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt thản nhiên."),t("br"),t("br"),n._v("Đạt tới hắn loại thực lực này, đã có thể hoàn toàn không để ý tới kẻ yếu ý nghĩ, ngoan ngoãn, không tìm phiền toái, thì cũng thôi đi, thật muốn tìm phiền toái, không ngại giáo huấn một lần."),t("br"),t("br"),n._v("Cho Ngọc Thần Thanh thông báo một tiếng, đổi một cái, hai vị thành chủ, căn bản không ai dám phản đối, thậm chí thực sự không được, phái mấy vị danh sư tới thẩm tra, dám ép mua ép bán, khẳng định là thổ hoàng đế, làm xằng làm bậy thời gian lâu dài, tra một cái một cái chuẩn."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Mặc dù mặt mũi xoắn xuýt, nhưng nghĩ đến vị tiền bối này thực lực, Hồ Vân sinh hay là thở phào nhẹ nhõm, lần nữa nhìn lại: “Ta sợ đắc tội thành chủ, đến lúc đó hắn ác ý đấu giá, khả năng liền có chút phiền phức.”"),t("br"),t("br"),n._v("“Ác ý đấu giá?”"),t("br"),t("br"),n._v("“Ừm, hắn sẽ dựa dẫm thân phận cùng thực lực, muốn cầu người khác, không tham dự cạnh tranh, cứ như vậy, Địa Tàng hoa chỉ sợ sẽ... Rất khó đem độc sư hấp dẫn ra tới!”"),t("br"),t("br"),n._v("Chần chờ một chút, Hồ Vân Sinh nói."),t("br"),t("br"),n._v("Hắn là nở phòng đấu giá, đối với loại tình huống này, biết đến rất rõ ràng."),t("br"),t("br"),n._v("Bình thường có thực lực người mạnh dựa dẫm thân phận ép cái khác nghĩ người mua từ bỏ, ác ý tiến hành đấu giá mua."),t("br"),t("br"),n._v("Phòng đấu giá liền từng gặp được nhiều lần, nhưng đều không có biện pháp gì tốt giải quyết."),t("br"),t("br"),n._v("“Cái này đơn giản, đợi đấu giá lúc bắt đầu ngươi tìm người, chỉ cần hắn dám nâng giá, liền trực tiếp gấp đôi gấp đôi thêm. Dù sao đều là ta đồ vật của mình, thêm đến một trăm vạn linh thạch thượng phẩm cũng không quan trọng.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hồ Vân Sinh nháy nháy mắt."),t("br"),t("br"),n._v("Bản thân cho mình nâng giá, làm như vậy không phải quá vô sỉ chút..."),t("br"),t("br"),n._v("Chẳng qua suy nghĩ kỹ một chút, đây đúng là một cái đối phó ác ý nâng giá biện pháp tốt."),t("br"),t("br"),n._v("Đối phương không biết ngược lại cũng thôi, biết, đoán chừng sẽ tươi sống tức chết, quả thực chính là không nói quy tắc."),t("br"),t("br"),n._v("“Được rồi, đi chuẩn bị đi, chỉ cần tìm được Độc điện vị trí, ta sẽ giải độc cho ngươi.”"),t("br"),t("br"),n._v("Trương Huyền không nói thêm lời."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hồ Vân Sinh nhẹ gật đầu, lui xuống."),t("br"),t("br"),n._v("Hắn vừa rời đi, Trương Huyền thân thể nhoáng một cái, biến mất tại chỗ, sau một khắc đã đi vào Thiên Nghĩ Phong sào."),t("br"),t("br"),n._v("Lúc này tổ ong, đã phát triển đến cực hạn, đường kính chừng hơn 500 mét, rộng rãi bao la, đủ loại kiến trúc tầng tầng lớp lớp, như cùng một cái cực lớn thôn xóm."),t("br"),t("br"),n._v("“Chủ nhân!” Thấy hắn đi tới, rất nhiều thú sủng đồng thời đi tới trước mặt."),t("br"),t("br"),n._v("Tổ ong phía trong, không chỉ có Tử Dương thú còn có trong mây mù rất nhiều vương giả, hắn lúc này, coi như gặp được cường giả, không tự mình ra tay, đem đám thánh thú này ném ra, đều có thể nhẹ nhõm đem Tĩnh Viễn thành dạng này địa phương nhỏ diệt đi."),t("br"),t("br"),n._v("“Cố gắng tu luyện, ta nhàn rỗi thời điểm sẽ tới cho các ngươi giảng giải trong vấn đề tu luyện.”"),t("br"),t("br"),n._v("Nhìn chúng thú một cái, Trương Huyền nói."),t("br"),t("br"),n._v("Đám thánh thú này là hắn phụ tá đắc lực, để vào tổ ong về sau, không làm gì liền cho bọn hắn giảng bài, hai, ba tháng qua, trên thực lực đều có tiến bộ rõ ràng."),t("br"),t("br"),n._v("Muốn không phải là không có Thánh thú tu luyện công pháp, chỉ sợ thời gian dài như vậy, những đại gia hỏa này cũng không biết tu luyện tới cái kia một cái cấp độ."),t("br"),t("br"),n._v("Để rất nhiều Thánh thú tiếp tục tu luyện, Trương Huyền thì đi tới một cái phòng."),t("br"),t("br"),n._v("Sạch sẽ chỉnh tề trên giường, Ngụy Như Yên nhắm hai mắt, an tĩnh nằm ở phía trên."),t("br"),t("br"),n._v("Lần này tới Độc điện là chuyên môn giải quyết trên người nàng vấn đề, một mực mang tại tổ ong bên trong."),t("br"),t("br"),n._v("Đưa tay tại cô gái mạch bên trên nối một chút, nhìn một hồi, thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Nhìn tới kiên trì một đoạn thời gian nữa, không có vấn đề quá lớn...”"),t("br"),t("br"),n._v("Mặc dù đối phương độc thể bộc phát, không có tìm được tốt phương pháp áp chế, nhưng bằng mượn hắn rất nhiều thủ đoạn, vẫn là để hắn tạm thời thoát ly nguy hiểm."),t("br"),t("br"),n._v("Cứ việc trong thời gian ngắn không cách nào tỉnh táo, chí ít tính mạng không ngại."),t("br"),t("br"),n._v("“Nha đầu này cũng thật là đáng thương...”"),t("br"),t("br"),n._v("Lắc đầu."),t("br"),t("br"),n._v("Vừa ra đời liền đụng phải ốm đau dằn vặt, thật vất vả lớn lên, lại mất đi thương yêu nhất phụ thân của nàng."),t("br"),t("br"),n._v("Khó trách được xưng là tai nạn chi thể, không chỉ có thể mang đến cho người khác tai hoạ, bản thân cũng là lắm tai nạn, không có có một ngày ngày tốt lành."),t("br"),t("br"),n._v("Thấy vấn đề của nàng không lớn, Trương Huyền cái này mới đi đến chuyên môn bố trí tu luyện tràng chỗ, ngồi xuống."),t("br"),t("br"),n._v("Đột phá đến Tòng Thánh đỉnh phong, đã hơn mười ngày, mặc dù không có đột phá bên dưới một cái cấp bậc cần có bảo vật, nhưng hắn mỗi ngày vẫn là cố gắng tu luyện, chưa từng ngừng."),t("br"),t("br"),n._v("Chân khí đang không ngừng rèn luyện bên dưới, càng ngày càng tinh thuần cũng càng ngày càng hùng hậu."),t("br"),t("br"),n._v("Từ khi đạt tới Bán Thánh về sau, rõ ràng cảm giác được đột phá so trước kia càng thêm khó khăn, trước kia đột phá một cái đại cấp bậc, chỉ cần một canh giờ là được, mà bây giờ khả năng cần nhiều tiêu tốn vài phút, thậm chí càng dài!"),t("br"),t("br"),n._v("“Tu luyện quả nhiên không có trong tưởng tượng đơn giản như vậy...”"),t("br"),t("br"),n._v("Than thở một tiếng, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Tu luyện quả thực không phải dễ dàng như vậy, đã định trước tràn đầy vất vả."),t("br"),t("br"),n._v("Ba canh giờ, nhoáng một cái vừa qua."),t("br"),t("br"),n._v("Đã tính toán một chút, đại khái đến ban đêm, đấu giá hội cũng gần như nhanh muốn bắt đầu, Trương Huyền lúc này mới thân thể nhoáng một cái, theo tổ ong đi ra."),t("br"),t("br"),n._v("Mới về đến phòng, chỉ thấy Hồ Vân Sinh đi tới."),t("br"),t("br"),n._v("“Tiền bối, đấu giá hội lập tức liền muốn bắt đầu...”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền theo ở phía sau hướng phòng đấu giá phương hướng đi tới, còn chưa tới đến dự định tốt phòng khách, lông mày đột nhiên nhíu một cái, ngừng lại."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-992"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-994"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);