(window.webpackJsonp=window.webpackJsonp||[]).push([[2291],{2646:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-985-khi-su-diet-to"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-985-khi-su-diet-to"}},[n._v("#")]),n._v(" Chương 985: Khi sư diệt tổ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 985: Khi sư diệt tổ"),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Thấy hắn dừng bước, Hồ Vân Sinh một mặt kỳ quái."),t("br"),t("br"),n._v("“Ngươi đi trước phòng khách, theo trước đó nói làm, quan sát muốn mua đến tột cùng có ai, đều nhớ kỹ, ta có chút sự tình, đi một chút sẽ trở lại!”"),t("br"),t("br"),n._v("Trương Huyền khai báo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không dám nhiều lời, Hồ Vân Sinh gật gật đầu, tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("Thấy hắn đi xa, Trương Huyền nhăn lại lông mày không có buông ra, ngược lại càng ngày càng chặt, thân thể nhoáng một cái, hướng về phía trước vọt ra ngoài."),t("br"),t("br"),n._v("Rất mau tới đến một cái đại sảnh."),t("br"),t("br"),n._v("Trong phòng đâu đâu cũng có người, hẳn là tới tham gia lần hội đấu giá này, liếc mắt nhìn hai phía, con mắt rơi vào một cái dung mạo thoạt nhìn hết sức bình thường trung niên nhân trên người."),t("br"),t("br"),n._v("Cái tên này thoạt nhìn chỉ có Thánh vực nhất trọng sơ kỳ, bình thản không có gì lạ, trong cơ thể một cỗ thủy thuộc tính vận chuyển chân khí, không có quá lớn nguy hại, nhưng mà chẳng biết tại sao, Trương Huyền cảm nhận được một loại đặc thù hoảng sợ."),t("br"),t("br"),n._v("Cùng ban đầu ở Địa quật giống nhau như đúc."),t("br"),t("br"),n._v("“Là... Dị Linh tộc nhân!”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Trong lòng đất, chém giết qua mấy trăm con Dị Linh tộc nhân, biết loại người này đáng sợ, cách đó không xa vị này, mặc dù không có tản mát ra sát lục chi khí, nhưng cho hắn đồng dạng đáng sợ áp lực."),t("br"),t("br"),n._v("Nhất là viện trưởng lệnh, không ngừng nhảy, tựa như lúc nào cũng sẽ lao ra."),t("br"),t("br"),n._v("“Minh Lý Chi Nhãn!”"),t("br"),t("br"),n._v("Đen nhánh con mắt bên trên, một đạo nhàn nhạt hoa văn chậm rãi xuất hiện, nhìn sang, quả nhiên thấy một đầu cao to Dị Linh tộc nhân, xuất hiện ở trước mắt, trên người nồng đậm sát khí, bị giam cầm ở sóng nước phía dưới."),t("br"),t("br"),n._v("Đối phương không biết dùng phương pháp gì, thế mà đem sát lục chi khí không chút nào tiết ra ngoài, để chung quanh cùng là Thánh vực người đều không thể nhìn ra."),t("br"),t("br"),n._v("Nếu không phải hắn vừa mới chém giết rất nhiều Dị Linh tộc nhân, đối hắn hiểu rõ rất nhiều, lại thêm nắm giữ Minh Lý Chi Nhãn, khẳng định cũng không có cách nào phát hiện."),t("br"),t("br"),n._v("“Thực lực của người này, có chút nhìn không thấu...”"),t("br"),t("br"),n._v("Nắm đấm âm thầm xiết chặt."),t("br"),t("br"),n._v("Đối phương không biết dùng cái gì đặc thù ngụy trang phương pháp, che đậy chung quanh quan sát, Minh Lý Chi Nhãn mặc dù có thể nhìn ra bản thể của hắn, cụ thể tu vi lại không cách nào xem thấu."),t("br"),t("br"),n._v("Đương nhiên cũng có thể là thực lực của đối phương vượt xa quá hắn."),t("br"),t("br"),n._v("Chỉ là... Mặc kệ thực lực như thế nào, một cái Dị Linh tộc nhân, ngụy trang nhân loại tới đây làm gì? Chẳng lẽ cũng phải tham gia đấu giá hội?"),t("br"),t("br"),n._v("Địa Tàng hoa đối Dị Linh tộc nhân, chẳng lẽ cũng có lực hấp dẫn?"),t("br"),t("br"),n._v("“Không thể để cho hắn đi vào...”"),t("br"),t("br"),n._v("Đỡ cái cằm, Trương Huyền trầm tư."),t("br"),t("br"),n._v("Nếu như cho cái tên này mua đi Địa Tàng hoa, còn thế nào tìm độc sư? Hơn nữa, một cái Thánh vực cấp bậc Dị Linh tộc nhân, chạy đến đám người dày đặc chỗ, muốn nói không có âm mưu quỷ kế gì, ai đều không tin."),t("br"),t("br"),n._v("Nhất định phải nghĩ phương pháp đuổi đi mới được."),t("br"),t("br"),n._v("“Chỉ là... Làm thế nào, mới có thể không kinh động những người khác?”"),t("br"),t("br"),n._v("Trương Huyền sầu muộn."),t("br"),t("br"),n._v("Nơi này là phòng đấu giá, mặc kệ là tự mình ra tay, vẫn là phái khôi lỗi, đều tất nhiên gây nên cực lớn chấn động, làm không cẩn thận đều sẽ đem nơi này phá hủy!"),t("br"),t("br"),n._v("Nhất là đối phương một khi biết bị biết phá thân phận, lộ ra Dị Linh tộc bộ dáng, khẳng định toàn bộ Tĩnh Viễn thành đều sẽ oanh động, đến lúc đó... Dẫn tới danh sư, lại nghĩ tìm tới độc sư, đi vào Độc điện, liền gần như không có khả năng."),t("br"),t("br"),n._v("Đang trầm tư, chỉ thấy trong mắt cái kia ngụy trang thành nhân loại Dị Linh tộc nhân, đi theo đám người sau lưng, hướng phòng đấu giá bên trong tràng đi đến, lại không ngăn trở, liền chỉ sợ lại không có cơ hội."),t("br"),t("br"),n._v("“Không kịp nghĩ nhiều...”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, biết thời gian không kịp quá nhiều suy nghĩ, Trương Huyền hai bước đi tới trước mặt, thân thể nhoáng một cái, chặn ở trước mặt đối phương."),t("br"),t("br"),n._v("“Làm gì!”"),t("br"),t("br"),n._v("Thấy hắn đột nhiên xuất hiện, chặn lại đường đi, ngụy trang Dị Linh tộc nhân nhướng mày."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Ngay sau đó bên cạnh hắn hai tên hộ vệ, đi tới, trường đao trong tay đồng thời ra khỏi vỏ, nguyên một đám đằng đằng sát khí."),t("br"),t("br"),n._v("Hai người này, rõ ràng đều là Tằm Phong cảnh cường giả đỉnh phong, có điều, cũng không phải là Dị Linh tộc nhân, mà là bình thường tu luyện giả."),t("br"),t("br"),n._v("“Không biết vị bằng hữu này, ngăn cản tại hạ, không biết muốn làm gì?”"),t("br"),t("br"),n._v("Thấy mọi người chung quanh đều đem ánh mắt tập trung tới, Dị Linh tộc nhân khoát tay áo, để hộ vệ không nên động thủ, nhìn lại."),t("br"),t("br"),n._v("Hắn cũng biết, ở đây náo lên, thân phận rất có thể phơi bày, được không bù mất."),t("br"),t("br"),n._v("Có thể ngụy trang thành nhân loại đi tới nơi này, tất nhiên có mục đích của mình."),t("br"),t("br"),n._v("“Thật sự là trời xanh có mắt, rốt cục để cho ta tìm tới ngươi...”"),t("br"),t("br"),n._v("Không để ý tới đối phương thái độ cùng hai tên hộ vệ, Trương Huyền đột nhiên trên mặt biến đến kích động dị thường, bỗng nhiên muốn đem hắn ôm lấy."),t("br"),t("br"),n._v("Nhướng mày, Dị Linh tộc nhân trong cơ thể một cỗ lực lượng bừng lên, ngăn lại động tác của hắn."),t("br"),t("br"),n._v("“Ta là Tôn Cường ah... Ngươi không biết ta?”"),t("br"),t("br"),n._v("Bị đối phương đẩy ra, Trương Huyền vội vàng nhìn qua, cẩn thận giới thiệu bản thân, tốt giống như bạn cũ lâu năm đồng dạng."),t("br"),t("br"),n._v("“Tôn... Cường?” Dị Linh tộc nhân cau mày: “Thật xin lỗi, ngươi khả năng nhận lầm!”"),t("br"),t("br"),n._v("Hắn ngụy trang thành nhân loại, đi tới nơi này, một người cũng không nhận ra, cái tên này từ đâu xuất hiện?"),t("br"),t("br"),n._v("“Làm sao có thể nhận lầm!”"),t("br"),t("br"),n._v("Không để ý tới thái độ của hắn, Trương Huyền bắt lại đối phương cánh tay: “Chúng ta từ nhỏ cùng nhau lớn lên, tiểu tử ngươi lúc còn trẻ, thế nhưng là rất cuồng vọng. Thực lực bạo tăng, đem lão sư của mình bạo đánh cho một trận; Vì báo thù, chế biến đặc biệt độc dược, độc chết một vị cường giả Thánh vực; Mấu chốt nhất là, không biết từ nơi nào học Vu hồn pháp quyết tu luyện, hồn lực đại tăng! Chuyện này ta có thể nhớ tinh tường, làm sao có thể quên!”"),t("br"),t("br"),n._v("“Đem lão sư đều đánh? Đây không phải khi sư diệt tổ ư?”"),t("br"),t("br"),n._v("“Phối chế độc dược, độc chết cường giả Thánh vực, đây không phải độc sư ư?”"),t("br"),n._v("“Còn học Vu hồn? Chẳng lẽ là vu hồn sư?”"),t("br"),t("br"),n._v("Mọi người chung quanh, nghe được hắn, tất cả đều ánh mắt lộ ra bất thiện."),t("br"),t("br"),n._v("Danh sư đại lục chú trọng truyền thừa, đối lão sư cực kỳ tôn trọng, khi sư diệt tổ, sẽ bị vạn người phỉ nhổ."),t("br"),t("br"),n._v("Càng quan trọng hơn là, độc sư, vu hồn sư chức nghiệp, càng là Danh Sư đường phong sát, đột nhiên xuất hiện, mọi người không có trực tiếp xông lên tới đánh người, là tốt lắm rồi."),t("br"),t("br"),n._v("“Ngươi nói bậy bạ gì đó?”"),t("br"),t("br"),n._v("Thấy cái này đột nhiên không quen biết gia hỏa, ăn nói bừa bãi, Dị Linh tộc nhân lông mày nhảy một cái, suýt chút nữa không có tại chỗ nổ tung: “Ta căn bản không biết ngươi...”"),t("br"),t("br"),n._v("Một khi bị mọi người vây công, thân phận của hắn rò rỉ, còn làm chuyện gì?"),t("br"),t("br"),n._v("Cánh tay chấn động mạnh một cái, một cỗ lực lượng đem trước mắt bắt lấy bàn tay của hắn đánh văng ra."),t("br"),t("br"),n._v("“Không biết ta? Cái kia tốt!”"),t("br"),t("br"),n._v("Không để ý tới động tác của đối phương, Trương Huyền lông mày giương lên, nói: “Ngươi nói không biết ta, ba năm trước đây ngươi bị người đuổi giết, vai trái bị xuyên thủng, sắp phải chết, là ai đưa ngươi cứu? Hiện tại có dám hay không đem bả vai lộ ra, nếu như không có đoán sai, chỗ này thương, nên đến bây giờ đều có vết sẹo a?”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Cái này Dị Linh tộc nhân sững sờ, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Ba năm trước đây hắn đích xác nhận qua thương, vai trái bị một loại đặc thù vũ khí đâm thủng, đến bây giờ cũng không hoàn toàn khôi phục, còn có lưu lại vết thương, coi như ngụy trang thành nhân loại, cũng không thể biến mất..."),t("br"),t("br"),n._v("Chỉ là, xuyên tại trong quần áo, chưa hề gặp người, coi như bằng hữu tốt nhất đều không biết, cái tên này, theo làm sao biết?"),t("br"),t("br"),n._v("“Làm sao không dám sao? Ngươi cái này chỗ vết thương, là bị hỏa thuộc tính trường mâu đâm vào, mà ngươi tu luyện chính là thủy thuộc tính công pháp, không chỉ có vết sẹo không cách nào loại bỏ, trong cơ thể còn có một chỗ ám thương, một đạo hỏa diễm chân khí, mỗi tháng đêm trăng tròn, đều sẽ tàn phá bừa bãi xung kích của ngươi hồn ẩn huyệt, để ngươi thống khổ không chịu nổi, điểm ấy... Ta nói không sai chứ?”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Vừa mới bắt đầu chỉ là kinh ngạc, lần này con mắt trừng đến sắp rơi ra, Dị Linh tộc nhân dọa đến sắp điên rồi."),t("br"),t("br"),n._v("Đối phương nói chính xác vô cùng."),t("br"),t("br"),n._v("Trên người hắn chỗ này thương, quả thực là bị hỏa thuộc tính trường mâu kích thương, mà hắn cũng quả thực thực sự tu luyện thủy thuộc tính công pháp, càng có một đạo hỏa diễm chân khí, mỗi tháng đều ở trong người giày vò lấy hắn... Có thể nói, nói một chút sai lầm đều không có!"),t("br"),t("br"),n._v("Có thể xác định, không biết đối phương, hơn nữa, bản thân khuyết điểm, cũng chưa từng cùng bất kỳ kẻ nào nói qua..."),t("br"),t("br"),n._v("Cái tên này làm sao mà biết được?"),t("br"),t("br"),n._v("“Thế nào, còn không nhớ ra được ta? Ngươi có phải hay không tại ẩn giấu cái gì, không tiện gặp người? Mới không dám nhận ta...”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Khụ khụ! Ta nhận ra, nguyên lai là Tôn Cường huynh...”"),t("br"),t("br"),n._v("Thấy kẻ trước mắt này càng nói càng nhiều, ánh mắt chung quanh càng ngày càng quái dị, Dị Linh tộc nhân sợ đối phương, nói tiếp, thân phận khó giữ được, đành phải mặt mũi xấu hổ."),t("br"),t("br"),n._v("“Này mới đúng mà! Nhận ra ta tới là tốt rồi, đến, đây là ngươi thích nhất uống rượu ngon, là huynh đệ, liền uống một ngụm!”"),t("br"),t("br"),n._v("Thấy hắn nhận ra, Trương Huyền lúc này mới thở phào nhẹ nhõm, cười rạng rỡ, cổ tay khẽ đảo, lấy ra một cái hồ lô, đưa tới."),t("br"),t("br"),n._v("“Uống rượu? Ở đây?”"),t("br"),t("br"),n._v("Dị Linh tộc nhân ngẩn người."),t("br"),t("br"),n._v("Coi như là bạn tốt, uống rượu cũng coi trọng nơi đi!"),t("br"),t("br"),n._v("Nơi này đang đấu giá nội thành bộ, nhiều người nhìn như vậy, ngươi để cho ta uống rượu... Ý gì?"),t("br"),t("br"),n._v("Không chỉ hắn kỳ quái, mọi người chung quanh cũng nguyên một đám tràn đầy nghi ngờ."),t("br"),t("br"),n._v("“Đúng vậy a, làm sao không tin ta? Cảm thấy ta trong rượu có vấn đề? Ngươi nha, vẫn là đa nghi như vậy, đến, ta trước uống một ngụm!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền giơ lên hồ lô rượu, mở ra nắp bình, lập tức rượu mùi thơm khắp nơi, ngẩng đầu uống một ngụm."),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Uống hết, đưa tới."),t("br"),t("br"),n._v("“Vẫn là hôm nào lại uống đi, đấu giá hội lập tức bắt đầu, ta còn có việc...”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Dị Linh tộc nhân cuối cùng lắc đầu."),t("br"),t("br"),n._v("Hắn căn bản không biết đối phương, xuất hiện để hắn uống rượu, không nghi ngờ cái kia là giả."),t("br"),t("br"),n._v("“Ngươi vẫn là loại này đa nghi tính cách, cùng Dị Linh tộc nhân thật giống, nhất là giống như kia cái gì Thanh Điền cái gì Thủy Diệp vương...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Kẽo kẹt!"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe nói như thế, Dị Linh tộc nhân con ngươi co rụt lại, toàn thân một cái cứng ngắc, ngay sau đó một đạo sát ý theo trong mắt chợt lóe lên."),t("br"),t("br"),n._v("Đối phương thoạt nhìn giống như nói bậy, có thể hầu như đem thân phận của hắn hoàn toàn minh xác!"),t("br"),t("br"),n._v("Nói cách khác, trước đó lời nói, căn bản không phải nói bậy, mà là đối phương... Đã biết hắn là ai."),t("br"),t("br"),n._v("Chỉ là... Hắn nghĩ nửa ngày, đều không nghĩ tới, lúc nào nhận biết một vị kêu Tôn Cường gia hỏa."),t("br"),t("br"),n._v("“Ngươi nói không sai, bạn tốt quả thực muốn uống rượu, có điều, nơi này không phải chỗ, có thể hay không cùng đi ra? Tìm quán rượu, chúng ta không say không về...”"),t("br"),t("br"),n._v("Đem trong lòng sát cơ ngăn chặn, Dị Linh tộc nhân cười một tiếng."),t("br"),t("br"),n._v("Đã đối phương nhận ra thân phận của hắn, cái kia chỉ có một loại phương pháp... Tìm cơ hội đem hắn giết!"),t("br"),t("br"),n._v("Nếu không, ở đây một khi rò rỉ, nhiệm vụ khẳng định không cách nào hoàn thành."),t("br"),t("br"),n._v("“Đi quán rượu? Đương nhiên có thể, có điều, ngươi muốn trước uống một ngụm, ta lại cùng ngươi đi qua, ngay cả ta kính rượu đều không uống, ai biết ngươi một lát nữa, lại là uống rượu, vẫn là động thủ với ta? Liền khi sư diệt tổ sự tình, đều làm qua, giết chết bạn tốt, cũng không tính là gì đi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Dị Linh tộc nhân khí sắc mặt đỏ lên."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-993"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-995"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);