(window.webpackJsonp=window.webpackJsonp||[]).push([[2292],{2647:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-986-thuy-diep-vuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-986-thuy-diep-vuong"}},[n._v("#")]),n._v(" Chương 986: Thủy Diệp vương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 986: Thủy Diệp vương"),t("br"),t("br"),n._v("Đến bây giờ, hắn đã khẳng định, phía bên kia tất nhiên nhận ra thân phận của mình."),t("br"),t("br"),n._v("Chỉ là... Hắn ngụy trang một chút dấu vết đều không có lưu lại, đến cùng làm sao nhìn ra được?"),t("br"),t("br"),n._v("Chẳng lẽ cái tên này, thực lực so với hắn còn còn đáng sợ hơn?"),t("br"),t("br"),n._v("“Uống rượu mà thôi, ngươi suy nghĩ nhiều...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, vừa rồi thất thố, bị hắn che giấu, Dị Linh tộc nhân nhẹ nhàng cười một tiếng, đưa tay nhận lấy hồ lô rượu, híp mắt lại, nhìn sang."),t("br"),t("br"),n._v("Phía bên kia nhận ra thân phận, còn để hắn uống rượu, nếu như nói không có vấn đề, đánh chết cũng không tin."),t("br"),t("br"),n._v("Ánh mắt tập trung, tinh thần lan tràn, rất mau đem rượu quét mắt một lần, nhịn không được cau mày."),t("br"),t("br"),n._v("Căn cứ hắn quan sát, trong hồ lô giả bộ, quả thực chỉ là bình thường rượu, đồ vật gì đều không có, nhiều nhất linh khí tương đối nồng đậm mà thôi."),t("br"),t("br"),n._v("“Rượu này ta có chút uống không quen, vẫn là uống ta đi...”"),t("br"),t("br"),n._v("Dừng lại một chút, vẫn là không quá yên tâm, đem hồ lô rượu, lần nữa đưa trở về, lấy ra một cái bình ngọc."),t("br"),t("br"),n._v("Đây là hắn mang theo người, khẳng định không có vấn đề."),t("br"),t("br"),n._v("Giơ lên bình ngọc, đang định mở ra nắp bình, uống một cái, chỉ thấy đối diện Tôn Cường, khẽ vươn tay đem cái bình cầm tới: “Uống không quen ta, đến, để ta nhìn ngươi đây là cái gì?”"),t("br"),t("br"),n._v("Nói xong mở ra nắp bình."),t("br"),t("br"),n._v("Nồng đậm mùi rượu phun ra ngoài, mùi thơm nức mũi, quả thực so vừa rồi hồ lô rượu bên trong, tốt hơn rất nhiều."),t("br"),t("br"),n._v("“Quả thực là mê rượu, khó trách chướng mắt ta, cho...”"),t("br"),t("br"),n._v("Nhìn một cái, cũng không có động tác khác, lần nữa đưa trở về."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy hắn chỉ là cầm một cái bình ngọc, cũng không làm bất kỳ động tác gì, Dị Linh tộc nhân thở phào nhẹ nhõm, nhận lấy bình ngọc, uống một ngụm."),t("br"),t("br"),n._v("“Hiện tại có thể cùng ta cùng một chỗ, đi tìm quán rượu, không say không về đi?”"),t("br"),t("br"),n._v("Cười nhạt một tiếng."),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Đại nhân...”"),t("br"),t("br"),n._v("Hai tên hộ vệ thấy mình hộ tống đại nhân, muốn cùng người khác uống rượu, tất cả đều hai mặt nhìn nhau... Không phải tới tham gia đấu giá hội sao? Làm sao còn chưa bắt đầu, ngược lại muốn đi uống rượu?"),t("br"),t("br"),n._v("“Các ngươi đi vào trước, ta một hồi liền trở lại...”"),t("br"),t("br"),n._v("Dị Linh tộc nhân một mặt tự tin khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Thấy hắn làm ra quyết định, hộ vệ chần chờ một chút, vẫn gật đầu."),t("br"),t("br"),n._v("Nhà bọn hắn chủ nhân đều phải nghe trước mắt vị này, phía bên kia mệnh lệnh tự nhiên không dám vi phạm."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Thấy hắn giao phó xong, Trương Huyền phía trước đi ra ngoài, rất mau ra Mặc Vân hiên."),t("br"),t("br"),n._v("Đi dọc theo đường phố một hồi, đi tới một cái yên tĩnh đường hầm."),t("br"),t("br"),n._v("“Ngươi rốt cuộc là ai?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, chặn ở phía trước, Dị Linh tộc nhân rốt cuộc kìm nén không được, ánh mắt âm hàn."),t("br"),t("br"),n._v("“Ta? Chính là người bình thường mà thôi, ngược lại là ngươi... Thân là Dị Linh tộc nhân, nhưng ngang nhiên ngụy trang thành nhân loại, chạy đến nơi đây, lá gan có thể thật là lớn!”"),t("br"),t("br"),n._v("Khóe miệng nâng lên, Trương Huyền mí mắt vừa nhấc: “Có phải hay không ah, Thủy Diệp vương?”"),t("br"),t("br"),n._v("“Ngươi... Đến cùng là ai?”"),t("br"),t("br"),n._v("Híp mắt lại, đối diện Dị Linh tộc nhân, bàn tay một trảo, một cỗ chân khí lập tức đem chung quanh toàn bộ bao vây, làm cho cả đường phố đều tựa hồ biến thành địa ngục."),t("br"),t("br"),n._v("Bị nhận ra thân phận, hắn cũng định ra tay."),t("br"),t("br"),n._v("“Ta là ai?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Thân phận của ta, ngươi bây giờ còn chưa tư cách biết!”"),t("br"),t("br"),n._v("“Không nói? Vậy cũng chớ đi!”"),t("br"),t("br"),n._v("Dị Linh tộc nhân Thủy Diệp vương, gào lên một tiếng, bỗng nhiên vồ tới."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Cuồng bạo chân khí như là sóng nước cuốn lên, toàn bộ đường phố tại lực lượng cường đại chèn ép xuống, tựa như lúc nào cũng sẽ đổ sụp."),t("br"),t("br"),n._v("Vừa ra tay, liền hiện ra thực lực của hắn, căn bản không phải Thánh vực nhất trọng, cũng không phải nhị trọng, mà là... Tứ trọng!"),t("br"),t("br"),n._v("Nguyên Thần cảnh!"),t("br"),t("br"),n._v("“Muốn giết ta?”"),t("br"),t("br"),n._v("Mặt đối công kích của đối phương, Trương Huyền cũng chẳng suy nghĩ gì nữa, cũng không trốn tránh, ngược lại cười một tiếng: “Không có ý tứ, ta có đi hay không còn chưa tới phiên ngươi khống chế... Ngược lại là ngươi, trước nằm xuống đi!”"),t("br"),t("br"),n._v("Nói, cùng trước đó tại Mặc Vân hiên đồng dạng, xoa cái búng tay."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Lời nói kết thúc, mới vừa rồi còn khí thế hung hăng Thủy Diệp vương, thân thể đột nhiên cứng đờ, rớt xuống, lực lượng toàn thân như thủy triều tan mất, co quắp tại trên mặt đất, từng ngụm từng ngụm hộc máu."),t("br"),t("br"),n._v("“Ngươi... Ngươi đối ta hạ độc?”"),t("br"),t("br"),n._v("Con ngươi thu hẹp, nhìn về phía cách đó không xa gia hỏa, Thủy Diệp vương tràn đầy khó tin."),t("br"),t("br"),n._v("Ngàn cẩn thận, vạn cẩn thận, phía bên kia lúc nào hạ độc, thế mà không có chút nào biết."),t("br"),t("br"),n._v("“Không sai! Ngươi lợi hại như vậy vương giả, không hạ độc, làm sao chế ngự?” Trương Huyền trên mặt cười khanh khách."),t("br"),t("br"),n._v("Đơn thuần Minh Lý Chi Nhãn, chỉ có thể nhìn ra hắn là Dị Linh tộc nhân, chân chính xác nhận thân phận, còn là dựa vào Thiên Đạo thư viện."),t("br"),t("br"),n._v("Cố ý đi ôm, dẫn đối phương vận chuyển chân khí, chẳng khác nào thi triển võ kỹ, lập tức nhận được kỹ càng tin tức cùng thiếu hụt."),t("br"),t("br"),n._v("Cái này ngụy trang cùng nhân loại giống nhau như đúc gia hỏa, thế mà cùng trước đó ở trong hang chém giết Kim Diệp Vương, Thanh Diệp Vương đồng dạng, cũng là một đầu Dị Linh tộc vương giả... Thủy Diệp vương!"),t("br"),t("br"),n._v("Thánh vực tứ trọng siêu cấp cường giả!"),t("br"),n._v(" Thực lực như thế đường đường vương giả, ngụy trang thành nhân loại, xuất hiện ở đây, nếu là không ngăn cản, tất nhiên gây nên vô cùng náo động lớn."),t("br"),t("br"),n._v("Đem Tĩnh Viễn thành diệt tuyệt, đều là có khả năng!"),t("br"),t("br"),n._v("Mấu chốt nhất là, coi như hắn thực lực bây giờ tăng nhiều, cùng đối chiến cũng là dữ nhiều lành ít, mượn nhờ khôi lỗi, có thể chém giết, cũng khẳng định sẽ gây ra vô cùng động tĩnh lớn, lý do an toàn, lúc này mới dụ dỗ phía bên kia uống xong “Rượu ngon”."),t("br"),t("br"),n._v("Trong rượu ẩn giấu đi hắn Thiên Đạo chân khí, tiến vào thân thể hoàn toàn có thể trong một ý nghĩ biến thành kịch độc."),t("br"),t("br"),n._v("Cái tên này mặc dù thông minh, không uống rượu của hắn, nhưng nằm mơ đều không nghĩ tới, bình ngọc cùng bàn tay của hắn tiếp xúc, chẳng khác nào trúng chiêu."),t("br"),t("br"),n._v("Thiên Đạo chân khí tinh thuần đến cực điểm, bản thân thừa dịp bắt được bình rượu công phu, truyền vào mấy đạo đi vào, thần không biết quỷ không hay, coi như dùng thần thức liếc nhìn, đều không thể phát hiện."),t("br"),t("br"),n._v("Chủ yếu nhất là... Nghĩ không ra!"),t("br"),t("br"),n._v("Lợi hại hơn nữa độc sư hạ độc, cũng cần độc phấn, đủ loại cổ trùng loại hình đồ vật, làm làm môi giới, chân khí ẩn chứa kịch độc... Đây là hết không dám nghĩ."),t("br"),t("br"),n._v("“Ngươi... Cùng chết đi!”"),t("br"),t("br"),n._v("Thấy đối phương thừa nhận thản nhiên như vậy, Thủy Diệp vương vẻ mặt đỏ lên, gào lên một tiếng, bàn tay trên mặt đất vỗ, lăng không mà lên, đối Trương Huyền liền vồ tới."),t("br"),t("br"),n._v("Thấy hắn còn muốn công kích, Trương Huyền cũng không nói nhảm: “Xuống!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngón tay hướng về phía trước một chút, Thủy Diệp vương một tiếng vù vù, lần nữa từ không trung rơi xuống, nặng nề ngã xuống đất, trên đầu mồ hôi lạnh ứa ra."),t("br"),t("br"),n._v("Thiên Đạo chân khí xâm nhập toàn thân rất nhiều khiếu huyệt, chỉ cần một đạo ý niệm, liền có thể để hắn trực tiếp mất mạng, độc tính bao lớn, cấp bảy kịch độc đều khó mà bằng được, coi như hắn thực lực mạnh mẽ, cũng không chịu nổi."),t("br"),t("br"),n._v("“Ngươi đây là cái gì độc?”"),t("br"),t("br"),n._v("Cũng ý thức được không được bình thường, Thủy Diệp vương răng cắn khanh khách vang vọng, khó tin."),t("br"),t("br"),n._v("Vì nhiệm vụ lần này, hắn chuyên môn làm chuẩn bị, phục dụng rất nhiều giải độc đan dược, theo lý thuyết, bình thường độc dược đối với hắn không có bất cứ tác dụng gì."),t("br"),t("br"),n._v("Phía bên kia lúc nào hạ độc, không thấy được ngược lại cũng thôi, cụ thể cái gì độc, thế mà cũng cảm giác không ra, cái này cũng có chút đáng sợ."),t("br"),t("br"),n._v("Chẳng lẽ, nhiệm vụ còn chưa bắt đầu, liền gặp một cái thất tinh độc sư?"),t("br"),t("br"),n._v("Hồng Viễn đế quốc Độc điện, lợi hại nhất độc sư, không phải cũng mới chỉ có lục tinh ư?"),t("br"),t("br"),n._v("Không có trả lời câu hỏi của hắn, Trương Huyền hai bước đi tới trước mặt, ngón tay liên tục điểm tới, trong nháy mắt đem trên người đối phương tu vi niêm phong lại."),t("br"),t("br"),n._v("Biết nhược điểm của đối phương, lại thêm Thiên Đạo chân khí, đã tiến nhập thể nội, đem hắn đồng phục, cũng không phải là đặc biệt khó khăn."),t("br"),t("br"),n._v("Phong ấn lại tu vi, năm ngón tay mở ra, đưa tay về phía trước, Thủy Diệp vương miệng bên trong bay ra một cái không lớn viên thuốc, bên ngoài dùng giấy dầu bọc, bình thường thời điểm, thả ở trong đó cũng sẽ không có sự tình."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nhìn thấy hắn đem trong miệng mình đồ vật lấy ra, Thủy Diệp vương biến sắc."),t("br"),t("br"),n._v("Không phải cái khác, đúng là hắn bị bắt về sau, vì phòng ngừa để lộ bí mật, dùng để tự sát dược vật."),t("br"),t("br"),n._v("Thứ này nấp rất kỹ, loại trừ bọn họ mấy vị thi hành nhiệm vụ Dị Linh tộc vương giả, cơ bản không người có thể biết, phía bên kia đến cùng là ai?"),t("br"),t("br"),n._v("Không chỉ biết tên của hắn, liền loại này cơ mật, đều rõ rõ ràng ràng!"),t("br"),t("br"),n._v("“Được rồi, như vậy hẳn là có thể hỏi ra vài thứ!”"),t("br"),t("br"),n._v("Đem giấy dầu bọc kịch độc hủy đi, Trương Huyền cười mỉm nhìn về phía trước mắt Thủy Diệp vương."),t("br"),t("br"),n._v("Kim Diệp Vương, Thanh Diệp Vương, bị hắn đồng phục về sau, cũng bởi vì loại kịch độc này mà chết rồi, hắn đã có chuẩn bị, đương nhiên sẽ không lại giẫm lên vết xe đổ."),t("br"),t("br"),n._v("“Nói đi, từ đâu tới, tới Tĩnh Viễn thành muốn làm gì...”"),t("br"),t("br"),n._v("Tất cả chuẩn bị sẵn sàng, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Muốn cho ta nói, nằm mơ!”"),t("br"),t("br"),n._v("Thủy Diệp vương cắn răng."),t("br"),t("br"),n._v("“Không nói? Đơn giản...”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Trương Huyền ngón tay một chút, mấy đạo chân khí dọc theo phía bên kia huyệt đạo, chui vào trong cơ thể."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Chân khí vừa tiến vào trong cơ thể, Thủy Diệp vương lập tức cảm giác giống như là bị ngàn vạn độc trùng cắn xé, thấu xương đau đớn."),t("br"),t("br"),n._v("Nếu như chỉ là thân thể, còn có thể chịu đựng lấy, nhưng đối phương chảy vào vào khí tức trong người, liền linh hồn đều có mãnh liệt uy áp, tựa như lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("“Để cho ta hướng như ngươi loại này cấp thấp người khuất phục, không có khả năng...”"),t("br"),t("br"),n._v("Cố nén đau đớn, Thủy Diệp vương răng cắn đến khanh khách vang vọng, đột nhiên một tiếng bạo hống, toàn bộ người thân thể giống như là thổi lên bóng bay đồng dạng phồng lên mà lên."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Nhìn thấy bộ dáng của hắn, Trương Huyền biến sắc, vội vàng hướng về phía trước bắt tới, hùng hậu chân khí tuôn ra, đang định ngăn chặn lực lượng của đối phương, lập tức nhìn thấy phía bên kia thân thể, mắt trần có thể thấy khô quắt xuống, ngay sau đó một cái cùng Thủy Diệp vương giống nhau như đúc hồn phách, từ đối phương mi tâm bay ra."),t("br"),t("br"),n._v("“Vu hồn?”"),t("br"),t("br"),n._v("Cảm nhận được phía bên kia khí tức cường đại, Trương Huyền sững sờ, lập tức con ngươi co rụt lại: “Không đúng, không phải Vu hồn, mà là... Nguyên thần!”"),t("br"),t("br"),n._v("Thánh vực tứ trọng Nguyên Thần cảnh, trong cơ thể thai anh, ngưng tụ thành nguyên thần, có thể cùng Vu hồn đồng dạng, rời khỏi thân thể, độc lập tồn tại, một đêm vạn dặm, thậm chí còn có thể một ý niệm lấy đầu người."),t("br"),t("br"),n._v("Cái tên này thân thể cùng tu vi bị bản thân dùng Thiên Đạo chân khí ngăn chặn, dưới tình thế cấp bách, thế mà bỏ thân thể cùng toàn thân tu vi, trực tiếp nguyên thần vọt ra."),t("br"),t("br"),n._v("Bất quá, còn không có đạt tới Thánh vực ngũ trọng, nguyên thần cưỡng ép xuất hiện, rất khó còn sống sót không nói, còn có thể để thân thể hoàn toàn chết, lại không đường lui thối lui."),t("br"),t("br"),n._v("“Ta muốn cùng ngươi đồng quy vu tận...”"),t("br"),t("br"),n._v("Nguyên thần rời khỏi thân thể, Thủy Diệp vương điên cuồng gào thét, lộ ra vẻ dữ tợn, hướng Trương Huyền lao đến."),t("br"),t("br"),n._v("Tình huống hiện tại hẳn phải chết không nghi ngờ, đã như vậy, muốn đem cái tên này, lôi kéo cùng một chỗ xuống địa ngục!"),t("br"),t("br"),n._v("Phía bên kia chẳng qua một cái Tòng Thánh, linh hồn yếu kém, chỉ cần nguyên thần xông vào phía bên kia trong cơ thể, có thể dễ dàng đem xé rách!"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Còn không có kịp phản ứng, điên cuồng nguyên thần liền tiến vào mi tâm, Trương Huyền thân thể cứng đờ."),t("br"),t("br"),n._v("Trước mắt điên cuồng, tiến nhập thể nội Thủy Diệp vương, đang định đem phía bên kia hồn phách thôn phệ, đột nhiên thấy được một cái uốn lượn như là một ngọn núi lớn Vu hồn."),t("br"),t("br"),n._v("“Ta muốn nuốt ngươi... Bà mẹ nó, đây là cái gì?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, toàn thân cứng ngắc."),t("br"),t("br"),n._v("(Từ hôm nay trở đi, thời gian đổi mới điều chỉnh làm 12 giờ trưa, cùng buổi tối bảy giờ, giữa trưa trước thời hạn chút, nếu không thời gian khoảng cách quá dài, sẽ bị mắng. Đổ mồ hôi)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-994"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-996"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);