(window.webpackJsonp=window.webpackJsonp||[]).push([[23],{378:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-997-lang-le-len-vao-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-997-lang-le-len-vao-ha"}},[n._v("#")]),n._v(" Chương 997: Lặng lẽ lẻn vào (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 997: Lặng lẽ lẻn vào (hạ)"),t("br"),t("br"),n._v("Cái này ba cái điện đường, đều có sáu, bảy mươi mét cao, tại bốn phía kiến trúc bên trong, hạc giữa bầy gà, vô cùng dễ thấy."),t("br"),t("br"),n._v("“Thử độc điện, tỷ thí điện, tàng thư điện... Phía trước hai cái, bình thường dùng độc, coi như quét dọn lại sạch sẽ, cũng sẽ có lưu lại vết... Tàng thư điện khác biệt, bên trong tất cả đều là thư tịch, có lẽ sạch sẽ nhất...”"),t("br"),t("br"),n._v("Thử độc điện dùng để thí nghiệm mới luyện chế độc dược, coi như tìm tới giải dược có thể trung hoà, cũng tất nhiên có lưu lại vết, tỷ thí điện cũng gần như, độc sư đối đầu, đủ loại độc vật bốc lên, chướng khí mù mịt, lưu lại rất nhiều khó mà giải quyết tai hoạ ngầm, coi đây là phán đoán, muốn tìm được tàng thư điện, hẳn không phải là vô cùng khó khăn."),t("br"),t("br"),n._v("Trong lòng hơi động, hướng tam đại điện đường nhìn kỹ đi qua."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chiếu xuống, điện đường tình huống chút xíu tất hiện, trong đó hai cái, độc vật bốc lên, cho người ta một loại đục ngầu cảm giác."),t("br"),t("br"),n._v("“Hẳn là cái kia!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền không có quá nhiều do dự, thân thể nhảy lên, trực tiếp hướng nhất trong suốt điện đường bay đi."),t("br"),t("br"),n._v("Cái này kiến trúc, so cái kia trước đó hai cái càng cao hơn đứng thẳng, mang theo thư quyển nặng nề chi khí, chung quanh bố trí cực kỳ cường đại trận pháp, xem ra không có đặc thù thủ lệnh lời nói, rất khó đi vào."),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Đổi lại học tập trận pháp bí tịch trước đó, khả năng quả thực không có cách nào, bất quá bây giờ đối với trận pháp nghiên cứu đã không kém gì Thất Tinh trận pháp sư, lại thêm Minh Lý Chi Nhãn, tìm tới trong đó sơ hở, không phải việc khó gì."),t("br"),t("br"),n._v("Hai mắt lấp lánh nhìn sang, thời gian không dài, quả nhiên cho hắn tìm được một cái không lớn sơ hở, nhẹ nhàng nhoáng một cái, chui vào."),t("br"),t("br"),n._v("Vu hồn cứ việc vô hình vô chất, mắt thường không cách nào phân biệt, nhưng mà trận pháp hội tụ linh khí, tiến vào bên trong, rất dễ dàng thay đổi chung quanh khí tràng, từ đó bị phát hiện."),t("br"),t("br"),n._v("Cho nên, muốn đi vào trong đó, vẫn là phải cẩn thận một chút."),t("br"),t("br"),n._v("Dọc theo trận pháp sơ hở, đi vào trong, chỉ chốc lát đi tới tàng thư điện trước mặt."),t("br"),t("br"),n._v("Cách đó không xa có hai cái Thánh vực nhất trọng độc sư trông coi, Trương Huyền trước thời hạn tính toán được rồi đi lại lộ tuyến, cũng không có gây nên sự chú ý của đối phương, xuyên thấu qua cửa lớn khe hở, nhẹ nhàng nhoáng một cái, chui vào."),t("br"),t("br"),n._v("Đại điện một tầng, xếp đặt rất nhiều giá sách, lít nha lít nhít thư tịch, lập tức đập vào mi mắt, đều là liên quan tới độc."),t("br"),t("br"),n._v("《 độc đạo chân giải 》, 《 Thanh Nguyên Tử ba mươi sáu loại dùng độc pháp 》, 《 giải độc công lược 》, 《 cơ sở độc công tu luyện 》... Đủ loại thư tịch tầng tầng lớp lớp, nhiều vô cùng, toàn sách là sách."),t("br"),t("br"),n._v("Những này là Độc điện thành lập trên vạn năm đến, vô số độc sư tiêu tốn tâm huyết sáng tác mà thành, có chính xác, có sai lầm, đối với người khác tới nói, không dám toàn bộ đều xem, để tránh loạn tư duy, nhưng đối Trương Huyền tới nói, hoàn toàn không cần lo lắng vấn đề này."),t("br"),t("br"),n._v("Thân thể nhoáng một cái, tìm một cái thoải mái nhất vị trí."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Một tiếng hừ nhẹ, ánh mắt như điện, trực tiếp hướng từng dãy thư tịch nhìn sang."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Từng quyển từng quyển thư tịch bị thu nhận trong đầu, bỏ đi giả giữ lại thực, chậm chạp tạo thành không có thiếu hụt Thiên Đạo công pháp."),t("br"),t("br"),n._v("Một canh giờ sau, tầng thứ nhất thư tịch liền bị thu nhận sạch sẽ."),t("br"),t("br"),n._v("“Đây là nhất tinh độc sư phương pháp tu luyện...”"),t("br"),t("br"),n._v("Nhìn một cái hình thành độc công, Trương Huyền đi lên lầu."),t("br"),t("br"),n._v("Cái này tàng thư điện bố cục, cùng danh sư học viện có chút tương tự, một tầng đối ứng nhất tinh, cấp bậc không có đạt tới, không cho phép quan sát cái kế tiếp đẳng cấp."),t("br"),t("br"),n._v("Mặc dù nhất tinh, nhị tinh độc sư nội dung, ban đầu ở Thanh Liên sơn mạch cũng đã học qua, nhưng mà nơi này thư tịch số lượng dự trữ, rõ ràng càng thêm phong phú, dù sao đều tới một chuyến, cũng không quan tâm thật lãng phí chút thời gian."),t("br"),t("br"),n._v("Lại nói, một chút râu ria thư tịch bên trong, có lẽ liền ghi chép như thế nào cứu chữa Ngụy Như Yên, bởi vậy, không có chút nào chủ quan, lần lượt nhìn sang."),t("br"),t("br"),n._v("Rất nhanh, tầng thứ hai nội dung cũng thu sạch ghi chép sạch sẽ."),t("br"),t("br"),n._v("Ngay sau đó lên tầng thứ ba."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hắn Vu hồn lặng lẽ chui vào tàng thư điện, quan sát thư tịch, thân thể vị trí sân nhỏ, đã gây nên sóng to gió lớn."),t("br"),t("br"),n._v("Nhìn thấy trên cây mang theo Đoàn sư huynh đám người, không ít độc sư, đều run lẩy bẩy, có chút hoảng sợ."),t("br"),t("br"),n._v("Vốn cho rằng vừa tới gia hỏa này, gặp được nhiều như vậy độc sư, sẽ mặc cho xâm lược, không dám nhiều lời, nằm mơ đều không có nghĩ đến, lại là cái quá giang long, dữ dội rối tinh rối mù!"),t("br"),t("br"),n._v("Vừa ra tay liền đem trong bọn họ cường đại nhất Đoàn Nhân sư huynh đánh ngất xỉu!"),t("br"),t("br"),n._v("“Nhanh đi bẩm báo Minh Chân phó điện chủ...”"),t("br"),t("br"),n._v("Trong đám người không biết ai hô lên."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nương theo một tiếng hô khẽ, một người vội vã hướng một cái phương hướng chạy tới."),t("br"),t("br"),n._v("Minh Chân phó điện chủ, là thất tinh độc sư, càng là vị này Đoàn Nhân sư huynh lão sư, người sau dám như thế tùy tiện, chính là dựa vào địa vị của hắn, bây giờ bị một cái vừa tới gia hỏa đánh ngất xỉu, chỉ sợ vị này phó điện chủ, sẽ giận tím mặt."),t("br"),t("br"),n._v("Một đường phi nhanh, thời gian không dài, đi tới phó điện chủ nơi ở, đang muốn đi vào, liền bị người chặn lại."),t("br"),t("br"),n._v("“Phó điện chủ đang cùng tất cả trưởng lão nghị sự, có chuyện gì, còn xin thông báo!”"),t("br"),t("br"),n._v("Hộ vệ mặt không hề cảm xúc."),t("br"),t("br"),n._v("“Cùng trưởng lão nghị sự?”"),t("br"),t("br"),n._v("Sửng sốt một chút, vị này độc sư lúc này mới nhớ tới, hôm nay là phó điện chủ cùng trưởng lão nghị sự thời gian, chần chờ một lát: “Đoàn Nhân sư huynh bị người đánh, hiện tại đang hôn mê bất tỉnh!”"),t("br"),t("br"),n._v("“Đoàn Nhân bị đánh?”"),t("br"),t("br"),n._v("Hộ vệ sững sờ."),t("br"),t("br"),n._v("“Vâng! Người này tên là Tôn Cường, là Hứa trưởng lão mang tới...” Độc sư không dám giấu diếm."),t("br"),t("br"),n._v("“Tốt, ta đi vào bẩm báo...”"),t("br"),t("br"),n._v("Biết Đoàn Nhân là Minh phó điện chủ coi trọng nhất đệ tử, gần nhất đang định họp đề bạt trở thành trưởng lão, hộ vệ không dám chần chừ, vội vã đi vào."),t("br"),t("br"),n._v("Chỉ chốc lát lần nữa trở về, vẫy vẫy tay: “Phó điện chủ để ngươi đi vào!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, vị này độc sư đi vào trong."),t("br"),t("br"),n._v("Chỉ chốc lát đi tới ở giữa nhất phòng khách, ngẩng đầu nhìn lên, nhịn không được rụt cổ một cái."),t("br"),t("br"),n._v("Gian phòng bên trong, không chỉ có Minh phó điện chủ, mặt khác hai cái phó điện chủ cũng tại, càng có loại trừ Hứa trưởng lão, Tiết trưởng lão ở bên trong hai mươi vị lục tinh đỉnh phong trưởng lão."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),n._v(" Thấy hắn đi vào, Minh phó điện chủ nhìn lại, ánh mắt như điện, mang theo uy áp."),t("br"),t("br"),n._v("Vị này Minh phó điện chủ, sáu mươi tuổi khoảng chừng niên kỉ kỷ, trên mặt sợi râu, hiện lên hoa râm sắc, toàn thân áo đen, phối hợp âm lãnh sắc mặt, cho người ta một loại không giận tự uy cảm giác."),t("br"),t("br"),n._v("“Là dạng này...”"),t("br"),t("br"),n._v("Độc sư vội vàng đem nhìn thấy tình huống nói một lần."),t("br"),t("br"),n._v("“Ngươi nói Hứa Du mang tới người, không nói hai lời, liền đem Đoàn Nhân kích thương?”"),t("br"),t("br"),n._v("Minh phó điện chủ híp mắt lại."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Độc sư gật đầu."),t("br"),t("br"),n._v("“Rất tốt, rất tốt! Một cái không biết từ nơi nào xuất hiện gia hỏa, dám ở Độc điện tìm phiền toái, thật sự là thật to gan!”"),t("br"),t("br"),n._v("Hàn quang lóe lên, Minh phó điện chủ cười lạnh thành tiếng: “Mang ta tới, ta ngược lại muốn xem xem, đến cùng hạng người gì, ai cho hắn lá gan, dám đối ta học sinh ra tay!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không dám nhiều lời, độc sư đi đầu đi ra ngoài."),t("br"),t("br"),n._v("“Minh phó điện chủ, vừa vặn chúng ta cũng không có việc gì, đã có náo nhiệt có thể nhìn, không ngại cùng đi!”"),t("br"),t("br"),n._v("“Đúng vậy a, người khác tới đến chúng ta Độc điện, đều khẩn trương lời nói đều nói không ra, cái tên này lại dám trực tiếp động thủ, ta cũng muốn nhìn xem, đến cùng là thần thánh phương nào!”"),t("br"),t("br"),n._v("Còn lại hai vị phó điện chủ, cũng đồng thời đứng lên."),t("br"),t("br"),n._v("“Chúng ta cũng muốn nhìn xem, Hứa Du đi ra ngoài một chuyến, mang theo cái gì nhân vật lợi hại!”"),t("br"),t("br"),n._v("“Loại sự tình này, sao có thể thiếu đi ta...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cái khác hai mươi vị trưởng lão, cũng đều con mắt tỏa ánh sáng, nở nụ cười."),t("br"),t("br"),n._v("Độc điện rất nhiều độc sư, chỉ có thể ở tại trên một hòn đảo, nhiều năm trước tới nay, đã sớm không có gì chuyện mới mẻ để bọn hắn chú ý, đã người mới tới này lớn lối như thế, tự nhiên muốn đi qua nhìn một chút."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thấy hai vị phó điện chủ cùng tất cả trưởng lão đều muốn đi, Minh phó điện chủ cũng không cự tuyệt, khoát tay áo, theo sát tại báo tin tức độc sư sau lưng, hướng Trương Huyền chỗ ở đi đến."),t("br"),t("br"),n._v("Rất mau tới đến trước mặt, quả nhiên thấy học sinh của mình cùng mấy cái thuộc hạ thê treo ở trên cây, trên người khung xương vỡ vụn không biết bao nhiêu khối, thương thế nghiêm trọng."),t("br"),t("br"),n._v("“Đáng ghét!”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, Minh phó điện chủ nắm đấm xiết chặt."),t("br"),t("br"),n._v("Đối phương biết rõ vị này là học sinh của mình, còn ra tay tàn nhẫn như vậy, rõ ràng không có đem hắn để vào mắt."),t("br"),t("br"),n._v("“Mở cửa!”"),t("br"),t("br"),n._v("Để cho người ta đem Đoàn Nhân cứu tỉnh, bản thân thì hướng trước mắt sân nhỏ đi tới, hừ lạnh một tiếng."),t("br"),t("br"),n._v("“Minh phó điện chủ... Tôn độc sư nói...”"),t("br"),t("br"),n._v("Canh giữ ở bên ngoài viện Lý Viên đám người, nhìn thấy vừa đưa ra nhiều như vậy phó viện trưởng cùng trưởng lão, tất cả đều sợ choáng váng, bờ môi run rẩy, nói không ra lời."),t("br"),t("br"),n._v("“Thế nào, lời của ta vô dụng?”"),t("br"),t("br"),n._v("Lông mày giương lên, Minh phó điện chủ quát."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Biết phó điện chủ uy tín không được khiêu khích, Lý Viên đành phải đem cửa sân mở ra, vốn muốn trước thời hạn báo tin vị kia Tôn Cường độc sư, hiện tại xem ra, không làm được."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Ánh mắt lạnh lùng, Minh phó điện chủ đi vào, cái khác hai vị phó điện chủ cùng tất cả trưởng lão, theo sát mà nối đuôi nhau mà vào."),t("br"),t("br"),n._v("Tất cả mọi người muốn nhìn một chút, đem Đoàn Nhân đánh như vậy thê gia hỏa, đến cùng bề ngoài ra sao."),t("br"),t("br"),n._v("Tiến vào viện, rất nhanh liền nhìn thấy một bóng người, khoanh chân ngồi trong sân ở giữa linh khí tập trung nhất chỗ, hai mắt nhắm nghiền, đang tu luyện."),t("br"),t("br"),n._v("“Ngươi chính là Tôn Cường?”"),t("br"),t("br"),n._v("Bản thân nhiều người như vậy tới, cái tên này còn có nhàn tâm tu luyện, con mắt đều không mở ra, Minh phó điện chủ vẻ mặt âm trầm đáng sợ."),t("br"),t("br"),n._v("Nói xong, chờ đợi đối phương trả lời, lại phát hiện, cách đó không xa gia hỏa... Con mắt vẫn như cũ không có mở ra, hình như cảm thấy bọn họ không tồn tại đồng dạng."),t("br"),t("br"),n._v("“Láo xược! Ngươi cũng đã biết chúng ta là ai?”"),t("br"),t("br"),n._v("Không nghĩ tới đường đường phó điện chủ bị như vậy coi thường, Minh Chân khí sắp bùng nổ."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn đều là thất tinh độc sư, địa vị kính trọng, không đứng dậy nghênh đón ngược lại cũng thôi, mí mắt đều không mở ra, quả thực phách lối tới cực điểm, không coi ai ra gì!"),t("br"),t("br"),n._v("Nhiều người như vậy đi vào, vừa lớn tiếng trách mắng, hắn không phải tin tưởng, có thể không có chút nào phát giác!"),t("br"),t("br"),n._v("Nếu thật là như vậy, khẳng định đã sớm chết thật nhiều lần, làm sao có thể sống đến bây giờ?"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Trách mắng xong, đối diện vẫn không có trả lời, hình như tên trước mắt, ngủ thiếp đi đồng dạng."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Minh phó điện chủ lửa giận thiêu đốt."),t("br"),t("br"),n._v("Gặp qua phách lối, chưa thấy qua phách lối như vậy, toàn bộ Độc điện, ba vị phó điện chủ, hai mươi vị trưởng lão đều tới, hô mấy tiếng, thế mà ngồi dưới đất, thanh âm không trở về..."),t("br"),t("br"),n._v("“Quả nhiên lá gan đủ lớn, cho là có Hứa trưởng lão chỗ dựa liền có thể muốn làm gì thì làm, vậy ta ngược lại muốn xem xem, Hứa trưởng lão có thể hay không giữ được ngươi...”"),t("br"),t("br"),n._v("Kìm nén sắp bùng nổ, Minh phó điện chủ gào lên một tiếng, chân khí trong cơ thể tuôn ra, liền muốn ra tay."),t("br"),t("br"),n._v("“Ha ha, Minh phó điện chủ, dạy bảo mặt hàng này, sao có thể để ngươi dơ bẩn tay...”"),t("br"),t("br"),n._v("Đúng lúc này, một cái tiếng cười vang lên, một vị trưởng lão đi tới."),t("br"),t("br"),n._v("(Quyển sách nhận được “Hàng năm hải ngoại được hoan nghênh nhất IP” giải thưởng, ta lên đài về sau, cảm tạ độc giả, cảm tạ mọi người, kết quả... Đều bị cắt giảm, ta lời thoại, lưu lại một nửa cũng chưa tới, đổ mồ hôi. Mặt khác, ai nói lão nhai xấu? Ngươi đi ra, tin hay không đánh không chết ngươi... Tiểu tử, ta văn học mạng giới ***, cũng không phải “Sóng” đến hư danh... Mặt khác, đề cử đại thần ek chocolate sách mới, trùng sinh chi Đế bá tinh không, mọi người có thể nhìn một chút. Cuối cùng, nguyệt phiếu khoảng cách Kiếm Thần kém rất xa, có cơ hội hay không sờ hắn một chút cái mông? Ô ô.)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1005"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1007"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);