(window.webpackJsonp=window.webpackJsonp||[]).push([[230],{583:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1184-cau-đuong-chu-nhan-thua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1184-cau-đuong-chu-nhan-thua"}},[n._v("#")]),n._v(" Chương 1184: Cẩu đường chủ nhận thua")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1184: Cẩu đường chủ nhận thua "),t("br"),t("br"),n._v("Cứ việc lĩnh ngộ sư giả chi tâm, hữu giáo vô loại."),t("br"),t("br"),n._v("Nhưng vị này Cẩu đường chủ, trong lòng hoài tư, không phân phải trái, biết người không rõ... Nhân vật như vậy, thực lực càng mạnh, đối nhân tộc càng là gieo họa, làm sao có thể đem Khưu Ngô cổ thánh lợi hại như thế chưởng pháp, truyền thừa ra ngoài?"),t("br"),t("br"),n._v("Trương Huyền tính tình hiền lành, lại không phải người hiền lành."),t("br"),t("br"),n._v("Người nào có thể thu vì đệ tử, người nào không thể nhận, có nhất định đúng mực."),t("br"),t("br"),n._v("“Là ta ý nghĩ hão huyền...”"),t("br"),t("br"),n._v("Cẩu đường chủ một mặt buồn bã."),t("br"),t("br"),n._v("Tự tay ép đối phương tiến hành xông đường, bây giờ lại muốn học tập đối phương võ kỹ, cái này sao có thể?"),t("br"),t("br"),n._v("Không có tại chỗ đem hắn giết chết, đã coi như là rất cho mặt mũi."),t("br"),t("br"),n._v("“Ta thua...” Lắc đầu, Cẩu đường chủ cao giọng tuyên bố: “Trương sư xông đường thông qua cửa thứ nhất!”"),t("br"),t("br"),n._v("Cửa thứ nhất là chiến thắng một cái Danh Sư đường tất cả danh sư, hiện tại đã hoàn thành."),t("br"),t("br"),n._v("“Chậm đã... Ta cùng Điền Thanh phó đường chủ không có đánh qua... Ta sợ hắn không chịu!”"),t("br"),t("br"),n._v("Trương Huyền đánh gãy đối phương."),t("br"),t("br"),n._v("“Ta cũng không là đối thủ, Điền Thanh tự nhiên càng thêm không phải, cho nên, đánh cùng không đánh, đã không còn ý nghĩa...”"),t("br"),t("br"),n._v("Cẩu đường chủ lắc đầu."),t("br"),t("br"),n._v("“Ta không phải là đối thủ, ta nhận thua!”"),t("br"),t("br"),n._v("Điền Thanh ôm quyền."),t("br"),t("br"),n._v("Thấy đối phương nhận thua, Trương Huyền tràn đầy tiếc hận."),t("br"),t("br"),n._v("Nói thật, hắn thật hy vọng đối phương đáp ứng, sau đó tốt đánh một trận tơi bời, đánh trước cái bán thân bất toại, vĩnh viễn không có cách nào đi lại lại nói."),t("br"),t("br"),n._v("Bất quá, đã nhận thua, liền thật không tiện động thủ."),t("br"),t("br"),n._v("Dù sao đây chỉ là xông đường, không phải giết người diệt khẩu."),t("br"),t("br"),n._v("“Danh sư tháp ở chỗ này, Trương sư đi theo ta...”"),t("br"),t("br"),n._v("Danh sư trong tháp ẩn chứa một tòa Danh Sư đường phía trong, lịch đại đỉnh phong nhất danh sư ý niệm, những người này, có đạt đến bát tinh hạ phẩm, thậm chí cao hơn... Muốn thông qua, làm cho đối phương công nhận, rất khó."),t("br"),t("br"),n._v("Bởi vậy, cửa thứ nhất khiêu chiến tất cả danh sư cũng chiến thắng, đối với xông đường tới nói, xem như rất dễ dàng."),t("br"),t("br"),n._v("Chân chính khó khăn ở đây."),t("br"),t("br"),n._v("Trong lịch sử không biết bao nhiêu thiên tài, đều bị nhốt ở đây."),t("br"),t("br"),n._v("Theo sau lưng, chỉ chốc lát nhìn thấy một cái bảy tầng tháp cao đứng sừng sững ở trước mắt, cho người ta một loại ổn trọng như biển, trấn áp khắp nơi cảm giác."),t("br"),t("br"),n._v("Biết cái này liên quan so trước đó muốn khó, Trương Huyền hít sâu một hơi, tinh thần tập trung, đẩy cửa đi vào."),t("br"),t("br"),n._v("Gặp hắn biến mất, một vị danh sư nhịn không được mở miệng: “Trương sư... Có thể thành công ư?”"),t("br"),t("br"),n._v("“Rất khó, cứ việc Thanh Nguyên đế quốc Danh Sư đường, tại bát đại phong hào đế quốc bên trong, không tính quá gần phía trước, nhưng trong lịch sử, cũng xuất hiện bốn vị bát tinh cấp bậc danh sư. Trong đó lợi hại nhất chúc sư, càng là đạt đến bát tinh trung phẩm cấp bậc! Bọn họ lưu lại ý niệm, bác học biết nhiều, chỉ sợ không phải dễ dàng như vậy thông qua!”"),t("br"),t("br"),n._v("Một vị hiểu rõ tình hình danh sư đạo."),t("br"),t("br"),n._v("“Đúng vậy a, danh sư có thể đạt tới bát tinh, xem như nhanh đạt tới toàn bộ đại lục đỉnh phong, kiến thức lượng, tâm cảnh còn là đối tu luyện đã hiểu, đều không phải là người bình thường có thể chống lại...”"),t("br"),t("br"),n._v("Vị thứ nhất danh sư gật đầu."),t("br"),t("br"),n._v("“Ai, chỉ mong Trương Huyền có thể thông qua đi, hắn có thể thông qua, thân là học sinh, cũng có vinh yên!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta cũng hi vọng hắn có thể thông qua, nhiều năm như vậy không có người xông đường, nếu như tại chúng ta nơi này xông, hơn nữa thành công, danh khí bao nhiêu, về sau ra ngoài cũng có tư cách kiêu ngạo...”"),t("br"),t("br"),n._v("Hai người khắp khuôn mặt là kỳ vọng."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Đem hai người đối thoại nghe vào trong tai, vết thương chằng chịt Cẩu đường chủ, lại không có áp chế ngực máu tươi, lại phun ra mấy miệng, lúc này mới coi như không có gì."),t("br"),t("br"),n._v("Người khác xông đường, bổn đường danh sư, đều hận không thể đem hắn chém giết, phía bên mình ngược lại tốt, tất cả đều ước gì đối phương thành công..."),t("br"),t("br"),n._v("Đây chính là các ngươi vị trí Danh Sư đường, một khi bị người xông đường thành công... Các ngươi cũng sẽ rất mất mặt được không? Kiêu ngạo cái rắm ah!"),t("br"),t("br"),n._v("“Chính nghĩa thì được ủng hộ, mất đạo quả trợ... Cẩu đường chủ, bất kể Trương sư có thể hay không thông qua, ngươi đã mất lòng người, e là cho dù tổng bộ không truy cứu trách nhiệm của ngươi, trong đường danh sư, đoán chừng cũng không có người nào bằng lòng nghe theo ngươi phân phó...”"),t("br"),t("br"),n._v("Nhìn thấy vẻ mặt của hắn, Ngô sư biết nghĩ cái gì, lắc đầu."),t("br"),t("br"),n._v("Điền Thanh phó đường chủ mưu hại Trương sư sự, cho dù rất bí ẩn, nhiều như vậy danh sư đồng thời trở về, lại gây ra động tĩnh lớn như vậy, khẳng định cũng lại giấu diếm không được."),t("br"),t("br"),n._v("Chỉ bằng vào sức một mình, liền chống lại cả tòa Danh Sư đường tất cả Chiến sư, hơn nữa đem hắn thu làm học sinh... Cứ việc chỉ là rưỡi sư tình nghĩa, nhưng cũng đầy đủ làm cho tất cả mọi người đổi cái nhìn."),t("br"),t("br"),n._v("Một vị đường chủ, vì không gây chuyện tình, yên lặng về hưu... Buông thả phó đường chủ mưu hại cái khác danh sư... Đầu này tội danh, liền sẽ để vô số danh sư phỉ nhổ, lại khó xoay người."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Cẩu đường chủ mặt xám như tro."),t("br"),t("br"),n._v("Từ khi đối phương hô lên xông đường bắt đầu, hắn cũng biết, bản thân vị đường chủ này, lại nghĩ xoay người khó khăn."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới... Trước đó còn cùng một chỗ kề vai chiến đấu Chiến sư nhóm, phản bội triệt để như vậy."),t("br"),t("br"),n._v("“Chẳng lẽ... Ta thật làm sai?”"),t("br"),t("br"),n._v("Thân thể quơ quơ, vẻ mặt buồn bã."),t("br"),t("br"),n._v("Năm trăm năm đường chủ, chức cao đã quen, để hắn không còn trước kia bình thường tâm, tất cả đều lấy địa vị của mình cân nhắc... Cuối cùng nhận được loại này báo ứng, cũng coi là trừng phạt đúng tội."),t("br"),n._v(" “Vị này Trương sư... Đến cùng lai lịch ra sao?”"),t("br"),t("br"),n._v("Kìm nén ngực khó chịu, cũng nhịn không được nữa, hỏi thăm đi ra."),t("br"),t("br"),n._v("“Hắn là ta gặp qua cố gắng nhất, thiên tài nhất nhân vật...” Ngô sư nắm đấm xiết chặt, trong mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Một cái bất kể lại nguy hiểm, lại gian nan, đều muốn đọc sách người, chỉ phần này học tập thái độ, liền là hắn làm không được, lại cần nghiêm túc học tập."),t("br"),t("br"),n._v("Có lẽ, chính vì vậy cố gắng, học giỏi như vậy, mới có hắn thành tựu của ngày hôm nay, tuổi còn trẻ, liền nắm giữ chính mình cũng không cách nào so sánh lực lượng!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi vào danh sư tháp cửa lớn, Trương Huyền không có ngừng lại, chậm rãi đi lên đi."),t("br"),t("br"),n._v("Danh sư tháp, trước đó xông qua một lần, biết quy tắc."),t("br"),t("br"),n._v("Trong này lưu giữ ý niệm, cấp bậc cũng rất cao, bởi vậy, không có khả năng khiêu chiến thực lực, trên cơ bản đều là khảo hạch một chút liên quan tới danh sư đồ vật, tỷ như: Giảng bài, nhìn người thiếu hụt, chỉ điểm võ kỹ..."),t("br"),t("br"),n._v("Bởi vậy, xông cái này liên quan, muốn không phải thực lực, mà là bác học nhất kiến thức."),t("br"),t("br"),n._v("Đây cũng là danh sư cái kia có được."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đi về phía trước không xa, một cái hư ảnh xuất hiện ở trước mắt, chính là cửa thứ nhất thủ hộ giả, Thanh Nguyên đế quốc Danh Sư đường, mười ba ngàn năm trước đi ra bát tinh hạ phẩm danh sư, Triệu Chân."),t("br"),t("br"),n._v("“Tuổi còn trẻ liền xông đường, ngươi đây là bị ủy khuất gì?”"),t("br"),t("br"),n._v("Thấy đi tới xông đường, cũng chỉ là người hai mươi tuổi thanh niên, Triệu Chân danh sư nhíu nhíu mày."),t("br"),t("br"),n._v("“Đương đại đường chủ, vì bảo vệ vị trí của mình, ô ta trong sạch...”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền khom người, đem chuyện nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Còn có loại sự tình này? Thật sự là một đời không bằng một đời! Nhìn tới Danh Sư đường cần chỉnh đốn...”"),t("br"),t("br"),n._v("Nghe xong giải thích, Triệu Chân lắc đầu, lần nữa nhìn qua: “Ngươi mặc dù rất oan ức, nhưng thân là Thanh Nguyên đế quốc Danh Sư đường thủ hộ giả, không thể tuỳ tiện cho ngươi thông qua, nếu không, người người có oan ức liền đến xông đường, Danh Sư đường uy nghiêm ở đâu?”"),t("br"),t("br"),n._v("“Đúng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Khảo hạch của ta, rất đơn giản, chỉ điểm võ kỹ!”"),t("br"),t("br"),n._v("Thấy đối phương thái độ thành khẩn, Triệu Chân hết sức hài lòng nhẹ gật đầu: “Ta thi triển một bộ võ kỹ, ngươi tìm ra không đủ, có thể tìm ra một chỗ, liền xem như thông qua...”"),t("br"),t("br"),n._v("“Tốt!” Trương Huyền vẻ mặt vui mừng."),t("br"),t("br"),n._v("Thấy trước mắt vị thanh niên này, không những không có khẩn trương, ngược lại tinh thần thả lỏng, một mặt cảm giác như trút được gánh nặng, Triệu Chân nhíu nhíu mày: “Đừng tưởng rằng tìm ra một chỗ thiếu hụt, mười phần đơn giản, ta thân là bát tinh hạ phẩm danh sư, thi triển ra võ kỹ, tự nhiên đều là đắc ý nhất, sửa chữa qua vô số lần, cảm thấy không có vấn đề! Một chỗ thiếu hụt cứ việc ít, rất có thể không ít thất tinh đỉnh phong danh sư, đều cần tìm tới mấy ngày, mấy tháng, thậm chí mấy năm, mới có thể có phát hiện!”"),t("br"),t("br"),n._v("“Biết, nhanh lên một chút bắt đầu đi! Ta còn muốn mau chóng đi cửa ải tiếp theo...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tràn đầy không vui, Triệu Chân danh sư hất lên ống tay áo."),t("br"),t("br"),n._v("Bản thân đường đường bát tinh danh sư, tự mình ra đề mục, tự nhiên không tưởng tượng đơn giản như vậy, cái tên này lại như vậy tùy tiện, còn muốn mau chóng đi tới một cửa ải... Ngươi liền xác định, có thể thông qua?"),t("br"),t("br"),n._v("“Ta chiêu này võ kỹ kêu Thu Phong Triền Ti thủ! Là ta quan sát gió thu quét xuống lá rụng, không cách nào bắt lấy, lại bị mạng nhện dính trụ, có cảm giác mà sáng lập ra, hiện tại ta thi triển một chút cho ngươi xem một chút...”"),t("br"),t("br"),n._v("Triệu Chân danh sư mười ngón tay xòe ra, từng đạo giả lập chân khí, từ ngón tay bắn ra, như là tạo thành mạng nhện, đem bốn phương tám hướng đều bao phủ ở bên trong, nếu có người tại trước mặt, khẳng định sẽ bị cuốn lấy, muốn chạy trốn cũng khó khăn."),t("br"),t("br"),n._v("“Ta bộ võ kỹ này chưa hề đối với người thi triển qua, cho nên ngươi cũng không cần suy nghĩ gì bàng môn tà đạo, tưởng là dựa vào nói bậy, liền có thể lừa dối qua ải. Tổng cộng mười chiêu, một chiêu so một chiêu lợi hại, đây là chiêu thứ nhất, Kim Ti Triền Tuyến... Ta đem mười chiêu thi triển xong, sẽ cho hai ngươi canh giờ, nếu như có thể tìm ra một chỗ thiếu hụt, liền xem như thông qua, tìm không ra, liền ra ngoài đi!”"),t("br"),t("br"),n._v("Một lần thi triển, Triệu Chân danh sư vừa nói."),t("br"),t("br"),n._v("Bình thường xông đường, đều sẽ cho đối phương hai ngày thời gian, trước mắt vị này tự cao tự đại, nói năng lỗ mãng, hắn trực tiếp giảm bớt đến hai canh giờ, chính là muốn bỏ đi nhuệ khí của đối phương."),t("br"),t("br"),n._v("Đợi sau hai canh giờ, không phát hiện được, lại cho cùng thư thả."),t("br"),t("br"),n._v("“Không cần thi triển mười chiêu, cũng không cần hai canh giờ, ta hiện tại liền nói đi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn xông đường chỉ là chịu đến sỉ nhục tạm thời khởi ý, hiện tại muốn làm, còn là mau chóng khảo hạch thất tinh danh sư, nghĩ biện pháp kiếm tiền tăng thực lực lên, không có rảnh ở đây chậm trễ quá nhiều."),t("br"),t("br"),n._v("“Hiện tại liền nói?”"),t("br"),t("br"),n._v("Triệu Chân sững sờ."),t("br"),t("br"),n._v("Hắn mới dùng một chiêu, liền muốn nói... Chẳng lẽ, một chiêu ngươi liền nhìn ra thiếu hụt?"),t("br"),t("br"),n._v("“Ngươi bộ này Thu Phong Triền Ti thủ, nhìn xem không sai, hẳn là tại Thánh vực trung kỳ võ kỹ bên trong, đều hàng bên trên gần phía trước, sau khi luyện thành, ra tay quấn quanh, kẻ địch khó mà chạy trốn. Bất quá... Thiếu hụt cũng là rất nhiều, ta mới vừa nhìn một cái, thô sơ giản lược đánh giá một chút, có lẽ... Có 37 chỗ thiếu hụt cùng sơ hở!”"),t("br"),t("br"),n._v("Trương Huyền mở miệng."),t("br"),t("br"),n._v("“37 chỗ?” Triệu Chân khuôn mặt trầm xuống."),t("br"),t("br"),n._v("Ta mười chiêu võ kỹ mới dùng một chiêu, có thể hay không nhìn ra thiếu sót cũng còn không biết, há miệng ra 37 chỗ?"),t("br"),t("br"),n._v("Trong nháy mắt, trong lòng đối vị này tới xông đường gia hỏa, không còn một tia hảo cảm."),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("Không nhìn ra sắc mặt hắn thay đổi, Trương Huyền còn tưởng rằng đối phương không tin, cảm thán một tiếng: “Sáng tạo võ kỹ, cần thực tiễn rèn luyện, cùng người chiến đấu mới có thể phát huy uy lực lớn nhất, ngươi không có truyền thụ qua người khác, cũng liền không có rèn luyện qua, chỉ có 37 chỗ sai lầm, nói thật, cũng xem là không tệ...”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Triệu Chân ý thức bị tức đến không ngừng lay động, suýt chút nữa không có kháng trụ tại chỗ sụp đổ: “Vậy ngươi nói cho ta một chút, có cái nào 37 chỗ... Không nói ra được, có tin ta hay không một bàn tay quất chết ngươi?”"),t("br"),t("br"),n._v("(Đầu tháng, tiếp tục cầu nguyệt phiếu! Mặt khác, vương hàm húc cái kia là tiết mục ngắn, mọi người có thể chú ý ta công chúng số, tối nay đem cái video này phát ra tới, thanh âm siêu ma tính. Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý là được!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1192"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1194"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);