(window.webpackJsonp=window.webpackJsonp||[]).push([[236],{589:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1190-mat-na-đong-xanh-yeu-cau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1190-mat-na-đong-xanh-yeu-cau"}},[n._v("#")]),n._v(" Chương 1190: Mặt nạ đồng xanh yêu cầu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1190: Mặt nạ đồng xanh yêu cầu"),t("br"),t("br"),n._v("“Không đúng! Nếu như là Thánh Nhân quý tộc ra tay, khẳng định đường đường chính chính, trực tiếp đem ta nghiền ép, không cần thiết lặng lẽ làm việc...”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, mặt nạ đồng xanh lông mày nhíu một cái, kịp phản ứng."),t("br"),t("br"),n._v("Thánh Nhân Trương gia, mệnh danh danh sư đại lục đệ nhất gia tộc, bên trong cao thủ nhiều như mây, nhiều vô số kể, nếu thật là biết bức họa này bí mật, khẳng định đã sớm tìm tới, một bàn tay đem bản thân đập chết, không cần thiết che lấp."),t("br"),t("br"),n._v("Mặc dù có danh sư đường tọa trấn, phần lớn chuyện chú ý quy tắc, nhưng... Tại chính thức thực lực trước mặt, quy tắc gì gì đó, cũng sẽ bị nghiền ép."),t("br"),t("br"),n._v("Trương gia, đang có loại này nghiền ép tất cả thực lực."),t("br"),t("br"),n._v("“Dù sao ta nhìn thấy, liền là mỹ nhân đi tắm...”"),t("br"),t("br"),n._v("Thấy đối phương không tin, Trương Huyền lắc đầu: “Thực sự không tin, có thể tìm người hỏi một chút, ta Trương Huyền, từ trước tới nay thanh danh tốt...”"),t("br"),t("br"),n._v("“Đủ rồi! Cho ngươi tới, là muốn cho bức họa này làm khôi phục!”"),t("br"),t("br"),n._v("Biết tiếp tục xoắn xuýt vấn đề này, sẽ không dứt, chậm trễ rất nhiều thời gian, mặt nạ đồng xanh hừ lạnh."),t("br"),t("br"),n._v("Cứ việc trước đó tác phẩm hội họa bị người thêm vào tân nội dung, nhưng chỉ cần thư họa sư trình độ đầy đủ, còn có thể khôi phục."),t("br"),t("br"),n._v("Dù sao vết mực niên đại khác biệt, chỉ cần trình độ đạt tới, không khó lắm."),t("br"),t("br"),n._v("Trước mắt vị này, có thể tuỳ tiện phá vỡ phong ấn, làm được bản thân cùng Mông Xung hội trưởng đều làm không được sự, tại thư hoạ trên chức nghiệp, đã đạt đến Thanh Nguyên đế quốc cao nhất trình độ, so với lúc trước Vô Bi lão nhân, đều không kém chút nào, từ hắn xuất thủ, hoàn thành khả năng cực lớn."),t("br"),t("br"),n._v("Trương Huyền tiếp tục lắc đầu: “Không phải ta không muốn, mà là, làm không được...”"),t("br"),t("br"),n._v("“Làm không được?”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh cười lạnh, ánh mắt mang theo sát khí: “Ngươi nhìn tới còn không có hiểu rõ tình cảnh của mình! Ngươi bây giờ bị ta bắt tới chính là linh hồn... Nói cách khác... Thân thể đã chết, hiện tại là cái người chết!”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh nói."),t("br"),t("br"),n._v("“Người chết?” Trương Huyền lộ ra hoảng sợ chi ý: “Ta chết đi?”"),t("br"),t("br"),n._v("Cúi đầu nhìn một cái, nhìn thấy phai mờ thân thể, lập tức mắt tối sầm lại, trực tiếp bị dọa ngất tới."),t("br"),t("br"),n._v("“...” Nhìn thấy kẻ trước mắt này động tác, mặt nạ đồng xanh nắm tóc, có chút điên cuồng."),t("br"),t("br"),n._v("Đối phương linh hồn bị hắn dùng bí pháp rút đi, chỉ cần không phải đầu óc có vấn đề, liền khẳng định biết!"),t("br"),t("br"),n._v("Bản thân nói chuyện, cố ý giả bộ kinh ngạc bộ dáng, còn giả vờ ngất... Đại ca, ngươi là hồn phách, choáng sao?"),t("br"),t("br"),n._v("Hồn thể bị giam cầm ở, chạy không thoát, cũng tán loạn không được, thân thể cũng đã chết... Dựa theo tình huống bình thường, không nên rất sợ hãi, không dám nhiều lời, ngoan ngoãn nghe theo phân phó ư?"),t("br"),t("br"),n._v("Bộ dáng này, làm sao cái tình huống?"),t("br"),t("br"),n._v("“Không cần phải giả bộ đâu, cái tế đàn này, đừng nói muốn choáng, muốn chết đều làm không được...”"),t("br"),t("br"),n._v("Khí hận không thể đi lên đem trước mắt vị này một chưởng vỗ chết, mặt nạ đồng xanh cắn răng nói."),t("br"),t("br"),n._v("“Choáng không được ah...” Trương Huyền tràn đầy tiếc nuối lần nữa ngồi dậy: “Quên đi, trước không choáng, có điều, ngươi nói, ta đích xác làm không được!”"),t("br"),t("br"),n._v("Trong bức họa ẩn giấu đi Khổng sư hư ảnh, dùng mỹ nhân đi tắm hình che lấp, liền đã định trước không có khả năng nói ra, muốn khôi phục, tuyệt không có khả năng."),t("br"),t("br"),n._v("Thấy cái tên này quả nhiên giả vờ ngất, hơn nữa một điểm xấu hổ đều không có, mặt nạ đồng xanh khí sắp nổ tung, còn là cố nín lại, híp mắt lại, trong giọng nói mang theo âm trầm cùng lạnh lùng: “Đừng tưởng rằng, là hồn phách, ta liền không có cách nào giáo huấn ngươi! Có thể đưa ngươi từ một nơi khác, bắt đến nơi đây, tự nhiên biết, một chút lợi hại bí pháp, có thể để ngươi muốn sống không được, muốn chết không xong!”"),t("br"),t("br"),n._v("“Dù sao đều đã chết, tốt nhất đem ta giết chết được rồi!” Mí mắt vừa nhấc, Trương Huyền một mặt lười biếng nói."),t("br"),t("br"),n._v("“Ngươi...” Ngực nhấp nhô, mặt nạ đồng xanh hô hấp khí gấp rút, cũng không biết là bị tức còn là kìm nén: “Đã ngươi không biết tốt xấu, vậy ta liền thi triển sưu hồn chi thuật, sưu hồn qua đi, ngươi sẽ hồn phi phách tán, hoàn toàn tử vong!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nói hết cũng không cần Trương Huyền trả lời, bàn tay lăng không một trảo, một cỗ lực lượng, lập tức hướng nghiền ép mà tới."),t("br"),t("br"),n._v("“Là... Vu hồn sư thủ đoạn!”"),t("br"),t("br"),n._v("Nhìn thấy động tác của hắn, Trương Huyền mắt sáng lên."),t("br"),t("br"),n._v("Lần này cam tâm tình nguyện bị đối phương bắt tới, liền là muốn nhìn một chút cái tên này đến cùng là ai, đến cùng muốn làm gì, không nghĩ tới đối phương trực tiếp thi triển ra vu hồn sư thủ đoạn..."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Trong lòng khiếp sợ, có điều, Trương Huyền cũng không chống cự đối phương công kích mà đến lực lượng, mà là mặc cho đi vào trong óc."),t("br"),t("br"),n._v("Bởi vì, đối phương vừa thi triển hắn liền đã nhìn ra, cũng không phải là sưu hồn thuật, mà là một loại thăm dò Hồn Thuật."),t("br"),t("br"),n._v("Sưu hồn thuật tương đối bạo lực, một khi thi triển, quả thực có thể biết đối phương trong đầu lưu giữ tin tức, nhưng tương tự sẽ phá hư hồn phách, cuối cùng dẫn đến hồn phi phách tán, thậm chí tử vong, thăm dò Hồn Thuật khác biệt, có thể dò xét bản thân muốn biết nhất bí mật, lại không làm thương hại đối phương."),t("br"),t("br"),n._v("Nhìn tới cái tên này, vẫn là hi vọng hắn có thể ra tay, khôi phục tác phẩm hội họa, không dám đem bản thân trực tiếp giết chết."),t("br"),t("br"),n._v("“Ta ngược lại muốn xem xem, ngươi đến cùng biết chút ít cái gì!”"),t("br"),t("br"),n._v("Quát lạnh một tiếng, mặt nạ đồng xanh ý niệm lập tức ở Trương Huyền trong ý thức dạo chơi, rất nhanh biết một chút muốn biết chuyện."),t("br"),t("br"),n._v("“Thế mà thật không biết phong ấn lại bí mật...”"),t("br"),t("br"),n._v("Thấy được càng nhiều, chân mày nhíu càng chặt."),t("br"),t("br"),n._v("Đi qua thăm dò hồn, hắn nhìn ra, đối phương quả thực đối phong ấn lại nội dung, không có chút nào hiểu rõ tình hình, nói cách khác, bộ kia mỹ nhân đi tắm hình có lẽ cùng hắn không quan hệ!"),t("br"),n._v(" Hơn nữa đi qua dò xét, đó có thể thấy được, đối phương chỉ là tính cách cảnh trực, danh khí rất tốt không nói, còn là cái Chính Trực có thiên phú ưu tú danh sư."),t("br"),t("br"),n._v("“Nhìn tới ta đoán sai...”"),t("br"),t("br"),n._v("Xem hết những này, mặt nạ đồng xanh ý niệm lui đi ra."),t("br"),t("br"),n._v("Còn tưởng rằng, là cái tên này sửa đổi tác phẩm hội họa, hiện tại xem ra, quả thực không phải, bất quá... Đã bắt tới, nhất định phải để hắn nghĩ biện pháp khôi phục trước đó nội dung, nếu không, những năm này vất vả, tất cả đều phải trả chi chảy về hướng đông."),t("br"),t("br"),n._v("“Quả nhiên là vu hồn sư, hơn nữa cùng Điền Thanh đám người có quan hệ mật thiết...”"),t("br"),t("br"),n._v("Đối phương ý niệm đi vào bản thân thức hải, Trương Huyền cũng đang lặng lẽ dò xét đối phương."),t("br"),t("br"),n._v("Chỉ bất quá, đạo ý niệm này quá nhỏ, ẩn chứa nội dung không nhiều, nếu không, mục đích của đối phương, dụng ý, sẽ bị dò xét rõ rõ ràng ràng."),t("br"),t("br"),n._v("Hắn đối Vu hồn lý giải, vượt xa quá đối phương, sắp đặt biểu hiện giả dối, lừa gạt đi qua, còn là rất dễ dàng."),t("br"),t("br"),n._v("Đi qua điều tra, Trung Thanh Vương cùng Điền Thanh, nên đều cùng hắn có mật thiết nào đó quan hệ, nguyên nhân chính là như vậy, mới có thể để cho Điền Thanh, mạo hiểm đi dụ dỗ Ngô sư đám người, hắn lặng lẽ núp ở nơi này, đem linh hồn của mình bắt."),t("br"),t("br"),n._v("“Ta lúc đầu muốn sưu hồn, chỉ là một khi như vậy, ngươi hẳn phải chết không nghi ngờ, thật sự không còn có cái gì nữa... Hiện tại cho ngươi hai con đường đi!”"),t("br"),t("br"),n._v("Biết trước mắt vị này, quả thực hoàn toàn không biết gì cả, mặt nạ đồng xanh hừ một tiếng: “Thứ nhất, giúp ta khôi phục tác phẩm hội họa, thành công, ta không chỉ không giết ngươi, còn có thể cho ngươi lần nữa sống lại, thậm chí so hiện tại càng mạnh!”"),t("br"),t("br"),n._v("“Thứ hai, sưu hồn, hoàn toàn biến mất ở trong thiên địa! Nói thật, ngươi đối thư hoạ hiểu rõ mặc dù rất nhiều, nhưng đạt tới như ngươi loại này thực lực thư họa sư, toàn bộ đại lục không biết bao nhiêu, ngươi không đồng ý, ta sớm muộn gì đều có thể đem phía trên nội dung khôi phục!”"),t("br"),t("br"),n._v("“Lần nữa sống lại?” Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Không sai!” Mặt nạ đồng xanh cổ tay khẽ đảo, một cái thân thể xuất hiện ở trước mắt: “Đây là Vô Hồn Kim Nhân, chỉ cần ngươi đáp ứng điều kiện thứ nhất, ta có thể để cho hồn phách của ngươi ở tạm trong đó, như vậy chẳng khác nào có thân thể, có thể cùng người bình thường đồng dạng sinh hoạt, sinh tồn!”"),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân...”"),t("br"),t("br"),n._v("Đối phương Vô Hồn Kim Nhân, so trước đó Trương Huyền đã gặp, cấp bậc cao quá nhiều, hẳn là có Nguyên Thần cảnh cường giả đỉnh phong thân thể luyện chế mà thành, không chỉ có như vậy, thủ pháp cũng mười phần tinh diệu, cùng người thật giống nhau như đúc, mảy may đều nhìn không ra vấn đề."),t("br"),t("br"),n._v("Có thể nói, linh hồn thật muốn tiến vào bên trong, thật cùng sống lại đồng dạng, bình thường thất tinh danh sư, chỉ sợ đều rất khó phát giác."),t("br"),t("br"),n._v("“Thân là danh sư, cho dù đối vu hồn sư chức nghiệp không hiểu rõ, Vô Hồn Kim Nhân, cũng hẳn là nghe nói qua chứ! Không chỉ có thể để cho thực lực ngươi bạo tăng, còn có thể cho ngươi thay đổi thân phận, trở thành nhân vật quyền cao chức trọng!”"),t("br"),t("br"),n._v("Gặp hắn khiếp sợ, mặt nạ đồng xanh cười lạnh."),t("br"),t("br"),n._v("“Trở thành nhân vật quyền cao chức trọng? Chẳng lẽ... Điền Thanh cùng Trung Thanh Vương, đều là Vô Hồn Kim Nhân?”"),t("br"),t("br"),n._v("Nhớ tới một việc, Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Điền Thanh có thể trở thành Danh Sư đường phó đường chủ, bản thân thành phần khẳng định không có vấn đề, trước kia kinh nghiệm tất nhiên cũng là trong sạch, sở dĩ phản bội nhân tộc, chẳng lẽ là bị người luyện chế thành Vô Hồn Kim Nhân, sau đó áp đặt vào một cái khác hồn phách?"),t("br"),t("br"),n._v("Còn có Trung Thanh Vương..."),t("br"),t("br"),n._v("Thật muốn như vậy, liền thật rất đáng sợ."),t("br"),t("br"),n._v("Đường đường Vô Hồn Kim Nhân, biến thành phó đường chủ đều không có người phát hiện... Mấu chốt nhất là, hai cái này chỉ là muốn giết chính mình mới bại lộ... Như vậy không có bại lộ, lại sẽ có bao nhiêu?"),t("br"),t("br"),n._v("Bí pháp rút ra linh hồn, sau đó thay đổi một cái khác linh hồn đi vào đối phương trong cơ thể..."),t("br"),t("br"),n._v("Khó trách lúc trước Danh Sư đường muốn tiêu diệt vu hồn sư, không lưu tình chút nào, đơn cái này một cái năng lực, cũng quá đáng sợ!"),t("br"),t("br"),n._v("Bởi vì, một khi đại quy mô thi triển, ngươi căn bản không biết, bên người đến cùng là bạn, còn là kẻ địch."),t("br"),t("br"),n._v("“Trung Thanh Vương cũng không phải là Vô Hồn Kim Nhân, thứ này cũng không phải tốt như vậy luyện chế!”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh lắc đầu."),t("br"),t("br"),n._v("“Không phải?”"),t("br"),t("br"),n._v("“Hắn chỉ là hiệu trung với ta đồ chơi thôi!” Mặt nạ đồng xanh hừ một tiếng."),t("br"),t("br"),n._v("“Cái kia... Điền Thanh đâu?”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục xem tới."),t("br"),t("br"),n._v("“Điền Thanh?”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh cười nhạo một tiếng, không có trả lời, mà là nhìn qua: “Muốn biết ta dưới mặt nạ dung mạo ư?”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu: “Nếu như có thể cho ta biết ngươi đến cùng là ai, liền có khả năng tin tưởng lời của ngươi, đáp ứng ngươi điều kiện! Nếu không, dù sao đều đã chết, không cần thiết vì giết ta người giúp một tay!”"),t("br"),t("br"),n._v("Đối với vị này mặt nạ đồng xanh hắn một mực rất ngạc nhiên, chỉ bất quá, cái tên này từ khi nhìn thấy bản thân, liền không có thi triển qua võ kỹ, vừa rồi thăm dò Hồn Thuật, là hồn sư đặc thù thủ đoạn, không tính là võ kỹ."),t("br"),t("br"),n._v("Bởi vậy, trong đầu một mực không có hình thành sách, cũng không biết đến cùng là ai."),t("br"),t("br"),n._v("Nếu là có thể tháo mặt nạ xuống tự nhiên tốt nhất."),t("br"),t("br"),n._v("“Ta che lấp dung mạo, chỉ là vì không muốn để cho những người khác phát hiện, ngươi bây giờ trốn không thoát, sinh tử chỉ ở ta nhất niệm bên trong, cho ngươi xem một chút cũng không sao...”"),t("br"),t("br"),n._v("Cười lạnh một câu, mặt nạ đồng xanh đưa tay chậm rãi đem mặt nạ trên mặt lấy xuống."),t("br"),t("br"),n._v("Đối phương hồn phách bị bản thân khống chế, sinh tử toàn ở một ý niệm, căn bản không sợ làm ra cái gì phản kháng đến, cho nên, đối với hắn mà nói, cho nhìn chân dung cũng không tính là gì."),t("br"),t("br"),n._v("Gặp mặt cỗ mở ra, Trương Huyền vội vàng nhìn sang, chỉ nhìn một cái, con ngươi co rụt lại, tràn đầy khó tin."),t("br"),t("br"),n._v("“Thế nào, tại sao là ngươi?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1198"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1200"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);