(window.webpackJsonp=window.webpackJsonp||[]).push([[239],{592:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1191-ta-la-thanh-đien-hoang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1191-ta-la-thanh-đien-hoang"}},[n._v("#")]),n._v(" Chương 1191: Ta là Thanh Điền Hoàng!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1191: Ta là Thanh Điền Hoàng!"),t("br"),t("br"),n._v("Xuất hiện ở trước mắt, không phải người khác, chính là Điền Thanh phó đường chủ!"),t("br"),t("br"),n._v("Hắn không phải đang cùng Ngô sư đám người chiến đấu ư?"),t("br"),t("br"),n._v("Mấu chốt nhất là, bản thân lần thứ nhất nhìn thấy vị này thời điểm, Điền Thanh đang bị giam giữ tại Danh Sư đường, căn bản không có đi ra... Làm sao sẽ biến thành vị này mặt nạ đồng xanh người?"),t("br"),t("br"),n._v("Trương Huyền tràn đầy khó tin."),t("br"),t("br"),n._v("Đã ngoài dự liệu của hắn."),t("br"),t("br"),n._v("“Không sai, Điền Thanh cùng ta là cùng một người!” Điền Thanh phó đường chủ gật đầu."),t("br"),t("br"),n._v("Cái tên này, từ khi bị bắt tới, vẫn không sợ vậy không lo lắng, hiện tại bộ dáng này, để hắn cực kỳ thoả mãn."),t("br"),t("br"),n._v("“Không đúng... Ngươi không phải Điền Thanh, đoán không sai, hẳn là Thanh Điền Hoàng đi!”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Trương Huyền đem suy đoán của chính mình nói ra."),t("br"),t("br"),n._v("Mấy ngày nay hắn một mực hoài nghi, Điền Thanh, vô cùng có khả năng liền là cái gọi là Thanh Điền Hoàng, nếu không, không có khả năng tìm bản thân phiền phức, cố ý hãm hại."),t("br"),t("br"),n._v("Chỉ là có chút tỉ mỉ không nghĩ ra, không dám xác nhận thôi."),t("br"),t("br"),n._v("“Ngươi quả nhiên thông minh, có điều, không nên nói đi ra...”"),t("br"),t("br"),n._v("Điền Thanh phó đường chủ thanh âm trở nên âm trầm vô cùng, một cỗ nồng đậm sát lục chi khí, cuốn tới, để cho người ta trong nháy mắt tựa như lâm vào luân hồi địa ngục, khó mà chống lại."),t("br"),t("br"),n._v("Cỗ này sát lục chi khí, so Thiên Diệp Vương đều muốn tinh thuần, ý thức hơi kém, khả năng bị tức tức một phúc xây, liền sẽ không chịu nổi, đã hôn mê."),t("br"),t("br"),n._v("“Đã ngươi biết ta là ai, nên rõ ràng, Dị Linh tộc nhân cùng nhân tộc ở giữa mâu thuẫn, không thể điều hòa... Quy thuận ta, không chỉ có thể sống, còn hưởng thụ vinh hoa phú quý! Không đồng ý... Ta vậy hết cách rồi, đành phải diệt sát!”"),t("br"),t("br"),n._v("Thanh Điền Hoàng không có phủ nhận, mà là lạnh lùng nhìn qua, giống như là nhìn xem người chết."),t("br"),t("br"),n._v("Đối phương đã phán đoán ra, tương đương với vạch mặt, cũng không cần phải lại tiến hành ngụy trang."),t("br"),t("br"),n._v("“Ngươi là Thanh Điền Hoàng, cũng là Điền Thanh... Vậy bên ngoài cùng Ngô sư đám người chiến đấu là ai? Ngươi... Một cái Dị Linh tộc nhân, cho dù ngụy trang khá hơn nữa, nắm giữ Danh Sư đường quyền lợi, chỉ sợ cũng rất dễ dàng bị nhận ra phát hiện đi!”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Đây chính là hắn vẫn nghĩ không thông địa phương."),t("br"),t("br"),n._v("Nếu như Thanh Điền Hoàng thật sự là Điền Thanh, ngụy trang thành bình thường danh sư, người khác nhìn không ra ngược lại cũng thôi, nhưng nắm giữ một phương quyền lợi, đều không có bị phát hiện, hắn đều có chút không tin."),t("br"),t("br"),n._v("Danh Sư đường thật như vậy mù, liền Dị Linh tộc nhân lẫn vào cao tầng cũng không biết, khẳng định đã sớm hủy diệt."),t("br"),t("br"),n._v("“Điền Thanh là ta một đạo ý niệm đầu thai, danh sư thân phận, là từng chút một khảo hạch đi ra, chân thực tồn tại! Vì cái thân phận này, ta chuẩn bị trọn tám trăm năm, không nghĩ tới... Bị ngươi phá hư, ngươi hẳn phải biết, ta có bao nhiêu muốn giết ngươi!”"),t("br"),t("br"),n._v("Thanh Điền Hoàng cười lạnh, trong mắt sát cơ như nước thủy triều."),t("br"),t("br"),n._v("Vì Điền Thanh cái thân phận này, hắn hao tốn không biết giá lớn bao nhiêu, thậm chí không tiếc tổn hại vô số tộc nhân sinh mệnh."),t("br"),t("br"),n._v("Nếu không phải trước mắt vị này, khẳng định vẫn là Danh Sư đường phó đường chủ, quản lý tất cả quyền lợi!"),t("br"),t("br"),n._v("800 năm vất vả, 800 năm trả giá, kết quả đều bị cứ thế mà phá vỡ."),t("br"),t("br"),n._v("Đương nhiên, những năm này mượn nhờ Danh Sư đường cùng danh sư, cũng tìm được tin tức rất quan trọng, tìm được muốn vật phẩm."),t("br"),t("br"),n._v("Khoảng cách thành công, chỉ kém cuối cùng một bước nhỏ."),t("br"),t("br"),n._v("Nếu không nào có tâm tư cùng vị này nói nhảm nhiều như vậy, khẳng định vừa thấy mặt liền đem nó chém giết."),t("br"),t("br"),n._v("“Ý niệm đầu thai?” Trương Huyền sững sờ, lập tức giật mình."),t("br"),t("br"),n._v("Khó trách căn cứ quan sát của hắn, vị kia Điền Thanh, nguyên thần bên trên có rất lớn thiếu hụt, không có gì bất ngờ xảy ra, nên rất khó đạt tới Xuất Khiếu cảnh, vốn cho rằng là trong tu luyện vấn đề xuất hiện, nguyên lai... Chỉ là một đạo ý niệm đầu thai!"),t("br"),t("br"),n._v("Ý niệm bản thân liền không được đầy đủ, tu luyện tới Nguyên Thần cảnh liền xem như đỉnh phong, muốn xuất khiếu, gần như không có khả năng."),t("br"),t("br"),n._v("Bởi vì ý niệm đầu thai, tương đương với lần nữa có sự sống, thân phận chân thật liền là Điền Thanh, cho nên, liền xem như thư viện, vậy không nhìn ra bản thân chủ nhân là ai."),t("br"),t("br"),n._v("Việc này nói đến đơn giản, thân là vu hồn sư, lại biết độ khó."),t("br"),t("br"),n._v("Ý niệm bản thân số âm tính, đầu thai về sau, tương đương với âm hồn quấn thân, thai nhi vừa ra đời, rất dễ dàng bởi vậy chết yểu."),t("br"),t("br"),n._v("Mấu chốt nhất là, loại này đầu thai, có hại thiên hòa, vì Thiên Đạo chỗ không cho phép, cho dù có thể may mắn thành công, cũng cần tiêu tốn cực lớn đánh đổi."),t("br"),t("br"),n._v("Cho dù nắm giữ vu hồn sư thủ đoạn, xác suất thành công vậy vạn không còn một!"),t("br"),t("br"),n._v("Bằng không, Mặc Hồn Sinh lúc trước vậy không đến mức vì Cửu Thiên Liên Thai liều mạng, mà nghĩ biện pháp đi lần nữa đầu thai."),t("br"),t("br"),n._v("Đương nhiên, mấu chốt nhất là, ý niệm đầu thai về sau, sẽ rơi vào trạng thái ngủ say, có ít người, khả năng cả một đời thẳng đến trước khi chết, đều không thể thức tỉnh lúc trước ký ức, cuối cùng tầm thường vô vi, cho đến chết."),t("br"),t("br"),n._v("Đây cũng chính là cái gọi là thai mê, thai bên trong chi mê."),t("br"),t("br"),n._v("Bởi vậy, đầu thai trừ phi là cùng đường mạt lộ, bây giờ không có phương pháp, không có khả năng có người làm như vậy."),t("br"),t("br"),n._v("Hơn nữa mỗi đầu thai một lần, đối linh hồn đều có cực lớn tổn hại, một lần không thành công, trên cơ bản liền không còn cơ hội thứ hai."),t("br"),t("br"),n._v("Đối phương không chỉ thành công, còn trở thành thất tinh đỉnh phong danh sư, khống chế Danh Sư đường, cho dù có bản tôn ở sau lưng cầm cự, vậy mười phần đáng sợ."),t("br"),t("br"),n._v("“Không sai! Không muốn làm hao mòn sự kiên nhẫn của ta, đã biểu lộ thân phận, trừ phi quy thuận tại ta, hiến tế linh hồn, nếu không ngươi hẳn phải biết, tuyệt không có khả năng còn sống rời đi nơi này.”"),t("br"),t("br"),n._v("Thanh Điền Hoàng hừ lạnh."),t("br"),t("br"),n._v("Dám ở trước mặt đối phương hào phóng cho thấy thân phận, liền căn bản không quan tâm đối phương để lộ bí mật, bởi vì hắn đã hoàn toàn nắm giữ quyền chủ động."),t("br"),t("br"),n._v("Muốn đánh giết đối phương, chỉ ở một cái ý niệm trong đầu mà thôi."),t("br"),t("br"),n._v("“Tốt a, ta quy thuận!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ừm?” Thanh Điền Hoàng sững sờ."),t("br"),t("br"),n._v("Tại nhân tộc phạm vi bên trong, sinh hoạt những năm này hắn biết danh sư kiên trì cùng tín niệm, trước mắt vị này, trước đó phách lối như vậy, liền Danh Sư đường đều có thể nhẹ nhõm xông qua... Vốn cho rằng sẽ có kiên trì, nằm mơ đều không có nghĩ đến trực tiếp đáp ứng."),t("br"),t("br"),n._v("Đây cũng quá sảng khoái đi!"),t("br"),t("br"),n._v("“Quy thuận muốn hiến tế linh hồn...”"),t("br"),t("br"),n._v("Chần chờ một chút, Thanh Điền Hoàng Đạo."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu."),t("br"),n._v(" “Vậy thì tốt, hiến tế đi...”"),t("br"),t("br"),n._v("Có chút không dám tin tưởng, chẳng qua Thanh Điền Hoàng vẫn gật đầu."),t("br"),t("br"),n._v("Một khi hiến tế linh hồn, một cái ý niệm trong đầu liền có thể để hắn tử vong, không cần sợ hãi phản bội, cho dù đối phương quỷ kế đa đoan, vậy không có cái gì tác dụng."),t("br"),t("br"),n._v("“Chờ một chút, ta hiến tế linh hồn, ngươi thật có thể tìm cho ta một cái thích hợp ta Vô Hồn Kim Nhân? Để cho ta sống tiếp?”"),t("br"),t("br"),n._v("Trương Huyền đánh gãy hắn."),t("br"),t("br"),n._v("“Điểm ấy không cần lo lắng!”"),t("br"),t("br"),n._v("Thanh Điền Hoàng xua tay, ánh mắt lộ ra ý khinh thường."),t("br"),t("br"),n._v("Còn tưởng rằng cái tên này có nhiều cốt khí, nguyên lai là muốn sống."),t("br"),t("br"),n._v("Cũng khó trách, sinh tử trước mặt, rất nhiều bình thường xương cứng danh sư, cũng sẽ nhận tội, thật giống như lúc trước Vưu Hư, đồng dạng ngoan ngoãn nghe bản thân phân phó."),t("br"),t("br"),n._v("“Cái kia... Ta có thể hay không nói cho ta biết trước, đi vào Vô Hồn Kim Nhân phương pháp, để cho ta thử một chút?”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp: “Muốn thật xác nhận có thể sống sót, ngươi không phải gạt ta, mới có thể thần phục! Lại nói, muốn cho ta giúp một tay khôi phục tác phẩm hội họa, cũng chỉ có thể cho ta thân thể, không có thân thể, chỉ bằng vào linh hồn, là không có cách nào cầm lấy bút vẽ!”"),t("br"),t("br"),n._v("Vu hồn sư Vu hồn, có thể cầm lấy đồ vật, nhưng linh hồn gần như không có khả năng làm được."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mày nhăn lại, muốn xem kẻ trước mắt này đến cùng nghĩ cái gì, chỉ thấy hắn một mặt đơn thuần, nhớ lại một chút, trước đó tại đối phương thức hải nhìn thấy cảnh tượng, Thanh Điền Hoàng nhẹ gật đầu: “Cũng tốt, đi vào Vô Hồn Kim Nhân phương pháp rất đơn giản...”"),t("br"),t("br"),n._v("Nói, sẽ tiến vào cùng khống chế Vô Hồn Kim Nhân phương pháp, cẩn thận giảng giải một lần."),t("br"),t("br"),n._v("“Ta biết rồi...”"),t("br"),t("br"),n._v("Thấy đối phương giảng giải cùng Mặc Hồn Sinh truyền thừa, không kém nhiều, Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Loại phương pháp này, hắn đã sớm ngưng tụ thành Thiên Đạo công pháp, hơn nữa học qua, lúc trước luyện chế tốt phân thân, để chia lìa hồn phách, tiến vào bên trong, liền là dùng cái này pháp quyết."),t("br"),t("br"),n._v("“Ngươi đem ta buông ra, ta đi vào tượng đồng...”"),t("br"),t("br"),n._v("Vùng vẫy một hồi, phát hiện còn bị vây ở tế đàn, không cách nào động đậy, Trương Huyền nói."),t("br"),t("br"),n._v("“Không muốn quét thủ đoạn gì, cái này Vô Hồn Kim Nhân là ta luyện chế thành công, tự nhiên biết hủy diệt phương pháp, đừng tưởng rằng, tiến vào bên trong, liền có thể cùng ta đối kháng!”"),t("br"),t("br"),n._v("Thanh Điền Hoàng hừ một tiếng."),t("br"),t("br"),n._v("“Hồn phách của ta chỉ có Thánh vực nhị trọng, cái này tượng đồng lực lượng mạnh hơn, với ta mà nói, tác dụng cũng không lớn, cho dù thật mưu đồ quấy rối, cũng không phải đối thủ...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Dừng lại một chút, Thanh Điền Hoàng gật đầu."),t("br"),t("br"),n._v("Nhìn tới thật có chút sợ bóng sợ gió."),t("br"),t("br"),n._v("Đối phương nói không sai, một cái Thánh vực nhị trọng linh hồn mà thôi, lợi hại hơn nữa lại có thể thế nào?"),t("br"),t("br"),n._v("Bàn tay lăng không một trảo, tế đàn bên trên ánh sáng lóe lên, Trương Huyền lập tức cảm thấy trên thân buông lỏng, bị nhốt lại hồn phách, lập tức đứng lên."),t("br"),t("br"),n._v("“Ta thử một chút!”"),t("br"),t("br"),n._v("Đứng dậy Trương Huyền, thân thể nhoáng một cái, chui vào trước mắt đầu này Vô Hồn Kim Nhân thân thể."),t("br"),t("br"),n._v("Sau một khắc, tượng đồng mở mắt."),t("br"),t("br"),n._v("Hoạt động một chút, cảm giác cùng khống chế khôi lỗi không có khác nhau quá nhiều, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Người, là có cảm giác, có cảm xúc, linh hồn đi vào nơi này, mặc dù có thể còn sống sót, nhưng không có cảm giác, cùng mượn xác hoàn hồn đồng dạng, trên thực tế cùng người chết, đã không sai biệt lắm!"),t("br"),t("br"),n._v("Khó trách vu hồn sư cứ việc có thể sống lâu dài, cũng không ít người không nguyện ý học cái nghề nghiệp này."),t("br"),t("br"),n._v("Làm người, tự nhiên muốn so loại này chỉ có thể sinh hoạt tại u ám bên trong, kéo dài hơi tàn sinh mệnh muốn tốt."),t("br"),t("br"),n._v("Thấy trước mắt vị này, đi vào kim thân bên trong, không có động tác khác, Thanh Điền Hoàng lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Kim thân là hắn luyện chế, đối phương tiến vào bên trong, chẳng khác nào bị giam tiến vào chiếc lồng, muốn giết chết, vô cùng đơn giản."),t("br"),t("br"),n._v("“Hiến tế đi!”"),t("br"),t("br"),n._v("Hừ một tiếng."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, khống chế tượng đồng, hai bước đi tới, tế đàn trước mặt: “Nếu là hiến tế, liền dùng cái tế đàn này đi!”"),t("br"),t("br"),n._v("Nói hết tượng đồng khẽ động, mi tâm một đạo linh hồn bắn đi ra, chậm rãi lơ lửng ở trên không."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này như vậy chủ động, Thanh Điền Hoàng nhẹ gật đầu, cũng tới đến tế đàn trước mặt, thân thể nhoáng một cái, Vu hồn ly thể."),t("br"),t("br"),n._v("Hắn tu luyện chính là Vu hồn nhất mạch công pháp, hồn phách âm hàn băng lãnh, để cho người ta còn chưa tới đến trước mặt liền nhìn mà phát khiếp."),t("br"),t("br"),n._v("Vu hồn sư, trọng yếu là linh hồn, chỉ có hiến tế cho hồn thể, mới có tác dụng chỗ."),t("br"),t("br"),n._v("“Hiến tế!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Thanh Điền Hoàng thoả mãn nhìn lại."),t("br"),t("br"),n._v("Vốn cho rằng đối phương là cái nhân vật hung ác, hiện tại xem ra, còn quá trẻ."),t("br"),t("br"),n._v("Chỉ cần hiến tế thành công, chính là mình trung thành thuộc hạ, không dám tiếp tục phản kháng."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, Vô Hồn Kim Nhân đột nhiên dọc theo tế đàn quay một vòng, bàn tay bỗng nhiên ở phía trên vỗ."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Toàn bộ tế đàn phù văn lập tức phát sáng lên, thời gian nháy mắt, một cỗ to lớn hấp lực bỗng nhiên đối không bên trong Thanh Điền Hoàng Vu hồn, thôn phệ mà tới."),t("br"),t("br"),n._v("“Ngươi... Sao có thể kích hoạt tế đàn?”"),t("br"),t("br"),n._v("Thanh Điền Hoàng biến sắc."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1199"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1201"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);