(window.webpackJsonp=window.webpackJsonp||[]).push([[242],{595:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1194-mot-con-mat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1194-mot-con-mat"}},[n._v("#")]),n._v(" Chương 1194: Một con mắt")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1194: Một con mắt"),t("br"),t("br"),n._v("“Ai?”"),t("br"),t("br"),n._v("Trương Huyền chỉ cảm thấy toàn thân lông tơ đột nhiên nổ lên."),t("br"),t("br"),n._v("Tại đầu óc hắn nói chuyện, thế mà trước thời hạn không có chút nào hiểu rõ tình hình... Đến cùng là ai?"),t("br"),t("br"),n._v("Vội vàng hướng thanh âm vang lên địa phương nhìn lại, giống như là thấy được một cái đen kịt thông đạo, lan tràn không biết bao xa, một cái không nhìn thấy phần cuối."),t("br"),t("br"),n._v("“Vào đi!”"),t("br"),t("br"),n._v("Gặp hắn phát hiện thông đạo, âm lãnh thanh âm vang lên lần nữa, ngay sau đó Trương Huyền liền cảm thấy thân thể nhoáng một cái, ý thức đã xuất hiện tại thông đạo trước mặt."),t("br"),t("br"),n._v("Thế mà bị đối phương hút tới nơi này."),t("br"),t("br"),n._v("“Ngươi đến cùng là ai?”"),t("br"),t("br"),n._v("Khuôn mặt khó coi, Trương Huyền hướng thông đạo chỗ sâu nhìn lại, lập tức nhìn thấy một cái con mắt thật to, xuất hiện ở trước mắt, lúc mở lúc đóng, tựa như chiếu tâm linh của người ta, để cho người ta tạp niệm bác sinh."),t("br"),t("br"),n._v("“Khó trách có thể đem ta cái này thuộc hạ, làm như thế... Nguyên lai là trời nhận danh sư! Nhiều năm như vậy, trời nhận danh sư lại xuất hiện à...”"),t("br"),t("br"),n._v("Cười lạnh vang lên."),t("br"),t("br"),n._v("“Ngươi có thể nhìn ra ta là trời nhận danh sư?”"),t("br"),t("br"),n._v("Híp mắt lại, Trương Huyền trong lòng căng thẳng."),t("br"),t("br"),n._v("Thân phận của hắn, liền xem như Lạc Thừa Tân loại này Thánh vực thất trọng cường giả, cũng nhìn không ra, đối phương lại một cái xem thấu, có phần quá lợi hại đi!"),t("br"),t("br"),n._v("“Liền cái này cũng nhìn không ra, có tư cách gì cùng Khổng sư tranh đấu nhiều năm?”"),t("br"),t("br"),n._v("Lóe lên một cái, con mắt khẽ nói."),t("br"),t("br"),n._v("“Cùng Khổng sư tranh đấu nhiều năm? Ngươi là... Ngoan Nhân?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Trước đó, bản thân Thiên Đạo chi sách bên trong phong ấn Ngoan Nhân liền đã nói với hắn, bắt giữ linh hồn bí pháp là hắn đặc thù, đoán không sai, Ngoan Nhân một bộ phận, tại Tề Bắc đầm lầy... Không nghĩ tới tại đây xuất hiện!"),t("br"),t("br"),n._v("Hơn nữa còn là một cái con mắt thật to!"),t("br"),t("br"),n._v("“Ngươi nghe nói qua ta?” Nhẹ nhàng cười một tiếng, con mắt thanh âm tiếp tục vang lên: “Cũng đúng, ta đem Khổng sư khốn tại Trần Thái, suýt chút nữa giết hắn... Danh Sư đường cho dù suy nghĩ tới vạn thế chi sư tôn nghiêm, khẳng định cũng sẽ có điều ghi chép!”"),t("br"),t("br"),n._v("“Đem Khổng sư vây khốn?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Thiên Đạo chi sách bên trong Ngoan Nhân, bởi vì chết thân thể một bộ phận, ký ức bị hao tổn, liên quan tới Khổng sư không ít chuyện đều không nhớ rõ, trước mắt cái này rõ ràng biết đến càng nhiều."),t("br"),t("br"),n._v("Khổng sư trong lòng hắn vẫn luôn là vô địch tồn tại, tại toàn bộ danh sư đại lục, càng là người người ngưỡng mộ... Lại bị Ngoan Nhân khốn tại Trần Thái suýt chút nữa chết rồi..."),t("br"),t("br"),n._v("Việc này, thế nhưng là chưa từng nghe nói qua."),t("br"),t("br"),n._v("“Nhìn tới ngươi không biết, có điều, không biết cũng được... Có thể nuốt một vị trời nhận danh sư, lực lượng nhất định có thể khôi phục không ít, đến lúc đó... Liền có thể giãy giụa nơi đó, biển rộng mặc cá bơi, trời cao mặc chim bay...”"),t("br"),t("br"),n._v("Con mắt tỏa ánh sáng, Ngoan Nhân thanh âm tràn đầy hưng phấn: “Nhìn tới chỉ có thể oan ức ngươi, chết đi cho ta!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngay sau đó Trương Huyền liền cảm thấy trước mắt lỗ đen sinh ra một cỗ to lớn hấp lực, tựa hồ muốn hồn phách của mình thôn phệ đi vào."),t("br"),t("br"),n._v("“Nguy rồi... Đây là Ngoan Nhân tự mình thi triển bắt giữ chi thuật...”"),t("br"),t("br"),n._v("Vẻ mặt khó coi."),t("br"),t("br"),n._v("Trước đó, Thanh Điền Hoàng mượn nhờ tế đàn, thiếu chút nữa chống lại không được, Ngoan Nhân tự mình thi triển, làm sao ngăn cản!"),t("br"),t("br"),n._v("Cảm nhận được mình tùy thời đều sẽ bị kéo xuống màu đen thông đạo, Trương Huyền da đầu nổ tung."),t("br"),t("br"),n._v("Không hổ là đã từng cùng Khổng sư chiến đấu qua Ngoan Nhân, thực lực bây giờ, cho dù trăm không còn một, cũng không phải hắn loại thực lực này có thể chống lại!"),t("br"),t("br"),n._v("Chênh lệch thực sự quá lớn!"),t("br"),t("br"),n._v("“Như vậy tiếp tục nữa, sớm muộn gì đều sẽ bị nuốt vào đi...”"),t("br"),t("br"),n._v("Mặc dù không biết trước mắt màu đen thông đạo là cái gì, nhưng chỉ cần ngã vào trong đó, coi như không chết cũng không xê xích gì nhiều, Trương Huyền cắn chặt răng, gắt gao kiên trì, cùng thời khắc đó, tinh thần lặng lẽ câu thông Thiên Đạo chi sách bên trong Ngoan Nhân."),t("br"),t("br"),n._v("“Nếu như đoán không sai, hẳn là đầu lâu của ta cùng con mắt...”"),t("br"),t("br"),n._v("Ngoan Nhân tinh thần ba động truyền đến."),t("br"),t("br"),n._v("“Nên làm cái gì?” Trương Huyền vội vàng nói."),t("br"),t("br"),n._v("“Loại này linh hồn bắt giữ phương pháp, mặc dù lợi hại, nhưng khoảng cách quá xa, còn là rất khó làm được, thật giống như Thanh Điền Hoàng mượn tế đàn... Thực lực của ta khôi phục lại đỉnh phong, cách nhau mấy vạn, mấy trăm ngàn cây số, có thể đem chủ nhân câu giết... Hiện tại... Hẳn là mượn một loại nào đó nối liền, chỉ cần đem nối liền chặt đứt, liền an toàn!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Ngoan Nhân nói."),t("br"),t("br"),n._v("Lực lượng mạnh nhất thời điểm, cách nhau mấy vạn cây số, mấy trăm ngàn cây số, giết một cái Thánh vực nhị trọng, chỉ cần chuẩn bị ổn thoả, có đối phương huyết dịch, một ý niệm liền có thể làm được, nhưng... Lúc trước bị đánh vỡ nát, đâu đâu cũng có tàn chi, cho dù khôi phục tốt hơn hắn, muốn làm được điểm ấy, cũng là không thể nào."),t("br"),t("br"),n._v("Khả năng duy nhất liền là cùng trước đó Thanh Điền Hoàng đồng dạng, mượn tế đàn loại hình đồ vật!"),t("br"),t("br"),n._v("“Nối liền? Không sai!”"),t("br"),t("br"),n._v("Nghe được hắn, Trương Huyền sửng sốt một chút, linh quang chợt lóe: “Là Thanh Điền Hoàng hồn phách...”"),t("br"),t("br"),n._v("Thanh Điền Hoàng Vu hồn đi vào thân thể của mình, cái tên này có lẽ chính là mượn hồn phách lực lượng, mới muốn bắt giữ bản thân."),t("br"),t("br"),n._v("“Nhìn tới cái tên này còn không có bị làm hao mòn sạch sẽ, ta ngược lại muốn xem xem, ngươi ở nơi nào...”"),t("br"),t("br"),n._v("Biết nguyên nhân, Trương Huyền thở phào nhẹ nhõm, ý thức chống cự màu đen thông đạo hấp lực, đồng thời thần thức lan tràn, ở trong người cẩn thận tìm kiếm."),t("br"),t("br"),n._v("“Tìm được...”"),t("br"),t("br"),n._v("Thời gian không dài, quả nhiên bị hắn phát hiện một đạo Vu hồn núp ở thức hải một bên."),t("br"),t("br"),n._v("Thiên Đạo chân khí không người khống chế dưới tình huống, có thể chạy toàn thân, cũng rất khó đi vào thức hải, nhìn tới cái tên này, giấu đến nơi đây, tránh thoát một kiếp."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, Thiên Đạo chân khí lập tức lao qua."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Thanh Điền Hoàng một tiếng hô, Trương Huyền lập tức cảm thấy trước mắt màu đen thông đạo cực tốc lui về phía sau, con mắt thật to cũng lắc lư hai lần, bất cứ lúc nào cũng sẽ biến mất."),t("br"),t("br"),n._v("“Không nghĩ tới bị phát hiện, có điều, không quan trọng, ngươi nhất định sẽ chết trên tay ta...”"),t("br"),t("br"),n._v("Phát ra trầm muộn gào thét, con mắt “Hô!” Một cái, mất đi tung tích."),t("br"),t("br"),n._v("Trương Huyền lúc này mới phát hiện, bản thân vẫn như cũ đứng tại trong cung điện dưới lòng đất, không nhúc nhích, trên đầu tràn đầy mồ hôi lạnh."),t("br"),t("br"),n._v("Vừa rồi trong nháy mắt đó, thật giống như lâm vào đặc thù huyễn cảnh, suýt chút nữa bị đối phương thôn phệ."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),n._v("Mặc dù an toàn, vẫn là không nhịn được lòng còn sợ hãi."),t("br"),t("br"),n._v("Thật là đáng sợ!"),t("br"),t("br"),n._v("Nếu là vừa rồi không có kịp phản ứng, chỉ sợ ý thức đã bị đối phương bắt đi."),t("br"),t("br"),n._v("Bị cái tên này nắm tới, chỉ sợ cũng không có Thanh Điền Hoàng tốt như vậy lừa dối."),t("br"),t("br"),n._v("“Chủ nhân, vừa rồi loại tình huống kia, ta không dám ra tay, một khi động thủ, chẳng khác nào bại lộ... Đối phương vô cùng có khả năng thi triển ra càng thêm điên cuồng phương pháp, sẽ chỉ đối chủ nhân càng thêm không có lợi!”"),t("br"),t("br"),n._v("Thiên Đạo chi sách Ngoan Nhân thanh âm truyền đến."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Phía bên mình Ngoan Nhân, chỉ cần ra tay, đối phương tất nhiên có thể cảm giác."),t("br"),t("br"),n._v("Bên này Ngoan Nhân, muốn thôn phệ đối phương, đối phương tất nhiên cũng muốn thôn phệ bên này... Cho nên, làm như vậy lời nói, sẽ chỉ làm đối phương càng thêm điên cuồng."),t("br"),t("br"),n._v("Nếu thực như thế làm, sẽ chỉ trước thời hạn làm cho đối phương biết Ngoan Nhân tồn tại, về sau lại nghĩ động thủ, không tiện."),t("br"),t("br"),n._v("Chí ít hiện tại, kẻ địch ở ngoài sáng, phía bên mình Ngoan Nhân tại ám... Có thể chiếm cứ rất lớn quyền chủ động."),t("br"),t("br"),n._v("“Thực lực của hắn đạt tới cái tình trạng gì? Ngươi có phải hay không đối thủ?”"),t("br"),t("br"),n._v("Không tại xoắn xuýt những này, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Hắn thực lực cụ thể, ta không nhìn ra, bất quá... Có lẽ thấp nhất đều đạt đến Xuất Khiếu cảnh! Ta thực lực bây giờ, không phải là đối thủ, bất quá... Đem Thanh Điền Hoàng ăn đi lời nói, hẳn là có thể đủ đạt tới đến xuất khiếu!”"),t("br"),t("br"),n._v("Chần chờ một chút, Ngoan Nhân nói."),t("br"),t("br"),n._v("“Ăn đi?” Sửng sốt một chút, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dị Linh tộc nhân, nắm giữ thôn phệ nhân loại, thôn phệ danh sư liền có thể tiến bộ bí pháp, Thanh Điền Hoàng mặc dù là Dị Linh tộc nhân, có thể tu luyện Vu hồn chi thuật, càng là đạt đến Xuất Khiếu cảnh, đem hắn thôn phệ, đối thực lực tăng lên, tất nhiên cũng có trợ giúp thật lớn."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Ngoan Nhân đáp."),t("br"),t("br"),n._v("“Chờ ta thẩm xong, tùy ngươi xử trí...” Dừng lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Bị Thiên Đạo chân khí vây khốn, lại thêm vừa rồi làm hao mòn, Thanh Điền Hoàng thực lực bây giờ đã hao tổn hơn phân nửa, không quan trọng gì."),t("br"),t("br"),n._v("Còn có chút nghi ngờ, hỏi xong lại tiễn cho đối phương không muộn."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Nói đến phức tạp, trên thực tế từ màu đen thông đạo xuất hiện, đến thời khắc này, chẳng qua mấy chục cái hô hấp mà thôi, ngẩng đầu nhìn lại, chỉ thấy Ngô sư đám người một mặt lo lắng nhìn qua."),t("br"),t("br"),n._v("“Ta không sao, Ngô sư... Ngươi đi theo ta! Đúng, đem cái tế đàn này, chuyển tới Danh Sư đường, đợi tổng bộ người tới, giao cho bọn hắn, xem như chứng cứ!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Ngô sư an bài một tiếng, đi theo Trương Huyền sau lưng, đi ra Địa Cung."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chiếu rọi, Trương Huyền bốn phía dò xét, thời gian không dài, tìm đúng phương hướng, trực tiếp bay tới đằng trước."),t("br"),t("br"),n._v("Trần Triết phủ đệ, đã tọa lạc tại Thanh Nguyên thành biên giới, hai người cấp tốc phi hành, thời gian không dài, liền đến đến nơi núi rừng sâu xa, tìm một hồi, nhìn thấy một cái nhà gỗ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Chính là cái này... Không nghĩ tới cả hai chênh lệch chừng mấy chục cây số!”"),t("br"),t("br"),n._v("Xác nhận một cái, Trương Huyền có chút kinh hãi."),t("br"),t("br"),n._v("Còn tưởng rằng hai cái tế đàn, cách nhau không xa, không nghĩ tới đã đạt đến mấy chục cây số... Khoảng cách xa như vậy, đem linh hồn của mình nhẹ nhõm bắt giữ tới, Ngoan Nhân chiêu này, thực sự quá mức đáng sợ."),t("br"),t("br"),n._v("Đi vào nhà gỗ, tìm được vừa rồi nấp kỹ nhẫn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn xem trong phòng cũng có một cái tương đồng tế đàn, Ngô sư có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Ngươi xem một chút đây là ai...”"),t("br"),t("br"),n._v("Đem nhẫn giữ tại lòng bàn tay, Thiên Đạo chân khí nhẹ nhàng xoay một cái, liền gạt bỏ trong đó linh hồn ấn ký, tinh thần khẽ động, một cái thi thể xuất hiện tại trước mặt."),t("br"),t("br"),n._v("“Đây là... Điền Thanh phó đường chủ? Hắn không phải tự bạo sao?”"),t("br"),t("br"),n._v("Ngô sư sững sờ."),t("br"),t("br"),n._v("Điền Thanh ở ngay trước mặt hắn tự bạo, thi thể cũng bị mang về Danh Sư đường, làm sao nơi này còn có một cái?"),t("br"),t("br"),n._v("Hơn nữa thoạt nhìn hoàn hảo không chút tổn hại, khí tức càng hơn?"),t("br"),t("br"),n._v("“Đây là hắn ngụy trang...”"),t("br"),t("br"),n._v("Trương Huyền bàn tay ở phía trên nhẹ nhàng vạch một cái."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Ngụy trang bị xóa đi, trước mắt thi thể, lập tức tăng lên một vòng, chừng cao hơn hai mét, dung mạo mặc dù không có biến hóa, khí tức trên thân lại mang theo làm người ta hoảng sợ chém giết chi ý."),t("br"),t("br"),n._v("“Dị Linh tộc nhân?”"),t("br"),t("br"),n._v("Ngô sư con mắt lập tức híp lại."),t("br"),t("br"),n._v("“Ừm, không sai, không riêng gì Dị Linh tộc nhân, còn là... Thanh Điền Hoàng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Thanh Điền nhất mạch Hoàng giả?”"),t("br"),t("br"),n._v("Ngô sư tràn đầy ngạc nhiên."),t("br"),t("br"),n._v("Điền Thanh phó đường chủ, làm sao có thể là Thanh Điền nhất mạch Hoàng giả?"),t("br"),t("br"),n._v("Nếu như là lời nói, làm sao có thể, ở chung mấy trăm năm, cũng chưa nhận ra được?"),t("br"),t("br"),n._v("“Là như vậy...”"),t("br"),t("br"),n._v("Biết đối phương khó tin, Trương Huyền đem tiên tri đạo sự, nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngô sư không ngừng run rẩy."),t("br"),t("br"),n._v("Đường đường Thanh Điền nhất mạch Dị Linh tộc nhân, thay đổi thân phận, biến thành phó đường chủ, quản lý một phương, bọn họ cũng không phát hiện, tội lỗi bao lớn, khó mà nói nên lời."),t("br"),t("br"),n._v("Nếu như là thật... Toàn bộ Danh Sư đường, còn có bao nhiêu là danh sư cũng là như vậy?"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1202"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1204"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);