(window.webpackJsonp=window.webpackJsonp||[]).push([[244],{597:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1196-chien-su-đuong-trao-đoi-bat-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1196-chien-su-đuong-trao-đoi-bat-đau"}},[n._v("#")]),n._v(" Chương 1196: Chiến sư đường trao đổi bắt đầu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1196: Chiến sư đường trao đổi bắt đầu "),t("br"),t("br"),n._v("Khổng sư biến mất trước đó, từng lưu lại sáu cái truyền thế thiên phù, một tấm canh giữ Danh Sư đường, còn lại năm tấm, không biết đi nơi nào, không người biết được."),t("br"),t("br"),n._v("Truyền thuyết, chỉ cần tập hợp đủ sáu cái, liền có thể mở ra Khổng miếu, tiến vào bên trong, tìm kiếm hắn lưu lại tuyệt thế bí tịch... Xuân Thu đại điển!"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, vài vạn năm đến, vô số người đều đang tìm kiếm, đáng tiếc, một mực không có tung tích."),t("br"),t("br"),n._v("Cũng hoặc là, sớm đã bị người nhận được, chỉ là không ai dám rêu rao mà thôi."),t("br"),t("br"),n._v("Dù sao, dính dáng Khổng sư, liền xem như danh sư, cũng có thể điên cuồng mất lý trí."),t("br"),t("br"),n._v("Thanh Điền Hoàng, chính là từ Ngoan Nhân trong miệng biết liên quan tới cái này truyền thế thiên phù tin tức, không tiếc tiêu tốn vô số đại giới, đủ loại bố cục."),t("br"),t("br"),n._v("“Căn cứ trí nhớ của hắn, Khưu Ngô cung, hẳn là sẽ có tìm kiếm truyền thế thiên phù chìa khoá, nhưng mà... Hình như không có cái gì...”"),t("br"),t("br"),n._v("Thông qua sưu hồn, hắn biết, đối phương tiêu tốn lớn như thế tâm huyết, tìm kiếm Khưu Ngô cung, cũng không phải là không có mục đích, mà là thông qua đủ loại manh mối biết, nơi này không chỉ lưu lại Khổng sư ý niệm, còn có tìm kiếm thiên phù chìa khoá."),t("br"),t("br"),n._v("Chỉ cần tìm được, liền có thể mượn cơ hội tìm kiếm được thiên phù, thậm chí có cơ hội tìm tới Khổng miếu vị trí cụ thể."),t("br"),t("br"),n._v("Khổng miếu, là lúc trước Khổng sư tự mình lưu lại, núp ở hư không bên trong, vị trí cụ thể, không có người biết."),t("br"),t("br"),n._v("“Chỉ sợ... Truyền thuyết đều là giả...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Địa phương khác chưa quen thuộc, Khưu Ngô cung hắn đều đi vào qua hai lần, cũng không có phát hiện qua cái gì chìa khoá!"),t("br"),t("br"),n._v("Thậm chí tới có liên quan đồ vật đều không có."),t("br"),t("br"),n._v("Nhìn như vậy đến, tin tức chưa chắc là thật."),t("br"),t("br"),n._v("Đương nhiên, chìa khoá chỉ là phụ trợ, hơn hai ngàn năm trước, nghe nói một vị thư họa sư, từng thấy qua truyền thế thiên phù, trên không trung ấn xuống Khổng sư cái bóng."),t("br"),t("br"),n._v("Biết tin tức này về sau, Thanh Điền Hoàng đem vị kia thư họa sư cầm tới thẩm vấn, đáng tiếc, cái gì đều không có nhận được."),t("br"),t("br"),n._v("Ba năm trước đây, trong lúc vô tình tìm được đối phương lưu lại di tích, tìm được bộ kia bát cảnh tác phẩm hội họa, hoài nghi cùng chuyện này liên quan đến, đáng tiếc phía trên bị phong ấn, cái gì cũng nhìn không ra."),t("br"),t("br"),n._v("Nghiên cứu rất lâu, tìm không thấy phương pháp, đành phải tìm tới giám bảo sư công hội Mục hội trưởng, hợp mưu hợp sức."),t("br"),t("br"),n._v("Về sau sự... Trương Huyền liền biết tất cả."),t("br"),t("br"),n._v("“Chẳng lẽ... Ta trong bức họa nhìn thấy cái chỗ kia, là truyền thế thiên phù vị trí?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền trong lòng suy đoán."),t("br"),t("br"),n._v("Muốn nói liên quan tới Khưu Ngô cung tin tức là giả, nhưng trên bức họa này, hắn nhưng là tận mắt thấy Khổng sư hư ảnh, sẽ không có sai."),t("br"),t("br"),n._v("“Quay lại tốt tốt tra một chút, tòa rặng núi này ở nơi đó, sau đó... Đi qua tìm xem...”"),t("br"),t("br"),n._v("Thầm nghĩ trong lòng."),t("br"),t("br"),n._v("Đem những này toàn bộ xem hết, Trương Huyền cũng nhìn thấy Danh Sư đường gian tế danh sách."),t("br"),t("br"),n._v("Chừng hơn một trăm cái, phân tán tại toàn bộ Thanh Nguyên đế quốc."),t("br"),t("br"),n._v("Dị Linh tộc nhân tuổi thọ lâu đời, vượt xa quá cường giả Thánh vực một ngàn tuổi, cho nên, thật muốn bố trí, còn là thật dễ dàng."),t("br"),t("br"),n._v("Danh sư cho dù ý chí kiên định, tràn ngập tín ngưỡng, nhưng cũng không ít người, không chịu nổi viên đạn bọc đường ăn mòn, cuối cùng biến thành đối phương có thể khống chế khôi lỗi."),t("br"),t("br"),n._v("Đem tất cả tên, sao chép một phần, thông qua đưa tin ngọc bài truyền ra ngoài, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Có những vật này, Ngô sư liền sẽ miễn mất không ít phiền phức, thậm chí có thể trực tiếp bắt được."),t("br"),t("br"),n._v("“Ngoan Nhân, giao cho ngươi...”"),t("br"),t("br"),n._v("Đem muốn biết tin tức toàn bộ biết, Trương Huyền lười nhác tiếp tục, nhẹ nhàng lắc một cái, đem Thanh Điền Hoàng Vu hồn, cho Ngoan Nhân."),t("br"),t("br"),n._v("Tại nhân loại cương vực sinh sống hai ngàn năm, vị này Dị Linh tộc nhân, không biết hại chết bao nhiêu vị danh sư, chỉ là thôn phệ, xem như tiện nghi hắn."),t("br"),t("br"),n._v("“Đa tạ chủ nhân...”"),t("br"),t("br"),n._v("Ngoan Nhân tràn đầy hưng phấn."),t("br"),t("br"),n._v("Đứng dậy, Trương Huyền duỗi lưng một cái, đang nghĩ làm sao đi kiếm lấy tinh nguyên linh thạch thượng phẩm, đột nhiên khóe miệng giật một cái, nhớ tới một việc."),t("br"),t("br"),n._v("“Đáp ứng Hình đường chủ giúp hắn huấn luyện Chiến sư, hình như ba ngày sau, cái khác ba đại Chiến sư đường người tới khiêu chiến...”"),t("br"),t("br"),n._v("Huấn luyện Chiến sư, an bài xong kế hoạch huấn luyện... Liền đến Danh Sư đường, kết quả, một chậm trễ liền là bốn ngày..."),t("br"),t("br"),n._v("Dựa theo thời gian suy tính lời nói, đế quốc khác Chiến sư đường người đã tới, làm không cẩn thận trao đổi, cũng bắt đầu!"),t("br"),t("br"),n._v("“Trở về nhìn một chút...”"),t("br"),t("br"),n._v("Rời khỏi Thiên Nghĩ Phong sào, lúc này mới phát hiện, đã mặt trời lên cao, thuộc về ngày thứ tư giữa trưa."),t("br"),t("br"),n._v("Lúc trước Hình đường chủ cũng đã có nói, nếu như huấn luyện tốt, muốn phân cho bản thân một nửa ban thưởng... Cũng không biết kết quả thế nào."),t("br"),t("br"),n._v("Bản thân không có nhìn, không biết đám người kia có hay không nghiêm túc dựa theo kế hoạch luyện tập, ngộ nhỡ thua... Nhưng là muốn tổn thất năm mươi viên tinh nguyên linh thạch thượng phẩm..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy nhức nhối!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong lòng cuống cuồng, trực tiếp hướng Chiến sư đường phương hướng bay đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Chiến sư đường, phòng luyện võ."),t("br"),t("br"),n._v("Vô số Chiến sư vây quanh ở bốn phía, nhìn xem trung tâm mấy cái đài cao."),t("br"),t("br"),n._v("Đám người phía trước nhất nhìn trên đài, bốn vị lão giả ngồi ngay ngắn phía trên, bên trong một cái, chính là Hình đường chủ."),t("br"),t("br"),n._v("“Hình đường chủ, ta làm sao nghe nói, ngươi Chiến sư, đều gia nhập cái gì Huyền Huyền hội? Cái này dường như là cái nhất đẳng đế quốc tổ chức a?”"),t("br"),t("br"),n._v("Giao lưu hội còn chưa bắt đầu, ba vị lão giả đồng loạt nhìn về phía trước mắt Hình đường chủ, nguyên một đám mày nhăn lại."),t("br"),t("br"),n._v("Hôm qua bọn họ đi tới, liền nghe nói, đường đường danh sư bên trong mạnh nhất sức chiến đấu, thế mà phần lớn đều gia nhập một cái nhất đẳng đế quốc danh sư tổ chức... Tất cả mọi người, tất cả đều bối rối."),t("br"),n._v(" Mặc dù Thanh Nguyên đế quốc Chiến sư đường, từ trước tới nay lót đáy, rất là sa đọa, thế nhưng không đến mức sa đọa đến tình trạng như thế đi!"),t("br"),t("br"),n._v("Hướng một đám bình thường danh sư khuất phục..."),t("br"),t("br"),n._v("Quả thực mất hết Chiến sư mặt mũi."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hình đường chủ gật đầu."),t("br"),t("br"),n._v("Huyền Huyền hội trên vạn người đều tại Chiến sư đường ở, cho dù muốn giấu diếm cũng không che giấu nổi."),t("br"),t("br"),n._v("“Không phải ta nói ngươi, Chiến sư... Vậy mà gia nhập nhất đẳng đế quốc tổ chức, ngươi đến cùng nghĩ như thế nào? Chẳng lẽ quên, Chiến sư tôn nghiêm cùng thân phận?”"),t("br"),t("br"),n._v("Bên cạnh một vị râu dê lão giả khẽ nói, khí tức khinh người."),t("br"),t("br"),n._v("Sùng Viễn đế quốc Chiến sư đường đường chủ, Triệu nghị."),t("br"),t("br"),n._v("“Không phải chư vị nghĩ như vậy, Huyền Huyền hội mặc dù chỉ là cái nhất đẳng đế quốc học sinh tổ chức, nhưng bọn hắn đối với chiến đấu lý giải, so với chúng ta Chiến sư đường cũng cao hơn sâu, Khổng sư từng nói qua, chọn mà tốt thì học, không tính mạnh yếu...”"),t("br"),t("br"),n._v("Hình đường chủ giải thích."),t("br"),t("br"),n._v("“Tốt rồi, nói với chúng ta những thứ vô dụng này, đi Tiềm Xung phong hào đế quốc Kiếm Trì danh ngạch có hạn, chúng ta tự nhiên hi vọng các ngươi càng yếu càng tốt!”"),t("br"),t("br"),n._v("Ngắt lời hắn, cái thứ nhất lão giả nói."),t("br"),t("br"),n._v("Hạo Hải đế quốc Chiến sư đường đường chủ, lưu Tần Sơn."),t("br"),t("br"),n._v("“Thanh Nguyên đế quốc Chiến sư đường, tự cam đọa lạc không quan trọng, nhưng chớ chọc đến tổng bộ nổi giận, đem bọn ngươi thủ tiêu... Đến lúc đó, thân là đường chủ, liền thẹn với liệt tổ liệt tông!”"),t("br"),t("br"),n._v("Vị thứ ba lão giả, cũng lắc đầu."),t("br"),t("br"),n._v("Thiên Vân đế quốc, Ngụy Thiên Thư!"),t("br"),t("br"),n._v("Bát đại phong hào trong đế quốc, hơi yếu bốn đại đế quốc, theo thứ tự là Sùng Viễn, Thiên Vân, Hạo Hải cùng Thanh Nguyên!"),t("br"),t("br"),n._v("Lúc đầu Thanh Nguyên xếp hạng thứ ba, đáng tiếc hai mươi năm trước Dị Linh tộc nhân hạo kiếp, tổn thất quá lớn, đến bây giờ đều không có khôi phục nguyên khí, bất kể quốc lực, còn là Danh Sư đường, Chiến sư đường thực lực, đều chỉ có thể xếp tới cuối cùng."),t("br"),t("br"),n._v("“Làm phiền ba vị phí tâm, chúng ta Thanh Nguyên phân bộ, cứ việc còn không có hoàn toàn khôi phục, bây giờ lại cũng có cực lớn tiến bộ, cùng trước kia không đồng dạng...”"),t("br"),t("br"),n._v("Thấy ba vị đường chủ, trong lời nói rõ ràng có chút xem thường, Hình đường chủ nhíu nhíu mày, đang nghĩ giải thích, liền nghe đến sau lưng một hồi tiếng hoan hô vang lên, tiến hành tranh tài tuyển thủ, đã đi vào sân bãi."),t("br"),t("br"),n._v("“Quả thực là không đồng dạng...”"),t("br"),t("br"),n._v("Triệu nghị đường chủ cười nhạo."),t("br"),t("br"),n._v("“Hình đường chủ, ngươi đây là ăn mày quân đoàn ư? Ngươi xác định để bọn hắn cùng chúng ta ba đại Danh Sư đường tinh anh chiến đấu?” Ngụy Thiên Thư cũng là sững sờ, mày nhăn lại."),t("br"),t("br"),n._v("“Ăn mày quân đoàn?”"),t("br"),t("br"),n._v("Hình đường chủ không hiểu, nhìn về phía trước, khóe miệng cũng là co lại."),t("br"),t("br"),n._v("Chỉ thấy Thạch Hạo vẫn như cũ mặc giặt bồn cầu quần áo lao động, trong tay còn cầm một cái chổi lông gà, Tiêu Đàm cầm vỉ đập ruồi, Tiêu Tần cầm trong tay lồng chim, Đổng Thụy đầu đầy u lớn..."),t("br"),t("br"),n._v("Hiển nhiên một đám không ăn cơm lang thang ăn mày."),t("br"),t("br"),n._v("“Liêu điện chủ, cái này... Là chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Vội vàng quay đầu."),t("br"),t("br"),n._v("“Đường chủ, Trương sư lưu lại huấn luyện thực sự quá nhiều... Bọn họ không hoàn thành, lại muốn so với thi đấu, đành phải trực tiếp lên đài...” Liêu điện chủ vội vàng giải thích."),t("br"),t("br"),n._v("Trương sư lưu lại kế hoạch huấn luyện, lít nha lít nhít, ba ngày căn bản làm không được, mấy người này cũng không biết trúng tà còn là bị tẩy não, luyện luyện, vậy mà dừng lại không được... Chiến sư đường tham gia trao đổi Chiến sư, nguyên một đám bộ dáng này, suy nghĩ một chút đều để người buồn bực."),t("br"),t("br"),n._v("Nghe xong giải thích, Hình đường chủ mặt như mướp đắng."),t("br"),t("br"),n._v("Sớm biết bộ dáng này, liền không tìm Trương sư huấn luyện, cái này tốt rồi, Chiến sư đường mất mặt không nói, làm không cẩn thận, còn có thể bị những người khác cười nhạo không biết bao nhiêu năm."),t("br"),t("br"),n._v("Chùi bồn cầu, đập ruồi, bắt chim, nhảy cây... Trương sư những này huấn luyện đến cùng muốn làm gì? Thật đáng tin cậy ư?"),t("br"),t("br"),n._v("“Sùng Viễn đế quốc Chiến sư đường, Lăng Hiên, Thánh vực nhị trọng trung kỳ, khiêu chiến Thanh Nguyên đế quốc bằng hữu, không biết là vị nào đi lên một trận chiến!”"),t("br"),t("br"),n._v("Chính không biết làm sao, liền nghe đến phía trước hét lớn một tiếng, Sùng Viễn phong hào đế quốc một vị Chiến sư đã nhảy lên tỷ thí đài, ánh mắt lấp lánh nhìn lại."),t("br"),t("br"),n._v("“Ngay từ đầu liền khiêu chiến Thanh Nguyên đế quốc... Đây là định cho chúng ta ra oai phủ đầu...”"),t("br"),t("br"),n._v("Hình đường chủ nắm đấm xiết chặt."),t("br"),t("br"),n._v("Lúc đầu, giao lưu hội tranh tài điều lệ, các đại đế quốc đối kháng, đều là có trình tự, phía trước cái này, vừa lên đài liền khiêu chiến Thanh Nguyên đế quốc, rõ ràng là muốn cho nhóm người mình ra oai phủ đầu."),t("br"),t("br"),n._v("Mà thân là chủ nhà, nhưng lại không thể không nghênh chiến."),t("br"),t("br"),n._v("“Là ta...” Tiêu Đàm nhảy lên đài cao."),t("br"),t("br"),n._v("Tiêu Đàm là võ kỹ điện thiên tài, thực lực đã đạt đến Thánh vực nhị trọng trung kỳ, cấp bậc này tranh tài, chính là từ hắn tham gia."),t("br"),t("br"),n._v("“Ngươi?”"),t("br"),t("br"),n._v("Gặp hắn một thân quần áo lao động, trong tay còn cầm một cái vỉ đập ruồi, Lăng Hiên cười lạnh: “Vậy thì tốt, động thủ đi! Để cho ta tới lãnh giáo một chút, Thanh Nguyên đế quốc Chiến sư cao chiêu!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nói hết trên thân khí thế sinh ra, lòng bàn tay cuồn cuộn, tựa như có lôi đình ẩn núp trong đó."),t("br"),t("br"),n._v("“Đây là Sùng Viễn đế quốc nổi danh nhất bôn lôi chưởng đi!”"),t("br"),t("br"),n._v("“Nghe nói chiêu này, thi triển đi ra, tựa như bôn lôi, tốc độ cực nhanh, cùng cấp bậc căn bản không có người có thể ngăn cản!”"),t("br"),t("br"),n._v("“Đúng vậy a, rất khó tu luyện, nhốt lại không biết bao nhiêu thiên tài, vị này Lăng Hiên có thể luyện thành, hơn nữa xuất chưởng phong lôi, lực lớn vô cùng, chỉ sợ... Đã đạt đến cảnh giới đại thành!”"),t("br"),t("br"),n._v("“Đâu chỉ đại thành, nghe nói đã đạt tới viên mãn như ý! Vị này Lăng Hiên là Sùng Viễn đế quốc Chiến sư đường, võ kỹ điện nhân vật thiên tài nhất, đối võ kỹ lĩnh ngộ, đánh vỡ ghi chép, không đủ ba mươi, liền tu luyện ba mươi bốn loại võ kỹ!”"),t("br"),t("br"),n._v("“Lợi hại như vậy...”"),t("br"),t("br"),n._v("Dưới đài nghị luận ầm ĩ."),t("br"),t("br"),n._v("Không để ý tới mọi người thảo luận, Tiêu Đàm tiến lên, trong tay vẫn như cũ cầm vỉ đập ruồi."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1204"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1206"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);