(window.webpackJsonp=window.webpackJsonp||[]).push([[245],{598:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1197-ba-ba-ba"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1197-ba-ba-ba"}},[n._v("#")]),n._v(" Chương 1197: Ba ba ba!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1197: Ba ba ba! "),t("br"),t("br"),n._v("“Cái này vỉ đập ruồi là cây trúc làm a, vừa mềm lại không lực lượng, dùng cái này đối kháng bôn lôi chưởng?”"),t("br"),t("br"),n._v("“Cái tên này đầu óc có vấn đề?”"),t("br"),t("br"),n._v("“Tiêu Đàm là võ kỹ điện xếp hàng thứ nhất siêu cấp thiên tài, đối võ kỹ tinh thông tới cực điểm... Dựa theo tình huống bình thường, cho dù so ra kém trên đài Lăng Hiên, khẳng định cũng có thể một trận chiến, cầm cái vỉ đập ruồi đi lên... Đây là muốn điên rồi sao?”"),t("br"),t("br"),n._v("Nhìn thấy Tiêu Đàm bộ dạng, tất cả mọi người bối rối, ngay cả Liêu điện chủ mấy người cũng nguyên một đám khóe miệng co giật, muốn nắm tóc."),t("br"),t("br"),n._v("Đối với Tiêu Đàm, bọn họ đưa cho cực lớn kỳ vọng cao, kết quả cái tên này, cùng Trương sư huấn luyện mấy ngày, làm sao cũng biến thành bệnh tâm thần?"),t("br"),t("br"),n._v("Cầm cái hạ phẩm Thánh khí, thậm chí Bán Thánh khí... Đều có thể chịu đựng, một cái thân trúc làm vỉ đập ruồi, hư không dùng sức... Như vậy cũng dám lên đài, đây là muốn điên ah!"),t("br"),t("br"),n._v("“Ngươi... Xác định dùng cái này so với ta võ?”"),t("br"),t("br"),n._v("Lăng Hiên cũng vẻ mặt đỏ lên, tức giận đến sắp nổi khùng."),t("br"),t("br"),n._v("Chiến sư trao đổi, trang nghiêm mà có uy nghiêm, cái tên này ngược lại tốt, cầm vỉ đập ruồi liền đến, quả thực không đem để vào mắt, cố ý nhục nhã."),t("br"),t("br"),n._v("“Ừm! Nếu như ngươi cảm thấy chịu thiệt, có thể sử dụng vũ khí!” Tiêu Đàm gật đầu."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Hàm răng cắn đến khanh khách vang vọng, Lăng Hiên bàn tay lớn hất lên: “Không cần, ta hôm nay liền dùng song chưởng giáo huấn ngươi, để các ngươi Thanh Nguyên Chiến sư đường biết... Yếu liền là yếu, chơi đùa nhiều hơn nữa, cũng vô dụng!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nương theo lời nói, một chưởng bắn ra, tiếng sấm vang rền, chân khí như điện, thời gian nháy mắt, toàn bộ tỷ thí đài liền bị tức tức che kín, dường như đi nhầm một bước, liền bị công kích, rơi vào bị động."),t("br"),t("br"),n._v("“Thử một chút đi!”"),t("br"),t("br"),n._v("Lên tiếng, Tiêu Đàm cũng không nhiều lời, lòng bàn tay vỉ đập ruồi nhẹ nhàng giơ lên, đối trước mặt lôi đình liền chụp đi qua."),t("br"),t("br"),n._v("“Tự tìm cái chết...”"),t("br"),t("br"),n._v("Thấy đối phương thực có can đảm dùng cái đồ chơi này mạnh mẽ chống đỡ hắn bôn lôi thủ, Lăng Hiên cắn răng, bàn tay cuồn cuộn, bỗng nhiên chụp lại."),t("br"),t("br"),n._v("Hắn muốn dùng lực lượng mạnh nhất, một chiêu liền đem đối phương đánh bại, để hắn biết... Cái gì là cường giả chân chính."),t("br"),t("br"),n._v("“Không cần nhìn, thắng bại đã định!” Triệu nghị đường chủ lắc đầu."),t("br"),t("br"),n._v("“Đúng vậy a, bôn lôi như điện, chiến lực như hồng, mặc dù chỉ là Thánh vực nhị trọng trung kỳ, trên thực tế Thánh vực tam trọng cường giả, cũng chưa chắc có thể vượt qua, Bán Thánh vũ khí, đều có thể tại chỗ đánh nát... Một cái gậy trúc làm thành vỉ đập ruồi... Khẳng định không kiên trì nổi!”"),t("br"),t("br"),n._v("Lưu Tần Sơn đường chủ cũng gật đầu."),t("br"),t("br"),n._v("“Nhìn tới năm nay Thanh Nguyên đế quốc, đoán chừng một cái danh ngạch đều không cầm được...” Ngụy Thiên Thư đường chủ nhẹ nhàng cười một tiếng, lời còn chưa dứt, liền nghe đến “Đùng!” Một cái, thanh âm thanh thúy, vang vọng toàn bộ đại sảnh."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Nhịn không được sững sờ, vội vàng ngẩng đầu nhìn lại, chỉ thấy Lăng Hiên bôn lôi thủ còn chưa tới đến Tiêu Đàm trước mặt, đối phương liền một vỉ đập ruồi, đập vào trên mặt của hắn."),t("br"),t("br"),n._v("Toàn bộ mặt đều là màu đỏ một chút, tựa như dùng muôi vớt che mặt, bị lăn dầu giội."),t("br"),t("br"),n._v("“Ah... Ta muốn giết ngươi!”"),t("br"),t("br"),n._v("Hiển nhiên cũng không nghĩ tới, đối phương vỉ đập ruồi nhanh như vậy, bị trước mặt mọi người quất mặt, Lăng Hiên khí sắp nổi khùng, bỗng nhiên vọt tới lại là một chưởng vỗ ra."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Bàn tay còn chưa tới đến đối phương trước mặt, lần nữa giòn vang, vỉ đập ruồi lần nữa rơi vào trên mặt."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lăng Hiên thật muốn nổi khùng, thân thể chợt lóe, trên đài lập tức xuất hiện năm cái huyễn ảnh, để cho người ta nhìn không ra đến cùng cái nào là thật."),t("br"),t("br"),n._v("Thân là Chiến sư, thân kinh bách chiến, mặc dù bị liên rút hai lần, tràn đầy khuất nhục, nhưng cũng biết, trước mắt vị này cứ việc ngoài dự đoán trang trách phục, chiến đấu chân chính lực nhưng không để khinh thường, lúc này thi triển ra cường đại nhất thân pháp võ kỹ."),t("br"),t("br"),n._v("Thất xảo huyễn bộ!"),t("br"),t("br"),n._v("Thánh vực trung kỳ võ kỹ, hoàn mỹ thi triển đi ra, có thể xuất hiện bảy cái huyễn ảnh, để cho người ta phân biệt không ra chân thực giả tạo."),t("br"),t("br"),n._v("Lực công kích không bằng bôn lôi thủ, nhưng danh khí nhưng lại xa xa vượt qua, từ khi luyện thành, chưa hề bại qua."),t("br"),t("br"),n._v("“Năm cái huyễn ảnh, đã bị hắn tu luyện đến tình trạng xuất thần nhập hóa, ngay cả ta đều phân biệt không ra...” Triệu nghị đường chủ vuốt râu, đang nghĩ nói lên hai câu, vãn hồi chút mặt mũi, liền nghe đến phía trước lần nữa “Đùng!” Một tiếng."),t("br"),t("br"),n._v("Vỉ đập ruồi, lần nữa rơi vào đối phương trên mặt, công bằng."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Lăng Hiên phát điên, dưới chân tăng tốc, huyễn ảnh càng thêm mê man, khó mà phân biệt."),t("br"),t("br"),n._v("Đùng! Đùng! Đùng!"),t("br"),t("br"),n._v("Liên tục ba đập, mỗi một lần đều chuẩn xác đập vào trên mặt, dường như huyễn ảnh đối trước mắt vị này, không có một chút ảnh hưởng."),t("br"),t("br"),n._v("“Lăng Hiên, hắn dùng binh khí, ngươi dùng kiếm...”"),t("br"),t("br"),n._v("Thấy mình người một mực bị quất, liền đối phương cọng lông đều không có làm bị thương một cái, Sùng Viễn đế quốc Chiến sư cũng nhịn không được nữa, trong đám người không biết ai hô một câu."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Cũng biết trước mắt vị này không dễ dàng đối phó, Lăng Hiên cổ tay khẽ đảo, một chuôi hạ phẩm Thánh khí trường kiếm xuất hiện, còn không có thi triển, kiếm khí liền cầu vồng bắn ra, thời gian nháy mắt, toàn bộ tỷ thí đài, sóng khí cuồn cuộn."),t("br"),t("br"),n._v("“Hàn Vũ kiếm ý... Là Lăng Hiên hai mươi ba tuổi năm đó, cảm ngộ mưa đông, bản thân cảm ngộ đi ra, một kiếm thi triển nhuận bát phương, không có người trốn được...”"),t("br"),t("br"),n._v("Triệu nghị đường chủ tiếp tục nói, vẫn như cũ chưa nói xong, chỉ thấy đứng tại kiếm khí bên trong Tiêu Đàm, không có quá nhiều động tác, trong tay vỉ đập ruồi lần nữa dựng thẳng lên."),t("br"),t("br"),n._v("Đùng! Đùng! Đùng!"),t("br"),t("br"),n._v("Lại là liên tiếp ba lần, Lăng Hiên toàn bộ mặt đều bị quất sưng."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Bi phẫn phía dưới, Lăng Hiên trường kiếm đưa ra, đủ loại kiếm chiêu, lộn xộn thay phiên mà tới."),t("br"),t("br"),n._v("Tiêu Đàm cầm trong tay vỉ đập ruồi, giống như là xuyên qua bụi hoa bươm bướm, dù là đóa hoa lại kín, đều có thể nhẹ nhõm tránh thoát, không chấm chút nào, càng quan trọng hơn là, trên tay không có chút nào ngừng lại."),t("br"),t("br"),n._v("Đùng! Đùng! Đùng! Đùng!"),t("br"),t("br"),n._v("Thanh âm ổn định mà có tiết tấu, nghe thế mà giống như là đang diễn tấu âm nhạc."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cái khác ba đại Chiến sư đường trợn mắt lên, khó tin, Hình đường chủ cùng Liêu điện chủ mấy người cũng hai mặt nhìn nhau, có chút phản ứng không kịp."),t("br"),t("br"),n._v("Trương Huyền phương pháp huấn luyện, bọn họ tất cả đều nhìn, quả thực cùng hố người không có gì khác nhau, làm sao... Một cái nháy mắt, Tiêu Đàm biến hóa như thế đại?"),t("br"),n._v("“Thật lợi hại, không nghĩ tới đập ruồi có uy lực lớn như vậy, ta thấy tận mắt Tiêu Đàm tại nhà vệ sinh tìm con ruồi, một cái vợt đi xuống, nói đánh gãy đối phương chân trước, tuyệt sẽ không đập tới chân sau!”"),t("br"),t("br"),n._v("“Con ruồi nhỏ như vậy, hơn nữa lại phi hành, có thể làm được điểm ấy?”"),t("br"),t("br"),n._v("“Đúng vậy a, lực khống chế có thể nói chính xác như phát...”"),t("br"),t("br"),n._v("Nhìn trên đài mấy vị Thanh Nguyên đế quốc Chiến sư lặng lẽ nói."),t("br"),t("br"),n._v("“Ah, ta hiểu được...”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, Liêu điện chủ vỗ đùi, bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("“Rõ ràng cái gì?” Ngụy điện chủ nhìn qua."),t("br"),t("br"),n._v("“Tiêu Đàm là võ kỹ điện thiên tài, tu luyện võ kỹ chủng loại hỗn tạp, thời điểm chiến đấu, rất dễ dàng phân tán tinh thần, sức chiến đấu tự nhiên cũng phát huy không đến cực hạn... Trương sư để hắn đập ruồi, thoạt nhìn không có tác dụng gì, trên thực tế lại liên lụy đến đại đạo chí giản đạo lý! Mỗi một con ruồi phi hành lộ tuyến khác biệt, đại biểu khác biệt võ kỹ cùng thân pháp, như muốn đánh trúng, cần quan sát lộ tuyến, trước thời hạn dự phán... Trọng yếu nhất chính là, càng có thể để cho hắn xóa táo bạo chi tâm, đem học tập tất cả võ kỹ, dung hội quán thông...”"),t("br"),t("br"),n._v("Liêu điện chủ giải thích."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngụy điện chủ trầm tư."),t("br"),t("br"),n._v("Suy nghĩ kỹ một chút, đúng là như thế."),t("br"),t("br"),n._v("Thân là võ kỹ điện thiên tài, Tiêu Đàm bản thân liền rất kiêu ngạo, để hắn đập ruồi, tất nhiên đã trải qua rất khó trong lòng lộ trình, nghĩ biện pháp vượt qua, đến hoàn toàn thích ứng, đối võ kỹ lý giải, tự nhiên sẽ đạt tới một cái mới tinh tình trạng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, đối phương bất kể như thế nào biến chiêu, làm sao di động, đều chạy không khỏi vỗ... Con ruồi chân đều có thể vuốt ve, không thương tổn căn bản, đối thủ mặt như thế lớn, tự nhiên không tránh khỏi."),t("br"),t("br"),n._v("Ba ba ba đùng!"),t("br"),t("br"),n._v("Thanh âm càng ngày càng vang dội."),t("br"),t("br"),n._v("“Ngừng... Ta nhận thua!”"),t("br"),t("br"),n._v("Không biết qua bao lâu, Lăng Hiên tất cả võ kỹ đều thi triển không sai biệt lắm, kết quả lại ngay cả đối phương một cọng lông đều không có làm bị thương, đành phải tràn đầy chán nản dừng tay."),t("br"),t("br"),n._v("Lúc này mặt đã không hình người, sưng cùng bánh nướng đồng dạng."),t("br"),t("br"),n._v("“Thanh Nguyên đế quốc, Tiêu Đàm chiến thắng!”"),t("br"),t("br"),n._v("Có người nhận thua, tranh tài lập tức có kết quả."),t("br"),t("br"),n._v("“Ta, Thiên Vân đế quốc Bạch Tốn, Thánh vực nhị trọng sơ kỳ, đồng dạng muốn thỉnh giáo Thanh Nguyên đế quốc Chiến sư đường cao thủ!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Một cái thân thể thanh niên cường tráng, nhảy lên đài cao."),t("br"),t("br"),n._v("“Ta Thánh vực nhị trọng sơ kỳ, đánh với ngươi một trận!”"),t("br"),t("br"),n._v("Thạch Hạo đi tới."),t("br"),t("br"),n._v("“Bạch Tốn là Thiên Vân đế quốc quyền pháp điện thiên tài, một đôi thiết quyền có thể nói vô địch, vị này quét dọn nhân viên muốn vượt qua, rất khó!”"),t("br"),t("br"),n._v("“Khụ khụ, hắn không phải quét dọn nhân viên, là chúng ta Nội Tức điện thiên tài, Thạch Hạo!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy hai người lên đài, hai bên Chiến sư riêng phần mình xiết chặt nắm đấm."),t("br"),t("br"),n._v("“Hình đường chủ, ngươi cảm thấy người của ngươi có thể thắng lợi ư?”"),t("br"),t("br"),n._v("Lưu Tần Sơn đường chủ nhìn qua."),t("br"),t("br"),n._v("Nếu như nói vừa rồi tất cả mọi người cảm thấy Thanh Nguyên Chiến sư đường, hoàn toàn chán chường, nhưng Tiêu Đàm thực lực, để hắn lau mắt mà nhìn, lần nữa nhìn thẳng vào lên."),t("br"),t("br"),n._v("“Ta... Cũng không biết!”"),t("br"),t("br"),n._v("Hình đường chủ lắc đầu."),t("br"),t("br"),n._v("Nói thật, Trương sư phương pháp huấn luyện thực sự quá mức quái dị, liền xem như hắn, đều không hoàn toàn không hiểu."),t("br"),t("br"),n._v("Đường đường Nội Tức điện thiên tài Thạch Hạo, đi quét vài ngày bồn cầu... Ai biết thực lực tăng tiến còn là lui về sau?"),t("br"),t("br"),n._v("“Muốn vượt qua Bạch Tốn, tuyệt không có khả năng!” Thiên Vân đế quốc Ngụy Thiên Thư đường chủ một mặt tự tin: “Ta đối với hắn có tuyệt đối tự tin, hắn là ta học sinh thân truyền, quyền pháp vô địch, căn bản không có người có thể chiến thắng...”"),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Nói còn chưa dứt lời, trên đài chiến đấu đã bắt đầu."),t("br"),t("br"),n._v("Bạch Tốn biết Chiến sư giao đấu, không có chút nào lưu tình, một quyền oanh kích mà tới."),t("br"),t("br"),n._v("Dùng sức chiến đấu mạnh nhất."),t("br"),t("br"),n._v("Ngay tại nắm đấm sắp rơi vào Thạch Hạo trước mặt thời điểm, chỉ thấy bàn tay hắn bỗng nhiên một dài, trực tiếp đặt tại trên mặt của đối phương, như là chùi bồn cầu đồng dạng, dùng sức lau."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Bạch Tốn khóe mắt."),t("br"),t("br"),n._v("Gào lên một tiếng, nắm đấm lần nữa đánh tới, không khí đều tựa hồ bị nghiền ép bạo liệt, có điều, đồng dạng không đi tới đối phương trước mặt, bàn tay của đối phương xuất hiện lần nữa tại trên mặt của mình, tiếp tục đùa cợt giặt, vặn ra một cái mảng lớn bùn viên."),t("br"),t("br"),n._v("“Ngươi mặt... Thật nhiều ngày không có rửa đi!”"),t("br"),t("br"),n._v("Thạch Hạo tại trên quần áo cọ xát tay."),t("br"),t("br"),n._v("“...” Bạch Tốn."),t("br"),t("br"),n._v("“Ta giết ngươi...”"),t("br"),t("br"),n._v("Bàn tay nhoáng một cái, Bạch Tốn sắc mặt tái xanh, bùn viên mất một chỗ, trên nắm tay mang lên trên quyền sáo, lần nữa oanh kích mà tới."),t("br"),t("br"),n._v("Quyền pháp điện Chiến sư, quyền sáo có thể phát huy ra càng mạnh sức chiến đấu, tương đương với sử dụng binh khí."),t("br"),t("br"),n._v("Gặp hắn dùng binh khí, Thạch Hạo cũng không xoắn xuýt, bàn tay một trảo, ném ở cách đó không xa chổi lông gà xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("“Phá băng quyền!”"),t("br"),t("br"),n._v("Một tiếng gầm thét, quyền sáo quang mang đại thịnh, Bạch Tốn một quyền đi, tựa hồ muốn trước mắt thân ảnh nghiền thành bụi phấn."),t("br"),t("br"),n._v("Nắm đấm mới hướng về phía trước đưa ra, khoảng cách thân thể của đối phương còn cách một đoạn, đột nhiên cảm thấy trên mặt căng thẳng... Một cái chổi lông gà đã nhét vào trong miệng, mang theo âu mùi thối, ở bên trong nhúng mấy lần."),t("br"),t("br"),n._v("“...” Bạch Tốn."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1205"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1207"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);