(window.webpackJsonp=window.webpackJsonp||[]).push([[250],{603:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1201-vi-su-thay-nguoi-lam-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1201-vi-su-thay-nguoi-lam-chu"}},[n._v("#")]),n._v(" Chương 1201: Vi sư thay ngươi làm chủ!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1201: Vi sư thay ngươi làm chủ! "),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy nữ hài hốc mắt có chút ửng hồng, dường như đã mới vừa khóc, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Mặc dù bị thổ lộ, không phải hắn muốn nhìn đến, nhưng nếu có người bắt nạt học sinh, tuyệt đối sẽ không hạ thủ lưu tình!"),t("br"),t("br"),n._v("“Không có gì...” Lạc Thất Thất nhìn qua: “Chỉ là muốn cùng lão sư một mình đợi một hồi!”"),t("br"),t("br"),n._v("“Ách, tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương bộ dáng này, khả năng có tâm sự gì, Trương Huyền đành phải lắc đầu."),t("br"),t("br"),n._v("Quá mức ưu tú, bị người ưa thích, là rất bình thường, chỉ cần có thể duy trì sơ tâm, cho dù một mình ở chung, cũng không tính là gì."),t("br"),t("br"),n._v("Làm được chính, ngồi đến đoan, đối phương tự nhiên sẽ bỏ đi suy nghĩ, tránh né càng nhiều, ngược lại sẽ cho đối phản sinh ra càng nhiều đoán mò, gây nên càng nhiều phiền phức."),t("br"),t("br"),n._v("Gặp hắn đáp ứng, Lạc Thất Thất vui mừng: “Cái kia... Chúng ta tìm một chỗ an tĩnh đi, nơi này quá ồn!”"),t("br"),t("br"),n._v("Chiến Sư đường, Huyền Huyền hội... Đâu đâu cũng có người quen, quả thực có chút ầm ĩ."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hai người hướng ra phía ngoài bay đi, thời gian không dài đi tới ngoài thành một tòa núi lớn phía trên, tại bên vách núi một cái trên tảng đá lớn rơi xuống."),t("br"),t("br"),n._v("Ngồi tại tảng đá phía trên, nhìn trước mắt trùng điệp vô tận phòng xá, Lạc Thất Thất không biết nghĩ cái gì."),t("br"),t("br"),n._v("Gió núi thổi lất phất mái tóc, đáp lên không tỳ vết chút nào trên mặt, cả người như là trong tranh đi ra tới đồng dạng."),t("br"),t("br"),n._v("Không thể không nói, cô gái này quả thực rất đẹp, hơn nữa rất có khí chất, từ trong tới ngoài cho người ta một loại rất an tĩnh cảm giác."),t("br"),t("br"),n._v("Thoải mái, sạch sẽ."),t("br"),t("br"),n._v("“Là không phải có tâm sự gì? Vi sư mặc dù bất tài, chỉ cần đủ khả năng, tuyệt sẽ không lùi bước!”"),t("br"),t("br"),n._v("Thấy đối phương đem hắn đưa đến nơi này, nhưng không nói lời nào, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Đối phương bộ đáng, khẳng định là có tâm sự, nhưng không có nói, cũng không có biện pháp giúp vội vàng giải quyết."),t("br"),t("br"),n._v("“Không có gì, chỉ là nhớ tới khi còn bé một số việc.”"),t("br"),t("br"),n._v("Nở nụ cười, Lạc Thất Thất an tĩnh nhìn về phía trước Thanh Nguyên thành: “Ta khi còn bé, thích nhất làm, liền là lúc rảnh rỗi, ngồi tại nóc phòng, nhìn mặt trời mọc, mặt trời lặn, bốn phía phi hành chim tước... Cảm thấy thật vui vẻ.”"),t("br"),t("br"),n._v("“Có một lần, ta thích một cái bảo vật, mười phần muốn, liền quấn lấy cha ta, để hắn giúp ta tìm kiếm, bị ta quấn bất đắc dĩ, cha đáp ứng... Bất quá, cần ta tại trong vòng thời gian quy định học được một bộ công pháp, ngay sau đó ta cố gắng tu luyện, tu luyện ah tu luyện, không biết ngày đêm, trọn một năm, cuối cùng thành công! Cha cũng không có lỡ hẹn, đem bảo vật tìm tới, cho ta.”"),t("br"),t("br"),n._v("“Cầm tới bảo vật cùng ngày, ta đặc biệt vui vẻ, đây chính là hao tốn công phu rất lớn mới đến, mỗi ngày đều tùy thân mang theo, thậm chí ôm nó ngủ... Đáng tiếc, về sau, một lần cùng người thời điểm chiến đấu, bị hủy diệt... Ta khóc trọn ba ngày, cha lại tìm cho ta một cái tương đồng, bất kể từ chỗ nào một điểm nhìn, đều giống nhau như đúc, nhưng mà... Ta đã không muốn!”"),t("br"),t("br"),n._v("Hai tay ôm bắp chân, Lạc Thất Thất cái cằm đáp lên phía trên, đầu nghiêng nhìn qua: “Lão sư biết tại sao không?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Đây cũng không phải là ta thích cái này... Dù là giống nhau như đúc, cũng không phải!” Lạc Thất Thất nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không biết trả lời như thế nào, Trương Huyền đành phải lên tiếng."),t("br"),t("br"),n._v("Thật ra thì khi còn bé, người người đều trải qua loại tình huống này, một cái mười phần yêu thích vật phẩm, một khi hủy, cho dù mua được đồng dạng, cũng mất cái loại này kỳ vọng."),t("br"),t("br"),n._v("Đây là tình cảm, không phải vật phẩm có thể thay thế."),t("br"),t("br"),n._v("“Về sau từ từ lớn lên, ta cũng dần dần rõ ràng... Ưa thích đồ vật, chưa hẳn liền thật sự có thể một mực nắm giữ, liền cùng cái này vỡ vụn đồ vật đồng dạng, dù là dùng hết thủ đoạn, cũng không có khả năng giống như lúc đầu!”"),t("br"),t("br"),n._v("Nhìn qua, Lạc Thất Thất dường như có không ít lại nói, nhưng lại không nói ra được."),t("br"),t("br"),n._v("“Chuyện gì xảy ra? Là có người hay không bắt nạt ngươi? Nếu như tội ác tày trời, vi sư thay ngươi giết hắn!”"),t("br"),t("br"),n._v("Thấy nàng vẻ mặt không đúng, Trương Huyền cau mày."),t("br"),t("br"),n._v("“Hì hì!”"),t("br"),t("br"),n._v("Lạc Thất Thất nở nụ cười, một mặt thú vị nhìn qua: “Ngươi thật có thể thay ta giết hắn?”"),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),t("span",[t("br"),n._v("Truyện Của Tui cＨấm vn ")]),n._v(" Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Là lão sư ngươi bắt nạt ta, động thủ đi...” Lạc Thất Thất nói."),t("br"),t("br"),n._v("“...” Trương Huyền im lặng."),t("br"),t("br"),n._v("Không nghĩ tới lại bị nha đầu này sáo lộ."),t("br"),t("br"),n._v("Lần trước trong nội tâm nàng có nghi ngờ, bản thân giúp một tay giải đáp, sau đó bị thừa cơ thổ lộ, lần này, nhìn tới lại là như vậy, giả trang ra một bộ điềm đạm đáng yêu bộ dáng, chỉ cần đáp lời, liền tiến vào hố sâu."),t("br"),t("br"),n._v("Chính không biết trả lời như thế nào, chỉ thấy nữ hài ánh mắt mịt mù nhìn qua: “Lão sư, ngươi lần trước trả lời cũng không được, ngươi biết ta đối với ngươi... Không chỉ là ưa thích!”"),t("br"),t("br"),n._v("“Không biết lớn nhỏ, nếu như tìm ta chỉ là muốn nói những này, vi sư liền đi!”"),t("br"),t("br"),n._v("Trương Huyền đứng dậy."),t("br"),t("br"),n._v("“Cùng lão sư chỉ đùa một chút... Đừng nóng giận!” Thè lưỡi, Lạc Thất Thất vội nói."),t("br"),t("br"),n._v("“Cũng không được cười, về sau không thể loạn mở!” Trương Huyền xua tay."),t("br"),t("br"),n._v("“Vâng! Lão sư... Có thể sẽ dạy ta một lần luyện đan ư?”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Lạc Thất Thất dừng lại một chút, nhìn qua."),t("br"),t("br"),n._v("“Có thể!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền gật đầu: “Nhưng có muốn học tập đan dược?”"),t("br"),t("br"),n._v("Đan dược chủng loại rất nhiều, mỗi một loại đều có không giống nhau phương pháp luyện chế."),t("br"),t("br"),n._v("“Ta muốn học tập luyện chế cấp bảy Linh Nguyên đan, không biết lão sư có thể hay không dạy cặn kẽ ta một lần...” Lạc Thất Thất nói."),t("br"),n._v("“Linh Nguyên đan?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Mới vừa cùng Hình đường chủ nói qua, bản thân muốn Linh Nguyên đan, ý định đi luyện đan sư công hội tìm kiếm, không nghĩ tới Lạc Thất Thất liền muốn học tập."),t("br"),t("br"),n._v("“Vâng!” Lạc Thất Thất nháy nháy mắt."),t("br"),t("br"),n._v("“Nhưng có đan phương cùng dược liệu?” Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Đã nàng muốn học, vừa vặn có thể suy tính một cái phương pháp luyện chế, đến lúc đó đi luyện đan sư công hội, liền có thể miễn đi không ít phiền phức."),t("br"),t("br"),n._v("“Ta trước thời hạn liền chuẩn bị tốt rồi...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Lạc Thất Thất cổ tay khẽ đảo, lấy ra một cái ngọc bài, đưa tới, Trương Huyền thần thức quét qua, nội dung phía trên, lập tức đi vào trong óc."),t("br"),t("br"),n._v("Chính là Linh Nguyên đan đan phương."),t("br"),t("br"),n._v("Hắn đối luyện đan lý giải đã đạt tới thất tinh đỉnh phong, dựa theo đan phương cân nhắc một lần, tìm ra tốt nhất phương pháp luyện chế."),t("br"),t("br"),n._v("“Đây là dược liệu...”"),t("br"),t("br"),n._v("Đang nghĩ đem hắn nói ra, chỉ thấy Lạc Thất Thất cười khanh khách nhìn qua, tay ngọc một phen, trước mắt xuất hiện một đống lớn dược liệu."),t("br"),t("br"),n._v("“Ngươi từ nơi nào tìm đến?”"),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Nữ hài lấy ra dược liệu, mỗi một cây đều có mấy ngàn năm dược linh, loại này cấp bậc, mỗi một cây đều là giá trên trời."),t("br"),t("br"),n._v("Bình thường Linh Nguyên đan, năm mươi năm phần dược vật liền có thể thành công, mấy ngàn năm, dược tính chân, nhiều gấp mấy chục lần không thôi."),t("br"),t("br"),n._v("“Lão sư biết ta thích nghiên cứu, đã từng tìm được một chỗ di tích, những thuốc này, liền là từ trong đó lấy được!”"),t("br"),t("br"),n._v("Lạc Thất Thất giải thích."),t("br"),t("br"),n._v("“Thật sự là vận khí tốt!” Trương Huyền cảm khái."),t("br"),t("br"),n._v("Căn cứ một kiện binh khí thủ pháp luyện chế, liền có thể suy đoán ra Ngô Dương Tử bị bắt Địa quật vị trí, đơn phần này cẩn thận, tìm tới một, hai cái di tích, cũng quả thực không tính là gì."),t("br"),t("br"),n._v("“Lão sư, những thuốc này, có thể luyện chế Linh Nguyên đan a?” Lạc Thất Thất nhìn qua."),t("br"),t("br"),n._v("“Đương nhiên có thể, không chỉ như vậy, khẳng định còn có thể luyện chế ra cực kỳ cao cấp cái khác...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu: “Bất quá, dược liệu quá mức trân quý, không thể có mảy may phạm sai lầm, nếu không, một khi nổ lô, chúng ta đều không chịu đựng nổi!”"),t("br"),t("br"),n._v("Dược liệu này quá trân quý, chỉ cần trình tự không sai, luyện chế ra Linh Nguyên đan rất đơn giản, nhưng trân quý như thế dược vật phối chế tốt rồi, làm không cẩn thận có thể đánh vỡ trước đó Linh Nguyên đan phẩm cấp, để hắn đạt tới cấp bậc cao hơn, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lạc Thất Thất gật đầu."),t("br"),t("br"),n._v("“Nếu là loại cấp bậc này dược vật, trước đó phương pháp luyện chế cần lại cải tiến một cái...”"),t("br"),t("br"),n._v("Dược liệu niên kỉ hạn khác biệt, phương pháp luyện chế tự nhiên cũng không giống nhau, đứng tại chỗ, Trương Huyền lại cân nhắc một lần, muốn ăn đòn một cái càng thêm phương pháp thích hợp, lúc này mới cổ tay khẽ đảo, đem Kim Nguyên đỉnh phóng ra."),t("br"),t("br"),n._v("Mặc dù cái tên này là Khí đỉnh, nhưng thực lực mạnh mẽ, chỉ cần cộng thêm mấy cái phong ấn lời nói, dùng để luyện đan không có vấn đề quá lớn."),t("br"),t("br"),n._v("“Để nó luyện chế đi! Chí ít có thể bảo đảm sẽ không nổ lô!”"),t("br"),t("br"),n._v("“Vâng!” Lạc Thất Thất lên tiếng."),t("br"),t("br"),n._v("Tất cả chuẩn bị ổn thoả, Trương Huyền lấy ra giấy bút, đem luyện đan trình tự, viết đi ra, để nữ hài ghi nhớ, lúc này mới chuẩn bị bắt đầu."),t("br"),t("br"),n._v("Có hắn ở một bên chỉ điểm, lại thêm trước đó trình tự, luyện chế quá trình mười phần thuận lợi, thời gian không dài, lô đỉnh mở ra, linh khí nồng nặc trực tiếp phóng hướng thiên không, năm viên viên thuốc, “Sưu!” Bay ra, muốn chạy trốn."),t("br"),t("br"),n._v("“Là chuẩn cấp tám đan dược!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền một tiếng phân phó, Kim Nguyên đỉnh lập tức vọt lên, đem chạy trốn đan dược bắt lấy, cất vào sớm chuẩn bị tốt bình ngọc."),t("br"),t("br"),n._v("Linh Nguyên đan, là cấp bảy đan dược, nhưng mà Lạc Thất Thất cho dược liệu niên đại quá cao, dược tính mười phần, vậy mà luyện chế được năm cái chuẩn cấp tám, cho dù không có đạt tới cấp tám, dược hiệu cũng hơn xa bình thường Linh Nguyên đan."),t("br"),t("br"),n._v("“Có thể luyện chế ra như vậy cấp bậc dược vật, Linh Nguyên đan phương pháp luyện chế, ngươi đã hoàn toàn học được...”"),t("br"),t("br"),n._v("Đem bình ngọc đưa cho nữ hài, Trương Huyền cười cười."),t("br"),t("br"),n._v("Cái này những đan dược này không riêng gì chuẩn cấp tám, hơn nữa còn đạt đến đan văn cấp bậc, hoa văn chảy xuôi, cho người ta một loại bất cứ lúc nào cũng sẽ phá không mà bay cảm giác."),t("br"),t("br"),n._v("Đan dược chia làm: Thành đan, sung mãn, hoàn mỹ, đan văn."),t("br"),t("br"),n._v("Có thể luyện chế ra đan văn, nói rõ đối loại đan dược này lý giải, đã đạt đến đỉnh phong nhất."),t("br"),t("br"),n._v("Chỉ cần sau khi trở về nghiêm túc tiêu hóa, đối khuôn mặt lý giải, tất nhiên có thể tăng lên một cái đại cấp bậc."),t("br"),t("br"),n._v("“Là lão sư chỉ điểm xuống mới thành công, đan dược về lão sư!” Cánh tay duỗi ra, Lạc Thất Thất đem bình thuốc đưa tới."),t("br"),t("br"),n._v("“Ta không thể nhận...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Dược liệu là đối phương, cũng là đối phương luyện chế, bản thân mặc dù chỉ điểm một cái, nhưng còn chưa đủ lấy nhận được như vậy thù lao."),t("br"),t("br"),n._v("“Cái kia... Như vậy, ta một viên, còn lại bốn cái cho lão sư, nếu như ngươi cảm thấy ta thua thiệt lời nói, có thể dùng linh thạch mua sắm!” Biết ý nghĩ của hắn, Lạc Thất Thất nặn ra một viên, đem còn lại đưa tới."),t("br"),t("br"),n._v("“Cái này... Cũng tốt!” Chần chờ một chút, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn vừa vặn cần Linh Nguyên đan, loại cấp bậc này, một viên có thể đủ đuổi kịp bình thường mấy chục viên trở lên, có cái này bốn cái đan dược, xung kích Thai Anh cảnh đỉnh phong, có lẽ liền không lại khó khăn!"),t("br"),t("br"),n._v("Lấy ra một viên trữ vật giới chỉ, đem từ Giám Bảo các nơi đó lấy được một vạn viên linh thạch thượng phẩm, để vào trong đó đưa tới."),t("br"),t("br"),n._v("Tiện tay nhận lấy, Lạc Thất Thất đem nhẫn sắp xếp gọn, đột nhiên ngẩng đầu nhìn Trương Huyền một cái, trong mắt mang theo trông đợi: “Lão sư, vừa rồi ngươi nói, có người bắt nạt ta, ngươi sẽ ra tay, phải không?”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không nghĩ tới nàng sẽ hỏi ra lời này, Trương Huyền sửng sốt một chút, nhẹ gật đầu: “Thực sự có người bắt nạt ngươi lời nói, vi sư... Thay ngươi làm chủ!”"),t("br"),t("br"),n._v("PS: Quyển thứ chín . Trương Huyền rốt cục muốn đi đến đại lục đỉnh phong nhất, hơn nữa cùng trong đó đỉnh phong thế lực, sinh ra va chạm... Kính xin kỳ vọng! Cái gì? Một cuốn kết thúc, có muốn hay không nghỉ ngơi một ngày? Hừ, lão nhai chưa từng nghỉ ngơi, buổi chiều còn có đổi mới, về sau... Xin gọi ta, nhai người sắt! Hoặc là người sắt nhai! Cố gắng đổi mới, ta là chuyên ngành."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1209"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1211"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);