(window.webpackJsonp=window.webpackJsonp||[]).push([[252],{605:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1203-danh-su-đuong-xay-ra-chuyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1203-danh-su-đuong-xay-ra-chuyen"}},[n._v("#")]),n._v(" Chương 1203: Danh Sư đường xảy ra chuyện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1203: Danh Sư đường xảy ra chuyện"),t("br"),t("br"),n._v("Thanh Nguyên đế quốc Danh Sư đường."),t("br"),t("br"),n._v("Chiếu rơm song song bày ở trên mặt đất, tám cỗ thi thể nằm ngang ở phía trên."),t("br"),t("br"),n._v("Mỗi một cái thi thể, đều người mặc danh sư phục, trước ngực bảy vì sao lấp lánh, hiển lộ rõ ràng thân phận."),t("br"),t("br"),n._v("Ở chính giữa lão giả, Trương Huyền hết sức quen thuộc, bởi vì hắn đãi ngộ không công bằng, mới tiến hành xông đường, thậm chí còn cùng hắn chiến đấu một hồi... Vốn cho rằng vị này, sẽ chờ đến tổng bộ thẩm vấn, cuối cùng bị tước đoạt đường chủ tư cách, không nghĩ tới, vậy mà chết tại nơi này."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Trương Huyền cau mày, nhìn về phía Ngô sư."),t("br"),t("br"),n._v("“Là buổi sáng hôm nay sự tình... Dựa theo ngươi cho ta danh sách, ta mới tra hỏi tám người, kết quả... Trong đường liền chết tám vị danh sư!”"),t("br"),t("br"),n._v("Ngô sư sắc mặt tái xanh."),t("br"),t("br"),n._v("Trương Huyền từ Thanh Điền Hoàng trong miệng biết danh sách về sau, truyền cho hắn, vì không rút dây động rừng, lặng lẽ thẩm vấn, vừa mới thẩm vấn người thứ tám, liền nghe đến thuộc hạ báo cáo, trong đường chết tám vị thất tinh cấp bậc danh sư."),t("br"),t("br"),n._v("Bao quát... Cẩu đường chủ!"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Thẩm vấn tám người, chết tám vị danh sư, hơn nữa đều là thất tinh, rất rõ ràng, đối phương đang cho bọn hắn cảnh cáo."),t("br"),t("br"),n._v("“Hơn nữa, cái chết của bọn họ cùng Trần Triết đồng dạng, đều là linh hồn xuất khiếu, chỉ còn lại có thân thể...”"),t("br"),t("br"),n._v("Ngô sư nói tiếp."),t("br"),t("br"),n._v("Trương Huyền trầm mặc."),t("br"),t("br"),n._v("Vừa mới tiến đến, hắn liền phát hiện, bao quát Cẩu đường chủ ở bên trong mọi người, đều là bị người dùng câu hồn thủ pháp, bắt đi ba hồn bảy phách, từ đó biến thành thi thể."),t("br"),t("br"),n._v("“Là... Ngoan Nhân!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt."),t("br"),t("br"),n._v("Có thể làm được điểm ấy, liền Cẩu đường chủ đều không thể may mắn thoát khỏi, chỉ sợ chỉ có Ngoan Nhân mới có thể làm đến."),t("br"),t("br"),n._v("Chỉ là... Hắn tại Tề Bắc đầm lầy, căn cứ Thanh Điền Hoàng lời khai, dường như có cái gì giam cầm, đem hắn cấm chế, không cách nào thoát ly... Làm sao có thể dùng loại bí pháp này, liên sát nhiều như vậy danh sư?"),t("br"),t("br"),n._v("“Bọn họ ở đâu bị phát hiện?” Trong lòng nghi ngờ, quay đầu hỏi."),t("br"),t("br"),n._v("“Đều tại chỗ ở của mình, người nhà tưởng là đang nghỉ ngơi, trời sáng gọi không dậy, mới phát hiện, đã cắt đứt hô hấp!”"),t("br"),t("br"),n._v("Nói một tiếng, Ngô sư nhịn không được nhìn qua: “Là không phải cùng Thanh Điền Hoàng liên quan đến? Có thể Thanh Điền Hoàng... Không phải là bị giết ư?”"),t("br"),t("br"),n._v("Thanh Điền Hoàng thân là vu hồn sư, nắm giữ loại này bí pháp đặc thù, giết chết Trần Triết sự tình, trước đó Trương sư đã cùng hắn nói rồi... Đã đều chết rồi, là ai dùng ra chiêu này, quỷ dị như vậy?"),t("br"),t("br"),n._v("Vô duyên vô cớ tử vong, liền một điểm dấu hiệu đều không có, đã huyên náo toàn bộ Danh Sư đường lòng người bàng hoàng, đều có chút sợ hãi."),t("br"),t("br"),n._v("Người đều sẽ đối với không biết cùng không thể lý giải đồ vật sinh ra sợ hãi, trước mắt cái này đúng là như thế."),t("br"),t("br"),n._v("Cẩu đường chủ có thể nói là toàn bộ Thanh Nguyên thành thực lực mạnh nhất tồn tại, hắn đều không có kháng trụ bị lặng yên không tiếng động chém giết, bất kể là ai, đều sẽ cảm giác đến cổ rét run."),t("br"),t("br"),n._v("“Thanh Điền Hoàng là chết, nhưng mà... Phía sau hắn người kia còn chưa có chết!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Người đứng phía sau?” Ngô sư nghi ngờ."),t("br"),t("br"),n._v("Ngoan Nhân sự tình, dính dáng quá lớn, Trương Huyền cũng không nói đến đi, cho dù là hắn, cũng không rõ."),t("br"),t("br"),n._v("“Ừm, Thanh Điền Hoàng mặc dù là Dị Linh tộc một vị Hoàng giả, nhưng... Vu hồn sư truyền thừa cắt đứt, hắn có thể học được... Là sau lưng có người truyền thụ! Động thủ người, hẳn là cái này!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Truyền thụ Thanh Điền Hoàng Vu hồn người? Người này... Thực lực gì? Vượt qua phong hào đế quốc tầng lớp, ta có thể lập tức trình báo tổng bộ, để hắn phái người tới!”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Ngô sư vội nói."),t("br"),t("br"),n._v("Danh Sư đường có danh sư đường quy củ, dưới tình huống bình thường, phân bộ có thể giải quyết sự tình, nhiều nhất làm cái văn bản hình thức báo lên, sẽ không để cho người ở phía trên xuống."),t("br"),t("br"),n._v("Vừa đến, người của tổng bộ bề bộn nhiều việc, không có rảnh thay thuộc hạ chùi đít; Thứ hai, đối phân bộ người cũng là rất tốt rèn luyện."),t("br"),t("br"),n._v("Sự tình gì đều muốn xin tổng bộ... Chân chính cao cấp danh sư, còn không tươi sống bận bịu chết?"),t("br"),t("br"),n._v("Bất quá... Một khi thực lực vượt qua phong hào đế quốc tầng lớp, liền không đồng dạng, sẽ dính dáng, không biết bao nhiêu nhân sinh linh khốn khổ lầm than, hoàn toàn có thể xin tổng bộ, phái cao hơn danh sư xuống, xử lý việc này."),t("br"),t("br"),n._v("“Phái người tới, đại khái cần bao lâu?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Cách chúng ta gần nhất Tiềm Xung phong hào đế quốc, nắm giữ nửa bước bát tinh danh sư... Để bọn hắn phái người tới, đại khái muốn chừng mười ngày... Còn nắm giữ bát tinh danh sư liên minh đế quốc, hiện tại báo tin, nhanh nhất cũng muốn hai mươi ngày mới có thể đi tới!”"),t("br"),t("br"),n._v("Tính toán một chút, Ngô sư nói."),t("br"),t("br"),n._v("“Mười ngày, hai mươi ngày? Quá muộn...” Trương Huyền lắc đầu, vẻ mặt nghiêm túc: “Chờ những người này chạy tới, ta sợ Danh Sư đường sẽ chết càng nhiều!”"),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Ngô sư có chút nóng nảy."),t("br"),t("br"),n._v("Mười ngày, hai mươi ngày quả thực quá muộn, đối phương giết người trong vô hình, trong vòng một đêm, liền chết tám cái... Thật muốn động thủ, thời gian dài như vậy đi qua, chỉ sợ toàn bộ Thanh Nguyên đế quốc thất tinh danh sư, đều không khác mấy bị giết tuyệt."),t("br"),t("br"),n._v("“Thanh Điền Hoàng sau lưng người kia, đến cùng thực lực gì, ta cũng không rõ ràng lắm, bất quá... Hẳn là sẽ không vượt qua Xuất Khiếu cảnh! Nếu không, Danh Sư đường tổng bộ khẳng định đã sớm phát hiện...”"),t("br"),t("br"),t("span",[t("br"),n._v("http://ngantruyen.com/"),t("br")]),n._v(" Không có trả lời câu hỏi của hắn, Trương Huyền phân tích."),t("br"),t("br"),n._v("Danh Sư đường tổng bộ, mặc dù bỏ mặc quyền lực, gặp được khó khăn để cấp dưới phân bộ xử lý, nhưng cũng sẽ thường xuyên tuần tra thiên hạ, một khi phát hiện vượt qua đẳng cấp sinh mệnh xuất hiện, sẽ đích thân ra tay xử lý."),t("br"),t("br"),n._v("Phong hào đế quốc, đỉnh phong nhất tồn tại, liền là Xuất Khiếu cảnh... Vị này Ngoan Nhân con mắt, có thể ở lại chỗ này cũng không có bị phát hiện, nói rõ... Sẽ không có đánh vỡ cực hạn này."),t("br"),t("br"),n._v("“Cho dù không cao hơn Xuất Khiếu cảnh... Nhưng Xuất Khiếu cảnh, mỗi một tầng, đều có rất lớn chênh lệch... Chỉ cần vượt qua sơ kỳ, không phải là chúng ta có thể chống lại!”"),t("br"),t("br"),n._v("Ngô sư nói."),t("br"),n._v("Xuất Khiếu cảnh, nguyên thần xuất khiếu, có thể mượn nhờ tự nhiên chi lực, khống chế hoàn cảnh bốn phía, mỗi tăng lên một cái tiểu cấp bậc, liền cần kinh nghiệm một lần lôi kiếp, nương theo kinh nghiệm càng nhiều, thực lực cũng liền càng mạnh, mỗi một cái tiểu cấp bậc ở giữa, đều không thể so sánh nổi, thậm chí có thể nói có khác nhau một trời một vực."),t("br"),t("br"),n._v("Lấy Xuất Khiếu cảnh sơ kỳ cùng trung kỳ làm thí dụ, cả hai cho dù chỉ kém một cái tiểu cấp bậc, sức chiến đấu lại kém gấp bội."),t("br"),t("br"),n._v("Cái trước, mười cái tính gộp lại, thậm chí sắp đặt thành trận pháp, cũng chưa chắc là người sau đối thủ."),t("br"),t("br"),n._v("Bởi vậy, cho dù đối phương vẫn tại Xuất Khiếu cảnh, nhưng đạt tới đỉnh phong... Cùng sơ kỳ, chênh lệch quá xa, căn bản không có khả năng chiến thắng."),t("br"),t("br"),n._v("“Ta biết, nhưng cụ thể cấp bậc, không rõ ràng lắm... Bất quá, bây giờ không phải là rầu rĩ cái này thời điểm, ngươi... Chẳng lẽ không kỳ quái, cái tên này là như thế nào giết chết những người này ư?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền biết chỉ bằng vào loại này phỏng đoán, là không thể nào biết thực lực đối phương, mà là dọc theo thi thể đầy đất quay một vòng, mở miệng nói."),t("br"),t("br"),n._v("“Làm sao giết chết?”"),t("br"),t("br"),n._v("“Không sai, ngày đó ta bị Thanh Điền Hoàng đánh lén, biết thủ đoạn của bọn hắn, mượn nhờ tế đàn, thậm chí linh hồn, đều có thể xâm nhập ý thức của người khác, đem hồn phách bắt đi... Nói cách khác, bất kể chiêu hồn bí pháp, bao nhiêu lợi hại, đều cần môi giới, mới có thể thi triển... Giết chết tám người này môi giới là cái gì? Có hay không cẩn thận thẩm tra?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Ngoan Nhân dùng câu hồn phương pháp, khoảng cách tới gần, có thể trực tiếp tiến hành, nhưng cách nhau mấy vạn cây số, chỉ có thể mượn nhờ đủ loại đồ vật."),t("br"),t("br"),n._v("Tỷ như... Tế đàn!"),t("br"),t("br"),n._v("Nhưng tế đàn Ngô sư mang đi, chờ tổng bộ thẩm tra, những người này... Lại là chết như thế nào?"),t("br"),t("br"),n._v("“Cái này... Ta không có chú ý điểm ấy...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Ngô sư không biết trả lời như thế nào."),t("br"),t("br"),n._v("“Cẩn thận tra một chút, có lẽ có thể tra ra cái gì...”"),t("br"),t("br"),n._v("Phân phó một tiếng, Trương Huyền không nói thêm lời, Minh Lý Chi Nhãn vận chuyển lần nữa hướng trước mắt nằm rất nhiều thi thể nhìn sang."),t("br"),t("br"),n._v("Cẩu đường chủ trước khi chết biểu lộ mang theo hoảng sợ, hẳn là gặp cái gì hắn khó tin sự tình."),t("br"),t("br"),n._v("Từng đạo hoa văn tại trong mắt chảy xuôi, nhìn một hồi, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cẩu đường chủ nguyên nhân cái chết cùng Trần Triết giống nhau như đúc, đều là linh hồn bị câu đi, biến thành thi thể."),t("br"),t("br"),n._v("Cho dù Minh Lý Chi Nhãn, cũng nhìn không ra như thế về sau."),t("br"),t("br"),n._v("“Đúng rồi, cái kia Trung Thanh Vương đâu? Nhốt tại nơi nào?”"),t("br"),t("br"),n._v("Không nhìn ra cái gì, Trương Huyền nhớ tới một người, vội vàng hỏi."),t("br"),t("br"),n._v("“Hắn bị lần nữa bắt, nhốt ở Danh Sư đường địa lao, tin tức khẳng định là truyền lại không đi ra... Tối hôm qua, Sở Thiên Hành bệ hạ, ý định cầu kiến... Bất quá, bị ta cự tuyệt!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Ngô sư nói."),t("br"),t("br"),n._v("“Sở Thiên Hành cầu kiến?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Cái tên này, chỉ là cái vương gia, cũng không phải vị hoàng đế này con ruột, làm sao vừa ra sự tình, liền đến? Không tiếc tiêu tốn to lớn đánh đổi?"),t("br"),t("br"),n._v("“Vâng, bất quá hai người bọn họ không có gặp được... Sẽ không có vấn đề gì a?”"),t("br"),t("br"),n._v("Ngô sư nói."),t("br"),t("br"),n._v("“Cái này không rõ ràng lắm, nhưng... Sở Thiên Hành rất có thể cũng có vấn đề!” Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Trung Thanh Vương, thông qua Thanh Điền Hoàng ký ức, đã xác định có vấn đề... Vị hoàng đế này lại cùng hắn như vậy thân thiết, muốn nói không có vấn đề, hắn cũng không tin."),t("br"),t("br"),n._v("“Có muốn hay không bắt tới thẩm vấn?”"),t("br"),t("br"),n._v("“Trước không vội, một khi thẩm vấn, động tĩnh lập tức làm lớn chuyện, đến lúc đó, càng thêm náo động... Hiện tại việc cấp bách, là tìm tới giết chết những người này môi giới, đến cùng là cái gì... Sau đó lại nghĩ biện pháp đem phía sau màn hắc thủ diệt trừ!”"),t("br"),t("br"),n._v("Trầm tư phút chốc, Trương Huyền phân tích: “Chỉ có hắc thủ chết rồi, mọi người mới có thể chân chính an toàn.”"),t("br"),t("br"),n._v("“Như thế!”"),t("br"),t("br"),n._v("Ngô sư gật đầu."),t("br"),t("br"),n._v("Trương sư nói không sai, đối một cái đế quốc Hoàng đế bệ hạ động thủ, rất dễ dàng lòng người bàng hoàng."),t("br"),t("br"),n._v("Gây nên náo động không nói, càng quan trọng hơn là, điều tra nhưng không tìm được chứng cứ, ảnh hưởng Danh Sư đường công tín lực."),t("br"),t("br"),n._v("Tọa trấn một phương Danh Sư đường, nghĩ đối đế quốc ra tay, nhất định phải có bằng chứng, để cho người ta không có cách nào phản bác, nếu không, sẽ để cho đế quốc khác nghĩ như thế nào?"),t("br"),t("br"),n._v("Chẳng phải người người cảm thấy bất an?"),t("br"),t("br"),n._v("Chỉ là hoài nghi, còn thiếu rất nhiều."),t("br"),t("br"),n._v("“Ta vậy thì phái người đi thăm dò...”"),t("br"),t("br"),n._v("Nghĩ thông suốt điểm ấy, Ngô sư nói."),t("br"),t("br"),n._v("“Không chỉ đi thăm dò, khả năng còn muốn chuẩn bị một số người, sau đó, cùng đi một chuyến Tề Bắc đầm lầy!”"),t("br"),t("br"),n._v("“Tề Bắc đầm lầy?” Ngô sư không hiểu."),t("br"),t("br"),n._v("“Ta hoài nghi cái kia hắc thủ, ẩn trốn ở đây...” Trương Huyền giải thích."),t("br"),t("br"),n._v("“Tốt, ta sẽ chọn lựa Danh Sư đường tinh nhuệ nhất lực lượng, còn có Chiến Sư đường cao thủ cùng một chỗ!” Ngô sư nói."),t("br"),t("br"),n._v("“Ừm, ngươi trước sớm chuẩn bị, ta cũng chuẩn bị một chút...”"),t("br"),t("br"),n._v("An bài xong, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Muốn đi Tề Bắc đầm lầy tìm kiếm Ngoan Nhân, cần làm hai chuyện, thứ nhất, đem Kim Nguyên đỉnh thực lực, lại đề thăng một chút, tranh thủ để hắn đạt tới Xuất Khiếu cảnh; Thứ hai, mau chóng để thôn phệ xong Thanh Điền Hoàng Ngoan Nhân, vượt qua lôi kiếp!"),t("br"),t("br"),n._v("Như vậy thực lực mới có thể chân chính đạt tới Xuất Khiếu cảnh, nếu không... Lấy hắn tu vi hiện tại, Ngoan Nhân lại không đủ mạnh... Tùy tiện đi qua, làm không cẩn thận lại biến thành đối phương thuốc bổ, đến lúc đó, thật sự không có biện pháp."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1211"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1213"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);