(window.webpackJsonp=window.webpackJsonp||[]).push([[254],{607:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1205-lam-kiet-đai-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1205-lam-kiet-đai-su"}},[n._v("#")]),n._v(" Chương 1205: Lâm Kiệt đại sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1205: Lâm Kiệt đại sư"),t("br"),t("br"),n._v("Xoay người nhìn lại, một cái lão giả đi tới, hoa râm chòm râu, giữa hai lông mày mang theo khí khái hào hùng, một thân tu vi, một cái khó mà xem thấu, không biết đến cùng đến cỡ nào cao xa."),t("br"),t("br"),n._v("“Lâm Kiệt đại sư...”"),t("br"),t("br"),n._v("Tần hội trưởng vội vàng ôm quyền, Sở Tường cũng một mặt cung kính."),t("br"),t("br"),n._v("Chuẩn bát tinh luyện khí sư, vô luận tu vi hay là đối luyện khí lý giải, đều không phải là bọn họ có thể so sánh."),t("br"),t("br"),n._v("“Đại sư... Vị này là Danh Sư đường thất tinh đỉnh phong danh sư, Trương Huyền Trương sư!” Sợ hai người gây ra hiểu lầm, Tần hội trưởng vội vàng giới thiệu."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền có thể xông đường thành công, đem Cẩu đường chủ đều đánh bại, xưng hô thất tinh đỉnh phong, tuyệt không là quá."),t("br"),t("br"),n._v("“Thất tinh đỉnh phong danh sư?” Lâm Kiệt đại sư sững sờ: “Trẻ tuổi như vậy, liền có loại thực lực này, khó trách có vốn liếng ngạo khí...”"),t("br"),t("br"),n._v("Trước đó còn tưởng rằng là cái gì người ngông cuồng, cố ý trước mặt nhiều người như vậy chửi bới, nếu là như vậy cấp bậc danh sư, đương nhiên sẽ không cố ý như vậy."),t("br"),t("br"),n._v("“Thực sự thật không tiện, ta Danh Sư đường có chuyện xảy ra, không thể bị dở dang, nghĩ mượn trước cái này luyện khí sảnh sử dụng, rèn luyện một cái binh khí của ta, không biết có thể?” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Người ta muốn giảng khóa, trực tiếp tới, muốn mượn dùng nơi này địa hỏa, quả thực có chút vô lễ."),t("br"),t("br"),n._v("“Rèn luyện vũ khí? Trương sư còn là một vị luyện khí sư?”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư tò mò nhìn qua."),t("br"),t("br"),n._v("“Từng hệ thống học qua một đoạn thời gian!”"),t("br"),t("br"),n._v("Trương Huyền nói: “May mắn nhận được lục tinh huy chương!”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư nhẹ gật đầu, hơi nghi hoặc một chút: “Không biết... Trương sư muốn rèn luyện cái gì vũ khí?”"),t("br"),t("br"),n._v("Đến cùng dạng gì vũ khí, nhất định muốn tại đây cái luyện khí sảnh?"),t("br"),t("br"),n._v("Thanh Nguyên đế quốc luyện khí sư công hội, cho dù không có Tiềm Xung đế quốc hùng vĩ, nắm giữ địa hỏa, có thể luyện khí tĩnh thất, không có một ngàn cũng ít nhất có sáu, bảy trăm ở giữa... Kiên trì tuyển ở đây, tất nhiên có tác dụng ý."),t("br"),t("br"),n._v("Nếu là đối phương là thất tinh đỉnh phong luyện khí sư, liền không hỏi nhiều, lục tinh... Thực sự có chút thấp, nơi này địa hỏa hung mãnh dị thường, hơi không khống chế được, không riêng gì không cách nào luyện thành vấn đề, làm không cẩn thận còn có thể để vũ khí hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("“Là nó!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Kim Nguyên đỉnh lập tức xuất hiện ở đại sảnh."),t("br"),t("br"),n._v("“Chủ nhân, đây là muốn rèn luyện ta? Ha ha, ta lại muốn tấn cấp?”"),t("br"),t("br"),n._v("Nhìn quanh một tuần, phát hiện là tại luyện khí sư công hội, đâu đâu cũng có luyện khí sư, Kim Nguyên đỉnh tràn đầy hưng phấn."),t("br"),t("br"),n._v("“Nửa bước Xuất Khiếu cảnh trung phẩm Thánh khí?”"),t("br"),t("br"),n._v("Còn tưởng rằng là cái Bán Thánh khí loại hình, không nghĩ tới đạt đến trung phẩm, lông mày nhảy một cái, Lâm Kiệt đại sư có chút không dám tin tưởng: “Trương sư... Muốn rèn luyện cái này lô đỉnh?”"),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy đối phương nghé con mới đẻ không sợ cọp bộ dáng, Lâm Kiệt đại sư nhịn không được an ủi: “Trung phẩm Thánh khí rèn luyện, không chỉ cần thực lực, còn cần đối linh tính bảo hộ, cùng chính xác nhất rèn luyện cùng đánh, không cho phép có nửa điểm qua loa... Nếu không, không những không thể để cho hắn tấn cấp, làm không cẩn thận còn có thể phá hư bên trong kết cấu, để đẳng cấp trượt xuống...”"),t("br"),t("br"),n._v("Loại cấp bậc này Thánh khí, liền xem như hắn, muốn rèn luyện, cũng nhiều nhất chỉ có hai thành nắm chắc thành công, một cái lục tinh luyện khí sư..."),t("br"),t("br"),n._v("Thực lực không bằng, cưỡng ép đem binh khí nấu lại, không những không thể để cho hắn tăng lên, làm không cẩn thận còn có thể rơi xuống cấp bậc, được không bù mất."),t("br"),t("br"),n._v("“Lão đầu, ngươi có ý tứ gì? Nhà chúng ta thiếu chủ, lần trước tùy tiện chỉ điểm cái học đồ, liền để ta từ hạ phẩm Thánh khí tấn cấp đến trung phẩm, cần dùng tới ngươi tới dạy bảo?”"),t("br"),t("br"),n._v("Trương Huyền còn chưa kịp nói chuyện, một bên Kim Nguyên đỉnh liền tràn đầy không vui khẽ nói."),t("br"),t("br"),n._v("Từ khi ra đời, nó bội phục nhất liền là trước mắt vị chủ nhân này... Đối phương lại dám hoài nghi, không có vọt thẳng đi qua, đặt mông ngồi đối phương đầu nở hoa, liền xem như rất khắc chế."),t("br"),t("br"),n._v("“Chỉ điểm học đồ?” Tất cả mọi người là sững sờ, có chút không hiểu."),t("br"),t("br"),n._v("Học đồ, thế nhưng là liền phàm khí đều rất khó luyện chế ra tới, chỉ điểm một chút, đem hạ phẩm Thánh khí, tấn cấp đến trung phẩm?"),t("br"),t("br"),n._v("“Ngươi không nói lời nào, không có người đem ngươi trở thành người câm!”"),t("br"),t("br"),n._v("Một bàn tay quất vào Kim Nguyên đỉnh nắp lò bên trên, để hắn im miệng, Trương Huyền quát tháo."),t("br"),t("br"),n._v("Lần trước, Tống Chân sở dĩ có thể thuận lợi như vậy để hắn tăng lên, là bởi vì đối phương tại luyện khí bên trên, ma luyện rất nhiều năm, thực lực không kém gì lục tinh luyện khí sư không nói, tại đủ loại phương pháp luyện khí bên trên, cũng đạt tới cực cao trình độ."),t("br"),t("br"),n._v("Lúc này mới đang cùng được chỉ điểm của mình, nhẹ nhõm hoàn thành... Đổi thành cái khác học đồ, khẳng định lại không được."),t("br"),t("br"),n._v("Cơ duyên xảo hợp mà thôi, rất khó phục chế, không có gì có thể đáng giá khoe khoang."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lô đỉnh uốn éo người, tràn đầy oan ức: “Ta nói chính là lời nói thật nha... Ngươi chính là tùy tiện chỉ điểm cái học đồ, để cho ta tấn cấp...”"),t("br"),t("br"),n._v("“Im miệng, tránh qua một bên đi...”"),t("br"),t("br"),n._v("Thấy nó còn dám phản bác, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh không còn dám nói nhảm, lùi về một bên."),t("br"),t("br"),n._v("“Cái tên này tự do tản mạn quen rồi, có chút vô lễ, để đại sư, hội trưởng chê cười...” Nhìn về phía mọi người, Trương Huyền tràn đầy thật không tiện."),t("br"),t("br"),n._v("Chủ nhân nói chuyện, một cái binh khí loạn xen vào, không biết lớn nhỏ."),t("br"),t("br"),n._v("“Không có gì!” Lâm Kiệt đại sư lắc đầu, trong mắt vẫn còn có chút khó hiểu: “Mới vừa nói, Trương sư chỉ điểm học đồ luyện chế... Không biết là chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Không đề cập tới cũng được!” Trương Huyền nói."),t("br"),t("br"),n._v("Tới đây là nghĩ tấn thăng Kim Nguyên đỉnh, còn là khiêm tốn một chút, một chút chuyện cũ mà thôi, giờ phút này lại nói đi ra, cũng không có gì ý tứ."),t("br"),t("br"),n._v("“Là ta lỗ mãng...”"),t("br"),t("br"),n._v("Đối phương không muốn nói, còn tưởng rằng liên lụy đến bí mật gì, Lâm Kiệt đại sư không hỏi thêm nữa."),t("br"),t("br"),n._v("“Sự tình không chần chờ, ta lại bắt đầu...”"),t("br"),n._v(" Thấy đem lại nói mở, không có gây nên hiểu lầm gì đó, Trương Huyền thở phào nhẹ nhõm, dừng lại một chút, nhìn về phía một bên Sở Tường: “Sở huynh, khả năng một lát nữa làm phiền ngươi một cái!”"),t("br"),t("br"),n._v("“Có chuyện gì, Trương sư phân phó chính là, chỉ cần ta có thể làm được, tuyệt không chối từ!”"),t("br"),t("br"),n._v("Sở Tường mỉm cười."),t("br"),t("br"),n._v("Không nói trước hai người nhận biết, chỉ nói trước mắt vị này xông đường thành công, trở thành Thanh Nguyên đế quốc tên thứ nhất sư, vô luận như thế nào quan hệ thân thiết, đều không đủ."),t("br"),t("br"),n._v("“Là như vậy, ta cái này vũ khí, bị ta cưng chiều hỏng, ta ra tay luyện chế lời nói, khả năng bởi vì đủ loại cố kỵ, ngược lại khó mà làm tốt... Cho nên, muốn mời ngươi giúp ta rèn luyện!” Dừng lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“Cưng chiều?” Kim Nguyên đỉnh vắt hết óc, làm sao đều không có muốn đi ra, trước mắt vị này, lúc nào cưng chiều qua nó."),t("br"),t("br"),n._v("Dường như, mới vừa rồi còn quất một cái tát..."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được là chuyện này, giật nảy mình, Sở Tường cười khổ một tiếng, vội vàng xua tay: “Trương sư, không phải ta không muốn giúp bận bịu, tu vi của ta liền Nguyên Thần cảnh cũng chưa tới, mặc dù là thất tinh luyện khí sư, nhưng... Luyện chế ra khiếu cảnh trung phẩm Thánh khí... Thực sự làm không được!”"),t("br"),t("br"),n._v("Liền Tần hội trưởng loại này thất tinh đỉnh phong cấp bậc luyện khí sư, đều không thể luyện chế cao cấp như vậy cái khác vũ khí, hắn một cái mới vừa đạt tới thất tinh luyện khí sư..."),t("br"),t("br"),n._v("Ngược lại thật sự là để mắt hắn..."),t("br"),t("br"),n._v("“Không cần khẩn trương, ta chỉ điểm ngươi luyện chế, chỉ cần nghiêm ngặt dựa theo ta nói trình tự không kém chút nào làm là được...” Trương Huyền nói."),t("br"),t("br"),n._v("Sở Tường rầu rĩ: “Trương sư lô đỉnh như thế trân quý như vậy, ta sợ xảy ra vấn đề...”"),t("br"),t("br"),n._v("“Không sao, một lát nữa, ta sẽ đối ngươi thi triển sư ngôn thiên bẩm, nghiêm ngặt dựa theo ta nói đi làm, xảy ra vấn đề, sẽ không trách ngươi...” Biết đối phương lo lắng cái gì, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Thấy đối phương nói như vậy, lại cự tuyệt rõ ràng là xem thường, rầu rĩ một hồi, Sở Tường đành phải nhẹ gật đầu."),t("br"),t("br"),n._v("“Vậy thì bắt đầu đi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền quay đầu nhìn về phía Kim Nguyên đỉnh, lông mày giương lên: “Còn không lăn đến địa hỏa bên kia đi?”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh run rụt lại, lòng tràn đầy oan ức, vội vàng bay đi."),t("br"),t("br"),n._v("“Cái này gọi cưng chiều?”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư cùng Tần hội trưởng cũng đều tức xạm mặt lại."),t("br"),t("br"),n._v("Mới vừa nói đối với mình vũ khí cưng chiều quá hung ác, luyện chế thời điểm, không xuống tay được, thật giống như bác sĩ không nguyện ý cho người thân làm giải phẫu đồng dạng... Kết quả là thấy cảnh này... Ngươi đây là cưng chiều?"),t("br"),t("br"),n._v("Đi tới đưa tới địa hỏa trận pháp trước mặt, Trương Huyền đối Sở Tường ra hiệu một cái."),t("br"),t("br"),n._v("Người sau rõ ràng ý tứ, hai bước đi tới trước mặt."),t("br"),t("br"),n._v("“Trương sư, đây là dẫn động địa hỏa phù văn lệnh bài...”"),t("br"),t("br"),n._v("Thấy muốn tiến hành luyện khí, Tần hội trưởng cổ tay khẽ đảo, lấy ra một cái lệnh bài."),t("br"),t("br"),n._v("Địa hỏa bị trận pháp từ dưới đất rút ra đi ra, cần đặc thù lệnh bài mới có thể kích hoạt, nếu không thì nhìn không ra hỏa diễm."),t("br"),t("br"),n._v("Đang định đưa tới, liền nghe đến cách đó không xa thanh niên, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Phía bên trái đi ba bước, đi về trước hai bước, lui về phía sau một bước, bên phải dời cũng một bước, vận chuyển chân khí trong cơ thể, tập trung ở chân phải, lún xuống dưới...”"),t("br"),t("br"),n._v("Tiếng nước âm bên trong mang theo đặc thù ý vị, Sở Tường không chần chờ chút nào, nghiêm ngặt dựa theo lời của hắn hành động, bàn chân trên mặt đất đạp mạnh."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Thu hút địa hỏa trận pháp lập tức vận chuyển lại, một đoàn nồng đậm hỏa diễm, phóng lên tận trời, đem Kim Nguyên đỉnh bao phủ ở bên trong."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tần hội trưởng con ngươi co rụt lại, trong mắt tràn đầy khó tin: “Không cần phù văn lệnh bài, nhen lửa địa hỏa... Làm sao làm được?”"),t("br"),t("br"),n._v("Địa hỏa nhiệt độ cực cao, liền xem như Thánh vực tam, tứ trọng cường giả, rơi vào trong đó, đều rất dễ dàng bị đốt thành tro bụi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, bình thường trận pháp không vận chuyển thời điểm, là không có hỏa diễm... Dựa theo tình huống bình thường, không có phù văn lệnh bài, muốn đem lửa dẫn ra, gần như không có khả năng, nhưng trước mắt vị này tại Trương sư chỉ điểm, lại nhẹ nhõm hoàn thành..."),t("br"),t("br"),n._v("Cho dù tận mắt nhìn thấy, đều khó mà tin tưởng."),t("br"),t("br"),n._v("“Là đối với trận pháp nắm giữ đến cực hạn, mới có thể làm đến! Vị này Trương sư, có lẽ còn có trận pháp sư phụ tu, hơn nữa chỉ sợ cũng đạt đến không thấp trình độ!”"),t("br"),t("br"),n._v("Gặp hắn bộ biểu tình này, một bên Lâm Kiệt đại sư gật đầu."),t("br"),t("br"),n._v("Chỉ nhìn một cái, liền biết rõ địa hỏa trận pháp kết cấu, hơn nữa nhẹ nhõm đem hỏa diễm dẫn ra, đối với trận pháp lý giải, cho dù tại bọn họ Tiềm Xung đế quốc, đều tuyệt đối tính được là một phương nhân vật."),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("Tần hội trưởng cũng tỉnh táo lại."),t("br"),t("br"),n._v("“Xem hắn luyện khí trình độ đi... Mặc dù danh sư có sư ngôn thiên bẩm, nhưng chỉ điểm người khác luyện khí... Hơn nữa để một cái chưa hề phối hợp qua người, rèn luyện trung phẩm Thánh khí, độ khó rất lớn!”"),t("br"),t("br"),n._v("Lắc đầu, Lâm Kiệt đại sư đối trước mắt cục diện, cũng không xem trọng."),t("br"),t("br"),n._v("Hai bên phối hợp qua vô số lần, rất có ăn ý ngược lại cũng thôi."),t("br"),t("br"),n._v("Trên thực tế cũng có như vậy tiến hành luyện khí."),t("br"),t("br"),n._v("Nhưng đối với đối phương, thực lực, chân khí trình độ, phương pháp luyện khí... Tất cả mọi chuyện đều không biết, liền chỉ điểm đối phương trực tiếp bắt đầu rèn luyện vượt qua thực lực bản thân trung phẩm Thánh khí..."),t("br"),t("br"),n._v("Dùng ánh mắt của hắn đến xem, có thể nói là nhiệm vụ không thể hoàn thành."),t("br"),t("br"),n._v("Vị này Trương sư, trực tiếp làm như vậy... Là tâm lớn, vẫn cảm thấy cái này lô đỉnh có thể báo hỏng ném đi?"),t("br"),t("br"),n._v("Lần nữa nhìn về phía Kim Nguyên đỉnh, trong mắt tràn đầy đồng tình."),t("br"),t("br"),n._v("Đây không phải cưng chiều..."),t("br"),t("br"),n._v("Làm sao cảm giác... Giống như là muốn giết chết nó đâu?"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1213"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1215"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);