(window.webpackJsonp=window.webpackJsonp||[]).push([[255],{609:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1206-so-tuong-luyen-khi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1206-so-tuong-luyen-khi"}},[n._v("#")]),n._v(" Chương 1206: Sở Tường luyện khí")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1206: Sở Tường luyện khí"),t("br"),t("br"),n._v("“Tốt rồi, chuẩn bị kỹ càng công cụ, bắt đầu đi!”"),t("br"),t("br"),n._v("Nhen lửa địa hỏa, thấy Sở Tường giật nảy mình, có chút ngẩn người, Trương Huyền nhíu nhíu mày, nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Có thể trẻ tuổi như vậy liền đạt tới Thánh vực tam trọng, càng là thất tinh luyện khí sư, mặt ngoài hoàn khố, trên thực tế không chút nào không đơn giản."),t("br"),t("br"),n._v("Hít sâu một hơi, rất nhanh tĩnh hạ tâm thần."),t("br"),t("br"),n._v("“Tiểu tử, ngươi yên tâm, không phải ca cùng ngươi thổi, ngươi nghiêm ngặt dựa theo chủ nhân lời nói đi làm, đỉnh gia ta một chút việc đều không có, còn có thể cấp bậc đại tăng...”"),t("br"),t("br"),n._v("Thấy Sở Tường bó tay bó chân, Kim Nguyên đỉnh cười to một tiếng."),t("br"),t("br"),n._v("Nghe nói như thế, Lâm Kiệt đại sư cùng Tần hội trưởng liếc mắt nhìn nhau, riêng phần mình tràn đầy lo lắng."),t("br"),t("br"),n._v("Cái này lô đỉnh đến cùng là tâm lớn, còn là đầu óc có vấn đề... Như vậy luyện chế, rớt cấp hay là việc nhỏ, trọng yếu nhất, ý niệm của ngươi cũng có khả năng sẽ bị tươi sống giết chết..."),t("br"),t("br"),n._v("Thật sự không có chút nào lo lắng?"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Sở Tường lên tiếng, quay đầu nhìn qua: “Trương sư, không biết muốn dung hợp khoáng thạch là cái gì...”"),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Ngón tay búng một cái, một cái tảng đá liền bay đi."),t("br"),t("br"),n._v("“Cái này...” Con ngươi co rụt lại, Sở Tường lần nữa quơ quơ: “Kim Tinh huyền thạch?”"),t("br"),t("br"),n._v("Kim Tinh huyền thạch, vô cùng trân quý, dung hợp một điểm, liền có thể để vũ khí uy lực đại tăng... Cho dù tại phong hào đế quốc, đều thuộc về rất hiếm thấy kỳ lạ khoáng thạch, nằm mơ đều không có nghĩ đến, đối phương tiện tay đem ra."),t("br"),t("br"),n._v("“Thứ này rất khó hòa tan đi...”"),t("br"),t("br"),n._v("Tần hội trưởng nhíu nhíu mày."),t("br"),t("br"),n._v("Kim Tinh huyền thạch cho dù rất thưa thớt, nhưng thân là luyện khí sư công hội hội trưởng, còn là có phương pháp có thể lấy được một chút, nhưng... Cái này đông Tây Cực khó hòa tan, liền xem như cái đại sảnh này địa hỏa, muốn tan ra, cũng đều cần tiêu tốn cực lớn công phu."),t("br"),t("br"),n._v("Tan không ra, làm sao tan đến trong binh khí, tăng lên phẩm chất?"),t("br"),t("br"),n._v("“Tiềm Xung đế quốc địa hỏa, muốn hòa tan, không khó lắm, cái này...”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư lắc đầu."),t("br"),t("br"),n._v("Cái này luyện khí sảnh địa hỏa mặc dù là nhiệt độ cao nhất, nhưng cùng Tiềm Xung đế quốc so, còn là kém một chút, muốn luyện hóa Kim Tinh huyền thạch, chỉ sợ không dễ dàng như vậy."),t("br"),t("br"),n._v("Đang tại tò mò, đối phương làm sao đem hắn hòa tan, liền nghe thanh niên phân phó một tiếng, ngay sau đó chỉ thấy Kim Nguyên đỉnh nắp lò mở ra, trực tiếp đem khoáng thạch nuốt xuống."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mà người nháy con mắt, Lâm Kiệt đại sư vỗ đùi, trong mắt tỏa ánh sáng: “Ta làm sao không nghĩ tới, muốn luyện chế cái này lô đỉnh, là Khí đỉnh, địa hỏa nhiệt độ mặc dù có hạn, nhưng mượn nhờ nó trung phẩm Thánh khí lực lượng, sẽ trở nên càng thêm nóng bỏng... Một mình hòa tan lời nói rất khó, đặt ở trong đó, cũng rất dễ dàng, chỉ là...”"),t("br"),t("br"),n._v("“Chỉ là cái gì?”"),t("br"),t("br"),n._v("“Chỉ là, như vậy là có thể hòa tan Kim Tinh huyền thạch, cũng có thiếu hụt, cái kia chính là thần thức không cách nào tiến vào bên trong, dò xét không ra đến ngọn nguồn lúc nào hoàn toàn hòa tan, thời gian sớm, thời gian muộn, đối luyện chế vũ khí, đều sẽ có rất lớn ảnh hưởng...”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư nói."),t("br"),t("br"),n._v("Địa hỏa thiêu đốt lại thêm lô đỉnh gia trì, lô đỉnh bên trong nhiệt độ, liền Kim Tinh huyền thạch đều có thể hòa tan, thần thức tiến vào bên trong lời nói, tất nhiên phải bị tổn thương! Vào không được, liền không có cách nào xác định trạng thái."),t("br"),t("br"),n._v("Đối với Kim Tinh huyền thạch loại này phẩm chất cao khoáng thạch, rèn luyện vũ khí, dùng hot thời gian dài ngắn, có rất lớn ảnh hưởng, có đôi khi, kém hơn một hai cái hô hấp, liền có khả năng dẫn đến thất bại."),t("br"),t("br"),n._v("“Không sai... Không chỉ có như vậy, cái này lô đỉnh một mực tại địa hỏa bên trong thiêu đốt, một khi thời gian phạm sai lầm, khoáng thạch bị lãng phí không nói, làm không cẩn thận cũng sẽ mất đi trước đó tốt nhất kết cấu, từ đó cấp bậc giảm lớn...”"),t("br"),t("br"),n._v("Tần hội trưởng cũng hiểu được."),t("br"),t("br"),n._v("Cái này lô đỉnh, bản thân liền là rất nhiều bên trong khoáng thạch, kim loại dung hợp mà thành, đã đạt đến một loại đặc thù cân bằng, này mới khiến hắn củng cố tại trung phẩm Thánh khí cấp bậc."),t("br"),t("br"),n._v("Giờ phút này, tại địa hỏa bên trong không ngừng thiêu đốt, sự cân bằng này rất dễ dàng đánh vỡ, lại thêm kim tinh quáng đá không biết lúc nào có thể hòa tan... Như vậy luyện chế, cho dù giải quyết địa hỏa nhiệt độ chưa đủ vấn đề, độ khó lại lần nữa tăng lên gấp bội không thôi."),t("br"),t("br"),n._v("Hai người đang tại lo lắng, liền nghe đến phía trước thanh niên thanh âm nhàn nhạt tiếp tục vang lên."),t("br"),t("br"),n._v("“Hướng về phía trước hai bước, chân khí hướng đông phương bắc hướng truyền vào, bấm tay dùng Tam Diệp luyện khí pháp, lấy mở nắp lò!”"),t("br"),t("br"),n._v("Thanh âm mang theo sư ngôn thiên bẩm đặc thù vận luật không ổn định, không chần chờ cùng do dự, như là quân nhân mệnh lệnh, đơn giản mạnh mẽ."),t("br"),t("br"),n._v("Nghe được lời nói, Sở Tường tuân thủ một cách nghiêm chỉnh, di động hai bước, ngón tay đột nhiên một điểm."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nắp lò trong nháy mắt mở ra, một đoàn chất lỏng màu vàng óng lập tức bay ra, lơ lửng giữa không trung."),t("br"),t("br"),n._v("“Hòa tan? Mà lại là vừa mới hòa tan, sai số không cao hơn một phần mười cái hô hấp, cái này...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Tần hội trưởng tràn đầy khó tin."),t("br"),t("br"),n._v("Mới vừa nói hết, thời gian không nắm chặt được, đối phương liền xác định được, hơn nữa không sai chút nào... Làm sao làm được?"),t("br"),t("br"),n._v("“Là kết hợp địa hỏa trận pháp, Sở Tường tiến lên, di động trận pháp kết cấu, chân khí rót vào phía đông bắc, để hỏa diễm nhiệt độ, trong nháy mắt lên cao, ngay sau đó sử dụng Tam Diệp luyện khí pháp... Bộ này thủ pháp, ngươi hẳn phải biết, một chỉ nhanh hơn một chỉ, lực lượng hung mãnh không nói, trọng yếu là, có thể đem hỏa diễm áp súc tại một điểm, nhiệt độ càng thêm tập trung kịch liệt... Kết hợp với vừa rồi trận pháp thay đổi, lúc này mới trong nháy mắt, hòa tan Kim Tinh huyền thạch... Cái này phương pháp luyện khí, phần này tính toán... Quá chính xác, thật là đáng sợ!”"),t("br"),n._v(" Đi đầu hiểu được, Lâm Kiệt đại sư nắm đấm xiết chặt."),t("br"),t("br"),n._v("Vừa rồi mặc dù chỉ là ngắn ngủi một câu, nhưng trong đó dính dáng đối với trận pháp nắm giữ, địa hỏa nhiệt độ, khoáng thạch điểm nóng chảy, chân khí thuộc tính, thậm chí phương pháp luyện khí rất nhiều phương diện! Chỉ có đối với mấy cái này, đều có một cái rõ ràng tính toán, mới có thể làm đến bây giờ như vậy chút xíu không kém."),t("br"),t("br"),n._v("Đừng nói lục tinh luyện khí sư, liền xem như hắn, vị này chuẩn bát tinh, muốn hoàn thành, đều làm không được."),t("br"),t("br"),n._v("Trong chớp nhoáng này lượng tính toán... Thực sự quá kinh khủng!"),t("br"),t("br"),n._v("“Bên trái dời ba tấc, trong tay luyện khí chùy giơ lên, hướng về phía lô đỉnh miệng đập, dùng lá liễu rơi xuống thủ pháp, rèn đúc 148 xuống, ba cái hô hấp một chùy, không thể nhiều, không thể thiếu, đều đặn nhanh tiến hành, sau đó...”"),t("br"),t("br"),n._v("Thanh niên lời nói tiếp tục vang lên."),t("br"),t("br"),n._v("Ngay sau đó, liền nghe đến đinh đinh đương đương tiếng gõ."),t("br"),t("br"),n._v("“Diệu... Thật là khéo!”"),t("br"),t("br"),n._v("Nhìn một hồi, Lâm Kiệt đại sư đột nhiên kinh hô."),t("br"),t("br"),n._v("“Đại sư...” Tần hội trưởng có chút không hiểu."),t("br"),t("br"),n._v("“Thực lực ngươi không đến, không nhìn ra vấn đề ở đâu... Lá liễu rơi xuống thủ pháp, là bình thường nhất phương pháp luyện khí, nghe không tính là gì, như là lá liễu rơi xuống đất, chậm chạp mà đều đặn... Nhưng mà, dùng tại nơi này, cũng quá xảo giây. Cái này lô đỉnh, vừa mới bị địa hỏa thiêu đốt, cũng có hòa tan xu thế, lúc này dùng qua nhanh, quá mạnh thủ pháp, đều sẽ để trong đó bộ sinh ra vết rách. Loại này vết rách không lớn, lại để hắn phẩm chất hạ thấp, nhưng dùng loại thủ pháp này, liền sẽ không xuất hiện loại vấn đề này, thậm chí còn có thể để cho phẩm chất càng thêm kỹ càng, thực lực lần nữa tăng thêm...”"),t("br"),t("br"),n._v("Lâm Kiệt tròng mắt đỏ hoe."),t("br"),t("br"),n._v("Trước mắt vị thanh niên này chỉ điểm, hóa mục nát thành thần kỳ, dùng chính là đơn giản nhất rèn luyện thủ pháp, lại làm cho toàn bộ lô đỉnh tốt hơn cùng Kim Tinh huyền thạch chất lỏng dung hợp."),t("br"),t("br"),n._v("Đinh đinh đang đang!"),t("br"),t("br"),n._v("Sở Tường đánh 148 sau đó, vừa vặn đem Kim Nguyên đỉnh lô miệng vị trí, đánh hoàn thành, ngay sau đó tại Trương Huyền phân phó xuống, chuỳ sắt trên không trung xoay một cái, hướng về phía lô đỉnh chân vạc, đánh đi qua."),t("br"),t("br"),n._v("“Vì cái gì không trực tiếp đánh thân đỉnh? Đánh chân vạc làm gì?”"),t("br"),t("br"),n._v("Tần hội trưởng vẫn như cũ không hiểu."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, rèn luyện một kiện binh khí, theo trình tự gõ, mới có thể càng thêm củng cố, mới vừa gõ xong miệng đỉnh, trực tiếp chân vạc... Khoảng cách có chút quá lớn đi!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư cũng nhíu nhíu mày, không nghĩ rõ ràng."),t("br"),t("br"),n._v("Ong ong ong!"),t("br"),t("br"),n._v("Đang tại khó hiểu, liền nghe đến trước mắt lô đỉnh, đột nhiên phát ra trầm muộn sóng âm tiếng, tựa như sóng lớn."),t("br"),t("br"),n._v("“Đây là... Ta hiểu được!”"),t("br"),t("br"),n._v("Nghe được cái này, Lâm Kiệt đại sư bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Tần hội trưởng vội vàng nhìn tới."),t("br"),t("br"),n._v("“Đây là thuỷ triều luyện khí pháp... Nghe nói là một vị bát tinh luyện khí sư, nghe được thuỷ triều thanh âm, sáng lập ra phương pháp luyện khí, đánh xong miệng đỉnh, sau đó là chân vạc, cả hai cách nhau xa nhất, va chạm thời điểm, liền sẽ sinh ra một loại đặc thù cộng minh, như là thuỷ triều... Cứ như vậy, thân đỉnh bên trên Kim Tinh huyền thạch, cho dù không cần rèn luyện, cũng có thể hoàn toàn dung hợp, thậm chí không phá hư trước đó kết cấu, càng hơn một bậc, mấu chốt nhất là, đi qua loại này thuỷ triều giống như chấn động, trong đó linh tính, cũng sẽ nhận được tinh luyện, trở nên càng thêm cường đại... Quá lợi hại, quá lợi hại! Hắn nghĩ như thế nào đi ra?”"),t("br"),t("br"),n._v("Càng nói càng cảm thấy kinh ngạc, Lâm Kiệt đại sư, cảm giác chính mình cũng có chút phản ứng không kịp."),t("br"),t("br"),n._v("Thuỷ triều luyện khí pháp, mười phần ít thấy, liền xem như hắn, đều không có dùng như thế nào qua, nằm mơ đều không có nghĩ đến, thế mà bị người thi triển tại nơi này..."),t("br"),t("br"),n._v("Phương pháp luyện khí rất nhiều, nhưng dùng phù hợp, chẳng những có thể tốt hơn để đủ loại kim loại dung hợp, thậm chí đối trong đó linh tính, cũng có rất lớn ích lợi."),t("br"),t("br"),n._v("Rất nhiều người mượn nhờ tốt khoáng thạch tăng lên vũ khí chất lượng, vũ khí là càng kiên cố hơn, tính bền dẻo càng đầy, nhưng trong đó linh tính, nhưng không có quá tăng nhiều thêm... Thật giống như tiểu Ngưu kéo dài xe, đối với binh khí thực lực tổng hợp tới nói, chưa chắc là chuyện tốt."),t("br"),t("br"),n._v("Trước mắt vị này, tại thời khắc mấu chốt sử dụng thuỷ triều luyện khí pháp, vừa vặn giải quyết vấn đề này."),t("br"),t("br"),n._v("Không chỉ vũ khí tiến bộ lớn, linh tính cũng đã nhận được tăng thêm... Nhất cử lưỡng tiện!"),t("br"),t("br"),n._v("“Chỉ điểm người khác luyện khí, còn có thể cân nhắc đến điểm ấy... Đối luyện khí muốn nắm giữ đến cái gì trình độ mới có thể làm đến?”"),t("br"),t("br"),n._v("Trước đó, hắn còn cảm thấy đối phương, chỉ điểm người khác ra tay luyện khí, muốn thành công rất khó, hiện tại xem ra... Tám chín phần mười có thể thành công!"),t("br"),t("br"),n._v("Đối phương tại luyện khí lý giải, cao hơn hắn sâu thực sự quá nhiều!"),t("br"),t("br"),n._v("Căn bản không tại một cái cấp bậc."),t("br"),t("br"),n._v("Lại nhìn một hồi, càng xem càng kinh ngạc."),t("br"),t("br"),n._v("Vị này Trương sư chỉ điểm, mỗi một bước, đều chính xác cùng kín đáo Thiên Công đồ vật đồng dạng, không có chút nào chỗ sơ suất cùng sai lầm, cho dù xuất thủ không phải mình, vẫn như cũ đem thực lực của đối phương, chân khí thuộc tính, đều tính toán ở bên trong."),t("br"),t("br"),n._v("Vừa mới bắt đầu, đối phương rèn luyện phương pháp, còn có thể xem hiểu, nương theo thời gian trôi qua, lại có thật nhiều, hắn đều không thể hiểu."),t("br"),t("br"),n._v("Cẩn thận suy tư, có thể hiểu rõ, nhưng... Lúc này, đối phương lại thi triển không biết bao nhiêu thủ pháp, quả thực để cho người ta hoa mắt, không kịp nhìn."),t("br"),t("br"),n._v("Không biết qua bao lâu, một tiếng kịch liệt nổ vang, Sở Tường tại Trương sư phân phó xuống, lui về phía sau mấy bước."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngay sau đó, luyện khí sảnh tất cả mọi người, liền cảm thấy một đạo huy hoàng khí tức bay thẳng mà lên, bầu trời bỗng nhiên âm trầm xuống."),t("br"),t("br"),n._v("“Là xuất khiếu kiếp...”"),t("br"),t("br"),n._v("Thân thể căng thẳng, tất cả mọi người biến sắc."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1214"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1216"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);