(window.webpackJsonp=window.webpackJsonp||[]).push([[256],{610:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1207-xuat-khieu-kiep"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1207-xuat-khieu-kiep"}},[n._v("#")]),n._v(" Chương 1207: Xuất Khiếu kiếp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1207: Xuất Khiếu kiếp"),t("br"),t("br"),n._v("Thánh vực ngũ trọng Xuất Khiếu cảnh, nguyên thần ly thể, muốn trở nên càng thêm cường đại, nhất định phải kinh nghiệm Xuất Khiếu kiếp."),t("br"),t("br"),n._v("Vũ khí cho dù đạt tới Xuất Khiếu cảnh, linh tính nhưng cũng ngưng tụ không được nguyên thần, thoát ly ràng buộc, có điều, lại có thể để cho biến hóa ra hai chân, giống như người đi lại, so trước kia linh hoạt rất nhiều."),t("br"),t("br"),n._v("Trước mắt cái này lô đỉnh, vốn là đạt đến nửa bước Xuất Khiếu thực lực, giờ phút này dung hợp Kim Tinh huyền thạch, quả nhiên một lần đột phá ràng buộc, đưa tới Xuất Khiếu kiếp."),t("br"),t("br"),n._v("Mây đen càng lúc càng lớn, đem nóc phòng vén lên, bụi bặm tự nhiên, lộ ra bầu trời bên ngoài."),t("br"),t("br"),n._v("Bên trong căn phòng rất nhiều luyện khí sư, lần thứ nhất nhìn thấy luyện chế vũ khí đạt tới xuất khiếu, tất cả đều kích động run rẩy, không có một cái rời đi."),t("br"),t("br"),n._v("“Răng rắc!”"),t("br"),t("br"),n._v("Lôi đình nổ vang mà tới, vô số tia chớp màu xanh lam vướng víu, tựa hồ muốn không khí xé rách."),t("br"),t("br"),n._v("“Cái này lô đỉnh, làm sao không có rèn luyện, liền đưa tới... Xuất Khiếu kiếp?”"),t("br"),t("br"),n._v("Tần hội trưởng tràn đầy khó tin."),t("br"),t("br"),n._v("Luyện chế vũ khí, có ba bước, thứ nhất, hòa tan kim loại, khoáng thạch; Thứ hai, rèn luyện khí thai; Thứ ba, tôi thép!"),t("br"),t("br"),n._v("Đối với lợi hại luyện khí sư, trước hai cái đều không khó, khó khăn nhất nắm giữ liền là cái thứ ba."),t("br"),t("br"),n._v("Rất nhiều vũ khí, chất liệu có hay không có thể hoàn mỹ dung hợp, có hay không có thể để cho phẩm chất tiến thêm một bước, liền nhìn luyện khí sư, đối tôi thép nắm chắc."),t("br"),t("br"),n._v("Trước mặt rất nhiều thủ pháp, hắn rất nhiều đều xem không hiểu, vốn cho rằng, sau cùng tôi thép, có thể để cho mở rộng tầm mắt, chân chính kiến thức đến, lợi hại luyện khí sư trình độ, nằm mơ đều không có nghĩ đến... Còn chưa kịp rèn luyện, liền đưa tới Xuất Khiếu kiếp!"),t("br"),t("br"),n._v("“Không phải không rèn luyện, mà là... Đang định rèn luyện!”"),t("br"),t("br"),n._v("Lâm Kiệt đại sư yết hầu phát khô."),t("br"),t("br"),n._v("http://ngantruyen.com/"),t("br"),n._v("“Đang định? Đại sư ý là... Dùng lôi đình rèn luyện?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Tần hội trưởng ý thức được cái gì, thân thể cứng đờ."),t("br"),t("br"),n._v("“Không sai, hắn định dùng lôi đình, tiến hành tôi thép...” Lâm Kiệt đại sư gật đầu."),t("br"),t("br"),n._v("“Xuất Khiếu kiếp, hung mãnh cuồng bạo, chân chính Xuất Khiếu cảnh cường giả đều rất khó chống lại, để một cái vừa mới tấn cấp vũ khí, mượn nhờ thứ này...”"),t("br"),t("br"),n._v("Tần hội trưởng cảm thấy không phải mình điên rồi, mà là cái kia Trương sư điên rồi."),t("br"),t("br"),n._v("Xuất Khiếu kiếp, là lôi đình, hung mãnh vô cùng, rất nhiều Xuất Khiếu cảnh cường giả, đều e ngại tam phân, không dám đón đỡ! Danh Sư đường Cẩu đường chủ, đã sớm đột phá cảnh giới, lại ngay cả một đạo lôi kiếp cũng không dám tiếp nhận, cũng là bởi vì tuổi già sức yếu, cảm thấy không chịu nổi, hơi không cẩn thận, liền sẽ hồn phi phách tán."),t("br"),t("br"),n._v("Lớn như thế hung đồ vật, mọi người tránh chi e sợ cho không bằng, cái tên này... Vậy mà dùng để tôi thép, nói cách khác, đem lực lượng sấm sét hấp thu vào vũ khí..."),t("br"),t("br"),n._v("Lá gan quá lớn đi!"),t("br"),t("br"),n._v("Hơi không cẩn thận, trước đó tất cả cố gắng hủy hoại chỉ trong chốc lát, ngược lại không có gì... Mấu chốt nhất là, vô cùng có khả năng không chịu nổi, tại chỗ nổ tung."),t("br"),t("br"),n._v("Xuất Khiếu cảnh vũ khí đột nhiên nổ tung, tương đương với loại cường giả cấp bậc này tự bạo... Làm không cẩn thận bọn họ toàn bộ phân bộ đều sẽ bị trực tiếp xóa đi."),t("br"),t("br"),n._v("Hoàn toàn từ danh sư đại lục xoá tên."),t("br"),t("br"),n._v("“Mọi người nhanh lui về phía sau, khai thông tất cả mọi người rời khỏi...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, vội vàng lớn tiếng phân phó."),t("br"),t("br"),n._v("Toàn bộ Thanh Nguyên thành thất tinh luyện khí sư, đều hội tụ ở đây, định nghe Lâm Kiệt đại sư giảng bài, thật muốn đều bị nổ chết, thật sự hoàn toàn kết thúc..."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("“Trốn...”"),t("br"),t("br"),n._v("“Nhanh đi Danh Sư đường mời người tới... Không khống chế được, rất có thể sinh linh đồ thán!”"),t("br"),t("br"),n._v("Rất nhiều luyện khí sư cũng ý thức được nguy hiểm, từng cái vẻ mặt trắng bệch, tất cả đều vội vã hướng ra phía ngoài thoát ra."),t("br"),t("br"),n._v("Rất nhanh trong phòng, cũng chỉ còn lại có Trương Huyền, Lâm Kiệt đại sư, Tần hội trưởng cùng Sở Tường bốn người."),t("br"),t("br"),n._v("Thời khắc này Sở Tường, cũng dọa đến vẻ mặt trở nên trắng, không ngừng run rẩy, vội vàng nhìn về phía cách đó không xa chỉ điểm hắn Trương sư, lại thấy đối phương hai tay chắp sau lưng, nhìn lên bầu trời lôi đình, chẳng những không có một điểm e ngại, ngược lại mang theo nồng đậm hưng phấn."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, hô lên."),t("br"),t("br"),n._v("“Không cần lo lắng, Xuất Khiếu kiếp mà thôi, Kim Nguyên đỉnh có thể vượt qua!”"),t("br"),t("br"),n._v("Biết đối phương lo lắng cái gì, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Bình thường rèn luyện, cho dù cũng có thể để phát huy Kim Tinh huyền thạch lực lượng, nhưng uy lực khẳng định kém xa lôi đình rèn luyện, làm như vậy, không chỉ có thể để tất cả tài liệu dung hợp hướng tới hoàn mỹ, quan trọng hơn còn có thể làm cho cả lô đỉnh mang theo lực lượng sấm sét, cùng cái khác Xuất Khiếu cảnh cường giả giao chiến, chiếm hữu ưu thế cực lớn!"),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Mọi người khiếp sợ quá trình bên trong, đạo thứ nhất lôi điện bổ xuống."),t("br"),t("br"),n._v("Rơi vào Kim Nguyên đỉnh trên thân, bị nhẹ nhõm hấp thu, không có gây nên bất kỳ gợn sóng nào, ngược lại để hắn ánh sáng lộng lẫy trở nên càng thêm loá mắt, dường như phẩm chất càng kiên cố hơn, dày đặc."),t("br"),t("br"),n._v("“Cái này...” Lâm Kiệt đại sư có chút không dám tin tưởng: “Cái này miệng lô đỉnh bản thân liền là trung phẩm Thánh khí, giờ phút này lại dung hợp Kim Tinh huyền thạch, có thể chống lại đạo thứ nhất lôi kiếp, không tính là gì, trong đó linh tính, làm sao cũng một chút việc đều không có?”"),t("br"),t("br"),n._v("Linh tính cùng Vu hồn đồng dạng, thuần âm, nhất e ngại liền là lôi đình!"),t("br"),t("br"),n._v("Đem lôi điện hút vào binh khí nội bộ tiến hành rèn luyện, linh tính tự nhiên sẽ bị công kích... Làm không cẩn thận lại biết bay xám chôn vùi!"),t("br"),t("br"),n._v("Có thể cái này lô đỉnh linh tính, thế mà một chút việc đều không có, ngược lại khống chế đỉnh lô, trên không trung loạn vặn, một bộ tiểu nhân đắc chí bộ dáng..."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Kim Nguyên đỉnh linh tính, trải qua một lần lôi đình xung kích, linh tính đã có lột xác, Xuất Khiếu kiếp lôi đình cho dù không yếu, nhưng cũng rất khó làm bị thương đối phương!”"),t("br"),t("br"),n._v("Thấy vị đại sư này, sắp làm chuyện điên rồ, Trương Huyền lắc đầu, giải thích một câu."),t("br"),t("br"),n._v("Tại lôi đình chi hải, Kim Nguyên đỉnh vì cứu người, linh tính bị lôi điện đánh cho ngủ say, khi đó kinh nghiệm, so giờ phút này, đều cường đại hơn!"),t("br"),t("br"),n._v("Cấp tám trận pháp, cho dù không có người chủ trì, uy lực cũng không phải Xuất Khiếu kiếp có thể so sánh được."),t("br"),n._v("Nguyên nhân chính là có lần này kinh nghiệm, Trương Huyền mới dám dùng lôi điện tôi thép."),t("br"),t("br"),n._v("“Trải qua lôi đình?” Giờ mới hiểu được tới, Lâm Kiệt đại sư thở phào nhẹ nhõm, có điều, lập tức lộ ra vẻ lo lắng: “Xuất Khiếu cảnh đồng dạng chia làm sơ kỳ, trung kỳ, hậu kỳ, đỉnh phong bốn cái cấp bậc, đối ứng bốn đạo lôi kiếp, không biết... Cái này Kim Nguyên đỉnh, có thể đạt tới cảnh giới gì?”"),t("br"),t("br"),n._v("Bình thường Xuất Khiếu cảnh cường giả, đạt tới sơ kỳ về sau, kinh nghiệm sơ kiếp, chỉ có một tia chớp, mặc dù khó có thể chịu đựng, nhưng chỉ cần có thể chống đỡ được, nguyên thần củng cố thương thế về sau, sẽ trở nên càng thêm cường đại, sau này bên trong kiếp, lại trải qua, liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("Sơ kỳ đến trung kỳ, rất nhiều người đều muốn tu luyện mấy chục năm, trên trăm năm, bởi vậy, có đầy đủ thời gian, để nguyên thần củng cố bị lôi đình mang tới vết thương."),t("br"),t("br"),n._v("Vũ khí khác biệt."),t("br"),t("br"),n._v("Một cái tấn cấp quá nhiều, trực tiếp đạt đến xuất khiếu đỉnh phong lời nói, liền sẽ dẫn tới bốn đạo lôi kiếp, sơ, trung, về sau, đỉnh phong... Bốn đạo lôi kiếp, cùng một chỗ tới, linh tính không chiếm được khôi phục, vô cùng có khả năng không chịu nổi, sụp đổ."),t("br"),t("br"),n._v("“Sở Tường phương pháp luyện khí còn là yếu đi chút, cho dù phối hợp không sai, vẫn như cũ không có đạt tới Xuất Khiếu cảnh đỉnh phong, hẳn là hậu kỳ trái phải!”"),t("br"),t("br"),n._v("Đã tính toán một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Kim Tinh huyền thạch mặc dù thưa thớt, hơn nữa đối vũ khí có rất lớn bổ ích, nhưng Kim Nguyên đỉnh trước kia chất liệu quá yếu, lại thêm Sở Tường phương pháp luyện khí hơi yếu... Cho dù tại hắn chỉ điểm xuống không có xuất hiện bất kỳ sai lầm, đạt tới Xuất Khiếu cảnh hậu kỳ đã là cực hạn."),t("br"),t("br"),n._v("Bất quá, chớ xem thường cảnh giới này."),t("br"),t("br"),n._v("Tu vi đạt tới Thánh vực ngũ trọng, mỗi một cái tiểu cấp bậc, đều so với lên trời còn khó hơn, vây khốn không biết bao nhiêu người."),t("br"),t("br"),n._v("Xuất Khiếu cảnh hậu kỳ thực lực, cộng thêm đi qua lôi điện rèn luyện, bình thường Xuất Khiếu cảnh đỉnh phong, cũng chưa chắc có thể là đối thủ, loại thực lực này, đừng nói Thanh Nguyên phong hào đế quốc, e là cho dù tại Tiềm Xung phong hào đế quốc, đều được cho đứng đầu nhất."),t("br"),t("br"),n._v("“Xuất Khiếu cảnh hậu kỳ? Cần trải qua ba đạo lôi kiếp... Chỉ sợ rất khó!”"),t("br"),t("br"),n._v("Khóe miệng co quắp một cái, Lâm Kiệt đại sư khắp khuôn mặt đúng vậy lo lắng."),t("br"),t("br"),n._v("Một cái đạt tới hậu kỳ, cần trải qua, sơ kiếp, bên trong kiếp cùng sau kiếp, ba đạo lôi điện, một đạo so một đạo đáng sợ, muốn thông qua... Khó như lên trời."),t("br"),t("br"),n._v("“Ta biết!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Bất quá, ta tin tưởng Kim Nguyên đỉnh, có thể thành công!”"),t("br"),t("br"),n._v("Một cái vượt qua ba đạo lôi kiếp, rất khó, bất quá... Chỉ có kháng qua, mới coi như chân chính rèn luyện thành công, thực lực tăng nhiều."),t("br"),t("br"),n._v("Không trải qua mưa gió làm sao có thể thấy cầu vồng?"),t("br"),t("br"),n._v("Kim Nguyên đỉnh cho dù thoạt nhìn có chút không đáng tin cậy, nhưng dù sao cũng là sống không biết bao nhiêu năm linh tính, bị danh sư học viện không biết bao nhiêu người tẩm bổ, nắm giữ danh sư ý niệm cùng tinh thần, nhất định có thể tiếp tục kiên trì."),t("br"),t("br"),n._v("Nếu không, ban đầu ở lôi đình chi hải, cũng không phải là ngủ say, mà là trực tiếp tử vong."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Đối thoại âm thanh bên trong, đạo thứ hai lôi điện lần nữa rơi xuống."),t("br"),t("br"),n._v("Lần này so vừa rồi càng thêm hung mãnh, còn chưa tới đến trước mặt, không khí tựa như là bị sấm sét xé rách cháy đen, bất cứ lúc nào cũng sẽ nứt ra."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Lôi đình rơi vào Kim Nguyên đỉnh trên thân, để hắn run rụt lại, bay ở trên không thân hình cũng lại không vững vàng, trực tiếp rơi trên mặt đất đập ra một cái hố to."),t("br"),t("br"),n._v("Thoạt nhìn cho dù chật vật, nhưng vẫn là kiên trì chịu đựng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đạo thứ hai kết thúc, không cần đối phương phản ứng, đạo thứ ba lôi đình lật đổ mà xuống."),t("br"),t("br"),n._v("Đạo này càng thêm cường đại, màu xanh đậm điện mang, chừng to bằng vại nước, hướng về phía Kim Nguyên đỉnh liền bổ xuống."),t("br"),t("br"),n._v("“Không tốt! So với trong tưởng tượng phải lớn hơn nhiều, có khả năng không kiên trì nổi!”"),t("br"),t("br"),n._v("Nhìn thấy lôi điện uy thế, Trương Huyền lại không trước đó bình tĩnh, vẻ mặt căng thẳng."),t("br"),t("br"),n._v("Mặc dù hắn đối Kim Nguyên đỉnh mười phần tin tưởng, nhưng trước mắt lôi đình thực sự thật là đáng sợ, thật muốn trùng kích vào đến, làm không cẩn thận, sẽ bị tại chỗ nổ hồn phi phách tán, linh tính biến mất."),t("br"),t("br"),n._v("“Kim Nguyên đỉnh, thôn phệ địa hỏa, lấy lửa đối lôi...”"),t("br"),t("br"),n._v("Biết chỉ bằng vào đối phương, khẳng định chống lại không được, Trương Huyền vội vàng hô, đồng thời lật bàn tay một cái, một đống trận kỳ xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Giơ tay lên bắn đi ra."),t("br"),t("br"),n._v("Sưu sưu sưu sưu!"),t("br"),t("br"),n._v("Trận kỳ rơi vào Kim Nguyên đỉnh bốn phía, lập tức tạo thành một cái tiếp đón địa hỏa trận pháp."),t("br"),t("br"),n._v("Lôi đình to lớn, mạnh mẽ chống đỡ là khẳng định không kháng nổi, đã như vậy, vậy liền lấy mạnh đụng mạnh!"),t("br"),t("br"),n._v("Dùng địa hỏa đối kháng lôi điện."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Trận pháp dẫn động, cuộn trào mãnh liệt địa hỏa, lập tức bị trận pháp rút ra tới, hình thành màu tím sậm ngọn lửa."),t("br"),t("br"),n._v("Kim Nguyên đỉnh cũng biết đến thời khắc sống còn, giãy dụa lấy từ trong hố bò lên đi ra, nắp lò mở ra, bỗng nhiên khẽ hấp."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Hỏa diễm lập tức đi vào đỉnh lô ổ bụng bên trong, tạo thành một cái to lớn Hỏa Long."),t("br"),t("br"),n._v("Hỏa Long phun ra ngoài, cùng trên bầu trời đánh tới đạo thứ ba lôi điện xung kích cùng một chỗ."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Đất rung núi chuyển, hai cổ lực lượng khổng lồ hình thành sóng xung kích hướng bốn phía tán dật, thời gian nháy mắt, liền đem luyện khí sư kiến trúc, toàn bộ thổi ngã, đổ sụp không biết bao nhiêu kiến trúc."),t("br"),t("br"),n._v("“Ta công hội...”"),t("br"),t("br"),n._v("Con mắt nhảy một cái, Tần hội trưởng khóc."),t("br"),t("br"),n._v("Ngươi đã nói không xông đường, liền để ngươi luyện khí... Biến thành như vậy, ta về sau làm sao đối liệt tổ liệt tông dặn dò?"),t("br"),t("br"),n._v("(Sau đó, còn có một canh)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1215"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1217"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);