(window.webpackJsonp=window.webpackJsonp||[]).push([[26],{380:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1000-menh-can-cua-ta"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1000-menh-can-cua-ta"}},[n._v("#")]),n._v(" Chương 1000: Mệnh căn của ta")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1000: Mệnh căn của ta"),i("br"),i("br"),n._v("Điện chủ vị trí phủ đệ, Hứa trưởng lão ngồi ở một bên, một mặt mỉm cười."),i("br"),i("br"),n._v("“Ngươi nói là lần này may mắn mà có một vị gọi Tôn Cường độc sư, đại nhân đại nghĩa, bất kể nguy hiểm, đưa ngươi cứu, nếu không, khả năng liền không về được?”"),i("br"),i("br"),n._v("Đối diện một cái ông lão nhìn lại."),i("br"),i("br"),n._v("Sáu, bảy mươi tuổi, một mặt mỉm cười, khuôn mặt hòa ái, nếu như không phải biết đây là nơi nào, có thể có thể nằm mộng cũng nghĩ không ra, bộ dáng như thế người lại là Hồng Viễn Độc điện điện chủ, thất tinh cấp bậc độc sư!"),i("br"),i("br"),n._v("“Đúng, vị này Tôn độc sư, nhiệt tình vì lợi ích chung, hữu dũng hữu mưu, không phải hắn, ta khả năng cùng Tiết trưởng lão đồng dạng, sớm đã rời đi!”"),i("br"),i("br"),n._v("Nói đến Tôn Cường, Hứa trưởng lão tràn đầy bội phục."),i("br"),i("br"),n._v("Theo Thánh vực tứ trọng trong tay cường giả đem hắn cứu ra, chỉ bằng vào phần này can đảm cùng khí phách, chính là không biết bao nhiêu người, không cách nào so sánh."),i("br"),i("br"),n._v("“Một cái Tòng Thánh, đối mặt mạnh như thế người, có thể mặt không đổi sắc, thản nhiên ứng đối, quả thực là cái hiếm có nhân tài, tốt, ta đồng ý hắn khảo hạch độc sư, gia nhập chúng ta Độc điện!”"),i("br"),i("br"),n._v("Mỉm cười, ông lão vuốt râu."),i("br"),i("br"),n._v("“Đa tạ điện chủ!”"),i("br"),i("br"),n._v("Hứa trưởng lão đứng dậy, nhẹ nhàng cười một tiếng, mang trên mặt tự tin và chắc chắn: “Yên tâm đi, ngươi nhất định sẽ không hối hận quyết định của ngày hôm nay, vị này Tôn Cường, tuyệt đối là một vị đáng tin cậy, để cho người ta dựa vào bằng hữu!”"),i("br"),i("br"),n._v("“Có thể được đến như ngươi vậy khen ngợi, ta đều có chút muốn nhìn một chút vị này Tôn độc sư, đến cùng là loại nhân vật nào...”"),i("br"),i("br"),n._v("Điện chủ gật gật đầu, chính muốn tiếp tục nói chuyện, chỉ thấy bên ngoài gian phòng một trưởng lão vội vã đi đến."),i("br"),i("br"),n._v("“Điện chủ...”"),i("br"),i("br"),n._v("“Phong trưởng lão, ngươi làm sao?”"),i("br"),i("br"),n._v("Quay đầu nhìn lại, nhận ra được, chính là Độc điện một vị trưởng lão, Phong Bình."),i("br"),i("br"),n._v("Chỉ bất quá, lúc này Phong trưởng lão, cùng trước kia khác biệt, mặt mũi bầm dập, trên người cũng tất cả đều là vết máu, hình như vừa vặn trải qua cái gì sinh tử kiếp khó, không nói ra được thảm liệt."),i("br"),i("br"),n._v("“Làm sao vậy? Ngươi còn không biết xấu hổ nói làm sao vậy?” Nhìn thấy Hứa trưởng lão, Phong Bình lập tức giận không chỗ phát tiết, rít lên một tiếng, liền muốn vọt qua đến cùng hắn quyết đấu."),i("br"),i("br"),n._v("Nếu không phải vị này Hứa Du, mang tên kia tới, bọn họ Độc điện còn sẽ thảm liệt như vậy?"),i("br"),i("br"),n._v("“Đường đường trưởng lão, vừa đến liền muốn xuất thủ, còn thể thống gì? Phong trưởng lão, chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Lông mày nhíu một cái, điện chủ trách mắng."),i("br"),i("br"),n._v("“Điện chủ...”"),i("br"),i("br"),n._v("Nghe được trách mắng, Phong trưởng lão ngừng lại, răng cắn chặt, một mặt bi phẫn nhìn lại: “Độc điện hai mươi vị trưởng lão, bị người giết... Mười bốn vị! Sống sót, chỉ có ta cùng Đỗ trưởng lão các loại sáu người, ngay cả Minh phó điện chủ cũng bị một kiếm chém rơi bả vai, ngàn cân treo sợi tóc...”"),i("br"),i("br"),n._v("“Cái gì? Trưởng lão bị giết mười bốn vị? Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Đột nhiên đứng dậy, điện chủ con ngươi co vào."),i("br"),i("br"),n._v("Hồng Viễn Độc điện, tổng cộng liền hai mươi hai vị trưởng lão, mới vừa nghe được Tiết trưởng lão bị người giết... Làm sao thời gian nháy mắt, lại chết mười bốn?"),i("br"),i("br"),n._v("Những trưởng lão này, không đều tại Độc điện không ra ngoài ư? Chẳng lẽ có kẻ địch giết đi vào?"),i("br"),i("br"),n._v("“Là... Hứa trưởng lão mang tới vị kia Tôn Cường làm!” Phong trưởng lão cắn răng."),i("br"),i("br"),n._v("“Tôn Cường? Xảy ra chuyện gì?” Hứa trưởng lão thân thể nhoáng một cái."),i("br"),i("br"),n._v("“Là như vậy...”"),i("br"),i("br"),n._v("Phong trưởng lão đem chuyện lúc trước kỹ càng nói một lần, bao quát đoạn nhân tìm phiền toái, Minh phó điện chủ giận dữ, cùng... Vây công Tôn Cường, bị hắn liên sát mấy người."),i("br"),i("br"),n._v("“Tại sao có thể như vậy...”"),i("br"),i("br"),n._v("Nghe xong giải thích, Hứa trưởng lão mắt tối sầm lại."),i("br"),i("br"),n._v("Ta là nói qua, Độc điện người bội phục thực lực mạnh, để ngươi không cần hạ thủ lưu tình, thế nhưng... Không để ngươi đem trọn cái Độc điện trưởng lão liên sát mười bốn ah!"),i("br"),i("br"),n._v("Mới vừa cùng điện chủ nói xong, đại nhân đại nghĩa, đáng tin cậy, liền làm ra cái này... Còn bàn giao thế nào?"),i("br"),i("br"),n._v("“Đi qua nhìn một chút!”"),i("br"),i("br"),n._v("Lạnh lùng nhìn hắn một cái, điện chủ hất lên ống tay áo, vội vã đi theo gió trường lão sau lưng đi ra ngoài."),i("br"),i("br"),n._v("Hứa trưởng lão đành phải đi theo."),i("br"),i("br"),n._v("Không lâu sau liền đến đến Trương Huyền nơi ở, lập tức nhìn thấy đầy đất hoành nằm thi thể."),i("br"),i("br"),n._v("Những người này, đều đạt đến Thánh vực nhất trọng đỉnh phong, đối độc nghiên cứu càng là tinh thông tới cực điểm, ra đến bên ngoài, một người đơn đấu ba, bốn cái cùng cấp bậc cường giả không thành vấn đề, mà giờ khắc này... Tất cả đều lạnh như băng nằm trên mặt đất."),i("br"),i("br"),n._v("Gấp vội vàng ngẩng đầu nhìn lại, chỉ thấy kẻ cầm đầu Tôn Cường, vẫn như cũ khoanh chân ngồi dưới đất, hai mắt nhắm nghiền tu luyện, hình như tất cả mọi chuyện không có quan hệ gì với hắn đồng dạng."),i("br"),i("br"),n._v("“Hoàng trưởng lão, Lý trưởng lão, Lưu trưởng lão...”"),i("br"),i("br"),n._v("Nhìn xem thi thể đầy đất, điện chủ da mặt không ngừng giật giật, con mắt sắp chảy ra máu."),i("br"),i("br"),n._v("Đây đều là bồi hắn mấy trăm năm lão nhân, không nghĩ tới, mười mấy cái hô hấp công phu, liền bị cái tên này giết sạch!"),i("br"),i("br"),n._v("Mấu chốt giết người, chẳng những không có sợ hãi, vẫn ở nơi này khoanh chân ngồi tu luyện... Có phần quá không đem Độc điện để ở trong mắt đi!"),i("br"),i("br"),n._v("“Điện chủ, ngươi nhất định phải giết cái tên này, báo thù cho chúng ta...”"),i("br"),i("br"),n._v("Minh phó điện chủ vội vàng đi tới gần, rít lên một tiếng."),i("br"),i("br"),n._v("Thương thế của hắn đã băng bó kỹ, cũng phục dụng đối ứng dược vật, chỉ bất quá quá nghiêm trọng, đã nguyên khí tổn hao nhiều, coi như chữa khỏi, cũng gần như phế đi."),i("br"),i("br"),n._v("“Đúng vậy a, điện chủ, cái tên này quá mức phách lối, cùng chúng ta chiến đấu, liền con mắt đều không mở ra, quả thực không coi ai ra gì tới cực điểm...”"),i("br"),i("br"),n._v("“Nhất định phải giết chết, nếu không, như thế nào xứng đáng Độc điện liệt tổ liệt tông!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Còn lại hai vị phó điện chủ cùng trưởng lão, cũng đồng thời hét."),i("br"),i("br"),n._v("Bọn họ độc sư ra ngoài, đều là người khác hoảng sợ chạy trốn, lúc nào nhận qua loại này oan ức, bị một cái nhắm mắt lại gia hỏa, liên sát mười mấy vị trưởng lão."),i("br"),n._v(" “Yên tâm đi, còn không người dám ở Độc điện làm loạn về sau, còn có thể ung dung ngoài vòng pháp luật!”"),i("br"),i("br"),n._v("Bàn tay lớn vẫy một cái, điện chủ nhìn lại, ánh mắt lạnh lùng như băng, dặn dò một tiếng: “Đem Hứa Du giam giữ, đợi ta xử lý cái tên này về sau, lại tiến hành thẩm vấn!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Nghe được phân phó, lập tức mấy vị trưởng lão tới, đem Hứa trưởng lão vây vào giữa."),i("br"),i("br"),n._v("“Điện chủ...”"),i("br"),i("br"),n._v("Hứa trưởng lão không nghĩ tới sẽ xuất hiện loại kết quả này, mặt mũi lo lắng, nhưng không có biện pháp nào."),i("br"),i("br"),n._v("Muốn giải thích, nhưng cũng không biết như thế nào mở miệng."),i("br"),i("br"),n._v("Vị này Tôn độc sư, cùng bản thân đến thời điểm, còn rất bình thường, nói phải khiêm tốn... Làm sao tìm được điện chủ hồi báo công phu, liền giết mười mấy vị trưởng lão, cái này mẹ nó cũng gọi khiêm tốn?"),i("br"),i("br"),n._v("Ngươi qua đây không phải khảo hạch độc sư, là tới muốn đem chúng ta Độc điện đoàn diệt a..."),i("br"),i("br"),n._v("Mặc dù buồn bực, nhưng mà vừa nghĩ tới Tôn Cường là ân nhân cứu mạng của hắn, vẫn là không nhịn được mở miệng: “Điện chủ, Tôn độc sư không phải lạm sát kẻ vô tội người, ta cảm thấy trong đó khẳng định có hiểu lầm...”"),i("br"),i("br"),n._v("“Đem miệng của hắn cho ta che lại!”"),i("br"),i("br"),n._v("Thấy Độc điện trưởng lão đều sắp chết hết, cái tên này thế mà còn nói hiểu lầm gì đó, điện chủ tức giận lông mày giương lên."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Một cái phó điện chủ gật gật đầu đi tới gần, ngón tay một chút, liền đem Hứa trưởng lão miệng ngăn chặn, để hắn lại nói không ra lời."),i("br"),i("br"),n._v("“Tôn Cường đúng không, lẻ loi một mình, liền dám đến Độc điện làm loạn, lá gan thật là không nhỏ, bất quá... Hôm nay chẳng cần biết ngươi là ai, lại có mục đích gì, đã tới, liền chớ đi...”"),i("br"),i("br"),n._v("Để thấy Hứa trưởng lão sẽ không đi thay cái tên này nói chuyện, điện chủ lúc này mới quay tới đầu đến, một tiếng trách mắng, đi tới Trương Huyền gần, ngón tay một chút, đầu ngón tay bay ra một loạt lớn chừng bằng móng tay giáp trùng, không nhiều không ít, vừa vặn chín cái."),i("br"),i("br"),n._v("Những này giáp trùng hiện lên ám kim sắc, cánh phản xạ ánh nắng, lộ ra hết sức dữ tợn."),i("br"),i("br"),n._v("“Là điện chủ ám kim độc trùng!”"),i("br"),i("br"),n._v("“Đây chính là liền Thánh vực tam trọng cường giả, đều có thể nhẹ nhõm độc chết Thánh vật!”"),i("br"),i("br"),n._v("“Đúng vậy a, vì nuôi những này ám kim độc trùng, điện chủ hao tốn không biết bao nhiêu tâm huyết, chỉ riêng Thánh vực dược liệu, liền ăn không dưới một trăm gốc... Công phu không phụ lòng người, mặc dù gian khổ, nhưng cũng nuôi thành tròn chín cái đi ra!”"),i("br"),i("br"),n._v("“Cái này chín cái độc trùng, mỗi một cái đều có thể độc chết một vị Thánh vực tam trọng, chín cái cộng lại, liền xem như Thánh vực tứ trọng cường giả, đều muốn tránh né, đưa chúng nó toàn bộ thả ra, xem ra điện chủ giận thật à!”"),i("br"),i("br"),n._v("“Cái tên này chết chắc, mặc dù không biết phục dụng cái gì thuốc giải độc, có thể chống lại đồng dạng độc vật, nhưng khẳng định ngăn không được điện chủ độc trùng...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn thấy điện chủ thả ra ám kim độc trùng, tất cả mọi người ánh mắt sáng lên."),i("br"),i("br"),n._v("Mặc dù đều nuôi dưỡng độc trùng, rắn độc loại hình, nhưng muốn nói cấp bậc, điện chủ ám kim độc trùng tuyệt đối là đỉnh phong nhất tồn tại."),i("br"),i("br"),n._v("Tùy tiện một cái bay ra ngoài, liền có thể độc chết Thánh vực tam trọng cường giả, để vô số người vì đó kiêng kị!"),i("br"),i("br"),n._v("Thật muốn đại khai sát giới, chín đại độc trùng bay ra, toàn bộ Hồng Viễn đế quốc, đều có thể lại bởi vậy diệt tuyệt."),i("br"),i("br"),n._v("Đây chính là thất tinh độc sư đáng sợ!"),i("br"),i("br"),n._v("Mọi người nhìn lại, vị này Tôn Cường, coi như phục dụng lợi hại gì thuốc giải độc vật, có thể chống lại một chút độc phấn, nhưng mà đối mặt lợi hại như vậy độc trùng, cũng chỉ có một con đường có thể đi... Cái kia chính là, chết!"),i("br"),i("br"),n._v("“Ô ô ô...”"),i("br"),i("br"),n._v("Cùng mọi người chờ mong khác biệt, Hứa trưởng lão mắt tối sầm lại, không ngừng la lên, đáng tiếc miệng lại bị che lại, cái gì đều nói không nên lời, chỉ có thể mặt mũi lo lắng."),i("br"),i("br"),n._v("Không để ý tới những người khác khiếp sợ, sắc mặt nghiêm túc, điện chủ ngón tay búng một cái, chín giọt máu tươi bay ra, chín cái ám kim độc trùng, phân biệt thôn phệ huyết dịch, trở nên nhan sắc càng thêm diễm lệ."),i("br"),i("br"),n._v("“Đi!”"),i("br"),i("br"),n._v("Lông mày vung lên, một chỉ khoanh chân ngồi dưới đất “Tôn Cường”, điện chủ sát ý sôi trào."),i("br"),i("br"),n._v("Ong ong ong!"),i("br"),i("br"),n._v("Nghe được mệnh lệnh, chín cái độc trùng thẳng tắp hướng bay tới, thời gian nháy mắt đi tới gần."),i("br"),i("br"),n._v("Trương Huyền dường như không ý thức được nguy hiểm, vẫn như cũ khoanh chân ngồi dưới đất, hai mắt đóng lại, không nhúc nhích."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Thấy hắn không biết tránh né, điện chủ vẻ mặt vui mừng, khống chế độc trùng, rơi vào trên người, lập tức, Trương Huyền cánh tay, cổ các loại trần trụi bên ngoài trên da thịt, bò đầy giáp trùng."),i("br"),i("br"),n._v("Răng rắc! Răng rắc! Răng rắc!"),i("br"),i("br"),n._v("Độc trùng há miệng cắn, máu tươi theo côn trùng miệng, hướng phía trong truyền vào."),i("br"),i("br"),n._v("“Cắn lên...”"),i("br"),i("br"),n._v("“Ám kim độc trùng chỉ cần cắn lên, nọc độc liền sẽ dọc theo huyết dịch tiến nhập thể nội, không cần ba cái hô hấp, liền sẽ mất mạng!”"),i("br"),i("br"),n._v("“Bị nhiều như vậy độc trùng đồng thời cắn lên, ta cảm thấy hắn liền một cái hô hấp đều không kiên trì nổi...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Vốn cho rằng cái tên này nhìn thấy độc trùng tới, sẽ dọa đến chạy trốn, không nghĩ tới, vẫn như cũ ngồi dưới đất, bị chín đầu độc trùng đồng thời cắn, liền xem như Thần Tiên, cũng không có cách nào cứu."),i("br"),i("br"),n._v("Hô! Hô!"),i("br"),i("br"),n._v("Cắn xong Trương Huyền, chín đầu ám kim độc trùng một lần nữa bay lên, chẳng qua mới bay không cao bao nhiêu, đột nhiên giống như là uống say, nguyên một đám nghiêng đầu một cái."),i("br"),i("br"),n._v("Lạch cạch! Lạch cạch! Lạch cạch!"),i("br"),i("br"),n._v("Tất cả đều rơi trên mặt đất, sáu đầu chân co quắp mấy lần, không có động tĩnh nữa."),i("br"),i("br"),n._v("“Phốc!”"),i("br"),i("br"),n._v("Sắc mặt trắng nhợt, điện chủ một ngụm máu tươi phun ra, liên tục lui về sau bảy, tám bước, ngồi ngay đó, giống như là gặp quỷ đồng dạng biểu lộ hướng về phía trước nhìn qua."),i("br"),i("br"),n._v("“Mệnh căn của ta...”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1008"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1010"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);