(window.webpackJsonp=window.webpackJsonp||[]).push([[261],{616:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1211-hinh-đuong-chu-xuat-khieu-kiep"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1211-hinh-đuong-chu-xuat-khieu-kiep"}},[n._v("#")]),n._v(" Chương 1211: Hình đường chủ Xuất Khiếu kiếp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vừa rồi sen ăn thịt người trong nháy mắt thôn phệ lực lượng, lại nhanh, lại hung ác, vừa chuẩn, cùng Quý sư nói đồng dạng, bình thường Hóa Phàm cảnh cường giả, chỉ sợ đều khó mà chạy trốn."),t("br"),t("br"),n._v("Nếu như đem thứ này, trồng trọt tại phủ đệ, người bình thường muốn vào đến, chỉ sợ cũng khó khăn."),t("br"),t("br"),n._v("“Loại trừ sen ăn thịt người, đầm lầy phía dưới, còn sinh hoạt lấy một chút đặc thù độc trùng, rơi vào trong đó, một khi bị cắn, sẽ làm tràng tê liệt, toàn thân thực lực đều không thi triển ra được, cuối cùng dần dần chìm vào đầm lầy, biến thành từng chồng bạch cốt, hàng năm ở đây tử vong danh sư, tu luyện giả, không ít hơn mười vạn người, nguyên nhân chính là như vậy, cái này một lần được phong làm cấm địa, không cho phép tu luyện giả đến đây mạo hiểm!”"),t("br"),t("br"),n._v("Quý sư vừa nói, vừa từ trong giới chỉ lấy ra một khối thịt chín, tiện tay ném vào cách đó không xa đầm lầy."),t("br"),t("br"),n._v("Thịt chín vừa mới bắt đầu phiêu phù ở đầm lầy phía trên, một lát sau, một chút hắc giáp côn trùng từ bùn bên trong chui ra, cắn đi lên, thời gian nháy mắt, liền mắt trần có thể thấy hóa thành nước đặc, tựa như bị cường toan hủ thực đồng dạng."),t("br"),t("br"),n._v("Thấy cảnh này, mọi người sắc mặt tất cả đều trở nên khó coi."),t("br"),t("br"),n._v("Trước khi đến liền biết nơi này rất đáng sợ, chỉ là không nghĩ tới, đáng sợ như thế, cho dù bọn họ đều là Nguyên Thần cảnh trở lên cường giả, đều không chịu được sống lưng rét run."),t("br"),t("br"),n._v("“Bất quá, mọi người cũng không cần lo lắng quá mức, lợi hại hơn nữa tử địa, cũng sẽ có sinh cơ, trong ao đầm sẽ có chút trần trụi bên ngoài mặt đất, liên tiếp cùng một chỗ, như là cầu nổi, chỉ cần tìm đúng những địa phương này, chúng ta liền có thể tiếp tục tiến lên, không cần lo lắng quá nhiều!”"),t("br"),t("br"),n._v("Quý sư cười cười, thân thể nhoáng một cái, bước ra xa hơn mười thước, đã đi tới đầm lầy bên trong một cái cứng rắn chỗ: “Mọi người đi sát ở sau ta, không trốn đạp sai cùng tụt lại phía sau!”"),t("br"),t("br"),n._v("Nói hết tiếp tục hướng phía trước nhảy xuống, liên tiếp vài chục cái, người đã đến vài trăm mét có hơn."),t("br"),t("br"),n._v("Trương Huyền nhìn kỹ lại, hắn chỗ đặt chân, màu sắc cùng địa phương khác thoáng khác biệt, có chút trôi nổi xanh, cho dù đồng dạng không có dài cây cối, lại cho người ta một loại có thể thai nghén sinh mệnh cảm giác, cùng cái khác đen kịt hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Những này mặt đất, tự nhiên hình thành khả năng rất nhỏ, đoán chừng là hậu nhân xây dựng đi ra...”"),t("br"),t("br"),n._v("Đầm lầy nguồn gốc hắn không biết, nhưng một khi hình thành, nhất định là một chỗ toàn thể sụp đổ, mấy chục mét liền có một chỗ có thể dung nạp người đứng yên mặt đất, có thể là người vì gây nên."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Không rầu rĩ những vấn đề này, để phi hành Thánh thú tại nguyên chỗ đợi chờ, Trương Huyền đám người theo sát ở phía sau cũng nhảy tới."),t("br"),t("br"),n._v("Tất cả mọi người là cường giả Thánh vực, trí nhớ mạnh mẽ, đối phương mặc dù chỉ đi một lần, vị trí cụ thể lại đều đã ghi vào trong óc, theo sát mà lên, không có chút nào sai lầm."),t("br"),t("br"),n._v("“Quý sư, ta nghe nói nơi này có Thánh thú, không biết làm sao sinh tồn?”"),t("br"),t("br"),n._v("Bọn họ tiến đến một lần, đều phiền toái như vậy, Thánh thú làm sao sống được?"),t("br"),t("br"),n._v("“Mảnh này đầm lầy, hướng phía trong đi, sẽ có một chút lục địa, lớn nhất đường kính cũng chỉ có mấy cây số, bởi vì đặc thù môi trường, có thể sinh ra một chút linh dược hoặc là đặc thù khoáng thạch, không ít Thánh thú, vì tăng lên bản thân, đều sẽ chiếm núi làm vua, canh giữ ở một chỗ, cùng đến đây thám hiểm tu luyện giả chiến đấu, thậm chí đem hắn giết chết!”"),t("br"),t("br"),n._v("Quý sư giới thiệu một chút: “Nhìn, phía trước liền có một khối!”"),t("br"),t("br"),n._v("Mọi người nhìn lại, quả nhiên thấy cách đó không xa có một mảnh lục địa, xanh mơn mởn, cùng trong biển đảo hoang đồng dạng, mọc đầy đủ loại cây cối, thậm chí có không ít chim tước sinh hoạt trong đó."),t("br"),t("br"),n._v("Nhanh chóng tiến lên, đi hơn mười phút, đi tới trước mặt."),t("br"),t("br"),n._v("Mảnh này lục địa, đường kính đại khái chừng năm trăm mét, đứng sừng sững lấy một cái không lớn đồi núi, lơ lửng tại đầm lầy phía trên, như là một cái móc ngược mai rùa."),t("br"),t("br"),n._v("Đi tới phía trên, mọi người rơi xuống."),t("br"),t("br"),n._v("“Chúng ta mau mau đi thôi, tận lực có thể tại trời tối phía trước, chạy tới trung tâm chiểu trạch, nếu không, ban đêm đầm lầy càng thêm đáng sợ!”"),t("br"),t("br"),n._v("Thấy mọi người tò mò ý định trên đất bằng loạn chuyển, Quý sư nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhìn một chút mặt trời, Trương Huyền dặn dò một câu: “Đi thôi!”"),t("br"),t("br"),n._v("Mặc dù khoảng cách trời tối còn sớm, nhưng đầm lầy phạm vi thực sự quá lớn, lại không thể phi hành, như vậy chạy tới lời nói, không có một, hai canh giờ, chỉ sợ rất khó làm được, không thể bị dở dang."),t("br"),t("br"),n._v("đăng ｎhập //ngantruyen.com/ để đọc truyện"),t("br"),n._v("Có Quý sư dẫn đường, quả thực an toàn không ít, có Thánh thú lục địa, trước thời hạn đi vòng, nguy hiểm chỗ, cũng trước thời hạn liền tránh đi, đi tiếp cận hai canh giờ, rốt cục đi tới đầm lầy chỗ sâu."),t("br"),t("br"),n._v("Trên đường gặp mấy đợt thám hiểm tu luyện giả, nhìn thấy bọn họ chủ động lách qua."),t("br"),t("br"),n._v("Vì che giấu tung tích, mọi người mặc dù không có mặc danh sư phục, nhưng thực lực ở nơi đó bày biện, trong thành thị, có danh sư đường tọa trấn, mọi người giảng quy củ, không dám làm loạn, mà ở đây, một khi phát hiện những người khác trong tay có bảo vật, lẫn nhau chặn giết, giết người cướp của không phải số ít."),t("br"),t("br"),n._v("Loại địa phương này liền cùng ngoài vòng pháp luật chỗ đồng dạng, chỉ cần đem thi thể ném vào đầm lầy, cho dù danh sư tới, cũng khó có thể dò xét."),t("br"),t("br"),n._v("“Phía trước chỗ kia hòn đảo liền là toàn bộ đầm lầy trung tâm, muốn tìm Qủy quật, liền muốn tới trước nơi này, sau đó lại nghĩ biện pháp, từng chút một dò xét, liền xem như ta, cũng không có biện pháp tốt hơn!”"),t("br"),t("br"),n._v("Lại đi nửa canh giờ, mặt trời đã rơi về phía tây, Quý sư lúc này mới chỉ về phía trước, nói."),t("br"),t("br"),n._v("Thuận ngón tay hắn phương hướng, mọi người nhìn lại, lập tức nhìn thấy một cái to lớn lục địa xuất hiện ở trước mắt, đường kính chừng bảy, tám cây số, một tòa dốc đứng ngọn núi đứng sừng sững trong đó, đủ loại cây cối lít nha lít nhít, từ xa nhìn lại, tựa như một tòa thành thị."),t("br"),t("br"),n._v("“Đi qua đi!”"),t("br"),t("br"),n._v("Theo sát trên đó, mọi người rất mau tới đến ở trên đảo."),t("br"),t("br"),n._v("Cùng phía ngoài lục địa không có gì khác biệt, thậm chí nơi này linh khí càng thêm nồng đậm, cho người ta một loại khí tức sảng khoái cảm giác."),t("br"),t("br"),n._v("“Khó trách sẽ có Thánh thú ở chỗ này, không muốn rời khỏi... Ở đây tu luyện, quả thực có thể làm ít công to!”"),t("br"),t("br"),n._v("Cảm thụ một cái, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nơi này linh khí nồng đậm, là tự nhiên tu luyện bảo địa, tại đây tu luyện, tấn cấp tốc độ, tất nhiên vượt xa ngoại giới."),t("br"),t("br"),n._v("Không ít Thánh thú, khẳng định cũng là phát hiện những bí mật này, biết rõ nguy hiểm, vẫn như cũ đổ xô tới."),t("br"),t("br"),n._v("“Đêm nay ngay ở chỗ này nghỉ ngơi đi, tìm Qủy quật, bình minh lại đi, ban đêm trong đầm lầy độc trùng, sẽ leo ra, một khi bị gặp gỡ, liền nguy hiểm...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Quý sư lấy ra một chút cao cấp Thánh thú nước tiểu, tưới vào bốn phía, dùng để cảnh cáo một chút độc trùng, độc thú, để bọn chúng không muốn đến đây."),t("br"),t("br"),n._v("Đi một đường, mọi người cũng cảm nhận được đầm lầy đáng sợ, không nói thêm lời, đem riêng phần mình chuẩn bị xong đồ vật, lấy ra ngoài."),t("br"),t("br"),n._v("Rượu ngon thịt nướng, ăn cũng coi như phong phú."),t("br"),t("br"),n._v("Ăn xong cơm tối, Trương Huyền cũng không nghỉ ngơi, khoanh chân ngồi dưới đất tiếp tục tu luyện."),t("br"),n._v("Thiên Đạo chân khí ở trong người chảy xuôi, từng lần một cọ rửa thai anh, tranh thủ để hắn mau chóng lột xác."),t("br"),t("br"),n._v("Ngũ Diệu kim thân tu luyện tới hoàn mỹ cảnh giới về sau, Trương Huyền đem Danh Sư đường phòng ngự điện rất nhiều công pháp, hội tụ thành Thiên Đạo kim thân, hiệu quả cũng không tệ, giờ phút này thân thể của hắn cho dù so ra kém Kim Nguyên đỉnh, cũng không kém nhiều."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Tu luyện không biết bao lâu, đột nhiên cảm thấy cách đó không xa một cỗ năng lượng nhanh chóng không ổn định, quay đầu nhìn lại, chỉ thấy Hình đường chủ vẻ mặt đỏ lên, tựa hồ có chút áp chế không nổi lực lượng trong cơ thể."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Hai bước đi tới trước mặt."),t("br"),t("br"),n._v("Những người khác cũng từ trong tu luyện thức giấc, đồng loạt vây quanh."),t("br"),t("br"),n._v("“Trương sư, ta có thể muốn độ Xuất Khiếu kiếp...”"),t("br"),t("br"),n._v("Hình đường chủ nói."),t("br"),t("br"),n._v("Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Hình đường chủ nguyên thần có hại, dựa vào sự giúp đỡ của hắn, nhận được chữa trị, rất nhanh đột phá nửa bước Xuất Khiếu, đạt đến ngụy xuất khiếu cảnh giới, dựa theo tình huống bình thường, chỉ cần hắn bằng lòng, lúc nào, đều có thể dẫn động lôi kiếp, trở thành chân chính Xuất Khiếu cảnh cường giả, làm sao đột nhiên ở đây bắt đầu?"),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Ta cũng không biết, vừa rồi chỉ tu luyện một hồi, liền cảm thấy nguyên thần đã đạt tới sung mãn trạng thái, lúc đầu muốn áp chế, lại áp chế không nổi...”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Hình đường chủ chân khí trong cơ thể dọc theo lỗ chân lông bắn ra bốn phía, ngay sau đó mọi người liền cảm thấy trên đầu trời u ám, lôi kiếp đã đã tới."),t("br"),t("br"),n._v("“Không cần khẩn trương, Xuất Khiếu kiếp mà thôi, nhờ vào thực lực của ngươi, có thể nhẹ nhõm vượt qua!”"),t("br"),t("br"),n._v("Ra hiệu mọi người rời khỏi lôi kiếp khu vực, không nên bị ảnh hưởng, Trương Huyền cười cười, nói."),t("br"),t("br"),n._v("Thân là Chiến Sư đường đường chủ, sức chiến đấu kinh người, mặc dù lôi kiếp tới có chút vội vàng, muốn vượt qua, không khó lắm."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mới vừa nói hết, lôi điện liền bổ xuống, trực tiếp đập xuống."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tinh thần khẽ động, Hình đường chủ nguyên thần rời khỏi thân thể, trực tiếp hướng lôi điện bay đi."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Tới đụng một cái, lập tức giống như là bị liệt hỏa thiêu đốt sợi bông, hình thể không ngừng thu nhỏ, tựa hồ tại thừa nhận khó có thể chịu đựng dằn vặt."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("“Xuất Khiếu kiếp nguy hiểm vô cùng, bình thường không phải mượn nhờ thân thể đối kháng ư? Hình đường chủ, làm sao nguyên thần xuất khiếu, đi nghênh đón? Cái này... Quá nguy hiểm!”"),t("br"),t("br"),n._v("Ngô sư cũng có chút không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Xuất Khiếu kiếp, lôi đình rèn luyện nguyên thần, dựa theo tình huống bình thường, vì lại càng dễ vượt qua, rất nhiều tu luyện giả đều là mượn nhờ thân thể chống cự một bộ phận lôi điện, còn lại để nguyên thần hấp thu... Hình đường chủ ngược lại tốt, trực tiếp nguyên thần xuất khiếu nghênh đón tiếp lấy, cái này cùng tự tìm cái chết không có gì khác biệt."),t("br"),t("br"),n._v("“Hắn hẳn là không nhận bản thân khống chế...”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("“Không nhận khống chế?”"),t("br"),t("br"),n._v("Mọi người không hiểu."),t("br"),t("br"),n._v("“Có lẽ có vu hồn sư động thủ với hắn, biết hắn muốn đột phá, dẫn động nguyên thần của hắn, muốn mượn nhờ lực lượng sấm sét, trước đem hắn diệt trừ, hoặc là, để chúng ta vì chú ý hắn, hao tổn khí lực...” Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("“Vu hồn sư? Chúng ta... Bị phát hiện?”"),t("br"),t("br"),n._v("Ngô sư đám người tất cả đều sầm mặt lại."),t("br"),t("br"),n._v("Bọn họ đổi đi danh sư phục, không có chút nào trì hoãn, đi tới nơi này, liền là nghĩ đuổi tại Thanh Điền Hoàng vị lão sư kia, phát hiện nhóm người mình trước đó, tìm được trước đối phương."),t("br"),t("br"),n._v("Không nghĩ tới đối với đối phương hoàn toàn không biết gì cả, người của mình lại trước trúng chiêu."),t("br"),t("br"),n._v("“Hẳn là...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền tiếp tục hướng trên không nhìn lại, chỉ thấy lôi đình thiêu đốt xuống, Hình đường chủ nguyên thần càng ngày càng yếu nhỏ, bất cứ lúc nào cũng sẽ bị làm hao mòn sạch sẽ."),t("br"),t("br"),n._v("Hắn thực lực bản thân mặc dù rất mạnh, nhưng nguyên thần đã từng nhận qua tổn hại, lại thêm bản thân chưa chuẩn bị xong, Xuất Khiếu kiếp đánh tới, nguyên thần mạnh mẽ chống đỡ, đã có chút không chịu nổi."),t("br"),t("br"),n._v("“Tiếp tục như vậy... Hình đường chủ có thể sẽ chết...”"),t("br"),t("br"),n._v("Ngụy điện chủ cùng Liêu điện chủ tràn đầy cuống cuồng."),t("br"),t("br"),n._v("Thân vì muốn tốt cho ở chung được mấy trăm năm lão bằng hữu, hai người không cách nào trơ mắt nhìn mình bạn tốt, bị lôi đình tươi sống đánh chết."),t("br"),t("br"),n._v("“Trương sư, ngươi nhưng có phương pháp, nhất định muốn mau cứu hắn...”"),t("br"),t("br"),n._v("Hai người đồng loạt nhìn lại."),t("br"),t("br"),n._v("Nhắc tới bên trong còn có ai có thể cứu người, khả năng cũng chỉ có trước mắt vị này."),t("br"),t("br"),n._v("“Yên tâm...”"),t("br"),t("br"),n._v("Không cần hai người nói, Trương Huyền cũng sẽ động thủ, không chần chờ chút nào, Minh Lý Chi Nhãn nhúc nhích, hướng về phía trước nhìn sang."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1219"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1221"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);