(window.webpackJsonp=window.webpackJsonp||[]).push([[265],{620:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1215-vay-cong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1215-vay-cong"}},[n._v("#")]),n._v(" Chương 1215: Vây công")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Những này Vô Hồn Kim Nhân, chừng hơn trăm, đen nhánh con mắt tại dạ minh châu chiếu rọi xuống, phản xạ hồng quang, cho người ta một loại âm trầm kinh khủng cảm giác, hình như trong nháy mắt đi vào nghĩa địa, bị vô số thi thể vây quanh."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Mọi người chỉ cảm thấy đầu đồng thời nổ tung, vây tại một chỗ, nhìn bốn phía tràn đầy cảnh giác."),t("br"),t("br"),n._v("Cửa hang bị Đoạn Long Tỏa phong bế, cũng liền bày tỏ cũng lại không trốn thoát được, chỉ có thể tiếp tục hướng phía trước, mà trước mắt nhiều như vậy tượng đồng, đánh như thế nào?"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Mọi người rung động còn không có kết thúc, một đầu Vô Hồn Kim Nhân lập tức liền lao đến, thân ảnh gấp vọt, bàn tay đột nhiên đánh xuống."),t("br"),t("br"),n._v("Chưởng lực tự nhiên, chân khí tung hoành, mọi người lập tức cảm nhận được nồng đậm cảm giác áp bách."),t("br"),t("br"),n._v("“Những này Vô Hồn Kim Nhân đều là Nguyên Thần cảnh đỉnh phong, đi qua phương pháp đặc thù luyện chế, thân thể cùng chân khí không có chút nào tổn hại, mấu chốt nhất là, hung hãn không sợ chết...”"),t("br"),t("br"),n._v("Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Không tiến vào, chỉ thấy không đến Ngoan Nhân, không cách nào giải quyết triệt để tai hoạ ngầm."),t("br"),t("br"),n._v("Tiến đến, không nghĩ tới cái tên này như vậy mất trí, vậy mà bắt lấy hơn một trăm vị Thánh vực tứ trọng danh sư, cũng đem luyện chế thành tượng đồng!"),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Ngụy điện chủ tiến về phía trước một bước, một chưởng đem xông tới tượng đồng đập bay, đang nghĩ xé nát, chỉ thấy Danh Sư đường một vị trưởng lão ngăn tại trước mặt: “Vị này là chúng ta Danh Sư đường Vương trưởng lão, cùng ta là quen biết đã lâu... Có thể hay không đừng hủy hoại thi thể, để cho ta dẫn hắn trở về an táng...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngụy điện chủ đang nghĩ nói chuyện, bị hắn đập bay tượng đồng lập tức lao đến, đối với hắn va chạm, liên tục lui về phía sau mấy bước, chỉ cảm thấy ngực khó chịu, suýt chút nữa hộc máu."),t("br"),t("br"),n._v("Thân là chiến sư, cứ việc sức chiến đấu vượt xa quá đối phương, có thể cái tên này không biết đau đớn cũng không biết nguy hiểm, liền xem như hắn, đều có chút đau đầu."),t("br"),t("br"),n._v("“Không giết hắn, chúng ta liền bị giết, ngươi muốn chết ở đây, chớ liên lụy chúng ta!”"),t("br"),t("br"),n._v("Ngực khó chịu, nhìn thấy cái khác tượng đồng cũng bắt đầu động, bất cứ lúc nào cũng sẽ xông lại, Ngụy điện chủ nhịn không được hét lớn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Vị trưởng lão này vẻ mặt khó coi."),t("br"),t("br"),n._v("Chung quanh rất nhiều thi thể, hắn đều biết, thậm chí không ít đều là ở chung được mấy trăm năm hảo hữu, vốn nghĩ chí ít có thể mang theo thi thể trở về, để bọn hắn nhập thổ vi an, nhưng bây giờ loại tình huống này, khẳng định không xong rồi!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đang tràn đầy rầu rĩ, một đầu Vô Hồn Kim Nhân lao đến, năm ngón tay như câu, xé rách mà tới."),t("br"),t("br"),n._v("“Hồ trưởng lão...”"),t("br"),t("br"),n._v("Nhìn thấy người này, nhớ tới trước kia thời gian, vị trưởng lão này cảm xúc kích động, hốc mắt một đỏ, thân thể chậm nửa nhịp, lập tức bả vai bị vạch ra vết máu, máu me đầm đìa."),t("br"),t("br"),n._v("“Tăng trưởng lão, hắn đã không phải là Hồ trưởng lão, ý thức tiêu tán, đã biến thành người chết...”"),t("br"),t("br"),n._v("Thấy vị này còn đắm chìm trong trước kia bạn cũ bên trong, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Chiến Sư đường người, rèn luyện qua sinh tử, biết nguy hiểm đã tới, đổi làm thế nào, rất nhanh liền có thể vứt bỏ trước đó tình cảm, toàn lực chiến đấu."),t("br"),t("br"),n._v("Danh Sư đường người, ở phương diện này bên trên còn kém một chút, quá mức xử trí theo cảm tính."),t("br"),t("br"),n._v("“Hắn...”"),t("br"),t("br"),n._v("Vị này Tăng trưởng lão, khắp khuôn mặt là rầu rĩ, cắn răng: “Đúng!”"),t("br"),t("br"),n._v("Hít sâu một hơi, lật bàn tay một cái, nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Bành bành bành!"),t("br"),t("br"),n._v("Chung quanh Vô Hồn Kim Nhân, giờ phút này tất cả đều lao đến, đem mọi người vây quanh ở trung tâm."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Nhiều như vậy Nguyên Thần cảnh cường giả đồng thời ra tay, mọi người lập tức cảm nhận được nồng đậm áp lực, bất cứ lúc nào cũng sẽ chống lại không được."),t("br"),t("br"),n._v("“Trương sư, chúng ta bây giờ làm sao bây giờ?”"),t("br"),t("br"),n._v("Liên tục hai chưởng bổ ra xông tới hai đầu tượng đồng, Liêu điện chủ nhịn không được nói."),t("br"),t("br"),n._v("Những người này, không biết đau đớn, không biết tử vong, chỉ có đem đầu chặt đi xuống, mới sẽ không động... Hơn trăm đầu đồng thời vây công, cho dù thực lực bọn hắn tất cả đều không yếu, giờ phút này cũng bất cứ lúc nào cũng sẽ trọng thương."),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân, có người khống chế mới có thể thi triển, tìm tới khống chế người đem hắn chém giết, nguy hiểm tự nhiên giải trừ...”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Những này tượng đồng, tất nhiên có người khống chế, mới có thể như vậy công kích, chỉ cần tìm được người này, nên có thể giải trừ tai hoạ ngầm."),t("br"),t("br"),n._v("“Tốt, chúng ta trước chống đỡ, Trương sư, ngươi mau chóng tìm kiếm!”"),t("br"),t("br"),n._v("Cắn răng, Ngụy điện chủ hét lớn một tiếng: “Chư vị, bày trận, bảo hộ Trương sư, Hình đường chủ cùng Ngô sư!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, mọi người lập tức hình thành vòng tròn, đem Trương Huyền cùng Hình đường chủ, Ngô sư ba người canh giữ ở trong đó, cảnh giác nhìn xem bốn phía."),t("br"),t("br"),n._v("Trương Huyền truyền cho bọn họ trận pháp, liên hợp lại uy lực cực mạnh, cứ việc chung quanh hơn trăm đầu tượng đồng xung kích, trong thời gian ngắn, cũng xông không tiến vào."),t("br"),t("br"),n._v("“Trương sư, có cần hay không chúng ta động thủ...”"),t("br"),t("br"),n._v("Được bảo hộ ở giữa, Ngô sư mặt mũi rầu rĩ, lặng lẽ truyền âm tới."),t("br"),t("br"),n._v("Thương thế của hắn, sớm đã bị Trương sư chân khí chữa khỏi, hiện tại suy yếu, chỉ là ngụy trang mà thôi."),t("br"),t("br"),n._v("“Không nên gấp gáp, các ngươi hai cái là át chủ bài, tạm thời không thể bại lộ...”"),t("br"),t("br"),n._v("Trương Huyền truyền âm."),t("br"),t("br"),n._v("“Thế nhưng là, ta sợ bọn họ không kiên trì nổi...”"),t("br"),t("br"),n._v("“Không sao, những này Vô Hồn Kim Nhân, mặc dù lợi hại, nhưng mà muốn đem phá vỡ trận pháp, còn có chút độ khó, ta xem trước một chút, đến cùng là ai khống chế, có lẽ có thể tìm tới Thanh Điền Hoàng vị lão sư kia tung tích!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thật ra thì, nhờ vào thực lực của hắn, có thể tuỳ tiện đem cái này hơn trăm đầu tượng đồng vây công phá vỡ, sở dĩ không nhúc nhích, là muốn tìm hiểu nguồn gốc, nhìn một chút đến cùng ai khống chế đám người kia, thuận tiện đem hắn cào ra tới!"),t("br"),t("br"),n._v("Nếu như là Ngoan Nhân lời nói, kia liền càng đơn giản, tìm tới vị trí, tìm đúng thiếu hụt, sau đó một lần hàng phục."),t("br"),n._v(" Một khi thành công, Danh Sư đường tai hoạ ngầm, chẳng khác nào giải quyết triệt để."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, đồng thời thần thức lan tràn, Trương Huyền hướng những này tượng đồng nhìn sang."),t("br"),t("br"),n._v("Là dùng còn sống danh sư, luyện chế mà thành, linh hồn bị tiêu diệt, thân thể giữ lại, mỗi một cái trong ánh mắt, đều có ngọn lửa lóe lên, hẳn là một loại đặc thù ý niệm."),t("br"),t("br"),n._v("Sở dĩ có thể đối mọi người tiến công, đang nhận lấy loại này ý niệm dẫn dắt."),t("br"),t("br"),n._v("“Nhìn một chút những này ý niệm từ đâu tới đây!”"),t("br"),t("br"),n._v("Tùy tiện tìm tới một cái tượng đồng, Trương Huyền nhìn kỹ đi qua, con mắt lóe lên, hoa văn tăng nhiều, trong phòng lập tức xuất hiện một đạo dây nhỏ, tới kết nối."),t("br"),t("br"),n._v("Những giây nhỏ này như là lưới đánh cá, mỗi một cái tượng đồng, đều kết nối một cái, giống như là liên tuyến như con rối, tất cả đầu dây hội tụ cùng một chỗ, trực tiếp hướng đại sảnh chỗ sâu lan tràn."),t("br"),t("br"),n._v("“Cái tên này thật mạnh hồn lực...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt ngưng trọng."),t("br"),t("br"),n._v("Một người trên sự khống chế trăm vị tượng đồng làm ra khác biệt công kích, tiến hành khác biệt chiến đấu... Cái này cần cực mạnh hồn lực mới có thể làm đến, liền xem như hắn, đều rất khó làm được."),t("br"),t("br"),n._v("“Phía trước có trận pháp ngăn cản...” Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Tiếp tục hướng bên trong nhìn lại, sợi tơ còn chưa tới phần cuối, liền có trận pháp ngăn trở, để hắn không thấy được."),t("br"),t("br"),n._v("Không nhìn thấy phần cuối, tìm không ra Ngoan Nhân vị trí cụ thể, nhóm người mình, sẽ một mực bị động."),t("br"),t("br"),n._v("“Chư vị chịu đựng, ta tiến lên nhìn một chút...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, từ mọi người bảo hộ bên trong, bay lên, Trương Huyền dọc theo dây nhỏ tiến lên phương hướng, nhanh chóng bay đi."),t("br"),t("br"),n._v("Chỉ có cùng những này tượng đồng chiến đấu, mới có thể duy trì đối phương một mực điều khiển, nếu không, một khi đem tượng đồng toàn bộ giải quyết đi, lại không khống chế, dây nhỏ đứt gãy, cho dù muốn tìm cũng không tìm thấy."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Còn không có bay xa, ba đầu tượng đồng liền lao đến, mang theo lực lượng cuồng bạo, hướng hắn xé rách mà tới."),t("br"),t("br"),n._v("“Trương sư cẩn thận...”"),t("br"),t("br"),n._v("Liêu đường chủ vội vàng hô."),t("br"),t("br"),n._v("Lời còn chưa dứt, liền thấy trên không Trương sư, mặt đều không xoay, nhìn cũng chưa từng nhìn tượng đồng một cái, bàn chân trên không trung nhẹ nhàng đạp mạnh."),t("br"),t("br"),n._v("Lạch cạch! Lạch cạch! Lạch cạch!"),t("br"),t("br"),n._v("Ba đầu tượng đồng liền từ không trung rơi tại xuống dưới, bằng phẳng rộng rãi nằm rạp trên mặt đất, đầu lâu cũng không ngẩng lên được."),t("br"),t("br"),n._v("“Ah ah...”"),t("br"),t("br"),n._v("Liều mạng giãy dụa, tượng đồng lại tựa như bị núi lớn ngăn chặn, thủy chung không thể động đậy."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Liêu đường chủ khóe miệng giật một cái."),t("br"),t("br"),n._v("Những này tượng đồng, hung hãn không sợ chết, liền xem như hắn, muốn vượt qua cũng khó khăn, Trương sư chỉ là bàn chân nhẹ nhàng đụng một cái, liền đem nó cố định trên mặt đất... Lực lượng mạnh mẽ, chỉ sợ ba cái hắn cũng không là đối thủ!"),t("br"),t("br"),n._v("“Nửa tháng trước, ra tay với hắn, còn miễn cưỡng chạy trốn, không chống đỡ được... Mới qua ngắn như vậy thời gian, cứ như vậy lợi hại, này thiên phú...”"),t("br"),t("br"),n._v("Cười khổ lắc đầu."),t("br"),t("br"),n._v("Hơn nửa tháng phía trước, lần thứ nhất nhìn thấy vị này Trương sư, hắn cùng Hình đường chủ, Ngụy điện chủ đồng thời ra tay, suýt chút nữa đem hắn chém giết, cứ việc ý thức chiến đấu rất mạnh, lực lượng cùng công kích thực sự quá yếu, căn bản ngăn cản không nổi sự tiến công của bọn họ..."),t("br"),t("br"),n._v("Mà bây giờ, cho dù ba người bọn họ liên thủ, nhìn tới cũng không phải đối thủ!"),t("br"),t("br"),n._v("Sống mấy trăm năm, gặp qua không ít thiên tài, nhưng mà... Trước mắt vị này tốc độ tiến bộ, cho dù tận mắt nhìn thấy, đều cảm thấy không thể tưởng tượng nổi, chưa từng nghe thấy."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không biết tùy tiện triển lộ vẫy, cho Liêu điện chủ mang đến bao nhiêu rung động, một chân đạp ba đầu tượng đồng lại không tiến công, tiếp tục đi đến."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Cũng không thấy hắn có bất kỳ động tác, chỉ cần xông tới tượng đồng tất cả đều bay ngược mà ra, từng cái dán tại trên mặt đất, trên vách tường, chụp đều chụp không xuống."),t("br"),t("br"),n._v("Tu vi đạt tới Thai Anh cảnh đỉnh phong, sức chiến đấu đã có thể so với Xuất Khiếu cảnh trung kỳ, căn bản không phải những này Nguyên Thần cảnh tượng đồng có thể chống lại."),t("br"),t("br"),n._v("Đi về phía trước một hồi, đánh bay hơn hai mươi phía trước tới tiến công tượng đồng, Trương Huyền lúc này mới đi tới đại sảnh phần cuối."),t("br"),t("br"),n._v("Trong mắt tế văn, ở đây biến mất, giống như là lan tràn đến bức tường chỗ sâu, lại hình như là căn bản không tồn tại."),t("br"),t("br"),n._v("“Là cái Thiên Công cơ quan!”"),t("br"),t("br"),n._v("Nhìn một cái bức tường, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Đây không phải bình thường bức tường, mà là từ cơ quan sắp đặt mà thành, rất hiển nhiên điều khiển những này tượng đồng ý niệm gia hỏa, giấu ở đằng sau."),t("br"),t("br"),n._v("“Những này cơ quan, vây khốn người khác có thể, muốn ngăn cản ta...”"),t("br"),t("br"),n._v("Lắc đầu, bàn tay ở phía trên nhẹ nhàng sờ một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trong đầu xuất hiện sách."),t("br"),t("br"),n._v("Đầu ngón tay đụng một cái, trong đó kiến thức trở thành ký ức."),t("br"),t("br"),n._v("“Lại là thất tinh đỉnh phong Thiên Công sư bố trí... Nhìn tới đống kia tượng đồng bên trong, còn có Thiên Công sư!”"),t("br"),t("br"),n._v("Xem hết nội dung, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thanh Nguyên thành, không có Thiên Công sư tổ hội, không có nghĩa là toàn bộ Thanh Nguyên đế quốc không có."),t("br"),t("br"),n._v("Thất tinh đỉnh phong Thiên Công sư, sắp đặt đi ra cơ quan, đổi lại người bình thường, không biết cửa ải dưới tình huống, rất khó phá giải, nhưng hắn không giống, thư viện kiến thức, đi vào trong óc đã đối cái này cơ quan rõ như lòng bàn tay."),t("br"),t("br"),n._v("Chập ngón tay làm kiếm, ở phía trước liên tục điểm bảy, tám lần."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Một hồi máy lò xo vận chuyển thanh âm vang lên, ngay sau đó liền thấy trước mắt bức tường, hướng hai bên tan ra, lộ ra một cái lối đi, vừa rồi biến mất dây nhỏ, đang lại hướng bên trong lan tràn."),t("br"),t("br"),n._v("Không do dự, đi thẳng vào, Trương Huyền mới đi hai bước, lập tức cảm thấy một đạo kiếm khí, bay thẳng mi tâm, hướng về phía hắn, bỗng nhiên bắn tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1223"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1225"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);