(window.webpackJsonp=window.webpackJsonp||[]).push([[269],{624:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1219-thanh-đien-hoang-khoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1219-thanh-đien-hoang-khoc"}},[n._v("#")]),n._v(" Chương 1219: Thanh Điền Hoàng khóc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1219: Thanh Điền Hoàng khóc"),t("br"),t("br"),n._v("Một mình một cái Thanh Điền Hoàng, sẽ rất khó chiến thắng, chia ra thành hai cái, gần như không có khả năng vượt qua, cho nên Trương Huyền thận trọng từng bước, trước đem phân thân giấu ở trữ vật giới chỉ, đặt ở một chỗ... Sau đó một mực lui về phía sau, bày ra điểm yếu, đem hắn dẫn tới tính toán tốt vị trí."),t("br"),t("br"),n._v("Cuối cùng làm bộ tự bạo, phân tán hai người chú ý, này mới khiến phân thân đánh lén, một lần thành công."),t("br"),t("br"),n._v("Nghe đơn giản, trên thực tế lại dính dáng rất nhiều, dùng cái gì chiêu số, trốn bao xa, ở nơi nào dừng lại... Phân thân theo cái gì góc độ đánh lén mới không bị phát hiện..."),t("br"),t("br"),n._v("Tính toán kỹ càng, lại thêm phân thân đi qua trong khoảng thời gian này tu luyện, đơn thuần lực lượng, so bản tôn còn mạnh hơn, lấy hữu tâm tính vô tâm, lúc này mới một lần đến tay."),t("br"),t("br"),n._v("Nếu không, chỉ bằng vào chính hắn cùng Kim Nguyên đỉnh, căn bản không có khả năng chiến thắng đối phương."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Đầu nổ tung, hai cỗ thi thể, từ không trung rớt xuống, Trương Huyền lập tức nghe được một tiếng tức giận gào thét, hai đầu vu hồn, phân biệt theo trên thi thể bay ra."),t("br"),t("br"),n._v("Thanh Điền Hoàng nguyên thần!"),t("br"),t("br"),n._v("Cái tên này thân thể phân tách, nguyên thần thế mà cũng chia thành hai cái."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nguyên thần vừa xuất hiện, không gian chung quanh trở nên càng thêm cứng lại, như là lâm vào ngược dòng bên trong, đừng nói tiến lên, có thể duy trì tại nguyên chỗ đều rất khó."),t("br"),t("br"),n._v("Mất đi thân thể ràng buộc, nguyên thần càng thêm cường đại, dường như có thể phát ra lực chiến đấu mạnh hơn."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Chịu đến thu hút, hai đại nguyên thần trên không trung chậm rãi dung hợp lại cùng nhau, lần nữa tạo thành một cái."),t("br"),t("br"),n._v("Nhìn tới đối phương phân tách bí pháp, cứ việc có thể biến thành hai cái, thân thể hủy đi, nguyên thần rất khó duy trì nguyên trạng."),t("br"),t("br"),n._v("“Ta muốn ngươi chết...”"),t("br"),t("br"),n._v("Khôi phục lại, lại không còn vừa rồi ưu thế, Thanh Điền Hoàng khuôn mặt tái mét, gào lên một tiếng, lần nữa hướng Trương Huyền bắt tới."),t("br"),t("br"),n._v("Đường đường Dị Linh tộc Hoàng giả, thi triển bí pháp, làm người không ra người quỷ không ra quỷ, không những không có đem đối phương chém giết, ngược lại thân thể đều hủy, mãnh liệt buồn bực để hắn sắp nổ tung."),t("br"),t("br"),n._v("Nếu như nói trước đó, chỉ là bởi vì kế hoạch bị phá hư, thuộc hạ tử vong, muốn chém giết đối phương giữ lại mặt mũi, mà bây giờ, là không đội trời chung."),t("br"),t("br"),n._v("“Vừa rồi liền nói muốn ta chết... Ta vẫn là sống rất tốt, đừng hô, trước bắt lấy ta lại nói...”"),t("br"),t("br"),n._v("Đối mặt với đối phương uy hiếp, Trương Huyền nhẹ nhàng cười một tiếng, không những không tránh, ngược lại hướng bàn tay của đối phương vọt tới."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Thanh Điền Hoàng to lớn nguyên thần bàn tay cùng thân thể của đối phương tiếp xúc, lập tức giống như là khối sắt gặp lưu toan, phát ra “Ầm!” Bị ăn mòn thanh âm, thời gian nháy mắt, liền xuất hiện một cái động lớn."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Một tiếng hô, Thanh Điền Hoàng vội vàng rút tay về, giống như là gặp quỷ đồng dạng nhìn qua, tràn đầy không thể tưởng tượng nổi: “Ngươi, ngươi...”"),t("br"),t("br"),n._v("Vừa rồi có thân thể thời điểm, đánh đối phương dục tiên dục tử, chỉ có thể trốn tránh, hiện tại còn lại nguyên thần, dựa theo tình huống bình thường, đối phương có lẽ càng thêm e ngại mới là, làm sao... Lại biến thành như vậy, ngược lại bản thân nguyên thần đều bị hủ thực?"),t("br"),t("br"),n._v("“Cái khác Xuất Khiếu cảnh cường giả, ta chống lại không được, đối với ngươi, lại có rất nhiều phương pháp...”"),t("br"),t("br"),n._v("Không để ý tới đối phương khiếp sợ, nhẹ nhàng cười một tiếng, Trương Huyền thân thể nhoáng một cái, chân khí trong cơ thể lập tức bừng lên, đem trọn cái đại sảnh che kín."),t("br"),t("br"),n._v("Đối phó cái khác nguyên thần xuất khiếu, lấy thực lực của hắn bây giờ, chỉ có thể bị động chịu đòn, nhưng... Đối phương là vu hồn sư, lấy vu hồn năng lực thành tựu nguyên thần... Thiên Đạo chân khí đúng lúc là hắn khắc tinh!"),t("br"),t("br"),n._v("Chỉ cần đem Thiên Đạo chân khí thả ra ngoài, đối phương cho dù lực lượng mạnh mẽ, cũng không có cách nào chống lại!"),t("br"),t("br"),n._v("Thật giống như dùng chuỳ sắt, đập lưu toan, cho dù cái trước lực công kích rất lớn, nhưng chỉ cần mỗi lần dính vào một điểm, thời gian lâu dài cũng sẽ bị ăn mòn cái gì đều không còn sót lại."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Thanh Điền Hoàng giận run rẩy."),t("br"),t("br"),n._v("Giờ này khắc này, hắn rốt cuộc minh bạch, tại đối phương từng bước tính toán xuống, lúc đầu chiếm cứ rất lớn ưu thế hắn, đã đã rơi vào bị động."),t("br"),t("br"),n._v("Vẻ mặt trắng bệch, nhìn xem bốn phía di tán chân khí, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Thân là Xuất Khiếu cảnh hậu kỳ cường giả, có thể rõ ràng phát hiện, bốn phía chân khí, đúng là hắn khắc tinh, thật muốn xông đi vào, tất nhiên sẽ bị tiêu diệt, càng ngày càng yếu."),t("br"),t("br"),n._v("“Muốn ta chết, trước kéo lên ngươi đệm lưng...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, gào lên một tiếng, Thanh Điền Hoàng bàn tay lăng không bắt đến."),t("br"),t("br"),n._v("Đối phương chân khí quái dị như vậy, có thể ăn mòn nguyên thần, vậy liền liều mạng nguyên thần bị ăn mòn sạch sẽ, cũng muốn trước đem hắn chém xuống dưới ngựa!"),t("br"),t("br"),n._v("Cùng lắm thì mọi người cùng nhau chết!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Dùng ra toàn lực, Thanh Điền Hoàng uy thế cùng vừa rồi có thân thể lúc so, không hề yếu, một cái nháy mắt, Trương Huyền giống như lần nữa rơi vào đầm lầy."),t("br"),t("br"),n._v("“Bách túc chi trùng chết cũng không hàng... Không hổ là Thanh Điền Hoàng, quả nhiên lợi hại!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền toàn thân huyệt đạo mở ra, chân khí trong cơ thể tuôn ra, bị giam cầm không gian lần nữa vỡ ra đến, cũng không cần võ kỹ, một chưởng bổ tới."),t("br"),t("br"),n._v("Bất kể nói thế nào, đối phương đều là Xuất Khiếu cảnh hậu kỳ cường giả, trải qua ba lần lôi kiếp, quả nhiên không phải hắn loại thực lực này, có thể tuỳ tiện chém giết."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Chân khí tuôn ra, trên không trung hội tụ thành chưởng ấn, cùng nguyên thần của đối phương tiếp xúc, lập tức phát ra Ầm thanh âm, hình như tại thịt tươi bên trên liền tưới cút ngay dầu."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Một tiếng hô, Thanh Điền Hoàng nguyên thần lập tức nhỏ một vòng."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),n._v(" Hàm răng cắn chặt, Thanh Điền Hoàng lần nữa gào thét, biết vận dụng không gian muốn chém giết đối phương, là không thể nào, thân thể nhoáng một cái, rời khỏi xa mấy chục mét, ngón tay một chỉ, nhấn tới."),t("br"),t("br"),n._v("Hắn nguyên thần lực khống chế mạnh, đối phương chân khí cho dù muốn công kích đến hắn, khoảng cách xa, cũng lực có không bằng."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Chỉ lực hội tụ thành một đạo, tránh chi mà đến, tốc độ như là sấm sét."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Quả nhiên, lần này, Trương Huyền lại không có chặn lại, bị đánh trúng ngực bay rớt ra ngoài."),t("br"),t("br"),n._v("Bắt chước làm theo, đem phân thân cũng đánh bay."),t("br"),t("br"),n._v("Không còn Thiên Đạo chân khí, đối phương thân là Xuất Khiếu cảnh hậu kỳ cường giả, không phải vô cùng đơn giản liền có thể tùy ý nghiền sát."),t("br"),t("br"),n._v("“Không hổ là sống không biết bao nhiêu năm lão quái vật... Kinh nghiệm chiến đấu quả nhiên phong phú, liếc mắt liền nhìn ra vấn đề của ta!”"),t("br"),t("br"),n._v("Thấy cái tên này nhanh như vậy liền phát hiện phía bên mình vấn đề, hơn nữa tìm được phương pháp giải quyết, vận chuyển chân khí khôi phục thương thế, Trương Huyền ánh mắt ngưng trọng."),t("br"),t("br"),n._v("Còn tưởng rằng, có Thiên Đạo chân khí cái này máy gian lận, có thể tuỳ tiện đem đối phương chém giết, hiện tại xem ra, không dễ dàng như vậy."),t("br"),t("br"),n._v("Thiên Đạo chân khí là vu hồn khắc tinh, nhưng đối phương chỉ cần đem khoảng cách kéo ra, không có võ kỹ chống đỡ, chân khí khoảng cách quá xa, lực liên kết liền sẽ yếu bớt... Cho dù có thể đem hắn đánh bại, tiêu hao cũng sẽ mười phần to lớn, làm không cẩn thận toàn thân chân khí đều sẽ bị hao tổn sạch sẽ."),t("br"),t("br"),n._v("Bây giờ còn chưa nhìn thấy Ngoan Nhân, chân khí liền tiêu hao sạch... Có chút được không bù mất."),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("Trương Huyền có chút ưu sầu, có điều, thời gian không kịp để hắn ngẫm nghĩ, Thanh Điền Hoàng lần nữa gào thét, lại là một chỉ điểm tới."),t("br"),t("br"),n._v("Ô ô ô!"),t("br"),t("br"),n._v("Lực lượng khổng lồ xé rách không khí, tựa hồ muốn hắn xuyên thủng."),t("br"),t("br"),n._v("Thân thể nhoáng một cái tránh thoát công kích, đang nghĩ phản kích, đột nhiên linh quang chợt lóe, nghĩ tới điều gì, Trương Huyền mỉm cười, cổ tay khẽ đảo."),t("br"),t("br"),n._v("Một cái tế đàn rơi trên mặt đất."),t("br"),t("br"),n._v("Bàn tay ở phía trên nhẹ nhàng khẽ vỗ."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Phía trên trận văn lập tức bị kích hoạt, một cỗ hùng hồn hấp lực, đối không bên trong Thanh Điền Hoàng thôn phệ mà tới."),t("br"),t("br"),n._v("“Ừm? Đây là ta tại Thanh Nguyên thành bố trí tế đàn...”"),t("br"),t("br"),n._v("Bị lực lượng thu hút, Thanh Điền Hoàng nguyên thần trên không trung nhoáng một cái, nhìn một cái, lập tức nhận ra được."),t("br"),t("br"),n._v("Không phải cái khác, đúng là hắn cái kia phân thân, tại Thanh Nguyên thành bố trí tế đàn, dùng để chém giết danh sư, bắt giữ đối phương linh hồn... Không nghĩ tới bị đối phương tùy thân mang theo, hơn nữa kích hoạt dùng để đối phó hắn!"),t("br"),t("br"),n._v("Thứ này, cần Dị Linh tộc nhân sát lục chi khí mới có thể kích hoạt, hơn nữa trận pháp hết sức phức tạp, liền xem như hắn, kích hoạt một lần, đều cần tiêu tốn rất lớn đánh đổi, tiêu hao rất nhiều năng lượng... Cái tên này đến cùng làm sao làm được?"),t("br"),t("br"),n._v("“Dùng chính là mình chân khí...”"),t("br"),t("br"),n._v("Vội vàng nhìn lại, lông mày nhảy một cái."),t("br"),t("br"),n._v("Đối phương vậy mà dùng chân khí của mình, cho tế đàn làm tiếp tế... Cái này muốn nhiều hùng hậu chân khí, mới dám xa xỉ như vậy?"),t("br"),t("br"),n._v("Đang cảm thấy đối phương chưa hẳn có thể kiên trì bao lâu, liền cảm thấy hấp lực càng ngày càng mạnh, nguyên thần một hồi lay động, cũng lại không khống chế được thân hình, lập tức bị hút tới phía trên tế đàn."),t("br"),t("br"),n._v("Tế đàn dính dáng Ngoan Nhân bí pháp, đối linh hồn có cực mạnh hấp lực, nếu như thân thể vẫn còn, có lẽ còn có thể chống lại, giờ phút này, chỉ còn lại có nguyên thần, cũng lại chống lại không được."),t("br"),t("br"),n._v("Mới vừa bị hút vào đi, đang nghĩ ngợi phương pháp phá giải, liền thấy hai cái Trương Huyền, đồng thời lao đến, hướng về phía hắn một hồi loạn đánh."),t("br"),t("br"),n._v("Bàn tay, nắm đấm, bàn chân... Có thể thi triển chiêu số, thời gian nháy mắt liền toàn bộ lạc ở trên người."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Tức giận oa oa kêu loạn, Thanh Điền Hoàng gào lên một tiếng, lực lượng toàn thân bạo phát, hao tốn cực lớn đánh đổi, mới tránh thoát tế đàn thu hút, bỗng nhiên hướng đối phương chộp tới."),t("br"),t("br"),n._v("Công kích còn chưa tới đến đối phương trước mặt, hai cái Trương Huyền đồng thời vọt ra ngoài, trốn đến hơn trăm mét có hơn."),t("br"),t("br"),n._v("Cúi đầu ý định đem tế đàn hủy đi, lại phát hiện, đã bị đối phương thu vào nhẫn."),t("br"),t("br"),n._v("Sắc mặt tái xanh, muốn xông qua, đem đối phương chém giết, chỉ thấy tế đàn lại xuất hiện ở phía xa, sinh ra cường đại lực hấp dẫn."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Lần nữa bị hút tới, vẫn không có động đậy, hai vị Trương Huyền lại lao đến, lại là một hồi đổ ập xuống."),t("br"),t("br"),n._v("“Ta nhất định muốn giết ngươi...”"),t("br"),t("br"),n._v("Tức giận sắp điên rồi, tốn sức vất vả, Thanh Điền Hoàng lần nữa giãy giụa tế đàn, lại phát hiện hai người này, lại chạy tới chỗ xa hơn, đem tế đàn kích hoạt."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Lại bị hút đi qua, lần nữa một hồi điên cuồng đánh..."),t("br"),t("br"),n._v("Liên tục mấy lần, Thanh Điền Hoàng nguyên thần càng ngày càng nhỏ, cũng càng ngày càng yếu kém."),t("br"),t("br"),n._v("“Không mang theo vô sỉ như vậy...”"),t("br"),t("br"),n._v("Cảm nhận được mình tùy thời đều sẽ sụp đổ, Thanh Điền Hoàng khóc."),t("br"),t("br"),n._v("Gặp qua lợi hại danh sư, gặp qua thực lực mạnh danh sư... Cũng không có gặp qua vô sỉ như vậy..."),t("br"),t("br"),n._v("(Không cách nào não bổ, có thể tham khảo Châu Tinh Trì 《 đại nội mật thám Linh Linh Phát 》, dùng nam châm đánh tơi bời người không mặt cái kia đoạn. Mặt khác, ngày hôm nay 518, để cho ta phát một cái, cho cái chính bản đặt mua cùng nguyệt phiếu đi!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1227"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1229"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);