(window.webpackJsonp=window.webpackJsonp||[]).push([[270],{623:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1220-nghe-loi-cua-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1220-nghe-loi-cua-ta"}},[n._v("#")]),n._v(" Chương 1220: Nghe lời của ta")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1220: Nghe lời của ta"),t("br"),t("br"),n._v("“Chủ nhân... Thật là mạnh!”"),t("br"),t("br"),n._v("Cắm trên mặt đất Kim Nguyên đỉnh giãy dụa lấy đứng lên, thấy rõ ràng phía trên tình huống, nắp đỉnh nứt ra một đạo khe hở, tựa như nhếch miệng."),t("br"),t("br"),n._v("Vốn cho rằng dung hợp Kim Tinh huyền thạch, kinh nghiệm ba lần lôi kiếp, thực lực đạt tới Xuất Khiếu cảnh hậu kỳ, đã là cao thủ cao cao thủ, làm sao đều không có nghĩ đến, chủ nhân ác hơn."),t("br"),t("br"),n._v("Thủ đoạn nhiều như vậy, bảo vệ tính mạng chiêu số mạnh như vậy Thanh Điền Hoàng, đều bị làm thành như vậy, suy nghĩ một chút đều cảm thấy thê."),t("br"),t("br"),n._v("Nhìn tới, làm gì cũng không thể cùng chủ nhân đối nghịch... Lời này là đúng."),t("br"),t("br"),n._v("Nếu không, bị bán đi còn tại giúp một tay kiếm tiền."),t("br"),t("br"),n._v("“Tốt rồi!”"),t("br"),t("br"),n._v("Lại một hồi quần ẩu về sau, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Một mặt hậm hực, phân thân tràn đầy vẫn chưa thỏa mãn."),t("br"),t("br"),n._v("Ngày ngày bị giam tại trong trữ vật giới chỉ, thật vất vả có cơ hội đánh người, hắn là rất chờ mong."),t("br"),t("br"),n._v("Lúc này Thanh Điền Hoàng nguyên thần, đã yếu kém không thể lại yếu kém, tựa như lúc nào cũng sẽ bị tiêu diệt."),t("br"),t("br"),n._v("Đã trải qua phân tách bí pháp, lại bị trước mắt hai vị này Trương Huyền không ngừng chơi đùa, không chỉ nguyên thần yếu kém, tinh thần cũng đạt tới bờ biên giới chuẩn bị sụp đổ."),t("br"),t("br"),n._v("“Ta muốn... Giết ngươi...”"),t("br"),t("br"),n._v("Một mặt uể oải, Thanh Điền Hoàng trong mắt tràn đầy hận ý."),t("br"),t("br"),n._v("“Ta biết rồi, chớ khẩn trương, ta sưu hồn một chút, nhẫn một cái, nhẫn một cái, rất nhanh liền được!”"),t("br"),t("br"),n._v("An ủi một câu, Trương Huyền ngón tay một điểm, một đạo ý niệm hình thành dây nhỏ, trực tiếp hướng Thanh Điền Hoàng mi tâm chui vào."),t("br"),t("br"),n._v("“Không muốn...”"),t("br"),t("br"),n._v("Thấy hắn thi triển loại bí pháp này, Thanh Điền Hoàng trong mắt tràn đầy hoảng sợ, dọa đến muốn chạy trốn, nhưng tế đàn hấp lực thực sự quá lớn, nằm ở phía trên bò loạn, liền là trốn đi không đi ra."),t("br"),t("br"),n._v("“Phân thân, tới đè lên, trước sưu xong hồn, biết muốn biết tin tức lại nói...”"),t("br"),t("br"),n._v("Thấy hắn chạy loạn, ý niệm không chui vào lọt, Trương Huyền dặn dò."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Một mặt hưng phấn, phân thân vội vã đi tới trước mặt, một chân liền hướng về phía Thanh Điền Hoàng đầu đạp tới."),t("br"),t("br"),n._v("Đùng chít chít!"),t("br"),t("br"),n._v("Vị này Dị Linh tộc Hoàng giả, nằm ở tế đàn bên trên, biến thành một cái “Đại” chữ, tràn đầy khuất nhục, toàn thân không ngừng nhúc nhích, muốn tránh thoát, lại bất lực."),t("br"),t("br"),n._v("“Còn không nghe lời?”"),t("br"),t("br"),n._v("Lắc đầu, phân thân quay đầu nhìn một cái: “Kiếm tới!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Băng Vũ kiếm lập tức bay tới, rơi vào lòng bàn tay: “Lộn xộn nữa, có tin ta hay không một kiếm cắm trong miệng ngươi?”"),t("br"),t("br"),n._v("Thanh Điền Hoàng lập tức ngừng lại, cứng ngắc tại nguyên chỗ, động cũng không dám động, ngoan cùng con rối người đồng dạng."),t("br"),t("br"),n._v("“Cái này còn tạm được!”"),t("br"),t("br"),n._v("Thoả mãn nhẹ gật đầu, phân thân nhìn về phía Trương Huyền: “Tốt rồi, đã bất động, ngươi sưu hồn đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền đầu ngón tay dây nhỏ lập tức chui vào, đối phương mi tâm."),t("br"),t("br"),n._v("Đang nghĩ cẩn thận quan sát, thẩm tra một cái Ngoan Nhân đến cùng ở nơi nào, đột nhiên toàn bộ Địa Cung một hồi lay động, một cái thâm trầm thanh âm vang lên."),t("br"),t("br"),n._v("“Đường đường trời nhận danh sư, thế mà còn biết vu hồn chi thuật... Nếu như ta đem tin tức này tiết lộ ra ngoài, sẽ có bao nhiêu người, muốn tới đây nghiên cứu ngươi? Giết ngươi?”"),t("br"),t("br"),n._v("Thanh âm bên trong mang theo cười gằn, tựa như ở bên tai vang lên, để cho người ta tìm mơ hồ phương vị."),t("br"),t("br"),n._v("“Ngươi rốt cục chịu lộ diện...”"),t("br"),t("br"),n._v("Ngừng sưu hồn, cổ tay khẽ đảo, đem tế đàn cùng Thanh Điền Hoàng cùng một chỗ thu vào trữ vật giới chỉ, Trương Huyền đứng thẳng người."),t("br"),t("br"),n._v("Hắn biết, Ngoan Nhân khẳng định tại lân cận, vừa rồi cái gọi là sưu hồn, chỉ là vì thu hút đối phương đi ra thủ đoạn."),t("br"),t("br"),n._v("“Có thể bằng Thánh vực tam trọng đỉnh phong cảnh giới, liền đem Xuất Khiếu cảnh hậu kỳ Thanh Điền Hoàng, hoàn toàn chém giết... Không hổ là trời nhận danh sư, cùng năm đó Khổng sư đồng dạng, đối tu luyện cùng chiến đấu, có đặc thù lĩnh ngộ, vượt xa những thiên tài khác!”"),t("br"),t("br"),n._v("Ngoan Nhân cảm khái: “Bất quá... Còn quá trẻ, ta muốn giết ngươi, dễ như trở bàn tay!”"),t("br"),t("br"),n._v("Nói hết, Trương Huyền lập tức cảm thấy một hồi kịch liệt lay động, trong đầu trời đất quay cuồng, bản thân vu hồn giống như là bị cái gì xé rách, bất cứ lúc nào cũng sẽ thoát ly thân thể."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Khuôn mặt trầm xuống."),t("br"),t("br"),n._v("Loại cảm giác này cùng ban đầu ở Trần Triết hậu viện đồng dạng, linh hồn không khỏi khống chế, lập tức liền sẽ bị đối phương thôn phệ."),t("br"),t("br"),n._v("Thiên Đạo vận chuyển chân khí, Trương Huyền chống cự lại to lớn hấp lực, có điều, lực lượng thực sự quá lớn, trong chớp mắt, trước mắt giống như là lần nữa nhiều hơn một cái đen kịt lỗ đen, muốn đem hắn thôn phệ đi vào."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Vu hồn bị rút ra xuất thân thể, đi tới tế đàn trước mặt."),t("br"),t("br"),n._v("“Cái này... Thật là lớn vu hồn!”"),t("br"),t("br"),n._v("Ngoan Nhân sững sờ, dường như cũng giật nảy mình."),t("br"),t("br"),n._v("Hắn tinh thông vu hồn bí thuật, vu hồn cũng rất cường đại, nằm mơ đều không có nghĩ đến, trước mắt cái này càng lớn, đã vượt ra khỏi có thể lý giải phạm trù."),t("br"),t("br"),n._v("“Ngươi hẳn phải biết, ta có thể nhẹ nhõm giết ngươi năng lực đi!”"),t("br"),t("br"),n._v("Khiếp sợ chỉ là một cái chớp mắt, rất nhanh khôi phục lại, Ngoan Nhân một đôi mắt, theo lỗ đen chậm rãi bay ra, an tĩnh nhìn lại."),t("br"),t("br"),n._v("“Quả thực có chém giết ta năng lực...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Biết liền tốt!” Ngoan Nhân cười cười: “Có thể giết ngươi, lại không động thủ, ngươi có lẽ rõ ràng... Ngươi đối ta còn có giá trị lợi dụng!”"),t("br"),t("br"),n._v("“Ngươi muốn cho ta thả ngươi ra ngoài?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),n._v(" “Nói chuyện với người thông minh, liền là đơn giản... Chỉ cần có thể giúp ta thoát vây, có thể không giết ngươi!” Ngoan Nhân nói."),t("br"),t("br"),n._v("“Không giết ta? Có phải hay không một khi thoát vây rời khỏi, còn là sẽ đem ta luyện chế thành tượng đồng hoặc là khôi lỗi?” Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Chỉ có khôi lỗi hoặc là tượng đồng, mới sẽ không nhiều lời nói nhảm!”"),t("br"),t("br"),n._v("Cũng không phủ nhận, Ngoan Nhân thản nhiên nói."),t("br"),t("br"),n._v("“Đã như vậy, dù sao cũng là một lần chết, ta vì sao muốn thả ngươi đi ra?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Dù sao đối phương đều muốn giết bản thân, làm gì còn làm phiền tâm lao động, đem hắn cứu ra?"),t("br"),t("br"),n._v("“Cái này không thể tuỳ theo ngươi, nếu như ngươi muốn tận mắt nhìn thấy, cùng ngươi tới một đám danh sư, tất cả đều chết ở chỗ này... Có thể không cần đáp ứng!”"),t("br"),t("br"),n._v("Ngoan Nhân hừ lạnh vang lên, ngay sau đó lỗ đen nhoáng một cái, hiện lên một hình ảnh."),t("br"),t("br"),n._v("Chính là Hình đường chủ đám người."),t("br"),t("br"),n._v("Lúc này mọi người, vẫn như cũ bị vô số Vô Hồn Kim Nhân vây khốn, thủ hộ ở bên ngoài Ngụy điện chủ, Liêu điện chủ đám người, đã bị thương không nhẹ, kiệt sức."),t("br"),t("br"),n._v("Mặc dù mọi người thực lực không yếu, nhưng đối mặt nhiều như vậy không biết đau đớn, không biết mệt nhọc Vô Hồn Kim Nhân, vẫn như cũ chống lại không được."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mọi người vốn là không kiên trì nổi, trong phòng, không biết cái gì cơ quan mở ra, lại một thân ảnh chậm rãi đi ra."),t("br"),t("br"),n._v("Đồng dạng là cái Vô Hồn Kim Nhân, chẳng qua cấp bậc cùng cái khác so, phải cường đại quá nhiều, đã đạt đến, Xuất Khiếu cảnh trung kỳ, chân khí theo huyệt đạo bắn ra, mang theo nồng đậm sát khí."),t("br"),t("br"),n._v("“Những này tượng đồng, vốn là không chịu nổi, nếu như ta lại ra tay, ngươi cảm thấy đám người này, có thể sống sót mấy cái?”"),t("br"),t("br"),n._v("Thấy người này xuất hiện, con mắt nhìn Trương Huyền một cái, Ngoan Nhân cười lạnh."),t("br"),t("br"),n._v("Ngay sau đó, hình ảnh bên trong đi ra Vô Hồn Kim Nhân, thân thể chợt lóe, xông vào đám người."),t("br"),t("br"),n._v("“Mọi người phòng ngự tốt...”"),t("br"),t("br"),n._v("Ngụy điện chủ biến sắc, vội vàng hét lớn, có điều, lời còn chưa dứt, đối phương đã đi tới trước mặt, bàn tay đối với hắn ngực bao phủ xuống."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, Ngụy điện chủ bay rớt ra ngoài, tầng tầng ngã tại trên vách tường, từng ngụm từng ngụm hộc máu."),t("br"),t("br"),n._v("Vị trí của hắn xuất hiện trống chỗ, trận pháp lập tức xuất hiện sơ hở, mọi người còn chưa kịp lần nữa điều chỉnh, chỉ thấy cái tên này, một cái chui vào đám người, đối với chiến đấu lực mạnh thứ hai Liêu điện chủ đánh ra."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, Liêu điện chủ thực lực, toàn lực bạo phát, có thể ngăn cản mấy chiêu... Nhưng chiến đấu thời gian dài như vậy, chân khí cùng thể lực, đều tiêu hao không sai biệt lắm, đồng dạng không có kịp phản ứng, liền bị đánh trúng, nằm rạp trên mặt đất, khuôn mặt trắng bệch."),t("br"),t("br"),n._v("Một cái nháy mắt, cùng Trương Huyền cùng đi mấy người, liền hai cái bị thương."),t("br"),t("br"),n._v("“Đáng ghét! Ta muốn ngươi chết!”"),t("br"),t("br"),n._v("Thấy liên tục hai vị đồng bạn trọng thương, Trương Cửu Tiêu gào thét, trước đó trung phẩm Thánh khí, xuất hiện tại lòng bàn tay, bỗng nhiên đối trước mắt tượng đồng bổ tới."),t("br"),t("br"),n._v("Dường như sớm có phòng bị, tượng đồng ngón tay một điểm."),t("br"),t("br"),n._v("“Ông!” Một cái, trường kiếm trên không trung ngừng lại."),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Trương Cửu Tiêu đã dùng hết toàn lực, đều không thể động đậy, hình như bị nhựa cao su đính vào tại chỗ."),t("br"),t("br"),n._v("Hắn bất quá là Nguyên Thần cảnh sơ kỳ cường giả, cho dù sức chiến đấu kinh người, cùng Xuất Khiếu cảnh trung kỳ so, cũng kém thực sự quá nhiều."),t("br"),t("br"),n._v("Đem hắn định trụ, Vô Hồn Kim Nhân, cười lạnh, đi tới trước mặt, nhẹ nhàng nhấn một cái."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Trương Cửu Tiêu đồng dạng bay ngược mà ra, có điều, người giữa không trung, ngực một đạo quang mang lóe lên, dường như cái gì hộ thân phù loại hình đồ vật kích hoạt, ngừng lại."),t("br"),t("br"),n._v("Thân là con em đại gia tộc, bảo vệ tính mạng pháp bảo vẫn có một ít."),t("br"),t("br"),n._v("“Thú vị... Bất quá, thứ này muốn ngăn trở ta, không có cửa đâu!”"),t("br"),t("br"),n._v("Thấy cảnh này, Ngoan Nhân cười lạnh."),t("br"),t("br"),n._v("Tại hắn khống chế xuống, Vô Hồn Kim Nhân đầu gối uốn lượn, bỗng nhiên đạp mạnh, lần nữa vọt tới, năm ngón tay như là đánh đàn, từng đạo công kích tự nhiên mà xuống."),t("br"),t("br"),n._v("Coong coong coong coong!"),t("br"),t("br"),n._v("Hộ thân phù bảo hộ lấy Trương Cửu Tiêu, để hắn không bị thương tổn, có điều, nương theo công kích càng ngày càng nhiều, hộ thể ánh sáng cũng liền càng ngày càng yếu."),t("br"),t("br"),n._v("Lợi hại hơn nữa hộ thân phù, cũng có năng lượng hạn chế, có thể chặn lại một, hai lần công kích ngược lại cũng thôi, nhiều lần, cũng không kiên trì nổi."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Quang mang biến mất, Trương Cửu Tiêu giống như những người khác, đồng dạng bị đánh bay, trọng thương nằm tại cách đó không xa."),t("br"),t("br"),n._v("Đánh bại ba người này, còn lại liền đơn giản, rất nhanh Ngô sư mang tới ba vị trưởng lão, cũng bị kích thương, loại trừ vốn là trọng thương Ngô sư cùng Hình đường chủ, có thể nói đã toàn quân bị diệt."),t("br"),t("br"),n._v("Vô Hồn Kim Nhân nhếch môi, đi tới Hình đường chủ đám người trước mặt, bàn tay nâng lên, bất cứ lúc nào cũng sẽ vỗ xuống."),t("br"),t("br"),n._v("“Thế nào? Nghe lời của ta, ta có thể tha bọn họ! Thân là trời nhận danh sư, cũng không thể tận mắt thấy bằng hữu của ngươi, từng cái chết tại trước mặt đi!”"),t("br"),t("br"),n._v("Thấy hai cái này bản thân liền trọng thương, khẳng định không phản kháng được, Ngoan Nhân nhìn chăm chú về phía trước mắt Trương Huyền, mang theo nồng đậm khiêu khích."),t("br"),t("br"),n._v("Danh sư, chú ý quy tắc, đạo nghĩa, nếu như tận mắt thấy bằng hữu chết tại trước mặt, bản thân rõ ràng có biện pháp, nhưng không nghĩ đi giúp, tất nhiên sẽ tâm niệm sụp đổ, sống sót cũng sẽ buồn bã đau lòng, về sau lại khó tiến bộ."),t("br"),t("br"),n._v("“Chết tại trước mặt?”"),t("br"),t("br"),n._v("Không để ý tới thái độ của hắn, Trương Huyền lắc đầu: “Ta không như thế cảm giác!”"),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Thấy cái tên này đến bây giờ đều mạnh miệng, đang nghĩ nói chuyện, chỉ thấy trong hình phong cách đột nhiên xoay một cái, mới vừa rồi còn trọng thương Ngô sư cùng Hình đường chủ, bỗng nhiên nổi lên, đồng thời ra tay, một cái nháy mắt, hai cổ hung mãnh lực lượng, đánh vào tượng đồng trên thân."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Không nghĩ tới trọng thương người có thể bộc phát ra to lớn như vậy công kích, Vô Hồn Kim Nhân còn không có kịp phản ứng, ngực liền bị tại chỗ bị đánh ra một cái động lớn, xương sườn toàn bộ đứt gãy."),t("br"),t("br"),n._v("Tràn đầy khiếp sợ, không biết chuyện gì xảy ra, Ngoan Nhân nghe được Trương Huyền thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Thế nào? Nghe lời của ta, ta có thể tha cái này tượng đồng! Thân là vây khốn Khổng sư tồn tại, cũng không thể tận mắt thấy thuộc hạ của ngươi, chết tại trước mặt đi!”"),t("br"),t("br"),n._v("“...” Ngoan Nhân."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1228"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1230"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);