(window.webpackJsonp=window.webpackJsonp||[]).push([[276],{632:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1225-hiem-thay-thay-tro"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1225-hiem-thay-thay-tro"}},[n._v("#")]),n._v(" Chương 1225: Hiếm thấy thầy trò")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1225: Hiếm thấy thầy trò"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cảm nhận được áp bức, tất cả mọi người thân thể cứng ngắc."),t("br"),t("br"),n._v("Trước mắt cái này đầu người thực sự quá lớn, lực lượng càng là vô cùng vô tận, cứ việc không có động thủ, toả ra khí thế, đã phong tỏa không gian, muốn chạy trốn, đều không thể làm được."),t("br"),t("br"),n._v("“Đây là... Xuất Khiếu cảnh đỉnh phong!”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Ngô sư nói."),t("br"),t("br"),n._v("Lấy thực lực của hắn, có thể nhẹ nhõm nhìn ra, đối phương đã đạt đến Xuất Khiếu cảnh đỉnh phong, loại lực lượng này, không phải bọn họ có thể chống lại!"),t("br"),t("br"),n._v("“Quả thực là Xuất Khiếu cảnh đỉnh phong...” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Cùng trước đó tại Thanh Nguyên thành lúc đoán đồng dạng, đầu này Ngoan Nhân, quả thực đã đạt đến Xuất Khiếu cảnh đỉnh phong, thực lực so Thanh Điền Hoàng, mạnh mẽ gấp bội không thôi."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Vội vàng nhìn tới, mọi người tràn đầy lo lắng."),t("br"),t("br"),n._v("Đổi lại cái khác thực lực, Xuất Khiếu cảnh hậu kỳ, cố gắng một cái, đều có thể một trận chiến, trước mắt loại này Xuất Khiếu cảnh đỉnh phong cường giả, mấu chốt cái đầu, còn như thế lớn... Đánh như thế nào?"),t("br"),t("br"),n._v("Đánh như thế nào đều là thua ah!"),t("br"),t("br"),n._v("“Trương Huyền, ngươi rất thông minh, có thể nghĩ ra lấy trận phá trận thủ pháp phá hư tế đàn... Thủ đoạn rất là cao minh... Bất quá, chính là bởi vì ngươi cao minh, mới để cho ta thoát ly ràng buộc, ha ha! Thế nào? Vẫn muốn giết ta, lại đem ta tự mình thả ra, chỉ có thể bị động chạy trốn, có phải hay không cảm thấy rất hối hận?”"),t("br"),t("br"),n._v("Ổn định lơ lửng giữa không trung, mặt người càng ngày càng rõ ràng, lạnh lùng nhìn lại, trong mắt mang theo nghiền ngẫm vui vẻ."),t("br"),t("br"),n._v("Không gian chung quanh đã bị hắn giam cầm, những người này cho dù muốn chạy trốn, cũng chạy trốn không xong, có thể nói đã là cá trong chậu, không có gì có thể sợ hãi."),t("br"),t("br"),n._v("“Là rất hối hận... Bất quá, đều đã như vậy, hối hận cũng vô dụng, ta chỉ là có chút tò mò, cái kia tế đàn, đến cùng là ai làm đi ra, sao có thể đưa ngươi vây khốn?”"),t("br"),t("br"),n._v("Mặt mũi hối hận, Trương Huyền cắn răng, nhịn không được nhìn qua."),t("br"),t("br"),n._v("Chỉ là hắn một mực không hiểu."),t("br"),t("br"),n._v("Tế đàn vừa nhìn liền biết là Dị Linh tộc nhân đồ vật, vì sao muốn vây khốn đối phương?"),t("br"),t("br"),n._v("“Dù sao, ngươi lập tức liền bị thôn phệ, giải thích với ngươi một cái, cũng không sao...”"),t("br"),t("br"),n._v("Cười lạnh, lơ lửng đầu người miệng mở ra, thanh âm tựa như sấm rền: “Thứ này là Thanh Điền Hoàng tên kia làm!”"),t("br"),t("br"),n._v("“Thanh Điền Hoàng?” Trương Huyền không hiểu, tràn đầy kinh ngạc: “Hắn... Không phải học sinh của ngươi, không phải cùng ngươi học tập vu hồn chi thuật ư?”"),t("br"),t("br"),n._v("Học sinh của mình, làm sao làm cái tế đàn, đem lão sư khóa lại?"),t("br"),t("br"),n._v("“Hừ, hơn hai ngàn năm trước, ta đem hắn triệu hoán tới, thời điểm đó ta, chỉ là một đôi vỡ vụn con mắt, cùng một đoạn suy nghĩ...”"),t("br"),t("br"),n._v("Ngoan Nhân hừ lạnh: “Ta truyền thụ cho hắn vu hồn chi thuật, mục đích rất đơn giản, là muốn mượn lực lượng của hắn, giúp ta tìm kiếm những bộ phận khác, khôi phục thực lực!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Điểm ấy không cần đoán, cũng có thể nghĩ đến."),t("br"),t("br"),n._v("Có thể bị xưng là Ngoan Nhân, tự nhiên là nhân vật hung ác, làm sao có thể người hiền lành đồng dạng, thu cái học sinh, miễn phí truyền thụ đã thất truyền vu hồn chi pháp?"),t("br"),t("br"),n._v("Khẳng định có mục đích của mình."),t("br"),t("br"),n._v("“Cái tên này quả thực rất để tâm, đem ta xem như lão sư đối đãi... Thậm chí, bắt không biết bao nhiêu thiên công sư cùng luyện khí sư, sưu tập vô số bảo vật, giúp ta rèn đúc cái này đầu lâu!”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("“Thiên công sư rèn đúc?”"),t("br"),t("br"),n._v("Trương Huyền nhớ tới dưới đất nhìn thấy những lít nha lít nhít đó gian phòng."),t("br"),t("br"),n._v("Trước đó không biết lấy làm gì, hiện tại xem ra, có lẽ chính là trong đầu những lỗ khảm đó."),t("br"),t("br"),n._v("Rèn đúc như thế lớn đầu lâu, không biết hao tốn bao nhiêu bảo vật, tiêu hao bao nhiêu nhân lực, nhìn tới hai ngàn năm đến, chết tại danh sư không ít, thiên công sư cùng luyện khí sư số lượng càng nhiều!"),t("br"),t("br"),n._v("Chỉ bất quá, phần lớn đều là bị Qủy quật rèn luyện tâm cảnh thu hút, lại thêm thủ đoạn mịt mờ, không có bị phát hiện thôi."),t("br"),t("br"),n._v("“Bất quá, cái tên này ngay từ đầu đối ta lễ độ cung kính, ta vẫn cho là, là bởi vì trung thành, không nghĩ tới... Thế mà muốn tính kế ta!”"),t("br"),t("br"),n._v("Ngoan Nhân thanh âm bên trong, lộ ra phẫn hận: “Không biết lúc nào, tìm tới tế đàn, bên ngoài là vì để cho ta mau chóng khôi phục lực lượng, trên thực tế phối hợp thiên công sư chế tạo đầu lâu... Cứ thế mà đem ta vây khốn! Không thể chạy trốn, chỉ có thể cầm tù ở đây...”"),t("br"),t("br"),n._v("“Cầm tù?”"),t("br"),t("br"),n._v("“Không sai, hắn không chỉ muốn cầm tù! Thật ra thì, nghe theo ta, học tập vu hồn mục đích, cũng rất đơn giản, ngay từ đầu liền đánh lấy, đoạt xá ta ký ức, muốn lấy mà thay vào ý định...”"),t("br"),t("br"),n._v("Ngoan Nhân cười lạnh."),t("br"),t("br"),n._v("Thanh Điền Hoàng, dị linh nhất tộc Hoàng giả, làm sao có thể nhẹ nhõm khuất phục cùng người, dù là đối phương là Ngoan Nhân Đại Đế."),t("br"),t("br"),n._v("“Đoạt xá... Giành lấy...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền than thở một tiếng, một mặt thương hại nhìn qua: “Ngươi một cái sống mấy vạn năm lão quái vật, từng cùng Khổng sư chiến đấu qua... Thế mà bị nho nhỏ Thanh Điền Hoàng thiết kế?”"),t("br"),t("br"),n._v("Bất kể nói thế nào, trước mắt vị này, đều là đã từng cùng Khổng sư chiến đấu tồn tại."),t("br"),t("br"),n._v("Thực lực mạnh mẽ không nói, thủ đoạn bảo mệnh, cũng tầng tầng lớp lớp."),t("br"),t("br"),n._v("Bị một cái liền hắn đều chơi không lại Thanh Điền Hoàng biến thành như vậy... Quả thực không thể tưởng tượng nổi."),t("br"),t("br"),n._v("“Ta đã sớm biết ý nghĩ của hắn, có điều, hơn hai ngàn năm trước, ta mới vừa chưa tỉnh lại, thực lực liền Thánh vực đều không có, căn bản chống lại không được! Nhất định phải mượn nhờ lực lượng của hắn cùng tài nguyên...”"),t("br"),t("br"),n._v("Hừ một tiếng, Ngoan Nhân mắt sáng lên: “Bất quá, hắn muốn giết ta, ta lại là làm sao có thể dễ dàng như vậy bị giết! Ta tại đầu óc hắn lặng lẽ lưu lại một đạo ý niệm, một khi gây bất lợi cho ta, liền có thể để hắn hình thần câu diệt!”"),t("br"),n._v(" “Cái này...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thật đúng là một đôi tốt thầy trò, ngươi phòng bị ta, ta phòng bị ngươi, ngươi muốn giết chết ta, ta cũng muốn giết chết ngươi..."),t("br"),t("br"),n._v("Vô gian đạo chơi rất trượt ah!"),t("br"),t("br"),n._v("“Đầu lâu chế tạo xong, ta muốn phục sinh, nắm giữ sức chiến đấu, nhất định phải đem ý niệm toả ra đến cả hòn đảo nhỏ... Lời như vậy, sẽ rơi vào ngắn ngủi hôn mê... Chính là thừa dịp lúc này, hắn sử dụng tế đàn, lặng lẽ xuống tay với ta...”"),t("br"),t("br"),n._v("Nghĩ đến chuyện này, Ngoan Nhân dường như còn là lên cơn giận dữ: “Ngay tại hắn muốn giết ta thời điểm, ta ở lại trong đầu hắn ý niệm có tác dụng... Hắn không muốn chết, tự nhiên muốn lần nữa nghe lời của ta, có điều, đối phương cũng có hậu thủ, ta cũng không dám thật đem hắn chém giết, cho nên, chỉ có thể như vậy giằng co...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngô sư, Hình đường chủ đám người hai mặt nhìn nhau."),t("br"),t("br"),n._v("Thầy trò bối phận mười phần quan trọng, tại danh sư đại lục, một khi phát hiện có học sinh khi sư diệt tổ, không có bất kỳ cái gì nhiều lời, trực tiếp giết chết."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, danh sư đại lục tất cả mọi người, bao quát bình thường tu luyện giả, đối “Sư” hết sức kính trọng."),t("br"),t("br"),n._v("Thiên địa sư quân thân thiết!"),t("br"),t("br"),n._v("Địa vị chỉ ở ngày, dưới mặt đất, thậm chí còn xếp tại Quân Vương, song thân phía trên!"),t("br"),t("br"),n._v("Như vậy thân phận, hầu như toàn bộ đại lục, đều rất ít xuất hiện, có học sinh thiết kế lão sư chuyện... Đôi này Dị Linh tộc nhân, lão sư ngày ngày nhớ sử dụng học sinh, học sinh ngày ngày nhớ làm sao đoạt xá lão sư..."),t("br"),t("br"),n._v("Thật là hiếm thấy!"),t("br"),t("br"),n._v("“Dị Linh tộc nhân chỉ thuần phục Linh Thần, đối ‘Sư’, cũng không thèm để ý...”"),t("br"),t("br"),n._v("Thấy bọn họ không hiểu, Trương Huyền giải thích."),t("br"),t("br"),n._v("Hắn xem như gặp qua không ít Dị Linh tộc nhân, cùng rất nhiều người đã từng quen biết, thậm chí toàn bộ Thanh Điền nhất mạch, theo “Hoàng” đến “Vương” bị diệt không còn một mống, đối với mấy cái này gia hỏa tập tính, biết rất nhiều."),t("br"),t("br"),n._v("Bọn họ chỉ thuần phục Linh Thần, nghe theo đối phương mệnh lệnh, cấp cho đối ngang hiến tế, cái gọi là “Sư”... Căn bản không thèm để ý."),t("br"),t("br"),n._v("Đừng nói, chỉ là thiết kế lão sư, cho dù giết, trong mắt bọn hắn, đều rất bình thường, không tính là cái gì đại nghịch bất đạo."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Thanh Điền Hoàng cùng cái này Ngoan Nhân, cứ việc ở chung được hơn hai nghìn năm, trên thực tế lại mỗi người đều có mục đích riêng phải đạt được, âm thầm đọ sức."),t("br"),t("br"),n._v("Nếu không, lấy Ngoan Nhân nhãn lực cùng kiến thức, lại thêm một vị Hoàng giả, làm sao có thể hơn hai nghìn năm đi qua, vẫn như cũ bị vây ở chỗ này?"),t("br"),t("br"),n._v("Thậm chí, Trương Huyền đều có chút hoài nghi, Ngô Dương Tử trong cung điện dưới lòng đất Ngoan Nhân, sở dĩ có thể nắm giữ ý thức, đều cùng Thanh Điền Hoàng liên quan đến."),t("br"),t("br"),n._v("“Có thể để cho đường đường Ngoan Nhân, đều sợ ném chuột vỡ bình, không biết... Vị này Thanh Điền Hoàng hậu chiêu đến cùng là cái gì?”"),t("br"),t("br"),n._v("Giải thích xong, Trương Huyền ngẩng đầu tiếp tục hỏi."),t("br"),t("br"),n._v("Đến cùng là gì gì đó đồ vật, có thể để cho vị này Ngoan Nhân, tại đối phương trong óc lưu lại ý niệm loại này to lớn ưu thế bên dưới, cũng không dám động thủ?"),t("br"),t("br"),n._v("“Là liên quan tới truyền thế thiên phù tin tức!”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("Nhìn tới, bị vây không biết bao nhiêu năm, hắn cũng bị nhịn gần chết, đối mặt trước mắt những này tùy thời có thể lấy chém giết danh sư, có nói ra được cơ hội."),t("br"),t("br"),n._v("“Truyền thế thiên phù?”"),t("br"),t("br"),n._v("Trương Huyền có sưu hồn Thanh Điền Hoàng kinh nghiệm, đã sớm biết tin tức này, không có quá lớn ngoài ý muốn."),t("br"),t("br"),n._v("Ngô sư đám người lại con ngươi co rụt lại."),t("br"),t("br"),n._v("Không để ý tới mọi người khiếp sợ, Ngoan Nhân hừ một tiếng, tiếp tục nói: “Là một cái hướng hắn hiệu trung khôi lỗi, tra ra cái này tin tức, các ngươi thân là danh sư, hẳn phải biết, thứ này trân quý!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Truyền thế thiên phù, là Khổng sư tự mình lưu lại, liên lụy đến Khổng sư tự mình sáng lập ra bí tịch , giá trị bao lớn, không chỉ có thể để bình thường danh sư điên cuồng, cho dù Danh Sư đường tổng bộ cửu tinh danh sư, biết tin tức, đều vô cùng có khả năng kìm nén không được."),t("br"),t("br"),n._v("“Nếu như ta có thể được đến thiên phù, liền có thể thoát ly loại người này không nhân quỷ không quỷ trạng thái, nhẹ nhõm tìm tới bản thân rải rác bộ phận, khôi phục đỉnh phong thực lực! Thậm chí còn có thể cướp đoạt Xuân Thu đại điển, vượt qua Khổng sư...”"),t("br"),t("br"),n._v("Con mắt thật to, không ngừng lóe lên, Ngoan Nhân tràn đầy hưng phấn."),t("br"),t("br"),n._v("Bình thường Dị Linh tộc nhân, đạt được truyền thế thiên phù, khả năng cũng sẽ không dùng, nhưng trước mắt vị này Ngoan Nhân, cùng Khổng sư chiến đấu không biết bao nhiêu lần, tất nhiên biết phương pháp sử dụng, đem hắn công hiệu phát huy đến lớn nhất."),t("br"),t("br"),n._v("“Ta tình huống hiện tại không cách nào rời khỏi, muốn có được truyền thế thiên phù, chỉ có thể dựa vào hắn... Cho nên, chỉ có thể giữ lại tính mạng của hắn! Hơn nữa, hắn còn biết ta một bộ phận phân thân vị trí, giết, muốn dung hợp, liền khó khăn!” Ngoan Nhân nói tiếp."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Truyền thế thiên phù lực hấp dẫn thực sự quá lớn, lại thêm khôi phục thực lực khát vọng, để hắn không dám đem là chém giết, cũng rất bình thường."),t("br"),t("br"),n._v("“Ngươi nói... Một cái hướng Thanh Điền Hoàng hiệu trung khôi lỗi nói... Có thể tra ra tin tức này, thậm chí liền Điền Thanh phó đường chủ cũng không biết... Vị này khôi lỗi địa vị cũng không thấp đi!”"),t("br"),t("br"),n._v("Trong lòng hơi động, Trương Huyền nghe được hắn trong lời nói vấn đề, hỏi lần nữa."),t("br"),t("br"),n._v("Điền Thanh phó đường chủ, là Thanh Điền Hoàng bản nhân ý niệm, không tính là khôi lỗi."),t("br"),t("br"),n._v("Trung Thanh Vương, chỉ có thể coi là được chó săn, tự nhiên càng không tính là, cái này khôi lỗi, có thể có được truyền thế thiên phù tin tức, địa vị tất nhiên không thấp... Có thể sẽ là ai?"),t("br"),t("br"),n._v("Vì cái gì Thanh Điền Hoàng ký ức bên trong, không có ghi chép?"),t("br"),t("br"),n._v("“Không cần lôi kéo ta lời nói, vị này đã hiệu trung với ta...”"),t("br"),t("br"),n._v("Ngoan Nhân cười lạnh, dường như đem cái kia giải thích giải thích xong, không muốn nhiều lời, ánh mắt phát lạnh, một đạo sát khí trực tiếp đối mọi người lan tràn mà tới: “Tốt rồi, biết nhiều như vậy bí mật, các ngươi có thể lên đường!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một đạo lực lượng hùng hồn, bỗng nhiên nghiêng ép mà xuống, mọi người tất cả đều hô hấp dồn dập, cảm giác ngực sắp nổ tung."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1233"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1235"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);