(window.webpackJsonp=window.webpackJsonp||[]).push([[277],{631:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1226-luu-duong-nguy-co-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1226-luu-duong-nguy-co-thuong"}},[n._v("#")]),n._v(" Chương 1226: Lưu Dương nguy cơ (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1226: Lưu Dương nguy cơ (thượng)"),t("br"),t("br"),n._v("“Trương sư, chúng ta thay ngươi khiêng, ngươi nghĩ biện pháp chạy trốn...”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, Ngô sư, Hình đường chủ chân khí trong cơ thể điên cuồng tuôn ra, ngăn tại lan tràn mà đến lực lượng, đem mọi người bảo hộ ở trung tâm."),t("br"),t("br"),n._v("Người trước mắt mặt thực sự quá lớn, thật là đáng sợ, bọn họ những người này khẳng định là trốn không thoát, nhưng... Dùng hết tính mạng lời nói, có lẽ có thể để cho Trương sư rời khỏi!"),t("br"),t("br"),n._v("Hắn là nhân tộc tương lai, không thể chết ở đây!"),t("br"),t("br"),n._v("“Trốn? Nếu như hòn đảo hoàn toàn lơ lửng phía trước, xoay người rời đi lời nói, có lẽ còn có thể trốn được, hiện tại...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Hắn nói nhiều như vậy, các ngươi tưởng là thật sự là lắm lời? Tìm thổ lộ hết đối tượng?”"),t("br"),t("br"),n._v("“Cái này...” Mọi người sững sờ."),t("br"),t("br"),n._v("Đối phương giải thích, dính dáng bí mật quá nhiều, quá mức kinh thế hãi tục, để mọi người trong lúc nhất thời quên chạy trốn, hiện tại xem ra, chỉ sợ là vì kéo dài thời gian."),t("br"),t("br"),n._v("“Ý niệm của hắn mới vừa từ trấn áp bên trong trốn ra được, muốn thống ngự to lớn như vậy hòn đảo, cần thời gian nhất định! Cho nên, dùng tin tức thu hút chú ý của chúng ta, trong khoảng thời gian này, đã hoàn toàn nắm trong tay đầu lâu, cho dù có thể lao ra, to lớn như vậy đầm lầy... Chúng ta cũng trốn không thoát, làm không cẩn thận, còn có thể bởi vì cuống cuồng, rơi vào địa phương nguy hiểm, tự tìm đường chết!” Trương Huyền cười khổ."),t("br"),t("br"),n._v("Đối phương loại thực lực này, cho dù đem Ngô sư, Hình đường chủ đám người toàn bộ liều chết, cũng chậm trễ không được bao lâu, cho dù trốn, lại có thể trốn bao xa?"),t("br"),t("br"),n._v("Chỉ sợ liền hơn mười cây số, cũng không đi ra, liền sẽ bị đuổi kịp."),t("br"),t("br"),n._v("“Trương sư biết loại tình huống này, vừa rồi vì cái gì không trốn...” Quý Thiên Hùng có chút nhịn không được."),t("br"),t("br"),n._v("Đã ngươi trước thời hạn liền nhìn ra mục đích của đối phương, vì cái gì không trốn đi, còn muốn ở đây một mực hỏi thăm? Bây giờ đối phương đã hoàn toàn nắm trong tay hòn đảo, thực lực đạt đến đỉnh phong, lại nghĩ chạy trốn, đã không thể nào."),t("br"),t("br"),n._v("Không có trả lời hắn, Trương Huyền tiến về phía trước một bước, ngẩng đầu nhìn về phía người trước mắt mặt."),t("br"),t("br"),n._v("“Chúng ta khẳng định trốn không thoát, đối thực lực ngươi bây giờ cũng chống lại không được... Hiện tại chỉ muốn biết, ngươi nói cái kia khôi lỗi đến cùng là ai?”"),t("br"),t("br"),n._v("“Khặc khặc, nói thật, loại tình huống này, còn quan tâm chuyện này, tâm tính cùng định lực, cho dù so ra kém năm đó Khổng sư, cũng không xê xích gì nhiều! Thiên tài như thế, ta đều có chút không muốn giết...”"),t("br"),t("br"),n._v("Ngoan Nhân khóe miệng nâng lên, mắt sáng lên: “Như vậy đi, ngươi chỉ cần hiệu trung với ta, đem linh hồn đối ta hiến tế... Không chỉ nói cho ngươi, còn lưu tính mệnh của ngươi, về sau đạt được truyền thế thiên phù, còn có thể dẫn ngươi đi tìm kiếm Xuân Thu đại điển, giúp ngươi trở thành vạn thế chi sư...”"),t("br"),t("br"),n._v("“Hiệu trung?” Trương Huyền lắc đầu: “Một ngày danh sư, cả đời danh sư! Đầu nhập vào Dị Linh tộc nhân, ta làm không được... Bất quá, cho dù không nói vị kia khôi lỗi là ai, ta hiện tại, đã đoán không sai biệt lắm!”"),t("br"),t("br"),n._v("“Ồ?” Ngoan Nhân trong mắt mang theo nghiền ngẫm, dường như không tin hắn có thể đoán được."),t("br"),t("br"),n._v("“Hẳn là Thanh Nguyên đế quốc Hoàng đế, Sở Thiên Hành đi!” Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Ừm?” Ngoan Nhân tròng mắt hơi híp."),t("br"),t("br"),n._v("“Có thể tin tức so Điền Thanh phó đường chủ đều muốn linh thông, tất nhiên thân cư cao vị... Ngô sư là chân chính danh sư, Cẩu đường chủ cũng bị giết... Duy nhất khả năng, chỉ có vị hoàng đế bệ hạ này!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thật ra thì điểm ấy không khó đoán."),t("br"),t("br"),n._v("Thanh Nguyên đế quốc, có thể biết truyền thế thiên phù tin tức, tuyệt đối quyền cao chức trọng, liền thân phận của Trung Thanh Vương đều không thể biết, loại trừ vị hoàng đế bệ hạ này, thực sự nghĩ không ra những người khác."),t("br"),t("br"),n._v("Lại thêm... Vị hoàng đế này, bản thân cử động, đã làm cho hoài nghi."),t("br"),t("br"),n._v("Trung Thanh Vương bị bắt, tiêu tốn to lớn đánh đổi, cũng muốn bảo lãnh; Tới thăm viếng Trung Thanh Vương, Danh Sư đường liền có tám vị danh sư bị câu đi hồn phách..."),t("br"),t("br"),n._v("Trước đó không nghĩ ra, bây giờ nghe Ngoan Nhân đã thuần phục vị này khôi lỗi, hơi liên hệ, liền có thể tưởng tượng đi ra."),t("br"),t("br"),n._v("“Không hổ là trời nhận danh sư, tư duy quả nhiên nhanh chóng, một cái đã nghĩ thông suốt mấu chốt...” Ngoan Nhân cười lạnh, không tại che lấp."),t("br"),t("br"),n._v("Người quang minh chính đại không nói chuyện mờ ám, lời nói đều nói trợn nhìn, che lấp lại không có cái gì ý nghĩa."),t("br"),t("br"),n._v("“Trời nhận danh sư?”"),t("br"),t("br"),n._v("“Trương sư là... Trời nhận danh sư?”"),t("br"),t("br"),n._v("Quý Thiên Hùng, Hình đường chủ đám người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Trong mọi người, chỉ có Ngô sư biết thân phận của Trương Huyền, cái khác đều không biết."),t("br"),t("br"),n._v("Giờ phút này nghe được trước mắt đầu này Dị Linh tộc nhân nói như vậy, tất cả đều khiếp sợ tột đỉnh, khó tin."),t("br"),t("br"),n._v("Trời nhận danh sư, vạn cổ không một, danh sư đại lục có lịch sử ghi chép nhiều năm như vậy, cũng chỉ xuất hiện một vị Khổng sư... Trước mắt vị này thế mà cũng là!"),t("br"),t("br"),n._v("“Làm sao? Các ngươi còn không biết? Ha ha... Hắn là trời nhận danh sư! Thiên địa công nhận tồn tại, chỉ cần sống tiếp, nương theo tu vi tăng thêm, tuyệt đối có thể trở thành vạn thế chi sư!”"),t("br"),t("br"),n._v("Thấy mọi người đều không biết, hừ một tiếng, Ngoan Nhân ánh mắt phát lạnh: “Bất quá, hiện tại, không có cơ hội! Ta cùng Khổng sư chiến đấu qua nhiều lần, thủ đoạn của hắn ta hết sức quen thuộc, ngươi bây giờ cho dù có nhiều hơn nữa phương pháp, đều là vô dụng! Thế nào? Quy thuận cùng ta, có thể không chết, nếu không, không chỉ ngươi muốn chết... Học sinh của ngươi, thân bằng hảo hữu, đều sẽ giết không còn một mống!”"),t("br"),t("br"),n._v("“Nói thật cho ngươi biết đi, các ngươi đi tới cái này thời điểm, ta đã thông tri Sở Thiên Hành, để hắn trước đem học sinh của ngươi bắt lại... Không có gì bất ngờ xảy ra, hiện tại đã bị khống chế...”"),t("br"),t("br"),n._v("Ngoan Nhân cười lạnh."),t("br"),t("br"),n._v("“Bắt học sinh của ta?” Trương Huyền ánh mắt phát lạnh."),t("br"),t("br"),n._v("Không nghĩ tới, đối phương thân là một đời Đại Đế, cư nhiên như thế không biết liêm sỉ, làm ra như vậy chuyện xấu xa."),t("br"),t("br"),n._v("đ"),t("br"),n._v("ọc ngantruyen.com/ “Không sai!”"),t("br"),t("br"),n._v("“Ngươi cảm thấy lời này, ta sẽ tin tưởng? Học sinh của ta, tại Chiến Sư đường tu luyện, có nhiều như vậy chiến sư bảo hộ, cho dù Sở Thiên Hành, muốn động thủ, cũng không có lá gan này đi!”"),t("br"),t("br"),n._v("Trương Huyền khẽ nói."),t("br"),t("br"),n._v("Hắn hiện tại chỉ có một cái thân truyền, Lưu Dương!"),t("br"),t("br"),n._v("Trước khi đi, chuyên môn dặn dò, tại Chiến Sư đường tu luyện... Cho dù Sở Thiên Hành là Thanh Nguyên đế quốc Hoàng đế bệ hạ, chỉ sợ cũng không dám đi Chiến Sư đường giương oai đi!"),t("br"),t("br"),n._v("“Để Sở Thiên Hành trực tiếp động thủ, khẳng định không thể... Nhưng nếu là đi mời đâu?”"),t("br"),t("br"),n._v("Ngoan Nhân cười nói."),t("br"),n._v("“Mời?” Trương Huyền nắm đấm căng thẳng."),t("br"),t("br"),n._v("Bọn họ trước khi đến, cũng chỉ là đối Sở Thiên Hành hoài nghi, phái danh sư nhìn chằm chằm... Không có chứng cớ xác thật."),t("br"),t("br"),n._v("Thân là một nước Đế Vương, tự mình mời học sinh của mình, đi hoàng cung làm khách, Lưu Dương hẳn là không có lý do cự tuyệt..."),t("br"),t("br"),n._v("Cái này muốn tới hoàng cung, liền thật lại không có biện pháp!"),t("br"),t("br"),n._v("Hiện tại Danh Sư đường đường chủ, Chiến Sư đường đường chủ, đều đến nơi này, cho dù muốn cứu người, đều rất khó làm được."),t("br"),t("br"),n._v("“Biết ngươi tới nơi này, không định chút thủ đoạn, ngươi cảm thấy, ta có thể cùng Khổng sư đấu?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Ngoan Nhân cười lạnh: “Tốt rồi, ta cho ngươi thời gian lựa chọn, hàng phục ta, ngươi cùng ngươi học sinh đều có thể sống, ta còn có thể truyền thụ cho ngươi hoàn chỉnh vu hồn bí pháp... Giúp ngươi trở thành vạn thế chi sư. Không thần phục... Ngươi không biết xấu hổ, trước đem học sinh của ngươi giết!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Híp mắt lại, Trương Huyền lâm vào lưỡng nan."),t("br"),t("br"),n._v("Lần thứ nhất... Bị người uy hiếp như vậy."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thanh Nguyên thành, Chiến Sư đường."),t("br"),t("br"),n._v("Trương Huyền đám người rời đi ngày thứ ba, trong đường, nghênh đón một vị khách nhân, Thanh Nguyên đế quốc Hoàng đế, Sở Thiên Hành."),t("br"),t("br"),n._v("“Chu đường chủ, lần này khả năng còn muốn làm phiền ngươi một cái, Trung Thanh Vương hiện tại còn bị Danh Sư đường giam giữ, hắn thật ra thì cũng không có quá lỗi lớn sai, chủ yếu là cùng Trương sư náo loạn chút mâu thuẫn, lần này tới, là muốn làm phiền ngươi giúp một tay giới thiệu một chút, cũng tốt để cho ta bồi tội!”"),t("br"),t("br"),n._v("Sở Thiên Hành một mặt vui vẻ."),t("br"),t("br"),n._v("Đi theo phía sau mấy cái hộ vệ, phía trước ngồi thì là vũ khí điện Chu điện chủ."),t("br"),t("br"),n._v("Hình đường chủ, Ngụy điện chủ, Liêu điện chủ đám người rời khỏi, hắn tạm thời chủ trì Chiến Sư đường công việc."),t("br"),t("br"),n._v("“Thực sự thật không tiện, bệ hạ tới thật không khéo, Trương sư hiện tại đang lúc bế quan, không tiện gặp khách...”"),t("br"),t("br"),n._v("Chu điện chủ lắc đầu, một mặt cười khổ."),t("br"),t("br"),n._v("Hình đường chủ trước khi đi chuyên môn đã thông báo, bọn họ rời khỏi, không thể rò rỉ tin tức, nếu có người đến tìm, từ chối không xong, liền nói là bế quan."),t("br"),t("br"),n._v("Đối phương là Thanh Nguyên đế quốc Hoàng đế, cho dù Chiến Sư đường, chỉ nghe Danh Sư đường sai khiến, bàng quan, cũng không tốt trực tiếp cự tuyệt."),t("br"),t("br"),n._v("“Bế quan? Cái kia... Ta nghe nói Trương sư còn có một vị thân truyền đệ tử, gọi là Lưu Dương? Không biết ta có thể hay không gặp mặt một lần, nghe nói hắn tuổi mới mười bảy, ta vừa vặn có một nữ, tuổi tác cùng hắn tương đương, dung mạo cũng nói qua được, cũng là có thể giới thiệu bọn họ quen biết một chút...”"),t("br"),t("br"),n._v("Sở Thiên Hành vội nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đối phương muốn gặp Trương Huyền, từ chối đi, đã thật không tiện, thấy cái học sinh, lại từ chối, chỉ sợ có chút bất cận nhân tình."),t("br"),t("br"),n._v("“Như vậy đi, ta phái người hỏi thăm một cái, nếu như Lưu Dương không nguyện ý đi ra, vậy ta cũng không có phương pháp!”"),t("br"),t("br"),n._v("Dừng lại một chút, Chu điện chủ nói."),t("br"),t("br"),n._v("“Vậy làm phiền điện chủ...” Sở Thiên Hành cười cười: “Ngươi cũng biết, ta lần này là tại chuyên môn tới chữa trị quan hệ, mong rằng điện chủ có thể nói ngọt...”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Chu điện chủ chào hỏi một tiếng, một vị chiến sư lập tức lui ra ngoài."),t("br"),t("br"),n._v("Vị này chiến sư vội vã hướng Lưu Dương nơi ở đi đến, còn chưa tới đến trước mặt, liền nghe đến “Bành bành bành bành!” Thanh âm không ngừng vang lên."),t("br"),t("br"),n._v("Gõ cửa đi vào, lập tức nhìn thấy Trương sư vị này thân truyền, đang liều mạng tu luyện, mồ hôi chảy xuôi xuống, cầm quần áo toàn bộ ướt đẫm, bởi vì dùng sức quá độ, cả người vẻ mặt đều có chút trắng."),t("br"),t("br"),n._v("“Lưu thiếu, ngươi... Ngươi kiềm chế một chút, ta xem ngươi thật giống như có chút dùng sức quá độ... Tiếp tục như vậy, tu vi tiến bộ chậm không nói, còn dễ dàng làm bị thương cơ sở!”"),t("br"),t("br"),n._v("Vị này chiến sư nói."),t("br"),t("br"),n._v("Chiến tử rõ ràng dặn dò, nhìn thấy Trương sư lấy lão sư lý giải đối đãi, trước mắt vị này Lưu Dương, không phải là danh sư, cũng không phải chiến sư, đành phải xưng hô “Lưu thiếu” hoặc là “Lưu công tử”."),t("br"),t("br"),n._v("Cứ việc chỉ là bình thường chiến sư, nhưng có thể trở thành Chiến Sư đường một thành viên, đều là danh sư bên trong tài năng xuất chúng tồn tại, liếc mắt liền nhìn ra đến, trước mắt vị này Lưu thiếu, dùng sức quá mạnh, đều có chút thoát lực."),t("br"),t("br"),n._v("Khắc khổ tu luyện là chuyện tốt, nhưng tu luyện quá mức, vượt ra khỏi thân thể phụ tải... Một khi làm bị thương cơ sở, lại nghĩ chữa trị, liền khó khăn!"),t("br"),t("br"),n._v("Mấu chốt nhất là, quá mức chỉ vì cái trước mắt, tâm cảnh liền sẽ sụp đổ, rất dễ dàng sinh ra khúc mắc, dẫn đến tẩu hỏa nhập ma."),t("br"),t("br"),n._v("“Ta biết... Nhưng mà thời gian không đợi người!”"),t("br"),t("br"),n._v("Lau mồ hôi, Lưu Dương ngừng lại, nắm đấm kìm lòng không được xiết chặt."),t("br"),t("br"),n._v("Lão sư, bảy vị thân truyền."),t("br"),t("br"),n._v("Triệu Nhã, Lộ Xung, Viên Đào, Ngụy Như Yên, Trịnh Dương, Vương Dĩnh... Tất cả đều có bản thân đường ra, hơn nữa một cái so một cái lợi hại."),t("br"),t("br"),n._v("Hắn theo sát sau lưng lão sư, lại tu vi tăng trưởng cực chậm... Trong lòng mang theo nồng đậm áp lực."),t("br"),t("br"),n._v("Nhất là tận mắt thấy Trịnh Dương thành chiến tử, dốc hết sức trấn áp bát phương... Càng thêm lo lắng, cả người tâm tính đều có chút mất cân bằng."),t("br"),t("br"),n._v("Hắn cũng muốn mạnh, muốn trở nên mạnh hơn, không thể so với những người khác yếu!"),t("br"),t("br"),n._v("Trong lòng nhẫn nhịn một hơi, liều mạng tu luyện, dù là thân thể không chịu nổi, đều muốn kiên trì."),t("br"),t("br"),n._v("“Ngươi tìm đến ta chuyện gì?”"),t("br"),t("br"),n._v("Lưu Dương nhìn qua: “Nếu như không có chuyện gì, ta muốn tiếp tục tu luyện!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1234"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1236"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);