(window.webpackJsonp=window.webpackJsonp||[]).push([[278],{633:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1227-luu-duong-nguy-co-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1227-luu-duong-nguy-co-ha"}},[n._v("#")]),n._v(" Chương 1227: Lưu Dương nguy cơ (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1227: Lưu Dương nguy cơ (hạ)"),t("br"),t("br"),n._v("“Là như vậy, Thanh Nguyên đế quốc Hoàng đế Sở Thiên Hành, muốn cùng Trương sư chữa trị quan hệ, đặc biệt chạy tới lấy lòng... Trương sư bế quan tu luyện, không tiện gặp khách, cho nên hắn muốn mời ngươi đi qua.”"),t("br"),t("br"),n._v("Vị này chiến sư vội nói."),t("br"),t("br"),n._v("“Mời ta đi qua? Nói với hắn... Không rảnh!” Lưu Dương khoát tay áo."),t("br"),t("br"),n._v("Hắn hiện tại thiếu nhất chính là thời gian, từng phút từng giây đều lãng phí không được."),t("br"),t("br"),n._v("“Lưu thiếu, tu luyện không thể một mực kéo căng dây đàn, còn cần lao động nhàn hạ kết hợp... Vị hoàng đế bệ hạ này, cứ việc cùng Trương sư náo loạn chút mâu thuẫn, có điều, lần này trong mắt của ta, hình như là thành tâm cầu hoà, không bằng gặp mặt một lần, cũng coi như hạ thấp cái thiện duyên. Dù sao đối phương là Thanh Nguyên đế quốc một nước chi chủ, ngộ nhỡ bởi vậy ghi hận, xuống chút ám chiêu, chẳng phải là cho Trương sư thêm phiền phức?”"),t("br"),t("br"),n._v("Vị này chiến sư nói."),t("br"),t("br"),n._v("“Cái này... Cũng được!” Chần chờ một chút, Lưu Dương nhẹ gật đầu."),t("br"),t("br"),n._v("Cái khác sáu vị đồng môn, đều vì lão sư làm vẻ vang, tranh màu, chỉ có hắn còn tại ăn lão sư, uống lão sư, dùng lão sư... Như là ký sinh trùng."),t("br"),t("br"),n._v("Không thể vì sư môn làm vẻ vang, nếu là lại cho thêm phiền phức, hắn người học sinh này còn mặt mũi nào, sống trên đời?"),t("br"),t("br"),n._v("Tuỳ thích vọt vào tắm, đổi một bộ quần áo sạch sẽ, cùng sau lưng chiến sư, chỉ chốc lát sau đi tới đại sảnh."),t("br"),t("br"),n._v("“Ngươi chính là Lưu Dương tiểu huynh đệ, tại hạ nghe đại danh đã lâu! Có thể trở thành Trương sư môn hạ, về sau tiền đồ vô lượng!”"),t("br"),t("br"),n._v("Thấy hắn đi tới, Sở Thiên Hành một mặt ý cười tiến lên đón, không có chút nào Hoàng đế bệ hạ ra vẻ."),t("br"),t("br"),n._v("“Bệ hạ khách khí!” Hơi hơi kinh ngạc, Lưu Dương nhẹ gật đầu, nói."),t("br"),t("br"),n._v("“Cũng không phải cái gì khách khí, ta là thật tâm muốn giao hảo quan hệ, ngươi cũng biết, lần trước bởi vì Trung Thanh Vương sự tình, cùng Trương sư huyên náo không quá vui vẻ...”"),t("br"),t("br"),n._v("Sở Thiên Hành vẫy tay một cái, sau lưng một cái hộ vệ đi tới, đưa qua một cái hộp ngọc."),t("br"),t("br"),t("span",[t("br"),n._v(" http://ngantruyen.com/"),t("br")]),n._v(" “Đây là ta chuyên môn hướng Trương sư bồi lễ nói xin lỗi, mong rằng không muốn chối từ!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Lưu Dương tiện tay đem hộp ngọc mở ra, một cỗ linh khí nồng nặc lập tức phun ra ngoài."),t("br"),t("br"),n._v("Tinh nguyên linh thạch thượng phẩm, năm cái!"),t("br"),t("br"),n._v("“Cái này...” Lưu Dương thân thể cứng đờ."),t("br"),t("br"),n._v("Lão sư nhu cầu cấp bách tinh nguyên linh thạch thượng phẩm sự tình, hắn cũng biết, nếu như có thể đạt được, xem như cho lão sư giúp chiếu cố rất lớn."),t("br"),t("br"),n._v("“Ha ha, cái này năm cái là cho Trương sư, ta cũng chuẩn bị cho ngươi quà tặng...”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ thay đổi, Sở Thiên Hành nhẹ nhàng cười một tiếng, lần nữa vẫy chào, lại một cái hộ vệ tiến lên, lần nữa đưa tới một cái hộp: “Ta biết tiểu huynh đệ quyền pháp vô địch, đặc biệt đi luyện khí sư công hội, tiêu tốn số tiền lớn chế tạo một bộ quyền sáo! Đã đạt đến hạ phẩm Thánh khí cấp bậc, đối ngươi vừa vặn phù hợp...”"),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Nương theo lời nói, hộ vệ đem hộp mở ra."),t("br"),t("br"),n._v("Một đạo nồng đậm lực lượng, từ bên trong tán dật mà ra, cúi đầu nhìn lại, quả nhiên là một bộ quyền sáo, phía trên tinh quang bắn ra bốn phía, cho người ta một loại Thánh khí uy áp."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lưu Dương yết hầu phát khô, thân thể không tự chủ được cứng ngắc."),t("br"),t("br"),n._v("Nói thật, hắn đã sớm muốn một bộ quyền sáo, phối hợp quyền pháp có thể phát huy ra uy lực to lớn, chỉ là... Lão sư thái vội vàng, hắn thật không tiện mở miệng, lúc này thấy đến loại cấp bậc này, lập tức tràn đầy kích động."),t("br"),t("br"),n._v("“Vô công bất thụ lộc, vật trân quý như vậy...”"),t("br"),t("br"),n._v("Chần chờ một chút, Lưu Dương nói."),t("br"),t("br"),n._v("“Tiểu huynh đệ nói gì vậy, có thể kết giao ngươi, xem như vận khí của ta, một cái hạ phẩm Thánh khí mà thôi, không tính là gì!” Sở Thiên Hành nở nụ cười."),t("br"),t("br"),n._v("“Vậy thì tốt, đã bệ hạ khách khí như thế, ta liền nhận, lão sư bên kia, ta sẽ thế chỗ truyền bệ hạ thiện ý!” Đưa tay nhận lấy, mang trên tay, cảm giác lớn nhỏ vừa vặn phù hợp, càng xem càng ưa thích."),t("br"),t("br"),n._v("Kiện binh khí này, bởi vì là vừa mới chế tạo ra đến, còn không có linh tính, vừa đến tay liền có thể sử dụng, miễn đi luyện hóa phiền phức."),t("br"),t("br"),n._v("“Thật không cần khách khí!”"),t("br"),t("br"),n._v("Sở Thiên Hành cười nhìn qua: “A, đúng, hoàng cung tối nay, tổ chức gia yến, ta mấy cái tuổi nhỏ con gái, thế nhưng là đã sớm nghe nói đại danh của ngươi, đòi muốn gặp mặt một lần, mong rằng tiểu huynh đệ, có thể vượt qua cổ động một chút!”"),t("br"),t("br"),n._v("“Cái này...” Lưu Dương dừng lại một chút: “Nếu là gia yến, ta đi qua không tốt lắm đâu...”"),t("br"),t("br"),n._v("“Khách khí, có thể tại Thanh Nguyên đế quốc gặp gỡ, chúng ta liền là người một nhà! Lúc đầu ta muốn mời Trương sư, đáng tiếc Trương sư bế quan, không cách nào rời khỏi... Nếu là huynh đệ có thể đến đây, hoàng thất chúng ta cũng coi như rồng đến nhà tôm!”"),t("br"),t("br"),n._v("Sở Thiên Hành cười rạng rỡ, trong mắt mang theo thành khẩn."),t("br"),t("br"),n._v("“Ta còn muốn tu luyện...” Lưu Dương nói."),t("br"),t("br"),n._v("“Cũng không quan tâm một ngày này, hai ngày. Tiểu huynh đệ cũng có thể cùng ta những không nên thân đó hoàng tử trao đổi một chút, thuận tiện dạy bảo bọn họ một chút, bảo bọn họ biết, nhân ngoại hữu nhân, thiên ngoại hữu thiên đạo lý. Nếu không lão tự đại tưởng là, vô địch thiên hạ... Để cho ta đau đầu!”"),t("br"),t("br"),n._v("Sở Thiên Hành nói tiếp."),t("br"),t("br"),n._v("“Cái này...” Lưu Dương chần chờ một chút, cuối cùng nhẹ gật đầu: “Thôi được!”"),t("br"),t("br"),n._v("Đối phương cho mới tinh quyền sáo, vừa vặn hắn cũng muốn thử một chút, nếu như có thể thừa cơ tăng cường lão sư uy danh, cũng không có áp lực lớn như vậy."),t("br"),t("br"),n._v("Nói thật, lão sư bảy cái đệ tử, đi sáu cái, một cái so một cái ưu tú... Trong lòng của hắn áp lực quá lớn, nóng lòng bày ra bản thân, hiện tại có cơ hội này, đương nhiên sẽ không chối từ."),t("br"),t("br"),n._v("Thấy bọn họ thương nghị xong, Chu điện chủ cũng không tiện nhiều lời."),t("br"),t("br"),n._v("Làm ra quyết định, Lưu Dương cũng không có gì có thể thu dọn, theo sát sau lưng Sở Thiên Hành, hướng hoàng cung đi đến."),t("br"),t("br"),n._v("Đi vào hoàng cung, quả nhiên thấy yến hội bắt đầu, vô số hoàng tử, công chúa chạy trong đó, từng cái tinh thần phấn chấn, lực lượng mười phần."),t("br"),t("br"),n._v("Không hổ là phong hào đế quốc công chúa, một cái so một cái xinh đẹp, ngồi ở trong đó, Lưu Dương nhìn một hồi, chỉ cảm thấy có chút hoa mắt."),t("br"),t("br"),n._v("Mặc dù so với Triệu Nhã, Vương Dĩnh đám người kém rất nhiều, nhưng từng cái cười yếu ớt Yên Yên, nhất cử nhất động, để lộ ra phong tình, để hắn đều có chút đạo tâm bất ổn."),t("br"),n._v("“Lưu Dương công tử...”"),t("br"),t("br"),n._v("Lúc này, một thanh niên đi tới trước mặt."),t("br"),t("br"),n._v("“Ngươi là...” Lưu Dương nhíu nhíu mày."),t("br"),t("br"),n._v("Theo quần áo đó có thể thấy được là một vị hoàng tử, chẳng qua địa vị cũng không cao, trước đó Sở Thiên Hành lúc giới thiệu, cũng không nói tỉ mỉ qua."),t("br"),t("br"),n._v("“Tại hạ Sở Tường, là Thanh Nguyên đế quốc, thứ bảy mươi bảy hoàng tử...”"),t("br"),t("br"),n._v("Thanh niên cười khổ: “Ta cùng Trương sư, từng có hai mặt duyên phận, được cho bằng hữu!”"),t("br"),t("br"),n._v("Nếu như Trương Huyền tại đây nhất định có thể nhận ra, không phải người khác, chính là đang kinh ngạc hồng sư tổ hội, cùng luyện khí sư tổ hội, gặp phải cái kia Sở Tường."),t("br"),t("br"),n._v("Không nghĩ tới, lại là một vị hoàng tử."),t("br"),t("br"),n._v("Khó trách, có thể vừa ra tay liền là một viên tinh nguyên linh thạch thượng phẩm."),t("br"),t("br"),n._v("Lúc trước, Trương Huyền hướng Sở Thiên Hành muốn một trăm viên linh thạch, trao đổi Trung Thanh Vương thời điểm, người sau từng nói qua, linh thạch ban thưởng cho mình hậu bối, trong quốc khố cũng không nhiều."),t("br"),t("br"),n._v("“Bạn của lão sư, thất kính thất kính!” Lưu Dương đứng dậy."),t("br"),t("br"),n._v("“Không cần khách khí...”"),t("br"),t("br"),n._v("Sở Tường cười gật đầu."),t("br"),t("br"),n._v("Hai người trò chuyện một hồi, trong lúc đó lại có mấy vị công chúa tới mời rượu, Lưu Dương hết sức cao hứng, uống nhiều mấy chén, mí mắt có chút cảm thấy chát, thân thể nhoáng một cái, hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Người tới, đem hắn nhốt vào địa lao, cắt đứt đưa tin, bao vây tất cả tin tức!”"),t("br"),t("br"),n._v("Đi tới trước mặt, Sở Thiên Hành con mắt chợt lóe."),t("br"),t("br"),n._v("“Vâng!” Mấy cái hộ vệ đi tới, đem Lưu Dương khóa lại, mang theo đi xuống."),t("br"),t("br"),n._v("“Phụ hoàng, cái này... Đây là có chuyện gì?” Sở Tường sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("“Ngươi không cần phải để ý đến!” Sở Thiên Hành khoát tay áo."),t("br"),t("br"),n._v("“Nhưng... Hắn là Trương Huyền học sinh! Trương sư, xông đường thành công, Danh Sư đường đều sợ hãi, đem hắn học sinh giam giữ, ta sợ...” Sở Tường tràn đầy cuống cuồng."),t("br"),t("br"),n._v("“Đủ rồi! Ngươi bình thường liều lĩnh, che giấu tung tích ở bên ngoài làm loạn ngược lại cũng thôi, chuyện này, ta tự có đúng mực!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương, Sở Thiên Hành sải bước đi ra ngoài."),t("br"),t("br"),n._v("Sở Tường nắm đấm xiết chặt."),t("br"),t("br"),n._v("Hắn là Hoàng đế thứ bảy con trai của mười bảy cái, lại là Tần phi sinh ra, địa vị không cao."),t("br"),t("br"),n._v("Vì có thể gây nên chú trọng, cố gắng tu luyện, không chỉ có thành thất tinh luyện khí sư, càng là tuổi còn trẻ liền đạt đến Thai Anh cảnh... Vốn cho rằng, như vậy có thể có được khen ngợi, ai biết... Bị người hạ độc thủ, nguyên thần bị hao tổn."),t("br"),t("br"),n._v("Theo cái này bắt đầu, hắn mới hiểu được, hoàng gia không tình thân, có chỉ là lợi ích."),t("br"),t("br"),n._v("Ngay sau đó, mới đổi lại bình thường quần áo, lưu luyến thanh lâu cùng nữ nhân... Thoạt nhìn phóng đãng ngỗ ngược, trên thực tế đều là giả tượng."),t("br"),t("br"),n._v("Bởi vì không làm như vậy lời nói, tranh quyền đoạt thế những hoàng tử kia, khẳng định sẽ đem hắn giết chết."),t("br"),t("br"),n._v("Hoàng tử những thủ đoạn này, phụ hoàng không có khả năng không biết... Nhưng không có khuyên can, để hắn càng thêm nản lòng thoái chí."),t("br"),t("br"),n._v("Vốn cho rằng lần này quan hệ thân thiết Trương sư, phụ hoàng muốn hoàn toàn đổi mới, không nghĩ tới, đem đối phương học sinh bắt."),t("br"),t("br"),n._v("Hắn cùng Trương Huyền gặp qua mấy mặt, biết đối phương từ trước tới nay bao che khuyết điểm... Vạn nhất xảy ra chuyện gì, phiền phức liền lớn."),t("br"),t("br"),n._v("“Không thể, muốn đem chuyện này, nói cho Trương sư... Nếu như về sau thật sự có hiểu lầm gì đó, cũng có thể tốt hơn giao thiệp...”"),t("br"),t("br"),n._v("Híp mắt lại, có quyết định."),t("br"),t("br"),n._v("Trương sư thủ đoạn, hắn nhưng là thấy tận mắt, tuyệt đối Thần Long giống như nhân vật, loại người này chỉ có thể kết giao, quyết không thể đắc tội."),t("br"),t("br"),n._v("Phụ hoàng chẳng biết tại sao muốn bắt đối phương học sinh, nhưng nhìn đối phương bộ dạng, hỏi thăm khẳng định cũng hỏi thăm không ra... Đã như vậy, không bằng lặng lẽ nói cho Trương sư, về sau thật muốn hai bên gây ra mâu thuẫn, cũng có thể từ đó hoà giải."),t("br"),t("br"),n._v("Biết bây giờ không phải là nói nhiều thời điểm, Sở Tường an tĩnh tham gia xong yến hội, lúc này mới cách Khai Hoàng cung."),t("br"),t("br"),n._v("Trở lại chỗ ở của mình, nghỉ ngơi rất lâu, thay đổi y phục, lặng lẽ hướng Chiến Sư đường đi đến."),t("br"),t("br"),n._v("“Ngươi tìm ai?”"),t("br"),t("br"),n._v("Một vị chiến sư chặn đường lại."),t("br"),t("br"),n._v("“Tại hạ bạn của Trương sư, đặc biệt tới thăm hỏi!” Sở Tường đem danh thiếp đưa tới."),t("br"),t("br"),n._v("“Bạn của Trương sư? Ngươi chờ!” Vị này chiến sư giật nảy mình, vội vã cầm danh thiếp đi ra ngoài."),t("br"),t("br"),n._v("Thời gian không dài, một tên mập theo ở phía sau đi tới."),t("br"),t("br"),n._v("“Nguyên lai là Sở Tường công tử, tại hạ Tôn Cường quản gia, thiếu chủ đã từng nói ngươi... Không biết tới tìm ta gia thiếu chủ chuyện gì?” Tôn Cường cười nhìn lại."),t("br"),t("br"),n._v("Thấy không phải Trương sư đi ra, Sở Tường có chút nóng nảy, nhịn không được nói: “Ta có chuyện quan trọng tìm Trương sư, mong rằng quản gia thông báo...”"),t("br"),t("br"),n._v("“Thiếu gia nhà ta đang lúc bế quan tu luyện, không thể quấy nhiễu, ta cũng không có cách nào...” Tôn Cường khoát tay áo: “Công tử có chuyện gì, cùng ta nói chính là, đợi thiếu gia bế quan đi ra, ta lại cùng hắn nói!”"),t("br"),t("br"),n._v("“Cái này...” Thấy đối phương nói như vậy, dừng lại một chút, biết chắc không thấy được Trương sư, Sở Tường đành phải lắc đầu, bố trí cái cách âm cấm chế: “Là như vậy, Trương sư học sinh Lưu Dương... Bị phụ hoàng ta bắt...”"),t("br"),t("br"),n._v("“Lưu Dương tiểu thiếu gia bị bắt?”"),t("br"),t("br"),n._v("Tôn Cường sững sờ, con mắt lập tức híp lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1235"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1237"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);