(window.webpackJsonp=window.webpackJsonp||[]).push([[28],{383:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1001-on-ao-qua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1001-on-ao-qua"}},[n._v("#")]),n._v(" Chương 1001: Ồn ào quá")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1001: Ồn ào quá"),t("br"),t("br"),n._v("Cái này chín đầu ám kim độc trùng, thế nhưng là hắn hao tốn mấy trăm năm, không biết tiêu hao bao nhiêu của cải, nuôi nấng không biết bao nhiêu máu tươi mới bồi dưỡng mà thành, có thể nói so con trai đều thân, tuyệt đối của quý, một chút tất cả đều rơi trên mặt đất, không có động tĩnh, không có tại chỗ hộc máu tử vong, liền xem như tâm chí kiên định."),t("br"),t("br"),n._v("Nhìn thấy bộ dáng của hắn cùng độc trùng tình huống, chung quanh cũng tất cả đều hai mặt nhìn nhau."),t("br"),t("br"),n._v("“Ám kim độc trùng bị... Cái này Tôn Cường độc chết?”"),t("br"),t("br"),n._v("“Trên người hắn lại có lợi hại hơn độc, liền độc trùng đều không chịu nổi?”"),t("br"),t("br"),n._v("“Muốn độc chết hắn, ngược lại bị độc chết...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Làm rõ chuyện gì xảy ra, nguyên một đám tràn đầy phát điên."),t("br"),t("br"),n._v("Lấy bọn họ đối độc nghiên cứu cùng thực lực, tự nhiên có thể nhìn ra, độc trùng nguyên nhân của cái chết —— là bị lợi hại hơn kịch độc tại chỗ độc chết!"),t("br"),t("br"),n._v("Cái này chín đầu giáp trùng, từ đầu đến cuối, cũng chỉ tại vị này Tôn Cường trên người dạo qua, xuất hiện loại tình huống này, chỉ có một cái khả năng, chính là trên người hắn độc, càng khủng bố hơn!"),t("br"),t("br"),n._v("Chạy tới độc người, đối phương nhắm mắt lại không nhúc nhích... Liền đem điện chủ độc trùng cứ thế mà hạ độc chết..."),t("br"),t("br"),n._v("Tất cả người vây xem, đều cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Đây chính là Độc điện, toàn bộ Hồng Viễn đế quốc, thậm chí Thanh Nguyên phong hào đế quốc dùng độc địa phương đáng sợ nhất, ở đây hạ độc chết điện chủ của quý... Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("“Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Đang tại kỳ quái, liền nghe đến điện chủ tức giận gào thét vang lên."),t("br"),t("br"),n._v("Lúc này điện chủ, lại không có trước đó nho nhã cùng trang nghiêm, hai mắt đỏ thẫm, để lộ ra cuồng loạn, dường như hận không thể đem tên trước mắt này, tại chỗ nuốt."),t("br"),t("br"),n._v("Cũng khó trách."),t("br"),t("br"),n._v("Bản mệnh độc trùng tử vong, không chỉ riêng này chút năm tích góp hủy hoại chỉ trong chốc lát, tu vi cũng thật to bị hao tổn, trước đó là Thánh vực tam trọng đỉnh phong thực lực, mà... Bây giờ có thể phát huy ra Thánh vực nhị trọng đỉnh phong là tốt lắm rồi."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Độc trùng bị giết, dùng độc khẳng định không được, điện chủ tiếng gầm gừ bên trong, chân khí trong cơ thể xao động, trên không trung hình thành một cái bàn tay khổng lồ, trực tiếp đập tới."),t("br"),t("br"),n._v("Bàn tay còn chưa tới đến “Tôn Cường” trước mặt, vị này một mực khoanh chân ngồi gia hỏa, bỗng nhiên đứng lên, con mắt vẫn như cũ đóng chặt, cổ tay khẽ đảo, một chuôi trường thương xuất hiện."),t("br"),t("br"),n._v("So với lúc trước Trịnh Dương càng thêm đáng sợ, một thương lăng không, hình như một đầu cự long phá mây mà ra, rong ruổi thiên hạ, bay lượn vạn dặm."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Hai cỗ lực lượng đối đầu, trên không trung biến mất."),t("br"),t("br"),n._v("Bị khí lãng xung kích, Trương Huyền liên tục lui về sau hai bước, bỗng nhiên đạp mạnh, nhanh như thiểm điện lao đến, người trên không trung, lại là một thương đâm tới."),t("br"),t("br"),n._v("Lần này, nhân thương hợp nhất, giống như hắn chính là thương, thương chính là hắn, cuộn trào mãnh liệt chân khí, phối hợp vô cùng cường đại thân thể, còn chưa tới đến trước mặt, mặt đất đá xanh liền toàn bộ nổ tung, điện chủ vị trí, sụp đổ xuống, xuất hiện một cái hố to."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Gào lên một tiếng, điện chủ lần nữa tiến lên đón."),t("br"),t("br"),n._v("Đổi lại bình thường, lực lượng của đối phương mặc dù cuồng bạo, nhưng mà nhờ vào Thánh vực tam trọng đỉnh phong sức chiến đấu, muốn nghiền sát, vẫn là rất nhẹ nhàng!"),t("br"),t("br"),n._v("Có thể chín đầu bản mệnh độc trùng vừa mới bị giết, bất kể thân thể vẫn là linh hồn, đều hứng chịu tới tổn thương rất nặng, thực lực bản thân, liền bình thường một phần mười đều không phát huy ra, cùng hắn liều, lại có chút khó mà chống lại cảm giác."),t("br"),t("br"),n._v("Đối phương... Có thể chỉ là cái Tòng Thánh ah!"),t("br"),t("br"),n._v("“Ta không tin, không giết được ngươi...”"),t("br"),t("br"),n._v("Lần nữa la lên, trong cơ thể lực lượng vận chuyển tới cực hạn, chân khí giống như nước sôi sôi trào, phát ra lộc cộc lộc cộc thanh âm."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mũi thương cùng chưởng ấn đối đầu, cực lớn tiếng nổ, vang vọng khắp nơi, tán dật mà ra chân khí, đem chung quanh vài trăm mét kiến trúc, toàn bộ thổi thành bột mịn, trước đó còn muốn xem náo nhiệt độc sư, cảm nhận được uy lực khổng lồ như thế, nguyên một đám sợ tới mức liên tiếp lui về phía sau, không dám tiếp tục trước mắt."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Điện chủ dùng ra toàn lực, mắt vẫn nhắm như cũ “Tôn Cường” tựa hồ có chút không chịu nổi, máu tươi phun ra, bay ngược ra ngoài, trùng điệp ngã xuống đất."),t("br"),t("br"),n._v("Mặc dù Trương Huyền tu luyện Phong Thánh giải, có thể vượt cấp chiến đấu, nhưng cũng là có hạn độ... Điện chủ lực lượng quá cường đại, căn bản không phải đối thủ."),t("br"),t("br"),n._v("Đương nhiên, nếu như Vu hồn không có ly thể, sử dụng Thiên Đạo thư viện, tra rõ ràng thiếu hụt, chế định phương pháp chiến đấu, coi như thực lực đối phương mạnh mẽ, ai thắng ai thua cũng không nhất định, mà bây giờ... Chỉ có cứng đối cứng man lực, liền nguy hiểm hơn nhiều."),t("br"),t("br"),n._v("“Chết!”"),t("br"),t("br"),n._v("Đem “Tôn Cường” đánh bay, điện chủ lần nữa bước ra một bước, theo sát mà lên."),t("br"),t("br"),n._v("Vừa rồi đối đầu, hắn cũng kiểm tra xong đối phương chân chính thực lực, so sánh với hắn, vẫn là chênh lệch một chút, chỉ cần không cho thở dốc cơ hội cũng, nhất định có thể chém giết!"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Cảm nhận được hắn chiêu số nguy hiểm, đối diện “Tôn Cường” dường như sợ hãi, trường thương trong tay trực tiếp ném ra, xoay người bỏ chạy."),t("br"),t("br"),n._v("Thời gian nháy mắt liền thoát ra vài trăm mét."),t("br"),t("br"),n._v("“Trốn chỗ nào...”"),t("br"),t("br"),n._v("Không nghĩ tới cái tên này, một chút nguyên tắc đều không nói, mới vừa rồi còn phách lối nhắm mắt lại cùng mình chiến đấu, hiện tại liền chạy mất dạng, điện chủ khí gầm thét liên tục, đuổi sát tới, lần nữa đập xuống."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Chưởng lực hùng hậu, đập ra một cái hố to, bất quá đối phương tốc độ thực sự quá nhanh, miễn cưỡng tránh thoát."),t("br"),t("br"),n._v("“Ngăn hắn lại cho ta...”"),t("br"),t("br"),n._v("Thấy hắn càng chạy càng nhanh, biết mình bản thân bị trọng thương, không thể kiên trì quá lâu, điện chủ quay đầu phân phó."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tất cả trưởng lão không dám do dự, theo sát tới."),t("br"),t("br"),n._v("Bọn họ đuổi nhanh, Trương Huyền chạy nhanh hơn, hơn nữa còn là nhắm mắt lại chẳng có mục đích chạy loạn, những nơi đi qua, vô số kiến trúc bị cứ thế mà va sụp, trong lúc nhất thời toàn bộ Độc điện khói bụi cuồn cuộn, súc lập không biết bao nhiêu năm cũ kỹ kiến trúc, không một may mắn thoát khỏi."),t("br"),t("br"),n._v("Nhìn thấy tổ tông cơ nghiệp, phải bị cái tên này phá sạch, điện chủ khóe mắt, cổ tay khẽ đảo, một cái lệnh bài xuất hiện tại lòng bàn tay, chân khí truyền vào, chậm rãi lơ lửng giữa không trung, giống như là trao đổi đặc thù nào đó lực lượng, phát ra hào quang chói sáng."),t("br"),n._v(" “Hậu bối thứ ba mươi đảm nhiệm Độc điện điện chủ, khởi động hộ điện đại trận...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo thanh âm, lệnh bài ánh sáng càng ngày càng sáng, trước đó ẩn nấp cả hòn đảo nhỏ cực lớn trận pháp, giống như là bị một cỗ đặc thù lực lượng kích hoạt, chậm chạp vận chuyển lên tới."),t("br"),t("br"),n._v("Năm đó Hồng Viễn Độc điện khai phái tổ sư lưu lại trận pháp này, không chỉ có thể đủ ẩn nấp hòn đảo, còn có thể đối xâm phạm người tiến hành vây khốn, độc chết!"),t("br"),t("br"),n._v("Nơi xa cái tên này, không chỉ chạy trốn, còn khắp nơi làm phá hư, lại không ngăn lại đánh giết, chỉ sợ Độc điện vạn năm cơ nghiệp, đều đem hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trận pháp vận chuyển lên đến, một cỗ cường đại lực lượng, lập tức hướng cách đó không xa chạy trốn Trương Huyền mạnh vọt qua."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trận pháp tốc độ quá nhanh, căn bản trốn tránh không ra, lập tức đem hắn phong ấn trong đó, giống như là bị giam tiến vào một cái chiếc lồng."),t("br"),t("br"),n._v("“Hừ, nhìn ngươi còn thế nào trốn...”"),t("br"),t("br"),n._v("Thấy trận pháp cường đại như thế, điện chủ ánh mắt sáng lên, vội vã bay tới, có điều, còn chưa tới đến trước mặt, liền thấy vị này “Tôn Cường”, giống như là nhớ ra cái gì đó, đột nhiên tại trong phong ấn lui về phía sau mấy bước, bàn chân trên mặt đất bỗng nhiên đạp mạnh."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Phong ấn lập tức vỡ vụn, ngay sau đó đứng tại chỗ bất động, tay phải giơ lên, bỗng nhiên một trảo."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Trước mắt hắn luyện hóa lệnh bài lập tức không bị khống chế, trực tiếp hướng đối phương bay đi."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, điện chủ suýt chút nữa không có tại chỗ hù chết."),t("br"),t("br"),n._v("Dễ dàng phá vỡ phong ấn, càng đem hắn lệnh bài trong tay đoạt đi... Chỉ có một khả năng, đối phương đối với trận pháp mười phần hiểu rõ, đã hoàn toàn biết được trong đó sơ hở chỗ, cũng đối hắn tiến hành khống chế!"),t("br"),t("br"),n._v("Cái này... Làm sao có thể?"),t("br"),t("br"),n._v("Trận pháp này, thế nhưng là lúc trước Tiên Tổ nghiên cứu không biết bao nhiêu năm, phối hợp địa hình, hoàn mỹ bố trí đi ra, coi như lịch đại tinh thông trận pháp tiền bối, đều chỉ có kiến thức nửa vời năng lực... Cái tên này mới đi đến bao lâu thời gian? Chẳng những hiểu rõ, còn tìm ra thiếu hụt..."),t("br"),t("br"),n._v("Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Trong lòng khiếp sợ, đang muốn lần nữa đoạt lại khống chế trận pháp lệnh bài, liền thấy phía trước thanh niên, mắt vẫn nhắm như cũ, bàn tay lần nữa khẽ đảo."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Một cỗ lực lượng lập tức đánh tới, điện chủ thân thể lập tức cảm thấy một chút cứng ngắc..."),t("br"),t("br"),n._v("Trận pháp bị đối phương khống chế, lấy đạo của người, trả lại cho người, đem hắn khóa ở trong đó."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, điện chủ lực lượng toàn thân tuôn ra, đang định đem phong ấn tránh thoát, liền thấy một cái bàn chân đối với khuôn mặt trực tiếp đạp tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Chỉ một chút, răng liền rơi mất mấy cái, miệng mũi máu tươi chảy dài."),t("br"),t("br"),n._v("“Tôn Cường, ngươi dám đánh ta, ta muốn ngươi chết...”"),t("br"),t("br"),n._v("Gào lên một tiếng, tiếng nói không có kết thúc, chỉ thấy quả đấm đối phương bàn chân càng lúc càng nhanh."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Không đến mười cái hô hấp, liền chịu ít nhất ba, bốn mươi bàn tay, năm, sáu mươi chân, cả người bị đánh hoàn toàn thay đổi, ném đi, tuyệt đối không có người tin tưởng là khuôn mặt hòa ái điện chủ."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, lấy thực lực của hắn, coi như trong thời gian ngắn chém giết không được đối phương, nhưng mà đối phương muốn thương tổn hắn cũng là rất khó làm được."),t("br"),t("br"),n._v("Kết quả, dùng trận pháp về sau, không những không có đem đối phương giết chết, còn để người ta mượn lực lượng, ngừng lại cuồng ẩu, điện chủ chỉ cảm thấy đầu choáng váng, sắp nổ."),t("br"),t("br"),n._v("Hắn nhưng là Độc điện điện chủ, toàn bộ Hồng Viễn Độc điện thực lực mạnh nhất, quyền thế cao nhất người..."),t("br"),t("br"),n._v("Khởi động đại trận, lại bị đối phương mượn nhờ, mạnh mẽ điên cuồng đánh, chuyện này là sao..."),t("br"),t("br"),n._v("Làm sao cảm giác, đối phương giống như là điện chủ, mà bản thân, ngược lại là kẻ ngoại lai đồng dạng?"),t("br"),t("br"),n._v("“Điện chủ...”"),t("br"),t("br"),n._v("Đuổi tới hỗ trợ tất cả trưởng lão, nhìn thấy “Tôn Cường” không ngừng điên cuồng đánh điện chủ, đánh sắp tử vong, nguyên một đám vò đầu bứt tai, không biết như thế nào cho phải."),t("br"),t("br"),n._v("Điện chủ ra mặt, bọn họ cảm thấy khẳng định sẽ bao quát bại cục, mở mày mở mặt, làm sao đều không có nghĩ đến, đầu tiên là độc trùng bị độc chết, sau đó lại bị điên cuồng đánh..."),t("br"),t("br"),n._v("“Đừng với vị này Tôn Cường có sát ý, nếu không, hắn sẽ giết ngươi...”"),t("br"),t("br"),n._v("Thấy điện chủ bất cứ lúc nào cũng sẽ bị đánh chết tươi, một trưởng lão nhớ tới tình huống trước, nhịn không được hô."),t("br"),t("br"),n._v("“Đừng có sát ý?”"),t("br"),t("br"),n._v("Mặt mũi sưng đỏ điện chủ, mặc dù không biết vị trưởng lão này nói như vậy, đến cùng để cái gì, nhưng đã có thể lớn tiếng như thế, tất nhiên không có sai, cố nén tức giận, đem sát ý trong lòng chậm rãi áp chế."),t("br"),t("br"),n._v("Bên này vừa mới áp chế xong, đối diện “Tôn Cường” quả nhiên ngừng lại, lần nữa khoanh chân ngồi tại cách đó không xa, tiếp tục tu luyện."),t("br"),t("br"),n._v("Từ đầu đến cuối, con mắt đều không có mở ra một chút, thật giống như trước đó tất cả cử động, tại mộng du đồng dạng."),t("br"),t("br"),n._v("“Cái này... Đến cùng làm sao cái tình huống? Ta thế mà bị người mộng du đánh?”"),t("br"),t("br"),n._v("Điện chủ hốc mắt đỏ lên."),t("br"),t("br"),n._v("Đường đường Độc điện điện chủ, bị cường giả đánh cũng liền nhịn, kết quả bị một cái Tòng Thánh đồng minh đánh hoàn toàn thay đổi, suýt chút nữa chết rồi... Cái này kêu chuyện gì!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cùng lúc đó, Trương Huyền Vu hồn, vừa vặn nhìn xong tầng thứ ba thư tịch."),t("br"),t("br"),n._v("Cảm nhận được bên ngoài linh khí tịch quyển, dường như có cực lớn trận pháp vận chuyển, lại có lầu các sụp đổ, nổ vang không dứt, lông mày nhíu một cái, tràn đầy không vui."),t("br"),t("br"),n._v("“Ồn ào quá, còn có thể hay không để cho người ta hảo hảo đọc sách?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1009"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1011"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);