(window.webpackJsonp=window.webpackJsonp||[]).push([[280],{634:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1229-thien-hoang-đe-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1229-thien-hoang-đe-ha"}},[n._v("#")]),n._v(" Chương 1229: Thiến Hoàng đế (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1229: Thiến Hoàng đế (hạ)"),t("br"),t("br"),n._v("Trong hoàng cung."),t("br"),t("br"),n._v("“Thế nào? Tên kia có hay không khuất phục?”"),t("br"),t("br"),n._v("Ngồi tại sau cái bàn mặt, Sở Thiên Hành ngẩng đầu nhìn một cái, cách đó không xa quỳ một người áo đen, nhàn nhạt hỏi."),t("br"),t("br"),n._v("“Bẩm báo bệ hạ, tên kia tuổi không lớn lắm, cũng là cái xương cứng, hỏi cái gì đều không nói, giống như người câm, các huynh đệ đã đem đủ loại hình pháp đều là dùng qua, cứ vậy không có mở miệng!”"),t("br"),t("br"),n._v("Người áo đen ôm quyền."),t("br"),t("br"),n._v("“Xương cứng? Xương cứng ta gặp qua không ít, trong tay ta, đều sẽ biến thành tôm chân mềm!”"),t("br"),t("br"),n._v("Ánh mắt phát lạnh, Sở Thiên Hành cười lạnh."),t("br"),t("br"),n._v("“Như thế... Bất quá, bệ hạ, chúng ta để hắn... Nói cái gì?” Người áo đen nghi ngờ."),t("br"),t("br"),n._v("Bệ hạ chỉ để bọn họ dùng hình, cũng không nói làm cho đối phương nhận tội cái gì, hoặc là muốn thẩm vấn ra cái gì, để hắn tràn đầy khó hiểu."),t("br"),t("br"),n._v("“Không cần nói cái gì, chỉ cần dằn vặt hắn ý niệm dao động, hoàn toàn khuất phục liền tốt, những chuyện khác, ta sẽ tự mình xử lý!”"),t("br"),t("br"),n._v("Sở Thiên Hành khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Người áo đen nhẹ gật đầu, xoay người đi ra ngoài."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Sở Thiên Hành ánh mắt phát lạnh, đứng dậy."),t("br"),t("br"),n._v("Chỉ cần đối phương ý niệm buông lỏng, bản thân liền có thể dựa theo “Ngoan Nhân” truyền thụ linh hồn bí pháp, đem hắn thuần phục, đến lúc đó, muốn chơi thế nào thì chơi thế đó!"),t("br"),t("br"),n._v("Rời ghế ngồi, hướng đi ra ngoài điện, vừa đi ra gian phòng, liền thấy vừa rồi nói chuyện cùng hắn người áo đen, nằm ở trên mặt đất, không nhúc nhích, hình như hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Sầm mặt lại, toàn thân vận chuyển chân khí."),t("br"),t("br"),n._v("Hắn là nửa bước Xuất Khiếu cảnh cường giả, hơi sử dụng lực lượng, chân khí lập tức như thủy ngân tại trong huyệt đạo chảy xuôi, phát ra “Phần phật! Phần phật!” Thanh âm."),t("br"),t("br"),n._v("Thuộc hạ lặng yên không tiếng động ngất đi, một điểm động tĩnh đều không có, chỉ sợ có cao thủ mai phục tại bốn phía."),t("br"),t("br"),n._v("Đang nghĩ đi qua nhìn một chút, chính mình cái này thuộc hạ, bị ai ra tay, thân thể kìm lòng không được lắc lư một cái, con ngươi kìm lòng không được thu hẹp: “Không tốt, là... Chí Tôn Thanh Phong tán!”"),t("br"),t("br"),n._v("Loại cảm giác này hết sức quen thuộc, chính là hai mươi năm trước, Dị Linh tộc nhân tiến công lúc, sử dụng độc dược."),t("br"),t("br"),n._v("Một khi ngửi vào, sẽ lập tức rã rời như bùn, nửa bước Xuất Khiếu cảnh đều khó mà may mắn thoát khỏi."),t("br"),t("br"),n._v("Năm đó, chính là như vậy bị bắt, cứ thế mà bị vị kia Thanh Điền Hoàng dằn vặt sắp tử vong, cuối cùng linh hồn hiến tế, trở thành đối phương khôi lỗi, mới lấy chạy trốn."),t("br"),t("br"),n._v("“May mắn có giải dược...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một cái bình ngọc xuất hiện tại lòng bàn tay, cố nén trong đầu mê muội, đem bên trong viên thuốc, lấy ra một viên, để vào trong miệng."),t("br"),t("br"),n._v("Hắn không giống chiến sư, chịu qua ý niệm lực huấn luyện, cho dù mê muội, cũng có thể kiên trì chiến đấu. Cứ việc tu vi là Xuất Khiếu cảnh đỉnh phong, lại là vô số dược liệu tích tụ đi lên, sức chiến đấu, so với bình thường nguyên thần đỉnh phong đều hơi có không bằng, chớ nói chi là chiến sư."),t("br"),t("br"),n._v("Viên thuốc đi vào cổ họng, lập tức tan ra, vốn cho rằng rất nhanh liền có thể năng lực khôi phục, ai biết đợi một hồi, không có phản ứng chút nào."),t("br"),t("br"),n._v("Thật giống như ăn thuốc giả đồng dạng."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Sắc mặt tái xanh."),t("br"),t("br"),n._v("Cái này giải dược, là Thanh Điền Hoàng tự mình cho, chỉ cần nuốt vào, liền sẽ không chịu đến Thanh Phong tán ảnh hưởng... Làm sao ăn hết rồi, vẫn như cũ rã rời vô lực?"),t("br"),t("br"),n._v("Đang tại khó hiểu, ý định lại tìm mấy loại thuốc giải độc thử một chút, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Thuốc này là dùng tổ sư truyền thụ cho thủ pháp luyện chế mà thành, dược lực mạnh mẽ hơn trước đó gấp năm lần không chỉ, bình thường giải dược, căn bản vô dụng!”"),t("br"),t("br"),n._v("Sầm mặt lại, vội vàng xoay người, lập tức liền thấy một đám người mặc khó hiểu quần áo người đi tới."),t("br"),t("br"),n._v("Hứa trưởng lão đám người, biết hành động lần này, dính dáng cực lớn, rời khỏi Chiến Sư đường về sau, liền đổi lại trước kia quần áo, như vậy đại biểu cá nhân, mà phi chiến sư đường."),t("br"),t("br"),n._v("Bọn họ luyện chế Chí Tôn Thanh Phong tán, dùng tổ sư truyền thụ cho phương pháp luyện chế, dược hiệu cao, hao tổn ít, trước kia giải dược, cho dù dùng, cũng không có cái gì tác dụng."),t("br"),t("br"),n._v("Nếu không có này dựa dẫm, cũng không dám như vậy quang minh chính đại, liền đến hoàng cung cướp người."),t("br"),t("br"),n._v("“Các ngươi nhưng biết đây là địa phương nào? Nơi này là Thanh Nguyên hoàng cung, dám đối Hoàng đế bệ hạ động thủ, đã đại nghịch bất đạo, tội không thể tha...”"),t("br"),t("br"),n._v("Lông mày giương lên, Sở Thiên Hành lớn tiếng quát tháo."),t("br"),t("br"),n._v("Bất quá, lời còn chưa dứt, liền nghe đến một cái thanh âm tức giận vang lên: “Tội không thể tha? Tha ngươi ma phê!”"),t("br"),t("br"),n._v("Ngay sau đó, còn không có kịp phản ứng, Sở Thiên Hành liền thấy một cái to lớn viên thịt đối với hắn lao đến."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Mắt tối sầm lại, bị một quyền bắn trúng hốc mắt, bay ngược ra ngoài, một đầu đụng tử đại sảnh trên cây cột, đầu rơi máu chảy."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, Sở Thiên Hành nhìn thấy một tên mập xuất hiện ở trước mắt, vừa rồi một quyền kia, đúng là hắn đánh tới."),t("br"),t("br"),n._v("“Ngươi là Trương sư cái kia quản gia... Tôn Cường?” Toàn thân căng thẳng."),t("br"),t("br"),n._v("Trung Thanh Vương cũng là bởi vì cái tên này, mới bị làm suýt chút nữa chết đi, hắn cũng làm điều tra, vừa nhìn thấy dung mạo liền nhận ra được."),t("br"),t("br"),n._v("“Không sai, chính là ngươi Cường gia gia!”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Tôn Cường quay đầu nhìn về phía một vị lão giả: “Hứa trưởng lão, nhưng có dao găm?”"),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Lấy ra một cây chủy thủ, thấp tới, Hứa trưởng lão hơi nghi hoặc một chút nhìn qua: “Ân công, muốn cái này làm gì?”"),t("br"),t("br"),n._v("“Cái tên này, dám bắt Lưu Dương... Ta muốn thiến hắn!”"),t("br"),t("br"),n._v("Tôn Cường cắn răng."),t("br"),t("br"),n._v("“Thiến?” Hứa trưởng lão đám người khóe miệng giật một cái."),t("br"),t("br"),n._v("Trong hoàng cung mọi người, trúng độc chỉ là trong thời gian ngắn rã rời, sẽ không đả thương cùng tu vi cùng tính mạng, cho dù Danh Sư đường thẩm tra, vì cứu người, tình bất đắc dĩ..."),t("br"),t("br"),n._v("Lại thêm Trương sư xông đường thành công, tất cả đều tại có khống chế bên trong."),t("br"),t("br"),n._v("Thật là muốn đem Hoàng đế thiến sạch... Chuyện liền nháo lớn rồi."),t("br"),t("br"),n._v("Chờ tại thí quân... Tin tức truyền ra ngoài, Danh Sư đường đều không gói được lửa."),t("br"),t("br"),n._v("“Tới mấy người, giúp ta đè lên, không làm phế cái này hai hàng, ta không họ Tôn!”"),t("br"),t("br"),n._v("Mập trừng mắt, Tôn Cường hừ lạnh."),t("br"),t("br"),n._v("Từ khi đi theo lão gia về sau, chưa từng chịu tội bậc này oan ức!"),t("br"),n._v("Đi tới Thanh Nguyên thành, đầu tiên là hắn bị bắt, sau đó là Lưu Dương... Thật đúng là mẹ nó tưởng là, ta Cường ca dễ khi dễ sao?"),t("br"),t("br"),n._v("Không biểu hiện ra điểm uy phong, không biết Mã vương gia có ba con mắt!"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mấy vị độc sư đi tới Sở Thiên Hành trước mặt, đem hắn tứ chi đè lại."),t("br"),t("br"),n._v("“Ngươi muốn làm gì? Ta là Thanh Nguyên đế quốc Hoàng đế, các ngươi làm như vậy, là đại nghịch bất đạo... Danh Sư đường là sẽ không bỏ qua cho các ngươi...”"),t("br"),t("br"),n._v("Vốn cho rằng nói đùa, thấy hắn nghiêm túc, Sở Thiên Hành vẻ mặt một chút trợn nhìn."),t("br"),t("br"),n._v("Phong hào đế quốc Hoàng đế, hàng tỉ người phía trên Chúa Tể... Từ trước tới nay đều là hắn dạy bảo người khác, bây giờ bị người đè lại thiến... Mãnh liệt buồn bực để hắn sắp hộc máu."),t("br"),t("br"),n._v("“Danh Sư đường? Hừ, Danh Sư đường thật muốn quản việc này, sớm đã đem như ngươi loại này u ác tính giết, còn cần đến ta động thủ?”"),t("br"),t("br"),n._v("Khoát tay áo, Tôn Cường tiếp tục dặn dò: “Đem hắn quần cởi ra!”"),t("br"),t("br"),n._v("Một vị độc sư nghe được mệnh lệnh, đi tới trước mặt, đang nghĩ hiểu quần, chỉ thấy Hứa trưởng lão đi tới."),t("br"),t("br"),n._v("“Ân công, còn là đừng động thủ, làm máu me khắp người, quá tàn nhẫn...”"),t("br"),t("br"),n._v("Nghe hắn khuyên can, Sở Thiên Hành thở phào nhẹ nhõm, lần nữa nhìn sang, tràn đầy cảm kích."),t("br"),t("br"),n._v("Ông lão này mặc dù dùng độc rất lợi hại, nhưng vẫn là biết đúng mực, chờ tránh thoát kiếp nạn này, cũng là có thể phong cái vương hầu... Bày tỏ cảm kích."),t("br"),t("br"),n._v("“Quả thực có chút tàn nhẫn...” Tôn Cường nhíu nhíu mày, ngừng lại: “Vậy ngươi nói làm như vậy?”"),t("br"),t("br"),n._v("Nói thiến đối phương, cũng chỉ là nhất thời tức giận, Hứa trưởng lão nói chuyện, cũng cảm thấy có chút không ổn."),t("br"),t("br"),n._v("“Như vậy...”"),t("br"),t("br"),n._v("Chần chờ một chút, Hứa trưởng lão cổ tay khẽ đảo, lấy ra một cái bình ngọc, lấy ra một viên đan dược, đưa cho Sở Thiên Hành: “Đem cái này ăn!”"),t("br"),t("br"),n._v("“Ăn?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Sở Thiên Hành tràn đầy do dự."),t("br"),t("br"),n._v("Bất quá, cái khác độc sư cũng không có tốt như vậy tính tình, thấy hắn chần chừ, cầm lấy bàn tay của hắn liền đem viên thuốc nhét vào miệng bên trong."),t("br"),t("br"),n._v("“Đây là cái gì?”"),t("br"),t("br"),n._v("Tôn Cường nhìn qua."),t("br"),t("br"),n._v("Để hắn dừng tay, bản thân lại mớm thuốc... Có ý tứ gì?"),t("br"),t("br"),n._v("“A, đây là một loại độc dược, sau khi ăn xong, không cần thiến, cũng sẽ tự rụng xuống... Hơn nữa, là vĩnh cửu, cho dù dùng Đoạn Tục đan cũng vô dụng, cũng không tàn nhẫn, lại có thể miễn cho về sau dài ra lại, nhất cử lưỡng tiện!”"),t("br"),t("br"),n._v("Hứa trưởng lão nói."),t("br"),t("br"),n._v("“Tự rụng xuống...” Thân thể nhoáng một cái, Sở Thiên Hành chỉ cảm thấy mắt tối sầm lại."),t("br"),t("br"),n._v("Mới vừa còn muốn lấy, đối phương thật tốt, về sau phong hầu... Kết quả là cho hắn ăn cái này..."),t("br"),t("br"),n._v("Buồn bực vẻ mặt đỏ lên, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Thân thể kịch liệt lay động bên dưới, quả nhiên cảm thấy nửa người dưới mát lạnh... Dường như thật có thứ gì tróc ra."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Cũng lại không chịu nổi, mắt tối sầm lại, hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Cái này... Thuốc thật là mạnh!”"),t("br"),t("br"),n._v("Nhìn thấy trong đũng quần chảy ra máu tươi, có đồ vật rớt xuống, không cần đao cũng đem vấn đề giải quyết, nhìn về phía Hứa trưởng lão, Tôn Cường tràn đầy bội phục."),t("br"),t("br"),n._v("Không hổ là người người kính sợ độc sư, đủ loại thủ đoạn kỳ lạ khó lường, bị đùa chơi chết, cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Đem hắn làm tỉnh lại, hỏi thăm một chút, Lưu Dương bị giam tại ở đâu!”"),t("br"),t("br"),n._v("Không thèm để ý vị này đã phế bỏ Hoàng đế bệ hạ, Tôn Cường khoát tay áo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hai vị độc sư đi tới, thời gian không dài, liền đem giam giữ địa phương hỏi lên."),t("br"),t("br"),n._v("Độc sư thủ đoạn, quá mức kinh khủng, đủ loại độc dược sử dụng phía dưới, người sắt đều không kiên trì nổi, huống chi một cái sống an nhàn sung sướng Hoàng đế."),t("br"),t("br"),n._v("Vội vã đi tới giam giữ Lưu Dương địa lao, chỉ thấy người sau thoi thóp, bị giày vò đến sắp tắt thở."),t("br"),t("br"),n._v("“Tiểu thiếu gia...”"),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, Tôn Cường vội vàng lấy ra mấy cái lúc trước thiếu gia cho hắn chữa thương đan dược, cho ăn đi xuống."),t("br"),t("br"),n._v("Có lần trước hắn bị bắt kinh nghiệm, thiếu gia sợ bọn họ trọng thương, chuyên môn lấy chút đan dược, đặt ở trữ vật giới chỉ bên trong, chuẩn bị bất cứ tình huống nào."),t("br"),t("br"),n._v("Uống vào viên thuốc, qua một hồi thật lâu, Lưu Dương lúc này mới khôi phục lại, nhìn về phía trước mắt bị mọi người đè lên Sở Thiên Hành, lửa giận thiêu đốt, tựa như hỏa diễm."),t("br"),t("br"),n._v("Vốn cho rằng đối phương thật sự là hảo tâm, tới mời hắn, nghĩ biện pháp hóa giải cùng lão sư ở giữa mâu thuẫn, nằm mơ đều không có nghĩ đến, thế mà vừa đến liền bị say ngất... Chịu đến như vậy khuất nhục!"),t("br"),t("br"),n._v("“Ta muốn giết hắn!”"),t("br"),t("br"),n._v("Gầm lên giận dữ, một chân đạp tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Lần nữa bay ngược mà ra, Sở Thiên Hành đâm vào trên tường, từng ngụm từng ngụm hộc máu."),t("br"),t("br"),n._v("“Đây là ngươi đưa ta quyền sáo, ngươi tới làm cái thứ nhất vật thí nghiệm đi!”"),t("br"),t("br"),n._v("Lấy ra quyền sáo, mang trên tay, đi tới trước mặt, Lưu Dương Thiên Đạo quyền pháp vận chuyển, thẳng tắp nện cho đi lên."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngực lập tức xẹp xuống, Sở Thiên Hành xương sườn đứt gãy."),t("br"),t("br"),n._v("Giơ lên nắm đấm, Lưu Dương đang nghĩ tiếp tục động thủ, một bên Hứa trưởng lão con ngươi đột nhiên thu hẹp."),t("br"),t("br"),n._v("“Không tốt, hắn, hắn... Chết!”"),t("br"),t("br"),n._v("“Chết rồi?”"),t("br"),t("br"),n._v("Lưu Dương sững sờ, vội vàng nhìn lại, lúc này mới phát hiện, Sở Thiên Hành quả nhiên một mặt hoảng sợ, chẳng biết lúc nào, đã cắt đứt hô hấp."),t("br"),t("br"),n._v("“Cái này nguy rồi...”"),t("br"),t("br"),n._v("Nhìn thấy Hoàng đế tử tại trước mặt, mọi người tất cả đều sắc mặt trắng nhợt, ngay cả Tôn Cường, đều thân thể kìm lòng không được cứng ngắc."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1237"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1239"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);