(window.webpackJsonp=window.webpackJsonp||[]).push([[285],{640:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1233-ve-thanh-nguyen-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1233-ve-thanh-nguyen-thanh"}},[n._v("#")]),n._v(" Chương 1233: Về Thanh Nguyên thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1233: Về Thanh Nguyên thành"),t("br"),t("br"),n._v("Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Như thế lớn hòn đảo, Thiên Đạo chi sách đều có thể để xuống, không hổ là thư viện xuất phẩm, chất lượng không thể nói."),t("br"),t("br"),n._v("Đem sách thu hồi, Trương Huyền bay lượn mà quay về."),t("br"),t("br"),n._v("Lần này tới Tề Bắc đầm lầy, mặc dù mạo hiểm vô cùng, thu hoạch nhưng cũng cực kỳ phong phú, không nói trước Ngoan Nhân dung hợp về sau, tất nhiên thực lực tăng nhiều, chỉ nói cái kia hơn trăm đầu Xuất Khiếu cảnh trung kỳ khôi lỗi, liền tuyệt đối có thể trở thành một đại chiến đấu lực."),t("br"),t("br"),n._v("Triệt để thuần phục lời nói, một khi thả ra ngoài, Thánh vực lục trọng sơ kỳ cường giả gặp gỡ, chỉ sợ đều muốn bị quần ẩu một hồi, không cách nào phản kháng."),t("br"),t("br"),n._v("Mấu chốt nhất là, những khôi lỗi này cùng lần trước thu phục Dị Linh tộc khôi lỗi khác biệt, thuộc về Thiên Công vật phẩm, không mang theo bất luận cái gì Dị Linh tộc khí tức, cho dù tại Danh Sư đường, cũng có thể tuỳ thích sử dụng, không cần lo lắng cái gì."),t("br"),t("br"),n._v("“Trương sư... Cái kia hòn đảo đâu?”"),t("br"),t("br"),n._v("Trở lại mọi người trước mặt, Ngô sư đám người tràn đầy nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Chuyện này dính dáng rất lớn, các ngươi còn là không nên hỏi...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lừa gạt đối phương, nói dối nói, chung quy có vạch trần vào cái ngày đó, cùng hắn như thế, còn không bằng không nói."),t("br"),t("br"),n._v("“Vâng!” Mọi người nhẹ gật đầu."),t("br"),t("br"),n._v("Cứ việc tò mò, nhưng nghĩ đến đối phương trời nhận thân phận của danh sư, tất cả đều nhịn xuống."),t("br"),t("br"),n._v("“Trương sư, chuyện lần này, có muốn hay không bẩm báo Danh Sư đường tổng bộ?”"),t("br"),t("br"),n._v("Mọi người một bên hướng ra phía ngoài rời khỏi, Ngô sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Chơi đùa không biết bao lâu, trời đã sáng lên, một đêm kinh hồn, may mắn tất cả mọi người không có quá đáng lo."),t("br"),t("br"),n._v("“Thôi được rồi...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Dính dáng Ngoan Nhân, liền nhất thiết phải dính dáng Thiên Đạo chi sách, giải thích, phiền phức vô cùng."),t("br"),t("br"),n._v("“Cái này...” Ngô sư dừng lại một chút: “Nếu như không bẩm báo, Cẩu đường chủ, Điền Thanh phó đường chủ tử vong liền không có cách nào giải thích, chỉ sợ càng thêm phiền phức...”"),t("br"),t("br"),n._v("Thanh Nguyên đế quốc Danh Sư đường, chết hai vị đường chủ, nhiều như vậy thất tinh danh sư, liền như thế đầu hổ đuôi rắn kết thúc lời nói, tổng bộ tất nhiên phái người tường tra, đến lúc đó, càng thêm phiền phức."),t("br"),t("br"),n._v("“Là như vậy ah...”"),t("br"),t("br"),n._v("Trương Huyền xoa xoa mi tâm."),t("br"),t("br"),n._v("Dựa theo ý nghĩ của hắn, khiêm tốn đem chỗ tốt lấy đi, tất cả mọi người giả vờ không biết tốt nhất... Bất quá, ra nhiều chuyện như vậy, muốn điệu thấp cũng khó khăn."),t("br"),t("br"),n._v("“Chủ động bẩm báo lời nói, có thể từ chúng ta quyết định giấu diếm cái gì, có thể miễn đi không ít phiền phức...”"),t("br"),t("br"),n._v("Ngô sư biết hắn lo lắng, nói."),t("br"),t("br"),n._v("Trước mắt vị này Trương sư, mặc dù tuổi trẻ, tính cách lại hết sức trầm ổn, có thể đem trời nhận danh sư loại đại sự này giấu diếm đến bây giờ, hành động thấp điều, có thể nói tấm gương."),t("br"),t("br"),n._v("Điệu thấp như vậy người, tự nhiên không muốn huyên náo dư luận xôn xao!"),t("br"),t("br"),n._v("Bất quá, chuyện quá lớn, không bẩm báo không được, trước thời hạn nói, có thể có hạn chế đem tin tức loại bỏ, đối bọn hắn đều có chỗ tốt."),t("br"),t("br"),n._v("“Ừm!” Suy tư một chút, Trương Huyền cổ tay khẽ đảo, hai bộ thi thể xuất hiện ở trước mắt: “Đây là Thanh Điền Hoàng thi thể, ngươi báo lên tổng bộ, liền nói tất cả đều là hắn làm ra, hiện tại đã đền tội!”"),t("br"),t("br"),n._v("“Thanh Điền Hoàng thi thể?”"),t("br"),t("br"),n._v("Ngô sư tràn đầy kinh ngạc."),t("br"),t("br"),n._v("Không phải đã chết rồi sao? Tại sao lại xuất hiện một cái?"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Trương Huyền đem tình huống cẩn thận giải thích một chút, Ngô sư giờ mới hiểu được tới, khiếp sợ tột đỉnh."),t("br"),t("br"),n._v("Không hổ là sống không biết bao nhiêu năm Hoàng giả, phân thân một cái tiếp một cái, như là đánh không chết con gián!"),t("br"),t("br"),n._v("Nếu như không phải trước mắt vị thanh niên này, bọn họ gặp gỡ, chỉ sợ đã sớm bị giết, chết không biết bao lâu."),t("br"),t("br"),n._v("“Chém giết Thanh Điền nhất mạch Hoàng giả, cùng thập đại vương giả, công lao cực lớn, ta hiện tại liền lên báo tổng bộ, chờ bọn họ phán quyết...”"),t("br"),t("br"),n._v("Rõ ràng chuyện gì xảy ra, Ngô sư biết trước mắt vị này lo lắng, lại không nói hòn đảo biến thành đầu người chuyện."),t("br"),t("br"),n._v("“Ừm!” Thấy hắn rõ ràng chính mình ý tứ, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Chỉ cần Ngoan Nhân sự tình, không nói là được."),t("br"),t("br"),n._v("Thanh Điền Hoàng mà thôi, chết thì chết, không tính là gì việc lớn."),t("br"),t("br"),n._v("Vừa vặn có thể mượn nhờ cái này thi thể, giải thích một chút Thanh Nguyên đế quốc phát sinh rất nhiều quái sự."),t("br"),t("br"),n._v("“Chuyện này đi chúng ta Danh Sư đường thông bẩm đi, có thể xin hình ảnh, tin tức chuẩn xác hơn một chút, cũng càng trực quan một chút!”"),t("br"),t("br"),n._v("Quý Thiên Hùng xen vào nói."),t("br"),t("br"),n._v("“Như vậy tốt nhất!”"),t("br"),t("br"),n._v("Ngô sư ánh mắt sáng lên, ngay sau đó quay đầu, hướng Trương Huyền nhìn lại, muốn nghe theo ý kiến của hắn."),t("br"),t("br"),n._v("Danh Sư đường, nắm giữ có thể trực tiếp hướng tổng bộ truyền lại hình ảnh vật phẩm, Thanh Điền Hoàng thi thể, dính dáng như thế lớn, tự nhiên muốn truyền lại đến phía trên mới được."),t("br"),t("br"),n._v("“Như vậy đi, Ngô sư, các ngươi đi Quý sư nơi đó, cho tổng bộ giải thích, ta còn có việc, liền về trước Thanh Nguyên thành!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Mặc dù Ngoan Nhân nói, lấy Lưu Dương áp chế, hắn giả bộ rất bình tĩnh, trên thực tế, đã sớm muốn trở về nhìn một chút, đến cùng thế nào."),t("br"),t("br"),n._v("Lưu Dương, cứ việc trong tính cách không có đặc biệt xông ra đặc thù, thoạt nhìn mười phần bình thường, nhưng dù sao cũng là hắn theo Thiên Huyền vương quốc mang đi ra học sinh, từng bước một đi đến ngày hôm nay, tiếp cận thời gian một năm, sớm đã có cảm tình sâu đậm."),t("br"),t("br"),n._v("“Cái này... Cũng tốt!” Biết hắn lo lắng cái gì, Ngô sư nhẹ gật đầu."),t("br"),t("br"),n._v("Thương nghị xong xuôi, không nói thêm lời, buổi trưa mười phần, mọi người rốt cục lần nữa đi ra đầm lầy, ngồi phi hành Thánh thú, đi tới Tề Bắc trên thành không, Ngô sư đám người bay xuống, Trương Huyền thì tiếp tục hướng Thanh Nguyên thành phương hướng bay đi."),t("br"),t("br"),n._v("Mọi người rời khỏi, Trương Huyền không chần chờ chút nào, thân thể nhoáng một cái, đi vào Thiên Nghĩ Phong sào."),t("br"),n._v("“Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Vừa mới tiến tới liền nghe đến một tiếng tức giận gào thét."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Ngay sau đó là đánh người thanh âm, đi tới trước mặt, chỉ thấy phân thân, không ngừng cuồng đánh bị hút tại tế đàn lên Thanh Điền Hoàng vu hồn, căn bản không cho đối phương cơ hội khôi phục."),t("br"),t("br"),n._v("Lúc này Thanh Điền Hoàng, mỏng manh bất cứ lúc nào cũng sẽ sụp đổ, nhưng mà trên mặt vẫn như cũ tràn đầy vẻ dữ tợn, hận không thể đem một người một phân thân, phanh thây xé xác."),t("br"),t("br"),n._v("“Tốt rồi, chớ ồn ào, ngoan ngoãn đừng động, để cho ta sưu cái hồn...” An ủi một câu, Trương Huyền bàn tay đặt tại đầu của đối phương bên trên, tinh thần khẽ động, bản thân to lớn hồn thể theo mi tâm bay ra."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Tại Thanh Điền Hoàng hoảng sợ ánh mắt bên trong, một đạo dây nhỏ lan tràn đi vào đối phương thức hải."),t("br"),t("br"),n._v("Một canh giờ sau, Trương Huyền vu hồn quay về, xoa xoa mi tâm, một ngụm trọc khí phun ra."),t("br"),t("br"),n._v("Lúc này Thanh Điền Hoàng ý thức, đã sụp đổ, bất cứ lúc nào cũng sẽ biến mất."),t("br"),t("br"),n._v("“Ngoan Nhân đã ngủ, ngươi đem cái tên này luyện hóa thôn phệ đi...”"),t("br"),t("br"),n._v("Đối phân thân khoát tay áo."),t("br"),t("br"),n._v("Còn không có rời khỏi đầm lầy, Ngoan Nhân ý niệm liền lâm vào ngủ say, làm hại hắn muốn hỏi thăm cái gì cũng không kịp."),t("br"),t("br"),n._v("Đối phương ngủ say, giữ lại Thanh Điền Hoàng nguyên thần cũng vô dụng, không bằng cho phân thân nuốt, có lẽ sẽ đối thực lực có rất lớn tăng thêm."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Phân thân mặt mũi hưng phấn, kéo lấy sống không thể yêu Thanh Điền Hoàng đi về phòng, thời gian không dài, truyền đến thảm thiết hô."),t("br"),t("br"),n._v("Lắc đầu, không để ý đối phương, Trương Huyền vuốt vuốt trong đầu vừa mới sưu hồn có được ký ức."),t("br"),t("br"),n._v("Liên quan tới Ngoan Nhân sự tình, cùng ở giữa nói gần như."),t("br"),t("br"),n._v("Thanh Điền Hoàng sở dĩ hiệu trung Ngoan Nhân, quả thực là vì đạt được truyền thừa của hắn, trở nên càng thêm cường đại, Ngoan Nhân muốn thu hắn làm đồ, thì là muốn mượn lực lượng của đối phương, tìm kiếm những bộ phận khác, khôi phục nguyên khí."),t("br"),t("br"),n._v("Dù sao đều là mỗi người đều có mục đích riêng phải đạt được."),t("br"),t("br"),n._v("“Ngoan Nhân chỉ đem trước ngũ trọng vu hồn tu luyện công pháp, truyền cho hắn...”"),t("br"),t("br"),n._v("Thông qua sưu hồn, tìm được Ngoan Nhân truyền cho Thanh Điền Hoàng vu hồn công pháp, có điều, chỉ có Thánh vực nhất trọng đến ngũ trọng."),t("br"),t("br"),n._v("“Đem vu hồn, chuyển hóa thành nguyên thần, quả thực không dễ dàng như vậy...”"),t("br"),t("br"),n._v("Gặp công pháp nghiên cứu một lần, Trương Huyền than thở."),t("br"),t("br"),n._v("Nguyên thần, là linh hồn hội tụ nguyên thai, hình thành thai hậu kỳ, dần dần tăng trưởng mà thành, từ chân khí cùng linh hồn tạo thành, bởi vậy, ly thể có thể sống sót, cũng có chân khí lực lượng."),t("br"),t("br"),n._v("Vu hồn khác biệt, là đơn thuần linh hồn tu luyện, đi qua đủ loại bí pháp rèn luyện, để hắn nắm giữ có thể so với thân thể sức chiến đấu."),t("br"),t("br"),n._v("Cái trước bởi vì có chân khí truyền vào, mang theo ánh sáng minh chính đại hương vị, mà người sau, âm lãnh vô cùng, sợ hãi ánh sáng mặt trời cùng lôi đình, trên bản chất cũng không tương đồng."),t("br"),t("br"),n._v("Bởi vì là đơn thuần linh hồn, liền cùng cơ bắp không có khung xương đồng dạng, muốn chuyển hóa thành nguyên thần, độ khó bao lớn, có thể tưởng tượng được."),t("br"),t("br"),n._v("Xuất Khiếu kiếp, liền là lớn nhất cửa ải khó."),t("br"),t("br"),n._v("Bởi vậy, vu hồn sư chức nghiệp, cứ việc có rất nhiều chỗ tốt, nhưng mà đạt tới Xuất Khiếu kiếp, không ít người chẳng khác nào đến đỉnh phong, lại khó tinh tiến."),t("br"),t("br"),n._v("Một vạn người, có thể thông qua lôi đình, không đủ một người."),t("br"),t("br"),n._v("Vị này Thanh Điền Hoàng, có thể cứ thế mà tu luyện tới Xuất Khiếu cảnh hậu kỳ, có thể nói là thiên tài trong thiên tài."),t("br"),t("br"),n._v("“Bất quá, ta tu luyện chính là trời đạo vu hồn, bản thân liền là dương thuộc tính, dung hợp thai hậu kỳ càng thêm cường đại, cũng không dính dáng vấn đề này!”"),t("br"),t("br"),n._v("Trương Huyền từ vừa mới bắt đầu, liền tu luyện chính là trời đạo vu hồn, không có thuộc tính âm hàn, càng dùng lôi đình rèn luyện qua, mặc dù chỉ là Thai Anh cảnh, lại cùng nguyên thần có cách làm khác nhau nhưng kết quả lại giống nhau đến kì diệu."),t("br"),t("br"),n._v("Vu hồn sư thường gặp phải vấn đề này, đối với hắn mà nói, vấn đề không lớn."),t("br"),t("br"),n._v("“Dung hợp kinh hồng sư, khải linh sư chờ công hội liên quan tới linh hồn tu luyện bí tịch, vu hồn Thiên Đạo công pháp, theo Thánh vực đệ nhất trọng, đến đệ tứ trọng đều có, hai tầng đầu có thể trực tiếp tu luyện, đằng sau hai tầng chỉ có thể tiếp tục tìm kiếm tinh nguyên linh thạch thượng phẩm!”"),t("br"),t("br"),n._v("Đem cái này ngũ trọng công pháp, cùng tại Thanh Nguyên đế quốc học tập đến rất nhiều sách dung hợp lại cùng nhau, rất nhanh hội tụ ra Thánh vực trước bốn nặng Thiên Đạo vu hồn pháp quyết."),t("br"),t("br"),n._v("Hai tầng đầu, bình thường linh thạch thượng phẩm liền có thể tu luyện, không đến hai canh giờ, cũng đã tu luyện hoàn thành!"),t("br"),t("br"),n._v("Nhưng mà sau hai tầng, thì cần muốn tinh nguyên linh thạch thượng phẩm, hiện tại hắn trong tay liền một viên đều không có, chỉ có thể đến Thanh Nguyên đế quốc lại nói."),t("br"),t("br"),n._v("Chuyến này làm trễ nải hơn mười ngày, chắc hẳn Chiến Sư đường ban thưởng cũng nên xuống."),t("br"),t("br"),n._v("Xử lý xong Thanh Điền Hoàng nguyên thần, Trương Huyền đem lấy đi khôi lỗi phóng ra."),t("br"),t("br"),n._v("Để tâm ma từng cái rời khỏi, sau đó mượn nhờ sư ngôn thiên bẩm thuần phục, không đến ba ngày thời gian, đám người kia, liền toàn bộ hiệu trung bản thân, thành trung thành thuộc hạ."),t("br"),t("br"),n._v("Khoảng cách Thanh Nguyên thành còn có một đoạn thời gian, Trương Huyền cũng không vội vã, đem Thanh Điền Hoàng trữ vật giới chỉ, cẩn thận nghiên cứu một lần."),t("br"),t("br"),n._v("Bảo vật là không ít, có điều, đồng thời không có tinh nguyên linh thạch thượng phẩm."),t("br"),t("br"),n._v("Nhìn tới, cái tên này là Xuất Khiếu cảnh cường giả, cho dù có, khẳng định cũng vậy bản thân dùng."),t("br"),t("br"),n._v("Lắc đầu, mặt mũi bất đắc dĩ, Trương Huyền đành phải mượn nhờ thời gian còn lại đọc sách, tu luyện, không lãng phí từng phút từng giây."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền bên này đi đường, cuống cuồng xanh trở lại nguyên đế quốc, bên kia Ngô sư mấy người cũng đến Tề Bắc thành Danh Sư đường, tiến hành một loạt nghi thức về sau, Quý Thiên Hùng bàn tay nhẹ nhàng điểm một cái, một cỗ hùng hồn khí tức, bỗng nhiên kích động, một cái lão giả hư ảnh chậm rãi xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Liên minh đế quốc Danh Sư đường đường chủ, bát tinh danh sư, sao hiên!"),t("br"),t("br"),n._v("“Chuyện gì xảy ra, muốn nhờ hình ảnh đưa tin tại ta?”"),t("br"),t("br"),n._v("Thanh âm đập vào mặt, mang theo nồng đậm cảm giác áp bách."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1241"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1243"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);